INSERT INTO [t127656] VALUES (575,'٣٢ - باب النهي عن قتل النساء والصبيان في الحرب، فإن بيتوا في دارهم جاز ذلك','952','543','0');
INSERT INTO [t127656] VALUES (576,'٣٣ - باب الإمام يخير في قتل الأسارى، فإن اختار القتل فلا يحرقهم، وقوله تعالى: {فإما منا بعد وإما فداء} [محمد: ٤] وقد ربط النبي -صلى الله عليه وسلم- ثمامة بسارية من سواري المسجد','953','543','0');
INSERT INTO [t127656] VALUES (577,'٣٤ - باب النهي عن تمني لقاء العدو، والأمر بالصبر عند اللقاء، والحرب خدعة، وإعمال الحيلة في قتل العدو','955','543','0');
INSERT INTO [t127656] VALUES (578,'٣٥ - باب ما يكره من التنازع والاختلاف في الحرب، وعقوبة من عصى إمامه. وقال الله -عز وجل-: {ولا تنازعوا فتفشلوا وتذهب ريحكم} [الأنفال: ٤٦].','957','543','0');
