INSERT INTO [t127656] VALUES (593,'٥٠ - باب تحليل الغنائم وقول الله -عز وجل-: {وعدكم الله مغانم كثيرة تأخذونها} [الفتح: ٢٠] الآية','987','543','0');
INSERT INTO [t127656] VALUES (594,'٥١ - باب كرامة الغازي المخلص حيا وميتا، وبركة ماله','988','543','0');
