INSERT INTO [t127656] VALUES (600,'٥٧ - باب أخذ الجزية من أهل الكتاب والمجوس، والمصالحة، وكم الجزية، وقوله تعالى: {قاتلوا الذين لا يؤمنون بالله ولا باليوم الآخر} [التوبة: ٢٩] الآية','1000','543','0');
INSERT INTO [t127656] VALUES (601,'٥٨ - باب إثم من قتل معاهدا، والوصاة بأهل الذمة، ولا يقر منهم أحد بجزيرة العرب','1004','543','0');
INSERT INTO [t127656] VALUES (602,'٥٩ - باب ما يحذر من الغدر، وإذا غدر المشرك هل يعفى عنه','1007','543','0');
INSERT INTO [t127656] VALUES (603,'٦٠ - باب إثم من عاهد ثم غدر. وذمة المسلمين واحدة، وأمان المرأة. وقوله تعالى: {الذين عاهدت منهم ثم ينقضون عهدهم} [الأنفال: ٥٦]','1009','543','0');
INSERT INTO [t127656] VALUES (604,'٦١ - باب يطاع الأمراء وتؤدي حقوقهم، ويصبر على أذاهم','1012','543','0');
INSERT INTO [t127656] VALUES (605,'٣٦ - كتاب بدء الخلق','1014','0','0');
INSERT INTO [t127656] VALUES (606,'١ - باب قوله تعالى: {وهو الذي يبدأ الخلق ثم يعيده وهو أهون عليه} [الروم: ٢٧]','1015','605','0');
INSERT INTO [t127656] VALUES (607,'٢ - باب في قوله تعالى: {الذي خلق سبع سماوات ومن الأرض مثلهن} [الطلاق: ١٢]','1017','605','0');
