INSERT INTO [t127656] VALUES (613,'١ - باب خلق آدم وذريته، وقوله تعالى: {ولقد خلقنا الإنسان من سلالة من طين (١٢) ثم جعلناه نطفة في قرار مكين} [المؤمنون: ١٢ - ١٣] الآية','1027','612','0');
INSERT INTO [t127656] VALUES (614,'٢ - باب في قوله تعالى: {ولقد أرسلنا نوحا إلى قومه} الآية [هود: ٢٥]','1031','612','0');
INSERT INTO [t127656] VALUES (615,'٣ - باب في قوله تعالى: {وإن إلياس لمن المرسلين (١٢٣)} [الصافات: ١٢٣] وقوله: {ورفعناه مكانا عليا (٥٧)} [مريم: ٥٧]','1032','612','0');
INSERT INTO [t127656] VALUES (616,'٤ - باب في قوله تعالى: {وإلى عاد أخاهم هودا} [هود: ٥٠]، قوله {إذ أنذر قومه بالأحقاف} [الأحقاف: ٢١]، وقوله {وأما عاد فأهلكوا بريح صرصر عاتية (٦)} الآية [الحاقة: ٦]','1032','612','0');
INSERT INTO [t127656] VALUES (617,'٥ - باب في ذي القرنين ويأجوج ومأجوج، وقوله تعالى: {ويسألونك عن ذي القرنين} إلى قوله: {نقبا} [الكهف: ٨٣ - ٩٧]','1033','612','0');
INSERT INTO [t127656] VALUES (618,'٦ - باب في ذكر إبراهيم وإسماعيل وأمه، وقول الله تعالى: {واتخذ الله إبراهيم خليلا} [النساء: ١٢٥]، وقوله عز وجل: {إن إبراهيم لأواه حليم} [التوبة: ١١٤]','1035','612','0');
INSERT INTO [t127656] VALUES (619,'٧ - باب في ذكر صالح وقوله تعالى: {كذب أصحاب الحجر المرسلين} الآيات [الحجر: ٨٠]','1045','612','0');
INSERT INTO [t127656] VALUES (620,'٨ - باب ذكر يوسف وأيوب عليهما السلام، وقوله تعالى: {لقد كان في يوسف وإخوته آيات للسائلين (٧)} [يوسف: ٧]','1047','612','0');
INSERT INTO [t127656] VALUES (621,'٩ - ذكر موسى عليه السلام','1048','612','0');
