INSERT INTO [t127656] VALUES (623,'١١ - باب ذكر يونس وقوله: {وإن يونس لمن المرسلين} إلى قوله: {وهو مليم} [الصافات: ١٣٩ - ١٤٢]','1053','612','0');
INSERT INTO [t127656] VALUES (624,'١٢ - باب ذكر داود وسليمان عليهما السلام','1055','612','0');
