INSERT INTO [t127656] VALUES (626,'١٤ - باب ذكر زكريا ويحيى عليهما السلام، وقوله تعالى: {يازكريا إنا نبشرك بغلام اسمه يحيى لم نجعل له من قبل سميا} [مريم: ٧]','1059','612','0');
INSERT INTO [t127656] VALUES (627,'١٥ - باب ذكر عيسى ومريم وآسية وقوله: {واذكر في الكتاب مريم إذ انتبذت من أهلها مكانا شرقيا} [مريم: ١٦]','1060','612','0');
INSERT INTO [t127656] VALUES (628,'١٦ - باب في وصف عيسى عليه السلام والتحذير من الغلو فيه وقوله تعالى: {ياأهل الكتاب لا تغلوا في دينكم ولا تقولوا على الله إلا الحق}. . . إلى قوله: {وكيلا} [النساء: ١٧١]','1062','612','0');
INSERT INTO [t127656] VALUES (629,'١٧ - باب في قوله تعالى في عيسى: {ويكلم الناس في المهد وكهلا} [آل عمران: ٤٦]','1066','612','0');
INSERT INTO [t127656] VALUES (630,'١٨ - قول الله -عز وجل-: {ياأيها الناس إنا خلقناكم من ذكر وأنثى} [الحجرات: ١٣] وما ينهى عنه من دعوى الجاهلية','1067','612','0');
INSERT INTO [t127656] VALUES (631,'١٩ - باب في مناقب قريش والأنصار وجهينة ومزينة','1070','612','0');
INSERT INTO [t127656] VALUES (632,'٢٠ - باب مناقب أسلم وغفار','1072','612','0');
