INSERT INTO [t127656] VALUES (1423,'٢ - باب قوله تعالى: {ولله الأسماء الحسنى فادعوه بها} [الأعراف: ١٨٠] ولا يسمى إلا بما سمى به نفسه','2177','1421','0');
INSERT INTO [t127656] VALUES (1424,'٣ - باب ما سمى الله تعالى به من وجه ونفس وذات وعين ويد وروح في كتابه وفي سنة نبيه','2178','1421','0');
INSERT INTO [t127656] VALUES (1425,'٤ - باب: لله تعالى من المحامد ما لم يطلع عليها أحد في هذه الدار، ويستطيع على ما شاء منها نبيه المختار','2180','1421','0');
INSERT INTO [t127656] VALUES (1426,'٥ - باب في قوله تعالى: {قل أي شيء أكبر شهادة قل الله} [الأنعام: ١٩]، فسمى نفسه شيئا، وفي قوله: {وكان عرشه على الماء} [هود: ٧] {وهو رب العرش العظيم} [التوبة: ١٢٩]','2181','1421','0');
INSERT INTO [t127656] VALUES (1427,'٦ - باب قوله تعالى: {تعرج الملائكة والروح إليه} [المعارج: ٤] و {إليه يصعد الكلم الطيب} [فاطر: ١٠]','2181','1421','0');
INSERT INTO [t127656] VALUES (1428,'٧ - باب قول الله تعالى: {وجوه يومئذ ناضرة (٢٢) إلى ربها ناظرة} [القيامة: ٢٢ - ٢٣]','2182','1421','0');
INSERT INTO [t127656] VALUES (1429,'٨ - باب كلام الله تعالى مع نبيه ومع المؤمنين يوم القيامة من غير حجاب يحجبهم به','2187','1421','0');
INSERT INTO [t127656] VALUES (1430,'٩ - باب لله تعالى مشيئة وإرادة','2190','1421','0');
INSERT INTO [t127656] VALUES (1431,'١٠ - باب في قوله تعالى: {ولا تنفع الشفاعة عنده إلا لمن أذن له حتى إذا فزع عن قلوبهم قالوا ماذا قال ربكم} [سبأ: ٢٣]، ولم يقل: ماذا خلق ربكم؟','2192','1421','0');
INSERT INTO [t127656] VALUES (1432,'١١ - باب وكلم الله موسى تكليما، وتكلم الله مع نبينا من غير واسطة، فقد سمع ما سمع موسى صلوات الله عليهما','2193','1421','0');
INSERT INTO [t127656] VALUES (1433,'١٢ - باب في قوله تعالى: {فلا تجعلوا لله أندادا وأنتم تعلمون} [البقرة: ٢٢] وقوله: {وتجعلون له أندادا ذلك رب العالمين} [فصلت: ٩]','2198','1421','0');
INSERT INTO [t127656] VALUES (1434,'١٣ - باب {ياأيها الرسول بلغ ما أنزل إليك من ربك وإن لم تفعل فما بلغت رسالته} [المائدة: ٦٧]','2199','1421','0');
INSERT INTO [t127656] VALUES (1435,'١٤ - باب في رواية النبي -صلى الله عليه وسلم- عن ربه تعالى','2199','1421','0');
INSERT INTO [t127656] VALUES (1436,'١٥ - باب في قوله تعالى: {فاقرءوا ما تيسر منه} [المزمل: ٢٠]، {ولقد يسرنا القرآن للذكر} [القمر: ١٧]؛ أي: هيأناه، ومنه: "كل ميسر لما خلق له"؛ أي: مهيأ','2200','1421','0');
INSERT INTO [t127656] VALUES (1437,'١٦ - باب قوله تعالى: {والله خلقكم وما تعملون} [الصافات: ٩٦] وقوله: {إنا كل شيء خلقناه بقدر} [القمر: ٤٩]','2202','1421','0');
INSERT INTO [t127656] VALUES (1438,'١٧ - باب قوله: {ونضع الموازين القسط ليوم القيامة} [الأنبياء: ٤٧] وأن أعمال العبد وأقوالهم توزن','2203','1421','0');
