INSERT INTO [t127663] VALUES (80,'فصل','426','79','0');
INSERT INTO [t127663] VALUES (81,'فصل','429','79','0');
INSERT INTO [t127663] VALUES (82,'٣٩ - مسألة: إذا نسي التشهد الأول، ثم ذكر بعد أن اعتدل قائما، وقبل أن يشرع في القراءة، فالمستحب له: أن يمضي في صلاته، ولا يرجع، فإن رجع، جاز','430','33','0');
INSERT INTO [t127663] VALUES (83,'٤٠ - مسألة: إذا قرأ في الأخيرتين من الظهر، والعصر، وعشاء الآخرة بالحمد وسورة ساهيا، أو صلى على النبي - صلى الله عليه وسلم - في التشهد الأول، أو دعا بما يدعو في التشهد الآخر، أو قرأ في موضع تشهده، أو موضع ركوعه وسجوده، أو تشهد في موضع قيامه، أو قال في موضع ركوعه: سمع الله لمن حمده، ونحو ذلك، فإنه يسجد في جميع ذلك سجود السهو','434','33','0');
INSERT INTO [t127663] VALUES (84,'٤١ - مسألة: إذا ترك تكبيرات العيدين، أو قرأ بالسورة، لم يسجد للسهو','438','33','0');
INSERT INTO [t127663] VALUES (85,'٤٢ - مسألة: إذا جهر فيما يسر، أو أسر فيما يجهر، لم يسجد للسهو في أصح الروايتين','441','33','0');
INSERT INTO [t127663] VALUES (86,'٤٣ - مسألة: إذا ترك تكبيرات الخفض، والرفع، والتسبيح في الركوع، والسجود، وقول: سمع الله لمن حمده، وقول: ربنا لك الحمد، والتشهد الأول، والصلاة على النبي - صلى الله عليه وسلم - في التشهد الأخير، فإنه يسجد للسهو','445','33','0');
INSERT INTO [t127663] VALUES (87,'٤٤ - مسألة','446','33','0');
