INSERT INTO [t127663] VALUES (127,'٧٧ - مسألة: إذا طلع الفجر الثاني، حرمت النوافل سوى ركعتي الفجر','650','33','0');
INSERT INTO [t127663] VALUES (128,'٧٨ - مسألة: إذا دخل في صلاة الصبح، ثم طلعت الشمس، أتم صلاته، ولم تبطل بطلوع الشمس','654','33','0');
INSERT INTO [t127663] VALUES (129,'٧٩ - مسألة: النوافل المرتبة مع الفرائض إذا فاتت، فإنها تقضى','658','33','0');
INSERT INTO [t127663] VALUES (130,'٨٠ - مسألة: إذا أدرك الناس في صلاة الصبح، ولم يصل ركعتي الفجر، فإنه يصلي معهم المكتوبة، ولا يتشاغل بها','664','33','0');
INSERT INTO [t127663] VALUES (131,'٨١ - مسألة: الأفضل في النوافل أن يسلم من كل ركعتين، بالليل والنهار','667','33','0');
INSERT INTO [t127663] VALUES (132,'٨٢ - مسألة: الوتر سنة مؤكدة، وليست بواجبة','682','33','0');
INSERT INTO [t127663] VALUES (133,'٨٣ - مسألة: أقل الوتر ركعة، وأكثره إحدى عشرة ركعة، يسلم من كل ركعتين، ويوتر بركعة، وإن كان الوتر بثلاث بسلام واحد، جاز، إلا أنه يجلس عقيب الثانية، ويقوم إلى الثالثة، وإن كان الوتر بخمس أو سبع بسلام واحد، لم يجلس إلا في الأخيرة، وإن أوتر بتسع بسلام، جلس عقيب الثامنة، ثم يقوم فيأتي بالركعة، ويسلم','704','33','0');
INSERT INTO [t127663] VALUES (134,'٨٤ - مسألة: القنوت مسنون في الوتر في سائر السنة','725','33','0');
INSERT INTO [t127663] VALUES (135,'٨٥ - مسألة: يقنت بعد الركوع','734','33','0');
INSERT INTO [t127663] VALUES (136,'٨٦ - مسألة: المستحب أن يقرأ في الشفع بـ {سبح} [الأعلى: ١]، {قل ياأيها الكافرون} [الكافرون: ١]، وفي الوتر بالإخلاص','741','33','0');
