INSERT INTO [t127663] VALUES (146,'٩٤ - مسألة: لا يجوز أن يأتم القادر على الركوع والسجود بالمومئ بحال، سواء كان إمام الحي، أو غيره','807','33','0');
INSERT INTO [t127663] VALUES (147,'٩٥ - مسألة: إذا صلى ركعة بإيماء، ثم صح، بنى على ما مضى','813','33','0');
INSERT INTO [t127663] VALUES (148,'٩٦ - مسألة: العاري إذا وجد في صلاته ما يستر به عورته، وكان قريبا، ستر عورته، وبنى على صلاته','818','33','0');
INSERT INTO [t127663] VALUES (149,'٩٧ - مسألة: من يقدر على القيام، ولا يقدر على الركوع والسجود، فإنه يصلي قائما، ويومئ إيماء بالركوع، وفي السجود يجلس فيومئ','821','33','0');
INSERT INTO [t127663] VALUES (150,'٩٨ - مسألة: إذا عجز المريض عن الإيماء برأسه، أومأ بعينيه وحاجبيه أو قلبه، ولا يسقط عنه فرض الصلاة','823','33','0');
INSERT INTO [t127663] VALUES (151,'٩٩ - مسألة: إذا كان بعينيه مرض، فقال الأطباء: إن صليت مستلقيا، زال، جاز له الاستلقاء','826','33','0');
INSERT INTO [t127663] VALUES (152,'١٠٠ - مسألة: إذا صلى في سفينة سائرة صلاة الفرض قاعدا، وهو قادر على القيام، لم تجزئه صلاته','829','33','0');
