INSERT INTO [t127663] VALUES (161,'١٠٦ - مسألة: لا تصح إمامة الفاسق، سواء كان فسقه في اعتقاده، أو في أفعاله في أصح الروايتين','889','33','0');
INSERT INTO [t127663] VALUES (162,'١٠٧ - مسألة: القارئ أولى بالإمامة من الفقيه، وهو أن يكون أحدهما يحسن جميع القرآن، ومن الفقه ما يتعلق بأحكام الصلاة، والآخر يحسن من القرآن ما يجزئ به الصلاة، ومن الفقه شيئا كثيرا','903','33','0');
INSERT INTO [t127663] VALUES (163,'١٠٨ - مسألة: إذا افتتح الصلاة منفردا، ثم ائتم بغيره، فسدت صلاته في أصح الروايتين','908','33','0');
INSERT INTO [t127663] VALUES (164,'١٠٩ - مسألة: فإن افتتح الصلاة منفردا، ثم صار إماما، فسدت صلاته في أصح الروايتين','916','33','0');
