INSERT INTO [t127663] VALUES (173,'١١٧ - مسألة: إذا أم رجلا أو امرأة، فمن شرط صحة الائتمام: أن ينوي إمامة من يؤمه','955','33','0');
INSERT INTO [t127663] VALUES (174,'١١٨ - مسألة: صلاة الفذ خلف الصف وحده باطلة','959','33','0');
INSERT INTO [t127663] VALUES (175,'١١٩ - مسألة: لا بأس بقتل القملة ودفنها في الصلاة','975','33','0');
