INSERT INTO [t127663] VALUES (187,'١٢٩ - مسألة: إذا ائتم المسافر بمقيم، لزمه الإتمام، ولا فرق بين أن يدرك مع المقيم ركعة، أو أقل؛ فإنه يلزمه التمام','1051','33','0');
INSERT INTO [t127663] VALUES (188,'١٣٠ - مسألة: إذا نسي صلاة سفر، فذكرها في الحضر، صلاها صلاة حضر','1055','33','0');
INSERT INTO [t127663] VALUES (189,'١٣١ - مسألة إذا دخل المسافر في صلاة مقيم، ثم أفسدها، وأراد أن يصليها وحده، فإنه يتممها أربعا','1060','33','0');
INSERT INTO [t127663] VALUES (190,'١٣٢ - مسألة: مسافر صلى بمسافرين ومقيمين، فأحدث الإمام قبل أن يستكمل ركعتين، فقدم مقيما ليصلي بقية الصلاة، وجب على المسافرين أن يتموا الصلاة أربعا','1062','33','0');
INSERT INTO [t127663] VALUES (191,'١٣٣ - مسألة: إذا سافر بعد دخول وقت الصلاة، فهل يجوز له القصر، أم لا؟','1065','33','0');
