INSERT INTO [t127663] VALUES (193,'١٣٥ - مسألة: إذا سافر سفر معصية، لم يجز له القصر، والفطر، والمسح ثلاثة أيام، وأكل الميتة','1070','33','0');
INSERT INTO [t127663] VALUES (194,'١٣٦ - مسألة: يجوز الجمع بين الظهر والعصر، والمغرب والعشاء في السفر الذي يقصر فيه الصلاة','1083','33','0');
INSERT INTO [t127663] VALUES (195,'١٣٧ - مسألة: لا يجوز الجمع في السفر الذي لا يجوز القصر فيه','1101','33','0');
INSERT INTO [t127663] VALUES (196,'١٣٨ - مسألة: يجوز الجمع بين الصلاتين في وقت إحداهما في الحضر لأجل المطر','1103','33','0');
INSERT INTO [t127663] VALUES (197,'١٣٩ - مسألة: فإذا ثبت جواز الجمع في الحضر لأجل المطر، فهل يجوز ذلك بين الظهر والعصر؟','1107','33','0');
INSERT INTO [t127663] VALUES (198,'١٤٠ - مسألة: الطين والوحل عذر في الجمع','1111','33','0');
