INSERT INTO [t127663] VALUES (200,'١٤٢ - مسألة: تجب الجمعة على من كان خارج مصر في موضع يسمع النداء من البلد إذا كان المؤذن صيتا، والأصوات هادئة، والريح ساكنة، وذلك مثل أن يكون في قرية ليس فيها أربعون نفسا','1121','33','0');
INSERT INTO [t127663] VALUES (201,'١٤٣ - مسألة: تقام الجمعة في كل قرية يستوطنها أربعون رجلا، أحرارا، بالغين، عاقلين، لا يظعنون عنها صيفا ولا شتاء','1129','33','0');
INSERT INTO [t127663] VALUES (202,'١٤٤ - مسألة: يجوز لأهل المصر أن يقيموا الجمعة فيما قرب من المصر من الصحراء','1139','33','0');
INSERT INTO [t127663] VALUES (203,'فصل','1142','202','0');
INSERT INTO [t127663] VALUES (204,'١٤٥ - مسألة: لا تنعقد الجمعة بأقل من أربعين رجلا','1143','33','0');
INSERT INTO [t127663] VALUES (205,'١٤٦ - مسألة: لا تصح الخطبة إلا بحضور عدد تنعقد بهم الجمعة','1150','33','0');
INSERT INTO [t127663] VALUES (206,'١٤٧ - مسألة إذا تفرق العدد قبل فراغ الإمام من الجمعة، فلم يبق معه أحد، أو بقي معه أقل من العدد المعتبر فيها، لم يجز أن يصليها جمعة، واستقبل الظهر','1155','33','0');
INSERT INTO [t127663] VALUES (207,'١٤٨ - مسألة: إذا زحم المأموم في السجود، فلم يتمكن من السجود على الأرض، وتمكن من السجود علي ظهر إنسان، لزمه ذلك','1159','33','0');
INSERT INTO [t127663] VALUES (208,'١٤٩ - مسألة: إذا ركع مع الإمام، ثم زحمه الناس، فلم يقدر علي السجود حتى سجد الإمام، وقام إلى الركعة الثانية، ثم زال الزحام، والإمام قائم في الركعة الثانية، فإنه يشتغل بقضاء السجدة التي فاتته من الركعة الأولى، وإن كان راكعا، تابع الإمام في الركوع، ولم يتشاغل بالقضاء','1163','33','0');
INSERT INTO [t127663] VALUES (209,'فصل','1167','208','0');
INSERT INTO [t127663] VALUES (210,'١٥٠ - مسألة: تجب الجمعة على الأعمى إذا وجد قائدا','1168','33','0');
INSERT INTO [t127663] VALUES (211,'١٥١ - مسألة: إذا صلى الجمعة [بالعبيد] والمسافرين، لم يجزئهم','1171','33','0');
INSERT INTO [t127663] VALUES (212,'١٥٢ - مسألة: لا يجوز أن يكون المسافر إماما في الجمعة، وكذلك العبد، إذا قلنا: إن الجمعة لا تجب عليه','1172','33','0');
INSERT INTO [t127663] VALUES (213,'١٥٣ - مسألة: إذا صلى الظهر في منزله يوم الجمعة قبل أن يصلي الإمام من لا عذر له، كانت صلاته باطلة','1175','33','0');
INSERT INTO [t127663] VALUES (214,'١٥٤ - مسألة: إذا صلى الظهر في بيته من لا جمعة عليه؛ كالعبد، والمسافر، والمرأة، والمريض، لم ينتقض ظهره','1184','33','0');
INSERT INTO [t127663] VALUES (215,'١٥٥ - مسألة: لا يكره للعبد والمسافر والمريض أن يصلوا الظهر في يوم الجمعة جماعة','1187','33','0');
INSERT INTO [t127663] VALUES (216,'١٥٦ - مسألة: لا يجوز أن يسافر يوم الجمعة بعد الزوال، رواية واحدة','1194','33','0');
INSERT INTO [t127663] VALUES (217,'فصل','1201','216','0');
INSERT INTO [t127663] VALUES (218,'١٥٧ - مسألة: والخطبة شرط في صحة الجمعة','1203','33','0');
INSERT INTO [t127663] VALUES (219,'١٥٨ - مسألة: إذا خطب على غير وضوء، أجزأه، وكذلك إن كان جنبا، ولم تكن خطبته في المسجد','1206','33','0');
INSERT INTO [t127663] VALUES (220,'١٥٩ - مسألة: إذا خطب جالسا لغير عذر، فقد أساء، وتجزئه','1209','33','0');
INSERT INTO [t127663] VALUES (221,'١٦٠ - مسألة: القعود بين الخطبتين ليس بواجب','1214','33','0');
