INSERT INTO [t127663] VALUES (230,'١٦٧ - مسألة: ويستحب أن يقرأ في الركعة الأولى من صلاة الجمعة بفاتحة الكتاب، وسورة الجمعة، وفي الثانية بفاتحة الكتاب، والمنافقين','1261','33','0');
INSERT INTO [t127663] VALUES (231,'١٦٨ - مسألة','1265','33','0');
INSERT INTO [t127663] VALUES (232,'فصل','1269','231','0');
INSERT INTO [t127663] VALUES (233,'١٦٩ - مسألة: إذا أدرك المأموم الإمام في الجمعة في التشهد، صلى أربعا','1276','33','0');
INSERT INTO [t127663] VALUES (234,'١٧٠ - مسألة: تصح الجمعة بغير سلطان في أصح الروايتين','1292','33','0');
INSERT INTO [t127663] VALUES (235,'١٧١ - مسألة','1300','33','0');
