INSERT INTO [t127663] VALUES (378,'٩٨ - مسألة: إذا دفن الميت من غير غسل نبش سواء أهيل عليه التراب أو لم يهل','1696','314','0');
INSERT INTO [t127663] VALUES (379,'٩٩ - مسألة: يكره المشي في المقبرة بنعلين','1697','314','0');
INSERT INTO [t127663] VALUES (380,'١٠٠ - مسألة: يكره الجلوس على القبر والاتكاء عليه وتوطيه','1700','314','0');
INSERT INTO [t127663] VALUES (381,'١٠١ - مسألة: وقت التعزية بعد الموت، وقبل الدفن وبعده','1702','314','0');
INSERT INTO [t127663] VALUES (382,'١٠٢ - مسألة: إذا ماتت امرأة حامل، وعسر خروج الولد، فإنه لا تشق بطنها','1703','314','0');
INSERT INTO [t127663] VALUES (383,'١٠٣ - مسألة: إذا لم يحضر أقارب المرأة، فإنه يدخلها الثقات من النساء على ما نقله الخرقي خلافا لأصحاب الشافعي رحمه الله في قولهم: لا مدخل للنساء في الدفن بحال','1705','314','0');
INSERT INTO [t127663] VALUES (384,'١٠٤ - مسألة: العدد الذي يدخله القبر غير منحصر','1706','314','0');
INSERT INTO [t127663] VALUES (385,'١٠٥ - مسألة: لا يكره البكاء بعد خروج الروح كما لا يكره قبل خروجها','1707','314','0');
INSERT INTO [t127663] VALUES (386,'١٠٦ - مسألة: إذا دفن قبل أن يصلى عليه، أخرج وصلي عليه','1708','314','0');
