INSERT INTO [t12842] VALUES (1,'أمالي ابن بشران ١٨','2','0','0');
INSERT INTO [t12842] VALUES (2,'«المؤذن يغفر له مد صوته، ويشهد له كل رطب ويابس، ولشاهد الصلاة خمس وعشرون درجة، ويكفر عنه','2','1','0');
INSERT INTO [t12842] VALUES (3,'من أشراط الساعة كلام السباع الإنس، والذي نفسي بيده، لا تقوم الساعة حتى يكلم السباع الإنس،','3','1','0');
INSERT INTO [t12842] VALUES (4,'" قرأ النجم، فسجد الناس كلهم إلا شيخا كبيرا، أخذ كفا من حصى، فقال: هذا يجزيني، فوضعه على','3','1','0');
INSERT INTO [t12842] VALUES (5,'السيئ لا يكفر السيئ، إنما يكفر الطيب السيئ»','4','1','0');
INSERT INTO [t12842] VALUES (6,'«كبرت خيانة، تحدث أخاك حديثا هو لك مصدق وأنت به كاذب»','4','1','0');
INSERT INTO [t12842] VALUES (7,'أتستغفر لأبويك، وهما مشركان؟ قال: أولم يستغفر إبراهيم لأبيه، وهو مشرك؟ فذكرت ذلك للنبي','5','1','0');
INSERT INTO [t12842] VALUES (8,'«يتعوذ من غلبة الدين»','5','1','0');
INSERT INTO [t12842] VALUES (9,'«مثل أمتي مثل المطر، لا يدرى أوله أنفع أو آخره»','6','1','0');
INSERT INTO [t12842] VALUES (10,'" إن أحدكم إذا مات عرض على مقعده بالغداة والعشي، إن كان من أهل الجنة فمن أهل الجنة، وإن كان','6','1','0');
INSERT INTO [t12842] VALUES (11,'«الدينار بالدينار، والدرهم بالدرهم، لا فضل بينهما»','6','1','0');
INSERT INTO [t12842] VALUES (12,'«مثل الذي يعتق عند الموت مثل الذي يهدي إذا شبع» وجدت في كتابة والدي محمد بن عبد الله، حدثني','7','1','0');
INSERT INTO [t12842] VALUES (13,'" لا يتمنين أحدكم الموت لضر نزل به، ولكن ليقل: اللهم أحيني ما كانت الحياة خيرا لي، وتوفني','8','1','0');
INSERT INTO [t12842] VALUES (14,'«لأن أمشي مع أخ لي في حاجة أحب إلي من أن أعتكف شهرا في مسجدي هذا، ومن مشى مع أخيه المسلم','8','1','0');
INSERT INTO [t12842] VALUES (15,'للأنبياء منابر من ذهب، فيجلسون عليها، ويبقى منبري لا أجلس عليه، أو قال: لا أقعد عليه،','8','1','0');
INSERT INTO [t12842] VALUES (16,'إذا استيقظ من الليل، قال: «لا إله إلا الله، سبحانك اللهم إني أستغفرك لذنبي، وأسألك رحمتك،','9','1','0');
INSERT INTO [t12842] VALUES (17,'" النادم ينتظر الرحمة، والمصر ينتظر المقت، وكل عامل سيقدم على ما أسلف عند موته، وملاك العمل','10','1','0');
INSERT INTO [t12842] VALUES (18,'" أول ما يقال للعبد يوم القيامة: ألم أصح جسمك؟ وأروك من الماء البارد؟ "','10','1','0');
INSERT INTO [t12842] VALUES (19,'" الإيمان بضع وستون، أو: بضع وسبعون، شعبة، أعلاها شهادة أن لا إله إلا الله، وأدناها إماطة','11','1','0');
INSERT INTO [t12842] VALUES (20,'«ألا أدلكم على ما يمحو الله به الخطايا، ويرفع به الدرجات؟» ، قالوا: بلى يا رسول الله، قال:','11','1','0');
INSERT INTO [t12842] VALUES (21,'«من ابتلي فصبر، وأعطي فشكر، وظلم فاستغفر، وظلم فغفر» ، ثم سكت، فقيل: ما له يا رسول الله؟','11','1','0');
INSERT INTO [t12842] VALUES (22,'«رحم الله رجلا قام من الليل فصلى، وأيقظ امرأته فصلت، فإن أبت نضح في وجهها الماء، رحم الله','11','1','0');
INSERT INTO [t12842] VALUES (23,'" تعلموا العلم، فإن تعلمه لله خشية، وطلبه عبادة، ومذاكرته تسبيح، والبحث عنه جهاد، وتعليمه','11','1','0');
INSERT INTO [t12842] VALUES (24,'أتي رسول الله صلى الله عليه وسلم ليلة أسري به بقدحين من خمر، ولبن، فنظر إليهما، ثم أخذ','13','1','0');
INSERT INTO [t12842] VALUES (25,'" إذا أردت أن تنام وأخذت مضجعك، فقل: هدأت العيون، وغارت النجوم، وأنت الحي القيوم "، قال:','13','1','0');
INSERT INTO [t12842] VALUES (26,'«ما أكرم شاب شيخا لسنه، إلا قيض الله له عند سنه من يكرمه»','14','1','0');
INSERT INTO [t12842] VALUES (27,'«من كان يؤمن بالله فلا يؤذين جاره، من كان يؤمن بالله فليقل خيرا أو ليصمت، ومن كان يؤمن','14','1','0');
INSERT INTO [t12842] VALUES (28,'«إن العفو لا يزيد العبد إلا عزا، فاعفوا يعزكم الله، وإن التواضع لا يزيد العبد إلا رفعة،','15','1','0');
INSERT INTO [t12842] VALUES (29,'ما شبع نبي الله صلى الله عليه وسلم ثلاثة أيام تباعا من خبز حنطة، حتى فارق','16','1','0');
INSERT INTO [t12842] VALUES (30,'" ليأتين على الناس زمان يكون خير المال فيه شاء، أو قال: غنم، يتبع شعف الجبال، ومواقع القطر،','16','1','0');
INSERT INTO [t12842] VALUES (31,'بعثنا رسول الله صلى الله عليه وسلم في سرية، فلما هجمنا على القوم، تقدمت أصحابي على فرسي،','16','1','0');
INSERT INTO [t12842] VALUES (32,'«ما أهدى مسلم لأخيه هدية أفضل من كلمة حكمة يزيده الله بها هدى، أو يرده بها عن','17','1','0');
INSERT INTO [t12842] VALUES (33,'يلتوي، ما يجد ما يملأ بطنه من الدقل، وما ترضون إلا ألوان الطعام، وألوان','17','1','0');
INSERT INTO [t12842] VALUES (34,'«الشهيد لا يجد ألم القتل، إلا كما يجد ألم القرصة»','18','1','0');
INSERT INTO [t12842] VALUES (35,'معلم الخير تستغفر له دواب الأرض، حتى الحوت في البحر»','18','1','0');
INSERT INTO [t12842] VALUES (36,'«ما أبعد هديكم من هدي نبيكم صلى الله عليه وسلم، أما هو فكان أزهد الناس في الدنيا، وأنتم','19','1','0');
INSERT INTO [t12842] VALUES (37,'«ما أخرج رسول الله صلى الله عليه وسلم ركبتيه بين يدي جليس له قط، ولا تناول يد أحد قط','19','1','0');
INSERT INTO [t12842] VALUES (38,'«فضل صلاة أحدكم في جماعة على صلاة الرجل وحده سبعا وعشرين»','20','1','0');
INSERT INTO [t12842] VALUES (39,'فطلع أبو بكر، وعمر رضي الله عنهما، فقال رسول الله صلى الله عليه وسلم: «الحمد لله الذي','20','1','0');
INSERT INTO [t12842] VALUES (40,'«كان، بأبي هو وأمي، طويل الصمت، دائم الفكرة، متواتر الأحزان، إذا تكلم تكلم بجوامع الكلام،','21','1','0');
INSERT INTO [t12842] VALUES (41,'«المستشار مؤتمن، والمسلم أخو المسلم لا يغتابه، ولا يخونه»','21','1','0');
INSERT INTO [t12842] VALUES (42,'" جاء مشركو قريش إلى النبي صلى الله عليه وسلم، يخاصمونه في القدر، فأنزل الله: {يوم يسحبون','22','1','0');
INSERT INTO [t12842] VALUES (43,'«إن للعبد المؤمن في الجنة لخيمة من لؤلؤة طولها ثلاثون ميلا، للمؤمن فيها أهلون يطوف عليهن','22','1','0');
INSERT INTO [t12842] VALUES (44,'إن شهدت أن لا إله إلا الله وأنك رسول الله وصليت الصلوات الخمس وأديت الزكاة وصمت رمضان','22','1','0');
INSERT INTO [t12842] VALUES (45,'انشق القمر على عهد رسول الله صلى الله عليه وسلم، فقال رسول الله صلى الله عليه وسلم:','23','1','0');
INSERT INTO [t12842] VALUES (46,'«من استعملناه منكم على عمل، فكتمنا مخيطا، فما فوقه فإنه غل يأتي به يوم القيامة» ، فقام رجل','23','1','0');
INSERT INTO [t12842] VALUES (47,'أما تعلمين ما كان يلقى رسول الله صلى الله عليه وسلم؟ فجعل يذكرها بأشياء من ثيابه حتى بكت،','24','1','0');
INSERT INTO [t12842] VALUES (48,'من قال: «لا إله إلا الله وحده لا شريك له، له الملك وله الحمد، وهو على كل شيء قدير، كان له','24','1','0');
INSERT INTO [t12842] VALUES (49,'أمالي ابن بشران ١٩','26','0','0');
INSERT INTO [t12842] VALUES (50,'«اللهم أعوذ بك من عذاب النار، ومن عذاب القبر، ومن فتنة المحيا والممات، ومن شر المسيح','26','49','0');
INSERT INTO [t12842] VALUES (51,'«أوصاني أن أنظر إلى من هو دوني ولا أنظر إلى من هو فوقي، وأوصاني بحب المساكين والدنو منهم،','27','49','0');
INSERT INTO [t12842] VALUES (52,'" من قال: سبحان الله كتب الله له عشرا، ومن قالها عشر مرات كتب الله له بها مائة، ومن استغفر','28','49','0');
INSERT INTO [t12842] VALUES (53,'«من أصبح وهمه غير الله عز وجل فليس من الله في شيء، ومن لم يهتم للمسلمين فليس منهم» ، هذا','29','49','0');
INSERT INTO [t12842] VALUES (54,'من تبعك على هذا الأمر؟ فقال: «حر وعبد» ، قال: قلت: ما الإسلام؟ قال: «طيب الكلام» ، قال: قلت: ما','29','49','0');
INSERT INTO [t12842] VALUES (55,'" إذا دخل أهل الجنة الجنة، وأهل النار النار، قيل لي: يا محمد، اشفع، فأخرج منها من أحببت "،','31','49','0');
INSERT INTO [t12842] VALUES (56,'ثلاثة: أهله، وماله، وعمله، فيرجع اثنان ويبقى واحد، يرجع أهله وماله، ويبقى عمله "، هذا حديث','31','49','0');
INSERT INTO [t12842] VALUES (57,'«إنا نقتدي ولا نبتدى، ونتبع ولا نبتدع، وإن أفضل ما تمسكنا بالأثر» ، هذا حديث صحيح من كلام','32','49','0');
INSERT INTO [t12842] VALUES (58,'" ما من يوم طلعت شمسه إلا وكل بجنبتيها ملكان، يناديان نداء يسمعه خلق الله كلهم غير الثقلين:','32','49','0');
INSERT INTO [t12842] VALUES (59,'«خير يوم طلعت فيه شمس يوم الجمعة، فيه خلق آدم عليه السلام، وفيه أهبط، وفيه تيب عليه، وفيه','33','49','0');
INSERT INTO [t12842] VALUES (60,'«اللهم اجعل خير عمري آخره، وخير عملي خواتمه، وخير أيامي يوم ألقاك» أنشدنا أبو علي محمد بن','34','49','0');
INSERT INTO [t12842] VALUES (61,'«يخرج قوم من أمتي يمرقون من الدين مروق السهم من الرمية، يقتلهم علي بن أبي طالب» ، ثلاث مرات،','36','49','0');
INSERT INTO [t12842] VALUES (62,'«ليس من المسلمين رجلان تواجها بسيفيهما فقتل أحدهما الآخر، إلا كانا في','37','49','0');
INSERT INTO [t12842] VALUES (63,'«لا يزني حين يزني وهو مؤمن، ولا يسرق حين يسرق وهو مؤمن، ولا يشرب الخمر حين يشربها وهو','37','49','0');
INSERT INTO [t12842] VALUES (64,'" كيف أنعم؟ أو قال: كيف أنتم؟ شك أبو طالب , وصاحب، قالا: الصور قد التقم القرن بفيه، وأصغى','38','49','0');
INSERT INTO [t12842] VALUES (65,'" أيما رجل مسلم كسب من حلال، فأطعم نفسه أو كساها فمن دونه من خلق الله عز وجل إلا كانت له','38','49','0');
INSERT INTO [t12842] VALUES (66,'«يسع كل مؤمن حتى يكون منتهاه إلى الجنة»','38','49','0');
INSERT INTO [t12842] VALUES (67,'من البيان سحرا، وإن من الشعر لحكمة»','39','49','0');
INSERT INTO [t12842] VALUES (68,'" تقتلون اليهود حتى يختبئ أحدهم وراء الحجر، فيقول الحجر: يا عبد الله، يا مسلم، هذا يهودي','39','49','0');
INSERT INTO [t12842] VALUES (69,'«وقع بين علي، وعثمان رضي الله عنهما كلام فيه غلظة أول النهار، فلما كان وسط النهار أصلحت','39','49','0');
INSERT INTO [t12842] VALUES (70,'يتعوذ من عذاب القبر وعذاب جهنم وفتنة الدجال»','39','49','0');
INSERT INTO [t12842] VALUES (71,'«ألا أعلمك كلمة؟» ، قال هاشم: ألا أدلك على كلمة، " من كنز الجنة من تحت العرش , لا قوة إلا','40','49','0');
INSERT INTO [t12842] VALUES (72,'رجل لا يتم ركوعه ولا سجوده، فلما انصرف، قال له: «منذ كم صليت هذه الصلاة؟» ، فذكر مدة، فقال:','40','49','0');
INSERT INTO [t12842] VALUES (73,'«ألا إنكم ولاة هذا الأمر من بعدي، فلا أعرفن ما شققتم على أمتي من بعدي، اللهم من شق على','40','49','0');
INSERT INTO [t12842] VALUES (74,'«إن للشهيد في سبيل الله عز وجل ست خصال» ، قال: وما هن يا رسول الله؟ قال: «تغفر له كل خطيئة','41','49','0');
INSERT INTO [t12842] VALUES (75,'«موت الغريب شهادة»','42','49','0');
INSERT INTO [t12842] VALUES (76,'«من قدم ثلاثة من ولده لم يبلغوا الحنث أدخله الله بفضل رحمته إياهم»','43','49','0');
INSERT INTO [t12842] VALUES (77,'" ست من كن فيه فقد بلغ حقيقة الإيمان: ضرب أعداء الله بالسيف، وابتكار الصلاة في اليوم الدجن،','43','49','0');
INSERT INTO [t12842] VALUES (78,'«الإيمان إقرار باللسان وعمل بالأركان ويقين بالقلب»','44','49','0');
INSERT INTO [t12842] VALUES (79,'«إنه لا ينبغي لامرئ مسلم يشهد مقاما فيه حق إلا تكلم به، فإنه لن يقدم أجله، ولن يحرم رزقا','44','49','0');
INSERT INTO [t12842] VALUES (80,'" أنزلت علي آنفا سورة، فقرأ: بسم الله الرحمن الرحيم {إنا أعطيناك الكوثر} [الكوثر: ١] ، {فصل','45','49','0');
INSERT INTO [t12842] VALUES (81,'«قد كان فيمن خلا قبلكم من الأمم ناس محدثون، فإن يكن في أمتي أحد فهو عمر بن الخطاب» ، قال ابن','45','49','0');
INSERT INTO [t12842] VALUES (82,'" كم صلى أمير المؤمنين؟ فقالوا: أربعا، قال: فصلى أربعا، قال: فقلنا: ألم تحدثنا أن النبي صلى','46','49','0');
INSERT INTO [t12842] VALUES (83,'أيصلي الرجل منا في الثوب الواحد؟ قال: «أوكلكم يجد ثوبين»','47','49','0');
INSERT INTO [t12842] VALUES (84,'«الخيل لرجل أجر، ولرجل ستر، وعلى رجل وزر، فأما الذي هي لرجل أجر فرجل ربطها في سبيل الله','47','49','0');
INSERT INTO [t12842] VALUES (85,'الحمر، فقال: " لم ينزل فيها شيء إلا الآية الجامعة الفاذة: {فمن يعمل مثقال ذرة خيرا يره {٧}','48','49','0');
INSERT INTO [t12842] VALUES (86,'«إني تارك فيكم الثقلين كتاب الله عز وجل وعترتي» ؟ قال: نعم','48','49','0');
INSERT INTO [t12842] VALUES (87,'«لكل نبي دعوة مستجابة، وإني وضعت دعوتي شفاعة لأمتي يوم القيامة»','48','49','0');
INSERT INTO [t12842] VALUES (88,'ما أصبح عند آل محمد صاع حب، ولا صاع تمر» ، وإن له يومئذ لتسع نسوة، ولقد رهن درعا له عند','49','49','0');
INSERT INTO [t12842] VALUES (89,'أمالي ابن بشران ٢٠','51','0','0');
INSERT INTO [t12842] VALUES (90,'دعا الحلاق فحلق رأسه، فدفعه إلى أبي طلحة، ثم حلق الشق الآخر، فقال: «اقسمه بين الناس» . هذا','51','89','0');
INSERT INTO [t12842] VALUES (91,'يخطب إلى جذع، فحن الجذع، فاحتضنه، وقال: " لولا أني احتضنته لحن إلى يوم','51','89','0');
INSERT INTO [t12842] VALUES (92,'ما ذئبان جائعان في حظيرة وثيقة يأكلان ويفرسان بأسرع من حب الشرف وحب المال في دين المرء','52','89','0');
INSERT INTO [t12842] VALUES (93,'أي الأعمال أفضل عند الله؟ قال رسول الله صلى الله عليه وسلم: إيمان بالله، وجهاد في سبيله،','53','89','0');
INSERT INTO [t12842] VALUES (94,'لما أنزل: {إذا جاء نصر الله والفتح} [النصر: ١] إلى آخرها، ما رأيت رسول الله صلى الله عليه','53','89','0');
INSERT INTO [t12842] VALUES (95,'لا ينكح المحرم ولا يخطب ولا ينكح','54','89','0');
INSERT INTO [t12842] VALUES (96,'إن الله أمر يحيى بن زكريا بخمس كلمات أن يعمل بهن، وأن يأمر بني إسرائيل أن يعملوا بهن،','54','89','0');
INSERT INTO [t12842] VALUES (97,'لما أمر النبي صلى الله عليه وسلم أبا بكر يصلي بالناس، وجد النبي صلى الله عليه وسلم من','56','89','0');
INSERT INTO [t12842] VALUES (98,'من كان منكم مصليا يوم الجمعة، فليصل بعدها أربعا','57','89','0');
INSERT INTO [t12842] VALUES (99,'إنما الماء من الماء.','58','89','0');
INSERT INTO [t12842] VALUES (100,'إن الله حبس عن أهل مكة الفيل، وسلط عليهم رسوله والمؤمنين، ألا إن مكة حرام على من كان','58','89','0');
INSERT INTO [t12842] VALUES (101,'والحسن والحسين على ظهره، وهو يمشي على أربع وهو يقول: نعم الحمل حملكما , ونعم العدلان','59','89','0');
INSERT INTO [t12842] VALUES (102,'إنما أنا بشر، وإني اشترطت على ربي عز وجل أي عبد من المسلمين سببته أو شتمته أن يكون له','59','89','0');
INSERT INTO [t12842] VALUES (103,'من داين الناس بدين يعلم الله منه أنه حريص على أدائه كان معه من الله عون وحافظ، فأنا ألتمس','60','89','0');
INSERT INTO [t12842] VALUES (104,'من خرج من بيته يطلب بابا من العلم ليرد به باطلا إلى الحق وضالا إلى الهدى كان عمله كعبادة','60','89','0');
INSERT INTO [t12842] VALUES (105,'من نزل منزلا، فقال: أعوذ بكلمات الله التامات من شر ما خلق وقي شر ذلك المنزل حتى يظعن','61','89','0');
INSERT INTO [t12842] VALUES (106,'ما زال جبريل يوصيني بالجار حتى ظننت أنه يورثه','61','89','0');
INSERT INTO [t12842] VALUES (107,'المؤمن غر كريم , والفاجر خب لئيم','61','89','0');
INSERT INTO [t12842] VALUES (108,'ليس من عمل إلا وهو يختم عليه، فإذا مرض المؤمن، قالت الملائكة: يا ربنا عبدك فلان قد','62','89','0');
INSERT INTO [t12842] VALUES (109,'ليصل أحدكم، فإذا كسل أو فتر فليقعد','62','89','0');
INSERT INTO [t12842] VALUES (110,'من ظن منكم أن لا يستيقظ فليوتر أوله، ومن ظن منكم أن يستيقظ فليوتر آخره، فإن صلاة آخر','62','89','0');
INSERT INTO [t12842] VALUES (111,'من كذب علي متعمدا ليحل حراما ويحرم حلالا أو يضل الناس بغير علم فليتبوأ مقعده من','63','89','0');
INSERT INTO [t12842] VALUES (112,'دخل يوم فتح مكة وعليه عمامة سوداء أخبرنا أبو الحسن أحمد بن إسحاق بن بنجاب، ثنا أحمد بن','63','89','0');
INSERT INTO [t12842] VALUES (113,'عذاب القبر لحق، قالت: فما سمعته بعد ذلك صلى صلاة إلا تعوذ من عذاب القبر. هذا حديث صحيح من','64','89','0');
INSERT INTO [t12842] VALUES (114,'القتيل في سبيل الله شهيد، والمرابط يموت على فراشه في سبيل الله شهيد، والمبطون شهيد،','64','89','0');
INSERT INTO [t12842] VALUES (115,'أهل الدرجات العلا لينظر إليهم من أسفل منهم كما ينظر أحدكم إلى الكوكب الدري الغابر في أفق','65','89','0');
INSERT INTO [t12842] VALUES (116,'إذا اغتسل الرجل يوم الجمعة ومس طيبا وأنصت ولم يلغ حتى يقضي الإمام خطبته ويركع شيئا إن بدا','65','89','0');
INSERT INTO [t12842] VALUES (117,'لأن أصلي الصبح في جماعة أحب إلي من أن أصلي ليلة حتى أصبح','66','89','0');
INSERT INTO [t12842] VALUES (118,'إذا كنتم ثلاثة فلا يتناج اثنان دون الثالث، ولا يقم أحد واحدا من مجلسه ثم','66','89','0');
INSERT INTO [t12842] VALUES (119,'الدواوين عند الله ثلاثة: ديوان لا يعبأ الله به شيئا، وديوان لا يترك الله منه شيئا، وديوان','66','89','0');
INSERT INTO [t12842] VALUES (120,'إن الله تجاوز لأمتي عما حدثت به أنفسها، ما لم ينطق به لسان أو تعمل به','67','89','0');
INSERT INTO [t12842] VALUES (121,'بأرضنا أعنابا نعتصرها فنشرب منها، قال: لا.','67','89','0');
INSERT INTO [t12842] VALUES (122,'قد أتى علينا زمان لسنا نقضي ولسنا هنالك، ثم إن الله قدر أن بلغنا من الأمر ما ترون، فمن','68','89','0');
INSERT INTO [t12842] VALUES (123,'ثلاث ساعات كان رسول الله صلى الله عليه وسلم ينهانا أن نصلي فيهن أو أن نقبر فيهن موتانا:','69','89','0');
INSERT INTO [t12842] VALUES (124,'إنه ليس من أهلي أحد أحب إلى عيني بعدي منك ولا أعز علي فقدا منك، وإني كنت نحلتك من أرضي','69','89','0');
INSERT INTO [t12842] VALUES (125,'ما بين قبري ومنبري روضة من رياض الجنة','71','89','0');
INSERT INTO [t12842] VALUES (126,'اختصم عند رسول الله صلى الله عليه وسلم سعد بن أبي وقاص وعبد بن زمعة في ابن أمة زمعة، فقال','71','89','0');
INSERT INTO [t12842] VALUES (127,'يكثر، أن يقول: يا مقلب القلوب ثبت قلبي على طاعتك ودينك. فقيل له: يا رسول الله، قال عفان:','72','89','0');
INSERT INTO [t12842] VALUES (128,'من سئل عن علم يعلمه فكتمه جئ به يوم القيامة ملجما بلجام من نار','73','89','0');
INSERT INTO [t12842] VALUES (129,'فذكر علي، وجعفر، وزيد، أيهم أحق بها، وكانت خالتها عند جعفر أسماء بنت عميس وكان رسول الله','73','89','0');
INSERT INTO [t12842] VALUES (130,'جاء رجل ونحن في الصف خلف رسول الله صلى الله عليه وسلم، فدخل في الصف، قال: الله أكبر','74','89','0');
INSERT INTO [t12842] VALUES (131,'{لن تنالوا البر حتى تنفقوا مما تحبون} [آل عمران: ٩٢] : إنه ليس لي مال أحب إلي من أرضي','76','89','0');
INSERT INTO [t12842] VALUES (132,'ثلاث من كن فيه فهو منافق، وإن صام وصلى وزعم أنه مسلم: إذا حدث كذب، وإذا وعد أخلف، وإذا','76','89','0');
INSERT INTO [t12842] VALUES (133,'ما قال عبد: لا إله إلا الله مخلصا، إلا صعدت لا يردها حجاب، فإذا وصلت إلى الله نظر الله','77','89','0');
INSERT INTO [t12842] VALUES (134,'إزرة المؤمن إلى نصف الساق، فما كان إلى الكعب فلا بأس، فما تحت الكعب ففي النار، لا ينظر','77','89','0');
INSERT INTO [t12842] VALUES (135,'أيصلي الرجل في ثوب واحد؟ فقال رسول الله صلى الله عليه وسلم: أوكلكم يجد','77','89','0');
INSERT INTO [t12842] VALUES (136,'من قرأ في كل ليلة إذا وقعت الواقعة لم تصبه فاقة أبدا','78','89','0');
INSERT INTO [t12842] VALUES (137,'قال موسى عليه السلام، حين كلم ربه: أي رب، أي عبادك أحب إليك؟ قال أكثرهم لي ذكرا. قال: أي','78','89','0');
INSERT INTO [t12842] VALUES (138,'رجلا أعتق ستة مملوكين له، ليس له مال غيرهم، فأقرع النبي صلى الله عليه وسلم بينهم، فأعتق','79','89','0');
INSERT INTO [t12842] VALUES (139,'إذا كبر في الصلاة سكت بين التكبير والقراءة، فقلت له: بأبي أنت وأمي أرأيت سكاتك بين','79','89','0');
INSERT INTO [t12842] VALUES (140,'ما مررت بحجر ولا مدر إلا استغفر لك، يا قبيصة، إذا صليت الفجر، فقل ثلاثا: سبحان الله','80','89','0');
INSERT INTO [t12842] VALUES (141,'اللهم منزل الكتاب سريع الحساب اهزم الأحزاب وزلزلهم','80','89','0');
INSERT INTO [t12842] VALUES (142,'أوصيك بتقوى الله والتكبير على كل شرف.','81','89','0');
INSERT INTO [t12842] VALUES (143,'الله اختارني واختار لي أصحابا، فجعل لي منهم وزراء، وأنصارا، وأصهارا، فمن سبهم فعليه لعنة','81','89','0');
INSERT INTO [t12842] VALUES (144,'من قال: أشهد أن الله هو الحق المبين، وأن الله على كل شيء قدير، وأشهد أن الساعة آتية لا','82','89','0');
INSERT INTO [t12842] VALUES (145,'أمالي ابن بشران ٢١','84','0','0');
INSERT INTO [t12842] VALUES (146,'«ما نزلت سورة من القرآن إلا وأنا أعلم أين نزلت؟ وما نزلت آية من القرآن إلا وأنا أعلم أين','84','145','0');
INSERT INTO [t12842] VALUES (147,'بياض خدي رسول الله صلى الله عليه وسلم وهو يسلم عن يمينه وعن يساره»','85','145','0');
INSERT INTO [t12842] VALUES (148,'يخطب إلى جذع , فحن الجذع فاحتضنه، وقال: «لولا أني احتضنته لحن إلى يوم','85','145','0');
INSERT INTO [t12842] VALUES (149,'«لا يصيب المؤمن من نصب ولا وصب ولا أذى حتى الهم يهمه إلا كفر الله به','86','145','0');
INSERT INTO [t12842] VALUES (150,'" قاربوا وسددوا فإنه لن ينجي أحدا منكم عمله، قلنا: يا رسول الله ولا أنت؟ قال: ولا أنا إلا','86','145','0');
INSERT INTO [t12842] VALUES (151,'«الرؤيا من الله، والحلم من الشيطان , فإذا رأى أحدكم شيئا يكرهه فليتفل عن شماله ثلاث مرات،','86','145','0');
INSERT INTO [t12842] VALUES (152,'أهدي إلى رسول الله صلى الله عليه وسلم ثوب إبريسيم أحسبه أنه حرير، فقال رسول الله صلى الله','87','145','0');
INSERT INTO [t12842] VALUES (153,'«إن الله خير عبدا بين الدنيا وبين ما عنده، فاختار ذلك العبد ما عند','87','145','0');
INSERT INTO [t12842] VALUES (154,'«إن ظل المؤمن يوم القيامة صدقته»','88','145','0');
INSERT INTO [t12842] VALUES (155,'رأى جنازة , فلما رآها قام» ، قال أبان وحدثني عثمان أن رسول الله صلى الله عليه وسلم كان يفعل','88','145','0');
INSERT INTO [t12842] VALUES (156,'عن الوصال، فقيل: يا رسول الله إنك تواصل. قال: إني أبيت وربي يطعمني','89','145','0');
INSERT INTO [t12842] VALUES (157,'«ضالة المسلم حرق النار»','89','145','0');
INSERT INTO [t12842] VALUES (158,'يصلي جالسا , وإذا أراد الركوع قام فقرأ قدر عشر آيات أو ما شاء الله ثم','90','145','0');
INSERT INTO [t12842] VALUES (159,'" إذا وضعت الجنازة فحملها الرجال على أعناقهم , فإن كانت صالحة قالت: قدموني','90','145','0');
INSERT INTO [t12842] VALUES (160,'" لن ينجي أحدا منكم عمله.','91','145','0');
INSERT INTO [t12842] VALUES (161,'كنت غلاما أسعى مع الغلمان , فإذا أنا بنبي الله صلى الله عليه وسلم خلفي مقبلا، قلت: ما جاء','91','145','0');
INSERT INTO [t12842] VALUES (162,'ما الإسلام؟ قال: " تعبد الله لا تشرك به شيئا، وتصلي الصلاة المكتوبة , وتؤتي الزكاة المفروضة','92','145','0');
INSERT INTO [t12842] VALUES (163,'أي الناس أفضل؟ فقال: «مؤمن يجاهد بماله ونفسه في سبيل الله» .','93','145','0');
INSERT INTO [t12842] VALUES (164,'هذا الوباء رجز وبقية عذاب , عذب به من قبلكم، إذا وقع بأرض وأنتم بها فلا تخرجوا منها، وإذا','94','145','0');
INSERT INTO [t12842] VALUES (165,'أرني الخاتم الذي بين كتفيك حتى أداويك، فإني من أطب العرب , فقال رسول الله صلى الله عليه','94','145','0');
INSERT INTO [t12842] VALUES (166,'«أتيت رسول الله صلى الله عليه وسلم أبايعه , فاشترط علي النصح لكل مسلم، فإني لكم','95','145','0');
INSERT INTO [t12842] VALUES (167,'«من استعمل عاملا على باب قوم، وفي تلك العصابة من هو أرضى لله منه، فقد خان الله وخان رسوله','95','145','0');
INSERT INTO [t12842] VALUES (168,'ما يحل للرجل من مال أخيه؟ قال: «يأكل حتى يشبع إذا كان جائعا، ويشرب حتى','95','145','0');
INSERT INTO [t12842] VALUES (169,'«من قال علي ما لم أقل فليتبوأ مقعده من النار»','96','145','0');
INSERT INTO [t12842] VALUES (170,'«نحن الآخرون الأولون يوم القيامة، إن الأكثرين هم الأقلون يوم القيامة , إلا من قال بالمال','96','145','0');
INSERT INTO [t12842] VALUES (171,'عوذ رسول الله صلى الله عليه وسلم الحسن والحسين , فقال: «أعيذكما بكلمات الله التامة من كل','96','145','0');
INSERT INTO [t12842] VALUES (172,'«لا تصوموا يوم الجمعة إلا وقبله يوما أو بعده يوما»','96','145','0');
INSERT INTO [t12842] VALUES (173,'«من غشنا فليس منا، والمكر والخديعة في النار»','97','145','0');
INSERT INTO [t12842] VALUES (174,'«لفتنة بعضكم أخوف عندي من فتنة الدجال، إنها ليس من فتنة صغيرة ولا كبيرة إلا تضع لفتنة','97','145','0');
INSERT INTO [t12842] VALUES (175,'" يؤتى بالوالي الذي كان يطاع في معصية الله , فيقذف في نار جهنم فتندلق فيها أقتابه، فيدور','97','145','0');
INSERT INTO [t12842] VALUES (176,'«اليمين في البيع منفقة للسلعة ممحقة للكسب»','98','145','0');
INSERT INTO [t12842] VALUES (177,'«إذا نظر أحدكم إلى من فضل عليه في المال والجسم، فلينظر إلى من هو دونه في المال','98','145','0');
INSERT INTO [t12842] VALUES (178,'أمالي ابن بشران ٢٢','101','0','0');
INSERT INTO [t12842] VALUES (179,'لا تسأل الإمارة، فإنك إن أعطيتها عن غير مسألة أعنت عليها، وإذا حلفت على يمين فرأيت غيرها','101','178','0');
INSERT INTO [t12842] VALUES (180,'بعثني إلى اليمن، ولو أؤمن أنه يموت لم أفارقه، قال: فبينا أنا ذات يوم إذ قال الحبر: مات','102','178','0');
INSERT INTO [t12842] VALUES (181,'" أخوف ما أتخوف عليكم ما يخرج لكم من بركات الأرض، فقال رجل ممن يسمع ذلك من النبي صلى الله','103','178','0');
INSERT INTO [t12842] VALUES (182,'" في صلاة الصبح إذا الشمس كورت، فلما أتى على هذه الآية: {والليل إذا عسعس} [التكوير: ١٧]','103','178','0');
INSERT INTO [t12842] VALUES (183,'دخلت مع رسول الله صلى الله عليه وسلم السوق، فقعد إلى البزازين فاشترى سراويل بأربعة دراهم،','104','178','0');
INSERT INTO [t12842] VALUES (184,'وبين يديه تور فيه ماء، فقال بأصابعه هكذا: «خذوا بسم الله» فجعل الماء ينحدر من بين أصابعه','105','178','0');
INSERT INTO [t12842] VALUES (185,'أهدى له ثلاث طوائر، فأطعم خادمه طيرا، فلما كان الغد أتاه به، فقال رسول الله صلى الله عليه','105','178','0');
INSERT INTO [t12842] VALUES (186,'الشيطان قد أيس أن يعبده المصلون، ولكن قد رضي بالتحريش»','106','178','0');
INSERT INTO [t12842] VALUES (187,'«من نسي صلاة فليصلها إذا ذكرها» .','107','178','0');
INSERT INTO [t12842] VALUES (188,'«لقد هممت أن أنهى عن الغيلة، حتى ذكرت أن فارس والروم يفعلون ذلك، فلا يضر أولادهم','107','178','0');
INSERT INTO [t12842] VALUES (189,'«لو كانت الدنيا تعدل عند الله شيئا ما أعطى كافرا منها شيئا»','108','178','0');
INSERT INTO [t12842] VALUES (190,'" {آمن الرسول بما أنزل إليه من ربه والمؤمنون} [البقرة: ٢٨٥] . إلى آخر الآية، قال جبريل عليه','108','178','0');
INSERT INTO [t12842] VALUES (191,'«لا عدوى، ولا طيرة، ولا هامة، ولا صفر، واتقوا المجذوم كما يتقى الأسد»','109','178','0');
INSERT INTO [t12842] VALUES (192,'«الرؤيا من الله، والحلم من الشيطان، فإذا رأى أحدكم شيئا يكرهه فليتفل عن شماله ثلاث مرات،','109','178','0');
INSERT INTO [t12842] VALUES (193,'ما من أحد أغير من الله عز وجل، أن يزني عبده، أو تزني أمته، يا أمة محمد لو تعلمون ما أعلم','110','178','0');
INSERT INTO [t12842] VALUES (194,'أتاني جبريل آنفا، فقال: يا محمد، من صلى عليك مرة، أو قال: واحدة، كتب الله تبارك وتعالى،','110','178','0');
INSERT INTO [t12842] VALUES (195,'«لعن الله اليهود حرمت عليهم الشحوم، فباعوها، وأكلوا أثمانها» أخبرنا أبو بكر محمد بن الحسين','111','178','0');
INSERT INTO [t12842] VALUES (196,'«ما أذن الله عز وجل لشيء، ما أذن لنبي حسن الصوت أن يتغنى بالقرآن» .','112','178','0');
INSERT INTO [t12842] VALUES (197,'أدنى أهل الجنة منزلة عند الله عز وجل من يتمنى على الله عز وجل، فيقول الله له: " لك ما سألت،','112','178','0');
INSERT INTO [t12842] VALUES (198,'«لا ينبغي للمؤمن أن يكون لعانا»','113','178','0');
INSERT INTO [t12842] VALUES (199,'رفع القلم عن ثلاثة: المجنون حتى يبرأ، والصبي حتى يعقل، وعن النائم حتى يستيقظ؟ " قال: بلى،','113','178','0');
INSERT INTO [t12842] VALUES (200,'«نهى أن تتبع جنازة معها رانة»','114','178','0');
INSERT INTO [t12842] VALUES (201,'أغمي على رسول الله صلى الله عليه وسلم إغماءة، فأفاق، فقال: «ائتوني بكتف ودواة، أكتب لكم','114','178','0');
INSERT INTO [t12842] VALUES (202,'«ما اسمك» ؟ قال: جمرة، قال: «ابن من؟» قال: ابن شهاب، قال: «ممن؟» قال: من الحرقة، قال: «أين مسكنك؟» ،','115','178','0');
INSERT INTO [t12842] VALUES (203,'«من لا يسأله عز وجل، يغضب عليه»','115','178','0');
INSERT INTO [t12842] VALUES (204,'«التسبيح للرجال والتصفيق للنساء»','116','178','0');
INSERT INTO [t12842] VALUES (205,'" إذا كان يوم القيامة نادى مناد: أين الظلمة، وأعوان الظلمة، وأشباه الظلمة، حتى من برى لهم','116','178','0');
INSERT INTO [t12842] VALUES (206,'المستشار مؤتمن "','116','178','0');
INSERT INTO [t12842] VALUES (207,'«عليكم بالحزن فإنه مفتاح القلب» قالوا: يا رسول الله وكيف الحزن؟ قال: «أحزنوا أنفسكم بالجوع','117','178','0');
INSERT INTO [t12842] VALUES (208,'«من سمع رجلا ينشد ضالة في المسجد فليقل لا أداها الله إليك، فإن المساجد لم تبن','118','178','0');
INSERT INTO [t12842] VALUES (209,': {بسم الله الرحمن الرحيم} [الفاتحة: ١] . الآية السابعة،','118','178','0');
INSERT INTO [t12842] VALUES (210,'ناسا من أهل الشرك قتلوا فأكثروا، وزنوا فأكثروا، وأتوا النبي صلى الله عليه وسلم، فقالوا:','119','178','0');
INSERT INTO [t12842] VALUES (211,'صلاة كنا نصليها مع رسول الله صلى الله عليه وسلم، إما نسيناها وإما تركناها عمدا، يكبر كلما','119','178','0');
INSERT INTO [t12842] VALUES (212,'" {فإما نذهبن بك فإنا منهم منتقمون {٤١} أو نرينك الذي وعدناهم فإنا عليهم مقتدرون} [الزخرف:','119','178','0');
INSERT INTO [t12842] VALUES (213,'نظر إلى أبي بكر وعمر، فقال: «هذان سيدا كهول أهل الجنة من الأولين والآخرين، إلا النبيين','120','178','0');
INSERT INTO [t12842] VALUES (214,'فلم يستيقظ أحد منهم، فضربتهم الشمس، فكان رسول الله صلى الله عليه وسلم، أولهم استيقاظا،','120','178','0');
INSERT INTO [t12842] VALUES (215,'«ما أكرم شاب شيخا، إلا قيض الله له من يكرمه عند سنه»','121','178','0');
INSERT INTO [t12842] VALUES (216,'" كيف كانت صلاة رسول الله صلى الله عليه وسلم في شهر رمضان؟ قالت: ما كان رسول الله صلى الله','121','178','0');
INSERT INTO [t12842] VALUES (217,'مزبلة، فاحتبس عندها، فكان أصحابه تأذوا بها، وقال: هذه دنياكم التي تحرصون عليها أخبرنا أبو','122','178','0');
INSERT INTO [t12842] VALUES (218,'لا يتوضأ رجل، فيحسن الوضوء، إلا غفر له ما بينه وبين الصلاة الأخرى، حتى يصليها» . أنا سمعته،','123','178','0');
INSERT INTO [t12842] VALUES (219,'أم ولد لرجل شتمت النبي صلى الله عليه وسلم، فسمعها، فقتلها، فنادى منادي النبي صلى الله','123','178','0');
INSERT INTO [t12842] VALUES (220,'«خيركم من أطعم الطعام»','124','178','0');
INSERT INTO [t12842] VALUES (221,'" لا نورث يعني نفسه ما تركناه فهو صدقة؟ قالوا: نعم، قال: فقال للعباس، وعلي، رضي الله عنهما،','124','178','0');
INSERT INTO [t12842] VALUES (222,'ما أعلم في الأرض مائة مؤمن، فنظر بعضنا إلى بعض، فقلنا: أما في شام الأرض وعراقها مائة','125','178','0');
INSERT INTO [t12842] VALUES (223,'«من حلف بغير الله فقد أشرك»','125','178','0');
INSERT INTO [t12842] VALUES (224,'«أقرب ما يكون العبد من ربه عز وجل وهو ساجد، فأكثروا الدعاء»','126','178','0');
INSERT INTO [t12842] VALUES (225,'الإسلام بدأ غريبا، وسيعود كما بدأ، فطوبى للغرباء» قيل: ومن الغرباء؟ قال: «النزاع من','126','178','0');
INSERT INTO [t12842] VALUES (226,'«من مات لا يشرك بالله عز وجل شيئا دخل الجنة»','127','178','0');
INSERT INTO [t12842] VALUES (227,'«ما من أحد يصلي هذه الصلوات الخمس في وقتهن، يحسن وضوءها، وركوعها، وسجودها، يموت غير مرتاب،','127','178','0');
INSERT INTO [t12842] VALUES (228,'أمالي ابن بشران ٢٣','129','0','0');
INSERT INTO [t12842] VALUES (229,'بايع رسول الله صلى الله عليه وسلم تحت الشجرة» , فلما خرجنا من العام المقبل نسيناها فلم','129','228','0');
INSERT INTO [t12842] VALUES (230,'«لا يدخل الجنة قتات»','130','228','0');
INSERT INTO [t12842] VALUES (231,'فتحول عن الخشبة إلى المنبر , فلما تحول عنها حنت الخشبة حنين الولد , قال: فقال أنس: والله','130','228','0');
INSERT INTO [t12842] VALUES (232,'«تصدقوا تصدقوا؛ فوالذي نفسي بيده ليأتين على الناس زمان يجيء الرجل بصدقته فلا يجد من','130','228','0');
INSERT INTO [t12842] VALUES (233,'صليت مع النبي صلى الله عليه وسلم وأبي بكر وعمر فقال له رجل: وعثمان , فسكت فلم يجهروا','131','228','0');
INSERT INTO [t12842] VALUES (234,'لي أخا قد حبب إليه قل هو الله أحد.','131','228','0');
INSERT INTO [t12842] VALUES (235,'«يضمن الله عز وجل لمن يخرج في سبيله أن يدخله الجنة أو يرده إلى منزله نائلا ما نال من أجر','132','228','0');
INSERT INTO [t12842] VALUES (236,'«سافروا تصحوا وتغنموا»','132','228','0');
INSERT INTO [t12842] VALUES (237,'جاء الحسن بن علي رضي الله عنهما حتى صعد على المنبر , فقال رسول الله صلى الله عليه وسلم: "','132','228','0');
INSERT INTO [t12842] VALUES (238,'«السخاء شجرة من أشجار الجنة لها أغصان متدلية في الدنيا , فمن كان سخيا تعلق بغصن من أغصانها','133','228','0');
INSERT INTO [t12842] VALUES (239,'«خلق الله عز وجل كل صانع وصنعته» أخبرنا أبو العباس أحمد بن إبراهيم بن علي الكندي , بمكة','133','228','0');
INSERT INTO [t12842] VALUES (240,'«إذا أقيمت الصلاة فلا صلاة إلا المكتوبة» .','134','228','0');
INSERT INTO [t12842] VALUES (241,'أصابه ذات يوم جوع، فوضع حجرا على بطنه , ثم قال: «ألا يا رب نفس طاعمة ناعمة في الدنيا جائعة','134','228','0');
INSERT INTO [t12842] VALUES (242,'«لا يقطع الخائن ولا المختلس ولا المنتهب»','135','228','0');
INSERT INTO [t12842] VALUES (243,'«إذا فعلت أمتي خمس عشرة خصلة حل بها البلاء» .','135','228','0');
INSERT INTO [t12842] VALUES (244,'«اللهم بارك لأمتي في بكورها وفي يوم خميسها»','136','228','0');
INSERT INTO [t12842] VALUES (245,'«أطفال المسلمين في جبل في الجنة يكفلهم إبراهيم وسارة حتى يدفعوهم إلى آبائهم يوم','137','228','0');
INSERT INTO [t12842] VALUES (246,'«يحشر الناس يوم القيامة فأكون أنا وأمتي على تل، فيكسوني ربي جل وعز حلة خضراء، ثم يؤذن لي،','137','228','0');
INSERT INTO [t12842] VALUES (247,'" إذا أشهد أحدكم فليتعوذ من أربع: من عذاب جهنم , وعذاب القبر , وفتنة المحيا والممات , وفتنة','138','228','0');
INSERT INTO [t12842] VALUES (248,'«من شهد أن لا إله إلا الله وأن محمدا عبده ورسوله وأن عيسى عبد الله وكلمته ألقاها إلى مريم','138','228','0');
INSERT INTO [t12842] VALUES (249,'هذا التسليم عليك قد عرفناه , فكيف الصلاة عليك؟ قال: قولوا: «اللهم صل على محمد عبدك ورسولك','139','228','0');
INSERT INTO [t12842] VALUES (250,'«أحد هذا الجبل يحبنا ونحبه»','139','228','0');
INSERT INTO [t12842] VALUES (251,'«سرعة المشي يذهب بهاء الوجه»','140','228','0');
INSERT INTO [t12842] VALUES (252,'إذا أتي بهدية قال: «اذهبوا بهذا إلى بيت فلانة؛ فإنها كانت تحب خديجة , واذهبوا بهذا إلى بيت','140','228','0');
INSERT INTO [t12842] VALUES (253,'الغضب والحدة لا يكونان إلا في صالحي أمتي وأبرارها وأتقيائها ثم تفيء»','140','228','0');
INSERT INTO [t12842] VALUES (254,'إني أحبك يا رسول الله. قال: فقال له النبي صلى الله عليه وسلم: «المرء مع من أحب» . وكان صفوان','141','228','0');
INSERT INTO [t12842] VALUES (255,'«على اليد ما أخذت حتى تؤديه»','142','228','0');
INSERT INTO [t12842] VALUES (256,'ما كدت أن أصلي العصر حتى كادت الشمس أن تغرب. فقال رسول الله صلى الله عليه وسلم: «ما','142','228','0');
INSERT INTO [t12842] VALUES (257,'أيصلي أحدنا في الثوب الواحد؟ فقال: «أوكلكم يجد ثوبين؟» .','144','228','0');
INSERT INTO [t12842] VALUES (258,'«إياك والبدع؛ والذي نفس محمد بيده لا يبتدع رجل في الإسلام شيئا ليس منه، ألا ما خلف خير مما','145','228','0');
INSERT INTO [t12842] VALUES (259,'يوم أحد: «احفروا وأعمقوا وأوسعوا وادفنوا الاثنين والثلاثة في قبر واحد , وقدموا أكثرهم','146','228','0');
INSERT INTO [t12842] VALUES (260,'يطوف بالبيت ويقبل الحجر، ويقول: «قد علمت أنك حجر لا تضر ولا تنفع , ولكن رأيت النبي صلى','146','228','0');
INSERT INTO [t12842] VALUES (261,'تقوم الليل وتصوم النهار، فبلغ ذلك رسول الله صلى الله عليه وسلم , فقال: «لكني أصلي وأنام','147','228','0');
INSERT INTO [t12842] VALUES (262,'«إذا صلى أحدكم بالناس فليخفف؛ فإن فيهم السقيم والشيخ الكبير وذا الحاجة»','147','228','0');
INSERT INTO [t12842] VALUES (263,'فلما صلى المغرب قاموا يتنفلون , فقال النبي صلى الله عليه وسلم: «إن هذه الصلاة إنما تصلى في','147','228','0');
INSERT INTO [t12842] VALUES (264,'" إذا هم أحدكم بالأمر لو أراد الأمر فليركع ركعتين من غير الفريضة فليقل: اللهم إني أستخيرك','148','228','0');
INSERT INTO [t12842] VALUES (265,'«لا يؤمن أحدكم حتى يحب لأخيه ما يحب لنفسه» أخبرنا أبو أحمد حمزة بن محمد بن العباس , ثنا','148','228','0');
INSERT INTO [t12842] VALUES (266,'«سووا صفوفكم؛ فإني أراكم من وراء ظهري» . فلقد يتبارى الرجل بلزق منكبه بمنكب صاحبه إذا قام في','150','228','0');
INSERT INTO [t12842] VALUES (267,'إذا سافر قال: «اللهم أنت الصاحب في السفر , والخليفة في الأهل , اللهم اصحبنا في سفرنا','150','228','0');
INSERT INTO [t12842] VALUES (268,'«عليكم بالباءة , فمن لم يجد فليصم؛ فإن الصوم له وجاء»','151','228','0');
INSERT INTO [t12842] VALUES (269,'«ألا تسألوني من أي شيء ضحكت؟» قال: " عجبت من منازلة العبد ربه عز وجل , يقول: رب أليس وعدتني','151','228','0');
INSERT INTO [t12842] VALUES (270,'يدعو بهذه الدعوات كلما سلم: «اللهم لا تخزني يوم القيامة , ولا تخزني يوم البأس؛ فإن من','152','228','0');
INSERT INTO [t12842] VALUES (271,'أتيت النبي صلى الله عليه وسلم وفي عنقي صليب من ذهب , قال: فقال لي رسول الله صلى الله عليه','152','228','0');
INSERT INTO [t12842] VALUES (272,'«ما من مسلم يشاك شوكة إلا كتب الله له بها حسنة»','153','228','0');
INSERT INTO [t12842] VALUES (273,'" {وظل ممدود} [الواقعة: ٣٠] ، قال: في الجنة شجرة يسير الراكب في ظلها مائة عام لا','153','228','0');
INSERT INTO [t12842] VALUES (274,'«صلى على النجاشي وكبر عليه أربعا»','153','228','0');
INSERT INTO [t12842] VALUES (275,'«نهى عن بيع الثمر حتى يبدو صلاحه»','154','228','0');
INSERT INTO [t12842] VALUES (276,'«المدينة من صبر على شدتها ولأوائها كنت له شفيعا أو شهيدا يوم القيامة»','154','228','0');
INSERT INTO [t12842] VALUES (277,'أتاني كتابك تعزم علي إلا رفعت إليك حوائجي , وهيهات؛ رفعت حوائجي إلى مولاي , فما أعطاني','154','228','0');
INSERT INTO [t12842] VALUES (278,'أمالي ابن بشران ٢٤','156','0','0');
INSERT INTO [t12842] VALUES (279,'رجلا سمع رجلا يقرأ: قل هو الله أحد يرددها، فلما أصبح جاء إلى رسول الله صلى الله عليه','156','278','0');
INSERT INTO [t12842] VALUES (280,'" أربع من أمر الجاهلية لا تتركونهن: الطعن في الأنساب، والفخر في الأحساب، والاستسقاء','156','278','0');
INSERT INTO [t12842] VALUES (281,'«خرج رسول الله صلى الله عليه وسلم وهو في سفر إلى الغائط، فاتبعته بإداوة فصببت عليه الوضوء،','157','278','0');
INSERT INTO [t12842] VALUES (282,'«إذا صلى أحدكم وبين يديه ما يستره فأراد أحد أن يمر بين يديه فليدفعه، فإن أبى','157','278','0');
INSERT INTO [t12842] VALUES (283,'وصية نوح عليه السلام ابنه، قال: " يا بني أوصيك باثنتين، وأنهاك عن اثنتين عن الشرك والكفر؛','157','278','0');
INSERT INTO [t12842] VALUES (284,'يرفع يديه مدا إذا دخل في الصلاة، ويكبر كلما ركع ورفع، والسكوت قبل القراءة يسأل الله عز','159','278','0');
INSERT INTO [t12842] VALUES (285,'يلتوي ما يجد ما يملأ بطنه من الدقل، وما ترضون إلا ألوان الطعام وألوان','159','278','0');
INSERT INTO [t12842] VALUES (286,'«كل يمين تحلف دون الله عز وجل فهو شرك» .','160','278','0');
INSERT INTO [t12842] VALUES (287,'سيكون بعدي أمراء فمن صدقهم بكذبهم وأعانهم على ظلمهم فليس مني ولست منه، ولن يرد على الحوض،','160','278','0');
INSERT INTO [t12842] VALUES (288,'«ما من غازية تغزوا في سبيل الله عز وجل فيصيبون غنيمة إلا تعجلوا ثلثي أجرهم من الآخرة ويبقى','161','278','0');
INSERT INTO [t12842] VALUES (289,'" مر ورقة بن نوفل على بلال وهو يعذب برمضاء مكة يقول: أحد أحد. فقال ورقة: أحد أحد والله يا','161','278','0');
INSERT INTO [t12842] VALUES (290,'«من كظم غيظا وهو يقدر على أن ينفذه دعاه الله فيخيره من أي الحور شاء»','162','278','0');
INSERT INTO [t12842] VALUES (291,'«لا تشربوا في آنية الذهب ولا الفضة؛ فإنها لهم في الدنيا ولكم في الآخرة»','163','278','0');
INSERT INTO [t12842] VALUES (292,'«تزوجت بعد أبيك يا جابر؟» قلت: نعم. قال: «أبكرا أم ثيبا؟» قلت: ثيبا، قال: «فهلا بكرا تضاحكها','163','278','0');
INSERT INTO [t12842] VALUES (293,'«خرجنا مع رسول الله صلى الله عليه وسلم في سفر فكنا إذا علونا على شرف كبرنا وإذا هبطنا','164','278','0');
INSERT INTO [t12842] VALUES (294,'«إزار المؤمن أو المسلم إلى أنصاف الساقين , ما بينه وبين الكعبين وما أسفل من ذلك ففي النار،','164','278','0');
INSERT INTO [t12842] VALUES (295,'«لا يتطهر الرجل في بيته ثم يخرج لا يريد إلا الصلاة إلا كان في صلاة حتى يقضي صلاته، ولا','165','278','0');
INSERT INTO [t12842] VALUES (296,'ما منكم من أحد يؤمن بالله ثم يسدد إلا سلك به في الجنة. ولقد وعدني ربي جل وعز أن يدخل','166','278','0');
INSERT INTO [t12842] VALUES (297,'«تسموا باسمي، ولا تكنوا بكنيتي»','167','278','0');
INSERT INTO [t12842] VALUES (298,'«الأرواح جنود مجندة، فما تعارف منها ائتلف، وما تناكر منها اختلف»','167','278','0');
INSERT INTO [t12842] VALUES (299,'«إذا راح أحدكم إلى الجمعة فليغتسل»','167','278','0');
INSERT INTO [t12842] VALUES (300,'دعا بماء في إناء، وأفرغ من يمينه على شماله، فغسل كفيه ثلاثا، ثم مضمض ثلاثا، ثم استنشق','167','278','0');
INSERT INTO [t12842] VALUES (301,'" سمعت صوت حصيات وقعن من السماء يوم بدر كأنهن وقعن في طست، قالت: فلما اصطف الناس أخذهن رسول','168','278','0');
INSERT INTO [t12842] VALUES (302,'وضع الله الحرج عن رجل، إلا رجلا اقترض امرأ مسلما ظلما فذلك حرج، وهلك»','169','278','0');
INSERT INTO [t12842] VALUES (303,'تداووا؛ فإن الله تبارك وتعالى لم ينزل داء إلا وقد وضع له شفاء غير داء واحد وهو','169','278','0');
INSERT INTO [t12842] VALUES (304,'ما خير ما أعطي العبد؟ قال: «خلق حسن»','169','278','0');
INSERT INTO [t12842] VALUES (305,'" بينما رجل يمشي بطريق اشتد عليه العطش فوجد بئرا فنزل فيها فشرب ثم خرج، وإذا كلب يلهث يأكل','170','278','0');
INSERT INTO [t12842] VALUES (306,'إذا حز به أمر قال: «لا إله إلا الله الحليم العظيم لا إله إلا الله رب العرش العظيم لا إله','171','278','0');
INSERT INTO [t12842] VALUES (307,'«كتب على ابن آدم حظه من الزنا، العين تزني وزناها النظر، والفم يزني وزناه التقبيل، واليدان','171','278','0');
INSERT INTO [t12842] VALUES (308,'كان نائما على سرير مزملا بالشريط، تحت رأسه وسادة من أدم حشوها ليف، فاستأذن عليه عمر رضي','172','278','0');
INSERT INTO [t12842] VALUES (309,'«العمرة إلى العمرة كفارة لما بينهما، والحج المبرور ليس له جزاء إلا','172','278','0');
INSERT INTO [t12842] VALUES (310,'«من لم يوتر فليس منا»','172','278','0');
INSERT INTO [t12842] VALUES (311,'«من صلى صلاة ثم جلس ينتظر التي تليها فهو في صلاة»','173','278','0');
INSERT INTO [t12842] VALUES (312,'«لا يشرب الخمر رجل ممسيا إلا أصبح مشركا، ولا يشربها مصبحا إلا أمسى','173','278','0');
INSERT INTO [t12842] VALUES (313,'ليوشك أن ينزل فيكم ابن مريم حكما عدلا وإماما مقسطا، يكسر الصليب ويقتل الخنزير ويضع الجزية','173','278','0');
INSERT INTO [t12842] VALUES (314,'«يوم الجمعة سيد الأيام، وأعظمها عند الله» .','173','278','0');
INSERT INTO [t12842] VALUES (315,'«من قال حين يصبح لا إله إلا الله وحده لا شريك له، له الملك وله الحمد وهو على كل شيء قدير،','174','278','0');
INSERT INTO [t12842] VALUES (316,'بين يدي الساعة لفتنا أو فتنة كقطع الليل المظلم يصبح الرجل فيها مؤمنا ويمسي كافرا، ويمسي','175','278','0');
INSERT INTO [t12842] VALUES (317,'«الله استوى على عرشه قبل أن يخلق شيئا، وكان أول ما خلق القلم فأمره أن يكتب ما هو كائن إلى','175','278','0');
INSERT INTO [t12842] VALUES (318,'أمالي ابن بشران ٢٥','177','0','0');
INSERT INTO [t12842] VALUES (319,'«احفظ الله يحفظك يا غلام، احفظ الله تجده تجاهك، إذا سألت فاسأل الله، وإذا استعنت فاستعن','177','318','0');
INSERT INTO [t12842] VALUES (320,'رجلا أجنب في شتاء، فسأل، فأمر بالغسل فاغتسل، فمات، فذكر ذلك للنبي صلى الله عليه وسلم،','178','318','0');
INSERT INTO [t12842] VALUES (321,'«إياكم ومشارة الناس؛ فإنها تبتر الغرة وتظهر العورة»','178','318','0');
INSERT INTO [t12842] VALUES (322,'«ما من مولود يولد إلا على هذه الملة، حتى يبين عنه لسانه، فأبواه يهودانه، أو ينصرانه، أو','178','318','0');
INSERT INTO [t12842] VALUES (323,'«إذا ولغ الكلب في إناء أحدكم فليغسله سبع مرات، وإذا انقطع شسع أحدكم، فلا يمش في نعله','179','318','0');
INSERT INTO [t12842] VALUES (324,'شكوا إلى رسول الله صلى الله عليه وسلم وسوسة الشيطان , التي يوسوس بها في صدورهم، فقال رسول','179','318','0');
INSERT INTO [t12842] VALUES (325,'إذا توفي جعلناه في أثوابه، فدخل علينا رسول الله صلى الله عليه وسلم، فقلت: رحمة الله عليك','179','318','0');
INSERT INTO [t12842] VALUES (326,'" ثلاث من عمل النبوة: تعجيل الإفطار، وتأخير السحور، ووضع الرجل يده اليمنى على اليسرى في','180','318','0');
INSERT INTO [t12842] VALUES (327,'«من صور صورة كلف يوم القيامة أن ينفخ فيها، ومن تحلم حلما كلف يوم القيامة أن يعقد بين','180','318','0');
INSERT INTO [t12842] VALUES (328,'«يدخل عليكم رجل من أهل الجنة» . فدخل أبو بكر فهنأناه، ثم قال: «يدخل عليكم رجل من أهل الجنة» .','181','318','0');
INSERT INTO [t12842] VALUES (329,'«خير صفوف الرجال المقدم وشرها المؤخر، وخير صفوف النساء المؤخر وشرها','181','318','0');
INSERT INTO [t12842] VALUES (330,'«أرى أمير المؤمنين يدنيك، فاحفظ مني خصالا ثلاثا لا تفشين له سرا، ولا يسمعن منك كذبا، ولا','182','318','0');
INSERT INTO [t12842] VALUES (331,'«إن قلوب بني آدم كلها بين أصبعين من أصابع الرحمن جل وعز، كقلب واحد يصرف كيف شاء» ، وقال رسول','183','318','0');
INSERT INTO [t12842] VALUES (332,'«من مات مدمن خمر لقي الله عز وجل كعابد وثن»','183','318','0');
INSERT INTO [t12842] VALUES (333,'عن الرعد؟ فقال: «ملك من ملائكة الله عز وجل موكل بالسحاب معه مخاريق من نار يسوق بها','184','318','0');
INSERT INTO [t12842] VALUES (334,'«إذا ضرب أحدكم فليجتنب الوجه، ولا يقل قبح الله وجهك ووجه من أشبه وجهك؛ فإن الله عز وجل خلق','184','318','0');
INSERT INTO [t12842] VALUES (335,'أصبح بحمد الله بارئا، فأخذ بيده عباس بن عبد المطلب، فقال له: أنت والله بعد ثلاث عبد','185','318','0');
INSERT INTO [t12842] VALUES (336,'مر النبي صلى الله عليه وسلم برجل يقول: اللهم إني أسألك تمام النعمة. قال: «هل تدري أيش قلت؟»','186','318','0');
INSERT INTO [t12842] VALUES (337,'كيف ينشرح الصدر للإسلام؟ قال: «إذا دخل النور القلب انشرح الصدر، وانفتح» .','186','318','0');
INSERT INTO [t12842] VALUES (338,'{فتلقى آدم من ربه كلمات فتاب عليه} [البقرة: ٣٧] . الآية، قال: " قال: يا رب ألم تخلقني بيدك؟','187','318','0');
INSERT INTO [t12842] VALUES (339,'«أخوف ما أخاف عليكم شهوات الغي في بطونكم، وفي فروجكم، ومضلات الهوى»','187','318','0');
INSERT INTO [t12842] VALUES (340,'«أربع بيني وبين عبدي، واحدة لي، وواحدة لك، وواحدة فيما بيني وبينك، وواحدة فيما بينك وبين','188','318','0');
INSERT INTO [t12842] VALUES (341,'«يأتي هذا الحجر يوم القيامة، وله عينان يبصر بهما , ولسان ينطق به، يشهد لمن استلمه','188','318','0');
INSERT INTO [t12842] VALUES (342,'«المستبان ما قالا فعلى البادئ ما لم يعتد المظلوم»','189','318','0');
INSERT INTO [t12842] VALUES (343,'«فسأله كيف تبيع؟» فأخبره، فأوحى الله إليه أدخل يدك فيه، فأدخل يده , فإذا هو مبلول، قال رسول','189','318','0');
INSERT INTO [t12842] VALUES (344,'«تقتلك الفئة الباغية» .','189','318','0');
INSERT INTO [t12842] VALUES (345,'" ليس على أهل لا إله إلا الله وحشة في قبورهم، وكأني بهم ينفضون التراب عن رءوسهم، ويقولون:','190','318','0');
INSERT INTO [t12842] VALUES (346,'جئت لأبايعك، وتركت أبواي يبكيان. قال: «فارجع إليهما، فأضحكهما كما','191','318','0');
INSERT INTO [t12842] VALUES (347,'" إذا صلى أحدكم فلم يدر كم صلى، فليسجد سجدتين وهو جالس، وإذا جاء أحدكم الشيطان، فقال: إنك','191','318','0');
INSERT INTO [t12842] VALUES (348,'" إن في الجنة لسوقا فيها كثبان من مسك، فيأتيه المؤمنون يوم الجمعة، فيبعث الله عز وجل ريح','191','318','0');
INSERT INTO [t12842] VALUES (349,'«تعرف إلى الله في الرخاء، يعرفك في الشدة»','192','318','0');
INSERT INTO [t12842] VALUES (350,'«أبو بكر صاحبي ومؤنسي في الغار سدوا كل خوخة في المسجد، غير خوخة أبي بكر»','192','318','0');
INSERT INTO [t12842] VALUES (351,'الله عز وجل ليس بتارك يوم الجمعة أحدا من المسلمين إلا غفر له»','192','318','0');
INSERT INTO [t12842] VALUES (352,'" لعن الله القدرية وقد فعل , لعن الله القدرية وقد فعل، لعن الله القدرية وقد فعل، ما قالوا','193','318','0');
INSERT INTO [t12842] VALUES (353,'نهى عن نبيذ الجر , وشهدته حين أمر به، وقال: «اجتنبوا المسكر»','195','318','0');
INSERT INTO [t12842] VALUES (354,'أن الرجلين اللذين لقوهما عويم بن ساعدة، ومعن بن عدي، فأما عويم، فهو الذي بلغنا أنه قال','195','318','0');
INSERT INTO [t12842] VALUES (355,'«ويل للمالك من المملوك، وويل للمملوك من المالك، وويل للغني من الفقير، وويل للفقير من','196','318','0');
INSERT INTO [t12842] VALUES (356,'" ألا أخبركما بمثلكما في الملائكة، ومثلكما في الأنبياء؟ مثلك يا أبا بكر في الملائكة، كمثل','196','318','0');
INSERT INTO [t12842] VALUES (357,'«أفضل الأعمال عند الله عز وجل، إيمان لا شك فيه، وغزو لا غلول فيه، وحج','197','318','0');
INSERT INTO [t12842] VALUES (358,'«لا تقوم الساعة إلا على شرار الناس»','198','318','0');
INSERT INTO [t12842] VALUES (359,'الناس يصعقون يوم القيامة فأكون أول من يفيق»','198','318','0');
INSERT INTO [t12842] VALUES (360,'فجاء بعير فسجد له، فقال أصحابه: يا رسول الله، تسجد لك البهائم والشجر، فنحن أحق أن نسجد','198','318','0');
INSERT INTO [t12842] VALUES (361,'«من أحيى أرضا ميتة فله فيها أجر، وما أكلت العوافي - يعني الطير - فهو له','199','318','0');
INSERT INTO [t12842] VALUES (362,'«لا تحل لك حتى تذوق العسيلة»','199','318','0');
INSERT INTO [t12842] VALUES (363,'يجمع خلق أحدكم في بطن أمه في أربعين يوما، ثم يكون علقة مثل ذلك، ثم يكون مضغة مثل ذلك، ثم','200','318','0');
INSERT INTO [t12842] VALUES (364,'«أهل القبور يعرضون على منازلهم من الجنة والنار غدوة وعشيا»','200','318','0');
INSERT INTO [t12842] VALUES (365,'" ثلاث كفارات، وثلاث درجات , وثلاث منجيات، وثلاث مهلكات، فأما الكفارات: فإسباغ الوضوء في','200','318','0');
INSERT INTO [t12842] VALUES (366,'أي الناس خير؟ قال: «من طال عمره، وحسن عمله» قال: يا رسول، أي الناس شر؟ قال: «من طال عمره،','202','318','0');
INSERT INTO [t12842] VALUES (367,'" أفضل الصلاة بعد الصلاة المفروضة، الصلاة في جوف الليل، وأفضل الصيام بعد شهر رمضان، شهر','202','318','0');
INSERT INTO [t12842] VALUES (368,'" أربع لا يصبن إلا بعجب: الصمت وهو أول العبادة، والتواضع , وذكر الله عز وجل، وقلة','202','318','0');
INSERT INTO [t12842] VALUES (369,'«حسب امرئ من الشر أن يشار إليه بالأصابع في دينه ودنياه، إلا من عصم الله عز','203','318','0');
INSERT INTO [t12842] VALUES (370,'«لتؤدن الحقوق إلى أهلها يوم القيامة، حتى يقص للشاة الجماء من الشاة القرناء','204','318','0');
INSERT INTO [t12842] VALUES (371,'«اليمين الكاذبة منفقة للسلعة، ممحقة للكسب»','204','318','0');
INSERT INTO [t12842] VALUES (372,'«ألا إن هذا المسجد لا يحل لجنب، ولا لحائض، إلا لرسول الله وأزواجه وعلي وفاطمة ابنة محمد','204','318','0');
INSERT INTO [t12842] VALUES (373,'«أو ما علمت يا عدي بن حاتم أن المغضوب عليهم اليهود , وأن الضالين هم','205','318','0');
INSERT INTO [t12842] VALUES (374,'«من أفطر يوما من رمضان، من غير مرض ولا عذر، لم يجز عنه صيام الدهر، ولو','205','318','0');
INSERT INTO [t12842] VALUES (375,'«لا تبيعوا الثمرة، حتى يبدو صلاحها» , نهى البائع والمشتري، ونهى رسول الله صلى الله عليه','206','318','0');
INSERT INTO [t12842] VALUES (376,'«إذا تبايع الرجلان، فكل واحد منهما بالخيار ما لم يتفرقا، وكانا جميعا أو يخير أحدهما الآخر','206','318','0');
INSERT INTO [t12842] VALUES (377,'" لا يقل أحدكم عبدي وأمتي، كلكم عبيد الله وكل نسائكم إماء الله، ولكن ليقل: غلامي، وجاريتي،','207','318','0');
INSERT INTO [t12842] VALUES (378,'«تفتح أبواب الجنة في كل يوم اثنين وخميس»','207','318','0');
INSERT INTO [t12842] VALUES (379,'إنا خرجنا حاجين، ومعنا صاحب لنا فمرض حتى أتينا ذا الصفاح، فمات فهيأناه، ثم انطلقنا،','207','318','0');
INSERT INTO [t12842] VALUES (380,'«في الكلب يصيد» .','208','318','0');
INSERT INTO [t12842] VALUES (381,'«أعطوا الأجير حقه قبل أن يجف عرقه»','208','318','0');
INSERT INTO [t12842] VALUES (382,'«طوبى لمن وجد في صحيفته استغفارا كثيرا»','208','318','0');
INSERT INTO [t12842] VALUES (383,'«من أمرك بهذا؟» .','209','318','0');
INSERT INTO [t12842] VALUES (384,'«لا يتقدمن أحد منكم الشهر بيوم أو يومين، إلا أن يكون صوما كان يصومه» .','209','318','0');
INSERT INTO [t12842] VALUES (385,'الأئمة من قريش لهم عليكم حق، ولكم عليهم حق مثله، ما داموا إذا حكموا عدلوا وإذا استرحموا','210','318','0');
INSERT INTO [t12842] VALUES (386,'ينهى عن القطع في الغزو لقطعتك» .','210','318','0');
INSERT INTO [t12842] VALUES (387,'«جعل السلب للقاتل»','211','318','0');
INSERT INTO [t12842] VALUES (388,'للمرء ما طابت به نفس إمامه»','211','318','0');
INSERT INTO [t12842] VALUES (389,'«يابن آدم، أنفق أنفق عليك»','211','318','0');
INSERT INTO [t12842] VALUES (390,'«يمين الله ملآى سحاء، لا يغيضها شيء الليل والنهار»','211','318','0');
INSERT INTO [t12842] VALUES (391,'«لا يقتسم ورثتي دينارا، ما تركت بعد نفقة نسائي، ومؤنة عاملي فهو صدقة»','211','318','0');
INSERT INTO [t12842] VALUES (392,'«كنت أغتسل أنا وهو ونحن جنبان من إناء واحد»','212','318','0');
INSERT INTO [t12842] VALUES (393,'«الإمام ضامن إن أحسن فله، وإن أساء فعليه، ولا عليهم»','212','318','0');
INSERT INTO [t12842] VALUES (394,'لا أعلم شيئا يقربكم من الجنة، ويباعدكم من النار إلا قد أخبرتكم به، ولا أعلم شيئا يقربكم','212','318','0');
INSERT INTO [t12842] VALUES (395,'لما أصاب آدم الخطيئة خرج هاربا من الجنة , فتعلق غصن من أغصان الجنة بشعره , فناداه الله','213','318','0');
INSERT INTO [t12842] VALUES (396,'أمالي ابن بشران ٢٦','215','0','0');
INSERT INTO [t12842] VALUES (397,'«إذا سمعتم بالدجال ففروا منه؛ فإن الرجل يأتيه ليرد عليه فيتبعه لما يرى معه من','215','396','0');
INSERT INTO [t12842] VALUES (398,'«ما رأيته يصوم شهرا إلا شعبان , فإنه كان يصله برمضان، يصوم الشهر حتى نقول لا يفطر، ويفطر','215','396','0');
INSERT INTO [t12842] VALUES (399,'«سجد لك خيالي وسوادي , وآمن بك فؤادي , وهذه يدي التي جنيت بها على نفسي , فيا عظيم هل يغفر','216','396','0');
INSERT INTO [t12842] VALUES (400,'«لا نورث ما تركنا فهو صدقة»','217','396','0');
INSERT INTO [t12842] VALUES (401,'«ما من نفس تقتل ظلما إلا كان على ابن آدم الأول كفل من دمها، وذلك بأنه أسن','217','396','0');
INSERT INTO [t12842] VALUES (402,'في الجمعة لساعة» . وجعل ابن عون يرينا بكفه اليمنى فقلنا: يزهدها.','218','396','0');
INSERT INTO [t12842] VALUES (403,'«تجوز لأمتي عما حدثت به أنفسها أو وسوست به أنفسها ما لم تعمل به أو تكلم','218','396','0');
INSERT INTO [t12842] VALUES (404,'«اللهم لا عيش إلا عيش الآخرة، فاغفر للأنصار والمهاجرة»','218','396','0');
INSERT INTO [t12842] VALUES (405,'«ما تركت في الناس بعدي فتنة أضر على الرجال من النساء»','218','396','0');
INSERT INTO [t12842] VALUES (406,'أرى رؤياكم قد تواطأت في السبع الأواخر، فمن كان متحريها فليتحراها في السبع','218','396','0');
INSERT INTO [t12842] VALUES (407,'«قمت على باب الجنة فرأيت أكثر أهلها المساكين، ورأيت أصحاب الحد محبوسين , إلا أصحاب النار','219','396','0');
INSERT INTO [t12842] VALUES (408,'«لا يزني الزاني وهو مؤمن , ولا يسرق حين يسرق وهو مؤمن , ولا يشرب الخمر حين يشربها وهو','219','396','0');
INSERT INTO [t12842] VALUES (409,'«أيما مسلم استرسل إلى مسلم فغبنه كان غبنة ذلك ربا»','219','396','0');
INSERT INTO [t12842] VALUES (410,'" {وكان تحته كنز لهما} [الكهف: ٨٢] قال: لوح من ذهب؛ لأن الذهب لا ينقص ولا يصدأ، فيه مكتوب:','220','396','0');
INSERT INTO [t12842] VALUES (411,'" ثلاثة لا يقبل معهن عمل: الشرك , والكفر , والرأي ".','221','396','0');
INSERT INTO [t12842] VALUES (412,'«ابتلينا بفتنة الضراء فصبرنا، وابتلينا بفتنة السراء فلم نصبر، فإن أكثر ما أخاف عليكم من','221','396','0');
INSERT INTO [t12842] VALUES (413,'«الدينار بالدينار , والدرهم بالدرهم , لا فضل بينهما» .','222','396','0');
INSERT INTO [t12842] VALUES (414,'«ما من أحد يموت سقطا ولا هرما , وإنما الناس فيما بين ذلك إلا بعث ابن ثلاثين سنة، فإن كان','222','396','0');
INSERT INTO [t12842] VALUES (415,'أي العمل أفضل؟ قال: «الإيمان بالله وتصديق به وجهاد في سبيله وحج مبرور» . فلما ولى قال: «هل لك','223','396','0');
INSERT INTO [t12842] VALUES (416,'«من مس فرجه فليتوضأ»','223','396','0');
INSERT INTO [t12842] VALUES (417,'{ومن يقتل مؤمنا متعمدا فجزاؤه جهنم} [النساء: ٩٣] قال: «هو جزاؤه إن','224','396','0');
INSERT INTO [t12842] VALUES (418,'«مثل المؤمن ومثل الإيمان كمثل فرس فر من آخيته يجول ثم يرجع إلى آخيته، وإن المؤمن يسهو ثم','224','396','0');
INSERT INTO [t12842] VALUES (419,'{ثم أورثنا الكتاب الذين اصطفينا من عبادنا فمنهم ظالم لنفسه ومنهم مقتصد ومنهم سابق','225','396','0');
INSERT INTO [t12842] VALUES (420,'اشتروا أنفسكم من الله عز وجل، يا صفية عمة رسول الله، ويا فاطمة بنت رسول الله اشتريا','225','396','0');
INSERT INTO [t12842] VALUES (421,'ليأتين على أحدكم يوم لأن يراني ثم لأن يراني أحب إليه من أن يكون له مثل أهله','225','396','0');
INSERT INTO [t12842] VALUES (422,'«اللاعب بالنردشير فكأنما غمس يده في لحم الخنزير ودمه»','226','396','0');
INSERT INTO [t12842] VALUES (423,'{فخانتاهما} [التحريم: ١٠] . قال: كانت خيانتهما أنهما كانتا على عبوديتهما، فكانت امرأة نوح','226','396','0');
INSERT INTO [t12842] VALUES (424,'" دخل عمر بن الخطاب يوما على ابنة علي بن أبي طالب، وكانت تحته وهي تبكي، فقال: ما يبكيك؟','226','396','0');
INSERT INTO [t12842] VALUES (425,'«التمر بالتمر , والحنطة بالحنطة , والشعير بالشعير , والذهب بالذهب , والفضة بالفضة , يدا','228','396','0');
INSERT INTO [t12842] VALUES (426,'«إذا رضي الله عز وجل عن العبد أثنى عليه سبعة أضعاف من الخير لم يعملها، وإذا سخط على العبد','229','396','0');
INSERT INTO [t12842] VALUES (427,'«لو أن دلوا من غساق يهراق في الدنيا لأنتن أهل الأرض»','229','396','0');
INSERT INTO [t12842] VALUES (428,'«الجنة مائة درجة، ولو أن العالمين اجتمعوا فيها في إحداهن لوسعتهم»','229','396','0');
INSERT INTO [t12842] VALUES (429,'إذا خرج إلى العيدين رجع في غير الطريق الذي يخرج فيه»','229','396','0');
INSERT INTO [t12842] VALUES (430,'«أين المتحابون بجلالي , اليوم أظلهم في ظلي يوم لا ظل إلا ظلي»','230','396','0');
INSERT INTO [t12842] VALUES (431,'«لا يجتمعان في النار اجتماعا يضر أحدهما مسلم قتل كافرا ثم سدد المسلم وقارب , ولا يجتمعان','230','396','0');
INSERT INTO [t12842] VALUES (432,'عبدي المؤمن بمنزلة كل خير يحمدني وأنا أنزع نفسه من بين جنبيه»','230','396','0');
INSERT INTO [t12842] VALUES (433,'" بينا أهل الجنة في نعيمهم إذ سطع لهم نور فرفعوا رؤوسهم، فإذا الرب عز وجل قد أشرف عليهم من','231','396','0');
INSERT INTO [t12842] VALUES (434,'أبي توفي وترك مالا ولم يوص، " فهل يكفي إن تصدقت عنه؟ قال: نعم "','231','396','0');
INSERT INTO [t12842] VALUES (435,'«لا يموتن أحدكم إلا وهو حسن الظن بربه عز وجل»','232','396','0');
INSERT INTO [t12842] VALUES (436,'إن كنت تريد أن لا تأتي الخير إلا عن نشاط، فإن نفسك إلى السآمة والفتور والملل أقرب، ولكن','232','396','0');
INSERT INTO [t12842] VALUES (437,'«أرأيتم نفسا إن نعمها صاحبها وفنقها وقاربها ذمته غدا قدام الله عز وجل، وإن خالفها وأنصبها','233','396','0');
INSERT INTO [t12842] VALUES (438,'" اضمنوا لي من أنفسكم أضمن لكم الجنة: اصدقوا إذا حدثتم , وأوفوا إذا نذرتم , وأدوا إذا','233','396','0');
INSERT INTO [t12842] VALUES (439,'إذا بايع بايع على شهادة أن لا إله إلا الله وأن محمدا رسول الله , وإقام الصلاة , وإيتاء','234','396','0');
INSERT INTO [t12842] VALUES (440,'«ألا أدلكم على عمل يمحو الله به الخطايا ويرفع به الدرجات» ؟ قالوا: بلى , قال: «إسباغ الوضوء','234','396','0');
INSERT INTO [t12842] VALUES (441,'" تخرج الدابة معها عصى موسى وخاتم سليمان، فتجلوا وجه المؤمن بالعصى، ويختم أنف الكافر','235','396','0');
INSERT INTO [t12842] VALUES (442,'«ما ضرب رسول الله صلى الله عليه وسلم خادما له قط ولا امرأة له قط , ولا ضرب بيده شيئا قط،','236','396','0');
INSERT INTO [t12842] VALUES (443,'«لا يصيب المؤمن هم ولا حزن ولا نصب ولا وصب ولا أذى إلا كفر به عنه»','236','396','0');
INSERT INTO [t12842] VALUES (444,'إذا قام من الليل هلل ثلاثا: لا إله إلا الله، والله أكبر , ثلاثا، ثم قال: «سبحانك اللهم','236','396','0');
INSERT INTO [t12842] VALUES (445,'«إن في الليل لساعة لا يوافقها عبد مسلم يسأل الله عز وجل فيها خيرا إلا أتاه إياه وذلك كل','237','396','0');
INSERT INTO [t12842] VALUES (446,'«إنما بعثت لأتمم صالح الأخلاق»','237','396','0');
INSERT INTO [t12842] VALUES (447,'«لأن أطعم أخا لي في الله لقمة أحب إلي من أن أتصدق لله بدرهمين، ولدرهمان أعطيهما إياه أحب','237','396','0');
INSERT INTO [t12842] VALUES (448,'«إن للشيطان فخوخا ومصالي، وإن من مصالي الشيطان وفخوخه البطر بأنعم الله , والفخر بإعطاء','238','396','0');
INSERT INTO [t12842] VALUES (449,'«قام في اثنتين من الصلاة ولم يجلس، فلما قضى صلاته سجد سجدتين وهو جالس ثم سلم بعد','239','396','0');
INSERT INTO [t12842] VALUES (450,'«تجدون الناس كمعادن الذهب والفضة، خيارهم في الجاهلية خيارهم في الإسلام إذا','239','396','0');
INSERT INTO [t12842] VALUES (451,'«الوائدة والموءودة في النار» .','240','396','0');
INSERT INTO [t12842] VALUES (452,'«إذا سمعتم المؤذن يؤذن فقولوا كما يقول، وصلوا علي؛ فإنه ليس أحد يصلي علي صلاة إلا صلى الله','240','396','0');
INSERT INTO [t12842] VALUES (453,'«مفتاح الصلاة الطهور , وتحريمها التكبير , وتحليلها التسليم، وفي كل ركعتين تسليمة، ولا صلاة','241','396','0');
INSERT INTO [t12842] VALUES (454,'" {وهم فيها كالحون} [المؤمنون: ١٠٤] قال: تشويه النار فتقلص شفته العليا حتى تبلغ وسط رأسه','241','396','0');
INSERT INTO [t12842] VALUES (455,'«الإيمان يمان، والكفر من قبل المشرق، وإن السكينة في أهل الغنم، وإن الربا والفخر في أهل','241','396','0');
INSERT INTO [t12842] VALUES (456,'«ما أغبرت قدما رجل في سبيل الله عز وجل إلا حرم الله عز وجل عليه النار» . قال: فما رأيت ماشيا','242','396','0');
INSERT INTO [t12842] VALUES (457,'" ثلاثة يؤتون أجرهم مرتين يوم القيامة: رجل كانت عنده أمة فأدبها فأحسن أدبها ثم أعتقها','242','396','0');
INSERT INTO [t12842] VALUES (458,'دعا بماء يوم خيبر من قربة، فقيل: يا رسول الله إنها جلد ميتة.','243','396','0');
INSERT INTO [t12842] VALUES (459,'" ثلاث قاصمات الظهر: فقر لا يجد الرجل متلذذا، وزوجة يأمنها وتخونه، وإمام يسخط الله ويرضي','243','396','0');
INSERT INTO [t12842] VALUES (460,'«لا يجزي ولد والده إلا أن يجده مملوكا فيشتريه فيعتقه»','244','396','0');
INSERT INTO [t12842] VALUES (461,'{قل هو الله أحد {١} الله الصمد {٢} لم يلد ولم يولد {٣} ولم يكن له كفوا أحد آية ١-٤} .','244','396','0');
INSERT INTO [t12842] VALUES (462,'«نهى أن يتقدم الشهر بصيام يوم أو يومين، إلا أن يكون رجل كان له صيام فأتى','245','396','0');
INSERT INTO [t12842] VALUES (463,'«لا يخلون رجل بامرأة؛ فإن الشيطان ثالثهما، ومن سرته حسنته وساءته سيئته فهو','245','396','0');
INSERT INTO [t12842] VALUES (464,'" أتدرون ما أكثر ما يدخل الناس النار؟ قالوا: الله ورسوله أعلم.','246','396','0');
INSERT INTO [t12842] VALUES (465,'«من أفطر يوما من رمضان من غير رخصة الله لم يقضه عنه صيام الدهر وإن صامه»','247','396','0');
INSERT INTO [t12842] VALUES (466,'«للجنة أقرب إلى أحدكم من شراك نعله، والنار مثل ذلك»','247','396','0');
INSERT INTO [t12842] VALUES (467,'" إذا ابتلى الله عز وجل العبد ببلاء في جسده قال للملك: اكتب له صالح عمله الذي كان يعمل','247','396','0');
INSERT INTO [t12842] VALUES (468,'«من بنى مسجدا يذكر الله عز وجل فيه بنى الله عز وجل له بيتا في الجنة، ومن أعتق نفسا مسلمة','248','396','0');
INSERT INTO [t12842] VALUES (469,'" ثلاث من كن فيه فهو منافق وإن صام وصلى وحج البيت وزعم أنه مؤمن: إذا حدث كذب , وإذا ائتمن','248','396','0');
INSERT INTO [t12842] VALUES (470,'«لا يؤمن المرء حتى يؤمن بالقدر خيره وشره»','249','396','0');
INSERT INTO [t12842] VALUES (471,'فأي المال نتخذ؟ قال: «لسانا ذاكرا , وقلبا شاكرا , وزوجة مواتية»','249','396','0');
INSERT INTO [t12842] VALUES (472,'أمالي ابن بشران ٢٧','251','0','0');
INSERT INTO [t12842] VALUES (473,'«من كسا مسلما على عري كساه الله عز وجل من خضر الجنة، ومن سقاه على ظمأ سقاه الله عز وجل من','251','472','0');
INSERT INTO [t12842] VALUES (474,'«لا يلج النار أحد بكى من خشية الله تعالى حتى يعود اللبن في الضرع، ولا يجتمع غبار في سبيل','252','472','0');
INSERT INTO [t12842] VALUES (475,'«من كانت عنده مظلمة لأخيه من عرض أو مال فليتحلله اليوم، قبل أن يؤخذ منه حين لا يكون دينار','252','472','0');
INSERT INTO [t12842] VALUES (476,'صدقة المرء المسلم تزيد في العمر، وتمنع ميتة السوء، ويذهب الله عز وجل بها الفخر','253','472','0');
INSERT INTO [t12842] VALUES (477,'الإمارة، فقال: «إنك ضعيف، وإنها أمانة، وإنها يوم القيامة خزي وندامة، إلا من أخذها بحقها','254','472','0');
INSERT INTO [t12842] VALUES (478,'إذا ما غضبت أجد رسول الله صلى الله عليه وسلم يطرف أنفي، قال: " يا عويش قولي: اللهم رب محمد','255','472','0');
INSERT INTO [t12842] VALUES (479,'«قرض مرتين في عفاف خير من صدقة مرة»','255','472','0');
INSERT INTO [t12842] VALUES (480,'لأعلم كلمة لا يقولها رجل عند حضرة الموت إلا وجد لها روحا حين تخرج روحه من جسده، وكانت له','256','472','0');
INSERT INTO [t12842] VALUES (481,'" أوصاني خليلي صلى الله عليه وسلم بثلاث لا أدعهن حتى أموت: الوتر قبل أن أنام، وصوم ثلاثة','257','472','0');
INSERT INTO [t12842] VALUES (482,'«بت في بيت خالتي ميمونة بنت الحارث وكان رسول الله صلى الله عليه وسلم عندها ليلتها، فقام','257','472','0');
INSERT INTO [t12842] VALUES (483,'«لا يوضع في الميزان شيء أفضل من حسن الخلق»','257','472','0');
INSERT INTO [t12842] VALUES (484,'إذا دخل رجب، قال: «اللهم بارك لنا في رجب وشعبان، وبلغنا رمضان»','258','472','0');
INSERT INTO [t12842] VALUES (485,'آخى رسول الله صلى الله عليه وسلم بين أبي بكر وعمر رضي الله عنهما، فأقبل أحدهما آخذا بيد','258','472','0');
INSERT INTO [t12842] VALUES (486,'دخل معاوية على النبي صلى الله عليه وسلم وكانت أم حبيبة بنت أبي سفيان إلى جانب النبي صلى','258','472','0');
INSERT INTO [t12842] VALUES (487,'«من نسي صلاة فليصلها إذا ذكرها»','259','472','0');
INSERT INTO [t12842] VALUES (488,'" أحب الكلام إلى الله عز وجل أربع: لا إله إلا الله، والله أكبر، وسبحان الله، والحمد لله، لا','259','472','0');
INSERT INTO [t12842] VALUES (489,'لما كانت الليلة التي قتل فيها عثمان بن عفان رأيت النبي صلى الله عليه وسلم أشعث أغبر، على','260','472','0');
INSERT INTO [t12842] VALUES (490,'دعاني النبي صلى الله عليه وسلم إلى السحور، فقال: «هلم إلى الغداء','260','472','0');
INSERT INTO [t12842] VALUES (491,'«لا تسبوا أصحابي، فوالذي نفسي بيده لو أن أحدكم أنفق مثل أحد ذهبا ما أدرك مد أحدهم ولا','261','472','0');
INSERT INTO [t12842] VALUES (492,'أفطر عندنا، فأصبح صائما، ثم قتل من يومه رضوان الله عليه "','262','472','0');
INSERT INTO [t12842] VALUES (493,'لعلي رضي الله عنه: «أنت مني بمنزلة هارون من موسى، إلا أنه لا نبي بعدي»','262','472','0');
INSERT INTO [t12842] VALUES (494,'قضى رسول الله صلى الله عليه وسلم الحاجة مني، ثم يضع رأسه ثم يبيت فيفيض عليه','263','472','0');
INSERT INTO [t12842] VALUES (495,'لأبي مالا ولي مالا، وهو يريد أن يأخذ مالي فيضيفه إلى ماله.','264','472','0');
INSERT INTO [t12842] VALUES (496,'«أبردوا بالظهر في الحر»','264','472','0');
INSERT INTO [t12842] VALUES (497,'لا عقل كالتدبير، ولا ورع كالكف، ولا حسن كحسن الخلق»','264','472','0');
INSERT INTO [t12842] VALUES (498,'«الفطرة على كل مسلم»','265','472','0');
INSERT INTO [t12842] VALUES (499,'«حجة لمن لم يحج خير من عشر غزوات، وغزوة لمن حج خير من عشر حجج، وغزوة في البحر خير من عشر','265','472','0');
INSERT INTO [t12842] VALUES (500,'«ما يليني منك؟» قال: بطني، قال: «ملأها الله تبارك وتعالى علما وحلما»','266','472','0');
INSERT INTO [t12842] VALUES (501,'«الذهب المشبك بالدر» قرأت على يوسف، حدثكم أبو الطيب ابن المنادي، وكان من أصحاب الحديث','266','472','0');
INSERT INTO [t12842] VALUES (502,'قتل الله تبارك وتعالى أبا جهل، قال: «الحمد لله الذي نصر عبده وأعز دينه»','269','472','0');
INSERT INTO [t12842] VALUES (503,'أينحني بعضنا لبعض إذا التقينا؟ قال: لا. قالوا: فيسجد بعضنا لبعض إذا التقينا؟ قال: لا،','269','472','0');
INSERT INTO [t12842] VALUES (504,'أمالي ابن بشران ٢٨','272','0','0');
INSERT INTO [t12842] VALUES (505,'«أمتي أمة مرحومة ليس عليها عذاب في الآخرة، إذا كان يوم القيامة أعطي كل رجل من أمتي رجلا من','272','504','0');
INSERT INTO [t12842] VALUES (506,'" {ثلة من الأولين {٣٩} وثلة من الآخرين} [الواقعة: ٣٩-٤٠] ، قال: قال رسول الله صلى الله عليه','273','504','0');
INSERT INTO [t12842] VALUES (507,'«مثل أهل بيتي مثل سفينة نوح، من ركب فيها نجا، ومن تخلف عنها غرق»','273','504','0');
INSERT INTO [t12842] VALUES (508,'واصل رسول الله صلى الله عليه وسلم، فبلغ ذلك الناس فواصلوا، فبلغ ذلك النبي صلى الله عليه','274','504','0');
INSERT INTO [t12842] VALUES (509,'«من قتل نفسه بحديدة فحديدته بيده ينجأ بها في بطنه في نار جهنم خالدا مخلدا فيها أبدا , ومن','274','504','0');
INSERT INTO [t12842] VALUES (510,'أرى الفتن تقع خلال بيوتكم كمواقع القطر»','274','504','0');
INSERT INTO [t12842] VALUES (511,'«ما من أحد يوم القيامة غنيا ولا فقيرا إلا ود أنه أوتي في الدنيا قوتا»','275','504','0');
INSERT INTO [t12842] VALUES (512,'كيف يحشرهم على وجوههم؟ قال: «الذي يحشرهم على أرجلهم لقادر على أن يحشرهم على','275','504','0');
INSERT INTO [t12842] VALUES (513,'«ما لي وللدنيا، وما أنا والدنيا إلا كراكب استظل تحت شجرة ثم راح وتركها»','275','504','0');
INSERT INTO [t12842] VALUES (514,'«من أحب دنياه أضر بآخرته، ومن أحب آخرته أضر بدنياه، فآثروا ما يبقى على ما','276','504','0');
INSERT INTO [t12842] VALUES (515,'وصية نوح ابنه، قال: " يا بني، أوصيك باثنتين، وأنهاك عن اثنتين: عن الشرك والكفر؛ فإن الله عز','276','504','0');
INSERT INTO [t12842] VALUES (516,'في إصبع رجل خاتم ذهب، قال: فقال: «ألم أنه عن هذا، لعن الله لابسه»','277','504','0');
INSERT INTO [t12842] VALUES (517,'قال للعباس بن عبد المطلب: «فيكم النبوة والمملكة»','278','504','0');
INSERT INTO [t12842] VALUES (518,'«سألت ربي عز وجل اللاهين فوهبنيهم وأعطانيهم» .','278','504','0');
INSERT INTO [t12842] VALUES (519,'" ألا أخبركم بخير هذه الأمة بعد نبيها صلى الله عليه وسلم؟ قالوا: بلى.','278','504','0');
INSERT INTO [t12842] VALUES (520,'لله تعالى في الليل والنهار عتقاء من النار، ولكل مسلم ومسلمة في كل يوم وليلة دعوة','279','504','0');
INSERT INTO [t12842] VALUES (521,'«ما من عبد يذنب ذنبا فيعلم أن الله عز وجل إن شاء أن يغفر له غفر له، وإن شاء أن يعذبه عليه','279','504','0');
INSERT INTO [t12842] VALUES (522,'«مثل المريض إذا برئ فصح كمثل البردة تقع من السماء في صفائها ولونها»','279','504','0');
INSERT INTO [t12842] VALUES (523,'«من توضأ في بيته فأحسن الوضوء، ثم زارني في بيت من بيوتي فإياي زار، وحق على المزور أن يكرم','280','504','0');
INSERT INTO [t12842] VALUES (524,'فأخرج كبة من شعر، قال «ما كنت أرى أحدا يفعل هذا غير اليهود، وإن رسول الله صلى الله عليه','280','504','0');
INSERT INTO [t12842] VALUES (525,'«من كان ذا لسانين في الدنيا جعل الله له يوم القيامة لسانين من نار»','281','504','0');
INSERT INTO [t12842] VALUES (526,'«الدعاء بين الأذان والإقامة لا يرد»','281','504','0');
INSERT INTO [t12842] VALUES (527,'«لو تعلمون ما أعلم لضحكتم قليلا ولبكيتم كثيرا»','281','504','0');
INSERT INTO [t12842] VALUES (528,'«كل شراب أسكر حرام»','281','504','0');
INSERT INTO [t12842] VALUES (529,'«أقلوا الحديث عني إلا ما علمتم، من كذب علي متعمدا فليتبوأ مقعده من','282','504','0');
INSERT INTO [t12842] VALUES (530,'إذا قام من مجلسه، قال: «سبحانك اللهم وبحمدك لا إله إلا أنت أستغفرك وأتوب','282','504','0');
INSERT INTO [t12842] VALUES (531,'أمالي ابن بشران ٢٩','285','0','0');
INSERT INTO [t12842] VALUES (532,'ما من مسلم ينفق نفقة في سبيل الله , عز وجل , إلا جاءت الملائكة يوم القيامة معهم الريحان','285','531','0');
INSERT INTO [t12842] VALUES (533,'المساجد بيوت الله في الأرض تضيء كما تضيء نجوم السماء لأهلها','286','531','0');
INSERT INTO [t12842] VALUES (534,'من أذن سبع سنين محتسبا؛ كتب الله له براءة من النار','286','531','0');
INSERT INTO [t12842] VALUES (535,'يصوم يوم عاشوراء ويأمر بصيامه.','286','531','0');
INSERT INTO [t12842] VALUES (536,'صوم يوم عاشوراء كفارة سنة، وصوم يوم عرفة كفارة سنتين','286','531','0');
INSERT INTO [t12842] VALUES (537,'لئن بقيت إلى قابل، لأصومن اليوم التاسع','287','531','0');
INSERT INTO [t12842] VALUES (538,'يأمرنا بصوم عاشوراء','287','531','0');
INSERT INTO [t12842] VALUES (539,'من وسع على أهله يوم عاشوراء؛ وسع الله عليه السنة كلها','287','531','0');
INSERT INTO [t12842] VALUES (540,'أسأل الله الرفيق الأعلى الأسعد مع جبريل وميكائيل وإسرافيل','287','531','0');
INSERT INTO [t12842] VALUES (541,'النادم ينتظر الرحمة، والمصر ينتظر المقت، وكل عامل سيقدم على ما سلف منه عند موته، وملاك','288','531','0');
INSERT INTO [t12842] VALUES (542,'ما في الجنة ورقة إلا وعليها مكتوب: لا إله إلا الله محمد رسول الله، أبو بكر الصديق، عمر','289','531','0');
INSERT INTO [t12842] VALUES (543,'يتعاقبون فيكم ملائكة بالليل وملائكة بالنهار، ويجتمعون في صلاة الفجر وصلاة العصر، ثم يعرج','289','531','0');
INSERT INTO [t12842] VALUES (544,'الذي يعلم الناس الخير، ليستغفر له كل دابة حتى الحوت في البحر.','290','531','0');
INSERT INTO [t12842] VALUES (545,'{وسلام على عباده الذين اصطفى} [النمل: ٥٩] قال: أصحاب رسول الله , صلى الله عليه وسلم','290','531','0');
INSERT INTO [t12842] VALUES (546,'طلبت الدنيا طلب من لا بد له فيها، وطلبت الآخرة طلب من لا حاجة له إليها، والدنيا قد كفيتها','290','531','0');
INSERT INTO [t12842] VALUES (547,'أمالي ابن بشران ٣٠','291','0','0');
INSERT INTO [t12842] VALUES (548,'ألا تصليان؟ ! فقلت: يا رسول الله، إنما أنفسنا بيد الله , عز وجل , فإذا شاء أن يبعثها','291','547','0');
INSERT INTO [t12842] VALUES (549,'أقعدني رسول الله , صلى الله عليه وسلم , في حجره ودعا لي بالحكمة.','291','547','0');
INSERT INTO [t12842] VALUES (550,'للأنبياء منابر من ذهب فيجلسون عليها، ويبقى منبري لا أجلس عليه , أو فقال: لا أقعد عليه','292','547','0');
INSERT INTO [t12842] VALUES (551,'من رضي بالله ربا، وبالإسلام دينا، وبمحمد نبيا؛ وجبت له الجنة ". فعجب لها أبو سعيد، فقال:','292','547','0');
INSERT INTO [t12842] VALUES (552,'أنا عند ظن عبدي، فليظن بي ما شاء.','293','547','0');
INSERT INTO [t12842] VALUES (553,'بينا أنا أسير في الجنة إذ عرض لي نهر حافتاه اللؤلؤ المجوف، قلت: يا جبريل , ما هذا؟ ! قال:','293','547','0');
INSERT INTO [t12842] VALUES (554,'لا تسبوا أصحابي، فلو أنفق أحدكم مثل أحد ذهبا، ما أدرك مد أحدهم ولا','293','547','0');
INSERT INTO [t12842] VALUES (555,'إذا حدث عن عائشة قال: حدثتني الصديقة بنت الصديق حبيبة حبيب الله، المبرأة من فوق سبع','294','547','0');
INSERT INTO [t12842] VALUES (556,'لو أن لابن آدم واديين من مال، لابتغى واديا ثالثا، ولا يملأ جوف ابن آدم إلا التراب، ويتوب','294','547','0');
INSERT INTO [t12842] VALUES (557,'لا يتمنين أحدكم الموت لضر نزل به؛ فإن كان لا بد فاعلا، فليقل: اللهم أحيني ما كانت الحياة','294','547','0');
INSERT INTO [t12842] VALUES (558,'أحدكم لن يموت حتى يستكمل رزقه؛ فلا تستبطئوا الرزق، فاتقوا الله وأجملوا في الطلب، خذوا ما','294','547','0');
INSERT INTO [t12842] VALUES (559,'من يهده الله فلا مضل له، ومن يضلل الله فلا هادي له، فقال الجاثليق: إن الله , عز وجل , لا','295','547','0');
INSERT INTO [t12842] VALUES (560,'من أكثر الاستغفار جعل الله , عز وجل , له من كل هم فرجا، ومن كل ضيق مخرجا، ورزقه من حيث لا','295','547','0');
INSERT INTO [t12842] VALUES (561,'من غسل يوم الجمعة واغتسل، وبكر وابتكر، ومشى ولم يركب، ودنا من الإمام فاستمع ولم يلغ؛ كان','296','547','0');
INSERT INTO [t12842] VALUES (562,'اللهم إنا نعوذ بك من عذاب جهنم، ونعوذ بك من عذاب القبر، ونعوذ بك من فتنة المسيح، ونعوذ بك','296','547','0');
INSERT INTO [t12842] VALUES (563,'احذر الصرعة على الغفلة حين. . . . إلى الرجعة، ولا تغترن بطول العافية؛ فإنه أجل ليس دونه','296','547','0');
INSERT INTO [t12842] VALUES (564,'علامة حب الله , عز وجل: كثرة ذكره؛ فإنك لن تحب شيئا إلا أكثرت ذكره، وعلامة الدين: الإخلاص','297','547','0');
INSERT INTO [t12842] VALUES (565,'أعلم شأن الحجاب حين أنزل، لقد كان أبي بن كعب يسألني عنه، فكان أول ما أنزل فيما ابتنى رسول','298','547','0');
INSERT INTO [t12842] VALUES (566,'مثل المؤمن الذي يقرأ القرآن مثل الأترجة , طعمها طيب وريحها طيب، ومثل المؤمن الذي لا يقرأ','299','547','0');
INSERT INTO [t12842] VALUES (567,'لا يحل لمسلم أن يروع مسلما.','299','547','0');
INSERT INTO [t12842] VALUES (568,'في الجنة بابا يقال له: الريان ".','299','547','0');
INSERT INTO [t12842] VALUES (569,'" من ابتلي من البنات بشيء فأحسن إليهن كن سترا له من النار.','300','547','0');
INSERT INTO [t12842] VALUES (570,'أزهد الناس في الدنيا، وأنتم أرغب الناس فيها.','300','547','0');
INSERT INTO [t12842] VALUES (571,'إذا قام من الليل هلل ثلاثا: لا إله إلا الله والله أكبر , ثلاثا. ثم قال: " سبحانك اللهم','300','547','0');
INSERT INTO [t12842] VALUES (572,'إنما الأعمال بالنية، وإنما لكل امرئ ما نوى، فمن كانت هجرته إلى الله ورسوله، فهجرته إلى','301','547','0');
INSERT INTO [t12842] VALUES (573,'لا تظهر الشماتة بأخيك، فيعافيه الله ويبتليك.','301','547','0');
INSERT INTO [t12842] VALUES (574,'لهذا الخير خزائن، ويجعل له مفاتيح، مفتاحه الوجل، وطوبى لرجل جعله الله , عز وجل , مفتاحا','302','547','0');
INSERT INTO [t12842] VALUES (575,'جاهدوا في سبيل الله , عز وجل , فإن الجهاد في سبيل الله , عز وجل , باب من أبواب الجنة،','302','547','0');
INSERT INTO [t12842] VALUES (576,'إذا ابتلى الله العبد ببلاء في جسده، قال للملك: اكتب له صالح عمله الذي كان يعمل؛ فإن شفاه','303','547','0');
INSERT INTO [t12842] VALUES (577,'للعبد المؤمن في الجنة لخيمة من لؤلؤ، طولها ثلاثون ميلا، للمؤمن فيها أهلون يطوف عليهم لا','303','547','0');
INSERT INTO [t12842] VALUES (578,'لكل نبي دعوة يدعو بها، وأريد أن أختبئ دعوتي شفاعة لأمتي في الآخرة.','303','547','0');
INSERT INTO [t12842] VALUES (579,'ثلاثة حق عليك أن توقرهم: ذو سلطان مقتصد، وحامل كتاب الله، وذو الشيبة','304','547','0');
INSERT INTO [t12842] VALUES (580,'حفت الجنة بالمكاره وأنت تكرهها، وحفت النار بالشهوات وأنت تطلبها، فما أنت إلا كالمريض','304','547','0');
INSERT INTO [t12842] VALUES (581,'إذا كان يوم القيامة جمع الله , عز وجل , الخلائق في صعيد واحد، ثم رفع لكل قوم آلهتهم التي','305','547','0');
INSERT INTO [t12842] VALUES (582,'الجنة مائة درجة ما بين كل درجتين كما بين السماء والأرض، والفردوس أعلى الجنة؛ فإذا سألتم','305','547','0');
INSERT INTO [t12842] VALUES (583,'أكثر من السجود؛ فإنه ليس من عبد يسجد لله سجدة إلا رفعه الله بها درجة.','306','547','0');
INSERT INTO [t12842] VALUES (584,'إذا خلص المؤمنون حبسوا بقنطرة بين الجنة والنار يتقاضون مظالم كانت بينهم في الدنيا، حتى','307','547','0');
INSERT INTO [t12842] VALUES (585,'هذان سيدا كهول أهل الجنة من الأولين والآخرين إلا النبيين والمرسلين.','307','547','0');
INSERT INTO [t12842] VALUES (586,'أي الناس أفضل؟ فقال رسول الله , صلى الله عليه وسلم: مؤمن يجاهد في سبيل الله بنفسه','307','547','0');
INSERT INTO [t12842] VALUES (587,'أبايعك على أن لا تشرك بالله شيئا، وتقيم الصلاة، وتؤتي الزكاة، وتفارق المشرك، وتنصح','308','547','0');
INSERT INTO [t12842] VALUES (588,'لكل أمة أمين، وأمين هذه الأمة أبو عبيدة بن الجراح.','308','547','0');
INSERT INTO [t12842] VALUES (589,'لن ينجي أحدا منكم عمله. قال رجل من القوم: ولا إياك يا رسول الله؟ ! قال: ولا إياي إلا أن','308','547','0');
INSERT INTO [t12842] VALUES (590,'{لهم البشرى في الحياة الدنيا وفي الآخرة} [يونس: ٦٤] قال: " هي الرؤيا الصالحة يراها المسلم','309','547','0');
INSERT INTO [t12842] VALUES (591,'خصاء أمتي الصيام»','309','547','0');
INSERT INTO [t12842] VALUES (592,'ترهب أمتي الجلوس في المساجد، وانتظار الصلاة بعد الصلاة»','309','547','0');
INSERT INTO [t12842] VALUES (593,'سياحة أمتي الغزو في سبيل الله، والحج والعمرة»','309','547','0');
INSERT INTO [t12842] VALUES (594,'صدقتك يوما بيوم وتكفي نفسك وعيالك وترحم المسكين واليتيم وتطعمه أفضل من','310','547','0');
INSERT INTO [t12842] VALUES (595,'هجرة أمتي من هجر ما حرم الله عليه، وهاجر إلي في حياتي أو زار قبري بعد موتي، أو مات وله','310','547','0');
INSERT INTO [t12842] VALUES (596,'الرجل المسلم إذا غشي أهله فإن لم يكن من وقعته تلك ولد كان له وصيف في الجنة، وإن كان له من','310','547','0');
INSERT INTO [t12842] VALUES (597,'أحب اللحم وآكله إذا وجدته، ولو سألت ربي , عز وجل , أن يطعمنيه في كل يوم','311','547','0');
INSERT INTO [t12842] VALUES (598,'جبريل , عليه السلام , أمرني بالطيب غبا ويوم الجمعة لا مترك له، يا عثمان , لا ترغب عن','311','547','0');
INSERT INTO [t12842] VALUES (599,'من سره أن يمد له في عمره , ويوسع في رزقه , ويدفع عنه ميتة السوء , فليتق الله وليصل','311','547','0');
INSERT INTO [t12842] VALUES (600,'من شر الناس ذو الوجهين، يأتي هؤلاء بوجه، وهؤلاء بوجه.','311','547','0');
INSERT INTO [t12842] VALUES (601,'برمت بالناس وأخلاقهم ... فصرت أستأنس بالوحدة قال أبو الفضل: فزدت أنا هذا، فقلت: هذا لعمري','312','547','0');
INSERT INTO [t12842] VALUES (602,'" يغزون جزيرة العرب فيفتحها الله، ويغزون فارس فيفتحها الله، ثم يغزون الدجال فيفتحه الله','313','547','0');
INSERT INTO [t12842] VALUES (603,'يخرج إذا خرج للصيف ليلة الجمعة، وإذا دخل للشتاء دخل ليلة الجمعة.','313','547','0');
INSERT INTO [t12842] VALUES (604,'الصدقة أعظم أجرا؟ قال: " أن تصدق وأنت صحيح شحيح تخاف الفقر وتأمل البقاء، ولا تمهل حتى {إذا','314','547','0');
INSERT INTO [t12842] VALUES (605,'فلا يموتن بينكم أحد ما دمت بين أظهركم إلا آذنتموني؛ فإن صلاتي عليه','314','547','0');
INSERT INTO [t12842] VALUES (606,'قم , فابعث بعث النار من ذريتك، فيقول: أي رب، وما بعث النار؟ ! فيقول: من كل ألف تسع مائة','314','547','0');
INSERT INTO [t12842] VALUES (607,'مثل المؤمنين في توادهم وتراحمهم، كمثل الجسد إذا اشتكى بعضه تداعى كله بالسهر','315','547','0');
INSERT INTO [t12842] VALUES (608,'" أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، فإذا قالوها عصموا مني دماءهم وأموالهم','316','547','0');
INSERT INTO [t12842] VALUES (609,'من غسل ميتا فستره؛ ستره الله من الذنوب، ومن كفن مسلما؛ كساه الله من','316','547','0');
INSERT INTO [t12842] VALUES (610,'طويل الصمت قليل الضحك','316','547','0');
INSERT INTO [t12842] VALUES (611,'من لم تفته الركعة الأولى أربعين صباحا؛ كتب الله , عز وجل , له براءتين: براءة من النار،','316','547','0');
INSERT INTO [t12842] VALUES (612,'لا يخرج رجل شيئا من الصدقة حتى يفك عنها لحيي سبعين شيطانا.','319','547','0');
INSERT INTO [t12842] VALUES (613,'من صور صورة، كلف يوم القيامة أن ينفخ فيها، وليس بنافخ.','319','547','0');
INSERT INTO [t12842] VALUES (614,'إني عبد الله وخاتم النبيين، وإن آدم لمنجدل في طينته، وسأخبركم عن ذلك؛ أنا دعوة أبي','320','547','0');
