INSERT INTO [t12852] VALUES (1,'لما نزلت " {إن الله وملائكته يصلون على النبي يأيها الذين آمنوا صلوا عليه وسلموا تسليما}','1','0','0');
INSERT INTO [t12852] VALUES (2,'إذا توضأ أحدكم فأحسن وضوءه، ثم خرج عامدا إلى الصلاة، فلا يشبكن أصابعه فإنه في','1','0','0');
INSERT INTO [t12852] VALUES (3,'ألا أهدي لك هدية؟ قلنا: يا رسول الله، قد عرفنا كيف السلام عليك، فكيف الصلاة عليك؟ فقال:','2','0','0');
INSERT INTO [t12852] VALUES (4,'معقبات في دبر كل صلاة، لا يخيب قائلهن وفاعلهن: تسبيح الله ثلاثا وثلاثين، وتحميده ثلاثا','2','0','0');
INSERT INTO [t12852] VALUES (5,'خرجت مع رسول الله صلى الله عليه وسلم حاجا ولي وفرة، فقملت وأخذني الصبيان، فأتيت النبي صلى','2','0','0');
INSERT INTO [t12852] VALUES (6,'{فمن كان منكم مريضا أو به أذى من رأسه ففدية من صيام أو صدقة أو نسك} [البقرة: ١٩٦] ، قال:','3','0','0');
INSERT INTO [t12852] VALUES (7,'«أيؤذيك هوامك؟» قلت: نعم. قال: فأمرني أن أحلق رأسي، قال: ثم دعاني، فقرأ علي هذه الآية، وفي','3','0','0');
INSERT INTO [t12852] VALUES (8,'من كانت له أرض فليهبها أو ليعمرها "','4','0','0');
INSERT INTO [t12852] VALUES (9,'جبريل، أو ملكا جاء إلى النبي صلى الله عليه وسلم، فقال: ما تعدون من شهد بدرا فيكم؟ قال:','4','0','0');
INSERT INTO [t12852] VALUES (10,'دخلت على رسول الله صلى الله عليه وسلم وعندهم قدر تفور بلحم، فأعجبني شحمة فأخذتها','4','0','0');
INSERT INTO [t12852] VALUES (11,'أسفروا بالفجر فكلما أسفرتم كان أعظم للأجر أو أجركم»','4','0','0');
INSERT INTO [t12852] VALUES (12,'نهى عن المزارعة»','5','0','0');
INSERT INTO [t12852] VALUES (13,'لمن الزرع؟ قال: زرعي بيدي وعملي، لي الشطر ولبني فلان الشطر.','5','0','0');
INSERT INTO [t12852] VALUES (14,'أصيب رافع يوم أحد أو يوم خيبر بسهم في ثندوته وأتى النبي صلى الله عليه وسلم، فقال: يا رسول','5','0','0');
INSERT INTO [t12852] VALUES (15,'كري الأرض البيضاء بالذهب والفضة؟ فقال: حلال لا بأس به إنما نهي عن','6','0','0');
INSERT INTO [t12852] VALUES (16,'مر النبي صلى الله عليه وسلم على أرض رجل من الأنصار، وقد عرف أنه محتاج، فقال: لمن هذه','6','0','0');
INSERT INTO [t12852] VALUES (17,'كل مسكر حرام، إن على الله حتما أن لا يشربها أحد في الدنيا إلا سقاه الله عز وجل يوم','7','0','0');
INSERT INTO [t12852] VALUES (18,'يشير إلى الشرق ويقول: ها إن الفتنة هاهنا إن الفتنة هاهنا من حيث يطلع قرن','7','0','0');
INSERT INTO [t12852] VALUES (19,'عن بيع الولاء وعن هبته»','7','0','0');
INSERT INTO [t12852] VALUES (20,'لا تبيعوا التمر حتى يبدو صلاحه»','7','0','0');
INSERT INTO [t12852] VALUES (21,'من ابتاع طعاما فلا يبعه حتى يقبضه»','8','0','0');
INSERT INTO [t12852] VALUES (22,'أتي النبي صلى الله عليه وسلم بيهودي ويهودية قد أحدثا جميعا، فقال لهم: «ما تجدون في','8','0','0');
INSERT INTO [t12852] VALUES (23,'يخدع في البيوع، فقال له النبي صلى الله عليه وسلم: «» من بايعت فقل: لا','8','0','0');
INSERT INTO [t12852] VALUES (24,'كل بيعين لا بيع بينهما إلا أن يتفرقا إلا بيع الخيار','8','0','0');
INSERT INTO [t12852] VALUES (25,'اليد العليا خير من السفلى','8','0','0');
INSERT INTO [t12852] VALUES (26,'يقيم من أذن "','9','0','0');
INSERT INTO [t12852] VALUES (27,'لا يتعار من الليل ساعة إلا أجرى السواك على فيه»','9','0','0');
INSERT INTO [t12852] VALUES (28,'تدرون ما الشجرة الطيبة؟ فأردت أن أقول هي النخلة، فنظرت فإذا أنا أصغر القوم فسكت، فقال','9','0','0');
INSERT INTO [t12852] VALUES (29,'أقلوا التعري، فإن معكم من لا يفارقكم إلا عند الغائط، وإتيان الرجل أهله فأكرموهم','9','0','0');
INSERT INTO [t12852] VALUES (30,'اعتمر النبي صلى الله عليه وسلم أربع عمر، إحداها في رجب، فسمعنا حركة عائشة رضي الله عنها','9','0','0');
INSERT INTO [t12852] VALUES (31,'بم أهل رسول الله صلى الله عليه وسلم؟ قال: بالحج، فانصرف ثم جاء العام المقبل، فقال: بم أهل','10','0','0');
INSERT INTO [t12852] VALUES (32,'أحلت لنا ميتتان ودمان، فأما الميتتان فالحوت والجراد، وأما الدمان فالكبد','10','0','0');
INSERT INTO [t12852] VALUES (33,'بيع الخمر. فقال: وما بلغك حديث شربها وبيعها؟ أو قال: ثمنها ". حدثنا الحسين، حدثنا الفضل بن','10','0','0');
INSERT INTO [t12852] VALUES (34,'إذا اشتريت الذهب بالفضة فلا تفارقه وبينك وبينه لبس»','11','0','0');
INSERT INTO [t12852] VALUES (35,'ثلاثة قد حرم الله تعالى عليهم الجنة: مدمن الخمر، والعاق، والديوث، والديوث الذي يقر في','11','0','0');
INSERT INTO [t12852] VALUES (36,'صلاة الليل، فقال: «مثنى، فإذا خشيت أن يرهقك أو يدركك الصبح، فاركع ركعة توتر لك ما','11','0','0');
INSERT INTO [t12852] VALUES (37,'يستسقي فلا ينزل حتى يجيش كل ميزاب» . وأبيض يستسقي الغمام بوجهه ... ثمال اليتامى عصمة','11','0','0');
INSERT INTO [t12852] VALUES (38,'أدخلت السلاح حرم الله، ولم يكن يدخل»','12','0','0');
INSERT INTO [t12852] VALUES (39,'ثلاثة يوم القيامة على كثبان المسك يغبطون: رجل ينادي في كل يوم وليلة لخمس صلوات، وإمام أم','12','0','0');
INSERT INTO [t12852] VALUES (40,'انظر هل ترى في السماء من شيء؟ قال: قلت: نعم. قال: ما ترى؟ قال: قلت: أرى','12','0','0');
INSERT INTO [t12852] VALUES (41,'عثمان رضي الله عنه، فقال: كان من الذين آمنوا، ثم اتقوا، ثم آمنوا، ثم','12','0','0');
INSERT INTO [t12852] VALUES (42,'ما تزال الخصومة بين الناس يوم القيامة حتى خاصم الجسد الروح»','13','0','0');
INSERT INTO [t12852] VALUES (43,'كنا في غزوة فحبسنا المشركون عن صلاة الظهر والعصر والمغرب والعشاء، فكثر ذاك علي، فقلت: في','13','0','0');
INSERT INTO [t12852] VALUES (44,'من كذب علي متعمدا فليتبوأ مقعده من النار»','13','0','0');
INSERT INTO [t12852] VALUES (45,'ينام، يعني: ساجدا، وكان يعرف نومه بنفخه، ثم يقوم فيمضي في صلاته "','14','0','0');
INSERT INTO [t12852] VALUES (46,'كنت مع النبي صلى الله عليه وسلم في غار، إذ نزلت عليه: {والمرسلات عرفا} [المرسلات: ١] ،','14','0','0');
INSERT INTO [t12852] VALUES (47,'كانت ليلة بدر ليلة تسع عشرة»','14','0','0');
INSERT INTO [t12852] VALUES (48,'أحرم بالحج وطاف بين الصفا والمروة "','14','0','0');
INSERT INTO [t12852] VALUES (49,'إذا راح أحدكم إلى الجمعة فليغتسل»','14','0','0');
INSERT INTO [t12852] VALUES (50,'من نكث صفقته فلا حجة له يوم القيامة» و «من مات وهو مفارق للجماعة فميتته ميتة','15','0','0');
INSERT INTO [t12852] VALUES (51,'إذا رأى المطر قال: اللهم صبا صبا "','15','0','0');
INSERT INTO [t12852] VALUES (52,'لا إيمان لمن لا أمانة له، ولا صلاة لمن لا طهر له، ولا دين لمن لا صلاة له، وموضع الصلاة من','15','0','0');
INSERT INTO [t12852] VALUES (53,'من صلى من الليل فليجعل آخر صلاة وترا قبل الصبح» .','16','0','0');
INSERT INTO [t12852] VALUES (54,'يجعل فص خاتمه في باطن كفه»','16','0','0');
INSERT INTO [t12852] VALUES (55,'جبريل عليه السلام، إذا جاء بالوحي كان أول ما يلقي علي بسم الله الرحمن','16','0','0');
INSERT INTO [t12852] VALUES (56,'الشهر تسع وعشرون»','16','0','0');
INSERT INTO [t12852] VALUES (57,'أخدع في البيع.','16','0','0');
INSERT INTO [t12852] VALUES (58,'الله يحب المؤمن المحترف»','17','0','0');
INSERT INTO [t12852] VALUES (59,'لا تدعوا ركعتي الفجر فإن فيهما الرغائب»','17','0','0');
INSERT INTO [t12852] VALUES (60,'من دعي فلم يجب فقد عصى الله ورسوله','17','0','0');
INSERT INTO [t12852] VALUES (61,'يتزعفر الرجل»','18','0','0');
INSERT INTO [t12852] VALUES (62,'ضالة المسلم حرق النار»','18','0','0');
INSERT INTO [t12852] VALUES (63,'حفظت سكتتين في الصلاة: سكتة إذا كبر الإمام حتى يقرأ، وسكتة إذا فرغ من قراءته ": قال: فأنكر','18','0','0');
INSERT INTO [t12852] VALUES (64,'ركعتي الفجر: هما أحب إلي من الدنيا وما فيها "','18','0','0');
INSERT INTO [t12852] VALUES (65,'كنا مع نبينا صلى الله عليه وسلم ووجهنا واحد، فلما قبض نظرنا هكذا وهكذا»','19','0','0');
INSERT INTO [t12852] VALUES (66,'أبرأ إلى كل خليل من خلة، ولو كنت متخذا خليلا لاتخذت أبا بكر خليلا، إن صاحبكم خليل الله','19','0','0');
INSERT INTO [t12852] VALUES (67,'من أحب الناس إليك؟ قال: عائشة.','19','0','0');
INSERT INTO [t12852] VALUES (68,'لما عافى الله أيوب عليه السلام أمطر عليه جرادا من ذهب، فجعل يجمعه في يومه، فقيل: يا أيوب،','20','0','0');
INSERT INTO [t12852] VALUES (69,'مثل المؤمن مثل النحلة، لا تأكل إلا طيبا ولا تضع إلا طيبا»','20','0','0');
INSERT INTO [t12852] VALUES (70,'خاتم الذهب»','20','0','0');
INSERT INTO [t12852] VALUES (71,'رجل يجد في قدميه ضربانا، فقال: اخضبهما بالحناء، وألق في الحناء شيئا من','20','0','0');
INSERT INTO [t12852] VALUES (72,'إذا كان إزارك واسعا فتوشح به، وإذا كان ضيقا فاتزر به وصل» .','21','0','0');
INSERT INTO [t12852] VALUES (73,'خرجنا مع رسول الله صلى الله عليه وسلم فمنا من أفرد الحج، ومنا من قرن، ومنا من تمتع، فخرج','21','0','0');
INSERT INTO [t12852] VALUES (74,'من علم أن الصلاة عليه حق واجب أو حق مكتوب دخل الجنة» .','21','0','0');
INSERT INTO [t12852] VALUES (75,'إذا رأيتم المداحين فاحثوا في وجوههم» .','22','0','0');
INSERT INTO [t12852] VALUES (76,'لما رجم علي عليه السلام المرأة دعا أولياءها، فقال: هذا ابنكم ترثونه ولا يرثكم، فإن جنى','22','0','0');
INSERT INTO [t12852] VALUES (77,'مررت بعجوز بالربذة منقطع بها من بني تميم، فقالت: أين تريدون؟ فقلت: نريد النبي صلى الله','22','0','0');
INSERT INTO [t12852] VALUES (78,'كلمات الفرج: «لا إله إلا الله الحكيم العليم الرحيم، لا إله إلا الله الحكيم الكريم، لا إله','24','0','0');
INSERT INTO [t12852] VALUES (79,'من قال: لا إله إلا الله لا يتخذها جنة لشيء من الكبائر يرتكبه، فهو من أهل الجنة','24','0','0');
INSERT INTO [t12852] VALUES (80,'من قال: لا إله إلا الله أنجته يوما من الدهر أصابه قبلها ما أصابه "','24','0','0');
INSERT INTO [t12852] VALUES (81,'الإيمان معرفة بالقلب، وإقرار باللسان، وعمل بالأبدان» .','24','0','0');
INSERT INTO [t12852] VALUES (82,'{كزرع} [الفتح: ٢٩] ، قال: أصل الزرع عبد المطلب، {أخرج شطأه} [الفتح: ٢٩] : محمد صلى الله','25','0','0');
INSERT INTO [t12852] VALUES (83,'طلع أبو بكر وعمر، فلما نظر إليهما رسول الله صلى الله عليه وسلم، قال: وهذان السمع','25','0','0');
