INSERT INTO [t12872] VALUES (1,'مختصر قيام الليل','1','0','0');
INSERT INTO [t12872] VALUES (2,'قيام الليل لمحمد بن نصر المروزي بسم الله الرحمن الرحيم، الحمد لله رب العالمين، وصلاته وسلامه على محمد خاتم النبيين وآله أجمعين. أما بعد، فإني اختصرت في هذا الجزء كتاب قيام الليل، تأليف الإمام أبي عبد الله محمد بن نصر المروزي رحمه الله، على أني أحذف','2','1','0');
INSERT INTO [t12872] VALUES (3,'باب حكم قيام الليل قال أبو عبد الله محمد بن نصر المروزي بسمرقند: قال الله تبارك وتعالى: يا أيها المزمل، قم الليل إلا قليلا، نصفه أو انقص منه قليلا، أو زد عليه ورتل القرآن ترتيلا، إنا سنلقي عليك قولا ثقيلا','3','1','0');
INSERT INTO [t12872] VALUES (4,'ذكر الترغيب في قيام الليل من كتاب الله عز وجل قال الله تبارك وتعالى لنبيه صلى الله عليه وسلم ومن الليل فتهجد به نافلة لك عسى أن يبعثك ربك مقاما محمودا وقال: واذكر اسم ربك بكرة وأصيلا ومن الليل فاسجد له وسبحه ليلا طويلا. وقال: ومن الليل فسبحه وأدبار','12','1','0');
INSERT INTO [t12872] VALUES (5,'باب ما جاء في قوله تتجافى جنوبهم عن المضاجع','18','1','0');
INSERT INTO [t12872] VALUES (6,'ذكر من قال التجافي عن المضاجع هي الصلاة بين المغرب والعشاء قال أنس بن مالك في قول الله تتجافى جنوبهم عن المضاجع قال: يصلون ما بين هاتين الصلاتين، المغرب والعشاء وعن عبد الله بن عيسى: كان ناس من الأنصار يصلون ما بين المغرب والعشاء فنزلت فيهم: تتجافى','22','1','0');
INSERT INTO [t12872] VALUES (7,'ذكر من قال التجافي عن المضاجع هي صلاة العشاء عن أم سلمة: تتجافى جنوبهم عن المضاجع قالت: عن صلاة العتمة وعن أنس بن مالك: انتظار الصلاة التي تدعى العتمة قال محمد بن نصر: والأخبار التي ذكرناها عن معاذ بن جبل عن النبي صلى الله عليه وسلم تدل على خلاف هذه','23','1','0');
INSERT INTO [t12872] VALUES (8,'باب ما جاء عن النبي صلى الله عليه وسلم ومن بعده في الترغيب في قيام الليل وفضيلته','34','1','0');
INSERT INTO [t12872] VALUES (9,'باب الركعتين قبل المغرب قال الله تعالى: وأتموا الصيام إلى الليل فأجمع أهل العلم على أن الشمس إذا غربت فقد دخل الليل وحل فطر الصائم وجاء الخبر عن النبي صلى الله عليه وسلم بأنه نهى عن الصلاة بعد العصر حتى تغرب الشمس. فإذا غربت الشمس فقد حلت الصلاة،','55','1','0');
INSERT INTO [t12872] VALUES (10,'ذكر من لم يركعهما عن النخعي قال: " كان بالكوفة من خيار أصحاب النبي صلى الله عليه وسلم علي بن أبي طالب رضي الله عنه، وعبد الله بن مسعود رضي الله عنه، وحذيفة بن اليمان رضي الله عنه، وأبو مسعود الأنصاري رضي الله عنه، وعمار بن ياسر رضي الله عنه،','61','1','0');
INSERT INTO [t12872] VALUES (11,'باب الركعتين بعد المغرب قال عمر بن الخطاب رضي الله عنه وأدبار السجود قال: " ركعتين بعد المغرب، وإدبار النجوم ركعتين قبل الفجر " وعن علي بن أبي طالب رضي الله عنه " أدبار السجود الركعتان بعد المغرب، وأدبار النجوم ركعتا الفجر " وعن الحسن بن علي رضي الله','63','1','0');
INSERT INTO [t12872] VALUES (12,'باب اختيار ركوع الركعتين بعد المغرب في البيت','68','1','0');
INSERT INTO [t12872] VALUES (13,'باب تعجيل الركعتين بعد المغرب عن ابن عباس رضي الله عنه قال: " التقى ملكان في صلاة المغرب، فقال أحدهما لصاحبه: اصعد بنا، فقال: إن صاحبي لم يصل، قال: فمن أجل ذلك نكره أن يؤخر المغرب "','79','1','0');
INSERT INTO [t12872] VALUES (14,'باب ما يستحب أن يقرأ في الركعتين بعد المغرب','82','1','0');
INSERT INTO [t12872] VALUES (15,'باب إطالة الركعتين بعد المغرب','85','1','0');
INSERT INTO [t12872] VALUES (16,'باب الترغيب في الصلاة ما بين المغرب والعشاء سوى الركعتين عن عبد الله بن عيسى رحمه الله: " كان ناس من الأنصار يصلون ما بين المغرب والعشاء فنزلت فيهم تتجافى جنوبهم عن المضاجع " وعن أنس بن مالك رضي الله عنه في قول الله تعالى: تتجافى جنوبهم عن المضاجع،','88','1','0');
INSERT INTO [t12872] VALUES (17,'باب الركعتين بعد العشاء','98','1','0');
INSERT INTO [t12872] VALUES (18,'باب ركوع الركعتين في البيت','100','1','0');
INSERT INTO [t12872] VALUES (19,'باب ما يستحب أن يقرأ فيهما عن علي بن أبي طالب رضي الله عنه: " ما أرى رجلا ولد في الإسلام وأدرك عقله الإسلام يبيت أبدا حتى يقرأ هذه الآية: الله لا إله إلا هو الحي القيوم لو تعلمون ما فيهما إنما أعطيها نبيكم من كنز تحت العرش، ولم يعطها أحد قبل نبيكم،','103','1','0');
INSERT INTO [t12872] VALUES (20,'باب الأربع ركعات بعد العشاء الآخرة','104','1','0');
INSERT INTO [t12872] VALUES (21,'باب أوقات الليل التي يستحب قيامها ويرجى إجابة الدعاء فيها','108','1','0');
INSERT INTO [t12872] VALUES (22,'باب الاستغفار بالأسحار والصلاة فيها قال الله تعالى: وبالأسحار هم يستغفرون وقال: والمستغفرين بالأسحار عن نافع أن ابن عمر كان يحيي الليل ثم يقول: يا نافع أأسحرنا؟، فأقول: لا فيعاود الصلاة، فإذا قلت: نعم، قعد يستغفر الله ويدعو حتى يصبح " وعن ابن','119','1','0');
INSERT INTO [t12872] VALUES (23,'باب إيقاظ الرجل أهله ومن يليه، والمرأة زوجها لقيام الليل','126','1','0');
INSERT INTO [t12872] VALUES (24,'باب ما يعاقب به تارك قيام الليل','129','1','0');
INSERT INTO [t12872] VALUES (25,'باب الاستعانة بقائلة النهار على قيام الليل','132','1','0');
INSERT INTO [t12872] VALUES (26,'باب إذا اعتاد الرجل قيام الليل نبه لذلك قال ابن مسعود رضي الله عنه: " إذا نام الرجل وهو يريد القيام من الليل أيقظه إما سنور، وإما صبي، وإما شيء فيستيقظ فيفتح عينيه وقد وكل به قرينان، قرين سوء وقرين صالح، فيقول قرين السوء: افتح بشر، نم إن عليك ليلا','134','1','0');
INSERT INTO [t12872] VALUES (27,'باب ما يبدأ به من ذكر الله عند الانتباه من النوم','136','1','0');
INSERT INTO [t12872] VALUES (28,'باب السواك عند الوضوء لقيام الليل','144','1','0');
INSERT INTO [t12872] VALUES (29,'باب الاغتسال لقيام الليل والتطيب ولبس الثياب الحسنة كان عبد الله بن زكريا رحمه الله وأصحابه يغتسلون كل ليلة بعد العشاء للعبادة','149','1','0');
INSERT INTO [t12872] VALUES (30,'باب ما يفتتح به قيام الليل من الذكر والدعاء','152','1','0');
INSERT INTO [t12872] VALUES (31,'باب كراهة السمر بعد العشاء','158','1','0');
INSERT INTO [t12872] VALUES (32,'باب إباحة السمر بعد العشاء لمذاكرة العلم أو في أمر من أمور المسلمين','162','1','0');
INSERT INTO [t12872] VALUES (33,'باب عدد صلاة النبي صلى الله عليه وسلم بالليل','166','1','0');
INSERT INTO [t12872] VALUES (34,'نوع آخر من صلاة رسول الله صلى الله عليه وسلم','170','1','0');
INSERT INTO [t12872] VALUES (35,'نوع ثالث من صلاة رسول الله صلى الله عليه وسلم','174','1','0');
INSERT INTO [t12872] VALUES (36,'نوع رابع من صلاة النبي صلى الله عليه وسلم','176','1','0');
INSERT INTO [t12872] VALUES (37,'باب اختيار النبي صلى الله عليه وسلم لأن يصلي من الليل مثنى مثنى','180','1','0');
INSERT INTO [t12872] VALUES (38,'باب افتتاح النبي صلى الله عليه وسلم صلاته من الليل بركعتين خفيفتين','184','1','0');
INSERT INTO [t12872] VALUES (39,'باب الاختيار لطول القيام في صلاة الليل','189','1','0');
INSERT INTO [t12872] VALUES (40,'باب الترتيل في القراءة عن حفصة رضي الله عنها: كان رسول الله صلى الله عليه وسلم يقرأ بالسورة فيرتلها حتى تكون أطول من أطول منها " وفي رواية: كان يصلي في سبحته ويرتل السورة حتى تكون قراءته أطول من أطول منها " وعن يعلى بن مملك: أنه سأل أم سلمة عن قراءة','193','1','0');
INSERT INTO [t12872] VALUES (41,'باب الجهر بالقراءة في صلاة الليل عن أم هانئ رضي الله عنها قالت: " كنت أسمع قراءة النبي صلى الله عليه وسلم من الليل وأنا على عريش أهلي " وكان أبو هريرة رضي الله عنه " إذا قرأ رفع طورا وخفض طورا، وذكر أنها قراءة رسول الله صلى الله عليه وسلم " وفي حديث','195','1','0');
INSERT INTO [t12872] VALUES (42,'باب مد الصوت بالقراءة عن قتادة: سألت أنسا رضي الله عنه: كيف كانت قراءة رسول الله صلى الله عليه وسلم؟ قال: " كانت مدا، ثم قرأ: بسم الله الرحمن الرحيم يمد " بسم الله "، ويمد " الرحمن "، ويمد ب " الرحيم " وقال مجاهد رحمه الله، وطاوس رحمه الله: "','202','1','0');
INSERT INTO [t12872] VALUES (43,'باب الترجيع في القراءة','203','1','0');
INSERT INTO [t12872] VALUES (44,'باب تحزين الصوت بالقراءة وتحسينه','206','1','0');
INSERT INTO [t12872] VALUES (45,'باب التغني بالقرآن والاستغناء به','213','1','0');
INSERT INTO [t12872] VALUES (46,'باب نزول الملائكة والسكينة وحضور عمار الدار صلاة المصلي بالليل لاستماع القرآن','217','1','0');
INSERT INTO [t12872] VALUES (47,'باب الوقوف عند آية الرحمة والعذاب، والدعاء عند ذلك عن ابن عمر رضي الله عنه: " إذا قرأت قل أعوذ برب الفلق فقل أعوذ برب الفلق، وإذا قرأت قل أعوذ برب الناس فقل أعوذ برب الناس " وعن الحسن رحمه الله أنه كان إذا مر بالآية فيها تخويف أو ترغيب وقف فتعوذ وسأل','220','1','0');
INSERT INTO [t12872] VALUES (48,'باب البكاء عند قراءة القرآن تقدم قوله: " إن هذا القرآن نزل بحزن فإذا قرأتموه فابكوا " إلى آخره','221','1','0');
INSERT INTO [t12872] VALUES (49,'باب ترديد المصلي الآية مرة بعد مرة، يتدبر ما فيها','230','1','0');
INSERT INTO [t12872] VALUES (50,'باب الجمع بين السور في ركعة عن عبد الله بن شقيق رحمه الله قال: سألت عائشة رضي الله عنها، أكان رسول الله صلى الله عليه وسلم يجمع بين السور؟ قالت: " نعم من المفصل " وعن ابن مسعود رضي الله عنه أن رجلا جاءه فقال: إني لأقرأ المفصل في ركعة، فقال عبد الله','232','1','0');
INSERT INTO [t12872] VALUES (51,'باب كراهة تقطيع السورة والجمع بين السور في ركعة','233','1','0');
INSERT INTO [t12872] VALUES (52,'باب قيام الليلة كلها وختم القرآن فيها تقدم قول عائشة رضي الله عنها: " لا أعلم نبي الله صلى الله عليه وسلم قرأ القرآن في ليلة، ولا قام ليلة حتى أصبح " وقول أنس رضي الله عنه: " ما كنا نشاء أن نراه من الليل مصليا إلا رأيناه، ولا نراه نائما إلا رأيناه "','237','1','0');
INSERT INTO [t12872] VALUES (53,'باب أكثر ما يختم فيه القرآن، وأقله من عدد الليالي','238','1','0');
INSERT INTO [t12872] VALUES (54,'باب ما يكفي من القرآن بالليل','242','1','0');
INSERT INTO [t12872] VALUES (55,'باب ما جاء في فضل قراءة تبارك الذي بيده الملك','253','1','0');
INSERT INTO [t12872] VALUES (56,'باب ثواب القراءة بالليل','257','1','0');
INSERT INTO [t12872] VALUES (57,'باب ما يقال في ركوع صلاة الليل وسجودها وفيما بين ذلك','298','1','0');
INSERT INTO [t12872] VALUES (58,'باب ذكر كراهة الصلاة مع النعاس والفتور','313','1','0');
INSERT INTO [t12872] VALUES (59,'باب من كانت له صلاة من الليل فغلب عليها بنوم أو غيره','320','1','0');
INSERT INTO [t12872] VALUES (60,'باب ذكر قضاء الرجل ما يفوته من قراءة الليل في صلاة النهار','324','1','0');
INSERT INTO [t12872] VALUES (61,'باب كراهة التطوع بعد طلوع الفجر سوى الركعتين','330','1','0');
INSERT INTO [t12872] VALUES (62,'باب ذكر صلاة الليل في السفر','334','1','0');
INSERT INTO [t12872] VALUES (63,'باب ذكر صلاة التطوع قاعدا','340','1','0');
INSERT INTO [t12872] VALUES (64,'باب ذكر صلاة التطوع قائما','344','1','0');
INSERT INTO [t12872] VALUES (65,'باب ذكر كيفية جلوس المصلي قاعدا في حال قراءته قال أبو عبد الله لم يأت في شيء من الأخبار التي رويناها عن النبي صلى الله عليه وسلم أنه صلى جالسا صفة جلوسه كيف كانت إلا في حديث روي عن حفص بن غياث أخطأ فيه حفص رواه عنه أبو داود الحفري عن حميد، عن عبد الله','356','1','0');
INSERT INTO [t12872] VALUES (66,'باب ذكر التربع في الصلاة عمن رخص فيه أو اختاره أو فعله من عذر سماك رحمه الله: رأيت ابن عمر رضي الله عنه وابن عباس رضي الله عنه متربعين في الصلاة. أبو رحال بن عبيد رحمه الله: رأيت أنس بن مالك رضي الله عنه يصلي متربعا في مسجد الكوفة مجاهد رحمه الله','358','1','0');
INSERT INTO [t12872] VALUES (67,'باب ذكر من كره التربع في الصلاة ابن مسعود رضي الله عنه: " لأن أجلس على الرضف أحب إلي من أن أجلس متربعا في صلاتي " وفي آخر: " لأن أقعد على جمرتين أحب إلي من أن أصلي متربعا " عبد الله بن عبد الله بن عمر رضي الله عنه: أنه كان يرى عبد الله بن عمر رضي الله','359','1','0');
INSERT INTO [t12872] VALUES (68,'باب ذكر من صلى محتبيا الزهري: رأيت سعيد بن المسيب يصلي محتبيا هشام بن عروة رحمه الله: " رأيت أبي يصلي محتبيا يقرأ " طلحة بن يحيي رحمه الله: " رأيت أبا بكر بن عبد الرحمن يصلي محتبيا، ورأيت عيسى بن طلحة يفعله " الحسن بن عمرو، عن أبيه: " رأيت سعيد بن','360','1','0');
INSERT INTO [t12872] VALUES (69,'باب من رأى أن يجلس كجلوسه في التشهد عاصم عن ابن سيرين: " أنه كان إذا صلى قاعدا كان قعوده مثل جلسته في الصلاة " وعن مجاهد رحمه الله " علمني سعيد بن جبير صلاة القاعد، فقال: يتربع إن شاء، وإن شاء ثنى رجليه وإن شاء نصب اليمنى وثنى اليسرى " وعن ابن أبي','361','1','0');
INSERT INTO [t12872] VALUES (70,'باب من صلى متكئا حميد الطويل رحمه الله: " رأيت بكرا يصلي متربعا ومتكئا "','362','1','0');
INSERT INTO [t12872] VALUES (71,'باب من صلى جالسا على دكان مدليا رجليه " كان لأبي برزة دكان يجلس عليه ويدلي رجليه ويصلي " قال محمد بن نصر: وأما من اختار أن يجلس المصلي قاعدا في حال قراءته كجلوسه للتشهد وبين السجدتين، فإنه ذهب إلى أن الجلوس للتشهد وبين السجدتين قد سنه رسول الله صلى','363','1','0');
INSERT INTO [t12872] VALUES (72,'باب ذكر كيفية ركوع المحتبي والمتربع وسجودهما اختلف أهل العلم في ذلك، فعن أبي حفص رحمه الله: " رأيت أنسا رضي الله عنه يصلي متربعا فإذا أراد أن يركع أو يسجد ثنى رجليه " عن سعيد بن جبير: " إذا صلى متربعا وأراد أن يركع أو يسجد ثنى رجليه " مجاهد رحمه الله','364','1','0');
INSERT INTO [t12872] VALUES (73,'مختصر قيام رمضان','365','0','0');
INSERT INTO [t12872] VALUES (74,'باب ذكر الصلاة تطوعا بالليل والنهار في جماعة','366','73','0');
INSERT INTO [t12872] VALUES (75,'باب الترغيب في قيام رمضان وفضيلته','371','73','0');
INSERT INTO [t12872] VALUES (76,'باب صلاة النبي صلى الله عليه وسلم جماعة ليلا تطوعا في شهر رمضان','375','73','0');
INSERT INTO [t12872] VALUES (77,'باب عدد الركعات التي يقوم بها الإمام للناس في رمضان تقدم حديث جابر رضي الله عنه أن النبي صلى الله عليه وسلم " صلى في رمضان في ليلة ثمان ركعات ثم أوتر " وعن السائب بن يزيد: " أمر عمر بن الخطاب رضي الله عنه أبي بن كعب رضي الله عنه وتميما الداري رضي الله','386','73','0');
INSERT INTO [t12872] VALUES (78,'باب مقدار القراءة في كل ركعة في قيام رمضان السائب بن يزيد: " أمر عمر بن الخطاب رضي الله عنه أبي بن كعب رضي الله عنه وتميما الداري رضي الله عنه أن يقوما للناس في رمضان فكان القارئ يقرأ بالمئين , حتى كنا نعتمد على العصي من طول القيام وما كنا ننصرف إلا في','389','73','0');
INSERT INTO [t12872] VALUES (79,'باب اختيار قيام آخر الليل على أوله تقدم قول عمر بن الخطاب رضي الله عنه: " والتي تنامون عنها أفضل من التي تقومون، يريد آخر الليل، وكان الناس يقومون أوله وينامون آخره. طاوس رحمه الله: سمع ابن عباس رضي الله عنه يقول: " دعاني عمر رضي الله عنه أتغدى','391','73','0');
INSERT INTO [t12872] VALUES (80,'باب حضور النساء الجماعة في قيام رمضان تقدم قول جابر رضي الله عنه: جاء أبي رضي الله عنه فقال: يا رسول الله كان مني الليلة شيء. الحديث وعن هشام بن عروة , عن أبيه جعل عمر بن الخطاب رضي الله عنه للناس قارئين , فكان أبي بن كعب رضي الله عنه يصلي بالرجال ,','392','73','0');
INSERT INTO [t12872] VALUES (81,'باب من كره أن يؤم الرجل النساء العلاء بن المسيب رحمه الله: قلت لحماد بن أبي سليمان رحمه الله: أقوم بأهلي في رمضان؟ قال: " لا إلا أن يكون معك رجل أرأيت إن أحدثت وليس معك رجل من تقدم؟ "','393','73','0');
INSERT INTO [t12872] VALUES (82,'باب المرأة تؤم النساء في قيام رمضان وغيره','394','73','0');
INSERT INTO [t12872] VALUES (83,'باب من كره أن تؤم المرأة النساء قال ابن عون: كتبت إلى نافع أسأله عن المرأة تؤم النساء , فكتب: " إن المرأة لا تؤم النساء " وعن مالك رحمه الله: " لا ينبغي للمرأة أن تؤم أحدا , وقد كان أزواج النبي صلى الله عليه وسلم والمهاجرات فما أمت امرأة قط أحدا ولا','396','73','0');
INSERT INTO [t12872] VALUES (84,'باب ذكر من اختار الصلاة وحده على القيام مع الناس إذا كان حافظا للقرآن تقدم صلاة النبي صلى الله عليه وسلم في بيته','398','73','0');
INSERT INTO [t12872] VALUES (85,'باب الإمام يؤم في القيام يقرأ في المصحف تقدم أن عائشة رضي الله عنها كان يؤمها غلام لها في المصحف وكان يقال له ذكوان في رمضان بالليل. وسئل ابن شهاب رحمه الله عن الرجل يؤم الناس في رمضان في المصحف قال: " ما زالوا يفعلون ذلك منذ كان الإسلام , كان خيارنا','401','73','0');
INSERT INTO [t12872] VALUES (86,'باب من كره أن يؤم في المصحف الأعمش عن إبراهيم رحمه الله: كانوا يكرهون أن يؤم الرجل في المصحف كراهية أن يتشبهوا بأهل الكتاب " ليث عن مجاهد رحمه الله أنه كره أن يؤم الرجل في المصحف " " ومر سليمان بن حنظلة بقوم يؤمهم رجل في مصحف في رمضان على مشجب فرمى به "','402','73','0');
INSERT INTO [t12872] VALUES (87,'باب التعوذ عند القراءة في قيام رمضان قال ابن شهاب رحمه الله: " ما زال القراء في رمضان حين يصلون إذا ختموا أم القرآن يستعيذون من الشيطان فيرفعون أصواتهم في كل ركعة: نعوذ بك من الشيطان الرجيم , إنك أنت السميع العليم , سبحانك رب العالمين , بسم الله الرحمن','404','73','0');
INSERT INTO [t12872] VALUES (88,'باب ما يبدأ به في أول ليلة من القرآن من قيام رمضان قال أبو حازم رحمه الله " كان أهل المدينة إذا دخل رمضان يبدؤن في أول ليلة بإنا فتحنا لك فتحا مبينا "','406','73','0');
INSERT INTO [t12872] VALUES (89,'باب الإنصات لقراءة الإمام في التراويح قال ابن جريج رحمه الله: قلت لعطاء: أبلغك أنه يجزئ الإمام عمن وراءه في استعاذة أو تكبير أو تشهد أو شيء إلا القراءة؟ قال: " ما بلغني أنه يجزئ عمن وراءه في شيء إلا في القراءة. قال عطاء: إذا سمعوا قراءته وعقلوها','407','73','0');
INSERT INTO [t12872] VALUES (90,'باب التغني بالقرآن في قيام رمضان عن نوفل ابن إياس الهذلي رحمه الله قال: كان الناس يقومون في رمضان في المسجد فكانوا إذا سمعوا قارئا حسن القراءة مالوا إليه , فقال عمر بن الخطاب رضي الله عنه: " قد اتخذوا القرآن أغاني والله لئن استطعت لأغيرن هذا فلم تمر','408','73','0');
INSERT INTO [t12872] VALUES (91,'باب من كره الصلاة بين التراويح قال بحير بن ريسان: رأيت عبادة بن الصامت رضي الله عنه " يزجر أناسا يصلون بعد تراويح الإمام في رمضان , فلما أبوا أن يطيعوه قام إليهم فضربهم " وكان عقبة بن عامر " يوكل بالناس في رمضان رجالا يمنعونهم من السبحة بين الأشفاع لئلا','410','73','0');
INSERT INTO [t12872] VALUES (92,'باب من رخص في الصلاة بين التراويح سئل الزهري رحمه الله عن الصلاة في قيام رمضان بين الأشفاع , فقال: إن قويت على ذلك فافعله وكان عامر بن عبد الله بن الزبير , وأبوعمرو , وسعيد بن عبد العزيز , والليث بن سعد , وابن جابر , وبكر بن مضر , وأبو بكر بن حزم ,','411','73','0');
INSERT INTO [t12872] VALUES (93,'باب إمامة الغلام الذي لم يحتلم في قيام رمضان وغيره','412','73','0');
INSERT INTO [t12872] VALUES (94,'باب التعقيب وهو رجوع الناس إلى المسجد بعد انصرافهم عنه سعيد , عن الحسن وقتادة أنهما كانا يكرهان التعقيب في رمضان قال سعيد: وهو رجوع الناس إلى المسجد بعدما ينصرفون " قتادة رحمه الله , عن أنس رضي الله عنه: أنه كان لا يرى بأسا بالتعقيب في رمضان , وقال','418','73','0');
INSERT INTO [t12872] VALUES (95,'باب أخذ الأجر على الإمامة في رمضان','419','73','0');
INSERT INTO [t12872] VALUES (96,'باب قيام رمضان في أرض الحرب','421','73','0');
INSERT INTO [t12872] VALUES (97,'باب الاجتهاد في العشر الأواخر من رمضان','423','73','0');
INSERT INTO [t12872] VALUES (98,'باب الترغيب في ليلة القدر وتفضيل العمل فيها على العمل في سائر السنة قال الله تعالى: إنا أنزلناه في ليلة القدر , وما أدراك ما ليلة القدر , ليلة القدر خير من ألف شهر عن مالك: سمعت من أثق به أن النبي صلى الله عليه وسلم أري أعمار الناس أو ما شاء الله من','427','73','0');
INSERT INTO [t12872] VALUES (99,'باب طلب ليلة القدر في العشر الأواخر','432','73','0');
INSERT INTO [t12872] VALUES (100,'باب التماس ليلة القدر في الوتر من العشر الأواخر','434','73','0');
INSERT INTO [t12872] VALUES (101,'باب طلب ليلة القدر ليلة إحدى وعشرين','443','73','0');
INSERT INTO [t12872] VALUES (102,'باب طلبها في ليلة أربع وعشرين','445','73','0');
INSERT INTO [t12872] VALUES (103,'باب طلبها في ليلة سبعة عشرين','449','73','0');
INSERT INTO [t12872] VALUES (104,'باب طلبها في ليلة سابع عشرة وتاسع عشرة عن ابن مسعود رضي الله عنه: " التمسوا ليلة القدر لسبع عشرة خلت من رمضان صبيحة يوم بدر , يوم الفرقان , يوم التقى الجمعان , وواحد وعشرين وثلاث وعشرين فإنها لا تكون إلا في وتر " وفي لفظ: " التمسوها في سبع عشرة أو تسع','451','73','0');
INSERT INTO [t12872] VALUES (105,'باب أمارات ليلة القدر','452','73','0');
INSERT INTO [t12872] VALUES (106,'باب ما يدعى به في ليلة القدر','456','73','0');
INSERT INTO [t12872] VALUES (107,'باب الترغيب في الدعاء عند ختم القرآن','458','73','0');
INSERT INTO [t12872] VALUES (108,'باب قيام ليلة العيد هارون بن عبيد الله الأسلمي رحمه الله: " بلغني أنه من أحيا ليلة العيد لم يمت قلبه يوم تموت القلوب " أبو أمامة رضي الله عنه: " من قام ليلة العيد إيمانا واحتسابا لم يمت قلبه حين تموت القلوب " وعن ابن المبارك مثله. وعن مجاهد: " ليلة','462','73','0');
INSERT INTO [t12872] VALUES (109,'باب من صلى ليلة القدر العشاء في الجماعة عن عبد الله بن عمرو: " من صلى العشاء الآخرة أصاب ليلة القدر " وعن الضحاك: " من صلى المغرب والعشاء في مسجد جماعة في رمضان فقد أصاب من ليلة القدر حظا وافيا " والله أعلم','465','73','0');
INSERT INTO [t12872] VALUES (110,'مختصر كتاب الوتر','466','0','0');
INSERT INTO [t12872] VALUES (111,'باب الترغيب في الوتر والحث عليه','467','110','0');
INSERT INTO [t12872] VALUES (112,'باب الأخبار الدالة على أن الوتر سنة وليس بفرض قال أبو عبد الله محمد بن نصر: افترض الله الصلاة على النبي صلى الله عليه وسلم وأمته أول ما افترض ليلة أسري به خمس صلوات في اليوم والليلة، فأخبر النبي صلى الله عليه وسلم بذلك أمته ثم لم يزل بعد هجرته وقدومه','473','110','0');
INSERT INTO [t12872] VALUES (113,'باب وقت الوتر أوله وآخره تقدم قوله: " إن الله أمدكم بصلاة هي خير من حمر النعم ". وقوله: " ما بين صلاة العشاء إلى طلوع الفجر ". وساقه هنا من عدة طرق. ثم قال محمد بن نصر: قد اختلفت ألفاظ متون هذه الأخبار التي جاءت عن النبي صلى الله عليه وسلم أنه قال','490','110','0');
INSERT INTO [t12872] VALUES (114,'باب الأوقات التي أوتر النبي صلى الله عليه وسلم فيها من الليل','492','110','0');
INSERT INTO [t12872] VALUES (115,'باب اختيار الوتر في آخر الليل لمن قوي عليه','494','110','0');
INSERT INTO [t12872] VALUES (116,'- باب اختيار الوتر أول الليل لمن خاف أن لا يقوم آخره تقدم قوله: " من خاف منكم أن لا يستيقظ آخر الليل فليوتر أول الليل " الحديث','499','110','0');
INSERT INTO [t12872] VALUES (117,'باب وتر النبي صلى الله عليه وسلم بركعة','503','110','0');
INSERT INTO [t12872] VALUES (118,'باب اختيار النبي صلى الله عليه وسلم التسليم بين كل ركعتين من صلاة الليل، والوتر بركعة','511','110','0');
INSERT INTO [t12872] VALUES (119,'باب الأخبار المروية عن السلف في الوتر بركعة','513','110','0');
INSERT INTO [t12872] VALUES (120,'باب الوتر بخمس ركعات بتسليمة واحدة','519','110','0');
INSERT INTO [t12872] VALUES (121,'باب الوتر بسبع وتسع تقدم حديث سعد بن هشام عن عائشة وفيه: " فيصلي تسع ركعات لا يجلس فيهن إلا عند الثامنة، فيحمد ربه، ويذكره، ويدعو، ثم ينهض ولا يسلم، ثم يصلي التاسعة فيقعد، ثم يحمد ربه ويذكره ويدعو، ثم يسلم تسليما يسمعنا ويصلي ركعتين بعدما يسلم،','523','110','0');
INSERT INTO [t12872] VALUES (122,'باب تخيير الموتر بين الواحدة والثلاث والخمس','528','110','0');
INSERT INTO [t12872] VALUES (123,'باب ذكر الوتر بثلاث عن الصحابة والتابعين','530','110','0');
INSERT INTO [t12872] VALUES (124,'باب الوتر على الدابة في السفر','539','110','0');
INSERT INTO [t12872] VALUES (125,'باب ما يقرأ به في الوتر','542','110','0');
INSERT INTO [t12872] VALUES (126,'باب أمر النبي صلى الله عليه وسلم أن يجعل آخر الصلاة من الليل وترا','545','110','0');
INSERT INTO [t12872] VALUES (127,'باب الرجل يوتر بركعة ثم ينام ثم يقوم من الليل ليصلي اختلف أصحابنا: فذهبت طائفة إلى أنه إذا قام من الليل شفع وتره بركعة أخرى، ثم صلى ركعتين ركعتين، ثم أوتر في آخر صلاته بركعة. واحتجوا بقول النبي صلى الله عليه وسلم: " اجعلوا آخر صلاتكم من الليل وترا "','548','110','0');
INSERT INTO [t12872] VALUES (128,'باب ذكر الأخبار المروية عمن شفع وتره من السلف عن عثمان بن عفان، أنه كان يشفع بركعة ويقول: " ما شبهتها إلا بالغريبة من الإبل. وفى رواية: إني إذا أردت أن أقوم من الليل أوترت بركعة، فإذا قمت ضممت إليها ركعة، فما شبهتها إلا بالغريبة من الإبل تضم إلى','549','110','0');
INSERT INTO [t12872] VALUES (129,'باب الأخبار المروية عمن أنكر أن يوتر مرتين في ليلة','551','110','0');
INSERT INTO [t12872] VALUES (130,'باب صلاة النبي صلى الله عليه وسلم بعد الوتر','553','110','0');
INSERT INTO [t12872] VALUES (131,'باب الصلاة بعد الوتر عمن بعد النبي صلى الله عليه وسلم عن ابن عون، قال: ذكروا عند إبراهيم الركعتين بعد الوتر فقال: عمن؟، قالوا: عن سعد بن هشام عن عائشة. فقال: هذا خبر لا أراه شيئا، كان الأسود يفعل ويفعل، ويرفع لها من زاده، ولو كان من هذا شيء','558','110','0');
INSERT INTO [t12872] VALUES (132,'باب إثبات القنوت في الوتر حدثنا إسحاق، أخبرنا وكيع، أخبرنا يونس بن أبي إسحاق، عن بريد بن أبي مريم، عن أبي الحوراء، عن الحسن بن علي، قال: علمني رسول الله صلى الله عليه وسلم كلمات أقولهن في قنوت الوتر فذكره. وفى رواية: " لأعلمك كلمات تقولهن عند','560','110','0');
INSERT INTO [t12872] VALUES (133,'باب القنوت في الوتر في السنة كلها عن الأسود، صحبت عمر ستة أشهر، فكان يقنت في الوتر " وكان عبد الله يقنت في الوتر السنة كلها " وعن علي، " أنه كان يقنت في رمضان كله، وفي غير رمضان في الوتر','563','110','0');
INSERT INTO [t12872] VALUES (134,'باب ترك القنوت في الوتر إلا في النصف الآخر من رمضان عن الحسن، أن أبي بن كعب، أم الناس في رمضان، فكان لا يقنت في النصف الأول ويقنت في النصف الآخر، فلما دخل العشر أبق وخلا عنهم، فصلى بهم معاذ القارئ وسئل سعيد بن جبير عن بدء القنوت في الوتر فقال: "','564','110','0');
INSERT INTO [t12872] VALUES (135,'باب من قنت السنة كلها إلا النصف الأول من رمضان قال سعيد، عن قتادة " كان يقنت السنة كلها في وتره إلا النصف الأول من رمضان، فإنه كان لا يقنت وكان يحدث عن الحسن، أنه كان يقنت في السنة كلها إلا النصف الأول من رمضان إذا كان إماما، إلا أن يصلي وحده، فكان','566','110','0');
INSERT INTO [t12872] VALUES (136,'باب من لم يقنت في الوتر كان ابن عمر لا يقنت في شيء من الصلاة، وقال أبو الشعثاء: سألت ابن عمر عن القنوت فقال: ما رأيت أحدا يفعله وعن أبي المهزم، صحبت أبا هريرة، عشر سنين، فما رأيته يقنت في وتره وكان عروة لا يقنت في شيء من الصلاة ولا في الوتر، إلا','567','110','0');
INSERT INTO [t12872] VALUES (137,'باب القنوت بعد الركوع','568','110','0');
INSERT INTO [t12872] VALUES (138,'باب القنوت قبل الركوع عن الأسود، أن عمر بن الخطاب، قنت في الوتر قبل الركوع. وفي رواية: بعد القراءة قبل الركوع عن ابن مسعود أنه قنت في الوتر بعد القراءة قبل الركوع وعن عبد الله بن شداد، " صليت خلف عمر، وعلي، وأبي موسى فقنتوا في صلاة الصبح قبل','572','110','0');
INSERT INTO [t12872] VALUES (139,'باب التكبير للقنوت عن طارق بن شهاب؟ أن عمر بن الخطاب " لما فرغ من القراءة كبر، ثم قنت، ثم كبر وركع، يعني في الفجر وعن علي، أنه كبر في القنوت حين فرغ من القراءة وحين ركع. وفي رواية: كان يفتتح القنوت بتكبيرة وكان عبد الله بن مسعود يكبر في الوتر إذا','573','110','0');
INSERT INTO [t12872] VALUES (140,'باب من كبر للقنوت بعد الركوع كان سعيد بن جبير يقنت في رمضان في الوتر بعد الركوع، إذا رفع رأسه كبر ثم قنت وعن شعبة، سمعت الحكم، وحمادا، وأبا إسحاق يقولون في القنوت: " إذا فرغ من الركوع كبر ثم قنت " وقال المزني، لا أعلم الشافعي ذكر موضع القنوت من','574','110','0');
INSERT INTO [t12872] VALUES (141,'باب رفع الأيدي عند القنوت عن الأسود، أن عبد الله بن مسعود: " كان يرفع يديه في القنوت إلى صدره " وعن أبي عثمان النهدي، كان عمر، يقنت بنا في صلاة الغداة، ويرفع يديه حتى يخرج ضبعيه وعن خلاس، رأيت ابن عباس، يمد بضبعيه في قنوت صلاة الغداة وكان أبو','575','110','0');
INSERT INTO [t12872] VALUES (142,'باب ما يدعى به في قنوت الوتر','576','110','0');
INSERT INTO [t12872] VALUES (143,'باب رفع الصوت في الدعاء في القنوت عن أبي عثمان النهدي: كان عمر يقنت بنا في صلاة الغداة، حتى يسمع صوته من وراء المسجد وعن الحسن، أن أبي بن كعب أم الناس، في رمضان، فكان يقنت في النصف الآخر حتى يسمعهم الدعاء','579','110','0');
INSERT INTO [t12872] VALUES (144,'باب تأمين المأموم خلف الإمام إذا دعا في القنوت','580','110','0');
INSERT INTO [t12872] VALUES (145,'باب مسح الرجل وجهه بيديه بعد فراغه من الدعاء','582','110','0');
INSERT INTO [t12872] VALUES (146,'باب أمر النبي صلى الله عليه وسلم بالوتر قبل الصبح','585','110','0');
INSERT INTO [t12872] VALUES (147,'باب الأخبار التي جاءت في الوتر بعد طلوع الفجر','591','110','0');
INSERT INTO [t12872] VALUES (148,'باب من نسي القنوت في الوتر عن الحسن،: إذا نسي القنوت في الوتر سجد سجدتي السهو. وفي رواية: إن قنت يعني في الوتر فحسن، وإن لم يقنت فليس عليه شيء وعن الأوزاعي " فيمن ترك قنوت الوتر: إنما ترك سنة، لا شيء عليه " وعن ابن أبي ليلى فيمن نسي القنوت في','594','110','0');
INSERT INTO [t12872] VALUES (149,'باب ما يدعى به في آخر الوتر وبعد الفراغ من الوتر','595','110','0');
