INSERT INTO [t12994] VALUES (121,'نعي المحمدان إلى الحجاج، أخوه، وابنه، وكان في عقب علة، فلم يتقار في موضعه فحملته البخارية','128','0','0');
INSERT INTO [t12994] VALUES (122,'يعجبني أن يكون صاحب العلم في كفاية؛ لأن الآفات والعسر إليه أسرع، وإذا احتاج','129','0','0');
INSERT INTO [t12994] VALUES (123,'كنت أذهب أنا وعبد الرحمن بن الأسود، نتبع حسن الصوت بالقرآن في المساجد في شهر','130','0','0');
INSERT INTO [t12994] VALUES (124,'صحبت سعيد بن جبير إلى مكة، فكان بساما ضحاكا كأحسن الخلق، قال: ثم أخذ مخراقا، فلفه ثم','131','0','0');
INSERT INTO [t12994] VALUES (125,'كان سعيد يبكي بالليل حتى عمش وفسدت عيناه','132','0','0');
INSERT INTO [t12994] VALUES (126,'ما في سبيل الله لاقى حمامه ... أبوك ولكن في سبيل الدراهم','133','0','0');
INSERT INTO [t12994] VALUES (127,'ابتغ الولد؛ فإن الرجل إذا مات ولا ولد له انقطع اسمه','135','0','0');
INSERT INTO [t12994] VALUES (128,'رحمتي وسعت كل شيء فسأكتبها للذين يتقون} [الأعراف: ١٥٦] ، قال: هي لهذه','136','0','0');
INSERT INTO [t12994] VALUES (129,'وتواصوا بالحق وتواصوا بالصبر} [العصر: ٣] ، قال: الحق كتاب الله، والصبر طاعة','137','0','0');
INSERT INTO [t12994] VALUES (130,'أن الحسن كان يقرأ (آمرنا) مترفيها، يريد أكثرنا، , فقال: هذا لا يكون. قال: ثم إن يونس قال:','138','0','0');
INSERT INTO [t12994] VALUES (131,'كان رقيقا، وكان يسمع النوح ويبكي','139','0','0');
INSERT INTO [t12994] VALUES (132,'لاقى الحارث بن كلدة أطباء فارس، فقالوا له: أي شيء الدواء؟ فقال لهم: ألا تدخل بطنك طعاما','140','0','0');
INSERT INTO [t12994] VALUES (133,'وضعوا جبالا على جبال، والناس حولهم نواس','141','0','0');
INSERT INTO [t12994] VALUES (134,'قالت نادبة لابنها: وا ابناه، أنت في أول يوم من البلى، وآخر يوم من','142','0','0');
INSERT INTO [t12994] VALUES (135,'المرء يجمع والزمان يفرق ... ويظل يرتق والخطوب تخرق ولمن يعادي عاقلا خير له ... من أن يكون','144','0','0');
INSERT INTO [t12994] VALUES (136,'ومن يكن همه الدنيا ليجمعها ... فسوف يوما على رغم يخليها لا دار للمرء بعد الموت يسكنها ...','145','0','0');
INSERT INTO [t12994] VALUES (137,'متى يبلغ البنيان يوما تمامه ... إذا كنت تبنيه وغيرك يهدم متى ينتهي عن سيئ من أتى به ...','146','0','0');
INSERT INTO [t12994] VALUES (138,'سألت أمير المؤمنين المهدي أبا عبيد الله ينظر رجلا من بقايا أهل المدينة من مشيختهم، فأخبر','147','0','0');
INSERT INTO [t12994] VALUES (139,'أنزل كتابا وأحل فيه حلالا وحرم فيه حراما , وجعل بعضه محكما وبعضه متشابها فأحل حلاله وحرم','148','0','0');
INSERT INTO [t12994] VALUES (140,'قضيت علي بغير الحق. قال: الله؟ قال: الله. فأتى ابن زياد فاستعفاه','149','0','0');
INSERT INTO [t12994] VALUES (141,'ما يذب به عن الإسلام أفضل','150','0','0');
INSERT INTO [t12994] VALUES (142,'هل دلكت الشمس؟ أي زالت','151','0','0');
INSERT INTO [t12994] VALUES (143,'لحفص بن سرجس: قل للعيون الخشوع ... ألا استعدي الدموع على ملوك أصيبوا ... كانوا أشباه','152','0','0');
INSERT INTO [t12994] VALUES (144,'لعمار بن أبي كبار: أخلقت ربطتي وأودى القميص ... وإزاري والبطن طاو خميص وأزادت عرسي الحقوق','153','0','0');
INSERT INTO [t12994] VALUES (145,'قد جعل ربك تحتك سريا} [مريم: ٢٤] قال: أما سمعت بقول القائل: [البحر','154','0','0');
INSERT INTO [t12994] VALUES (146,'فإذا رجل من الجن يقول: يا صاحب الكنانة المكسورة ... خل سبيل الظبية المصرورة فإنها لصبية','155','0','0');
INSERT INTO [t12994] VALUES (147,'أعز أمر الله حيث كنت يعزك الله','156','0','0');
INSERT INTO [t12994] VALUES (148,'رأس العقل بعد الإيمان بالله التودد إلى الناس','157','0','0');
INSERT INTO [t12994] VALUES (149,'استعينوا على النساء بالعري فإن المرأة إذا عريت لزمت بيتها','158','0','0');
INSERT INTO [t12994] VALUES (150,'إذا دخلت على أخيك المسلم فكل من طعامه واشرب من شرابه، ولم يشرب فلما رفعت الموائد أتينا','159','0','0');
INSERT INTO [t12994] VALUES (151,'دعيا إلى وليمة فجاء ابن سيرين قبل الحسن فنظر إلى البيت فإذا هو منجدر بالديباج وحجله من','160','0','0');
INSERT INTO [t12994] VALUES (152,'رأيت كأني أوثقت أبي بحبل ثم ذبحته قال: وما ذاك الحبل الذي أوثقته؟ قال: قلت: حبل أسود،','161','0','0');
INSERT INTO [t12994] VALUES (153,'ليست من طعام الأحرار','162','0','0');
INSERT INTO [t12994] VALUES (154,'أبصر أعرابي صحناة، فقال: قاتلها الله كأنها قيء نسر','163','0','0');
INSERT INTO [t12994] VALUES (155,'إن الشعر جزل من كلام العرب يتبلغ به القوم في ناديهم ويسكن به الغيظ ويعطى به','164','0','0');
INSERT INTO [t12994] VALUES (156,'ادخلوا وهو خاص لنا','165','0','0');
INSERT INTO [t12994] VALUES (157,'كان يقول لبنيه: تباذلوا فيما بينكم فإنه أود لكم','166','0','0');
INSERT INTO [t12994] VALUES (158,'كان الناس في الزمان الأول أفضلهم المسارع في الخير وإن أفضل أهل زمانكم','167','0','0');
