INSERT INTO [t12994] VALUES (162,'الشر في أربع: الدراهم والفراغ والصحة والشبع','171','0','0');
INSERT INTO [t12994] VALUES (163,'استقبال الشمس واستدبارها دواء','172','0','0');
INSERT INTO [t12994] VALUES (164,'ما رأيت أحدا أفضل من عبد الله بن عون','173','0','0');
