INSERT INTO [t12994] VALUES (167,'أماوي ما يغني الثراء عن الفتى ... إذا حشرجت يوما وضاق بها الصدر أماوي إما مانع فمبين ...','176','0','0');
INSERT INTO [t12994] VALUES (168,'إذا حشرجت يوما وضاق بها الصدر أماوي إني لا أقول لسائل ... إذا جاء يوما حل في مالنا نذر','177','0','0');
INSERT INTO [t12994] VALUES (169,'كان أبو سفيان بن الحارث من أحب الناس إلى النبي صلى الله عليه وسلم في الجاهلية وكان شديدا','178','0','0');
