INSERT INTO [t12994] VALUES (171,'أنا صائم , فقال محارب: تؤجر ويخصب العيال، قال أبو محمد: وكان الحسين بن الحسن على قضاء','180','0','0');
INSERT INTO [t12994] VALUES (172,'بلغني أن التاجر يكلم أخاه في الدرهم، فقلت: نعم يا أبا سعيد وفي الدانق، قال: ويحه ما أبقى','181','0','0');
INSERT INTO [t12994] VALUES (173,'شجعان العرب، قال: أحمر قريش وابن الكلبية وصاحب البغل الديزج، فقال: والله ما يعرف هؤلاء','182','0','0');
INSERT INTO [t12994] VALUES (174,'اكتب يا ابن أخي فوالله ما سمعتها من أحد أكثر مما سمعتها من نفسي','183','0','0');
INSERT INTO [t12994] VALUES (175,'فتوضئوا وأعادوا الصلاة','184','0','0');
