INSERT INTO [t12994] VALUES (177,'قضى عثمان بن عفان في ذلك بين رجلين أصاب ذلك أحدهما من صاحبه فسأله الذي أصيب أن يقيده منه','186','0','0');
INSERT INTO [t12994] VALUES (178,'كان أصحاب رسول الله صلى الله عليه وسلم ليسوا بالمتحزقين ولا متماوتين يتناشدون الأشعار','187','0','0');
INSERT INTO [t12994] VALUES (179,'جعل قوم لرجل جعلا على أن يغضب الأحنف فأتاه فأوسعه شرا , فقال له الأحنف: هل لك في طعام','188','0','0');
