INSERT INTO [t12994] VALUES (181,'في درع رسول الله صلى الله عليه وسلم حلقتان من فضة في موضع الصدر وحلقتان من خلف ظهره قال','190','0','0');
INSERT INTO [t12994] VALUES (182,'رأيت عيش الدنيا في ثلاث: امرأة تسرك إذا نظرت إليها وتحفظ غيبك إذا غبت عنها ومملوك لا تهتم','191','0','0');
INSERT INTO [t12994] VALUES (183,'خرج الفرزدق حاجا فلقيه رجل , فقال: أين تريد يا أبا فراس؟ فقال:','192','0','0');
INSERT INTO [t12994] VALUES (184,'نظر قوم إلى معاوية بن قرة في يوم صائف وقد أقبل من مكان بعيد وعليه عباءة له مؤتزر بها ,','193','0','0');
INSERT INTO [t12994] VALUES (185,'سوء حمل الغنى يورث مرحا , وسوء حمل الفاقة يضع الشرف , والحسد داء ليس له شفاء , والشماتة','194','0','0');
INSERT INTO [t12994] VALUES (186,'ولست إذا ما سرني الدهر ضاحكا ... ولا خاشعا ما عشت من حادث الدهر ولا جاعلا عرضي لمالي','195','0','0');
INSERT INTO [t12994] VALUES (187,'قلت لها هل لك في وصل من ... يهواك حتى ينفد الدهر قالت وما أرجو بوصل امرئ ... ليس له نهي','196','0','0');
INSERT INTO [t12994] VALUES (188,'أرى الدنيا قد انتقضت عراها ... وآن خرابها ودنا فناها','197','0','0');
INSERT INTO [t12994] VALUES (189,'كفى حزنا أني أروح وأغتدي ... ومالي من مال أصون به عرضي وأكبر ما ألقى صديقي بمرحبا ...','198','0','0');
INSERT INTO [t12994] VALUES (190,'أبني إني ليس يشخصني عنكم ... قلى لكم ولا بغض إلا لأكسبكم بذاك غنى ... يكفيكم ويرى لكم عرض','199','0','0');
