INSERT INTO [t12994] VALUES (195,'جالست عمرا بعدما فرغت من عطاء ست سنين','204','0','0');
INSERT INTO [t12994] VALUES (196,'ما يلقى منك عمرو قد غلبت على وسادته','205','0','0');
INSERT INTO [t12994] VALUES (197,'لم نر ممن جاءنا من الشام يسأل عن مثل مسألته، يعني سليمان بن موسى','206','0','0');
INSERT INTO [t12994] VALUES (198,'أروى هذا عنك؟','207','0','0');
INSERT INTO [t12994] VALUES (199,'الدين النصيحة إن الدين النصيحة إن الدين النصيحة قيل: لمن يا رسول الله؟ قال: لله ولكتابه','208','0','0');
INSERT INTO [t12994] VALUES (200,'قسم غنما بين أصحابه فصار لسعد تيس , فقال: لقد جمعت الشر كله فلو كنت من المعز لكنت أنثى أو','209','0','0');
