INSERT INTO [t12994] VALUES (202,'كانوا يسمعون كل ليلة زمن قتل ابن الزبير قائلا يقول: [البحر الطويل] ليبك على الإسلام من','211','0','0');
INSERT INTO [t12994] VALUES (203,'يوشك من فر من منيته ... في بعض غراته يوافقها','212','0','0');
