INSERT INTO [t12994] VALUES (205,'مثلت الدنيا على طائر فمصر والبصرة الجناحان والجزيرة الجؤجؤ والشام الرأس واليمن','214','0','0');
INSERT INTO [t12994] VALUES (206,'ثم لتسألن يومئذ عن النعيم} [التكاثر: ٨] قال أبي: يا رسول الله , وأي نعيم نسأل عنه وإنما','215','0','0');
