INSERT INTO [t12994] VALUES (210,'الله أطعمك. ثم دخلت بيتا آخر فشربت قال: الله سقاك، ثم دخلت بيتا آخر فأكلت وشربت، قال أبو','219','0','0');
INSERT INTO [t12994] VALUES (211,'أنفق يا قيس ينفق الله عليك ثلاثا فلما كان بعد ذلك خرجت في سبيل الله ومعي راحلة تمر وأنا','220','0','0');
INSERT INTO [t12994] VALUES (212,'استعمل جده الهيثم على صدقات قومه فلما قبض النبي صلى الله عليه وسلم وارتدت العرب وفى بما','221','0','0');
