INSERT INTO [t12994] VALUES (216,'بعث أبو موسى من العراق إلى عمر بن الخطاب رحمة الله عليه بحلية فوضعت بين يديه وفي حجره','225','0','0');
INSERT INTO [t12994] VALUES (217,'أربع قواصم الظهر: إمام تطيعه ويضلك، وزوجة تأمنها وتخونك، وجار إن علم خيرا ستره وإن علم','226','0','0');
INSERT INTO [t12994] VALUES (218,'كانت امرأة في الجاهلية تطوف بالبيت ولها ستة بنين يسترونها من الناس وهي تقول: [البحر','227','0','0');
INSERT INTO [t12994] VALUES (219,'كنت في حلقة فيها يونس بن عبيد وعوف فسأل سائل عن عشرة أولياء عفا واحد وأبى تسعة، فقال عوف:','228','0','0');
INSERT INTO [t12994] VALUES (220,'إن هذه لحبوة صدق في يزيد','229','0','0');
INSERT INTO [t12994] VALUES (221,'الحسن وابن سيرين سيدا أهل البصرة عربهم ومواليهم غضب من غضب ورضي من رضي','230','0','0');
