INSERT INTO [t12994] VALUES (223,'لم يؤيد الملك بمثل كلب ولم تعل المنابر بمثل قريش ولم يطلب التراث بمثل تميم ولم ترع','232','0','0');
INSERT INTO [t12994] VALUES (224,'ناب مضر كنانة وفرسان مضر قيس، ورجال مضر تميم، وألسنة مضر أسد قال عبد الله بن عوف: وكان','233','0','0');
INSERT INTO [t12994] VALUES (225,'بيني وبين لئام الناس معتبة ... ما تنقضي وكرام الناس خلاني','234','0','0');
INSERT INTO [t12994] VALUES (226,'إن يكن لك دين فلك كرم , وإن يكن لك عقل فلك مروءة , وإن يكن لك مال فلك شرف وإلا فأنت','235','0','0');
INSERT INTO [t12994] VALUES (227,'كنت أطوف مع عمر بن الخطاب رحمة الله عليه حول الكعبة فإذا أعرابي على عنقه امرأة مثل المهاة','236','0','0');
INSERT INTO [t12994] VALUES (228,'الحكمة ليست عن كبر السن، ولكنه، عطاء الله يعطيه من يشاء فإياك ودناءة الأمور ومراق','237','0','0');
INSERT INTO [t12994] VALUES (229,'لم يزل للناس وجوه يرفعون حوائج الناس فأكرم وجوه الناس فبحسب المسلم الضعيف من العدل أن','238','0','0');
INSERT INTO [t12994] VALUES (230,'كان عبد الله بن رواحة نائما إلى جنب امرأته فقام إلى جارية له إلى جنب الحجرة فوقع عليها','239','0','0');
INSERT INTO [t12994] VALUES (231,'شهدت بأن وعد الله حق ... وأن النار مثوى الكافرينا وأن العرش فوق الماء طاف ... وفوق العرش','240','0','0');
INSERT INTO [t12994] VALUES (232,'شهدت بإذن الله أن محمدا ... رسول الذي فوق السماوات من عل','241','0','0');
INSERT INTO [t12994] VALUES (233,'ليس لملوك صديق ولا لحسود غنى، وطول النظر في الحكمة تلقيح للعقل وأهل هذه الأهواء آفة أمة','242','0','0');
