INSERT INTO [t12994] VALUES (323,'كان عمر بن عبد العزيز كثيرا يرجع: [البحر المنسرح] تغترق الطرف وهي لاهية ... كأنما مس','332','0','0');
INSERT INTO [t12994] VALUES (324,'ابن شبرمة: [البحر البسيط] حتى متى لا نرى عدلا نسر به ... ولا ندال على قوم بما','333','0','0');
