INSERT INTO [t12994] VALUES (456,'كنا إذا صلينا خلف الحجاج فإنما نتلفت ما بقي علينا من الشمس فيقول: إلام ما تلتفتون أعمى','466','0','0');
INSERT INTO [t12994] VALUES (457,'شهدت الوليد بن عبد الملك بدمشق صلى الجمعة والشمس كالشرف ثم صلى العصر','467','0','0');
INSERT INTO [t12994] VALUES (458,'كان أهل معد من بني سليم يلقون خبطا وقرعا من الجن حتى ولي عليهم زيد بن أسلم فأمرهم أن','468','0','0');
INSERT INTO [t12994] VALUES (459,'أتأمرني أن أجعل بنتي طحانة','469','0','0');
INSERT INTO [t12994] VALUES (460,'مر عمر بن الخطاب رحمة الله عليه بحفارين يحفرون قبر زينب بنت جحش في يوم صائف فضرب عليهم','470','0','0');
INSERT INTO [t12994] VALUES (461,'أصلح قلبك والبس ما شئت','471','0','0');
INSERT INTO [t12994] VALUES (462,'لأن أعرب آية أحب إلي من أعي آية','472','0','0');
INSERT INTO [t12994] VALUES (463,'كان الحسن إذا نظر إلى الغوغاء قال: هؤلاء قتلة الأنبياء','473','0','0');
INSERT INTO [t12994] VALUES (464,'أفلا تركت لهما واحدا تقر به أعينهما؟','474','0','0');
INSERT INTO [t12994] VALUES (465,'هرب من الحجاج , فقال: [البحر الطويل] ودون يد الحجاج من أن تنالني ... نشاط لأيدي الناعجات','475','0','0');
INSERT INTO [t12994] VALUES (466,'فدققت الباب دقا شديدا فجبهني البواب فخرج أسماء فزعا','476','0','0');
INSERT INTO [t12994] VALUES (467,'أين الملوك التي عن حظها غفلت ... حتى سقاها بكأس الموت ساقيها أموالنا لذوي الميراث نجمعها','477','0','0');
INSERT INTO [t12994] VALUES (468,'لأعلمنك كلمة هي خير من الدنيا وما فيها والله لئن علم الله منك إخراج الآدميين من قلبك حتى','478','0','0');
INSERT INTO [t12994] VALUES (469,'فانكشف فخذه من تحت القباء وأبصر رجل من أهل نجران شامة في فخذه , فقال: هذا الذي نجده في','479','0','0');
INSERT INTO [t12994] VALUES (470,'أن معاوية بن عياض بن غطيف، أتى عمر بن الخطاب وعليه قباء وخفان رقيقان فأنكر ذلك عليه قال:','480','0','0');
INSERT INTO [t12994] VALUES (471,'إنا نغشى الذكر وإن قوما دخلوا يبكون وإنا لا نبكي قال: فإن لم تبك العيون فلتبك القلوب','481','0','0');
INSERT INTO [t12994] VALUES (472,'ما أخبر بموت أحد من إخواني إلا خيل إلي أن عضوا من أعضائي سقط','482','0','0');
INSERT INTO [t12994] VALUES (473,'كان هشام بن عبد الملك لا يكتب إليه بكتاب فيه ذكر الموت','483','0','0');
INSERT INTO [t12994] VALUES (474,'إذا ركعت فلا تصوب رأسك فإنك تستقبل بقفاك القبلة','484','0','0');
INSERT INTO [t12994] VALUES (475,'إنى كنت أقسم زكاتي في إخواني فلما وليت رأيت أن استأمرك في قال: فكتب إليه: أما بعد فابعث','485','0','0');
INSERT INTO [t12994] VALUES (476,'كان لعمر بن عبد العزيز ابن من فاطمة فخرج يلعب مع الغلمان فشجه غلام فاحتملوا ابن عمر والذي','486','0','0');
INSERT INTO [t12994] VALUES (477,'إذا تفل في بئر عذب، فتفل في بئر فصارت أجاجا. قال: وبلغه أن النبي صلى الله عليه وسلم كان','487','0','0');
INSERT INTO [t12994] VALUES (478,'ألقي في الجب قال: يا شاهد غير غائب ويا قريب غير بعيد ويا غالب غير مغلوب اجعل لي فرجا','488','0','0');
INSERT INTO [t12994] VALUES (479,'لا عليك متى كان الالتقاء إذا كانت القلوب سليمة','489','0','0');
INSERT INTO [t12994] VALUES (480,'أصبحت أنهض مثل الطفل معتمدا ... على اليدين كذاك الشيخ يعتمد من عاش أخلقت الأيام جدته ...','490','0','0');
INSERT INTO [t12994] VALUES (481,'ما يمنعكما من إتياني وأنتما عبدان لي؟ قالا: إن صدقت نفسك علمت أنا لسنا بعبدين لك قال:','491','0','0');
INSERT INTO [t12994] VALUES (482,'الصحة غنى الجسد','492','0','0');
INSERT INTO [t12994] VALUES (483,'العافية الملك الخفي','493','0','0');
INSERT INTO [t12994] VALUES (484,'لا يحزنهم الفزع الأكبر} [الأنبياء: ١٠٣] والحسن بن صالح حاضر. فقال علي: إنه لو كان فزع','494','0','0');
INSERT INTO [t12994] VALUES (485,'إن الله أذل ابن آدم بالموت. قال: اذهب حيث شئت إنك ميت قال الحسن: أي ذل أذل من الموت؟ يأتي','495','0','0');
INSERT INTO [t12994] VALUES (486,'يومئذ يتذكر الإنسان وأنى له الذكرى. يقول يا ليتني قدمت لحياتي} [الفجر: ٢٤] قال: علم والله','496','0','0');
INSERT INTO [t12994] VALUES (487,'رآني أبي وأنا أرمي حماما , فقال: يا بني، أما سمعت قولي: [البحر الكامل] وترى لئيم القوم','497','0','0');
INSERT INTO [t12994] VALUES (488,'يعاتب بني تميم: [البحر الطويل] أبني تميم إنني أنا عمكم ... لا تحرمن نصيحة الأعمام إني أرى','498','0','0');
INSERT INTO [t12994] VALUES (489,'إذا ما أراد الله ذل عشيرة ... رماها بتشتيت الهوى والتخاذل فأول عجز القوم فيما ينوبهم ...','499','0','0');
INSERT INTO [t12994] VALUES (490,'إن يك أحد من الشعراء أحسن فقد أحسنت','500','0','0');
INSERT INTO [t12994] VALUES (491,'إن قتلتني فإن الذي يطلب بثأري حي وما على حقي من توان','501','0','0');
INSERT INTO [t12994] VALUES (492,'عجبت للعاقل كيف يخلو عقله من نفعه وهو يرى المنايا للأخلاء مسلبات','502','0','0');
INSERT INTO [t12994] VALUES (493,'رأيت الأحوص الأنصاري حين وقفه أبو بكر بن محمد بن عمرو بن حزم في سوق المدينة وإنه ليصيح:','503','0','0');
INSERT INTO [t12994] VALUES (494,'توقوا على صبيانكم أن يخرجوا في فحمة العشاء ليلة السبت وليلة الأربعاء فإنهم في هذا الوقت','504','0','0');
INSERT INTO [t12994] VALUES (495,'ما سلم رجل على عدو له تسليمة إلا حل من نفسه عقدة','505','0','0');
INSERT INTO [t12994] VALUES (496,'اتهم الرجل إذا لم يعرف شيئا عابه','506','0','0');
INSERT INTO [t12994] VALUES (497,'إذا قل مالي أو أصبت بنكبة ... فنيت حياتي عفة وتكرما وأعرض عن ذي المال حتى يقال لي ... قد','507','0','0');
INSERT INTO [t12994] VALUES (498,'فلولا تركت الشيخ حتى كنت آتيه , فقال أبو بكر: والذي بعثك بالحق لإسلام أبي طالب كان أقر','508','0','0');
INSERT INTO [t12994] VALUES (499,'إذا رجعت نفسي إلي كئيبة ... لخوف أمور مفظعات أظلت رجعت إليها القول ما من مصيبة ... تكون','510','0','0');
INSERT INTO [t12994] VALUES (500,'أستبقي مودتهم','511','0','0');
INSERT INTO [t12994] VALUES (501,'من سبقنا إلى الود كيف لنا أن نلحق به؟ ومن ابتدأنا بالمعروف فقد استرقنا','512','0','0');
INSERT INTO [t12994] VALUES (502,'أوصى رجل ابنه , فقال: يا بني اغتنم مسالمة من لا يدين لك بمحاربته وليكن هربك من السلطان','513','0','0');
INSERT INTO [t12994] VALUES (503,'العاقل لا يحدث من يخاف تكذيبه ولا يعد ما لا يجد إنجازه ولا يضمن ما يخاف العجز','514','0','0');
INSERT INTO [t12994] VALUES (504,'لا يزال في الناس تقية ما تعجب من عجب','515','0','0');
INSERT INTO [t12994] VALUES (505,'الحطب والوقود توقد النار والوضوء الماء، قلت: فالوضوء العمل؟ قال: لا','516','0','0');
INSERT INTO [t12994] VALUES (506,'إني قد بذلت لك من جاهي ما قد صنته عن غيرك فضعني من كرمك بحيث وضعت نفسي من','517','0','0');
INSERT INTO [t12994] VALUES (507,'لا تجزع وإن أعسرت يوما ... فقد أيسرت في الدهر الطويل. ولا تيأس فإن اليأس كفر ... لعل الله','518','0','0');
