INSERT INTO [t13005] VALUES (1,'ذكر ما يدل على أن الإيمان الذي أمر الله عز وجل عباده أن يعتقدوه، ما سأل جبريل عليه السلام رسول الله صلى الله عليه وسلم ليتعلم أصحابه أمر دينهم','1','0','0');
INSERT INTO [t13005] VALUES (2,'ذكر ما يدل على الفرق بين الإيمان والإسلام عن سؤال جبريل رسول الله صلى الله عليه وسلم','3','0','0');
INSERT INTO [t13005] VALUES (3,'ذكر ما يدل على أن الإيمان والإسلام اسمان لمعنى واحد وأن الإسلام الإقرار باللسان، والعمل بالأركان، وأن الإيمان اعتقاد بالقلب','5','0','0');
INSERT INTO [t13005] VALUES (4,'ذكر ما يدل على أن ابتداء الإيمان أن يؤمن العبد بالله عز وجل وحده وكتبه ورسله. . . . إلخ','6','0','0');
INSERT INTO [t13005] VALUES (5,'ذكر ما يدل على أن من الإيمان أن يؤمن بالقدر خيره وشره','8','0','0');
INSERT INTO [t13005] VALUES (6,'ذكر ما يدل على أن من الإيمان أن يؤمن بحلو القدر، ومره خيره وشره','10','0','0');
INSERT INTO [t13005] VALUES (7,'ذكر ما يدل على أن من الإيمان أن يؤمن بالبعث بعد الموت','12','0','0');
INSERT INTO [t13005] VALUES (8,'ذكر ما يدل على أن من الإيمان أن يؤمن العبد بأن لله جنة ونارا','14','0','0');
INSERT INTO [t13005] VALUES (9,'ذكر ما يدل على أن من الإيمان أن يعتقد العبد لقاء الله عز وجل','24','0','0');
INSERT INTO [t13005] VALUES (10,'ذكر وجوب النية للإسلام والإيمان بالله وحده لا شريك له','27','0','0');
INSERT INTO [t13005] VALUES (11,'ذكر ما يدل على أن أعلى الإيمان التي دعا إليها وأولها شهادة أن لا إله إلا الله','29','0','0');
INSERT INTO [t13005] VALUES (12,'ذكر قول النبي صلى الله عليه وسلم لوفد عبد القيس: " أتدرون ما الإيمان؟ "، ثم فسرها لهم فقال: " شهادة أن لا إله إلا الله "','33','0','0');
INSERT INTO [t13005] VALUES (13,'ذكر ما بعث الله عز وجل به رسوله عليه السلام إلى عباده ليدعوهم إليه، وهي شهادة أن لا إله إلا الله، وأن محمدا عبده ورسوله صلى الله عليه وسلم','36','0','0');
INSERT INTO [t13005] VALUES (14,'ذكر بيان حق الله عز وجل على عباده بعد شهادة أن لا إله إلا الله','45','0','0');
INSERT INTO [t13005] VALUES (15,'ذكر قول النبي صلى الله عليه وسلم: " من علم أن لا إله إلا الله دخل الجنة "','47','0','0');
INSERT INTO [t13005] VALUES (16,'ذكر قول النبي صلى الله عليه وسلم: " من قال لا إله إلا الله وكفر بما يعبد من دون الله "','50','0','0');
INSERT INTO [t13005] VALUES (17,'ذكر قول النبي صلى الله عليه وسلم: " من لقي الله بشهادة أن لا إله إلا الله وأنه رسول الله لم يحجب عن الجنة "','52','0','0');
INSERT INTO [t13005] VALUES (18,'ذكر قول النبي صلى الله عليه وسلم لعمه: " قل لا إله إلا الله أشهد لك بها عند الله وأحاج لك بها "','55','0','0');
INSERT INTO [t13005] VALUES (19,'ذكر الخصال التي بني عليها الإسلام أولها شهادة أن لا إله إلا الله','59','0','0');
INSERT INTO [t13005] VALUES (20,'ذكر قول النبي صلى الله عليه وسلم من شهد أن لا إله إلا الله وأنه عبده ورسوله وأن عيسى عبد الله وروحه أدخله الله الجنة من أي أبوابها شاء','64','0','0');
INSERT INTO [t13005] VALUES (21,'ذكر ما يدل على أن النبي صلى الله عليه وسلم بايع من أجابه على شهادة أن لا إله إلا الله لا يشركوا به شيئا','68','0','0');
INSERT INTO [t13005] VALUES (22,'ذكر ما يدل على أن قول لا إله إلا الله يوجب اسم الإسلام ويحرم مال قائلها ودمه','73','0','0');
INSERT INTO [t13005] VALUES (23,'ذكر ما يدل على أن قول لا إله إلا الله يمنع القتل','81','0','0');
INSERT INTO [t13005] VALUES (24,'ذكر ما يدل على أن من لقي الله بالتوحيد غير مشرك ولا شاك دخل الجنة','87','0','0');
INSERT INTO [t13005] VALUES (25,'ذكر ما يدل على أن قائل لا إله إلا الله، وأن محمدا رسول الله مستيقنا معتقدا بها قلبه دخل الجنة','110','0','0');
INSERT INTO [t13005] VALUES (26,'ذكر ما يدل على أن المقر بالتوحيد إشارة إلى السماء بأن الله في السماء دون الأرض، وأن محمدا رسول الله صلى الله عليه وسلم يسمى مؤمنا','114','0','0');
INSERT INTO [t13005] VALUES (27,'ذكر حق الله على العباد وهو الإقرار بالوحدانية','116','0','0');
INSERT INTO [t13005] VALUES (28,'ذكر أمر النبي صلى الله عليه وسلم أمراء الأجناد، وسراياه أن يدعوا الناس إلى شهادة أن لا إله إلا الله، وأن محمدا عبده ورسوله صلى الله عليه وسلم','140','0','0');
INSERT INTO [t13005] VALUES (29,'ذكر ما يدل على أن الإيمان بالله علم ومعرفة وإقرار','143','0','0');
INSERT INTO [t13005] VALUES (30,'ذكر أمر النبي صلى الله عليه وسلم الوفود إذا قدموا عليه أن يعبدوا الله ولا يشركوا به شيئا','145','0','0');
INSERT INTO [t13005] VALUES (31,'ذكر أمر النبي صلى الله عليه وسلم السرايا أن يدعوا إلى توحيد الله ويقاتلوا عليه','147','0','0');
INSERT INTO [t13005] VALUES (32,'ذكر بيعة النبي صلى الله عليه وسلم أصحابه على شهادة أن لا إله إلا الله، وأن محمدا رسول الله','150','0','0');
INSERT INTO [t13005] VALUES (33,'ذكر ما يدل على أن اسم الإيمان يقع على غير ما ذكر جبريل عليه السلام " وأن شهادة لا إله إلا الله، وأن محمدا رسول الله، وإقام الصلاة، وإيتاء الزكاة، وصوم رمضان، وحج البيت أصل الإيمان، وأساسه وأنها بضع وسبعون أو بضع وستون شعبة، أفضلها لا إله إلا الله','173','0','0');
INSERT INTO [t13005] VALUES (34,'ذكر معنى الإيمان من وصف الرسول صلى الله عليه وسلم وأنها بضع وسبعون شعبة. . . . . إلخ " قال الله عز وجل: آمن الرسول معناه: صدق الرسول. وقوله: يؤمنون بالغيب يصدقون. وقوله: لن نؤمن لك لن نصدقك. وقوله: وما أنت بمؤمن لنا، يعني بمصدق لنا. وللإيمان','178','0','0');
INSERT INTO [t13005] VALUES (35,'بيان ما تقدم من الأثر','180','0','0');
INSERT INTO [t13005] VALUES (36,'ذكر ما يدل على أن اسم الإيمان واقع على من يصدق بجميع ما أتى به المصطفى صلى الله عليه وسلم عن الله نية وإقرارا وعملا وإيمانا. . . . . إلخ','184','0','0');
INSERT INTO [t13005] VALUES (37,'ذكر الأخبار الدالة على الفرق بين الإيمان والإسلام ومن قال بهذا القول من أئمة أهل الآثار قال الزهري: " الإسلام هي الكلمة، والإيمان العمل ".','189','0','0');
INSERT INTO [t13005] VALUES (38,'ذكر الأخبار الدالة والبيان الواضح من الكتاب أن الإيمان والإسلام اسمان لمعنى واحد إلخ " فقال الله عز وجل: ولا يرضى لعباده الكفر. وقال: ورضيت لكم الإسلام دينا. وقال: فمن يرد الله أن يهديه يشرح صدره للإسلام، وقال: أفمن شرح الله صدره للإسلام فهو على','201','0','0');
INSERT INTO [t13005] VALUES (39,'ذكر ما يدل على أن الإيمان هو الطاعات كلها ٠٠٠إلخ قال أهل التأويل: " صلاتكم إلى القبلة الأولى وتصديقكم نبيكم صلى الله عليه وسلم واتباعه إلى القبلة الأخرى أي ليعطيكم أجرهما جميعا، إن الله بالناس لرءوف رحيم. قاله علي بن أبي طالب، وعبد الله بن عباس رضي','204','0','0');
INSERT INTO [t13005] VALUES (40,'بيان ما تقدم من الأثر','206','0','0');
INSERT INTO [t13005] VALUES (41,'ذكر اختلاف أقاويل الناس في الإيمان ما هو " فقالت طائفة من المرجئة: الإيمان فعل القلب دون اللسان، وقالت طائفة منهم: " الإيمان فعل اللسان دون القلب، وهم أهل الغلو في الإرجاء "، وقال جمهور أهل الإرجاء: " الإيمان هو فعل القلب واللسان جميعا "، وقالت','209','0','0');
INSERT INTO [t13005] VALUES (42,'ذكر خبر يدل على أن الإيمان قول باللسان، واعتقاد بالقلب، وعمل بالأركان يزيد وينقص','221','0','0');
INSERT INTO [t13005] VALUES (43,'ذكر خبر يدل على أن الإيمان ينقص حتى لا يبقى في قلب العبد مثقال حبة خردل، وأن المجاهدة بالقلب واللسان واليد من الإيمان','226','0','0');
INSERT INTO [t13005] VALUES (44,'ذكر المثل الذي ضربه الله والنبي صلى الله عليه وسلم للمؤمن والإيمان " قال الله عز وجل: ألم تر كيف ضرب الله مثلا كلمة طيبة كشجرة طيبة أصلها ثابت وفرعها في السماء تؤتي أكلها كل حين بإذن ربها، فضربها مثلا لكلمة الإيمان وجعل لها أصلا وفرعا وثمرا تؤتيه كل','231','0','0');
INSERT INTO [t13005] VALUES (45,'ذكر الأخبار التي جاءت عن النبي صلى الله عليه وسلم الدالة على أساس الإيمان وشعبه','237','0','0');
INSERT INTO [t13005] VALUES (46,'ذكر الأبواب والشعب التي قالها النبي صلى الله عليه وسلم إنها الإيمان وإنها: قول باللسان، ومعرفة بالقلب، وعمل بالأركان التي علمهن جبريل عليه السلام الصحابة وكذلك روي عنه من رواية علي بن أبي طالب رضي الله عنه وبين المصطفى مجملها " فمن أفعال القلوب:','248','0','0');
INSERT INTO [t13005] VALUES (47,'ذكر صفة أصحاب رسول الله صلى الله عليه وسلم ومنزلتهم من الإيمان واتباعهم القرآن قال عبد الله بن عباس: قوله: يتلونه حق تلاوته، قال: " يتبعونه حق اتباعه، يحلون حلاله ويحرمون حرامه، ولا يحرفونه عن مواضعه ". وقال قتادة: " هؤلاء أصحاب محمد صلى الله','250','0','0');
INSERT INTO [t13005] VALUES (48,'ذكر ما يدل على أن أداء الوضوء من الإيمان، وأن الله لا يقبل الصلاة إلا بوضوء وفضل من أتم الوضوء','260','0','0');
INSERT INTO [t13005] VALUES (49,'ذكر أول ما يدعى إليه العبد وهو التوحيد والمعرفة ثم الصلوات الخمس ثم الزكاة " قال الله عز وجل: وأقيموا الصلاة وآتوا الزكاة وروى أبو أيوب أن النبي صلى الله عليه وسلم، قال: " تقيم الصلاة وتؤتي الزكاة "','263','0','0');
INSERT INTO [t13005] VALUES (50,'ذكر ما يدل على أن مانع الزكاة وتارك الصلاة يستحق اسم الكفر','267','0','0');
INSERT INTO [t13005] VALUES (51,'ذكر ما يدل على أن صوم رمضان من الإيمان وأحد الأركان الذي قاله رسول الله صلى الله عليه وسلم قال الله عز وجل فمن شهد منكم الشهر فليصمه','274','0','0');
INSERT INTO [t13005] VALUES (52,'ذكر ما يدل على أن الحج المبرور من الإيمان','280','0','0');
INSERT INTO [t13005] VALUES (53,'ذكر ما يدل على أن الجهاد في سبيل الله عز وجل من الإيمان قال الله عز وجل: والذين جاهدوا فينا لنهدينهم سبلنا وإن الله لمع المحسنين','286','0','0');
INSERT INTO [t13005] VALUES (54,'ذكر ما يدل على أن الإيمان بما أتى به النبي صلى الله عليه وسلم من الكتاب والحكمة من الإيمان قال الله عز وجل: فلا وربك لا يؤمنون حتى يحكموك فيما شجر بينهم ثم لا يجدوا في أنفسهم حرجا مما قضيت ويسلموا تسليما "','307','0','0');
INSERT INTO [t13005] VALUES (55,'ذكر منزلة إيمان أبي بكر وعمر رضي الله عنهما من إيمان المصطفى صلى الله عليه وسلم','311','0','0');
INSERT INTO [t13005] VALUES (56,'ذكر ما يدل على أن المؤمنين يتفاضلون في الإيمان، وفضل عمر رضي الله عنه على الناس','318','0','0');
INSERT INTO [t13005] VALUES (57,'ذكر خبر جامع من تفسير الإيمان والإسلام شبيه بما فسره جبريل عليه السلام " وهو قول النبي صلى الله عليه وسلم: " إنما الدين النصيحة " بكلمة واحدة جامعة، فلما سئل: لمن؟ ، قال: " لله ولكتابه ولرسوله ولأئمة المسلمين وعامتهم "، فجمعت هذه الكلمة كل خير يؤمن','332','0','0');
INSERT INTO [t13005] VALUES (58,'ذكر بيعة النبي صلى الله عليه وسلم أصحابه على النصح لكل مسلم','335','0','0');
INSERT INTO [t13005] VALUES (59,'ذكر الخصال التي سأل جبريل المصطفى صلى الله عليه وسلم مما تقدم وزيادة الألفاظ التي أوردها الناقلون لها','344','0','0');
INSERT INTO [t13005] VALUES (60,'ومما يدل على أن حب الله ورسوله والحب في الله والبغض فيه من الإيمان','349','0','0');
INSERT INTO [t13005] VALUES (61,'ذكر ما يدل على أن حب رسول الله صلى الله عليه وسلم من الإيمان','353','0','0');
INSERT INTO [t13005] VALUES (62,'ذكر قول النبي صلى الله عليه وسلم: " أنا أتقاكم وأعلمكم بالله، وإن التقى من فعل القلب " قال الله تعالى: ولكن يؤاخذكم بما كسبت قلوبكم','358','0','0');
INSERT INTO [t13005] VALUES (63,'ذكر ما يدل على أن من أحب رسول الله صلى الله عليه وسلم يكون معه في الجنة','360','0','0');
INSERT INTO [t13005] VALUES (64,'ذكر الخصال التي إذا فعلها المسلم ازداد إيمانا','366','0','0');
INSERT INTO [t13005] VALUES (65,'ذكر صفة درجات الإسلام والإيمان','379','0','0');
INSERT INTO [t13005] VALUES (66,'ذكر المثل الذي ضربه النبي صلى الله عليه وسلم لأهل الإسلام في تراحمهم وتواصلهم','391','0','0');
INSERT INTO [t13005] VALUES (67,'ذكر صفة المؤمن المسلم المتقي، ومكان التقى منه','397','0','0');
INSERT INTO [t13005] VALUES (68,'ذكر ما يدل على أن حقيقة الإيمان والإسلام في صدر العبد','402','0','0');
INSERT INTO [t13005] VALUES (69,'ذكر ما يدل على أن الحب في الله، وإفشاء السلام من الإيمان','405','0','0');
INSERT INTO [t13005] VALUES (70,'ذكر وصف النبي صلى الله عليه وسلم الأمانة، وأنها نزلت في قلوب أصحابه، ثم تعلموا القرآن والسنة، ثم أخبر عن رفعها وأنها من الإيمان','412','0','0');
INSERT INTO [t13005] VALUES (71,'ذكر ما يدل على أن الوسوسة التي تقع في قلب المسلم من أمر الرب عز وجل صريح الإيمان','417','0','0');
INSERT INTO [t13005] VALUES (72,'ذكر الأخبار الدالة على أن الله عز وجل يتجاوز عن ما يتوسوس به العبد إذا لم يعمل به أو يتكلم','425','0','0');
INSERT INTO [t13005] VALUES (73,'ذكر ما يقول المرء المسلم عند وساوس القلب','430','0','0');
INSERT INTO [t13005] VALUES (74,'ذكر درجات الأنبياء في الوساوس مع اليقين','447','0','0');
INSERT INTO [t13005] VALUES (75,'ذكر ما يدل على درجات المرء المسلم المحسن','453','0','0');
INSERT INTO [t13005] VALUES (76,'ذكر فضل المؤمن المحسن في الإسلام بعد الإساءة في الجاهلية','464','0','0');
INSERT INTO [t13005] VALUES (77,'ذكر فضل من أسلم على ما سلف من الخير في الجاهلية','470','0','0');
INSERT INTO [t13005] VALUES (78,'ذكر فضل من آمن من أهل الكتاب بنبيه صلى الله عليه وسلم ثم آمن بالمصطفى صلى الله عليه وسلم','478','0','0');
INSERT INTO [t13005] VALUES (79,'ذكر وجوب الإيمان على كل من سمع بالنبي صلى الله عليه وسلم من أهل الكتابين، والإقرار بما أرسل به وجاء به عن الله عز وجل','485','0','0');
INSERT INTO [t13005] VALUES (80,'ذكر وجوب الإيمان بنبوة عيسى ابن مريم عليه السلام، وأنه عبد الله ورسوله وكلمته وروح منه ألقاها إلى مريم','489','0','0');
INSERT INTO [t13005] VALUES (81,'ذكر وجوب الإيمان بنزول عيسى ابن مريم عليه السلام، وإيمانه بالمصطفى عليه السلام وبشريعته','493','0','0');
INSERT INTO [t13005] VALUES (82,'ذكر ابتداء الإسلام والإيمان وتغربه، وأنه سيعود غريبا كما بدأ','508','0','0');
INSERT INTO [t13005] VALUES (83,'ذكر قول النبي صلى الله عليه وسلم: " الإيمان هاهنا " نحو اليمن، ومعنى قوله أنه أراد الحجاز لأن مكة يمانية','514','0','0');
INSERT INTO [t13005] VALUES (84,'ذكر ما يدل على أن الإسلام يعود كما بدأ حتى لا يبقى منه شيء','537','0','0');
INSERT INTO [t13005] VALUES (85,'ذكر خبر يدل على ما تقدم من ابتداء الإسلام','543','0','0');
INSERT INTO [t13005] VALUES (86,'ذكر الأعمال التي يستحق بها العامل زيادة إيمانه والتي توجب النقصان','552','0','0');
INSERT INTO [t13005] VALUES (87,'ذكر الذنوب التي تخرج العبد من الإيمان إلى الشرك والكبائر','558','0','0');
INSERT INTO [t13005] VALUES (88,'ذكر بيعة النبي صلى الله عليه وسلم أصحابه على اجتناب الكبائر','580','0','0');
INSERT INTO [t13005] VALUES (89,'ذكر ما يدل على أن مواجهة المسلم بالقتال أخاه كفر لا يبلغ به الشرك والخروج من الإسلام','593','0','0');
INSERT INTO [t13005] VALUES (90,'ذكر ما يدل على أن رفع الصوت على النبي صلى الله عليه وسلم كان من الكبائر قال الله عز وجل: لا ترفعوا أصواتكم فوق صوت النبي إلى قوله أن تحبط أعمالكم','595','0','0');
INSERT INTO [t13005] VALUES (91,'ذكر أخبار جاءت عن النبي صلى الله عليه وسلم على معنى الندب، والتحذير " منها: لا يزني وهو مؤمن معناه أنه غير مؤمن في حين ركوبه الزنا، وقيل: غير مستكمل للإيمان "','606','0','0');
INSERT INTO [t13005] VALUES (92,'ذكر ما يدل على أن النفاق على ضروب: نفاق كفر، ونفاق قلب، ولسان، وأفعال وهي دون ذلك قال الله عز وجل: إن المنافقين في الدرك الأسفل من النار','620','0','0');
INSERT INTO [t13005] VALUES (93,'ذكر الأخبار الدالة على حرمة مال المسلم','661','0','0');
INSERT INTO [t13005] VALUES (94,'ذكر قول النبي صلى الله عليه وسلم: " من ادعى إلى غير أبيه فليس منا "، واختلاف الألفاظ فيه','686','0','0');
INSERT INTO [t13005] VALUES (95,'ذكر وجوب الإيمان بما أتى به المصطفى عليه السلام عن الله عز وجل من الكتاب والحكمة','782','0','0');
INSERT INTO [t13005] VALUES (96,'ذكر وجوب الإيمان بما أخبر به النبي صلى الله عليه وسلم عما رأى في بدء أمره حين شق صدره وملئ حكمة وإيمانا، ثم أراهم أثر المخيط فيه معجزة له وتصديقا بما أخبر به','813','0','0');
INSERT INTO [t13005] VALUES (97,'ذكر وجوب الإيمان بما أخبر به المصطفى عليه السلام عن الإسراء قبل أن يوحى إليه','820','0','0');
INSERT INTO [t13005] VALUES (98,'ذكر اختلاف ألفاظ حديث ابن عباس رضي الله عنه في الرؤية ليلة المعراج','868','0','0');
INSERT INTO [t13005] VALUES (99,'ذكر وجوب الإيمان برؤية الله عز وجل','908','0','0');
INSERT INTO [t13005] VALUES (100,'ذكر وجوب الإيمان بما أخبر به الرسول صلوات الله عليه من الآيات المستقبلة إلى قيام الساعة','1121','0','0');
INSERT INTO [t13005] VALUES (101,'ذكر وجوب الإيمان بما يكون بعده من الآيات','1127','0','0');
INSERT INTO [t13005] VALUES (102,'ذكر وجوب الإيمان بالآيات العشر التي أخبر بها رسول الله صلى الله عليه وسلم التي تكون قبل الساعة','1131','0','0');
INSERT INTO [t13005] VALUES (103,'ذكر وجوب الإيمان بطلوع الشمس من مغربها، وقوله: يوم يأتي بعض آيات ربك قال أهل التأويل: هو طلوع الشمس من مغربها','1143','0','0');
INSERT INTO [t13005] VALUES (104,'ذكر وجوب الإيمان بخروج الدابة','1155','0','0');
INSERT INTO [t13005] VALUES (105,'ذكر وجوب الإيمان بخروج الدجال، ويأجوج، ومأجوج','1159','0','0');
INSERT INTO [t13005] VALUES (106,'ذكر صفة الدجال ونعته التي وصفها النبي صلى الله عليه وسلم لئلا يشتبه أمره على من يراه','1174','0','0');
INSERT INTO [t13005] VALUES (107,'ذكر وجوب الإيمان بنزول عيسى ابن مريم عليهما السلام لقتال الدجال، وقيام الساعة، والصعق. قال الله عز وجل: ونفخ في الصور فصعق من في السموات ومن في الأرض إلا من شاء الله','1197','0','0');
INSERT INTO [t13005] VALUES (108,'ذكر وجوب الإيمان بالسؤال في القبر. قال الله عز وجل: يثبت الله الذين آمنوا بالقول الثابت في الحياة الدنيا وفي الآخرة','1199','0','0');
INSERT INTO [t13005] VALUES (109,'ذكر وجوب الإيمان بالبعث والنشور','1210','0','0');
INSERT INTO [t13005] VALUES (110,'ذكر وجوب الإيمان بالحوض','1212','0','0');
INSERT INTO [t13005] VALUES (111,'ذكر وجوب الإيمان بالقيامة والمحاسبة، وذكر الميزان في حديث عمر رضي الله عنه لما سأل جبريل النبي صلى الله عليه وسلم','1218','0','0');
