INSERT INTO [t13012] VALUES (153,'ما يقال في خلال المجلس من الذكر إذا أمسك عن الرواية في خلال المجلس للاستراحة، ذكر الله تعالى في تلك الحال، وقد كان جماعة من أكابر السلف يفعلون ذلك','1158','0','0');
INSERT INTO [t13012] VALUES (154,'كراهة تكرير الحديث وإعادته','1166','0','0');
INSERT INTO [t13012] VALUES (155,'باب تحري المحدث الصدق في مقاله وإيثاره ذلك على اختلاف أموره وأحواله','1171','0','0');
INSERT INTO [t13012] VALUES (156,'حذره إذا روى الحديث وتوقيه خوفا من وقوع الزلل والوهم فيه','1178','0','0');
INSERT INTO [t13012] VALUES (157,'اختيار الرواية من أصل الكتاب لأنه أبعد من الخطأ وأقرب للصواب. الاحتياط للمحدث والأولى به أن يروي من كتابه ليسلم من الوهم والغلط ويكون جديرا بالبعد من الزلل','1189','0','0');
INSERT INTO [t13012] VALUES (158,'جواز رواية المحدث من حفظه، والقول في تأدية معنى الحديث دون لفظه الرواية عن الحفظ جائزة لمن كان متقنا لها متحفظا فيها','1204','0','0');
INSERT INTO [t13012] VALUES (159,'القول في رد الحديث إلى الصواب إذا كان راويه قد خالف موجب الإعراب بعض من أوجب رواية الحديث على لفظه كان يروي الحديث ملحونا إذا كان قد سمعه كذلك ولا يغيره ويحكى ذلك من التابعين عن أبي معمر عبد الله بن سخبرة ونافع مولى ابن عمر ومحمد بن سيرين','1221','0','0');
INSERT INTO [t13012] VALUES (160,'الترغيب في تعلم النحو والعربية لأداء الحديث بالعبارة السوية','1235','0','0');
INSERT INTO [t13012] VALUES (161,'من عاب اللحن وشدد فيه','1250','0','0');
INSERT INTO [t13012] VALUES (162,'ذكر من كان يذهب إلى جواز رواية الحديث على المعنى وبعض المحفوظ عنه في ذلك','1263','0','0');
INSERT INTO [t13012] VALUES (163,'ذكر تسمية الصحابة الذين روي عنهم ما ذكرناه آنفا','1277','0','0');
INSERT INTO [t13012] VALUES (164,'الكتابة عن المحدث في المذاكرة إذا أورد المحدث في المذاكرة شيئا أراد السامع له أن يدونه عنه فينبغي له إعلام المحدث ذلك ليتحرى في تأدية لفظه وحصر معناه','1283','0','0');
INSERT INTO [t13012] VALUES (165,'باب ذكر الحكم فيمن روى من حفظه حديثا فخولف فيه يلزم الراوي إذا خالفه فيما رواه راو غيره أن يرجع إلى أصل كتابه فيطالعه ويستثبت منه','1289','0','0');
INSERT INTO [t13012] VALUES (166,'من خالفه آخر أحفظ منه فرجع إلى قوله إذا روى المحدث من حفظه ما ليس له به كتاب فخالفه فيه من هو أثبت أو أحفظ منه لزمه الرجوع إلى قوله','1302','0','0');
INSERT INTO [t13012] VALUES (167,'مراجعة المحدث وتوقيفه عندما يتخالج في النفس من روايته لا يجوز للطالب أن ينكر على المحدث شيئا رواه إذا لم يعرفه أو وقع في نفسه شيء من سماعه إياه لكن ينبغي له أن يوقفه عليه ويستثبته فيه فما أخبره به قبله منه لكونه أمينا في نفسه عدلا في حديثه','1312','0','0');
INSERT INTO [t13012] VALUES (168,'استحباب التحديث والتكفير لمن حلف أن لا يحدث','1320','0','0');
INSERT INTO [t13012] VALUES (169,'قول المحدث حدثنا وأخبرنا','1327','0','0');
INSERT INTO [t13012] VALUES (170,'باب إملاء الحديث وعقد المجلس له يستحب عقد المجالس لإملاء الحديث لأن ذلك أعلى مراتب الراوين ومن أحسن مذاهب المحدثين مع ما فيه من جمال الدين والاقتداء بسنن السلف الصالحين','1334','0','0');
INSERT INTO [t13012] VALUES (171,'من كان يعقد المجلس في يوم الخميس','1347','0','0');
INSERT INTO [t13012] VALUES (172,'من لم يتفرغ للحديث نهارا فحدث ليلا','1352','0','0');
INSERT INTO [t13012] VALUES (173,'تعيين المحدث للطلبة يوم المجلس ينبغي للمحدث أن يعين لأصحابه يوم المجلس لئلا ينقطعوا عن أشغالهم وليستعدوا لإتيانه ويعد بعضهم بعضا به والأصل في ذلك','1357','0','0');
INSERT INTO [t13012] VALUES (174,'عقد المجالس في المساجد يستحب للمحدث أن يجعل تحديثه في المسجد وأن لا يخلي يوم الجمعة من الإملاء في مسجد الجامع','1363','0','0');
INSERT INTO [t13012] VALUES (175,'جلوس المحدث تجاه القبلة','1369','0','0');
INSERT INTO [t13012] VALUES (176,'التحليق قبل صلاة الجمعة','1373','0','0');
INSERT INTO [t13012] VALUES (177,'سعة الحلقة','1378','0','0');
INSERT INTO [t13012] VALUES (178,'باب اتخاذ المستملي ينبغي للمحدث أن يتخذ من يبلغ عنه الإملاء إلى من بعد في الحلقة','1382','0','0');
INSERT INTO [t13012] VALUES (179,'إشراف المستملي على الناس يستحب للمستملي أن يستملي وهو جالس على موضع مرتفع أو على كرسي فإن لم يجد استملى قائما','1390','0','0');
INSERT INTO [t13012] VALUES (180,'اتباع المستملي لفظ المحدث يستحب له أن لا يخالف لفظ الراوي في التبليغ عنه بل يلزمه ذلك وخاصة إذا كان الراوي من أهل الدراية والمعرفة بأحكام الرواية','1394','0','0');
INSERT INTO [t13012] VALUES (181,'ما يبتدئ به المستملي من القول ينبغي أن يقرأ في المجلس سورة من القرآن قبل الأخذ في الإملاء','1400','0','0');
INSERT INTO [t13012] VALUES (182,'قوله للمحدث من ذكرت إذا صلى المستملي على النبي صلى الله عليه وسلم أقبل على المحدث فقال له من حدثك؟ أو من ذكرت رحمك الله؟','1408','0','0');
INSERT INTO [t13012] VALUES (183,'جواب المحدث لمستمليه وتلفظه بما يرويه إذا فعل المستملي ما ذكرته قال الراوي: نا فلان ثم نسب شيخه الذي سماه حتى يبلغ بنسبه منتهاه','1411','0','0');
INSERT INTO [t13012] VALUES (184,'الاقتصار على الاسم أو النسب والاكتفاء بذكر الكنية أو اللقب جماعة من المحدثين تقتصر في الرواية عنهم على ذكر أسمائهم دون أنسابهم إذ كان أمرهم لا يشكل ومنزلتهم من العلم لا تجهل فمنهم أيوب بن أبي تميمة السخيتاني ويونس بن عبيد وسعيد بن أبي عروبة وهشام بن أبي','1415','0','0');
INSERT INTO [t13012] VALUES (185,'أصحاب الألقاب قد غلبت ألقاب جماعة من أهل العلم على أسمائهم فاقتصر الناس على ذكر ألقابهم في الرواية عنهم فمنهم غندر واسمه محمد بن جعفر','1421','0','0');
INSERT INTO [t13012] VALUES (186,'أصحاب الكنى وفي المحدثين جماعة اكتفى الرواة عنهم بذكر كناهم دون أسمائهم وأنسابهم لغلبتها عليهم واشتهارهم بها والأمن من دخول اللبس فيها فمنهم أبو الزناد وهو: عبد الله بن ذكوان وقيل إن كنيته أبو عبد الرحمن وكان يلقب أبا الزناد فغلب عليه، وأبو بشر وهو:','1431','0','0');
INSERT INTO [t13012] VALUES (187,'التلطف لسؤال المحدث عن اسمه ونسبه','1434','0','0');
INSERT INTO [t13012] VALUES (188,'نسبة المحدث إلى أمه إذا كان الراوي معروفا باسم أمه وهو الغالب عليه جاز نسبته إليه وذلك مثل ابن بحينة وهو: عبد الله بن مالك بن القشب الأسري وأمه بحينة بنت الحارث بن المطلب بن عبد مناف، وعبد الله ابن أم مكتوم الأعمى وهو: عبد الله بن عمرو بن شريح بن قيس','1437','0','0');
INSERT INTO [t13012] VALUES (189,'تعريف المحدث بالنقص كالعمى والعور ونحوهما من الآفات لم يختلف العلماء أنه يجوز ذكر الشيخ وتعريفه بصفته التي ليست نقصا في خلقته كالطول والزرقة والشقرة والحمرة والصفرة وقد جاءت الرواية عن حميد الطويل وإسحاق بن يوسف الأزرق وحسين بن الحسن الأشقر وجعفر بن زياد','1441','0','0');
INSERT INTO [t13012] VALUES (190,'من روى عن شيخ فأثنى عليه ومدحه وعظمه','1449','0','0');
INSERT INTO [t13012] VALUES (191,'استحباب الرواية عن جماعة وألا يقتصر على شيخ واحد يستحب للراوي أن لا يقتصر في إملائه على الرواية عن شيخ واحد من شيوخه بل يروي عن جماعتهم ويقدم من علا إسناده منهم','1465','0','0');
INSERT INTO [t13012] VALUES (192,'تجنب الرواية عن الضعفاء والمخالفين من أهل البدع والأهواء','1469','0','0');
INSERT INTO [t13012] VALUES (193,'الاقتداء بذوي السنن المستقيم في ذكر تاريخ السماع القديم للسماع المتقدم مزية على ما تأخر عنه لأن المتأخر يكون بعرض الخطر وعدم أمان الغرر لكبر سن الراوي وتغير أحواله وتناقص آلاته واختلال حفظه وبعد ذكره ولو سلم الراوي عند كبر السن وتناهي العمر من دخول الوهم','1479','0','0');
INSERT INTO [t13012] VALUES (194,'من روى حديثا ذكر أنه سمعه أولا نازلا وآخرا عاليا','1486','0','0');
INSERT INTO [t13012] VALUES (195,'من روى حديثا ذكر أنه سأل شيخه عنه حتى حدثه به','1493','0','0');
INSERT INTO [t13012] VALUES (196,'من روى حديثا يتفرد بروايته فذكر أنه لا يوجد إلا عنده','1497','0','0');
INSERT INTO [t13012] VALUES (197,'من روى حديثا اشترط في روايته البراءة من عهدته','1499','0','0');
INSERT INTO [t13012] VALUES (198,'تحريم رواية الأخبار الكاذبة ووجوب إسقاط الأحاديث الباطلة يجب على المحدث أن لا يروي شيئا من الأخبار المصنوعة والأحاديث الباطلة الموضوعة فمن فعل ذلك باء بالإثم المبين ودخل في جملة الكذابين كما أخبر الرسول صلى الله عليه وسلم','1502','0','0');
INSERT INTO [t13012] VALUES (199,'استحباب رواية المشاهير والصدوف عن الغرائب والمناكير','1507','0','0');
INSERT INTO [t13012] VALUES (200,'اختبار جياد الأحاديث وعيونها التي لا يدخل عليها التعليل في أسانيدها ولا متونها','1514','0','0');
INSERT INTO [t13012] VALUES (201,'الصلاة على النبي صلى الله عليه وسلم كلما ذكر والترحم على الصحابة رضي الله عنهم إذا انتهى المستملي في الإسناد إلى ذكر النبي صلى الله عليه وسلم استحب له الصلاة عليه رافعا صوته بذلك وهكذا يفعل في كل حديث عاد فيه ذكره صلى الله عليه وسلم','1522','0','0');
INSERT INTO [t13012] VALUES (202,'ذكر ما يستحب في الإملاء روايته لكافة الناس وما يكره من ذلك خوف دخول الشبهة فيه والإلباس ينبغي أن يملى من الأحاديث ما تعلق بأصول المعارف والديانات وتضمن الدلائل على صحة المذاهب والاعتقادات إذ كان ذلك أس الشرع ودعامته وأصل كل نوع من التكليف وقاعدته','1535','0','0');
INSERT INTO [t13012] VALUES (203,'كراهة رواية أحاديث بني إسرائيل المأثورة عن أهل الكتاب','1557','0','0');
INSERT INTO [t13012] VALUES (204,'إملاء فضائل الصحابة ومناقبهم والنشر لمحاسن أعمالهم وسوابقهم. إن الله تعالى اختار لنبيه أعوانا جعلهم أفضل الخلق وأقواهم إيمانا وشد بهم أزر الدين وأظهر بهم كلمة المؤمنين وأوجب لهم الثواب الجزيل وألزم أهل الملة ذكرهم بالجميل. فخالفت الرافضة أمر الله فيهم','1572','0','0');
INSERT INTO [t13012] VALUES (205,'كلام المحدث على الحديث ووصفه إياه بالصحة والثبوت وغير ذلك من الصفات والنعوت يستحب للراوي أن ينبه على فضل ما يرويه ويبين المعاني التي لا يعرفها إلا الحفاظ من أمثاله وذويه فإن كان الحديث عاليا علوا متفاوتا وصفه بذلك','1582','0','0');
INSERT INTO [t13012] VALUES (206,'كراهة إملال السامع وإضجاره بطول إملاء المحدث وإكثاره ينبغي للمحدث أن لا يطيل المجلس الذي يرويه بل يجعله متوسطا ويقتصد فيه حذرا من سآمة السامع وملله وأن يؤدي ذلك إلى فتوره عن الطلب وكسله فقد قال أبو العباس محمد بن يزيد المبرد فيما بلغني عنه: من أطال','1601','0','0');
INSERT INTO [t13012] VALUES (207,'ختم المجلس بالحكايات ومستحسن النوادر والإنشادات','1611','0','0');
INSERT INTO [t13012] VALUES (208,'ما سن في المجلس عند انقضائه من الاستغفار والحمد لله على آلائه','1622','0','0');
INSERT INTO [t13012] VALUES (209,'المعارضة بالمجلس المكتوب وإتقانه وإصلاح ما أفسد منه زيغ القلم وطغيانه','1630','0','0');
INSERT INTO [t13012] VALUES (210,'ما قيل في فوات المجلس والإعادة والاعتياض من تعذر استدراكه بالإجازة قد جرت العادة في الحديث بكراهة تكرير ماضيه واستثقال الإعادة لفائته ومنقضيه حتى قال بعض الشعراء يخاطب أحد الثقلاء','1637','0','0');
INSERT INTO [t13012] VALUES (211,'صورة الإجازة عرضت على أبي الحسن بن رزقويه في ورقة أسماء جماعة سألوه الإجازة وذلك لأنه كف بصره فأمرني أن أكتب تحت أسمائهم وأملى علي: قد أجزت لكل شخص ممن ذكر في هذه الورقة أن يروي عن كتابي إليه جميع ما أحب روايته مما حمل عني من سائر العلوم وصح عنده وزال','1652','0','0');
INSERT INTO [t13012] VALUES (212,'باب المنافسة في الحديث بين طلبته وكتمان بعضهم بعضا للضن بإفادته','1653','0','0');
INSERT INTO [t13012] VALUES (213,'باب وجوب المناصحة فيما يروى وذكر إفادة الطلبة بعضهم بعضا','1676','0','0');
INSERT INTO [t13012] VALUES (214,'باب القول في انتقاء الحديث وانتخابه لمن عجز عن كتبه على الوجه واستيعابه','1697','0','0');
INSERT INTO [t13012] VALUES (215,'رسم الحافظ العلامة على ما ينتخبه','1707','0','0');
INSERT INTO [t13012] VALUES (216,'ما ينبغي أن يصدف عن الاشتغال به في الانتقاء ينبغي للمنتخب أن يقصد تخير الأسانيد العالية والطرق الواضحة والأحاديث الصحيحة والروايات المستقيمة ولا يذهب وقته في الترهات من تتبع الأباطيل والموضوعات وتطلب الغرائب والمنكرات','1710','0','0');
INSERT INTO [t13012] VALUES (217,'ذكر ما يجب على الحفاظ من بيان أحوال الكذابين والنكير عليهم وإنهاء أمرهم إلى السلاطين إذا سلك الراوي طريقا تلحق به الظنة وتلوح ممن سلكها للعلماء أمارات التهمة لزم أهل المعرفة بيان أمره وإظهار حاله وإشادة ذكره ليتوقف عن الاحتجاج به وإن كان غير مقطوع على','1740','0','0');
INSERT INTO [t13012] VALUES (218,'من يجوز إطلاق اللفظ في وصفه وتسميته بالحفظ الوصف بالحفظ على الإطلاق ينصرف إلى أهل الحديث خاصة وهو سمة لهم لا يتعداهم ولا يوصف بها أحد سواهم لأن الراوي يقول نا فلان الحافظ فيحسن منه إطلاق ذلك إذ كان مستعملا عندهم يوصف به علماء أهل النقل ونقادهم. ولا يقول','1750','0','0');
INSERT INTO [t13012] VALUES (219,'ذكر بعض أخبار الموصوفين بالإكثار من كتب الحديث وسماعه','1761','0','0');
INSERT INTO [t13012] VALUES (220,'فصل قد ذكرنا أن الحفظ أرفع درجات الحديث وأعلاها وأشرف منازل الرواية وأسماها وأبنا عزة وجود المتحققين به وذلك غير مانع من ابتغائه وطلبه','1779','0','0');
INSERT INTO [t13012] VALUES (221,'باب القول في كتب الحديث على وجهه وعمومه وذكر الحاجة إلى ذلك في الجمع لأصناف علومه من أول ما ينبغي أن يستعمله الطالب شدة الحرص على السماع والمسارعة إليه والملازمة للشيوخ','1788','0','0');
INSERT INTO [t13012] VALUES (222,'فأما الأحاديث المسندات إلى النبي صلى الله عليه وسلم فهي أصل الشريعة ومنها تستفاد الأحكام وما اتصل منها سنده وثبتت عدالة رجاله فلا خلاف بين العلماء أن قبوله واجب والعمل به لازم والراد له آثم','1810','0','0');
INSERT INTO [t13012] VALUES (223,'وأما الأحاديث الموقوفات على الصحابة فقد جعلها كثير من الفقهاء بمنزلة المرفوعات إلى النبي صلى الله عليه وسلم في لزوم العمل بها وتقديمها على القياس وإلحاقها بالسنن','1813','0','0');
INSERT INTO [t13012] VALUES (224,'وأما الأحاديث المرسلات عن النبي صلى الله عليه وسلم فهي أيضا عند خلق من العلماء بمنزلة المسندات المتصلة في تقبلها والعمل بمتضمنها ومن لم يرها كذلك من نقاد الآثار وحفاظ الأخبار فإنه يكتبها للاعتبار بها ولن يجعلها علة لغيرها','1816','0','0');
INSERT INTO [t13012] VALUES (225,'وأما المقاطيع فهي الموقوفات على التابعين فيلزم كتبها والنظر فيها لتتخير من أقوالهم ولا تشذ عن مذاهبهم','1819','0','0');
INSERT INTO [t13012] VALUES (226,'وأما أحاديث الضعاف ومن لا يعتمد على روايته فتكتب للمعرفة وأن لا تقلب إلى أحاديث الثقات ويعتبر بها أيضا غيرها من الروايات','1821','0','0');
