INSERT INTO [t13035] VALUES (1,'*','1','0','0');
INSERT INTO [t13035] VALUES (2,'باب ذكر الأمر بلزوم الجماعة والنهي عن الفرقة بل الاتباع وترك الابتداع قال محمد بن الحسين رحمه الله: إن الله عز وجل بمنه وفضله أخبرنا في كتابه عمن تقدم من أهل الكتابين اليهود والنصارى أنهم إنما هلكوا لما افترقوا في دينهم، وأعلمنا مولانا الكريم أن الذي','5','1','0');
INSERT INTO [t13035] VALUES (3,'باب ذكر أمر النبي صلى الله عليه وسلم أمته بلزوم الجماعة وتحذيره إياهم الفرقة','7','1','0');
INSERT INTO [t13035] VALUES (4,'باب ذكر افتراق الأمم في دينهم وعلى كم تفترق هذه الأمة؟ قال محمد بن الحسين رحمه الله: أخبرنا النبي صلى الله عليه وسلم: عن أمة موسى عليه السلام: " أنهم اختلفوا على إحدى وسبعين ملة، كلها في النار إلا واحدة "، وأخبرنا عن أمة عيسى عليه السلام أنهم','23','1','0');
INSERT INTO [t13035] VALUES (5,'باب ذكر خوف النبي صلى الله عليه وسلم على أمته وتحذيره إياهم سنن من قبلهم من الأمم','35','1','0');
INSERT INTO [t13035] VALUES (6,'باب ذم الخوارج وسوء مذاهبهم , وإباحة قتالهم وثواب من قتلهم أو قتلوه قال محمد بن الحسين: لم يختلف العلماء قديما وحديثا أن الخوارج قوم سوء عصاة لله تعالى ولرسوله صلى الله عليه وسلم، وإن صلوا وصاموا، واجتهدوا في العبادة، فليس ذلك بنافع لهم، نعم ,','41','1','0');
INSERT INTO [t13035] VALUES (7,'باب ذكر السنن والآثار فيما ذكرناه','44','1','0');
INSERT INTO [t13035] VALUES (8,'باب ذكر قتل علي بن أبي طالب كرم الله وجهه للخوارج مما أكرمه الله تعالى بقتالهم','60','1','0');
INSERT INTO [t13035] VALUES (9,'باب ذكر ثواب من قاتل الخوارج فقتلهم أو قتلوه','68','1','0');
INSERT INTO [t13035] VALUES (10,'باب في السمع والطاعة لمن ولي أمر المسلمين والصبر عليهم وإن جاروا , وترك الخروج عليهم ما أقاموا الصلاة','74','1','0');
INSERT INTO [t13035] VALUES (11,'باب فضل القعود في الفتنة عن الخوض فيها وتخوف العقلاء على قلوبهم أن تهوى ما يكرهه الله تعالى ولزوم البيوت والعبادة لله تعالى','86','1','0');
INSERT INTO [t13035] VALUES (12,'باب الحث على التمسك بكتاب الله تعالى وسنة رسول الله صلى الله عليه وسلم وسنة أصحابه رضي الله عنهم وترك البدع وترك النظر والجدال فيما يخالف فيه الكتاب والسنة وقول الصحابة رضي الله عنهم','99','1','0');
INSERT INTO [t13035] VALUES (13,'باب التحذير من طوائف يعارضون سنن النبي صلى الله عليه وسلم بكتاب الله تعالى وشدة الإنكار على هذه الطبقة قال محمد بن الحسين: ينبغي لأهل العلم والعقل إذا سمعوا قائلا يقول: قال رسول الله صلى الله عليه وسلم في شيء قد ثبت عند العلماء، فعارض إنسان جاهل فقال','108','1','0');
INSERT INTO [t13035] VALUES (14,'باب ذم الجدال والخصومات في الدين','125','1','0');
INSERT INTO [t13035] VALUES (15,'باب ذكر النهي عن المراء في القرآن','157','1','0');
INSERT INTO [t13035] VALUES (16,'باب تحذير النبي صلى الله عليه وسلم أمته الذين يجادلون بمتشابه القرآن وعقوبة الإمام لمن يجادل فيه','167','1','0');
INSERT INTO [t13035] VALUES (17,'باب ذكر الإيمان بأن القرآن كلام الله تعالى، وأن كلامه ليس بمخلوق ومن زعم أن القرآن مخلوق فقد كفر قال محمد بن الحسين: اعلموا رحمنا الله وإياكم أن قول المسلمين الذين لم يزغ قلوبهم عن الحق، ووفقوا للرشاد قديما وحديثا أن القرآن كلام الله تعالى ليس بمخلوق','174','1','0');
INSERT INTO [t13035] VALUES (18,'باب ذكر النهي عن مذاهب الواقفة قال محمد بن الحسين: وأما الذين قالوا: القرآن كلام الله ووقفوا فيه وقالوا: لا نقول: غير مخلوق، فهؤلاء عند كثير من العلماء ممن رد على من قال بخلق القرآن، قالوا: هؤلاء الواقفة: مثل من قال: القرآن مخلوق وأشر؛ لأنهم','208','1','0');
INSERT INTO [t13035] VALUES (19,'باب ذكر اللفظية، ومن زعم أن هذا القرآن حكاية للقرآن الذي في اللوح المحفوظ كذبوا قال محمد بن الحسين: احذروا رحمكم الله هؤلاء الذين يقولون: إن لفظه بالقرآن مخلوق، وهذا عند أحمد بن حنبل، ومن كان على طريقته منكر عظيم، وقائل هذا مبتدع، خبيث ولا يكلم،','218','1','0');
INSERT INTO [t13035] VALUES (20,'الرد على المرجئة','227','1','0');
INSERT INTO [t13035] VALUES (21,'باب تفريع معرفة الإيمان والإسلام وشرائع الدين قال محمد بن الحسين: الحمد لله الذي بنعمته تتم الصالحات، والحمد لله على كل حال، أما بعد فاعلموا رحمنا وإياكم أن الله تعالى بعث محمدا صلى الله عليه وسلم إلى الناس كافة ليقروا بتوحيده، فيقولوا: لا إله إلا','228','1','0');
INSERT INTO [t13035] VALUES (22,'باب معرفة أي يوم نزلت هذه الآية قوله تعالى اليوم أكملت لكم دينكم الآية','231','1','0');
INSERT INTO [t13035] VALUES (23,'باب على كم بني الإسلام؟','235','1','0');
INSERT INTO [t13035] VALUES (24,'باب ذكر سؤال جبريل للنبي عليهما السلام عن الإسلام ما هو؟ وعن الإيمان ما هو؟','240','1','0');
INSERT INTO [t13035] VALUES (25,'باب ذكر أفضل الإيمان ما هو؟ وأدنى الإيمان ما هو؟','245','1','0');
INSERT INTO [t13035] VALUES (26,'باب ذكر ما دل على زيادة الإيمان ونقصانه','249','1','0');
INSERT INTO [t13035] VALUES (27,'باب القول بأن الإيمان تصديق بالقلب، وإقرار باللسان، وعمل بالجوارح لا يكون مؤمنا، إلا أن تجتمع فيه هذه الخصال الثلاث قال محمد بن الحسين: اعملوا رحمنا الله وإياكم أن الذي عليه علماء المسلمين أن الإيمان واجب على جميع الخلق، وهو تصديق بالقلب، وإقرار','289','1','0');
INSERT INTO [t13035] VALUES (28,'باب كفر من ترك الصلاة','311','1','0');
INSERT INTO [t13035] VALUES (29,'باب ذكر الاستثناء في الإيمان من غير شك فيه قال محمد بن الحسين رحمه الله: من صفة أهل الحق، ممن ذكرنا من أهل العلم: الاستثناء في الإيمان، لا على جهة الشك، نعوذ بالله من الشك في الإيمان، ولكن خوف التزكية لأنفسهم من الاستكمال للإيمان، لا يدري أهو ممن','324','1','0');
INSERT INTO [t13035] VALUES (30,'باب فيمن كره من العلماء لمن يسأل لغيره، فيقول له: أنت مؤمن؟ هذا عندهم مبتدع رجل سوء قال محمد بن الحسين رحمه الله: إذا قال لك رجل: أنت مؤمن؟ فقل: آمنت بالله وملائكته وكتبه ورسله واليوم الآخر والموت والبعث من بعد الموت والجنة والنار وإن أحببت أن لا','345','1','0');
INSERT INTO [t13035] VALUES (31,'باب في المرجئة، وسوء مذاهبهم عند العلماء','356','1','0');
INSERT INTO [t13035] VALUES (32,'باب الرد على القدرية قال محمد بن الحسين رحمه الله: حسبي الله وكفى ونعم الوكيل، والحمد لله أهل الحمد والثناء، والعزة والبقاء، والعظمة والكبرياء، أحمده على تواتر نعمه، وقديم إحسانه وقسمه، حمد من يعلم أن مولاه الكريم يحب الحمد، فله الحمد على كل حال','377','1','0');
INSERT INTO [t13035] VALUES (33,'باب ذكر ما أخبر الله تعالى أنه يختم على قلوب من أراد من عباده فلا يهتدون إلى الحق، ولا يسمعونه، ولا يبصرونه، لأنه مقتهم فطبع على قلوبهم قال الله تعالى في سورة البقرة: إن الذين كفروا سواء عليهم أأنذرتهم أم لم تنذرهم لا يؤمنون ختم الله على قلوبهم،','378','1','0');
INSERT INTO [t13035] VALUES (34,'باب ذكر ما أخبر الله عز وجل أنه يضل من يشاء، ويهدي من يشاء وأن الأنبياء لا يهدون إلا من سبق في علم الله أنه يهديه قال الله عز وجل في سورة النساء فما لكم في المنافقين فئتين والله أركسهم بما كسبوا أتريدون أن تهدوا من أضل الله ومن يضلل الله فلن تجد له','383','1','0');
INSERT INTO [t13035] VALUES (35,'باب ذكر ما أخبر الله تعالى أنه أرسل الشياطين على الكافرين يضلونهم ولا يضلون إلا من سبق في علمه أنه لا يؤمن ولا يضرون أحدا إلا بإذن الله وكذلك السحرة لا يضرون أحدا إلا بإذن الله. قال الله تعالى في سورة البقرة: واتبعوا ما تتلوا الشياطين على ملك سليمان','388','1','0');
INSERT INTO [t13035] VALUES (36,'باب ذكر ما أخبر الله تعالى أن مشيئة الخلق تبع لمشيئة الله سبحانه وتعالى فمن شاء الله له أن يهتدي اهتدى، ومن شاء أن يضل لم يهتد أبدا، قال الله تعالى في سورة البقرة: كان الناس أمة واحدة، فبعث الله النبيين مبشرين ومنذرين، وأنزل معهم الكتاب بالحق،','391','1','0');
INSERT INTO [t13035] VALUES (37,'باب ذكر السنن والآثار المبينة بأن الله عز وجل خلق خلقه، من شاء خلقه للجنة، ومن شاء خلقه للنار، في علم قد سبق','404','1','0');
INSERT INTO [t13035] VALUES (38,'باب الإيمان بأن الله تعالى قدر المقادير على العباد قبل أن يخلق السموات والأرض','421','1','0');
INSERT INTO [t13035] VALUES (39,'باب الإيمان بما جرى به القلم مما يكون أبدا','426','1','0');
INSERT INTO [t13035] VALUES (40,'باب الإيمان بأن الله عز وجل قدر على آدم عليه السلام المعصية قبل أن يخلقه','434','1','0');
INSERT INTO [t13035] VALUES (41,'باب الإيمان بأن السعيد والشقي من كتب في بطن أمه','441','1','0');
INSERT INTO [t13035] VALUES (42,'باب الإيمان بأنه لا يصح لعبد الإيمان، حتى يؤمن بالقدر خيره وشره لا يصح له الإيمان إلا به','455','1','0');
INSERT INTO [t13035] VALUES (43,'باب ما ذكر في المكذبين بالقدر','465','1','0');
INSERT INTO [t13035] VALUES (44,'باب الإيمان أن كل مولود يولد على الفطرة','479','1','0');
INSERT INTO [t13035] VALUES (45,'باب ذكر ما تأدى إلينا عن أبي بكر وعمر رضي الله عنهما من ردهما على القدرية , وإنكارهما عليهم','499','1','0');
INSERT INTO [t13035] VALUES (46,'باب ما ذكر عن التابعين وغيرهم من الرد عليهم قال محمد بن الحسين رحمه الله: اعلموا رحمنا الله وإياكم أن من القدرية صنفا إذا قيل لبعضهم: من إمامكم في مذهبكم هذا؟ فيقولون: الحسن، وكذبوا على الحسن، قد أجل الله الكريم الحسن عن مذهب القدرية ونحن نذكر عن','539','1','0');
INSERT INTO [t13035] VALUES (47,'ابن سيرين','551','1','0');
INSERT INTO [t13035] VALUES (48,'مطرف بن عبد الله','558','1','0');
INSERT INTO [t13035] VALUES (49,'إياس بن معاوية','562','1','0');
INSERT INTO [t13035] VALUES (50,'زيد بن أسلم','565','1','0');
INSERT INTO [t13035] VALUES (51,'محمد بن كعب القرظي','571','1','0');
INSERT INTO [t13035] VALUES (52,'إبراهيم النخعي','576','1','0');
INSERT INTO [t13035] VALUES (53,'القاسم وسالم وغيرهما','580','1','0');
INSERT INTO [t13035] VALUES (54,'مجاهد','585','1','0');
INSERT INTO [t13035] VALUES (55,'جماعة من التابعين , وغيرهم من العلماء','589','1','0');
INSERT INTO [t13035] VALUES (56,'باب سيرة عمر بن عبد العزيز رحمه الله في أهل القدر','602','1','0');
INSERT INTO [t13035] VALUES (57,'باب ترك البحث والتنقير عن النظر في أمر المقدر كيف؟ ولم؟ بل الإيمان به والتسليم','622','1','0');
INSERT INTO [t13035] VALUES (58,'كتاب التصديق بالنظر إلى الله عز وجل قال محمد بن الحسين رحمه الله: الحمد لله على جميل إحسانه , ودوام نعمه حمد من يعلم أن مولاه الكريم يحب الحمد , فله الحمد على كل حال , وصل الله على محمد النبي وأصحابه , وحسبنا الله ونعم الوكيل , أما بعد: فإن الله تعالى','662','0','0');
INSERT INTO [t13035] VALUES (59,'فمما روى جرير بن عبد الله البجلي','682','58','0');
INSERT INTO [t13035] VALUES (60,'ومما روى أبو هريرة رضي الله عنه','687','58','0');
INSERT INTO [t13035] VALUES (61,'مما رواه أبو سعيد الخدري رضي الله عنه','692','58','0');
INSERT INTO [t13035] VALUES (62,'ومما رواه صهيب رضي الله عنه','695','58','0');
INSERT INTO [t13035] VALUES (63,'ومما روى أبو رزين العقيلي رضي الله عنه','699','58','0');
INSERT INTO [t13035] VALUES (64,'ومما روى أبو موسى الأشعري رضي الله عنه','702','58','0');
INSERT INTO [t13035] VALUES (65,'ومما روى عبد الله بن مسعود رضي الله عنه','706','58','0');
INSERT INTO [t13035] VALUES (66,'ومما روى ابن عباس رضي الله عنه','708','58','0');
INSERT INTO [t13035] VALUES (67,'ومما روي عن أنس بن مالك رضي الله عنه','710','58','0');
INSERT INTO [t13035] VALUES (68,'ومما روى جابر بن عبد الله رضي الله عنه','712','58','0');
INSERT INTO [t13035] VALUES (69,'ومما روى عبد الله بن عمر رضي الله عنهما','716','58','0');
INSERT INTO [t13035] VALUES (70,'ومما روى عدي بن حاتم الطائي رضي الله عنه','721','58','0');
INSERT INTO [t13035] VALUES (71,'حديث شجرة طوبى قال محمد بن الحسين رحمه الله: قد ذكر الله عز وجل ما أعد للمؤمنين من الكرامات في الجنة في غير موضع من كتابه عز وجل , وعلى لسان رسوله صلى الله عليه وسلم فكان مما أكرمهم به أنه قال عز وجل الذين آمنوا وعملوا الصالحات طوبى لهم وحسن مآب , وقد','724','58','0');
INSERT INTO [t13035] VALUES (72,'باب الإيمان بأن الله عز وجل يضحك قال محمد بن الحسين: رحمه الله: اعلموا وفقنا الله وإياكم للرشاد من القول والعمل أن أهل الحق يصفون الله عز وجل بما وصف به نفسه عز وجل , وبما وصفه به رسوله صلى الله عليه وسلم , وبما وصفه به الصحابة رضي الله عنهم , وهذا','730','58','0');
INSERT INTO [t13035] VALUES (73,'باب التحذير من مذاهب الحلولية قال محمد بن الحسين رحمه الله: الحمد لله الذي بنعمته تتم الصالحات , والحمد لله على كل حال , وصلى الله على محمد وآله وسلم أما بعد فإني أحذر إخواني المؤمنين مذهب الحلولية الذين لعب بهم الشيطان فخرجوا بسوء مذهبهم عن طريق أهل','752','58','0');
INSERT INTO [t13035] VALUES (74,'باب ذكر السنن التي دلت العقلاء على أن الله عز وجل على عرشه فوق سبع سماواته وعلمه محيط بكل شيء , لا يخفى عليه شيء في الأرض ولا في السماء','757','58','0');
INSERT INTO [t13035] VALUES (75,'كتاب الإيمان والتصديق بأن الله عز وجل كلم موسى عليه السلام الحمد لله المحمود على كل حال , وصلى الله على محمد النبي وعلى آله وسلم أما بعد فإنه من ادعى أنه مسلم ثم زعم أن الله عز وجل لم يكلم موسى فقد كفر , يستتاب فإن تاب وإلا قتل فإن قال قائل: لم؟ قيل:','781','0','0');
INSERT INTO [t13035] VALUES (76,'باب الإيمان والتصديق بأن الله عز وجل ينزل إلى سماء الدنيا كل ليلة قال محمد بن الحسين رحمه الله: الإيمان بهذا واجب , ولا يسع المسلم العاقل أن يقول: كيف ينزل؟ ولا يرد هذا إلا المعتزلة وأما أهل الحق فيقولون: الإيمان به واجب بلا كيف , لأن الأخبار قد صحت','799','75','0');
INSERT INTO [t13035] VALUES (77,'باب الإيمان بأن الله عز وجل خلق آدم على صورته بلا كيف','822','75','0');
INSERT INTO [t13035] VALUES (78,'باب الإيمان بأن قلوب الخلائق بين إصبعين من أصابع الرب عز وجل بلا كيف','830','75','0');
INSERT INTO [t13035] VALUES (79,'باب الإيمان بأن الله عز وجل يمسك السماوات على إصبع والأرضين على إصبع , والجبال والشجر على إصبع , والخلائق كلها على إصبع , والماء والثرى على إصبع','839','75','0');
INSERT INTO [t13035] VALUES (80,'باب ما روي أن الله عز وجل يقبض الأرض بيده , ويطوي السماوات بيمينه','844','75','0');
INSERT INTO [t13035] VALUES (81,'باب الإيمان بأن الله عز وجل يأخذ الصدقات بيمينه , فيربيها للمؤمن','847','75','0');
INSERT INTO [t13035] VALUES (82,'باب الإيمان بأن لله عز وجل يدين وكلتا يديه يمين','851','75','0');
INSERT INTO [t13035] VALUES (83,'باب الإيمان بأن الله عز وجل خلق آدم عليه السلام بيده وخط التوراة لموسى بيده , وخلق جنة عدن بيده , وقد قيل: العرش , والقلم , وقال لسائر الخلق: كن فكان , فسبحانه','856','75','0');
INSERT INTO [t13035] VALUES (84,'باب الإيمان بأن الله عز وجل لا ينام قال الله عز وجل: الله لا إله إلا هو الحي القيوم , لا تأخذه سنة ولا نوم الآيةوأخبرنا النبي صلى الله عليه وسلم قال: " إن الله عز وجل لا ينام , ولا ينبغي له أن ينام "','868','75','0');
INSERT INTO [t13035] VALUES (85,'باب التحذير من مذاهب أقوام يكذبون بشرائع مما يجب على المسلمين التصديق بها','874','75','0');
INSERT INTO [t13035] VALUES (86,'باب وجوب الإيمان بالشفاعة قال محمد بن الحسين: اعلموا رحمكم الله , أن المنكر للشفاعة يزعم أن من دخل النار فليس بخارج منها , وهذا مذهب المعتزلة يكذبون بها , وبأشياء سنذكرها إن شاء الله تعالى , مما لها أصل في كتاب الله عز وجل , وسنن رسول الله صلى الله عليه','879','75','0');
INSERT INTO [t13035] VALUES (87,'باب ما روي أن الشفاعة إنما هي لأهل الكبائر','889','75','0');
INSERT INTO [t13035] VALUES (88,'باب ما روي أن الشفاعة لمن لم يشرك بالله تعالى','898','75','0');
INSERT INTO [t13035] VALUES (89,'باب ذكر قول النبي صلى الله عليه وسلم " لكل نبي دعوة يدعو بها , واختبأت دعوتي شفاعة لأمتي "','902','75','0');
INSERT INTO [t13035] VALUES (90,'باب ذكر قول النبي صلى الله عليه وسلم " إن الله خيرني بين أن يدخل نصف أمتي الجنة أو الشفاعة فاخترت الشفاعة "','907','75','0');
INSERT INTO [t13035] VALUES (91,'باب الإيمان بأن أقواما يخرجون من النار فيدخلون الجنة بشفاعة النبي صلى الله عليه وسلم وشفاعة المؤمنين','913','75','0');
INSERT INTO [t13035] VALUES (92,'باب ذكر شفاعة العلماء والشهداء يوم القيامة','926','75','0');
INSERT INTO [t13035] VALUES (93,'كتاب الإيمان بالحوض الذي أعطي النبي صلى الله عليه وسلم','937','0','0');
INSERT INTO [t13035] VALUES (94,'باب التصديق والإيمان بعذاب القبر','955','93','0');
INSERT INTO [t13035] VALUES (95,'باب ذكر الإيمان والتصديق بمسألة منكر ونكير','976','93','0');
INSERT INTO [t13035] VALUES (96,'كتاب التصديق بالدجال , وأنه خارج في هذه الأمة','985','0','0');
INSERT INTO [t13035] VALUES (97,'باب استعاذة النبي صلى الله عليه وسلم من فتنة الدجال وتعليمه لأمته أن يستعيذوا بالله من فتنة الدجال','986','96','0');
INSERT INTO [t13035] VALUES (98,'باب الإيمان بنزول عيسى ابن مريم عليه السلام حكما عدلا فيقيم الحق ويقتل الدجال','1005','96','0');
INSERT INTO [t13035] VALUES (99,'كتاب الإيمان بالميزان: أنه حق توزن به الحسنات والسيئات','1013','0','0');
INSERT INTO [t13035] VALUES (100,'كتاب الإيمان والتصديق بأن الجنة والنار مخلوقتان وأن نعيم الجنة لا ينقطع عن أهلها أبدا وأن عذاب النار لا ينقطع عن أهلها أبدا قال محمد بن الحسين رحمه الله: اعلموا رحمنا الله وإياكم أن القرآن شاهد أن الله عز وجل خلق الجنة والنار قبل أن يخلق آدم عليه الصلاة','1030','0','0');
INSERT INTO [t13035] VALUES (101,'باب دخول النبي صلى الله عليه وسلم الجنة قال محمد بن الحسين رحمه الله: قد تقدم ذكرنا في الباب الذي مضى مثل قوله صلى الله عليه وسلم: " اطلعت في الجنة , فرأيت أكثر أهلها الفقراء , واطلعت في النار , فرأيت أكثر أهلها النساء " وسنذكر في هذا الباب مالا يجهله','1054','100','0');
INSERT INTO [t13035] VALUES (102,'باب ذكر الإيمان بأن أهل الجنة خالدون فيها أبدا وأن أهل النار من الكفار والمنافقين خالدون فيها أبدا قال محمد بن الحسين رحمه الله تعالى: بيان هذا في كتاب الله عز وجل , وفى سنن رسول الله صلى الله عليه وسلم , قال الله تعالى في سورة النساء: والذين آمنوا','1062','100','0');
INSERT INTO [t13035] VALUES (103,'باب فضائل النبي صلى الله عليه وسلم قال محمد بن الحسين الآجري رحمه الله: الحمد لله الذي بنعمته تتم الصالحات , والحمد لله على كل حال , وصلى الله على محمد النبي وآله وسلم أما بعد فإنه مما ينبغي لنا أن نبينه للمسلمين من شريعة الحق التي ندبهم الله عز وجل','1071','100','0');
INSERT INTO [t13035] VALUES (104,'باب ذكر ما نعت الله عز وجل به نبيه محمدا صلى الله عليه وسلم في كتابه من الشرف العظيم مما تقر به أعين المؤمنين قال محمد بن الحسين رحمه الله: اعلموا رحمنا الله وإياكم أن الله جل ذكره شرف نبيه محمدا صلى الله عليه وسلم بأعلى الشرف , ونعته بأحسن النعت ,','1073','100','0');
INSERT INTO [t13035] VALUES (105,'باب ذكر متى وجبت النبوة للنبي صلى الله عليه وسلم','1092','100','0');
INSERT INTO [t13035] VALUES (106,'باب في قول الله عز وجل لنبيه صلى الله عليه وسلم: ورفعنا لك ذكرك','1101','100','0');
INSERT INTO [t13035] VALUES (107,'باب ذكر قول الله عز وجل وتقلبك في الساجدين قال محمد بن الحسين رحمه الله: اعلموا رحمنا الله وإياكم أن النكاح كان في الجاهلية على أنواع غير محمودة إلا نكاحا واحدا نكاح صحيح: وهو هذا النكاح الذي سنه رسول الله صلى الله عليه وسلم لأمته , يخطب الرجل إلى','1108','100','0');
INSERT INTO [t13035] VALUES (108,'باب: ذكر مولد رسول الله صلى الله عليه وسلم ورضاعه ومنشئه إلى الوقت الذي جاءه الوحي','1114','100','0');
INSERT INTO [t13035] VALUES (109,'باب ذكر مبعثه صلى الله عليه وسلم قال محمد بن الحسين رحمه الله: اعلموا رحمنا الله وإياكم أن نبينا محمدا صلى الله عليه وسلم لم يزل نبيا من قبل خلق آدم عليه الصلاة والسلام يتقلب في أصلاب الأنبياء , وأبناء الأنبياء بالنكاح الصحيح حتى أخرجه الله تعالى من بطن','1119','100','0');
INSERT INTO [t13035] VALUES (110,'باب كيف نزل عليه الوحي صلى الله عليه وسلم','1122','100','0');
INSERT INTO [t13035] VALUES (111,'باب ذكر صفة النبي صلى الله عليه وسلم ونعته في الكتب السالفة من قبله','1130','100','0');
INSERT INTO [t13035] VALUES (112,'باب صفة رسول الله صلى الله عليه وسلم في التوراة والإنجيل وقد أمروا باتباعه في كتبهم قال محمد بن الحسين رحمه الله: قد تقدم ذكرنا لقول الله عز وجل: عذابي أصيب به من أشاء , ورحمتي وسعت كل شيء فسأكتبها للذين يتقون ويؤتون الزكاة , والذين هم بآياتنا يؤمنون','1134','100','0');
INSERT INTO [t13035] VALUES (113,'باب ذكر كيف كان ينزل الوحي على الأنبياء وعلى محمد نبينا صلى الله عليه وسلم , وعليهم أجمعين','1142','100','0');
INSERT INTO [t13035] VALUES (114,'باب ذكر ما ختم الله عز وجل بمحمد صلى الله عليه وسلم الأنبياء وجعله خاتم النبيين','1150','100','0');
INSERT INTO [t13035] VALUES (115,'باب ذكر ما استنقذ الله عز وجل الخلق بالنبي صلى الله عليه وسلم وجعله رحمة للعالمين صلى الله عليه وسلم','1157','100','0');
INSERT INTO [t13035] VALUES (116,'باب ما روي أن نبينا صلى الله عليه وسلم أكثر الأنبياء تبعا يوم القيامة','1165','100','0');
INSERT INTO [t13035] VALUES (117,'باب ذكر عدد أسماء رسول الله صلى الله عليه وسلم التي خصه الله عز وجل بها','1170','100','0');
INSERT INTO [t13035] VALUES (118,'باب صفة خلق رسول الله صلى الله عليه وسلم وأخلاقه الحميدة الجميلة التي خصه الله تعالى بها','1177','100','0');
INSERT INTO [t13035] VALUES (119,'باب ذكر ما خص الله عز وجل به النبي صلى الله عليه وسلم , أنه أسري به إليه قال محمد بن الحسين , رحمه الله: ومما خص الله عز وجل به النبي صلى الله عليه وسلم , مما أكرمه به , وعظم شأنه زيادة منه له في الكرامات أنه أسري بمحمد صلى الله عليه وسلم بجسده وعقله','1188','100','0');
INSERT INTO [t13035] VALUES (120,'باب ذكر ما خص الله عز وجل به النبي صلى الله عليه وسلم من الرؤية لربه عز وجل','1194','100','0');
INSERT INTO [t13035] VALUES (121,'باب ذكر ما فضل الله عز وجل به نبينا صلى الله عليه وسلم في الدنيا من الكرامات على جميع الأنبياء عليهم الصلاة والسلام','1206','100','0');
INSERT INTO [t13035] VALUES (122,'باب ذكر دلائل النبوة مما شاهده الصحابة رضي الله عنهم من النبي صلى الله عليه وسلم مما خصه بها مولاه الكريم','1214','100','0');
INSERT INTO [t13035] VALUES (123,'حديث الحنانة','1232','100','0');
INSERT INTO [t13035] VALUES (124,'باب ذكر سجود البهائم لرسول الله صلى الله عليه وسلم تعظيما له وإكراما له صلى الله عليه وسلم','1238','100','0');
INSERT INTO [t13035] VALUES (125,'باب ذكر فضل نبينا صلى الله عليه وسلم في الآخرة على سائر الأنبياء عليهم السلام','1242','100','0');
INSERT INTO [t13035] VALUES (126,'باب ما روي أن نبينا صلى الله عليه وسلم أول الناس دخولا الجنة','1247','100','0');
INSERT INTO [t13035] VALUES (127,'باب ذكر ما أعطي النبي صلى الله عليه وسلم من الشفاعة للخلق في يوم القيامة خصوصا له قال محمد بن الحسين رحمه الله: قد تقدم ذكر ما في هذا الكتاب , أعني كتاب الشريعة في باب: من كذب بالشفاعة فلم أحب إعادته خشية أن يطول به الكتاب. وباب: الحوض الذي أعطي','1253','100','0');
INSERT INTO [t13035] VALUES (128,'باب ذكر الكوثر الذي أعطي النبي صلى الله عليه وسلم في الجنة','1254','100','0');
INSERT INTO [t13035] VALUES (129,'باب ذكر ما خص الله عز وجل به النبي صلى الله عليه وسلم من المقام المحمود يوم القيامة قال محمد بن الحسين رحمه الله: اعلموا رحمنا الله وإياكم أن الله عز وجل أعطى نبينا صلى الله عليه وسلم , من الشرف العظيم والحظ الجزيل ما لم يعطه نبيا قبله مما قد تقدم ذكرنا','1264','100','0');
INSERT INTO [t13035] VALUES (130,'باب ذكر وفاة النبي صلى الله عليه وسلم','1283','100','0');
INSERT INTO [t13035] VALUES (131,'باب ذكر ما مدح الله عز وجل به المهاجرين والأنصار في كتابه مما أكرمهم الله به قال الله عز وجل: والسابقون الأولون من المهاجرين والأنصار والذين اتبعوهم بإحسان رضي الله عنهم ورضوا عنه وأعد لهم جنات تجري تحتها الأنهار خالدين فيها أبدا ذلك الفوز العظيم وقال','1287','100','0');
INSERT INTO [t13035] VALUES (132,'باب ذكر ما نعتهم به النبي صلى الله عليه وسلم من الفضل العظيم والحظ الجزيل','1292','100','0');
INSERT INTO [t13035] VALUES (133,'باب ذكر حزن النبي صلى الله عليه وسلم على الأنصار السبعين الذين قتلوا يوم بئر معونة','1314','100','0');
INSERT INTO [t13035] VALUES (134,'باب ذكر بيعة الأنصار للنبي صلى الله عليه وسلم على الإسلام بمكة وتصديقهم إياه','1319','100','0');
INSERT INTO [t13035] VALUES (135,'باب ذكر فضل جميع الصحابة رضي الله عنهم قال محمد بن الحسين رحمه الله: قد ذكرت من فضل المهاجرين والأنصار ما حضرني ذكره , وأنا أذكر فضل جميع الصحابة من المهاجرين والأنصار , وغيرهم من سائر الصحابة رضي الله عنهم','1323','100','0');
INSERT INTO [t13035] VALUES (136,'باب ذكر الشهادة للعشرة بالجنة رضي الله عنهم أجمعين قال محمد بن الحسين رحمه الله: واجب على كل مسلم عقل عن الله عز وجل وصانه عن مذاهب الرافضة والناصبة , أن يشهد لمن شهد له النبي صلى الله عليه وسلم بالجنة إذ كان على حراء فتزلزل به الجبل , ومعه أبو بكر ,','1347','100','0');
INSERT INTO [t13035] VALUES (137,'باب ذكر خلافة أبي بكر وعمر وعثمان وعلي رضي الله عنهم ونفعنا بمحبتهم قال محمد بن الحسين رحمه الله: اعلموا رحمنا الله وإياكم أن خلافة أبي بكر , وعمر , وعثمان , وعلي رضي الله عنهم بيانها في كتاب الله عز وجل وفي سنة رسول الله صلى الله عليه وسلم , وبيان من','1355','100','0');
INSERT INTO [t13035] VALUES (138,'باب بيان خلافة أبي بكر الصديق رضي الله عنه بعد رسول الله صلى الله عليه وسلم قال محمد بن الحسين رحمه الله: اعلموا رحمنا الله وإياكم أنه لم يختلف من شمله الإسلام وأذاقه الله الكريم طعم الإيمان أنه لم يكن خليفة بعد رسول الله صلى الله عليه وسلم , إلا أبو','1363','100','0');
INSERT INTO [t13035] VALUES (139,'باب ذكر الأخبار التي دلت على ما قلنا','1368','100','0');
INSERT INTO [t13035] VALUES (140,'باب ذكر خلافة أمير المؤمنين عمر بن الخطاب رضي الله عنه وعن جميع الصحابة أجمعين قال محمد بن الحسين رحمه الله: وكان أحق الناس بالخلافة بعد أبي بكر رضي الله عنه عمر بن الخطاب رضي الله عنه لما جعل الله الكريم فيه من الأحوال الشريفة الكريمة والدليل على ذلك','1386','100','0');
INSERT INTO [t13035] VALUES (141,'باب ذكر خلافة أمير المؤمنين عثمان بن عفان رضي الله عنه , وعن جميع الصحابة قال محمد بن الحسين رحمه الله: لما طعن عمر رضي الله عنه , وتيقن أنه الموت كان من حسن توفيق الله الكريم له , ونصيحته لله عز وجل في رعيته , وحسن النظر لهم حيا وميتا , أنه جعل الأمر','1398','100','0');
INSERT INTO [t13035] VALUES (142,'باب ذكر خلافة أمير المؤمنين علي بن أبي طالب رضي الله عنه وعن ذريته الطيبة قال محمد بن الحسين رحمه الله: اعلموا رحمنا الله وإياكم أنه لم يكن بعد عثمان رضي الله عنه أحد أحق بالخلافة من علي رضي الله عنه لما أكرمه الله عز وجل به من الفضائل التي خصه الله','1409','100','0');
INSERT INTO [t13035] VALUES (143,'آخر ذكر خلافة أمير المؤمنين علي بن أبي طالب رضي الله عنه قال محمد بن الحسين رحمه الله: ومذهبنا أنا نقول في الخلافة والتفضيل بأبي بكر وعمر وعثمان وعلي رضي الله عنهم هذا طريق أهل العلم','1420','100','0');
INSERT INTO [t13035] VALUES (144,'باب ذكر تثبيت محبة أبي بكر وعمر وعثمان وعلي رضي الله عنهم في قلوب المؤمنين قال محمد بن الحسين رحمه الله: من علامة من أراد الله عز وجل به خيرا من المؤمنين وصحة إيمانهم محبتهم لأبي بكر وعمر وعثمان وعلي رضي الله عنهم كذا قال النبي صلى الله عليه وسلم','1422','100','0');
INSERT INTO [t13035] VALUES (145,'باب ذكر أتباع علي بن أبي طالب رضي الله عنه في خلافته لسنن أبي بكر وعمر وعثمان رضي الله عنهم , ونفعنا بحب الجميع قال محمد بن الحسين رحمه الله: فإن قال قائل: فهل غير علي بن أبي طالب في خلافته شيئا مما سنه أبو بكر وعمر وعثمان رضي الله عنهم؟ قيل له: معاذ','1431','100','0');
INSERT INTO [t13035] VALUES (146,'باب ذكر فضائل أبي بكر وعمر رضي الله عنهما قال محمد بن الحسين رحمه الله: المحمود الله على كل حال وصلى الله على محمد النبي وآله وسلم، اعلموا رحمنا الله وإياكم أنه قد تقدم ذكرنا لفضائل المهاجرين والأنصار , ولفضائل العشرة , أولهم أبو بكر وعمر , ولأبي بكر','1442','100','0');
INSERT INTO [t13035] VALUES (147,'باب تصديق أبي بكر رضي الله عنه لرسول الله صلى الله عليه وسلم وأنه أول الناس إسلاما','1443','100','0');
INSERT INTO [t13035] VALUES (148,'باب ذكر مواساة أبي بكر رضي الله عنه للنبي صلى الله عليه وسلم بنفسه وماله وأهله','1459','100','0');
INSERT INTO [t13035] VALUES (149,'باب ذكر قضاء أبي بكر دين رسول الله صلى الله عليه وسلم وعداته بعد موته','1469','100','0');
INSERT INTO [t13035] VALUES (150,'باب ذكر قصة أبي بكر رضي الله عنه في الغار مع النبي صلى الله عليه وسلم','1475','100','0');
INSERT INTO [t13035] VALUES (151,'باب ذكر قول النبي صلى الله عليه وسلم لأبي بكر رضي الله عنه وهما في الغار: " ما ظنك يا أبا بكر باثنين الله ثالثهما "','1480','100','0');
INSERT INTO [t13035] VALUES (152,'باب في قول الله عز وجل: فأنزل الله سكينته عليه','1484','100','0');
INSERT INTO [t13035] VALUES (153,'باب ما ذكر أن الله عز وجل عاتب جميع الناس في النبي صلى الله عليه وسلم إلا أبا بكر رضي الله عنه , فإنه أخرجه من المعاتبة','1487','100','0');
INSERT INTO [t13035] VALUES (154,'باب ذكر صبر أبي بكر رضي الله عنه في ذات الله عز وجل مع رسول الله صلى الله عليه وسلم محبة لله تعالى ولرسوله يريد بذلك وجه الله عز وجل','1491','100','0');
INSERT INTO [t13035] VALUES (155,'باب ذكر بيان تقدمة أبي بكر رضي الله عنه على جميع الصحابة رضي الله عنهم في حياة رسول الله صلى الله عليه وسلم وبعد وفاته','1495','100','0');
INSERT INTO [t13035] VALUES (156,'باب ذكر صلاة النبي صلى الله عليه وسلم خلف أبي بكر الصديق رضي الله عنه','1508','100','0');
INSERT INTO [t13035] VALUES (157,'باب قول النبي صلى الله عليه وسلم: " ما طلعت الشمس ولا غربت على أحد بعد النبيين والمرسلين أفضل من أبي بكر رضي الله عنه "','1513','100','0');
INSERT INTO [t13035] VALUES (158,'فضائل أبي بكر وعمر رضي الله عنهما','1517','100','0');
INSERT INTO [t13035] VALUES (159,'باب ذكر منزلة أبي بكر وعمر رضي الله عنهما من رسول الله صلى الله عليه وسلم','1524','100','0');
INSERT INTO [t13035] VALUES (160,'باب إخبار النبي صلى الله عليه وسلم أن أبا بكر وعمر رضي الله عنهما وزيراه وأميناه من أهل الأرض','1532','100','0');
INSERT INTO [t13035] VALUES (161,'باب فضل إيمان أبي بكر وعمر رضي الله عنهما','1536','100','0');
INSERT INTO [t13035] VALUES (162,'باب ما روي أن أبا بكر وعمر رضي الله عنهما وزنا بالأمة فرجحا بإيمانهما','1540','100','0');
INSERT INTO [t13035] VALUES (163,'باب ذكر فضل درجات أبي بكر وعمر في الجنة','1543','100','0');
INSERT INTO [t13035] VALUES (164,'باب أمر النبي صلى الله عليه وسلم بالاقتداء بأبي بكر وعمر رضي الله عنهما','1549','100','0');
INSERT INTO [t13035] VALUES (165,'كتاب فضائل أمير المؤمنين عمر بن الخطاب رضي الله عنه','1554','0','0');
INSERT INTO [t13035] VALUES (166,'باب ذكر دعاء النبي صلى الله عليه وسلم لعمر بن الخطاب رضي الله عنه بأن يعز الله عز وجل به الإسلام','1555','165','0');
INSERT INTO [t13035] VALUES (167,'باب ابتداء إسلام عمر رضي الله عنه كيف كان','1558','165','0');
INSERT INTO [t13035] VALUES (168,'باب ذكر إعزاز الإسلام وأهله بإسلام عمر بن الخطاب رضي الله عنه','1560','165','0');
INSERT INTO [t13035] VALUES (169,'باب ما روي أن الله عز وجل جعل الحق على قلب عمر ولسانه , وأن السكينة تنطق على لسانه','1568','165','0');
INSERT INTO [t13035] VALUES (170,'باب ذكر قول النبي صلى الله عليه وسلم: " قد كان يكون في الأمم محدثون فإن يكن في أمتي فعمر بن الخطاب رضي الله عنه " قال محمد بن الحسين رحمه الله: هذا موافق للباب الذي قبله , ومعناه عند العلماء والله أعلم أن الله عز وجل يلقي في قلبه الحق , وينطق به لسانه','1576','165','0');
INSERT INTO [t13035] VALUES (171,'باب ما روي أن غضب عمر بن الخطاب عز ورضاه عدل','1579','165','0');
INSERT INTO [t13035] VALUES (172,'باب ذكر موافقة عمر بن الخطاب رضي الله عنه لربه عز وجل مما نزل به القرآن','1582','165','0');
INSERT INTO [t13035] VALUES (173,'باب ذكر قول النبي صلى الله عليه وسلم: " لو كان بعدي نبي لكان عمر بن الخطاب رضي الله عنه "','1586','165','0');
INSERT INTO [t13035] VALUES (174,'باب إخبار النبي صلى الله عليه وسلم بالعلم والدين الذي أعطي عمر بن الخطاب','1590','165','0');
INSERT INTO [t13035] VALUES (175,'باب ذكر بشارة النبي صلى الله عليه وسلم لعمر بن الخطاب رضي الله عنه بما أعد الله عز وجل له في الجنة','1594','165','0');
INSERT INTO [t13035] VALUES (176,'باب ما روي أن الشيطان يفرق من عمر بن الخطاب رضي الله عنه هيبة له','1602','165','0');
INSERT INTO [t13035] VALUES (177,'باب ما روي أن عمر بن الخطاب رضي الله عنه قفل الإسلام؛ وأن الفتن تكون بعده','1604','165','0');
INSERT INTO [t13035] VALUES (178,'باب ما روي أن عمر بن الخطاب سراج أهل الجنة','1608','165','0');
INSERT INTO [t13035] VALUES (179,'باب ذكر جوامع فضائل أبي بكر وعمر رضي الله عنهما قال محمد بن الحسين رحمه الله: قد اختصرت من ذكر فضائل أبي بكر وعمر رضي الله عنهما ما حضرني ذكره بمكة , وفضائلهما بحمد الله كثيرة , وفيما ذكرته مقنع لمن علمه؛ فزاده الله الكريم محبة لهما رضي الله عنهما','1610','165','0');
INSERT INTO [t13035] VALUES (180,'باب ذكر مقتل عمر بن الخطاب رضي الله عنه','1613','165','0');
INSERT INTO [t13035] VALUES (181,'ذكر نوح الجن على عمر رضي الله عنه','1617','165','0');
INSERT INTO [t13035] VALUES (182,'كتاب ذكر فضائل أمير المؤمنين عثمان بن عفان رضي الله عنه وعن جميع الصحابة قال محمد بن الحسين رحمه الله: أول فضائل عثمان بن عفان رضي الله عنه بعد الإيمان بالله عز وجل وبرسوله صلى الله عليه وسلم أن الله عز وجل أكرمه بأن زوجه بابنتي رسول الله صلى الله عليه','1623','0','0');
INSERT INTO [t13035] VALUES (183,'باب ذكر تزويج عثمان رضي الله عنه بابنتي رسول الله صلى الله عليه وسلم , فضيلة خص بها','1624','182','0');
INSERT INTO [t13035] VALUES (184,'باب ذكر مواساة عثمان رضي الله عنه للنبي صلى الله عليه وسلم بماله وتجهيزه لجيش العسرة','1631','182','0');
INSERT INTO [t13035] VALUES (185,'باب إخبار النبي صلى الله عليه وسلم بفتن كائنة وأن عثمان رضي الله عنه وأصحابه منها برءاء','1636','182','0');
INSERT INTO [t13035] VALUES (186,'باب إخبار النبي صلى الله عليه وسلم لعثمان رضي الله عنه أنه يقتل مظلوما','1640','182','0');
INSERT INTO [t13035] VALUES (187,'باب بذل عثمان دمه دون دماء المسلمين وترك النصرة لنفسه وهو يقدر رضي الله عنه','1646','182','0');
INSERT INTO [t13035] VALUES (188,'باب ذكر إنكار أصحاب رسول الله صلى الله عليه وسلم قتل عثمان رضي الله عنه وتعظيم ذلك عندهم وعرضهم أنفسهم لنصرته ومنعه إياهم','1652','182','0');
INSERT INTO [t13035] VALUES (189,'باب ذكر عذر عثمان رضي الله عنه عند أصحاب رسول الله صلى الله عليه وسلم','1669','182','0');
INSERT INTO [t13035] VALUES (190,'باب سبب قتل عثمان بن عفان رضي الله عنه إيش السبب الذي قتل به رضي الله عنه. قال محمد بن الحسين رحمه الله: فإن قال قائل: قد ذكرت عن النبي صلى الله عليه وسلم أنه ذكر فتنة تكون من بعده , ثم قال في عثمان: " فاتبعوا هذا وأصحابه فإنهم يومئذ على هدى "','1680','182','0');
INSERT INTO [t13035] VALUES (191,'باب ذكر قصة ابن سبأ الملعون وقصة الجيش الذين ساروا إلى عثمان رضي الله عنه فقتلوه','1684','182','0');
INSERT INTO [t13035] VALUES (192,'ذكر مسير الجيش الذين أشقاهم الله عز وجل بقتل عثمان رضي الله عنه وأعاذ الله الكريم أصحاب رسول الله صلى الله عليه وسلم من قتله','1687','182','0');
INSERT INTO [t13035] VALUES (193,'باب ما روي في قتلة عثمان رضي الله عنه','1692','182','0');
INSERT INTO [t13035] VALUES (194,'باب فيمن يشنأ عثمان رضي الله عنه أو يبغضه','1703','182','0');
INSERT INTO [t13035] VALUES (195,'باب ذكر إكرام النبي صلى الله عليه وسلم لعثمان رضي الله عنه وفضله عنده','1706','182','0');
INSERT INTO [t13035] VALUES (196,'كتاب فضائل أمير المؤمنين علي بن أبي طالب رضي الله عنه قال محمد بن الحسين رحمه الله: الحمد لله المتفضل علينا بالنعم الدائمة , ظاهرة وباطنة , حمد من يعلم أن مولاه الكريم يحب الحمد , فله الحمد على كل حال , وصلى الله على محمد النبي , وعلى آله الطيبين وسلم،','1717','0','0');
INSERT INTO [t13035] VALUES (197,'باب ذكر جامع مناقب علي بن أبي طالب رضي الله عنه','1719','196','0');
INSERT INTO [t13035] VALUES (198,'باب ذكر محبة الله عز وجل ورسوله صلى الله عليه وسلم لعلي بن أبي طالب رضي الله عنه وأن عليا محب لله عز وجل ورسوله صلى الله عليه وسلم','1724','196','0');
INSERT INTO [t13035] VALUES (199,'باب ذكر منزلة علي رضي الله عنه من رسول الله صلى الله عليه وسلم كمنزلة هارون من موسى','1738','196','0');
INSERT INTO [t13035] VALUES (200,'باب ذكر قول النبي صلى الله عليه وسلم " من كنت مولاه فعلي مولاه , ومن كنت وليه فعلي وليه "','1748','196','0');
INSERT INTO [t13035] VALUES (201,'باب ذكر دعاء النبي صلى الله عليه وسلم لمن والى علي بن أبي طالب رضي الله عنه , وتولاه , ودعائه به على من عاداه','1758','196','0');
INSERT INTO [t13035] VALUES (202,'باب ذكر عهد النبي صلى الله عليه وسلم إلى علي رضي الله عنه أنه لا يحبه إلا مؤمن ولا يبغضه إلا منافق والمؤذي لعلي رضي الله عنه المؤذي لرسول الله صلى الله عليه وسلم','1767','196','0');
INSERT INTO [t13035] VALUES (203,'باب ذكر ما أعطى علي بن أبي طالب رضي الله عنه من العلم والحكمة وتوفيق الصواب في القضاء , ودعا النبي صلى الله عليه وسلم له بالسداد والتوفيق','1787','196','0');
INSERT INTO [t13035] VALUES (204,'باب ذكر دعاء النبي صلى الله عليه وسلم لعلي رضي الله عنه بالعافية من البلاء مع المغفرة','1799','196','0');
INSERT INTO [t13035] VALUES (205,'باب أمر النبي صلى الله عليه وسلم لعلي رضي الله عنه بقتل الخوارج وأن الله عز وجل أكرمه بقتالهم','1804','196','0');
INSERT INTO [t13035] VALUES (206,'باب ذكر جوامع فضل علي بن أبي طالب رضي الله عنه الشريفة الكريمة عند الله عز وجل وعند رسوله صلى الله عليه وسلم وعند المؤمنين','1811','196','0');
INSERT INTO [t13035] VALUES (207,'باب ذكر مقتل أمير المؤمنين علي بن أبي طالب رضي الله عنه وما أعد الله الكريم لقاتله من الشقاء في الدنيا والآخرة قال محمد بن الحسين رحمه الله تعالى: قد قال النبي صلى الله عليه وسلم وهو على حراء وقد تحرك الجبل , فقال: " اثبت حراء , فإنما عليك نبي , وصديق','1834','196','0');
INSERT INTO [t13035] VALUES (208,'باب ذكر ما فعل بقاتل علي بن أبي طالب كرم الله وجهه','1840','196','0');
INSERT INTO [t13035] VALUES (209,'كتاب فضائل فاطمة رضي الله عنها قال محمد بن الحسين رحمه الله: اعلموا رحمنا الله وإياكم أن فاطمة رضي الله عنها كريمة على الله عز وجل , وعلى رسوله صلى الله عليه وسلم , وعند جميع المؤمنين , شرفها عظيم , وفضلها جزيل , النبي صلى الله عليه وسلم أبوها , وعلي','1844','0','0');
INSERT INTO [t13035] VALUES (210,'باب ذكر قول النبي صلى الله عليه وسلم إن فاطمة رضي الله عنها سيدة نساء عالمها','1845','209','0');
INSERT INTO [t13035] VALUES (211,'باب ذكر إكرام النبي صلى الله عليه وسلم لفاطمة رضي الله عنها وعظم قدرها عنده','1853','209','0');
INSERT INTO [t13035] VALUES (212,'باب غضب النبي صلى الله عليه وسلم لغضب فاطمة رضي الله عنها','1856','209','0');
INSERT INTO [t13035] VALUES (213,'باب ذكر تزويج فاطمة رضي الله عنها بعلي بن أبي طالب رضي الله عنه وعظيم ما شرفهما الله عز وجل به في التزويج من الكرامات التي خصهما الله عز وجل بها','1860','209','0');
INSERT INTO [t13035] VALUES (214,'باب ذكر بيان فضل فاطمة رضي الله عنها في الآخرة على سائر الخلائق','1866','209','0');
INSERT INTO [t13035] VALUES (215,'كتاب فضائل الحسن والحسين رضي الله عنهما قال محمد بن الحسين رحمه الله: الحمد لله المحمود على كل حال والمصطفى رسول الله صلى الله عليه وسلم وعلى آله أجمعين قال محمد بن الحسين: اعلموا رحمنا الله وإياكم: أن الحسن والحسين رضي الله عنهما خطرهما عظيم ,','1868','0','0');
INSERT INTO [t13035] VALUES (216,'باب ذكر قول النبي صلى الله عليه وسلم: " الحسن والحسين سيدا شباب أهل الجنة "','1869','215','0');
INSERT INTO [t13035] VALUES (217,'باب شبه الحسن والحسين رضي الله عنهما برسول الله صلى الله عليه وسلم','1879','215','0');
INSERT INTO [t13035] VALUES (218,'باب ذكر محبة النبي صلى الله عليه وسلم للحسن والحسين رضي الله عنهما','1885','215','0');
INSERT INTO [t13035] VALUES (219,'باب حث النبي صلى الله عليه وسلم أمته على محبة الحسن والحسين وأبيهما وأمهما رضي الله عنهم أجمعين','1889','215','0');
INSERT INTO [t13035] VALUES (220,'باب قول النبي صلى الله عليه وسلم للحسن والحسين رضي الله عنهما " هما ريحانتاي من الدنيا "','1894','215','0');
INSERT INTO [t13035] VALUES (221,'باب ذكر حمل النبي صلى الله عليه وسلم للحسن والحسين رضي الله عنهما على ظهره في الصلاة وغير الصلاة','1899','215','0');
INSERT INTO [t13035] VALUES (222,'باب ذكر ملاعبة النبي صلى الله عليه وسلم للحسن والحسين رضي الله عنهما','1907','215','0');
INSERT INTO [t13035] VALUES (223,'باب ذكر إخبار النبي صلى الله عليه وسلم عن صلاح المسلمين بالحسن بن علي رضي الله عنهما','1913','215','0');
INSERT INTO [t13035] VALUES (224,'باب إخبار النبي صلى الله عليه وسلم بقتل الحسين رضي الله عنه وقوله: " اشتد غضب الله على قاتله "','1918','215','0');
INSERT INTO [t13035] VALUES (225,'باب ذكر نوح الجن على الحسين رضي الله عنه','1927','215','0');
INSERT INTO [t13035] VALUES (226,'باب في الحسن والحسين رضي الله عنهما من أحبهما فللرسول صلى الله عليه وسلم يحب ومن أبغضهما فللرسول صلى الله عليه وسلم يبغض','1931','215','0');
INSERT INTO [t13035] VALUES (227,'فضائل خديجة أم المؤمنين رضي الله عنها قال محمد بن الحسين رحمه الله: المحمود الله على كل حال , والمصطفى رسول الله صلى الله عليه وسلم , وعلى آله الطيبين وسلم. قال محمد بن الحسين: اعلموا رحمنا الله وإياكم أن خديجة أم المؤمنين رضي الله عنها فضلها عظيم ,','1937','215','0');
INSERT INTO [t13035] VALUES (228,'باب ذكر تزويج النبي صلى الله عليه وسلم بخديجة رضي الله عنها وولدها منه','1940','215','0');
INSERT INTO [t13035] VALUES (229,'باب ذكر غضب النبي صلى الله عليه وسلم لخديجة رضي الله عنها وحسن ثنائه عليها','1942','215','0');
INSERT INTO [t13035] VALUES (230,'باب إخبار النبي صلى الله عليه وسلم أن خديجة رضي الله عنها سيدة نساء عالمها','1945','215','0');
INSERT INTO [t13035] VALUES (231,'باب بشارة النبي صلى الله عليه وسلم لخديجة رضي الله عنها بما أعد الله عز وجل لها في الجنة','1949','215','0');
INSERT INTO [t13035] VALUES (232,'كتاب جامع فضائل أهل البيت رضي الله عنهم قال محمد بن الحسين رحمه الله: قد ذكرت من فضائل أمير المؤمنين علي بن أبي طالب , وفاطمة , والحسن والحسين رضي الله عنهم ما حضرني ذكره بمكة , زادها الله شرفا , وفضلهم كثير عظيم , وأنا أذكر فضل أهل البيت جملة , الذين','1954','0','0');
INSERT INTO [t13035] VALUES (233,'باب ذكر قول الله عز وجل إنما يريد الله ليذهب عنكم الرجس أهل البيت ويطهركم تطهيرا قال محمد بن الحسين رحمه الله: هم الأربعة الذين حووا جميع الشرف , وهم: علي بن أبي طالب , وفاطمة , والحسن والحسين رضي الله عنهم','1959','232','0');
INSERT INTO [t13035] VALUES (234,'باب ذكر أمر النبي صلى الله عليه وسلم أمته بالتمسك بكتاب الله عز وجل وبسنة رسوله صلى الله عليه وسلم وبمحبة أهل بيته والتمسك على ما هم عليه من الحق والنهي عن التخلف عن طريقتهم الجميلة الحسنة','1967','232','0');
INSERT INTO [t13035] VALUES (235,'باب ذكر قول الله عز وجل: وتقطعت بهم الأسباب قال محمد بن الحسين رحمه الله: ومن فضائل أهل بيت رسول الله صلى الله عليه وسلم في الدنيا والآخرة: أن كل سبب ونسب يوم القيامة منقطع إلا نسب رسول الله صلى الله عليه وسلم وسببه وصهره قال ابن عباس: وتقطعت بهم','1978','232','0');
INSERT INTO [t13035] VALUES (236,'باب فضل جعفر بن أبي طالب رضي الله عنه قال محمد بن الحسين رحمه الله تعالى: جعفر بن أبي طالب رضي الله عنه أخو علي بن أبي طالب رضي الله عنه , قتل على عهد رسول الله صلى الله عليه وسلم في بعض غزواته , فقاتل قتالا شديدا حتى قطعت يداه , فيقال: إنه أخذ الرمح','1984','232','0');
INSERT INTO [t13035] VALUES (237,'باب فضل حمزة بن عبد المطلب رضي الله عنه قال محمد بن الحسين رحمه الله قال: أنبأنا أبو بكر بن أبي داود في " كتاب المصابيح " , يقال: أبو عمارة , ويقال: أبو يعلى حمزة بن عبد المطلب أسد الله عز وجل , وأسد رسوله , شهد بدرا , وصلى القبلتين , وهاجر بمهاجرة','1992','232','0');
INSERT INTO [t13035] VALUES (238,'كتاب فضائل العباس بن عبد المطلب وولده رضي الله عنهم أجمعين قال محمد بن الحسين رحمه الله: كان النبي صلى الله عليه وسلم يكرم عمه العباس بن عبد المطلب رضي الله عنه , ويعظمه ويغضب لغضبه ويقول له: " يا عم ". ويدعو له ولولده بأن يسترهم الله عز وجل من النار','1998','0','0');
INSERT INTO [t13035] VALUES (239,'باب ذكر تعظيم قدر العباس رضي الله عنه عند رسول الله صلى الله عليه وسلم','1999','238','0');
INSERT INTO [t13035] VALUES (240,'باب ذكر دعاء النبي صلى الله عليه وسلم للعباس رضي الله عنه ولولده , وأنه قد أجيب في ذلك','2005','238','0');
INSERT INTO [t13035] VALUES (241,'باب ذكر من آذى العباس رضي الله عنه فقد آذى رسول الله صلى الله عليه وسلم','2010','238','0');
INSERT INTO [t13035] VALUES (242,'باب غضب النبي صلى الله عليه وسلم لغضب العباس رضي الله عنه','2014','238','0');
INSERT INTO [t13035] VALUES (243,'باب ما روي أن للعباس رضي الله عنه شفاعة يشفع بها للناس يوم القيامة','2017','238','0');
INSERT INTO [t13035] VALUES (244,'باب فضل عبد الله بن عباس رضي الله عنه وما خصه الله الكريم به من الحكمة والتأويل الحسن للقرآن','2021','238','0');
INSERT INTO [t13035] VALUES (245,'باب ذكر ما انتشر من علم ابن عباس رضي الله عنه','2028','238','0');
INSERT INTO [t13035] VALUES (246,'باب ذكر وفاة ابن عباس رضي الله عنه بالطائف , والآية التي رؤيت عند دفنه','2035','238','0');
INSERT INTO [t13035] VALUES (247,'باب إيجاب حب بني هاشم أهل بيت النبي صلى الله عليه وسلم على جميع المؤمنين قال محمد بن الحسين رحمه الله: واجب على كل مؤمن ومؤمنة محبة أهل بيت رسول الله صلى الله عليه وسلم: بنو هاشم , علي بن أبي طالب وولده وذريته , وفاطمة وولدها وذريتها , والحسن والحسين','2038','238','0');
INSERT INTO [t13035] VALUES (248,'باب ذكر فضل بني هاشم على غيرهم','2043','238','0');
INSERT INTO [t13035] VALUES (249,'باب فضل قريش على غيرهم','2046','238','0');
INSERT INTO [t13035] VALUES (250,'باب ذكر فضائل طلحة والزبير وسعد وسعيد وعبد الرحمن بن عوف وأبي عبيدة بن الجراح رضي الله عنهم.','2049','238','0');
INSERT INTO [t13035] VALUES (251,'باب ذكر فضل طلحة والزبير رضي الله عنهما','2053','238','0');
INSERT INTO [t13035] VALUES (252,'باب فضل سعد بن أبي وقاص رضي الله عنه','2061','238','0');
INSERT INTO [t13035] VALUES (253,'باب ذكر فضل سعيد بن زيد بن عمرو بن نفيل رضي الله عنه قال محمد بن الحسين رحمه الله: قد ذكرنا فضله أنه من العشرة المشهود لهم بالجنة , وأنهم ممن قبض النبي صلى الله عليه وسلم وهو عنهم راض , وهو ممن رضيهم عمر بن الخطاب رضي الله عنه وسائر الصحابة , وكان مجاب','2065','238','0');
INSERT INTO [t13035] VALUES (254,'باب ذكر فضل عبد الرحمن بن عوف رضي الله عنه','2072','238','0');
INSERT INTO [t13035] VALUES (255,'باب فضل أبي عبيدة بن الجراح رضي الله عنه','2078','238','0');
INSERT INTO [t13035] VALUES (256,'كتاب مذهب أمير المؤمنين علي بن أبي طالب رضي الله عنه في أبي بكر وعمر وعثمان رضي الله عنهم أجمعين. قال محمد بن الحسين رحمه الله: أما بعد , فإن سائلا سأل , عن مذهب أمير المؤمنين علي بن أبي طالب رضي الله عنه في أبي بكر وعمر وعثمان رضي الله عنهم وكيف كانت','2084','0','0');
INSERT INTO [t13035] VALUES (257,'باب ذكر مذهب أمير المؤمنين علي بن أبي طالب رضي الله عنه في أبي بكر وعمر وعثمان رضي الله عنهم','2088','256','0');
INSERT INTO [t13035] VALUES (258,'ذكر دفن أبي بكر وعمر رضي الله عنهما مع النبي صلى الله عليه وسلم قال محمد بن الحسين رحمه الله: الحمد لله الذي بنعمته تتم الصالحات , والحمد لله على كل حال , وصلى الله على محمد النبي وآله وسلم. أما بعد: فإن سائلا سأل عن دفن أبي بكر وعمر رضي الله عنهما مع','2122','256','0');
INSERT INTO [t13035] VALUES (259,'باب ذكر قول النبي صلى الله عليه وسلم: " بين قبري ومنبري روضة من رياض الجنة "','2124','256','0');
INSERT INTO [t13035] VALUES (260,'باب ذكر وفاة النبي صلى الله عليه وسلم وعدد سنيه التي قبض عليها','2129','256','0');
INSERT INTO [t13035] VALUES (261,'باب ذكر دفن النبي صلى الله عليه وسلم في بيت عائشة رضي الله عنها','2136','256','0');
INSERT INTO [t13035] VALUES (262,'باب ذكر دفن أبي بكر وعمر رضي الله عنهما مع النبي صلى الله عليه وسلم قال محمد بن الحسين رحمه الله: لم يختلف جميع من شمله الإسلام , وأذاقه الله الكريم طعم الإيمان أن أبا بكر وعمر رضي الله عنهما دفنا مع النبي صلى الله عليه وسلم في بيت عائشة رضي الله عنها','2141','256','0');
INSERT INTO [t13035] VALUES (263,'باب ذكر صفة قبر النبي صلى الله عليه وسلم , وصفة قبر أبي بكر وصفة قبر عمر رضي الله عنهما','2158','256','0');
INSERT INTO [t13035] VALUES (264,'كتاب فضائل عائشة رضي الله عنها قال محمد بن الحسين رحمه الله: اعلموا رحمنا الله وإياكم أن عائشة رضي الله عنها وجميع أزواج رسول الله صلى الله عليه وسلم أمهات المؤمنين , فضلهن الله عز وجل برسوله صلى الله عليه وسلم , أولهن خديجة رضي الله عنها وقد ذكرنا','2166','0','0');
INSERT INTO [t13035] VALUES (265,'باب ذكر تزويج النبي صلى الله عليه وسلم لعائشة رضي الله عنها','2168','264','0');
INSERT INTO [t13035] VALUES (266,'باب ذكر مقدار سن عائشة رضي الله عنها وقت تزوجها رسول الله صلى الله عليه وسلم','2173','264','0');
INSERT INTO [t13035] VALUES (267,'باب ذكر محبة رسول الله صلى الله عليه وسلم لعائشة رضي الله عنها وملاعبته إياها','2178','264','0');
INSERT INTO [t13035] VALUES (268,'باب سلام جبريل عليه السلام على عائشة رضي الله عنها','2189','264','0');
INSERT INTO [t13035] VALUES (269,'باب ذكر علم عائشة رضي الله عنها','2193','264','0');
INSERT INTO [t13035] VALUES (270,'باب ذكر جامع فضائل عائشة رضي الله عنها','2200','264','0');
INSERT INTO [t13035] VALUES (271,'حديث الإفك قال محمد بن الحسين رحمه الله: إن الله عز وجل لم يزد عائشة رضي الله عنها في قصة الإفك إلا شرفا ونبلا وعزا , وزاد من رماها من المنافقين ذلا وخزيا , ووعظ من تكلم فيها من غير المنافقين من المؤمنين بأشد ما يكون من الموعظة , وحذرهم أن يعودوا لمثل','2205','264','0');
INSERT INTO [t13035] VALUES (272,'كتاب فضائل معاوية بن أبي سفيان رضي الله عنه قال محمد بن الحسين رحمه الله: معاوية رحمه الله كاتب رسول الله صلى الله عليه وسلم على وحي الله عز وجل وهو القرآن بأمر الله عز وجل , وصاحب رسول الله صلى الله عليه وسلم ومن دعا له النبي صلى الله عليه وسلم أن يقيه','2210','0','0');
INSERT INTO [t13035] VALUES (273,'باب ذكر دعاء النبي صلى الله عليه وسلم لمعاوية رضي الله عنه','2211','272','0');
INSERT INTO [t13035] VALUES (274,'باب بشارة النبي صلى الله عليه وسلم لمعاوية رحمه الله بالجنة','2221','272','0');
INSERT INTO [t13035] VALUES (275,'باب ذكر مصاهرة النبي صلى الله عليه وسلم لمعاوية بأخته أم حبيبة رحمه الله','2227','272','0');
INSERT INTO [t13035] VALUES (276,'باب ذكر استكتاب النبي صلى الله عليه وسلم لمعاوية رحمه الله بأمر من الله عز وجل','2232','272','0');
INSERT INTO [t13035] VALUES (277,'باب ذكر مشاورة النبي صلى الله عليه وسلم لمعاوية رحمه الله','2240','272','0');
INSERT INTO [t13035] VALUES (278,'باب ذكر صحبة معاوية رحمه الله للنبي صلى الله عليه وسلم ومنزلته عنده','2242','272','0');
INSERT INTO [t13035] VALUES (279,'باب ذكر تواضع معاوية رحمه الله في خلافته','2247','272','0');
INSERT INTO [t13035] VALUES (280,'باب ذكر تعظيم معاوية لأهل بيت رسول الله صلى الله عليه وسلم وإكرامه إياهم','2256','272','0');
INSERT INTO [t13035] VALUES (281,'باب ذكر تزويج أبي سفيان رحمه الله بهند أم معاوية رحمة الله عليهم','2263','272','0');
INSERT INTO [t13035] VALUES (282,'باب ذكر وصية النبي صلى الله عليه وسلم لمعاوية رضي الله عنه: " إن وليت فاعدل "','2266','272','0');
INSERT INTO [t13035] VALUES (283,'باب فضائل عمار بن ياسر رحمه الله','2270','272','0');
INSERT INTO [t13035] VALUES (284,'باب فضل عمرو بن العاص رحمه الله','2275','272','0');
INSERT INTO [t13035] VALUES (285,'ذكر الكف عما شجر بين أصحاب رسول الله صلى الله عليه وسلم ورحمة الله تعالى عليهم أجمعين قال محمد بن الحسين رحمه الله: ينبغي لمن تدبر ما رسمناه من فضائل أصحاب رسول الله صلى الله عليه وسلم وفضائل أهل بيته رضي الله عنهم أجمعين أن يحبهم ويترحم عليهم ويستغفر','2279','272','0');
INSERT INTO [t13035] VALUES (286,'باب ذكر اللعنة على من سب أصحاب رسول الله صلى الله عليه وسلم قال محمد بن الحسين رحمه الله: قد علم النبي صلى الله عليه وسلم أنه سيكون في آخر الزمان أقوام يلعنون أصحابه , فلعن صلى الله عليه وسلم من لعن أصحابه أو سبهم فقال: " من لعن أصحابي فعليه لعنة الله','2286','272','0');
INSERT INTO [t13035] VALUES (287,'باب ذكر ما جاء في الرافضة وسوء مذهبهم قال محمد بن الحسين رحمه الله: أول ما نبتدئ به من ذكرنا في هذا الباب , أنا نجل علي بن أبي طالب كرم الله وجهه , وفاطمة رضي الله عنها , والحسن والحسين رضي الله عنهما , وعقيل بن أبي طالب رضي الله عنه , وأولادهم , وأولاد','2305','272','0');
INSERT INTO [t13035] VALUES (288,'باب ذكر هجرة أهل البدع والأهواء قال محمد بن الحسين رحمه الله: ينبغي لكل من تمسك بما رسمناه في كتابنا هذا وهو كتاب الشريعة أن يهجر جميع أهل الأهواء من الخوارج والقدرية والمرجئة والجهمية , وكل من ينسب إلى المعتزلة , وجميع الروافض , وجميع النواصب , وكل من','2345','272','0');
INSERT INTO [t13035] VALUES (289,'باب عقوبة الإمام والأمير لأهل الأهواء قال محمد بن الحسين رحمه الله: ينبغي لإمام المسلمين ولأمرائه في كل بلد إذا صح عنده مذهب رجل من أهل الأهواء - ممن قد أظهره - أن يعاقبه العقوبة الشديدة , فمن استحق منهم أن يقتله قتله , ومن استحق أن يضربه ويحبسه وينكل','2372','272','0');
