INSERT INTO [t13036] VALUES (1,'ما أنعم الله على عبده نعمة في أهل أو مال أو ولد فيقول: ما شاء الله لا قوة إلا بالله، فيرى','1','0','0');
INSERT INTO [t13036] VALUES (2,'أحسني جوار نعم الله، فإنها قلما نفرت عن أهل بيت فكادت أن ترجع إليهم','2','0','0');
INSERT INTO [t13036] VALUES (3,'لا يرزق الله عبدا الشكر فيحرمه الزيادة، لأن الله يقول: {لئن شكرتم لأزيدنكم} [إبراهيم:','3','0','0');
INSERT INTO [t13036] VALUES (4,'اللهم أعني على ذكرك، وشكرك، وحسن عبادتك','4','0','0');
INSERT INTO [t13036] VALUES (5,'كيف لي أن أشكرك، وإني لا أصل شكرك إلا بنعمتك؟ قال: فأتاه الوحي: أن يا داود، أليس تعلم أن','5','0','0');
INSERT INTO [t13036] VALUES (6,'كيف لي أن أشكرك وأصغر نعمة وضعتها عندي من نعمك لا يجازي بها عملي كله؟ قال: فأتاه الوحي:','6','0','0');
INSERT INTO [t13036] VALUES (7,'ما قال عبد قط: الحمد لله إلا وجبت عليه نعمة بقوله الحمد لله فما جزاء تلك النعمة؟ جزاؤها','7','0','0');
INSERT INTO [t13036] VALUES (8,'إن الله أنعم على العباد على قدره، وكلفهم الشكر على قدرهم','8','0','0');
INSERT INTO [t13036] VALUES (9,'إنك لتحمد الله على نعمة عظيمة','9','0','0');
INSERT INTO [t13036] VALUES (10,'ما قال عبد كلمة أحب إليه وأبلغ في الشكر عنده من أن يقول: الحمد لله الذي أنعم علينا،','10','0','0');
INSERT INTO [t13036] VALUES (11,'الحمد لله، اللهم ربنا لك الحمد كما خلقتنا، ورزقتنا، وهديتنا، وعلمتنا، وأنقذتنا، وفرجت','11','0','0');
INSERT INTO [t13036] VALUES (12,'كيف يستطيع آدم أن يؤدي شكر ما صنعته إليه؟ خلقته بيدك، ونفخت فيه من روحك، وأسكنته جنتك،','12','0','0');
INSERT INTO [t13036] VALUES (13,'كان علي إذا دخل الخلاء قال: بسم الله الحافظ المؤدي، وإذا خرج مسح بيديه بطنه ثم قال: يا','13','0','0');
INSERT INTO [t13036] VALUES (14,'سمي نوح عليه السلام عبدا شكورا؛ لأنه لم يلبس جديدا، ولم يأكل طعاما إلا','14','0','0');
INSERT INTO [t13036] VALUES (15,'الحمد لله الذي يطعم ولا يطعم، من علينا فهدانا، وأطعمنا وسقانا، وكل بلاء حسن أبلانا، الحمد','15','0','0');
INSERT INTO [t13036] VALUES (16,'اللهم إني أعوذ بك من زوال نعمتك، وفجأة نقمتك، وتحول عافيتك، وجميع سخطك','16','0','0');
INSERT INTO [t13036] VALUES (17,'إن الله ليمتع بالنعمة ما شاء، فإذا لم يشكر قلبها عليهم عذابا','17','0','0');
INSERT INTO [t13036] VALUES (18,'إن النعمة موصلة بالشكر، والشكر معلق بالمزيد، وهما مقرونان في قرن، فلن ينقطع المزيد من','18','0','0');
INSERT INTO [t13036] VALUES (19,'الشكر ترك المعاصي','19','0','0');
INSERT INTO [t13036] VALUES (20,'كل نعمة لا تقرب من الله فهي بلية','20','0','0');
INSERT INTO [t13036] VALUES (21,'ذكر النعمة يورث الحب لله','21','0','0');
INSERT INTO [t13036] VALUES (22,'ألا تدخل بيتا دخله رسول الله صلى الله عليه وسلم ونطعمك سويقا وتمرا؟ ثم قال: إن الله عز','22','0','0');
INSERT INTO [t13036] VALUES (23,'إني لأرجو أن لا يقعد الله عبدا بين يديه فيعذبه','23','0','0');
INSERT INTO [t13036] VALUES (24,'يؤتى بالنعم يوم القيامة والحسنات والسيئات فيقول لنعمة من نعمه: خذي حقك من حسناته، فما','24','0','0');
INSERT INTO [t13036] VALUES (25,'لو أن لكل شعرة مني لسانين يسبحانك الليل والنهار ما قضيت نعمة من نعمك','25','0','0');
INSERT INTO [t13036] VALUES (26,'ينزل بالعبد الأمر فيدعو فيصرفه عنه، فيأتيه الشيطان فيضعف شكره، فيقول: إن الأمر كان أيسر','26','0','0');
INSERT INTO [t13036] VALUES (27,'قيدوا النعم بالشكر','27','0','0');
INSERT INTO [t13036] VALUES (28,'لأن أعافى فأشكر أحب إلي من أن أبتلى فأصبر','28','0','0');
INSERT INTO [t13036] VALUES (29,'إن كان هؤلاء تقبل منهم صيامهم فما هذا فعل الشاكرين، وإن كان هؤلاء لم يتقبل منهم صيامهم','29','0','0');
INSERT INTO [t13036] VALUES (30,'تقووا بهذه النعم التي أصبحتم فيها على الهرب من نار الله الموقدة، التي تطلع على الأفئدة،','30','0','0');
INSERT INTO [t13036] VALUES (31,'إذا رأيت سابغ نعمه عليك وأنت تعصيه، فاحذره','31','0','0');
INSERT INTO [t13036] VALUES (32,'إذا رأيت الله عز وجل يعطي العباد على ما يشاءون على معاصيهم إياه، فذلك استدراج منه','32','0','0');
INSERT INTO [t13036] VALUES (33,'أكثروا ذكر هذه النعمة، فإن ذكرها شكر','33','0','0');
INSERT INTO [t13036] VALUES (34,'أربع من أعطيهن فقد أعطي خير الدنيا والآخرة: قلب شاكر، ولسان ذاكر، وبدن على البلاء صابر،','34','0','0');
INSERT INTO [t13036] VALUES (35,'ما يعبأ الله بهذه، قال: فأنطقها الله فقالت: يا داود، أتعجبك نفسك، فوالذي نفسي بيده، لأنا','35','0','0');
INSERT INTO [t13036] VALUES (36,'إن داود نبي الله ظن في نفسه أن أحدا لم يمدح خالقه أفضل مما مدحه، وأن ملكا نزل وهو قاعد في','36','0','0');
INSERT INTO [t13036] VALUES (37,'الحمد لله حمدا كما ينبغي لكرم وجه ربي عز جلاله، فأوحى الله إليه: يا داود، أتعبت','37','0','0');
INSERT INTO [t13036] VALUES (38,'كنت أسألك فتشكر الله، وإني سألتك اليوم فشككت في الشكر','38','0','0');
INSERT INTO [t13036] VALUES (39,'لا يزال لسانك رطبا من ذكري','39','0','0');
INSERT INTO [t13036] VALUES (40,'أصبحت بين نعمتين لا أدري أيهما أفضل؟: ذنوب سترها الله فلا يستطيع أن يعيرني بها أحد، ومودة','40','0','0');
INSERT INTO [t13036] VALUES (41,'الشكر ترك المعصية','41','0','0');
INSERT INTO [t13036] VALUES (42,'كم من نعمة أنعمتها علي قل لك عندها شكري، وكم من بلية ابتليتني بها قل لك عندها صبرى، فيا','42','0','0');
INSERT INTO [t13036] VALUES (43,'خيري ينزل إليك، وشرك يصعد إلي، وأتحبب إليك بالنعم، وتتبغض إلي بالمعاصي، ولا يزال ملك كريم','43','0','0');
INSERT INTO [t13036] VALUES (44,'خيرك إلي نازل، وشري إليك صاعد، وكم ملك كريم قد صعد إليك بعمل قبيح، أنت مع غنائك عني تتحبب','44','0','0');
INSERT INTO [t13036] VALUES (45,'أصبحنا مغرقين في النعم، موقرين من الشكر، يتحبب إلينا ربنا وهو عنا غني، ونتمقت إليه ونحن','45','0','0');
INSERT INTO [t13036] VALUES (46,'من كرمك أنك تطاع فلا تعصى، ومن حلمك أنك تعصى كأنك لا ترى، وأي زمن من لم يعصك فيه سكان','46','0','0');
INSERT INTO [t13036] VALUES (47,'ما أنعم الله على عبد نعمة فعلم أنها من عند الله إلا كتب له شكرها، وما علم من عبد ندامة','47','0','0');
INSERT INTO [t13036] VALUES (48,'من لبس ثوبا جديدا فقال: بسم الله والحمد لله، غفر له، وسمعته يقول: من أكل طعاما فقال: بسم','48','0','0');
INSERT INTO [t13036] VALUES (49,'ما من عبد توكل بعبادة الله إلا غرم السماوات والأرض، يعني رزقه، فجعله في يدي بني آدم','49','0','0');
INSERT INTO [t13036] VALUES (50,'إذا أنعم الله على عبده نعمة أحب أن يرى أثر نعمته على عبده','50','0','0');
INSERT INTO [t13036] VALUES (51,'كلوا، واشربوا، وتصدقوا في غير مخيلة ولا سرف؛ فإن الله يحب أن يرى أثر نعمته على','51','0','0');
INSERT INTO [t13036] VALUES (52,'هل لك مال؟ قلت: نعم قال: من أي المال؟ قلت من كل المال، قد آتاني الله من الإبل، والخيل،','52','0','0');
INSERT INTO [t13036] VALUES (53,'إن الله يحب أن يرى أثر نعمته على عبده في مأكله ومشربه','53','0','0');
INSERT INTO [t13036] VALUES (54,'من أعطي خيرا فرؤي عليه سمي حبيب الله، محدثا بنعمة الله، ومن أعطي خيرا فلم ير عليه سمي','54','0','0');
INSERT INTO [t13036] VALUES (55,'لو رأيت ما نزل بنا هاهنا زمن الحجاج؟ فقال: مررت كأنك لم تدع إلى ضر مسك، أرجع فاحمد الله','55','0','0');
INSERT INTO [t13036] VALUES (56,'من عرف نعمة الله بقلبه، وحمده بلسانه، لم يستتم ذلك حتى يرى الزيادة، لقول الله عز وجل:','56','0','0');
INSERT INTO [t13036] VALUES (57,'إذا كنت تتقلب في نعمتي وأنت تتقلب في معصيتي فاحذرني لا أصرعك بين معاصيك، يا ابن آدم اتقني','57','0','0');
INSERT INTO [t13036] VALUES (58,'الشكر نصف الإيمان، والصبر نصف الإيمان، واليقين الإيمان كله','58','0','0');
INSERT INTO [t13036] VALUES (59,'لا تضركم دنيا إذا شكرتموها','59','0','0');
INSERT INTO [t13036] VALUES (60,'بلغني أن الله عز وجل إذا أنعم على قوم سألهم الشكر، فإذا شكروه كان قادرا على أن يزيدنهم،','60','0','0');
INSERT INTO [t13036] VALUES (61,'رب شاكر نعمة غيره ومنعم عليه ولا يدري، ويا رب حامل فقه غير فقيه','61','0','0');
INSERT INTO [t13036] VALUES (62,'يعدد المصائب، وينسى النعم ٦٣ - أنشدنا محمود الوراق في ذلك: [البحر السريع] يا أيها الظالم','62','0','0');
INSERT INTO [t13036] VALUES (63,'التحدث بالنعم شكر، وتركها كفر، ومن لا يشكر القليل لا يشكر الكثير، ومن لا يشكر الناس لا','63','0','0');
INSERT INTO [t13036] VALUES (64,'لأن أعافى وأشكر أحب إلي من أن أبتلى فأصبر','64','0','0');
INSERT INTO [t13036] VALUES (65,'الحمد لله، استغفر الله، قال: فانتظرته حتى وضع ما على ظهره، وقلت له: ما تحسن غير ذا؟ قال:','65','0','0');
INSERT INTO [t13036] VALUES (66,'اللهم إني أعوذ بك أن أبدل نعمك كفرا، أو أكفرها بعد معرفتها، أو أنساها فلا أثني','66','0','0');
INSERT INTO [t13036] VALUES (67,'ما لي أسمع الجن خيرا منكم جوابا لردها منكم؟ ما أتيت على قول {فبأي آلاء ربكما تكذبان}','67','0','0');
INSERT INTO [t13036] VALUES (68,'ما لي أراكم سكوتا؟ للجن كانوا أحسن منكم ردا، ما قرأت عليهم من مرة: {فبأي آلاء ربكما','68','0','0');
INSERT INTO [t13036] VALUES (69,'الحمد لله الذي جعله عذبا فراتا برحمته، ولم يجعله أجاجا','69','0','0');
INSERT INTO [t13036] VALUES (70,'كان يقول ذلك إذا شرب الماء','70','0','0');
INSERT INTO [t13036] VALUES (71,'لا آكل الخبيص، أو الفالوذج، لا أقوم بشكره، قال: فلقيت الحسن فقلت له في ذلك، فقال الحسن:','71','0','0');
INSERT INTO [t13036] VALUES (72,'أفلا أكون عبدا شكورا','72','0','0');
INSERT INTO [t13036] VALUES (73,'لم تأت على القوم إلا وفيهم مصل','73','0','0');
INSERT INTO [t13036] VALUES (74,'الحمد لله الذي كساني ما أواري به عورتي، وأتجمل به في حياتي، ثم مد يده فنظر إلى كل شيء','74','0','0');
INSERT INTO [t13036] VALUES (75,'لا أرجع حتى أشتري قميصا جديدا وألبسه وأحمد الله. قال مسعر: يرجو الثواب','75','0','0');
INSERT INTO [t13036] VALUES (76,'إني روأت في أمري فلم أر خيرا لا شرا معه إلا المعافاة والشكر، فرب شاكر بلاء في بلاء، ورب','76','0','0');
INSERT INTO [t13036] VALUES (77,'الستر من العافية','77','0','0');
INSERT INTO [t13036] VALUES (78,'من نعم الله على العبد أن يكون مأمونا على ما جاء به','78','0','0');
INSERT INTO [t13036] VALUES (79,'ما أصيب عبد بمصيبة إلا كان لله عليه فيها ثلاث نعم: أن لا تكون كانت في دينه، وأن لا تكون','79','0','0');
INSERT INTO [t13036] VALUES (80,'ليس بفقيه من لم يعد البلاء نعمة، والرخاء مصيبة','80','0','0');
INSERT INTO [t13036] VALUES (81,'مما يجب لله على ذي النعمة بحق نعمته ألا يتوصل بها إلى معصيته','81','0','0');
INSERT INTO [t13036] VALUES (82,'إذا كان شكري نعمة الله نعمة ... علي وفي أمثالها يجب الشكر فكيف وقوع الشكر إلا بفضله ...','82','0','0');
INSERT INTO [t13036] VALUES (83,'المؤمن عندي بمنزلة كل خير، يحمدني وأنا أنزع نفسه من بين جنبيه','83','0','0');
INSERT INTO [t13036] VALUES (84,'الحمد لله الذي لا يحمد على مكروه سواه','84','0','0');
INSERT INTO [t13036] VALUES (85,'ما هذا أجر أنعم الله عليك','85','0','0');
INSERT INTO [t13036] VALUES (86,'كلما أنعمت علي نعمة قل عندها شكري، وكلما ابتليتني ببلية قل عندها صبرى، فيا من قل شكري عند','86','0','0');
INSERT INTO [t13036] VALUES (87,'إني لأرجو أن لا يهلك عبد بين اثنتين: نعمة يحمد الله عليها، وذنب يستغفر','87','0','0');
INSERT INTO [t13036] VALUES (88,'فلتكن التقوى من بالك على كل حال، وخف الله في كل نعمة عليك لقلة الشكر عليها مع المعصية','88','0','0');
INSERT INTO [t13036] VALUES (89,'ذكرت أهل الجنة، وأهل النار، فشبهت أهل الجنة بأهل العافية، وأهل النار بأهل البلاء، فذلك','89','0','0');
INSERT INTO [t13036] VALUES (90,'إذا أحب أحدكم أن يعلم قدر نعمة الله عليه فلينظر إلى من تحته، ولا ينظر إلى من هو','90','0','0');
INSERT INTO [t13036] VALUES (91,'من لم يعرف نعمة الله عليه إلا في مطعمه ومشربه فقد قل علمه، وحضر عذابه','91','0','0');
INSERT INTO [t13036] VALUES (92,'سلم على رجل فرد عليه السلام، فقال عمر للرجل: كيف أنت؟ قال الرجل: أحمد الله إليك، قال عمر:','92','0','0');
INSERT INTO [t13036] VALUES (93,'لعلنا نلتقي في اليوم مرارا نسأل بعضنا ببعض، وأن نتقرب بذلك إلا لنحمد','93','0','0');
INSERT INTO [t13036] VALUES (94,'وأسبغ عليكم نعمه ظاهرة وباطنة} [لقمان: ٢٠] قال: لا إله إلا الله','94','0','0');
INSERT INTO [t13036] VALUES (95,'ما أنعم الله على العباد نعمة من أن عرفهم أن لا إله إلا الله، قال: وإن لا إله إلا الله لهم','95','0','0');
INSERT INTO [t13036] VALUES (96,'أصبحتم زهرا وأصبح الناس غبرا، وأصبح الناس ينسجون وأنتم تلبسون، وأصبح الناس يعطون وأنتم','96','0','0');
INSERT INTO [t13036] VALUES (97,'يا لها من نعمة ما أسبغها، يا لها من كرامة ما أظهرها، وأنه ما زال عن جادة قوم شيء أشد','97','0','0');
INSERT INTO [t13036] VALUES (98,'ما قال عبد الحمد لله، إلا وجبت عليه نعمة بقوله: الحمد لله، قال: فما جزاء تلك النعمة؟ قال:','98','0','0');
INSERT INTO [t13036] VALUES (99,'أحمده على ما لو أعطى به الخلق لم أعطهم إياه به، قال: وما ذاك؟ قال: أرأيت بصرك؟ أرأيت','99','0','0');
INSERT INTO [t13036] VALUES (100,'أيسرك ببصرك هذا الذي تبصر به مائة ألف درهم؟ قال: الرجل لا، قال: فبيديك مائة ألف؟ قال','100','0','0');
INSERT INTO [t13036] VALUES (101,'الصحة غنى الجسد','101','0','0');
INSERT INTO [t13036] VALUES (102,'أفضل الدعاء لا إله إلا الله، وأفضل الذكر الحمد لله','102','0','0');
INSERT INTO [t13036] VALUES (103,'إن الحمد أكثر الكلام تضعيفا','103','0','0');
INSERT INTO [t13036] VALUES (104,'إن سلمهم الله وغنمهم فإن لله علي في ذلك شكرا فقال: فلم يلبثوا أن غنموا وسلموا، فقال بعض','104','0','0');
INSERT INTO [t13036] VALUES (105,'لئن ردها الله علي لأحمدنه بمحامد يرضاها، فما لبث أن أتي بها بسرجها ولجامها، فركبها، فلما','105','0','0');
INSERT INTO [t13036] VALUES (106,'من قال: الحمد لله رب العالمين على كل نعمة كانت أو هي كائنة، خاصة أو عامة، فقد حمد الله','106','0','0');
INSERT INTO [t13036] VALUES (107,'ما أكثر من يلقاني فيدعو لي بالخير، ما أعرفهم، وما صنعت إليهم خيرا قط، فقال أبو حازم: لا','107','0','0');
INSERT INTO [t13036] VALUES (108,'إني أحبك، فقل: اللهم أعني على ذكرك، وشكرك، وحسن عبادتك، قال الصنابحي: قال لي معاذ: إني','108','0','0');
INSERT INTO [t13036] VALUES (109,'أسألك تمام النعمة في الأشياء كلها، والشكر لك عليها حتى ترضى وبعد الرضا، والخيرة في جميع','109','0','0');
INSERT INTO [t13036] VALUES (110,'ما أنعم الله على عبد نعمة فقال: الحمد لله، إلا كان ما أعطى أكثر مما أخذ، وبلغني عن سفيان','110','0','0');
INSERT INTO [t13036] VALUES (111,'لنعم الله فيما زوى عنا من الدنيا من نعمة أفضل مما بسط لنا منها، وذلك أن الله لم يرضها','111','0','0');
INSERT INTO [t13036] VALUES (112,'ينبغي للعالم أن يحمد الله على ما أعطاه، وأين يقع ما أعطاه، والحسنات تأتي عليه إلى ما','112','0','0');
INSERT INTO [t13036] VALUES (113,'أنعم الله علينا في كذا، فعل بنا كذا، فعل بنا كذا','113','0','0');
INSERT INTO [t13036] VALUES (114,'نسبغ عليهم النعم، ونمنعهم الشكر فقال غير سفيان: كلما أحدثوا ذنبا أحدثت لهم نعمة. قال ابن','114','0','0');
INSERT INTO [t13036] VALUES (115,'ذلك مكر الله بالعباد المضيعين قال: فقال يونس: إن العبد إذا كانت له عند الله منزلة فحفظها','115','0','0');
INSERT INTO [t13036] VALUES (116,'نعمة الله فيما زوى عني من الدنيا أعظم من نعمته علي فيما أعطاني منها، إني رأيته أعطاها','116','0','0');
INSERT INTO [t13036] VALUES (117,'الحمد لله الذي سوى خلقه فعدله، وكرم صورة وجهي وحسنها، وجعلني من','117','0','0');
INSERT INTO [t13036] VALUES (118,'بنعمة ربي، لا بما قدمت يداي، ولا بإرادتي، إنني كنت خاطئا','118','0','0');
INSERT INTO [t13036] VALUES (119,'العافية الملك الخفي','119','0','0');
INSERT INTO [t13036] VALUES (120,'وكم من مدخل لو مت فيه ... لكنت به نكالا في العشيره وقيت السوء والمكروه فيه ... ورحت بنعمة','120','0','0');
INSERT INTO [t13036] VALUES (121,'دعي عثمان إلى قوم اجتمعوا على ميتة لهم، فانطلق ليأخذهم، فتفرقوا قبل أن يبلغهم، فأعتق رقبة','121','0','0');
INSERT INTO [t13036] VALUES (122,'أصبحت بين نعمتين أميل بينهما، لا أدري أيهما أفضل، ذنب ستره الله فأصبحت لا أخاف أن يعيرني','122','0','0');
INSERT INTO [t13036] VALUES (123,'نعمة الله فيما زوى عني من الدنيا أفضل من نعمته فيما أعطاني','123','0','0');
INSERT INTO [t13036] VALUES (124,'الحمد لله الذي أذاقني لذته، وأبقى منفعته في جسدي، وأخرج عني أذاه','124','0','0');
INSERT INTO [t13036] VALUES (125,'فسمي عبدا شكورا','125','0','0');
INSERT INTO [t13036] VALUES (126,'ما شكر العينين يا أبا حازم؟ قال: إن رأيت بهما خيرا أعلنته، وإن رأيت بهما شرا سترته، قال:','126','0','0');
INSERT INTO [t13036] VALUES (127,'إني أبشركم بما يسرني، أنه جاءني من نحو أرضكم عين لي فأخبرني أن قد نصر نبيه، وأهلك عدوه،','127','0','0');
INSERT INTO [t13036] VALUES (128,'ما ابتلى الله عبدا ابتلاء إلا كان لله عليه فيه نعمة ألا يكون ابتلاه بأشد','128','0','0');
INSERT INTO [t13036] VALUES (129,'ما من الناس إلا مبتلى بعافية لينظر كيف شكره؟ ويبتليه لينظر كيف صبره؟','129','0','0');
INSERT INTO [t13036] VALUES (130,'ينزل البلاء ليستخرج الدعاء','130','0','0');
INSERT INTO [t13036] VALUES (131,'أنعم الله على عبد في حاجته أكثر من تضرعه إليه فيها','131','0','0');
INSERT INTO [t13036] VALUES (132,'كان إذا جاءه أمر يسره خر ساجدا شكرا لله','132','0','0');
INSERT INTO [t13036] VALUES (133,'لما تاب الله عليه سجد وألقى رداءه إلى الذي بشره','133','0','0');
INSERT INTO [t13036] VALUES (134,'بشرت الحسن بموت الحجاج وهو مختف، فسجد','134','0','0');
INSERT INTO [t13036] VALUES (135,'من صلى عليك صليت عليه، ومن سلم عليك سلمت عليه، فسجدت شكرا','135','0','0');
INSERT INTO [t13036] VALUES (136,'متى شئت أن ترى، من النعمة عليك أكثر منها عليه رأيته، قال سلام: إي والله، إذا أغلقت عليك','136','0','0');
INSERT INTO [t13036] VALUES (137,'اذكر المطروحين في الطريق، اذكر الذين لا مأوى لهم، ولا من يخدمهم، قال: ثم دخلت عليه بعد','137','0','0');
INSERT INTO [t13036] VALUES (138,'كم عاملته تبارك اسمه بما يكره فعاملك بما تحب، قلت: ما لا أحصي ذلك كثرة، قال: فهل قصدت','138','0','0');
INSERT INTO [t13036] VALUES (139,'ما كان الله لينعم على عبد في الدنيا فيفضحه في الآخرة، وحق على المنعم أن يتم على من أنعم','139','0','0');
INSERT INTO [t13036] VALUES (140,'يحق على المنعم أن يتم على من أنعم عليه','140','0','0');
INSERT INTO [t13036] VALUES (141,'الله أكرم من أن ينعم بنعمة إلا أتمها، أو يستعملها بعمل إلا قبله','141','0','0');
INSERT INTO [t13036] VALUES (142,'أنا في شيء قد شغل قلبي، قلت: ما هو؟ قالت: أريد أن أعرف نعمة الله علي طرفة عين، أو أعذب','142','0','0');
INSERT INTO [t13036] VALUES (143,'إنه ليكون في المجلس الرجل الواحد يحمد الله فتنقضي لأهل ذلك المجلس حوائجهم','143','0','0');
INSERT INTO [t13036] VALUES (144,'سروا عبدي المؤمن فكان لا يأتيه شيء يحبه إلا قال: الحمد لله، الحمد لله، ما شاء الله، قال:','144','0','0');
INSERT INTO [t13036] VALUES (145,'عبادتك خمسين سنة تعدل سكون هذا العرق','145','0','0');
INSERT INTO [t13036] VALUES (146,'أخبرني ما أدنى نعمتك علي، فأوحى الله إليه: يا داود، تنفس فتنفس فقال: هذا أدنى نعمتي','146','0','0');
INSERT INTO [t13036] VALUES (147,'ما أدري ما أقول، غير أنه ينبغي لهذا العبد أن لا يفتر عن الحمد والاستغفار، وابن آدم بين','147','0','0');
INSERT INTO [t13036] VALUES (148,'ما أفضل الشكر؟ قال: أن تشكرني على كل حال','148','0','0');
INSERT INTO [t13036] VALUES (149,'أتدري ماذا لله تعالى علي من هذه القرحة من نعمة؟ فأسكت قال: إذ لم يجعلها على حدقتي، ولا','149','0','0');
INSERT INTO [t13036] VALUES (150,'أكثر الدعاء بالعافية','150','0','0');
INSERT INTO [t13036] VALUES (151,'إن الناس لم يعطوا في هذه الدنيا شيئا أفضل من العفو والعافية، فسلوهما','151','0','0');
INSERT INTO [t13036] VALUES (152,'أشهد أنك فرد أحد صمد، لم يلد، ولم يولد، ولم يكن له كفوا أحد، وأشهد أن وعدك حق، ولقاءك حق،','152','0','0');
INSERT INTO [t13036] VALUES (153,'من تمام النعمة فوزا من النار، ودخولا إلى الجنة','153','0','0');
INSERT INTO [t13036] VALUES (154,'أكثروا سؤال العافية، فإن المبتلى وإن اشتد بلاؤه ليس بأحق بالدعاء من المعافى الذي لا يأمن','154','0','0');
INSERT INTO [t13036] VALUES (155,'أحدثك بما أحفظ، فقرأت عليه، فقال: هو هو','155','0','0');
INSERT INTO [t13036] VALUES (156,'إذا ذكر اسم الله على طعامه، وحمده على آخره، لم يسئل عن نعيم ذلك الطعام','156','0','0');
INSERT INTO [t13036] VALUES (157,'اللهم ما أصبح بنا من نعمة، أو عافية، أو كرامة في دين أو دنيا جرت علينا فيما مضى، أو هي','157','0','0');
INSERT INTO [t13036] VALUES (158,'اللهم لك الحمد بالإسلام، ولك الحمد بالقرآن، ولك الحمد بالأهل والمال، بسطت رزقنا، وأظهرت','158','0','0');
INSERT INTO [t13036] VALUES (159,'انظروا إلى من هو أسفل منكم، فإنه أجدر ألا تزدروا نعمة الله عليكم','159','0','0');
INSERT INTO [t13036] VALUES (160,'سمع حامد بحمد الله، ونعمته، وحسن بلائه علينا، ثلاثا، اللهم صاحبنا فأفضل علينا، ثلاثا،','160','0','0');
INSERT INTO [t13036] VALUES (161,'كن يقظانا، مرتادا لنفسك أخدانا، وكل خدن لا تؤتيك على مسرتي فلا تصحبه، فإنه لك عدو، وهو','161','0','0');
INSERT INTO [t13036] VALUES (162,'خلق الله آدم حين خلقه فأخرج أهل الجنة من صفحته اليمنى، وأخرج أهل النار من صفحته اليسرى،','162','0','0');
INSERT INTO [t13036] VALUES (163,'من قال حين يصبح: اللهم ما أصبحت بي من نعمة، أو بأحد من خلقك، فمنك وحدك، لا شريك لك، فلك','163','0','0');
INSERT INTO [t13036] VALUES (164,'من ابتلي فصبر، وأعطي فشكر، وظلم فغفر، ثم شكر ثم سكت قالوا: ما له يا رسول الله؟ قال: أولئك','164','0','0');
INSERT INTO [t13036] VALUES (165,'أكثر ذكر الموت يسلك عما سواه، وعليك بالدعاء؛ فإنك لا تدري متى يستجاب لك، وعليك بالشكر؛','165','0','0');
INSERT INTO [t13036] VALUES (166,'الحمد لله الذي هدانا، وأطعمنا، وسقانا، ونعمنا، الله أكبر، اللهم ألفتنا نعمك ونحن بكل شر،','166','0','0');
INSERT INTO [t13036] VALUES (167,'الحمد لله الذي أطعمني، وسقاني، وهداني، وكل بلاء حسن أبلاني، الحمد لله الرازق ذي القوة','167','0','0');
INSERT INTO [t13036] VALUES (168,'الحمد لله الذي أطعم وسقى، وسوغه وجعل له مخرجا','168','0','0');
INSERT INTO [t13036] VALUES (169,'رءوس النعم ثلاث، فأولها نعمة الإسلام التي لا تتم نعمة إلا بها، والثانية نعمة العافية التي','169','0','0');
INSERT INTO [t13036] VALUES (170,'أبلانا الله في سفرنا كذا، ثم قال: وإن تعداد النعم من الشكر','170','0','0');
INSERT INTO [t13036] VALUES (171,'الحمد لله على نعمته، فقال رجل مع وهب: أي شيء بقي عليك من النعمة تحمد الله عليها؟ فقال له','171','0','0');
INSERT INTO [t13036] VALUES (172,'إذا أنعم الله على عبده نعمة فحمده عندها فقد أدى شكرها','172','0','0');
INSERT INTO [t13036] VALUES (173,'أتي بختنصر بدانيال النبي عليه السلام فأمر به فحبس وضرى أسدين فألقاهما في جب معه، وطين','173','0','0');
INSERT INTO [t13036] VALUES (174,'الحمد لله الذي خلقني فأحسن خلقي وخلقي، وزان مني ما شان من غيري','174','0','0');
INSERT INTO [t13036] VALUES (175,'أنظر فما كان في وجهي زين وهو في غيري شين أحمد الله عليه','175','0','0');
INSERT INTO [t13036] VALUES (176,'عمل رجل من أهل الكوفة بخلق دنيء فأعتق جارية له إذ عافاه الله من ذلك','176','0','0');
INSERT INTO [t13036] VALUES (177,'وأمطر أهل الكوفة مطرا فهدمت منه البيوت فأعتق ابن أبي داود جارية له؛ شكرا لله إذ عافاه من','177','0','0');
INSERT INTO [t13036] VALUES (178,'ما تمام النعمة؟ قال: أن تضع رجلا على الصراط ورجلا في الجنة','178','0','0');
INSERT INTO [t13036] VALUES (179,'إذا أردت أن تعلم قدر ما أنعم الله عليك فغمض عينيك','179','0','0');
INSERT INTO [t13036] VALUES (180,'أما الظاهرة فالإسلام، وأما الباطنة فستره عليكم بالمعاصي','180','0','0');
INSERT INTO [t13036] VALUES (181,'لله عز وجل على أهل النار منة، ولو شاء أن يعذبهم بأشد من النار لعذبهم','181','0','0');
INSERT INTO [t13036] VALUES (182,'لأن أعافى وأشكر أحب إلي من أن أبتلى فأصبر','182','0','0');
INSERT INTO [t13036] VALUES (183,'جلساء الرحمن يوم القيامة من جعل فيه خصال الكرام، والسخاء، والحلم، والرحمة، والرأفة،','183','0','0');
INSERT INTO [t13036] VALUES (184,'من رأى صاحب بلاء فقال: الحمد لله الذي عافاني مما ابتلاك به، وفضلني عليك، وعلى جميع من خلق','184','0','0');
INSERT INTO [t13036] VALUES (185,'الشكر يأخذ بحزم الحمد وأصله وفرعه، وينظر في نعم من الله في بدنه، وسمعه، وبصره، ويديه،','185','0','0');
INSERT INTO [t13036] VALUES (186,'ما أنعم الله على عبد من نعمة في الدنيا فشكرها لله، وتواضع بها لله، إلا أعطاه نفعها في','186','0','0');
INSERT INTO [t13036] VALUES (187,'من لا يرى لله نعمة إلا في مطعم، أو مشرب، أو لباس، فقد قصر علمه، وحضر','187','0','0');
INSERT INTO [t13036] VALUES (188,'ما أدري أي النعمتين أفضل علي وعليكم؟ نعمة المسلك، أم نعمة المخرج، إذ أخرجه الله منا، قال','188','0','0');
INSERT INTO [t13036] VALUES (189,'ما من عبد يشرب من الماء القراح، فيدخل بغير أذى، ويخرج بغير أذى، إلا وجب عليه','189','0','0');
INSERT INTO [t13036] VALUES (190,'يا لها من نعمة، تأكل بلذة، وتخرج سرحا، لقد كان ملك من ملوك هذه القرية يرى الغلام من','190','0','0');
INSERT INTO [t13036] VALUES (191,'أصبح بنا من نعم الله ما لا نحصيه، مع كثرة ما نعصيه، فما ندري أيها نشكر؟ أجميل ما ظهر، أم','191','0','0');
INSERT INTO [t13036] VALUES (192,'كتبت إليك وأنا مسرور مستور، فأنا بهما مغرور، ذنب ستره علي فقد طابت نفسي لي كأنه مغفور،','192','0','0');
INSERT INTO [t13036] VALUES (193,'أراك حببت إليك العزلة، فما يمنعك من مخالطة الناس؟ فقال: ما أشغلني عن الناس، قال: فأت ذا','193','0','0');
INSERT INTO [t13036] VALUES (194,'عفا الله عنا وعنكم، لئن كنتم أصبحتم مستيقنين أن قد تقبل منكم هذا الشهر لقد كان لكم أن','194','0','0');
INSERT INTO [t13036] VALUES (195,'لئن شكرتم لأزيدنكم} [إبراهيم: ٧] قال: أي من طاعتي','195','0','0');
INSERT INTO [t13036] VALUES (196,'أنا الصغير الذي ربيته فلك الحمد، وأنا الضعيف الذي قويته فلك الحمد، وأنا الفقير الذي','196','0','0');
INSERT INTO [t13036] VALUES (197,'اختط لك الأنف فأقامه وأتمه، وحسن تمامه، ثم أدار منك الحدقة فجعلها بجفون مطبقة، وبأشفار','197','0','0');
INSERT INTO [t13036] VALUES (198,'اللهم لك الحمد بما بسطت في رزقنا، وأظهرت أمننا، وأحسنت معافاتنا، ومن كل ما سألناك من صالح','198','0','0');
INSERT INTO [t13036] VALUES (199,'وإن تعدوا نعمة الله لا تحصوها} [النحل: ١٨] قال: سبحان من لم يجعل في أحد من معرفة نعمته','199','0','0');
INSERT INTO [t13036] VALUES (200,'ما أدري أنعمته علي فيما بسط علي أفضل، أم نعمته فيما زوى عني','200','0','0');
INSERT INTO [t13036] VALUES (201,'خصلتان من كانتا فيه كتبه الله صابرا شاكرا، ومن لم يكونا فيه لم يكتبه صابرا ولا شاكرا، من','201','0','0');
INSERT INTO [t13036] VALUES (202,'أربع خصال من كن فيه بنى الله له بيتا في الجنة، من كان عصمة أمره لا إله إلا الله، وإذا','202','0','0');
INSERT INTO [t13036] VALUES (203,'لم يأكل شيئا قط إلا حمد الله، ولم يشرب شرابا قط إلا حمد الله، ولم يمش مشيا قط إلا حمد','203','0','0');
INSERT INTO [t13036] VALUES (204,'إذا أكل قال: الحمد لله، وإذا شرب قال: الحمد لله، وإذا لبس قال: الحمد لله، وإذا ركب قال:','204','0','0');
INSERT INTO [t13036] VALUES (205,'لو لم يعذب الله على معصيته، لكان ينبغي أن لا يعصى لشكر نعمته','205','0','0');
