INSERT INTO [t13038] VALUES (1,'يلقى في النار وتقول: هل من مزيد؟ حتى يضع رجله فيها، أو قال: قدمه , فتقول: قط','1','0','0');
INSERT INTO [t13038] VALUES (2,'يلقى في النار وتقول: هل من مزيد؟ حتى يضع رجله فيها , أو قال: قدمه فتقول: قط','2','0','0');
INSERT INTO [t13038] VALUES (3,'يلقى في النار , وتقول: هل من مزيد؟ حتى يضع رجله، أو قدمه فتقول: قط قط','3','0','0');
INSERT INTO [t13038] VALUES (4,'اختصمت الجنة والنار فقالت النار: يدخلني الجبابرة والمتكبرون، وقالت الجنة: يدخلني ضعفاء','4','0','0');
INSERT INTO [t13038] VALUES (5,'جهنم تسأل المزيد حتى يضع فيها قدمه فيزوي بعضها إلى بعض وتقول: قط قط','5','0','0');
INSERT INTO [t13038] VALUES (6,'افتخرت الجنة والنار، فقالت النار: يدخلني الجبابرة والملوك والأشراف، وقالت الجنة: يدخلني','6','0','0');
INSERT INTO [t13038] VALUES (7,'يلقى في النار أهلها وتقول: هل من مزيد؟','7','0','0');
INSERT INTO [t13038] VALUES (8,'لا تزال جهنم يلقى فيها وتقول: هل من مزيد؟ حتى يضع الجبار تبارك وتعالى فيها قدمه؛ فهنالك','8','0','0');
INSERT INTO [t13038] VALUES (9,'تحاجت الجنة والنار، فقالت النار: أوثرت بالمتكبرين والمتجبرين، وقالت الجنة: فما لي لا','9','0','0');
INSERT INTO [t13038] VALUES (10,'يلقى في النار وتقول: هل من مزيد؟ مرتين حتى يأتيها تبارك وتعالى فيضع قدمه فيها وتزوي','10','0','0');
INSERT INTO [t13038] VALUES (11,'اختصمت الجنة والنار، فقالت النار: يا رب يدخلني الجبارون والملوك والأشراف، وقالت الجنة: يا','11','0','0');
INSERT INTO [t13038] VALUES (12,'يمين الله عز وجل ملأى لا يغيضها نفقة، سحاء الليل والنهار، وقال: أرأيتم ما أنفق منذ خلق','12','0','0');
INSERT INTO [t13038] VALUES (13,'خلق القلم، فأخذه بيده اليمنى وكلتا يديه يمين، كتب ما يكون فيها من عمل معمول، بر أو فجور،','13','0','0');
INSERT INTO [t13038] VALUES (14,'رحمتي غلبت غضبي','14','0','0');
INSERT INTO [t13038] VALUES (15,'رحمتي تغلب غضبي','15','0','0');
INSERT INTO [t13038] VALUES (16,'أنفق أنفق عليك، فإن يمين الله ملأى سحاء لا ينقصها شيء بالليل والنهار أخرجه مسلم في','16','0','0');
INSERT INTO [t13038] VALUES (17,'إن الله عز وجل يبسط يده لمسيء الليل ليتوب بالنهار، ولمسيء النهار ليتوب بالليل حتى تطلع','17','0','0');
INSERT INTO [t13038] VALUES (18,'يحمل الخلق على إصبع، والسموات على إصبع، والأرضين على إصبع، والشجر على إصبع، والثرى على','18','0','0');
INSERT INTO [t13038] VALUES (19,'يحمل السموات على إصبع، والأرضين على إصبع، والخلائق على إصبع، والثرى على إصبع، فضحك رسول','19','0','0');
INSERT INTO [t13038] VALUES (20,'يمسك السموات على إصبع، والأرضين على إصبع، والجبال على إصبع، والشجر والثرى على إصبع،','20','0','0');
INSERT INTO [t13038] VALUES (21,'يجعل السموات على إصبع، والأرضين على إصبع، والخلائق على إصبع، والشجر على إصبع، والثرى على','21','0','0');
INSERT INTO [t13038] VALUES (22,'يحمل الخلائق على إصبع، والسموات على إصبع، والأرضين على إصبع، والشجر على إصبع، والثرى على','22','0','0');
INSERT INTO [t13038] VALUES (23,'يمسك السموات على إصبع، والأرضين على إصبع، والجبال على إصبع، والشجر والخلائق على إصبع، ثم','23','0','0');
INSERT INTO [t13038] VALUES (24,'يمسك السموات على إصبع، والأرضين على إصبع، والجبال على إصبع، والشجر على إصبع، والخلائق على','24','0','0');
INSERT INTO [t13038] VALUES (25,'يضع السموات على إصبع، والأرضين على إصبع، والجبال والثرى على إصبع، والشجر والماء على إصبع،','25','0','0');
INSERT INTO [t13038] VALUES (26,'الله عز وجل خلق ثلاثة أشياء بيده، خلق آدم بيده، وكتب التوراة بيده، وغرس الفردوس','26','0','0');
INSERT INTO [t13038] VALUES (27,'قلوب بني آدم كلها بين إصبعين من أصابع الرحمن عز وجل كقلب واحد يصرفه كيف يشاء، ثم قال رسول','27','0','0');
INSERT INTO [t13038] VALUES (28,'ضحك ربنا عز وجل من قنوط عباده، وقرب غيره وقال: قلت: أويضحك الرب عز وجل؟ قال: نعم، قلت: لن','28','0','0');
INSERT INTO [t13038] VALUES (29,'ضحك الله عز وجل من رجلين قتل أحدهما صاحبه ثم دخلا الجنة قال عبد الرحمن: سئل الزهري، عن','29','0','0');
INSERT INTO [t13038] VALUES (30,'فيتجلى لهم يضحك، قال سمعت النبي صلى الله عليه وسلم. . . رواه مسلم في الإيمان عن عبيد الله','30','0','0');
INSERT INTO [t13038] VALUES (31,'يتجلى لهم ضاحكا','31','0','0');
INSERT INTO [t13038] VALUES (32,'يتجلى لنا ربنا ضاحكا','32','0','0');
INSERT INTO [t13038] VALUES (33,'إن كرسيه وسع السموات والأرض، وإن له لأطيطا كأطيط الرحل الجديد إذا ركب من','33','0','0');
INSERT INTO [t13038] VALUES (34,'الكرسي موضع القدمين، ولا يقدر قدر العرش شيء،','34','0','0');
INSERT INTO [t13038] VALUES (35,'لا يستشفع بالله على أحد من خلقه، شأن الله أعظم من ذلك، ويحك أتدري ما الله عز وجل؟ إن عرشه','35','0','0');
INSERT INTO [t13038] VALUES (36,'لا يستشفع بالله على أحد من خلقه، شأن الله عز وجل أعظم من ذلك، ويحك أتدري ما الله عز وجل،','36','0','0');
INSERT INTO [t13038] VALUES (37,'قلوب الخلائق بين إصبعين من أصابع الله عز وجل','37','0','0');
INSERT INTO [t13038] VALUES (38,'يا مقلب القلوب ثبت قلبي على دينك، فقال له بعض الصحابة: أتخاف علينا وقد آمنا بك وبما جئت','38','0','0');
INSERT INTO [t13038] VALUES (39,'اللهم ثبت قلبي على دينك، فقال له بعض أصحابه: يا رسول الله أتخاف علينا وقد آمنا بك وصدقنا','39','0','0');
INSERT INTO [t13038] VALUES (40,'ما من قلب إلا وهو بين إصبعين من أصابع رب العالمين، إذا شاء أن يقيمه أقامه، وإذا شاء أن','40','0','0');
INSERT INTO [t13038] VALUES (41,'إذا ضرب أحدكم فليتجنب الوجه، ولا يقول: قبح الله وجهك ووجه من أشبه وجهك؛ فإن الله عز وجل','41','0','0');
INSERT INTO [t13038] VALUES (42,'لا تقبحوا الوجه؛ فإن الله عز وجل خلق آدم على صورته','42','0','0');
INSERT INTO [t13038] VALUES (43,'إذا ضرب أحدكم فليتجنب الوجه، ولا يقول: قبح الله وجهك ووجه من أشبه وجهك؛ فإن الله خلق آدم','43','0','0');
INSERT INTO [t13038] VALUES (44,'خلق الله عز وجل آدم على صورته وطوله ستون ذراعا','44','0','0');
INSERT INTO [t13038] VALUES (45,'لا تقبحوا الوجه؛ فإن الله خلق آدم على صورة الرحمن عز وجل','45','0','0');
INSERT INTO [t13038] VALUES (46,'إذا ضرب أحدكم فليتجنب الوجه؛ فإن صورة الإنسان على صورة الرحمن عز وجل','46','0','0');
INSERT INTO [t13038] VALUES (47,'وعدني ربي عز وجل أن يدخل الجنة من أمتي سبعين ألفا بغير حساب ولا عذاب، مع كل ألف سبعون','47','0','0');
INSERT INTO [t13038] VALUES (48,'الله عز وجل يقبل الصدقات، ويأخذها بيمينه، ويربيها لأحدكم كما يربي أحدكم فلوه أو مهره، وإن','48','0','0');
INSERT INTO [t13038] VALUES (49,'ما تصدق امرؤ بصدقة من كسب طيب - ولا يقبل الله إلا الطيب - إلا وضعها حين يضعها في كف','49','0','0');
INSERT INTO [t13038] VALUES (50,'وهي عندنا حق لا نشك فيها، ولكن إذا قيل كيف وضع قدمه وكيف ضحك؟ قلنا لا يفسر هذا ولا سمعنا','50','0','0');
INSERT INTO [t13038] VALUES (51,'هذه الأحاديث يعني مثل الكرسي موضع القدمين ونحو هذا، فقال وكيع: أدركنا إسماعيل بن أبي','51','0','0');
INSERT INTO [t13038] VALUES (52,'حق على ما سمعنا ممن نثق به ونرضاه','52','0','0');
INSERT INTO [t13038] VALUES (53,'من رأيتموه ينكر هذه الأحاديث فاحسبوه من الجهمية','53','0','0');
INSERT INTO [t13038] VALUES (54,'كل شيء وصف الله به نفسه في القرآن فقراءته تفسيره لا كيف ولا مثل','54','0','0');
INSERT INTO [t13038] VALUES (55,'نسلم هذه الأحاديث كما جاءت، ولا نقول كيف هذا ولم جاء هذا','55','0','0');
INSERT INTO [t13038] VALUES (56,'نقر بها ونحدث بها بلا كيف','56','0','0');
INSERT INTO [t13038] VALUES (57,'أشهد أنك فوق العرش، فوق سبع سموات، ليس كما يقول أعداؤك الزنادقة','57','0','0');
INSERT INTO [t13038] VALUES (58,'أما نحن فأخذنا ديننا عن أبناء التابعين عن أصحاب رسول الله صلى الله عليه وسلم فهم عمن','58','0','0');
INSERT INTO [t13038] VALUES (59,'يحدثون أن الله عز وجل يرى في الآخرة، حتى جاءنا ابن يهودي صباغ فزعم أن الله لا يرى يعني','59','0','0');
INSERT INTO [t13038] VALUES (60,'أمضها بلا كيف','60','0','0');
INSERT INTO [t13038] VALUES (61,'سلموا للسنة','61','0','0');
