INSERT INTO [t13041] VALUES (1,'باب تبين أحوال من نقل عنه الحديث ممن لم ينقل على صحبه','1','0','0');
INSERT INTO [t13041] VALUES (2,'باب الألف','41','0','0');
INSERT INTO [t13041] VALUES (3,'أبي بن عباس بن سهل بن سعد الساعدي الأنصاري المدني حدثنا أحمد بن زكير الحضرمي قال: حدثنا أيوب بن إسحاق بن سافري قال: سمعت يحيى بن معين يقول: ابنا العباس: أبي وعبد المهيمن ضعيفان','42','2','0');
INSERT INTO [t13041] VALUES (4,'أسامة بن زيد الليثي مولى لهم، مدني حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: حدثنا يحيى بن سعيد بأحاديث أسامة بن زيد ثم تركه , وقال يقول سمعت سعيد بن المسيب، على النكرة لما قال. وأخبرني آدم بن موسى قال: سمعت محمد بن إسماعيل البخاري يقول: كان','44','2','0');
INSERT INTO [t13041] VALUES (5,'أسامة بن زيد بن أسلم مولى عمر بن الخطاب رضي الله عنه، مدني حدثنا عبد الله بن أحمد بن حنبل قال: سألت أبي عن أسامة بن زيد بن أسلم فقال: أخشى أن لا يكون قويا في الحديث. حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين قال: أسامة ,','52','2','0');
INSERT INTO [t13041] VALUES (6,'أنس بن عبد الحميد أخو جرير بن عبد الحميد','53','2','0');
INSERT INTO [t13041] VALUES (7,'أنيس بن خالد التميمي، كوفي حدثنا آدم بن موسى قال: سمعت البخاري محمد بن إسماعيل يقول: أنيس بن خالد سمع المسيب بن رافع وجامع بن أبي راشد ومحارب بن دثار، روى عنه زيد بن حباب، ليس بذاك','55','2','0');
INSERT INTO [t13041] VALUES (8,'أسد بن عطاء أسد بن عطاء مجهول , روى عن عكرمة حديثا لا يتابع عليه , على أن دونه مندل , فلعله أتى منه , والحديث ما حدثناه محمد بن زنجويه الأصبهاني','56','2','0');
INSERT INTO [t13041] VALUES (9,'أسد بن عمرو البجلي، كوفي حدثنا آدم بن موسى قال: سمعت محمد بن إسماعيل البخاري قال: أسد بن عمرو أبو المنذر البجلي كوفي صاحب رأي ليس بذاك عندهم. حدثنا عبد الله بن أحمد قال: سألت أبي عن أسد بن عمرو صدوق؟ قال: أصحاب أبي حنيفة ليس ينبغي أن يروى عنهم شيء','58','2','0');
INSERT INTO [t13041] VALUES (10,'أسد بن وداعة، شامي','62','2','0');
INSERT INTO [t13041] VALUES (11,'أسد بن عبد الله البجلي كوفي حدثنا آدم بن موسى قال: سمعت محمد بن إسماعيل البخاري قال: أسد بن عبد الله البجلي سمع من يحيى بن عفيف عن جده، ولم يتابع في حديثه، كان على خراسان','64','2','0');
INSERT INTO [t13041] VALUES (12,'أسيد بن زيد الجمال كوفي حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد، سمعت يحيى بن معين يقول: أسيد بن زيد الجمال كذاب , ذهبت إلى الكرخ ونزلت في دار الحذائين فأردت أن أقول له: يا كذاب فغرقت من شغار الحذائين','66','2','0');
INSERT INTO [t13041] VALUES (13,'أشعث بن عبد الله الأعمى وهو الحداني، في حديثه وهم','69','2','0');
INSERT INTO [t13041] VALUES (14,'أشعث بن سعيد أبو الربيع السمان حدثنا آدم بن موسى قال: سمعت البخاري قال: أبو الربيع السمان: عن عاصم بن عبيد الله وأبي بشر وأبي هاشم روى عنه وكيع وأبو نعيم ليس بمتروك , وليس بالحافظ عندهم. قال البخاري: وقال ابن معين: ليس بثقة. حدثنا محمد بن عيسى قال','73','2','0');
INSERT INTO [t13041] VALUES (15,'أشعث بن سوار كوفي حدثنا محمد بن زكريا البلخي قال: حدثنا محمد بن المثنى قال: قال: ما سمعت يحيى ولا عبد الرحمن حدثا عن سفيان عن أشعث بن سوار شيئا قط. حدثنا ابن عيسى قال: حدثنا عمرو بن علي قال: كان يحيى , وعبد الرحمن لا يحدثان عن أشعث بن سوار , قال','76','2','0');
INSERT INTO [t13041] VALUES (16,'أشعث بن براز الهجيمي بصري حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين يقول: أشعث بن براز الهجيمي ليس بشيء','78','2','0');
INSERT INTO [t13041] VALUES (17,'أشعث ابن عم حسن بن صالح كوفي كان له مذهب، ليس ممن يضبط الحديث','80','2','0');
INSERT INTO [t13041] VALUES (18,'إياس بن خليفة مجهول في الرواية، في حديثه وهم','82','2','0');
INSERT INTO [t13041] VALUES (19,'إياس بن أبي إياس مجهول أيضا، حديثه غير محفوظ','88','2','0');
INSERT INTO [t13041] VALUES (20,'أمية بن سعيد الأموي مجهول أيضا , في حديثه وهم، ولعله أتي من عمرو بن الحصين','90','2','0');
INSERT INTO [t13041] VALUES (21,'أبان الرقاشي عن أبي موسى بصري حدثنا آدم بن موسى قال: سمعت البخاري قال: أبان الرقاشي , عن أبي موسى روى عنه ابنه يزيد ولم يصح حديثه','93','2','0');
INSERT INTO [t13041] VALUES (22,'أبان بن تغلب، كوفي حدثنا محمد بن إسماعيل وأحمد بن علي الأبار قالا: حدثنا الحسن بن علي الحلواني قال: سمعت يزيد بن هارون وقيل له: رأيت أبان بن تغلب , قال: نعم , قالوا: فكيف لم تسمع منه شيئا؟ - قال الصائغ: فكيف لم تسأله عن شيء؟ - قال: لم يكن','95','2','0');
INSERT INTO [t13041] VALUES (23,'أبان بن عثمان الأحمر، كوفي','96','2','0');
INSERT INTO [t13041] VALUES (24,'أبان بن أبي عياش , وهو أبان بن فيروز بصري حدثنا أحمد بن صدقة قال: حدثنا محمد بن حرب الواسطي قال: سمعت يزيد بن هارون يقول: قال شعبة: ردائي وحماري في المساكين صدقة إن لم يكن أبان بن أبي عياش يكذب في هذا الحديث قال: قلت له: فلم سمعت منه؟ قال: ومن','98','2','0');
INSERT INTO [t13041] VALUES (25,'أبان بن جبلة، كوفي، أبو عبد الرحمن حدثني آدم بن موسى قال: سمعت البخاري قال: أبان بن جبلة عن أبي إسحاق الهمداني، كنيته أبو عبد الرحمن الكوفي منكر الحديث','102','2','0');
INSERT INTO [t13041] VALUES (26,'أبان بن صمعة بصري حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال حدثنا علي بن المديني قال: سمعت يحيى يقول: كان أبان بن صمعة قد تغير بآخرة. قال علي: وسمعت عبد الرحمن، يقول: أتيت أبان بن صمعة وقد اختلط البتة , قلت لعبد الرحمن: قبل أن يموت بكم؟ قال','103','2','0');
INSERT INTO [t13041] VALUES (27,'أبان بن المحبر شامي عن نافع وغيره، منكر الحديث','104','2','0');
INSERT INTO [t13041] VALUES (28,'أبان بن أبي حازم البجلي كوفي حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: كان عبد الرحمن يحدث عن سفيان عن أبان بن أبي حازم وهو أبان بن عبد الله البجلي وما سمعت عبد الرحمن حدث عنه بشيء قط','106','2','0');
INSERT INTO [t13041] VALUES (29,'باب إبراهيم','107','2','0');
INSERT INTO [t13041] VALUES (30,'إبراهيم بن إسماعيل بن مجمع بن جارية الأنصاري المدني حدثنا آدم بن موسى قال: سمعت البخاري يقول: إبراهيم بن إسماعيل بن مجمع بن جارية الأنصاري يروى عنه، وهو كثير الوهم , يروي عن الزهري وعمرو بن دينار، يكتب حديثه. حدثنا محمد بن عيسى قال: حدثنا عياش قال','108','29','0');
INSERT INTO [t13041] VALUES (31,'إبراهيم بن إسماعيل بن أبي حبيبة مديني حدثنا آدم بن موسى قال: سمعت البخاري قال: إبراهيم بن إسماعيل بن أبي حبيبة المديني الأنصاري الأشهلي عن داود بن الحسين منكر الحديث. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين يقول: عبد','109','29','0');
INSERT INTO [t13041] VALUES (32,'إبراهيم بن إسماعيل بن يحيى بن سلمة بن كهيل كوفي حدثنا محمد بن عبد الله الحضرمي قال: كان ابن نمير لا يرضى إبراهيم بن إسماعيل ويضعفه , قال: روى مناكير','111','29','0');
INSERT INTO [t13041] VALUES (33,'إبراهيم بن الأسود الكناني حدثنا آدم بن موسى قال: سمعت البخاري قال: إبراهيم بن الأسود الكناني من أهل السراة ويقال إبراهيم بن عبد الله بن الأسود , عن ابن أبي نجيح , ويزيد بن أبي يزيد فيه نظر','113','29','0');
INSERT INTO [t13041] VALUES (34,'إبراهيم بن البراء بن النضر بن أنس بن مالك يحدث عن الثقات بالبواطيل','114','29','0');
INSERT INTO [t13041] VALUES (35,'إبراهيم بن بكر الشيباني كثير الوهم، بصري','116','29','0');
INSERT INTO [t13041] VALUES (36,'إبراهيم بن باب القصار بصري','119','29','0');
INSERT INTO [t13041] VALUES (37,'إبراهيم بن أبي بكر بن المنكدر عن محمد بن المنكدر، لا يتابع على حديثه من وجه يثبت','121','29','0');
INSERT INTO [t13041] VALUES (38,'إبراهيم بن بشار الرمادي بصري حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: كأن سفيان الذي يروي عنه إبراهيم بن بشار ليس هو سفيان بن عيينة. حدثنا عبد الله قال: سمعت أبي ذكر إبراهيم بن بشار الرمادي , فقال: كان يحضر معنا عند سفيان بن عيينة , فكان يملي على','123','29','0');
INSERT INTO [t13041] VALUES (39,'إبراهيم بن الحكم بن أبان العدني حدثنا محمد بن موسى النهرتيري قال: سمعت أحمد بن حنبل سئل عن إبراهيم بن الحكم بن أبان فقال: ما أدري , خلط. حدثنا عبد الله بن أحمد قال: سألت أبي عن إبراهيم بن الحكم بن أبان فقال: ليس بشيء , ليس بثقة. حدثنا عبد الله في','131','29','0');
INSERT INTO [t13041] VALUES (40,'إبراهيم بن سليمان أبو إسماعيل المؤدب حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين قال: أبو إسماعيل المؤدب ضعيف','132','29','0');
INSERT INTO [t13041] VALUES (41,'إبراهيم بن جريج الرهاوي','133','29','0');
INSERT INTO [t13041] VALUES (42,'إبراهيم بن حرب العسقلاني حدث بمناكير منها ما حدثناه خير بن عرفة الأنصاري','136','29','0');
INSERT INTO [t13041] VALUES (43,'إبراهيم بن خثيم بن عراك بن مالك الليثي المدني حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى يقول: ابن خثيم بن عراك بن مالك كانوا يصيحون به يا ذاك لا شيء , وكان لا يكتب عنه','138','29','0');
INSERT INTO [t13041] VALUES (44,'إبراهيم بن رستم خراساني، كثير الوهم','140','29','0');
INSERT INTO [t13041] VALUES (45,'إبراهيم بن زياد القرشي حدثني آدم بن موسى قال: سمعت البخاري قال: إبراهيم بن زياد القرشي لم يصح إسناده','143','29','0');
INSERT INTO [t13041] VALUES (46,'إبراهيم بن زكريا الواسطي مجهول وحديثه خطأ','145','29','0');
INSERT INTO [t13041] VALUES (47,'إبراهيم بن زكريا الضرير بصري، صاحب مناكير وأغاليط','148','29','0');
INSERT INTO [t13041] VALUES (48,'إبراهيم بن صالح بن درهم، بصري','151','29','0');
INSERT INTO [t13041] VALUES (49,'إبراهيم بن صرمة الأنصاري المدني','153','29','0');
INSERT INTO [t13041] VALUES (50,'إبراهيم بن طهمان الخراساني كان يغلو في الإرجاء. حدثنا محمد بن سعيد بن بلج الرازي بالري قال: سمعت عبد الرحمن بن الحكم بن بشير بن سلمان يذكر عن عبد العزيز بن أبي عثمان قال: كان رجل من المغاربة يجالس سفيان وكان سفيان يستخفه، ثم جفاه فشكا ذلك إلينا , قال','155','29','0');
INSERT INTO [t13041] VALUES (51,'إبراهيم بن عبد الرحمن الجبلي عن عاصم الأحول، وإبراهيم ليس بمعروف في النقل، والحديث غير محفوظ','156','29','0');
INSERT INTO [t13041] VALUES (52,'إبراهيم بن عبد الله بن سمرة الأسدي عن أبيه مجهولين جميعا، وحديثهما غير محفوظ','158','29','0');
INSERT INTO [t13041] VALUES (53,'إبراهيم بن عبد الرحمن السكسكي حدثنا عبد الله بن أحمد قال: حدثني أبو بكر بن خلاد قال: سمعت يحيى بن سعيد يقول: كان شعبة يقول في إبراهيم السكسكي يعني يطعن فيه. حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سألت يحيى عن إبراهيم السكسكي فقال:','160','29','0');
INSERT INTO [t13041] VALUES (54,'إبراهيم بن عبد الملك أبو إسماعيل القناد عن قتادة يهم في الحديث','161','29','0');
INSERT INTO [t13041] VALUES (55,'إبراهيم بن العلاء أبو هارون الغنوي حدثني محمد بن منصور القهستاني قال: حدثنا عبد الله بن الحارث قال: حدثنا شعيب بن حرب قال: سمعت شعبة يقول: لأن أقدم فيضرب عنقي أحب إلي من أن أقول: حدثنا أبو هارون الغنوي','164','29','0');
INSERT INTO [t13041] VALUES (56,'إبراهيم بن عمر بن أبان حدثنا آدم بن موسى الحواري قال: حدثنا محمد بن إسماعيل البخاري قال: إبراهيم بن عمر بن أبان بن عثمان بن عفان سمع أباه، روى عنه يوسف البراء، في حديثه بعض المناكير','165','29','0');
INSERT INTO [t13041] VALUES (57,'إبراهيم بن عثمان أبو شيبة الكوفي قاضي واسط هو جد بني أبي شيبة. حدثنا عبد الله بن أحمد بن حنبل قال: حدثني أبي قال: حدثنا أمية بن خالد قال: قلت لشعبة: إن أبا شيبة حدثنا عن الحكم عن عبد الرحمن بن أبي ليلى أنه قال: شهد صفين من أهل بدر سبعون رجلا , فقال','166','29','0');
INSERT INTO [t13041] VALUES (58,'إبراهيم بن سعد بن إبراهيم الزهري حدثنا عبد الله بن أحمد، حدثني أبي قال: ذكر عند يحيى بن سعيد عقيل وإبراهيم بن سعد، فجعل كأنه يضعفهما، يقول: عقيل وإبراهيم بن سعد، قال: إن هؤلاء ثقات لم يخبرهما يحيى. حدثنا عبد الله، حدثني أبي، حدثنا وكيع مرة عن','167','29','0');
INSERT INTO [t13041] VALUES (59,'إبراهيم بن عطية الواسطي الثقفي حدثني آدم بن موسى قال: سمعت البخاري يقول: إبراهيم بن عطية الواسطي الثقفي أبو إسماعيل , عن يونس بن خباب ومغيرة عنده مناكير، وكان هشيم يدلس به. حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: سمعت يزيد بن هارون يقول','168','29','0');
INSERT INTO [t13041] VALUES (60,'إبراهيم بن الفضل المخزومي مدني حدثنا آدم بن موسى قال: سمعت البخاري يقول: إبراهيم بن الفضل أبو إسحاق المديني المخزومي منكر الحديث يروي عن المقبري. حدثنا محمد بن عيسى , قال: حدثنا العباس بن محمد قال: سمعت يحيى قال: إبراهيم بن الفضل ليس بشيء. حدثنا','169','29','0');
INSERT INTO [t13041] VALUES (61,'إبراهيم بن محمد بن الحارث التيمي المدني وحدثني آدم بن موسى قال: سمعت محمد بن إسماعيل البخاري قال: إبراهيم بن محمد بن الحارث التيمي لم يثبت حديثه روى عنه موسى بن عبيدة ضعف لذلك','172','29','0');
INSERT INTO [t13041] VALUES (62,'إبراهيم بن محمد بن عبد العزيز بن عمر بن عبد الرحمن بن عوف الزهري حدثني آدم بن موسى قال: سمعت البخاري قال: إبراهيم بن محمد بن عبد العزيز بن عمر بن عبد الرحمن بن عوف الزهري أبو إسحاق سمع إبراهيم بن المنذر ويعقوب بن محمد فيه نظر، قال: وأراه ابن أبي ثابت','174','29','0');
INSERT INTO [t13041] VALUES (63,'إبراهيم بن محمد بن أبي يحيى الأسلمي المديني حدثنا محمد بن أحمد بن النضر الأزدي قال: حدثنا أبو بكر بن عفان السرخسي: قال خرج ابن عيينة علينا من منزله وكان منزله بقعيقعان فقال: ألا فاحذروا ابن أبي داود المرجئ لا تجالسوه، واحذروا إبراهيم بن أبي يحيى لا','175','29','0');
INSERT INTO [t13041] VALUES (64,'إبراهيم بن محمد الثقفي مديني حدثني آدم بن موسى، قال سمعت البخاري قال: إبراهيم بن محمد الثقفي , عن هشام بن أبي هشام، ولم يصح حديثه','176','29','0');
INSERT INTO [t13041] VALUES (65,'إبراهيم بن محمد العباسي حديثه غير محفوظ، ولا أصل له','178','29','0');
INSERT INTO [t13041] VALUES (66,'إبراهيم بن محمد، شامي، مجهول، وقع إلى أصبهان، حديثه منكر غير محفوظ','180','29','0');
INSERT INTO [t13041] VALUES (67,'إبراهيم بن محمد بن عاصم مجهول في النقل، حديثه غير محفوظ','182','29','0');
INSERT INTO [t13041] VALUES (68,'إبراهيم بن مسلم الهجري حدثني آدم بن موسى قال: حدثنا محمد بن إسماعيل قال: حدثنا عبد الله بن محمد قال: كان ابن عيينة يضعف إبراهيم بن مسلم الهجري. حدثنا محمد بن زكريا قال: حدثنا محمد بن المثنى قال: ما سمعت يحيى يحدث عن سفيان عن إبراهيم الهجري وكان عبد','184','29','0');
INSERT INTO [t13041] VALUES (69,'إبراهيم بن المهاجر بن مسمار المدني حدثني آدم بن موسى قال: سمعت البخاري يقول: إبراهيم بن المهاجر بن مسمار المدني منكر الحديث','185','29','0');
INSERT INTO [t13041] VALUES (70,'إبراهيم بن المهاجر الكوفي حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: قلت ليحيى: إن إسرائيل روى عن إبراهيم بن المهاجر ثلاثمائة , قال: إبراهيم بن المهاجر لم يكن بالقوي. حدثنا عبد الله بن أحمد بن حنبل قال: سألت يحيى عن إبراهيم بن','187','29','0');
INSERT INTO [t13041] VALUES (71,'إبراهيم بن المختار الرازي حدثنا أحمد بن علي الأبار قال: سألت زنيجا عن إبراهيم بن المختار فقال: تركته، ولم يرضه. حدثنا آدم بن موسى قال: سمعت البخاري قال: إبراهيم بن المختار أبو إسماعيل التميمي من أهل خوار، سمع محمد بن إسحاق، لا أدري كيف حديثه؟','188','29','0');
INSERT INTO [t13041] VALUES (72,'إبراهيم بن مهدي المصيص حدث بمناكير','189','29','0');
INSERT INTO [t13041] VALUES (73,'إبراهيم بن معاوية الزيادي بصري، لا يتابع على حديثه','191','29','0');
INSERT INTO [t13041] VALUES (74,'إبراهيم بن هدبة أبو هدبة، واسطي حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى يقول: قدم أبو هدبة فاجتمع عليه الخلق فقالوا له: أخرج رجليك , فقالوا ليحيى: لم قالوا له: أخرج رجليك؟ قال: كانوا يخافون أن يكون رجله رجل حمار، يكون شيطانا.','193','29','0');
INSERT INTO [t13041] VALUES (75,'إبراهيم بن هراسة أبو إسحاق الشيباني حدثنا محمد بن عبد الحميد قال: حدثنا أحمد بن محمد الحضرمي قال: سألت يحيى بن معين عن إبراهيم بن هراسة فقال: كذاب. حدثني آدم بن موسى قال: سمعت البخاري قال: إبراهيم بن هراسة أبو إسحاق الكوفي متروك الحديث , تكلم فيه','195','29','0');
INSERT INTO [t13041] VALUES (76,'إبراهيم بن يزيد الخوزي، مكي حدثنا عبد الله بن أحمد بن سعدويه المروزي قال: حدثنا أحمد بن عبد الله بن بشير المروزي قال: حدثنا سفيان بن عبد الملك قال: سألت ابن المبارك عن حديث لإبراهيم بن يزيد الخوزي فأبى أن يحدثني به. حدثنا محمد بن عيسى قال: حدثنا','196','29','0');
INSERT INTO [t13041] VALUES (77,'إبراهيم بن أبي حية المكي وهو إبراهيم بن اليسع بن أسعد. حدثني آدم بن موسى قال: سمعت البخاري قال: إبراهيم بن أبي حية المكي هو إبراهيم بن اليسع بن أسعد أبو إسماعيل المكي عن هشام بن عروة منكر الحديث','199','29','0');
INSERT INTO [t13041] VALUES (78,'إبراهيم بن يوسف بن أبي إسحاق السبيعي كوفي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: إبراهيم بن يوسف بن أبي إسحاق ليس بشيء','202','29','0');
INSERT INTO [t13041] VALUES (79,'إبراهيم بن يزيد بن قديد عن الأوزاعي، في حديثه وهم وغلط','203','29','0');
INSERT INTO [t13041] VALUES (80,'باب إسماعيل','207','2','0');
INSERT INTO [t13041] VALUES (81,'إسماعيل بن إبراهيم أبو يحيى التيمي الكوفي حدثنا عبد الله بن الحسن عن علي بن المديني قال: أبو يحيى التيمي ضعيف. وحدثني آدم بن موسى قال: سمعت البخاري , قال: إسماعيل بن إبراهيم أبو يحيى التيمي كوفي حدث عن مخارق ومطرف , قال ابن نمير: هو ضعيف جدا','210','80','0');
INSERT INTO [t13041] VALUES (82,'إسماعيل بن إبراهيم الكرابيسي عن أبي عون ليس لحديثه أصل مسند إنما هو موقوف من حديث ابن عوف','211','80','0');
INSERT INTO [t13041] VALUES (83,'إسماعيل بن إبراهيم القرشي يقال: حمصي، في حديثه وهم','213','80','0');
INSERT INTO [t13041] VALUES (84,'إسماعيل بن أبي إسحاق أبو إسرائيل الملائي في حديثه وهم واضطراب، وله مع ذاك مذهب سوء','215','80','0');
INSERT INTO [t13041] VALUES (85,'إسماعيل بن إسحاق الأنصاري كوفي كان بمصر منكر الحديث، ومن حديثه','219','80','0');
INSERT INTO [t13041] VALUES (86,'إسماعيل بن أبان الغنوي كوفي حدثني آدم بن موسى قال: سمعت محمد بن إسماعيل قال: إسماعيل بن أبان , عن هشام بن عروة متروك الحديث , تركه أحمد وكنيته أبو إسحاق كوفي. حدثنا عبد الله بن أحمد قال: سألت أبي عن إسماعيل بن أبان الغنوي؟ فقال: كتبنا عنه , عن هشام','221','80','0');
INSERT INTO [t13041] VALUES (87,'إسماعيل بن رافع المديني مولى مزينة حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: لم أسمع يحيى , ولا عبد الرحمن حدثا عن إسماعيل بن رافع بشيء قط , قال: وقد رأيته. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين يقول: إسماعيل بن','222','80','0');
INSERT INTO [t13041] VALUES (88,'إسماعيل بن زكريا الخلقاني أبو زياد حدثنا محمد بن عبد الرحمن قال: حدثنا عبد الملك بن محمد الميموني قال: سمعت يحيى بن معين يقول: إسماعيل بن زكريا ضعيف. قال الميموني: فقلت لأبي عبد الله أحمد بن محمد بن حنبل: إسماعيل بن زكريا كيف هو؟ فقال لي: أما','223','80','0');
INSERT INTO [t13041] VALUES (89,'إسماعيل بن سميع الحنفي كوفي حدثنا أحمد بن علي الأبار قال: حدثنا محمد بن حميد قال: حدثنا جرير قال: كتبت حديث إسماعيل بن سميع , فقيل لي: إنه يرى رأي الخوارج، فتركته. حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثني علي قال: قلت ليحيى: زعم','225','80','0');
INSERT INTO [t13041] VALUES (90,'إسماعيل بن ثابت بن مجمع','226','80','0');
INSERT INTO [t13041] VALUES (91,'إسماعيل بن إياس بن عفيف الكندي حدثني آدم بن موسى قال: سمعت البخاري قال: إسماعيل بن إياس بن عفيف الكندي روى عنه يحيى بن أبي الأشعث ولم يصح حديثه , ولم يثبت','228','80','0');
INSERT INTO [t13041] VALUES (92,'إسماعيل بن حماد بن أبي سليمان حديثه غير محفوظ ويحكيه عن مجهول، كوفي','230','80','0');
INSERT INTO [t13041] VALUES (93,'إسماعيل بن بشير بن سلمان الكوفي يهم في غير حديث , وكاد أن يغلب عليه الوهم','232','80','0');
INSERT INTO [t13041] VALUES (94,'إسماعيل بن جستاس حدثنا آدم بن موسى قال: سمعت البخاري يقول: إسماعيل بن جستاس: في كلب الصيد أربعين درهما , قال البخاري وهذا حديث لا يتابع عليه','235','80','0');
INSERT INTO [t13041] VALUES (95,'إسماعيل بن سليمان الرازي أخو إسحاق، الغالب على حديثه الوهم','237','80','0');
INSERT INTO [t13041] VALUES (96,'إسماعيل بن سلمان الأزرق كوفي حدثنا محمد بن عيسى قال حدثنا عباس بن محمد قال: سمعت يحيى بن معين يقول: إسماعيل الأزرق ليس بشيء , وهو إسماعيل بن سلمان','240','80','0');
INSERT INTO [t13041] VALUES (97,'إسماعيل بن شبيب الطائفي عن ابن جريج، أحاديثه مناكير، ليس منها شيء محفوظ','242','80','0');
INSERT INTO [t13041] VALUES (98,'إسماعيل بن شروس الصنعاني حدثني آدم بن موسى قال: سمعت البخاري قال: إسماعيل بن شروس أبو المقدام صنعاني , قال البخاري , قال عبد الرازق , عن معمر: كان يثبج الحديث','248','80','0');
INSERT INTO [t13041] VALUES (99,'إسماعيل بن عبد الرحمن الأودي لا يتابع على حديثه ولا يعرف إلا به','250','80','0');
INSERT INTO [t13041] VALUES (100,'إسماعيل بن عباد بصري، حديثه غير محفوظ','253','80','0');
INSERT INTO [t13041] VALUES (101,'إسماعيل بن عبد الملك بن أبي الصفير ابن أخي عبد العزيز بن رفيع حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: سمعت يحيى بن سعيد القطان يقول: تركت إسماعيل بن عبد الملك ثم كتبت عن سفيان عنه. وحدثنا محمد بن زكريا قال: حدثنا محمد بن المثنى قال: ما سمعت','255','80','0');
INSERT INTO [t13041] VALUES (102,'إسماعيل بن عبيد الله بن سلمان المكي','256','80','0');
INSERT INTO [t13041] VALUES (103,'إسماعيل بن عمرو البجلي، كوفي كان بأصبهان، في حديثه مناكير , ويحيل على من لا يحتمل','258','80','0');
INSERT INTO [t13041] VALUES (104,'إسماعيل بن عبد الله بن أبي أويس المديني حدثني محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين يقول: أبو أويس , وابنه ضعيفان. وحدثني أسامة الدقاق بصري , يقول: سمعت يحيى بن معين يقول: إسماعيل بن أبي أويس يسوى فلسين','260','80','0');
INSERT INTO [t13041] VALUES (105,'إسماعيل بن عبد الرحمن السدي محمد بن عيسى , قال: حدثنا عمر بن شبة قال: حدثنا أبو بكر بن خلاد قال: سمعت المعتمر بن سليمان يقول: إن بالكوفة كذابين: الكلبي , والسدي. حدثنا عبد الله بن أحمد قال: حدثني أبي قال: حدثنا أبو أحمد الزبيري قال: حدثنا عبد','261','80','0');
INSERT INTO [t13041] VALUES (106,'إسماعيل بن عياش الحمصي أبو عتبة إذا حدث عن غير أهل الشام اضطرب , وأخطأ','262','80','0');
INSERT INTO [t13041] VALUES (107,'إسماعيل بن قيس بن سعد بن زيد بن ثابت حدثنا آدم بن موسى قال: سمعت البخاري قال: حدثني عبد الرحمن بن شيبة المديني قال: حدثنا إسماعيل بن قيس بن سعد بن زيد بن ثابت أبو مصعب المديني قال البخاري: منكر الحديث , كان قد أتى عليه إحدى وتسعين سنة , وكان عنده','267','80','0');
INSERT INTO [t13041] VALUES (108,'إسماعيل بن مسلم، مكي حدثنا أحمد بن أصرم المزني قال: قلت لأحمد بن محمد بن حنبل: حدثنا عن علي بن مسهر عن إسماعيل بن مسلم فلما قلت له إسماعيل بن مسلم قال بيده هكذا كأنه ضعفه. حدثني آدم بن موسى قال: سمعت البخاري قال: إسماعيل بن مسلم عن الحسن والزهري','269','80','0');
INSERT INTO [t13041] VALUES (109,'إسماعيل بن مسلم اليشكري عن ابن عون لا يعرف بنقل الحديث وحديثه منكر غير محفوظ، بصري','273','80','0');
INSERT INTO [t13041] VALUES (110,'إسماعيل بن مخراق كذا قال البخاري وهو إسماعيل بن داود بن مخراق مدني. حدثني آدم بن موسى قال: سمعت البخاري قال: إسماعيل بن مخراق منكر الحديث مدني','275','80','0');
INSERT INTO [t13041] VALUES (111,'إسماعيل بن مجالد بن سعيد لا يتابع على حديثه','277','80','0');
INSERT INTO [t13041] VALUES (112,'إسماعيل بن مختار، كوفي حدثني آدم بن موسى قال: سمعت البخاري يقول: إسماعيل بن مختار عن عطية سمع منه هناد بن السرى , لم يصح حديثه في الكوفيين','279','80','0');
INSERT INTO [t13041] VALUES (113,'إسماعيل بن يعلى الثقفي أبو أمية بصري حدثنا آدم بن موسى قال: سمعت البخاري قال: إسماعيل بن يعلى أبو أمية البصري الثقفي سكتوا عنه. حدثنا محمد بن زكريا قال: حدثنا أبو موسى محمد بن المثنى , قال: ما سمعت عبد الرحمن يعني ابن مهدي يحدث عن أبي شيئا قط.','280','80','0');
INSERT INTO [t13041] VALUES (114,'إسماعيل بن يحيى الشيباني , يقال الشعيري','282','80','0');
INSERT INTO [t13041] VALUES (115,'باب إسحاق','284','2','0');
INSERT INTO [t13041] VALUES (116,'إسحاق بن إبراهيم المسعودي حدثني آدم بن موسى قال: سمعت البخاري قال: إسحاق بن إبراهيم المسعودي رفع حديثا لا يتابع على رفعه','285','115','0');
INSERT INTO [t13041] VALUES (117,'إسحاق بن إبراهيم الحنيني حدثني آدم بن موسى قال: سمعت البخاري قال: إسحاق بن إبراهيم الحنيني عن مالك وهشام بن سعد أبو يعقوب في حديثه نظر سكن طرسوس','287','115','0');
INSERT INTO [t13041] VALUES (118,'إسحاق بن إبراهيم بن نسطاس مديني حدثني آدم بن موسى قال: سمعت البخاري قال: إسحاق بن إبراهيم بن نسطاس أبو يعقوب مولى كثير بن الصلت عن سعيد بن إسحاق وإسماعيل بن مصعب وهشام بن الوليد وغيرهم رواه عنه ابن أبي أويس ومرحوم فيه نظر وقال في موضع آخر: منكر الحديث','290','115','0');
INSERT INTO [t13041] VALUES (119,'إسحاق بن بشر الكاهلي كان ببغداد منكر الحديث','292','115','0');
INSERT INTO [t13041] VALUES (120,'إسحاق بن بشر القرشي مجهول حدث بمناكير','294','115','0');
INSERT INTO [t13041] VALUES (121,'إسحاق بن إدريس الأسواري بصري كان يذهب إلى القدر حدثنا ابن الأعرابي قال: سمعت عباس بن محمد قال: سمعت يحيى يقول: إسحاق بن إدريس الأسواري كذاب. حدثنا آدم بن موسى قال: سمعت البخاري يقول: إسحاق بن إدريس الأسواري البصري تركه الناس. وحدثنا آدم بن موسى','296','115','0');
INSERT INTO [t13041] VALUES (122,'إسحاق بن الحارث الكوفي حدثني آدم قال: سمعت البخاري قال: إسحاق بن الحارث الكوفي عن كردم روى عنه ابنه عبد الرحمن بن إسحاق، يتكلمون فيه، وفيه نظر، قال: وضعف أحمد: عبد الرحمن بن إسحاق','297','115','0');
INSERT INTO [t13041] VALUES (123,'إسحاق بن عبد الله بن أبي فروة مدني حدثنا محمد بن إسماعيل قال: حدثنا عبدة بن عبد الرحيم المروزي قال: حدثنا بقية قال: حدثنا عتبة بن أبي حكيم قال: جلس إسحاق بن عبد الله بن أبي فروة في مسجد المدينة يحدث، والزهري إلى جانبه فجعل يقول: قال رسول الله صلى','299','115','0');
INSERT INTO [t13041] VALUES (124,'إسحاق بن الصباح','302','115','0');
INSERT INTO [t13041] VALUES (125,'إسحاق بن يحيى بن طلحة بن عبيد الله التيمي القرشي حدثني محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سألت يحيى بن سعيد عن إسحاق بن يحيى بن طلحة فقال: ذاك شبه لا شيء. حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن','304','115','0');
INSERT INTO [t13041] VALUES (126,'إسحاق أبو الغصن حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: حدثنا يحيى بن سعيد بحديث إسحاق أبي الغصن، ثم تركه بعد سمعته يقول: حدثنا إسحاق أبو الغصن قال: بعت من رجل بغلا فخرج على رجله جرب فخاصمني إلى شريح فقال للمشتري: بينتك أنه باعك وهذا به،','306','115','0');
INSERT INTO [t13041] VALUES (127,'إسحاق بن نجيح الملطي حدثنا محمد بن عثمان العبسي قال: سمعت يحيى بن معين يقول: كان ببغداد قوم يضعون الحديث كنت أرى منهم إسحاق بن نجيح الملطي. حدثنا عبد الله بن أحمد، قال: سمعت أبي يقول: إسحاق بن نجيح الملطي هو من أكذب الناس يحدث عن البتي وعن ابن','307','115','0');
INSERT INTO [t13041] VALUES (128,'إسحاق بن ناصح الجوهري بصري','309','115','0');
INSERT INTO [t13041] VALUES (129,'إسحاق بن محمد الفروي جاء عن مالك بأحاديث كثيرة لا يتابع عليها. وسمعت أبا جعفر الصائغ يقول: كان إسحاق الفروي كف وكان يلقن، منها ما حدثناه علي بن عبد العزيز','311','115','0');
INSERT INTO [t13041] VALUES (130,'أسماء بن الحكم الفزاري','314','2','0');
INSERT INTO [t13041] VALUES (131,'باب أيوب','316','2','0');
INSERT INTO [t13041] VALUES (132,'أيوب بن عائذ الطائي حدثني آدم بن موسى قال: سمعت البخاري قال: أيوب بن عائذ الطائي كان يرى الإرجاء، وهو صدوق','317','131','0');
INSERT INTO [t13041] VALUES (133,'أيوب بن عتبة قاضي اليمامة حدثنا عبد الله بن أحمد قال: سمعت يحيى بن معين يقول: كان يقال: ثلاثة كان يتقى حديثهم: محمد بن طلحة بن مصرف، وأيوب بن عتبة وفليح قلت له: ممن سمعت هذا؟ قال: من أبي كامل المظفر بن مدرك. حدثنا محمد بن عيسى قال: حدثنا عباس','318','131','0');
INSERT INTO [t13041] VALUES (134,'أيوب بن خوط أبو أمية الحبطي حدثنا محمد بن عثمان بن أبي شيبة قال: سمعت يحيى بن معين وقال له أبو بديل التميمي: يا أبا زكريا إن أحمد بن يونس يحدث عن أيوب بن خوط فقال يحيى: كان أيوب ضعيفا لا يكتب حديثه. حدثنا عبد الله بن محمد بن سعدويه حدثنا أحمد بن عبد','323','131','0');
INSERT INTO [t13041] VALUES (135,'أيوب بن سيار الزهري أبو سنان حدثنا محمد بن عثمان العبسي قال: قلت ليحيى بن معين: إن عند منجاب كتابا عن أيوب بن سيار قال وما يصنع بأيوب بن سيار، كان أيوب كذابا. حدثنا يحيى بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين يقول: أيوب بن سيار ليس','328','131','0');
INSERT INTO [t13041] VALUES (136,'أيوب بن سويد أبو مسعود الرملي حدثنا عبد الله بن محمد المروزي قال: حدثنا أحمد بن عبد الله بن بشير المروزي قال: حدثنا سفيان بن عبد الملك قال: سمعت ابن المبارك يقول: أيوب بن سويد ارم به. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين يقول','331','131','0');
INSERT INTO [t13041] VALUES (137,'أيوب بن جابر اليمامي أخو محمد بن جابر حدثنا محمد بن عثمان بن أبي شيبة قال: سألت يحيى بن معين عن أيوب بن جابر فقال: ذهبت إلى أيوب بن جابر وقد كتبت عنه وكان أيوب بن جابر، ومحمد بن جابر ليسا بشيء','332','131','0');
INSERT INTO [t13041] VALUES (138,'أيوب بن ذكوان عن الحسن شامي حدثني آدم قال: سمعت البخاري يقول: أيوب بن ذكوان عن الحسن منكر الحديث روى عنه أخوه نوح بن ذكوان','334','131','0');
INSERT INTO [t13041] VALUES (139,'أيوب بن مدرك الحنفي حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين يقول: أيوب بن مدرك الحنفي ليس بشيء وفى موضع آخر كذاب','336','131','0');
INSERT INTO [t13041] VALUES (140,'أيوب أبو العلاء وهو أيوب بن أبي مسكين واسطي حدثنا عبد الله بن أحمد: قال سألت أبي عن أيوب أبي العلاء فقال: لا بأس به، وكان يزيد بن هارون لا يستخفه أظنه كان لا يحفظ الإسناد','338','131','0');
INSERT INTO [t13041] VALUES (141,'أيوب بن واقد أبي الحسن الكوفي حدثنا عبد الله بن أحمد قال: سألت أبي عن أيوب بن واقد قال: ضعيف الحديث. حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى يقول: أيوب بن واقد بصرى ليس بثقة كان يحدث عن مغيرة عن إبراهيم أنه كان يكره بيع القرد.','339','131','0');
INSERT INTO [t13041] VALUES (142,'أيوب بن محمد أبو الجميل الجمل اليمامي يهم في بعض حديثه','341','131','0');
INSERT INTO [t13041] VALUES (143,'أيوب بن منصور الكوفي في حديثه وهم','345','131','0');
INSERT INTO [t13041] VALUES (144,'أيوب بن وائل حدثني آدم بن موسى قال: سمعت البخاري يقول: أيوب بن وائل عن نافع عن ابن عمر في الدعاء لا يتابع عليه','347','131','0');
INSERT INTO [t13041] VALUES (145,'أغلب بن تميم الكندي، ويقال المسعودي حدثني آدم قال: سمعت البخاري، قال: أغلب بن تميم الكندي أبو حفص سمع منه زيد بن حباب يحدث عن معاذ بن عبد الله عن أنس منكر الحديث','349','2','0');
INSERT INTO [t13041] VALUES (146,'أصرم بن غياث النيسابوري حدثني آدم بن موسى قال: سمعت البخاري قال: أصرم بن غياث النيسابوري أبو غياث منكر الحديث','351','2','0');
INSERT INTO [t13041] VALUES (147,'أصرم بن حوشب الهمداني حدثني آدم قال: سمعت البخاري قال: أصرم بن حوشب متروك الحديث','353','2','0');
INSERT INTO [t13041] VALUES (148,'أزور بن غالب حدثني آدم بن موسى قال: سمعت البخاري قال: أزور بن غالب منكر الحديث','355','2','0');
INSERT INTO [t13041] VALUES (149,'أسباط بن محمد القرشي ربما يهم في شيء حدثنا عبد الله بن أحمد قال: حدثني الحسن بن عيسى قال: سألت ابن المبارك عن أسباط ومحمد بن فضيل بن غزوان فسكت، فلما كان بعد أيام رآني، فقال: يا حسن صاحبيك لا أرى أصحابنا يرضونهما','357','2','0');
INSERT INTO [t13041] VALUES (150,'أحوص بن حكيم حدثنا محمد بن عثمان بن أبي شيبة قال: سمعت يحيى بن معين سئل عن الأحوص بن حكيم فقال: ليس بشيء. حدثني آدم بن موسى قال: سمعت البخاري قال: قال لي علي: لم يرو يحيى عن الأحوص. حدثنا عبد الله، قال: حدثنا أبي قال: قال أبو بكر بن عياش: حدث','360','2','0');
INSERT INTO [t13041] VALUES (151,'أخنس والد بكير بن الأخنس كوفي حدثني آدم بن موسى قال: سمعت البخاري قال أخنس والد بكير بن الأخنس روى عنه ابنه سمع ابن مسعود ولم يصح حديثه','362','2','0');
INSERT INTO [t13041] VALUES (152,'أجلج بن عبد الله الكندي كوفي حدثنا محمد بن عيسى قال حدثنا عمرو بن علي قال: سمعت يحيى، يقول: ما كان الأجلج يفصل بين علي بن الحسين، والحسين بن علي. سمعته يقول: حدثنا حبيب بن أبي ثابت قال: كنا عنده حسين بن علي فقال: لا طلاق إلا بعد نكاح. حدثنا أحمد','364','2','0');
INSERT INTO [t13041] VALUES (153,'أوس بن عبد الله الربعي أبو الجوزاء حدثني آدم بن موسى قال: سمعت البخاري، يقول: أوس بن عبد الله الربعي أبو الجوزاء بصري سمع عبد الله بن عمرو، روى عنه عمران بن مسلم، في إسناده نظر','366','2','0');
INSERT INTO [t13041] VALUES (154,'أوس بن عبد الله بن بريدة بن خصيب الأسلمي حدثني آدم بن موسى قال: سمعت البخاري قال: أوس بن عبد الله بن بريدة بن خصيب الأسلمي سكن مرو، وقال البخاري: فيه نظر','368','2','0');
INSERT INTO [t13041] VALUES (155,'أيفع عن ابن عمر رضي الله عنه حدثني آدم بن موسى قال: سمعت البخاري يقول: أيفع عن ابن عمر منكر الحديث','371','2','0');
INSERT INTO [t13041] VALUES (156,'أفلح بن سعيد القبائي مدني حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن يعني ابن مهدي يحدث عن أفلح بن سعيد - شيخ من أهل قباء - شيئا قط','373','2','0');
INSERT INTO [t13041] VALUES (157,'أحمد بن الحارث الغساني حدثني آدم بن موسى قال: سمعت البخاري قال: أحمد بن الحارث الغساني، ويعرف بالغنوي أبو عبد الله بصري، سمع ساكنة بنت الجعد فيه نظر','374','2','0');
INSERT INTO [t13041] VALUES (158,'أحمد بن داود ابن أخت عبد الرزاق حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين يقول: أحمد ابن أخت عبد الرزاق كذاب لم يكن بثقة ولا مأمون. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول أحمد ابن أخت عبد الرازق من أكذب الناس','376','2','0');
INSERT INTO [t13041] VALUES (159,'أحمد بن عمران الأخنسي حدثني آدم بن موسى قال: سمعت البخاري يقول: أحمد بن عمران الأخنسي وكان ببغداد يتكلمون فيه منكر الحديث','378','2','0');
INSERT INTO [t13041] VALUES (160,'أحمد بن محمد بن أبي بزة المقرئ منكر الحديث ويوصل الأحاديث','380','2','0');
INSERT INTO [t13041] VALUES (161,'أحمد بن بشير الكوفي مولى عمرو بن حريث حدثني أحمد بن محمود الهروي حدثنا عثمان بن سعيد قال: قلت ليحيى بن معين: عطاء بن المبارك تعرفه؟ قال: من روى عنه؟ قلت: ذاك الشيخ الضعيف أحمد بن بشير، قال: مه، كأنه يتعجب من ذكري أحمد بن بشير، قال: لا أعرفه.','383','2','0');
INSERT INTO [t13041] VALUES (162,'أرقم بن أبي أرقم عن ابن عباس حدثني آدم بن موسى قال: سمعت البخاري، يقول: أرقم بن أبي الأرقم عن ابن عباس، شيخ مجهول لا يعرف إلا بهذا','384','2','0');
INSERT INTO [t13041] VALUES (163,'أمية بن خالد القيسي بصري حدثني الخضر بن داود قال: حدثنا أحمد بن محمد بن هانئ قال: سمعت أبا عبد الله سئل عن أمية بن خالد فلم أره يحمده في الحديث، وقال: إنما كان يحدث من حفظه لا يخرج كتابا','386','2','0');
INSERT INTO [t13041] VALUES (164,'أصبغ مولى عمرو بن حريث كوفي حدثنا عبد الله بن محمد بن سعدويه المروزي قال: حدثنا أحمد بن عبد الله بن بشير المروزي قال: حدثنا سفيان بن عبد الملك قال: حدثنا ابن المبارك قال: حدثنا إسماعيل بن أبي خالد عن أصبغ مولى عمرو بن حريث وأصبغ حي في وثاق قد تغير','388','2','0');
INSERT INTO [t13041] VALUES (165,'أصبغ بن نباتة الحنظلي كوفي كان يقول بالرجعة. حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: ما سمعت يحيى ولا عبد الرحمن حدثا عن الأصبغ بن نباتة شيئا قط. حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: حدثنا عبد الرحمن بن صالح الأزدي قال: حدثنا','391','2','0');
INSERT INTO [t13041] VALUES (166,'أصبغ بن سفيان الكلبي','393','2','0');
INSERT INTO [t13041] VALUES (167,'أصبغ أبو بكر الشيباني مجهول، وحديثه غير محفوظ','395','2','0');
INSERT INTO [t13041] VALUES (168,'إسرائيل بن يونس بن أبي إسحاق السبيعي مختلف فيه حدثنا عبد الله بن أحمد قال: حدثني أبي قال: حدثنا مؤمل قال: حدثنا إسرائيل قال: حدثنا عبد الأعلى عن أبي عبد الرحمن عن علي رفعه وتجعلون رزقكم قال مؤمل: قلت لسفيان: إسرائيل رفعه، قال: صبيان صبيان. حدثنا','397','2','0');
INSERT INTO [t13041] VALUES (169,'أزهر بن سعد السمان بصري حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: سألت عليا عن حديث عبيدة عن علي عن النبي عليه السلام في التسبيح قلت: من يقول عن عبيدة؟ فقال: حدثنا أزهر عن ابن عون، عن محمد بن عبيدة عن علي قال علي: ورأيته في أصله مرسلا عن','398','2','0');
INSERT INTO [t13041] VALUES (170,'أزهر بن سنان أبو خالد القرشي عن محمد بن واسع في حديثه وهم','401','2','0');
INSERT INTO [t13041] VALUES (171,'أزهر بن عبد الله خراساني عن محمد بن عجلان حديثه غير محفوظ من حديث ابن عجلان','406','2','0');
INSERT INTO [t13041] VALUES (172,'أويس القرني الزاهد حدثني آدم بن موسى قال: سمعت البخاري قال: أويس القرني في إسناده نظر. أخبرنا محمد بن عيسى قال: حدثنا بندار قال: حدثنا أبو داود قال: حدثنا شعبة قال: جئت إلى عمرو بن مرة قلت: أخبرني عن أويس القرني تعرفونه فيكم قال: لا. حدثنا محمد','408','2','0');
INSERT INTO [t13041] VALUES (173,'باب الباء','412','0','0');
INSERT INTO [t13041] VALUES (174,'بشر بن حرب أبو عمرو الندبي بصري حدثني آدم بن موسى قال: سمعت محمد بن إسماعيل البخاري قال: بشر بن حرب أبو عمرو الندبي رأيت علي بن المديني يضعفه يروي عن ابن عمر يتكلمون فيه، وقال لي علي: كان يحيى لا يروي عنه وهو بصري. حدثنا محمد بن عيسى قال: حدثنا','413','173','0');
INSERT INTO [t13041] VALUES (175,'بشر بن نمير القشيري البصري حدثني آدم بن موسى قال سمعت البخاري قال: بشر بن نمير القشيري البصري عن القاسم بن عبد الرحمن روى عنه حماد بن زيد ويزيد بن زريع نسبه يزيد بن هارون، منكر الحديث، وقال في الكتاب الكبير: بشر بن نمير مضطرب، تركه علي فيما أفتى عنه','414','173','0');
INSERT INTO [t13041] VALUES (176,'بشر بن عمارة الخثعمي عن أبي روق حدثني آدم بن موسى قال: سمعت محمد بن إسماعيل البخاري قال: حدثنا محمد بن الصلت عن بشر بن عمارة عن أبي روق عن عطية عن أبي سعيد وعن غيره قال: وكنت تعرف، وتنكر','416','173','0');
INSERT INTO [t13041] VALUES (177,'بشر بن رافع الحارثي النجراني أبو الأسباط حدثنا عبد الله بن أحمد قال: سألت أبي عن بشر بن رافع فقال: هو النجراني ليس بشيء، ضعيف الحديث، حدث عنه عبد الرزاق وصفوان بن عيسى','418','173','0');
INSERT INTO [t13041] VALUES (178,'بشر بن الحسين الأصبهاني حدثني آدم بن موسى قال: سمعت البخاري قال: بشر بن الحسين الأصبهاني فيه نظر','422','173','0');
INSERT INTO [t13041] VALUES (179,'بشر بن المنذر قاضي المصيصة في حديثه وهم','425','173','0');
INSERT INTO [t13041] VALUES (180,'بشر بن إبراهيم الأنصاري عن الأوزاعي بأحاديث موضوعة لا يتابع عليها','427','173','0');
INSERT INTO [t13041] VALUES (181,'بشر بن السري هو في الحديث مستقيم حدثنا أحمد بن علي الأبار قال: حدثنا عوام بن إسماعيل قال: قال الحميدي: كان بشر بن السرى جهميا لا يحل أن يكتب عنه. حدثنا جعفر بن محمد الفريابي حدثنا أحمد بن محمد المقدمي حدثنا سليمان بن حرب قال: سأل بشر بن السرى حماد','430','173','0');
INSERT INTO [t13041] VALUES (182,'بشير بن المهاجر الغنوي كوفي حدثنا محمد بن عيسى قال: حدثنا حمدان بن علي قال: قلت لأحمد بن حنبل: بشير بن المهاجر يروي عن ابن بريدة قال: كوفي مرجيء متهم يتكلم. حدثني الخضر بن داود قال: حدثنا أحمد بن محمد قال: سمعت أبا عبد الله وذكر بشير بن المهاجر','431','173','0');
INSERT INTO [t13041] VALUES (183,'بشير بن زاذان حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى يقول: بشير بن زاذان ليس بشيء','433','173','0');
INSERT INTO [t13041] VALUES (184,'بشير بن ميمون أبو صيفي حدثنا عبد الله بن أحمد قال: سألت أبي عن أبي صيفي يحدث عن مجاهد قال: كتبنا عنه عن مجاهد عن سعيد المقبري ثم قدم علينا بعد، فحدثنا عن الحكم بن عتيبة وليس هو بشيء. حدثنا محمد بن عبد الحميد السهمي قال: حدثنا أحمد بن محمد الحضرمي','435','173','0');
INSERT INTO [t13041] VALUES (185,'بشير مولى بني هاشم مجهول بنقل الحديث ولا يتابع على حديثه','439','173','0');
INSERT INTO [t13041] VALUES (186,'بشار بن موسى الخفاف حدثني أحمد بن محمود الهروي حدثنا عثمان بن سعيد قال: سألت يحيى عن بشار الخفاف فقال: ليس بثقة قال عثمان: بلغني أن علي بن المديني كان يحسن القول في بشار هذا','441','173','0');
INSERT INTO [t13041] VALUES (187,'بكر بن معبد حدثني آدم بن موسى قال: سمعت البخاري قال: بكر بن معبد عن العوام بن المقطع لا يتابع عليه','442','173','0');
INSERT INTO [t13041] VALUES (188,'بكر بن الأسود أبو عبيدة الناجي كان يرى القدر حدثني آدم بن موسى قال: سمعت البخاري قال: قال يحيى بن معين: بكر بن الأسود أبو عبيدة الناجي كذاب. وحدثني محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين قال: أبو عبيدة الناجي صاحب الحسن الذي','444','173','0');
INSERT INTO [t13041] VALUES (189,'بكر أبو عتبة الأعنق عن ثابت وعطاء حدثني آدم قال: سمعت البخاري قال: بكر أبو عتبة الأعنق عن ثابت وعطاء لا يتابع عليه','446','173','0');
INSERT INTO [t13041] VALUES (190,'بكر بن خنيس حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت يحيى ولا عبد الرحمن حدثا عن بكر بن خنيس شيئا قط. حدثنا محمد بن عيسى حدثنا العباس بن محمد قال: سمعت يحيى بن معين يقول: بكر بن خنيس ليس بشيء. حدثنا محمد بن عيسى قال: حدثنا عمرو','448','173','0');
INSERT INTO [t13041] VALUES (191,'بكر بن عبد الله بن الشرود صنعاني حدثنا محمد بن عبد الحميد السهمي قال: حدثنا أحمد بن محمد الحضرمي قال: قال لنا يحيى بن معين: بكر بن الشرود كذاب ومسكنه باليمن. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: بكر بن الشرود صنعاني ليس بشيء.','450','173','0');
INSERT INTO [t13041] VALUES (192,'بكر بن قرواش حدثني آدم قال سمعت البخاري يقول: بكر بن قرواش سمع منه أبو الطفيل قال البخاري: قال علي: لم أسمع يذكره إلا في هذا الحديث','452','173','0');
INSERT INTO [t13041] VALUES (193,'بكر بن بكار أبو عمرو القرشي القيس حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى يقول: بكر بن بكار ليس بشيء','454','173','0');
INSERT INTO [t13041] VALUES (194,'بكير بن مسمار أخو مهاجر بن مسمار حدثني آدم بن موسى قال: سمعت البخاري يقول: بكير بن مسمار أخو مهاجر مولى سعد بن أبي وقاص المدني روى عنه أبو بكر الحنفي قال البخاري: في حديثه بعض النظر','456','173','0');
INSERT INTO [t13041] VALUES (195,'بكير بن معروف حدثنا عبد الله بن محمد بن سعدويه قال: حدثنا أحمد بن عبد الله بن بشير المروزي قال: حدثنا سفيان بن عبد الملك قال: سمعت ابن المبارك قال: بكير بن معروف ارم به','457','173','0');
INSERT INTO [t13041] VALUES (196,'بكير بن عامر البجلي حدثنا عبد الله بن أحمد قال: سألت أبي عن بكير بن عامر قال: كوفي ليس هو بذاك في الحديث، ليس هو بالقوي في الحديث. حدثنا محمد بن عيسى قال حدثنا عباس قال: سمعت يحيى يقول: بكير بن عامر البجلي ضعيف. حدثنا محمد بن أحمد قال: حدثنا','459','173','0');
INSERT INTO [t13041] VALUES (197,'بكار بن عبد الله بن عبيدة بن أخي موسى بن عبيدة الربذي حدثنا آدم بن موسى قال: سمعت البخاري قال: بكار بن عبد الله بن عبيدة الربذي ترك من أجل موسى بن عبيدة قال البخاري: قال علي بن يحيى بن سعيد: كنا نتقي موسى تلك الأيام','461','173','0');
INSERT INTO [t13041] VALUES (198,'بكار بن عبد العزيز بن أبي بكرة حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى يقول: بكار بن عبد العزيز ليس بشيء','463','173','0');
INSERT INTO [t13041] VALUES (199,'بكار بن محمد بن عبد الله بن محمد بن سيرين حدثني آدم بن موسى قال: سمعت البخاري قال: بكار بن محمد بن عبد الله من ولد ابن سيرين يتكلمون فيه','465','173','0');
INSERT INTO [t13041] VALUES (200,'بحر بن مرار بصري من آل أبي بكرة حدثني آدم بن موسى قال: سمعت البخاري قال: بحر بن مرار قال يحيى القطان: رأيت بحرا اختلط. حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت يحيى يقول: أخذت أطراف بحر بن مرار عن عبد الرحمن بن أبي بكرة فسألته','469','173','0');
INSERT INTO [t13041] VALUES (201,'بحر بن كنيز السقاء أبو الفضل الباهلي حدثنا أحمد بن علي الأبار قال: حدثنا علي بن حجر قال: حدثنا إسماعيل بن إبراهيم عن أيوب قال: كان بحر السقاء يحدث عن سعد بن إبراهيم عن قتادة قال: فيقول سعد: لعن الله قتادة، ولعن من يحدثنا عنه. حدثني آدم قال: سمعت','471','173','0');
INSERT INTO [t13041] VALUES (202,'بحير بن ريسان عن عبادة بن الصامت حدثني آدم بن موسى قال: سمعت البخاري قال: بحير بن ريسان عن عبادة بن الصامت لا يتابع عليه وأبو سفيان مجهول لا يعرف','474','173','0');
INSERT INTO [t13041] VALUES (203,'بزيع مولى حنظلة كوفي حدثني آدم بن موسى قال: سمعت البخاري قال: بزيع سمع الضحاك روى عنه محمد بن سلام وأبو معاوية، كوفي مولى يحيى بن عبد الرحمن من سبي بخارى، كان أبو نعيم يتكلم فيه. حدثنا عبد الله بن أحمد بن حنبل قال: سألت أبي عن بزيع الذي يحدث عن','476','173','0');
INSERT INTO [t13041] VALUES (204,'بزيع بن حسان أبو الخليل الخصاف بصري','477','173','0');
INSERT INTO [t13041] VALUES (205,'بريد بن أصرم سمع عليا رضي الله عنه حدثني آدم بن موسى قال: سمعت البخاري قال: بريد بن أصرم سمع عليا روى عنه عتيبة. وعتيبة وبريد مجهولان','482','173','0');
INSERT INTO [t13041] VALUES (206,'بريد بن عبد الله بن أبي بردة بن أبي موسى الأشعري كوفي حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: طلحة بن يحيى أحب إلى من بريد بن عبد الله بن أبي بردة، بريد يروي أحاديث مناكير. حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: لم أسمع يحيى، ولا عبد','485','173','0');
INSERT INTO [t13041] VALUES (207,'البراء بن عبد الله الغنوي حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سألت يحيى عن حديث ابن أبي عروبة عن أبي رجاء عن أبي موسى، في القنوت فقال: لم يسمعه من أبي رجاء إنما هذا حديث البراء الغنوي، وكأنه لم يرض البراء. حدثنا محمد بن عيسى قال','493','173','0');
INSERT INTO [t13041] VALUES (208,'البراء بن يزيد الغنوي عن أبي نضرة حدثنا محمد بن عيسى قال: حدثنا العباس قال: سمعت يحيى بن معين قال: البراء بن يزيد الغنوي صاحب أبي نضرة ضعيف، وفى موضع آخر البراء بن يزيد الغنوي بصري ليس بذاك','495','173','0');
INSERT INTO [t13041] VALUES (209,'بقية بن الوليد الحمصي أبو يحمد الكلاعي حدثنا أحمد بن علي الأبار قال: حدثنا أحمد بن مصعب قال: حدثنا الفضل بن موسى قال: قال بقية بن الوليد: ذاكرت حماد بن زيد أحاديث فقال: ما أجود أحاديثك لو كان لها أجنحة يعني أسانيد. حدثنا عبد الله بن أحمد قال: سمعت','497','173','0');
INSERT INTO [t13041] VALUES (210,'بختري بن المختار كوفي حدثني آدم بن موسى قال: سمعت البخاري قال: بختري بن المختار عن أبي بردة وأبي بكر بن أبي موسى، قال البخاري: يخالف في حديثه','498','173','0');
INSERT INTO [t13041] VALUES (211,'بدر بن مصعب كوفي مخالف في حديثه','499','173','0');
INSERT INTO [t13041] VALUES (212,'بريدة بن سفيان بن فروة الأسلمي حدثنا عبد الله بن أحمد قال: حدثني يحيى بن معين قال: حدثنا يعقوب بن إبراهيم بن سعد قال: سمعت أبي يقول بريدة بن سفيان الذي يروي عنه محمد بن إسحاق كان معنا في طريق الري يشرب الخمر. حدثنا عبد الله قال: سألت أبي عن بريدة بن','501','173','0');
INSERT INTO [t13041] VALUES (213,'باذام أبو صالح مولى أم هانئ حدثنا زكريا بن يحيى قال: حدثنا عبد الله بن محمد الزهري قال: حدثنا سفيان بن عيينة عن محمد بن قيس عن حبيب بن أبي ثابت قال: كنا نسمى أبا صالح باذام مولى أم هانئ: دروغزن. حدثنا محمد بن سعيد بن بلج قال: حدثنا عبد الرحمن بن','502','173','0');
INSERT INTO [t13041] VALUES (214,'بلهط بن عباد عن محمد بن المنكدر مجهول في الرواية، حديثه غير محفوظ ولا يتابع عليه','503','173','0');
INSERT INTO [t13041] VALUES (215,'بريه بن عمر بن سفينة لا يتابع على حديثه ولا يعرف إلا به','505','173','0');
INSERT INTO [t13041] VALUES (216,'باب التاء','507','0','0');
INSERT INTO [t13041] VALUES (217,'تمام بن نجيح الأسدي يحدث بمناكير. حدثني آدم بن موسى قال: سمعت البخاري قال: تمام بن نجيح الأسدي فيه نظر','508','216','0');
INSERT INTO [t13041] VALUES (218,'تمام بن بزيع الشقري عن محمد بن كعب القرظي حدثني آدم قال: سمعت البخاري قال: تمام بن بزيع الشقري يتكلمون فيه','510','216','0');
INSERT INTO [t13041] VALUES (219,'تميم بن محمود الأنصاري حدثني أدم قال: سمعت البخاري قال: تميم بن محمود روى عنه جعفر بن عبد الله والد عبد الحميد بن جعفر، في حديثه نظر','515','216','0');
INSERT INTO [t13041] VALUES (220,'تليد بن سليمان أبو إدريس المحاربي الكوفي حدثني أحمد بن محمد بن صدقة قال: سمعت أبا معمر إسماعيل بن إبراهيم يقول: تليد بن سليمان أبو إدريس وكان أعرجا سمعه قوم ينتقص عثمان وهو على سطح فرموا به فانكسرت رجله فعرج. حدثنا محمد بن عيسى قال: حدثنا العباس بن','517','216','0');
INSERT INTO [t13041] VALUES (221,'باب الثاء','518','0','0');
INSERT INTO [t13041] VALUES (222,'ثابت بن أبي صفية أبو حمزة الثمالي كوفي ويقال ابن دينار حدثنا محمد بن عثمان العبسي قال: وسألت يحيى بن معين عن ثابت بن أبي صفية الثمالي فقال: ليس بذاك. حدثنا عبد الله قال: سألت أبي عن أبي حمزة الثمالي فقال: كان ضعيف الحديث ليس بشيء. حدثنا محمد بن','519','221','0');
INSERT INTO [t13041] VALUES (223,'ثابت بن زهير حدثني آدم بن موسى قال: سمعت البخاري يقول: ثابت بن زهير عن الحسن ونافع، منكر الحديث','520','221','0');
INSERT INTO [t13041] VALUES (224,'ثابت بن قيس أبو الغصن مدني حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى يقول: ثابت بن قيس أبو الغصن ليس حديثه بذاك، وهو صالح','522','221','0');
INSERT INTO [t13041] VALUES (225,'ثابت بن زيد بن ثابت بن زيد بن أرقم حدثنا عبد الله بن أحمد قال: سألت أبي عن ثابت بن زيد بن ثابت بن زيد بن أرقم فقال: روى عنه ابن أبي عروبة وحدثنا عنه معتمر، له أحاديث مناكير. قلت له: تحدث عنه قال: نعم. قالت: أهو ضعيف؟ قال: أنا أحدث عنه','523','221','0');
INSERT INTO [t13041] VALUES (226,'ثابت بن يزيد الأودي أبو السري حدثنا عبد الله بن أحمد قال: حدثني أبي قال: حدثنا يحيى بن سعيد عن ثابت بن يزيد الأودي. قال أبي: قال حفص بن غياث أو أبي إدريس: إن ثابت بن يزيد الأودي هذا لم يكن شيئا. حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال:','525','221','0');
INSERT INTO [t13041] VALUES (227,'ثابت بن عجلان حدثنا عبد الله بن أحمد قال: سألت أبي عن ثابت بن عجلان قال: كان يكون بالباب والأبواب. قلت: هو ثقة، فسكت، كأنه مرض في أمره','527','221','0');
INSERT INTO [t13041] VALUES (228,'ثابت بن حماد بصري حديثه غير محفوظ وهو مجهول بالنقل','529','221','0');
INSERT INTO [t13041] VALUES (229,'ثابت بن موسى العابد الضرير كوفي عن الأعمش حديثه باطل ليس له أصل ولا يتابعه عليه ثقة الذي حدثناه محمد بن عبد الله الحضرمي ومحمد بن أيوب، ومحمد بن عثمان في آخرين','531','221','0');
INSERT INTO [t13041] VALUES (230,'ثمامة بن حصين الشاعر أبو ثفال المري سماه لنا محمد بن إسماعيل ورواه عن الحسن بن علي الحلواني عن سعيد بن عفير. حدثني آدم بن موسى قال: سمعت البخاري يقول: أبو ثفال المري عن رباح بن عبد الرحمن في حديثه نظر. حدثنا إبراهيم بن عبد الوهاب الأبزاري قال: حدثنا','534','221','0');
INSERT INTO [t13041] VALUES (231,'ثمامة بن عبيدة العبدي بصري حدثني آدم بن موسى قال: سمعت البخاري قال: ثمامة بن عبيده العبدي ضعفه علي ونسبه إلى الكدي من ناحية البصرة','536','221','0');
INSERT INTO [t13041] VALUES (232,'ثعلبة بن يزيد الحماني عن علي حدثني آدم قال: سمعت البخاري قال: ثعلبة بن يزيد الحماني عن علي قال البخاري: لا يتابع في حديثه نظر','538','221','0');
INSERT INTO [t13041] VALUES (233,'ثور بن يزيد الكلاعي الحمصي حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: سمعت يزيد بن هارون يقول: كان ثور بن يزيد قدريا. حدثنا عبد الله بن أحمد قال: سمعت أبي، يقول: ثور بن يزيد الكلاعي كان يرى القدر وكان من أهل حمص نفوه وأخرجوه منها لأنه كان','541','221','0');
INSERT INTO [t13041] VALUES (234,'ثوير بن أبي فاختة أبو الجهم مولى جعدة بن هبيرة حدثنا بشر بن موسى قال: حدثنا الحميدي قال: ذكر لسفيان ثوير بن أبي فاختة فغمزه. حدثنا زكريا بن يحيى قال: سمعت أبا عبد الله محمد بن أبي صفوان البصري الأموي قال: سمعت أبي، يقول سمعت: سفيان الثوري يقول:','543','221','0');
INSERT INTO [t13041] VALUES (235,'باب الجيم','544','0','0');
INSERT INTO [t13041] VALUES (236,'جعفر بن الزبير الشامي حدثنا محمد بن عثمان قال: حدثنا علي بن المديني قال: حدثنا غندر قال: رأيت شعبة راكبا على حمار فقيل له: أين تريد يا أبا بسطام؟ قال: أذهب فأستعدي على هذا يعني جعفر بن الزبير، وضع على رسول الله صلى الله عليه وسلم أربعمائة حديثا','545','235','0');
INSERT INTO [t13041] VALUES (237,'جعفر بن عبد الله بن عثمان بن حميد القرشي الحميدي مكي في حديثه وهم واضطراب','546','235','0');
INSERT INTO [t13041] VALUES (238,'جعفر بن برقان الجزري ضعيف في روايته عن الزهري. حدثنا عبد الله بن أحمد قال: سألت أبي عن جعفر بن برقان قال: إذا حدث عن غير الزهري فلا بأس، ثم قال في حديث الزهري يخطيء. حدثني أحمد بن محمود الهروي قال: حدثنا عثمان بن سعيد قال: قلت ليحيى بن معين، وذكر','550','235','0');
INSERT INTO [t13041] VALUES (239,'جعفر بن محمد بن عباد بن جعفر المخزومي حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سألت سفيان عن جعفر بن محمد بن عباد بن جعفر وكان قدم اليمن فما روي عنه شيء، فقلت لسفيان: روى عنه معمر أحاديث يحيى بن سعيد فقال سفيان: إنما وجدنا ذاك','552','235','0');
INSERT INTO [t13041] VALUES (240,'جعفر بن زياد الأحمر كوفي حدثنا محمد بن عثمان بن أبي شيبة قال: سأل الأزرق بن علي يحيى بن معين، وأنا أسمع عن جعفر بن زياد الأحمر قال: كان يتشيع، وكان ثقة. حدثنا أحمد بن محمود حدثنا عثمان بن سعيد قال: سئل يحيى عن جعفر الأحمر، فقال بيده، لم يضعفه ولم','554','235','0');
INSERT INTO [t13041] VALUES (241,'جعفر بن جسر بن فرقد القصاب بصري وحفظه فيه اضطراب شديد، كان يذهب إلى القدر وحدث بمناكير','555','235','0');
INSERT INTO [t13041] VALUES (242,'جعفر بن أبي جعفر الأشجعي واسم أبي جعفر ميسرة حدثني آدم بن موسى قال: سمعت البخاري قال: جعفر بن أبي جعفر الأشجعي عن أبيه، هو ضعيف الحديث منكر الحديث','557','235','0');
INSERT INTO [t13041] VALUES (243,'جعفر بن الحارث أبو الأشهب الواسطي حدثني آدم بن موسى قال: سمعت البخاري قال: جعفر بن الحارث الواسطي أبو الأشهب عن منصور في حفظه شيء يكتب حديثه. وقال عنه في موضع آخر: جعفر بن الحارث أبو الأشهب الواسطي منكر الحديث. حدثنا محمد بن عيسى قال: حدثنا عباس بن','559','235','0');
INSERT INTO [t13041] VALUES (244,'جعفر بن سليمان الضبعي بصري حدثنا محمد بن مروان القرشي قال: حدثنا أحمد بن المقدام العجلي قال: كنا في مجلس يزيد بن زريع قال: من أتى جعفر بن سليمان الضبعي، وعبد الوارث التنوري فلا يقربني، وكان التنوري ينسب إلى الاعتزال، وكان جعفر ينسب إلى الرفض.','560','235','0');
INSERT INTO [t13041] VALUES (245,'جعفر بن ميمون حدثنا عبد الله بن أحمد قال: سألت أبي عن جعفر بن ميمون فقال: أخشى أن يكون ضعيف الحديث. حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين يقول: جعفر بن ميمون ليس بثقة','561','235','0');
INSERT INTO [t13041] VALUES (246,'جعفر بن مرزوق المدائني عن يحيى بن سعيد الأنصاري وغيره أحاديثه مناكير لا يتابع منها على شيء','563','235','0');
INSERT INTO [t13041] VALUES (247,'جميل بن زيد الطائي','565','235','0');
INSERT INTO [t13041] VALUES (248,'جميل بن عمارة كوفي حدثني آدم بن موسى قال: سمعت البخاري قال: جميل بن عمارة روى عنه إسماعيل بن نشيط سمع سالما قال البخاري: فيه نظر','567','235','0');
INSERT INTO [t13041] VALUES (249,'جابر بن يزيد الجعفي حدثنا الحسين بن أحمد بن منصور قال: حدثنا داود بن رشيد قال: حدثنا ابن علية قال: حدثنا أيوب قال: قلت لسعيد بن جبير: إن جابر بن يزيد يقول كذا وكذا فقال: كذب جابر. حدثنا أحمد بن علي قال: حدثنا إبراهيم بن زياد سبلان ح وحدثنا محمد','568','235','0');
INSERT INTO [t13041] VALUES (250,'جابر بن نوح الحماني حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين يقول: جابر بن نوح إمام مسجد بني حمان ولم يكن بثقة وكان أبوه نوح ثقة، وقال في موضع آخر: جابر بن نوح ليس حديثه بشيء، كان حفص بن غياث يضعفه','571','235','0');
INSERT INTO [t13041] VALUES (251,'جرير بن أيوب البجلي عن أبي زرعة بن عمرو بن جرير حدثني آدم بن موسى قال: سمعت البخاري قال: جرير بن أيوب عن أبي زرعة بن عمرو بن جرير بن عبد الله البجلي روى عنه وكيع وعبد الله بن رجاء البصري، منكر الحديث','573','235','0');
INSERT INTO [t13041] VALUES (252,'جرير بن حازم أبو النضر الأزدي البصري حدثنا عبد الله بن أحمد بن حنبل قال: سألت يحيى بن معين عن جرير بن حازم قال: هو في قتادة ضعيف، روى عنه أحاديث مناكير. حدثنا عبد الله قال: سمعت أبي يقول: حدثنا عفان قال: اجتمع جرير بن حازم، وحماد بن زيد فجعل جرير','575','235','0');
INSERT INTO [t13041] VALUES (253,'جرير بن عبد الحميد الضبي حدثني محمد بن عيسى الهاشمي قال: حدثني جعفر بن عامر قال: سمعت أحمد بن حنبل يقول: جرير بن عبد الحميد لا يفصل بين مغيرة، وإبراهيم كان يكره فذكرت ذلك لخلف بن سالم قال أحمد: اشتكت عينه فحلفت عليه أمه أن لا يجيء إلى جرير، مثل','580','235','0');
INSERT INTO [t13041] VALUES (254,'جراح بن المنهال أبو العطوف الجزري حدثني آدم بن موسى قال: سمعت البخاري قال: جراح بن المنهال أبو العطوف روى عنه يزيد بن هارون، منكر الحديث. حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سألت يحيى عن أبي العطوف الجزري، فقال: ليس بشيء. حدثنا محمد','581','235','0');
INSERT INTO [t13041] VALUES (255,'جزي بن بكير العبسي عن حذيفة حدثني آدم بن موسى قال: سمعت البخاري قال: جزي بن بكير العبسي عن حذيفة منكر الحديث','583','235','0');
INSERT INTO [t13041] VALUES (256,'جميع بن ثوب شامي حدثني آدم بن موسى قال: سمعت البخاري قال: جميع بن ثوب عن خالد بن معدان وحبيب بن عبيد ويزيد بن حمير، منكر الحديث','585','235','0');
INSERT INTO [t13041] VALUES (257,'جارود بن يزيد النيسابوري حدثني آدم بن موسى قال: سمعت محمد بن إسماعيل البخاري قال: جارود بن يزيد النيسابوري منكر الحديث، وكان أبو أسامة يرميه بالكذب. محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين يقول: جارود ليس بشيء','587','235','0');
INSERT INTO [t13041] VALUES (258,'جسر بن فرقد القصاب حدثني آدم قال: حدثنا محمد بن إسماعيل قال: جسر بن فرقد أبو جعفر يروي عنه يحيى بن الضريس، وغيره عن الحسن وليس بذاك. حدثنا عبد الله بن أحمد قال: قال لي يحيى بن معين: ابتدأ من عنده وذكر جسر فقال: ليس بشيء','589','235','0');
INSERT INTO [t13041] VALUES (259,'جارية بن هرم أبو شيخ الفقيمي حدثنا حدثني محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سمعت يحيى يقول: دخلت على موسى بن دينار المكي أنا وحفص بن غياث فجعلت لا أريده على شيء إلا لقنه فخرجنا فاتبعنا أبو الشيخ الفقيمي، فجعلت أبين له أمره فجعل','591','235','0');
INSERT INTO [t13041] VALUES (260,'جلاس بن عمير حدثني آدم قال: سمعت البخاري قال: جلاس بن عمير روى عنه أبو جناب ولا يصح حديثه','593','235','0');
INSERT INTO [t13041] VALUES (261,'جلد بن أيوب حدثنا بشر بن موسى الأسدي قال: حدثنا الحميدي قال: كان سفيان بن عيينة يقول: جلد، وما جلد، ومن جلد، ومتى كان جلد؟ . حدثنا محمد بن إسماعيل وأحمد بن علي قالا: حدثنا الحسن بن علي الحلواني قال: حدثنا أحمد بن شبويه قال: قال ابن المبارك:','595','235','0');
INSERT INTO [t13041] VALUES (262,'جويبر بن سعيد البلخي عن الضحاك حدثني أدم بن موسى قال: حدثنا محمد بن إسماعيل قال: جويبر بن سعيد البلخي عن الضحاك قال علي: كنت أعرف جويبر بحديثين يعني ثم أخرج هذه الأحاديث بعد فضعفه. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: كان وكيع إذا أتى على','596','235','0');
INSERT INTO [t13041] VALUES (263,'جعد بن درهم أستاذ جهم حدثنا الحسن قال: حدثنا أبو صالح قال: حدثنا سفيان عن عمرو بن إسماعيل قال: جمعت بين أبي بيهس والجعد بن درهم فاختصما، قال: وصلب الجعد هشام','597','235','0');
INSERT INTO [t13041] VALUES (264,'جعدة من ولد أم هانئ حدثني أدم قال: سمعت البخاري قال: جعدة من ولد أم هانئ صالح، روى عنه شعبة، لا يعرف إلا بحديث فيه نظر','598','235','0');
INSERT INTO [t13041] VALUES (265,'جبارة بن المغلس الحماني كوفي','600','235','0');
INSERT INTO [t13041] VALUES (266,'باب الحاء','602','0','0');
INSERT INTO [t13041] VALUES (267,'الحارث بن عبد الله الهمداني الخارفي الأعور حدثنا عبد الله بن أحمد بن حنبل قال: حدثني أبي قال: حدثنا أبو أسامة قال: حدثني مفضل بن مهلهل قال: حدثني مغيرة قال: سمعت الشعبي يقول: حدثني الحارث الأعور وأنا أشهد أنه أحد الكاذبين. حدثنا محمد بن إسماعيل','603','266','0');
INSERT INTO [t13041] VALUES (268,'الحارث بن محمد عن أبي الطفيل حدثني آدم بن موسى قال: سمعت البخاري قال: الحارث بن محمد عن أبي الطفيل: كنت على الباب يوم الشورى. رواه زافر عن الحارث ولم يبين سماعه منه ولم يتابع زافر عليه','606','266','0');
INSERT INTO [t13041] VALUES (269,'الحارث بن عبيد أبو قدامة الإيادي بصري حدثنا عبد الله بن أحمد بن محمد بن حنبل قال: سألت يحيى بن معين عن الحارث بن عبيد أبي قدامة الإيادي فقال: ضعيف الحديث، وسألت أبي فقال: هو مضطرب الحديث. حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: سمعت عبد','608','266','0');
INSERT INTO [t13041] VALUES (270,'الحارث بن شبل عن أم النعمان عن عائشة بصري حدثنا محمد بن عيسى قال: سمعت العباس بن محمد الدوري قال: سمعت يحيى بن معين يقول: الحارث بن شبل عن أم النعمان، بصري ليس بشيء. حدثنا آدم بن موسى قال: سمعت محمد بن إسماعيل البخاري قال: الحارث بن شبل عن أم','610','266','0');
INSERT INTO [t13041] VALUES (271,'الحارث بن النعمان يقال له: ابن أخت سعيد بن جبير عن أنس وسعيد بن جبير كوفي. حدثني آدم بن موسى قال: سمعت البخاري قال: الحارث بن النعمان سمع أنسا منكر الحديث، روى عنه سعيد بن عمارة','614','266','0');
INSERT INTO [t13041] VALUES (272,'الحارث بن وجيه بصري، عن مالك بن دينار حدثنا أحمد بن علي الأبار قال: سمعت نصر بن علي الجهضمي يضعف الحارث بن وجيه. وحدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين يقول: الحارث بن وجيه ليس حديثه بشيء. وحدثني آدم بن موسى قال:','618','266','0');
INSERT INTO [t13041] VALUES (273,'الحارث بن عمرو ابن أخي المغيرة بن شعبة كوفي حدثني آدم بن موسى قال: سمعت البخاري قال: الحارث بن عمرو ابن أخي المغيرة بن شعبة عن أصحاب معاذ، عن معاذ روى عنه أبو عون قال البخاري: ولا يصح ولا يعرف إلا مرسلا','620','266','0');
INSERT INTO [t13041] VALUES (274,'الحارث بن ثقف كوفي حدثنا محمد بن عثمان بن أبي شيبة قال: سمعت يحيى بن معين وذكر الحارث بن ثقف فقال يحيى: كان ضعيفا، ولا أحفظ للحارث حديثا مسندا إلا مقطعات','622','266','0');
INSERT INTO [t13041] VALUES (275,'الحارث بن حصيرة كوفي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: الحارث بن حصيرة كان شيعيا. حدثنا أحمد بن علي الأبار قال: حدثنا أبو غسان قال: سمعت جريرا وقيل له: رأيت الحارث بن حصيرة؟ قال: نعم رأيت شيخا طويل السكوت منطويا على أمر عظيم','624','266','0');
INSERT INTO [t13041] VALUES (276,'الحارث بن نبهان حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين يقول: الحارث بن نبهان لا يكتب حديثه، وفى موضع آخر: ضعيف، وفى موضع: آخر ليس بشيء. حدثني آدم بن موسى قال: سمعت البخاري قال: الحارث بن نبهان عن عاصم والأعمش، منكر الحديث','626','266','0');
INSERT INTO [t13041] VALUES (277,'الحارث بن غسان المري بصري','630','266','0');
INSERT INTO [t13041] VALUES (278,'الحارث بن سريج النقال بغدادي','633','266','0');
INSERT INTO [t13041] VALUES (279,'الحارث بن أفلح مديني حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: الحارث بن أفلح لم يكن بشيء، روى عنه مروان بن معاوية وقد روى عنه غير مروان أيضا','635','266','0');
INSERT INTO [t13041] VALUES (280,'الحسن بن أبي جعفر الجفري بصري حدثني آدم بن موسى قال: سمعت البخاري يقول: الحسن بن أبي جعفر الجفري البصري عن أبي الزبير، منكر الحديث وهو الحسن بن عجلان. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين يقول: الحسن بن أبي جعفر','638','266','0');
INSERT INTO [t13041] VALUES (281,'الحسن بن دينار أبو سعيد التميمي بصري حدثنا عبد الله بن محمد بن سعدويه المروزي قال: حدثنا أحمد بن عبد الله بن بشير المروزي قال: حدثنا سفيان بن عبد الملك قال: سمعت ابن المبارك يقول: أما الحسن بن دينار فكان يرى رأي القدر، فكان يحمل كتبه إلى بيوت الناس','640','266','0');
INSERT INTO [t13041] VALUES (282,'الحسن بن ذكوان بصري حدثنا الخضر بن داود قال: حدثنا أحمد بن محمد بن هانئ قال: قلت لأبي: عبد الله الحسن بن ذكوان ما تقول فيه؟ فقال: أحاديثه أباطيل. يروي عن حبيب بن أبي ثابت. فقلت له: نعم غير حديث عجيب عن عاصم بن ضمرة، عن علي في المسألة وعسب الفحل','641','266','0');
INSERT INTO [t13041] VALUES (283,'الحسن بن رزين بصري مجهول في الرواية مجهول بالنقل وحديثه غير محفوظ','644','266','0');
INSERT INTO [t13041] VALUES (284,'الحسن بن رشيد في حديثه وهم ويحدث بمناكير','646','266','0');
INSERT INTO [t13041] VALUES (285,'الحسن بن زريق كوفي عن ابن عيينة بحديث ليس له أصل من حديث ابن عيينة عن الزهري وليس بمحفوظ عن ابن عيينة عن الزهري','651','266','0');
INSERT INTO [t13041] VALUES (286,'الحسن بن زياد اللؤلؤي من أصحاب نعمان حدثني محمد بن عثمان العبسي قال: سمعت يحيى بن معين سئل عن الحسن بن زياد اللؤلؤي فقال: كان ضعيف الحديث. حدثني محمد بن عبد الحميد السهمي قال: حدثنا أحمد بن محمد الحضرمي قال: سألت يحيى بن معين عن الحسن بن زياد','653','266','0');
INSERT INTO [t13041] VALUES (287,'الحسن بن سوار البغوي خرساني','654','266','0');
INSERT INTO [t13041] VALUES (288,'حسن بن صالح بن حي الهمداني الكوفي حدثنا عبد الله بن أحمد بن حنبل قال: حدثنا أبو معمر إسماعيل بن إبراهيم الهذلي قال: حدثنا أبو أسامة قال: سمعت زائدة يقول: إن ابن حي، هذا قد استصلب منذ زمان وما نجد أحدا يصلبه. حدثني محمد بن عيسى قال: حدثنا إبراهيم','656','266','0');
INSERT INTO [t13041] VALUES (289,'الحسن بن عبد الله بن أبي عون الثقفي كوفي في حديثه وهم','657','266','0');
INSERT INTO [t13041] VALUES (290,'الحسن بن علي الهاشمي عن الأعرج حدثني آدم بن موسى الخواري قال: سمعت البخاري قال: الحسن بن علي الهاشمي منكر الحديث، يحدث عن الأعرج','660','266','0');
INSERT INTO [t13041] VALUES (291,'الحسن بن علي الشروي عن عطاء لا يتابع على حديثه وهو مجهول بالنقل','663','266','0');
INSERT INTO [t13041] VALUES (292,'الحسن بن علي الهمداني مجهول أيضا لا يتابع على حديثه ولا يعرف إلا به','665','266','0');
INSERT INTO [t13041] VALUES (293,'الحسن بن علي النميري كوفي مجهول وفضل بن الربيع نحوه ولا يتابع عليه إلا من هو دونه أو مثله','667','266','0');
INSERT INTO [t13041] VALUES (294,'الحسن بن علي بن عاصم بن صهيب الواسطي حدثنا محمد بن أحمد بن حماد قال: حدثنا معاوية بن صالح الأشعري قال: سمعت يحيى بن معين يقول: علي بن عاصم ليس بشيء وابنه الحسن','669','266','0');
INSERT INTO [t13041] VALUES (295,'الحسن بن عمرو بن سيف العبدي بصري ويقال: باهلي حدثنا عبد الرحمن بن الفضل قال: حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن عمرو كذاب','670','266','0');
INSERT INTO [t13041] VALUES (296,'الحسن بن عمارة أبو محمد مولى بجيلة كوفي حدثنا أحمد بن علي الأبار قال: حدثنا أحمد بن سعيد الدارمي قال: حدثنا نضر بن شميل قال: حدثنا شعبة قال: أفادني الحسن بن عمارة عن الحكم سبعين حديثا فلم يكن لها أصل. أخبرنا زكريا بن يحيى قال: حدثنا محمد بن عبد','673','266','0');
INSERT INTO [t13041] VALUES (297,'الحسن بن قتيبة المدائني كثير الوهم','680','266','0');
INSERT INTO [t13041] VALUES (298,'الحسن بن محمد البلخي منكر الحديث يقال: كان قاضي مرو','684','266','0');
INSERT INTO [t13041] VALUES (299,'الحسن بن محمد بن عبيد الله بن أبي يزيد مكي عن ابن جريج لا يتابع على حديثه ولا يعرف إلا به وليس بمشهور بالنقل','687','266','0');
INSERT INTO [t13041] VALUES (300,'الحسن بن مسلم بن صالح العجلي بصري عن ثابت مجهول في النقل وحديثه غير محفوظ وقد روي في قل هو الله أحد أحاديث صالحة الأسانيد من حديث ثابت وأما في إذا زلزلت وقل يا أيها الكافرون أسانيدها مقارب هذا الإسناد','689','266','0');
INSERT INTO [t13041] VALUES (301,'الحسن بن السكن عن الأعمش لا يتابع عليه ولا يعرف إلا به. حدثنا عبد الله بن أحمد قال: قال أبي: الحسن بن السكن روى عن الأعمش، منكر الحديث','691','266','0');
INSERT INTO [t13041] VALUES (302,'الحسن بن يحيى الخشني شامي حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين يقول: الحسن بن يحيى الخشني ليس بشيء','693','266','0');
INSERT INTO [t13041] VALUES (303,'الحسين بن عبد الله بن عبيد الله بن عباس الهاشمي حدثني أحمد بن محمود الهروي قال حدثنا عثمان بن سعيد قال سألت يحيي بن معين عن حسين بن عبيد الله الذي يروي عنه ابن إسحاق قال ضعيف قال فحسين بن عبد الله الذي يروي عنه ابن جريج قال هو هو حدثنا آدم بن موسى قال:','695','266','0');
INSERT INTO [t13041] VALUES (304,'حسين بن عبد الله بن ضميرة مديني حدثنا محمد بن أحمد بن داود السمناني قال: حدثنا مهدي بن علي قال: حدثنا مطرف بن عبد الله أبو مصعب قال: سمعت مالكا يقول: إن ها هنا قوما يحدثون في هذا المسجد يعني مسجد النبي صلى الله عليه وسلم يكذبون منهم حسين بن ضميرة.','698','266','0');
INSERT INTO [t13041] VALUES (305,'حسين بن قيس الرجي أبو علي ويقال: حنش لا يعرف إلا به حدثنا عبد الله قال: سألت أبي عن حسين بن قيس يقال له حنش فقال: متروك الحديث ضعيف الحديث. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين يقول: حسين بن قيس أبو علي الرجي هو حنش','701','266','0');
INSERT INTO [t13041] VALUES (306,'حسين بن أبي سفيان السلمي الواسطي والد سفيان بن حسين حدثني آدم بن موسى قال: سمعت البخاري قال: حسين بن أبي سفيان روى عنه عبد الرحمن بن إسحاق حديثه ليس بمستقيم','705','266','0');
INSERT INTO [t13041] VALUES (307,'حسين بن حسن الأشقر حدثنا إبراهيم بن عبد الوهاب قال: حدثنا أحمد بن محمد بن هانئ الأثرم قال: قلت لأبي عبد الله: حسين الأشقر تحدث عنه؟ كالمنكر لذلك، فقال لي: لم يكن عندي ممن يكذب في الحديث وذكر عنه التشيع فقال له العباس بن عبد العظيم حدث في أبي بكر','707','266','0');
INSERT INTO [t13041] VALUES (308,'حسين بن الحسن العوفي حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين سئل عن حسين بن الحسن فقال: ذاك العوفي ضعيف','711','266','0');
INSERT INTO [t13041] VALUES (309,'حسين بن ذكوان المعلم بصري، ضعيف، مضطرب الحديث حدثنا عبد الله بن أحمد بن حنبل قال: حدثنا أبو بكر بن خلاد قال: سمعت يحيى وذكر أحاديث حسين المعلم فقال: فيه اضطراب. حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: قلت ليحيى بن سعيد: إن يزيد','712','266','0');
INSERT INTO [t13041] VALUES (310,'حسين بن واقد أبو علي المروزي قاضي مرو','713','266','0');
INSERT INTO [t13041] VALUES (311,'حسين بن وردان عن أبي الزبير لا يتابع عليه على حديثه لا يعرف إلا به','715','266','0');
INSERT INTO [t13041] VALUES (312,'حسين بن علوان حدثنا محمد بن عثمان بن أبي شيبة قال: سمعت يحيى بن معين سئل عن الحسين بن علوان فقال: كان كذابا','717','266','0');
INSERT INTO [t13041] VALUES (313,'حسين بن عبيد الله التميمي عن شريك لا يتابع على حديثه هذا أو هو مجهول بالنقل','718','266','0');
INSERT INTO [t13041] VALUES (314,'حسين بن سليمان مولى قريش، كوفي، مجهول، لا يتابع على حديثه هذا وليس بمعروف ولا يعرف بالنقل','720','266','0');
INSERT INTO [t13041] VALUES (315,'حسين بن أبي بردة كوفي، في حديثه وهم، يخالف في حديثه','722','266','0');
INSERT INTO [t13041] VALUES (316,'حسين بن ميمون الخندفي كوفي','724','266','0');
INSERT INTO [t13041] VALUES (317,'حسين أبو المنذر عن الرقاشي حدثني آدم بن موسى قال: سمعت البخاري قال: حسين أبو المنذر عن الرقاشي سمع منه معتمر ولم تصح روايته','727','266','0');
INSERT INTO [t13041] VALUES (318,'حسين بن عمران الجهني حدثني آدم قال: سمعت البخاري قال: حسين بن عمران الجهني، قال البخاري: لا يتابع على حديثه','729','266','0');
INSERT INTO [t13041] VALUES (319,'حسان بن إبراهيم الكرماني في حديثه وهم','731','266','0');
INSERT INTO [t13041] VALUES (320,'الحكم بن أبان العدني حدثنا عبد الله بن محمد بن سعدويه قال: حدثنا أحمد بن عبد الله بن بشير المروزي قال: حدثنا سفيان بن عبد الملك قال: سمعت عبد الله بن المبارك يقول: الحكم بن أبان وحسام وأيوب بن سويد: ارم بهولاء','734','266','0');
INSERT INTO [t13041] VALUES (321,'الحكم بن عبد الله بن سعد الأيلي حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين يقول: الحكم بن عبد الله الأيلي ضعيف، وقال في موضع آخر: ليس بشيء. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى يقول: الحكم بن عبد','736','266','0');
INSERT INTO [t13041] VALUES (322,'الحكم بن عبد الله أبو مطيع قاضي بلخ حدثنا عبد الله بن أحمد قال: سألت أبي عن الحكم بن عبد الله أبي مطيع البلخي قال: لا ينبغي أن يروى عنه، إنه كان يقول: الجنة والنار خلقتا فستفنيان، وهذا كلام جهم، لا يروى عنه شيء. حدثنا محمد بن عيسى قال: حدثنا عباس','738','266','0');
INSERT INTO [t13041] VALUES (323,'الحكم بن سنان أبو عون القربي بصري حدثني آدم بن موسى قال: سمعت البخاري قال: الحكم بن سنان أبو عون الباهلي البصري، عن مالك بن دينار يكتب حديثه. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين قال: الحكم بن سنان ضعيف','739','266','0');
INSERT INTO [t13041] VALUES (324,'الحكم بن عبد الملك عن قتادة','741','266','0');
INSERT INTO [t13041] VALUES (325,'الحكم بن عطية القيسي عن ثابت بصري حدثني آدم بن موسى قال: سمعت محمد بن إسماعيل قال: الحكم بن عطية، عن ثابت عن أنس. . . قال: كان أبو الوليد يضعفه','745','266','0');
INSERT INTO [t13041] VALUES (326,'الحكم بن ظهير الفزاري عن السدي وعاصم حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى يقول: الحكم بن ظهير ليس بشيء، وفى موضع آخر ليس بثقة. حدثني آدم قال: سمعت البخاري يقول: الحكم بن ظهير الفزاري عن السدي، وعاصم بن بهدلة، تركوه منكر','748','266','0');
INSERT INTO [t13041] VALUES (327,'الحكم بن يعلى بن عطاء المحاربي','751','266','0');
INSERT INTO [t13041] VALUES (328,'الحكم بن سعيد المديني عن الجعيد بن عبد الرحمن حدثني آدم بن موسى قال: سمعت البخاري قال: الحكم بن سعيد المديني عن الجعيد بن عبد الرحمن منكر الحديث','753','266','0');
INSERT INTO [t13041] VALUES (329,'حبيب بن حسان بن أبي الأشرس كوفي','755','266','0');
INSERT INTO [t13041] VALUES (330,'حبيب بن أبي حبيب عن عمرو بن هرم حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: سألت عبد الصمد عن أمر حبيب بن أبي حبيب قال: دفع إلي كتابه وكتبته، فإنما كان في كتابه: وسئل وسئل، فحدثني وقال حبيب يعني جابر بن زيد، ثم بلغني بعد أنه كتب نسخة آخرى.','758','266','0');
INSERT INTO [t13041] VALUES (331,'حبيب بن سالم مولى النعمان بن بشير حدثني آدم قال: سمعت البخاري قال: حبيب بن سالم مولى النعمان بن بشير عن النعمان بن بشير قال البخاري: فيه نظر','759','266','0');
INSERT INTO [t13041] VALUES (332,'حبيب بن أبي ثابت وهو حبيب بن قيس كوفي حدثنا عبد الله بن أحمد قال: حدثنا أبو بكر بن خلاد قال: سمعت يحيى بن سعيد، يقول: حبيب بن أبي ثابت عن عطاء ليست بمحفوظة سمعته يقول إن كانت محفوظة فقد نزل عنها يعني عطاء نزل عنها. وحدثني عبد الرحمن بن الفضل قال:','762','266','0');
INSERT INTO [t13041] VALUES (333,'حبيب المالكي كوفي حدثنا محمد بن سعيد بن بلج الرازي قال: سمعت عبد الرحمن بن الحكم بن بشير بن سلمان يذكر عن قوقل قال: كان بالكوفة رجل يقال له: حبيب المالكي فكان رجلا له فضل وصحة، قال: فذكرناه لابن المبارك فأثنى عليه قال: قلت عنده حديث غريب، قال: ما','765','266','0');
INSERT INTO [t13041] VALUES (334,'حبيب بن أبي العالية حدثنا عبد الله قال: سألت أبي عن حبيب بن أبي العالية فقال: روى عنه هشيم فقال: ما أدري له أحاديث، كأنه ضعفه','766','266','0');
INSERT INTO [t13041] VALUES (335,'حبيب بن رزيق كاتب مالك بن أنس حدثنا عبد الله بن أحمد بن حنبل قال: سمعت أبي وذكر حبيبا الذي كان يقرأ على مالك بن أنس فقال: ليس بثقة. قدم علينا رجل أحسبه قال: من أهل خراسان كتب عن حبيب كتابا عن ابن أخى ابن شهاب، عن عمه، عن سالم، والقاسم فإذا هي','767','266','0');
INSERT INTO [t13041] VALUES (336,'حميد بن قيس المقرئ المكي حدثنا عبد الله قال: سمعت أبي يقول: حميد بن قيس مكي، قارئ أهل مكة، ليس هو بقوي في الحديث','768','266','0');
INSERT INTO [t13041] VALUES (337,'حميد بن هلال العدوي بصري حدثنا محمد بن عيسى حدثنا صالح بن أحمد حدثنا علي بن عبد الله قال: سمعت يحيى يقول: كان محمد بن سيرين لا يرضى حميد بن هلال','769','266','0');
INSERT INTO [t13041] VALUES (338,'حميد بن زاذويه الطويل بصري حدثنا محمد بن إسماعيل قال: حدثنا الحميدي قال: حدثنا سفيان قال: كان عندنا شويب بصري يقال له درست، فقال لي: إن حميدا قد اختلط عليه، ما سمع من أنس، ومن ثابت وقتادة عن أنس إلا بشيء يسير، فكنت أقول له أخبرني بما ثبت عن غير','770','266','0');
INSERT INTO [t13041] VALUES (339,'حميد بن مالك اللخمي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: حميد بن مالك اللخمي ضعيف لم يحدث عنه إلا إسماعيل بن عياش','773','266','0');
INSERT INTO [t13041] VALUES (340,'حميد بن الأسود','774','266','0');
INSERT INTO [t13041] VALUES (341,'حميد بن علي الأعرج كوفي حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين يقول: حميد بن عطاء ليس بشيء، روى عنه عبيد الله بن موسى، وخلف بن خليفة حدثني آدم بن موسى قال: حدثنا محمد بن إسماعيل البخاري قال: حميد بن علي الأعرج كوفي، عن','776','266','0');
INSERT INTO [t13041] VALUES (342,'حميد بن وهب القرشي عن ابن طاوس ولا يتابع عليه، وحميد مجهول في النقل','779','266','0');
INSERT INTO [t13041] VALUES (343,'حميد بن صخر مديني حدثنا محمد بن عيسى قال: حدثنا حمدان بن علي الوراق قال: سئل أحمد بن حنبل عن حميد بن صخر فقال: ضعيف','781','266','0');
INSERT INTO [t13041] VALUES (344,'حرمي بن عمارة بن أبي حفصة حدثنا الخضر بن داود قال: حدثنا أحمد بن محمد قال: قال أبو عبد الله في حرمي بن عمارة كلاما معناه: أنه صدوق ولكن كانت فيه غفلة، فذكرت له عن علي بن المديني، عن حرمي بن عمارة، عن شعبة، عن قتادة، عن أنس: من كذب. . . فأنكره،','782','266','0');
INSERT INTO [t13041] VALUES (345,'حفص بن سليمان الأسدي المقرئ كوفي حدثنا عبد الله بن أحمد قال: حدثني أبي قال: حدثنا يحيى القطان قال: ذكر شعبة حفص بن سليمان فقال: كان يأخذ كتب الناس وينسخها وقال شعبة: أخذ مني حفص بن سليمان كتابا فلم يرده، وكان يستعير كتب الناس. حدثنا عبد الله قال:','783','266','0');
INSERT INTO [t13041] VALUES (346,'حفص بن عمر بن أبي العطاف مديني حدثني آدم بن موسى قال: سمعت البخاري قال: حفص بن عمر بن أبي العطاف المديني، عن أبي الزناد منكر الحديث','784','266','0');
INSERT INTO [t13041] VALUES (347,'حفص بن عمر أبو عمر الضرير حدثنا محمد بن عبد الحميد السهمي قال: حدثنا أحمد بن محمد الحضرمي قال: سألت يحيى بن معين عن حفص بن عمر الضرير قال: لا يرضى','787','266','0');
INSERT INTO [t13041] VALUES (348,'حفص بن عمر العدني يعرف بالفرخ. لا يقيم الحديث','789','266','0');
INSERT INTO [t13041] VALUES (349,'حفص بن عمر بن ميمون مولى علي بن أبي طالب أبو إسماعيل الأبلي بصري','796','266','0');
INSERT INTO [t13041] VALUES (350,'حفص بن عمر أبو عمران الواسطي حدثني آدم قال: سمعت البخاري قال: حفص بن عمر أبو عمران الإمام الواسطي يتكلمون فيه','800','266','0');
INSERT INTO [t13041] VALUES (351,'حفص بن أسلم العدوي ويقال: الجحدري، ويقال: السلمي حدثنا آدم بن موسى قال: سمعت البخاري قال: حفص بن أسلم العدوي ويقال: الجحدري عن ثابت روى عنه سليمان بن حرب قال البخاري: صاحب العجائب عجائب','801','266','0');
INSERT INTO [t13041] VALUES (352,'حجاج بن أرطاة أبو أرطاة النخعي الكوفي حدثنا حبان بن إسحاق البلخي حدثنا إسحاق بن محمد البلخي يقال له: ابن باجويه قال: سمعت يحيى بن يعلى بن الحارث المحاربي يقول: أمرنا زائدة أن نترك حديث الحجاج بن أرطاة. حدثنا عبد الله بن محمد بن سعدويه المروزي قال:','803','266','0');
INSERT INTO [t13041] VALUES (353,'حجاج بن أبي زينب أبو يوسف الصيقل واسطي حدثنا عبد الله بن أحمد بن حنبل قال: سألت أبي عن حجاج بن أبي زينب الواسطي فقال: أخشى أن يكون ضعيف الحديث، حدث عنه هشيم، ومحمد بن يزيد. حدثنا أحمد بن علي الأبار قال: حدثنا الحسن بن شجاع البلخي قال: سألت علي بن','813','266','0');
INSERT INTO [t13041] VALUES (354,'حجاج بن فروخ واسطي حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد بن حاتم قال: سمعت ابن معين يقول: حجاج بن فروخ ليس بشيء','815','266','0');
INSERT INTO [t13041] VALUES (355,'حجاج بن تميم جزري عن ميمون بن مهران روى عنه أحاديث لا يتابع على شيء منها','817','266','0');
INSERT INTO [t13041] VALUES (356,'حجاج بن نصير الفساطيطي بصري حدثنا محمد بن أحمد بن حماد قال: حدثنا معاوية بن صالح: قال سمعت يحيى بن معين قال: حجاج بن نصير الفساطيطي ضعيف. حدثني آدم بن موسى قال: سمعت البخاري قال: الحجاج بن نصير أبو محمد الفساطيطي البصري عن شعبة سكتوا عنه. حدثنا','820','266','0');
INSERT INTO [t13041] VALUES (357,'حجاج بن دينار الواسطي حدثنا عبد الله بن أحمد بن حنبل قال: سألت يحيى بن معين عن حجاج بن دينار فقال: واسطي، وقال بيده يحركها، قلت ليحيى: قد حدث عنه شعبة؟ قال: نعم','823','266','0');
INSERT INTO [t13041] VALUES (358,'حمران بن أعين أخو عبد الملك كوفي حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي بن المديني قال: سمعت سفيان يقول: كانوا ثلاثة إخوة: عبد الملك بن أعين، وحمران بن أعين وزرارة بن أعين كانوا شيعة وكان أشدهم في هذا الأمر حمران بن أعين. حدثنا','825','266','0');
INSERT INTO [t13041] VALUES (359,'حريث بن أبي حريث سمع ابن عمر حدثنا آدم بن موسى قال: سمعت البخاري قال: حريث بن أبي حريث سمع ابن عمر وزيد بن حارثة وأبا إدريس، وقبيصة بن ذؤيب روى عنه يونس بن حابس في الصرف، قاله أبو المغيرة عن الأوزاعي. . . لا يتابع عليه','826','266','0');
INSERT INTO [t13041] VALUES (360,'حريث بن أبي مطر كوفي حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: لم أسمع يحيى ولا عبد الرحمن يحدثان عن حريث بن أبي مطر بشيء قط. حدثني آدم بن موسى قال: سمعت البخاري قال: حريث بن أبي مطر ويقال: ابن عمر ليس بقوي','827','266','0');
INSERT INTO [t13041] VALUES (361,'حريث بن السائب عن الحسن ولا يتابع على حديثه','828','266','0');
INSERT INTO [t13041] VALUES (362,'حنش بن المعتمر أبو المعتمر كوفي حدثني آدم بن موسى قال: سمعت البخاري قال: حنش بن المعتمر أبو المعتمر الكناني وقال بعضهم: حنش بن ربيعة سمع عليا روى عنه سماك بن حرب، والحكم بن عتيبة يتكلمون في حديثه','830','266','0');
INSERT INTO [t13041] VALUES (363,'حارثة بن أبي الرجال مدني حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين يقول: حارثة بن أبي الرجال ضعيف، وقال في موضع آخر: حارثة ليس بثقة. حدثني آدم قال: سمعت البخاري قال: حارثة بن أبي الرجال اسم أبي الرجال محمد بن عبد الرحمن','831','266','0');
INSERT INTO [t13041] VALUES (364,'حنظلة بن عبيد الله السدوسي ويقال: ابن أبي صفية حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد حدثنا علي بن عبد الله قال: سمعت يحيى وذكر حنظلة السدوسي فقال: رأيته وتركته على عمد، قلت ليحيى كان قد اختلط؟ قال: نعم. حدثنا محمد بن عبد الرحمن قال: حدثنا','834','266','0');
INSERT INTO [t13041] VALUES (365,'حمزة بن نجيح بصري حدثني آدم بن موسى قال: سمعت البخاري قال: قال موسى بن إسماعيل: كان حمزة بن نجيح معتزليا','838','266','0');
INSERT INTO [t13041] VALUES (366,'حمزة بن أبي حمزة النصيبي، وهو حمزة بن ميمون حدثنا محمد بن عيسى قال: حدثنا عباس قال: سألت يحيى عن حمزة النصيبي فقال: ليس بشيء. وحدثنا في موضع آخر قال: حدثنا عباس قال: سمعت يحيى قال: حمزة بن أبي حمزة الجزري ليس يسوى فلسا. حدثني آدم قال: سمعت','839','266','0');
INSERT INTO [t13041] VALUES (367,'حمزة بن إسماعيل','841','266','0');
INSERT INTO [t13041] VALUES (368,'حمزة أبو عمر العائذي حدثني أحمد بن محمود حدثنا عثمان بن سعيد قال: سألت يحيى فقلت: عوف عن حمزة أبي عمر من حمزة؟ قال: شيخ لا يعرف','843','266','0');
INSERT INTO [t13041] VALUES (369,'حمزة بن واصل المنقري بصري عن قتادة مجهول في الرواية وحديثه غير محفوظ من حديث قتادة. حدثنا محمد بن إسماعيل قال: حدثنا إبراهيم بن يعقوب قال: حدثنا محمد بن سعيد القرشي قال: حدثنا حمزة بن واصل المنقري وكان يلزم مسجد حماد بن سلمة، وحماد أمرنا أن نكتب عنه','845','266','0');
INSERT INTO [t13041] VALUES (370,'حبان بن علي العنزي أخو مندل كوفي حدثنا عبد الله بن أحمد قال: قلت لأبي: حبان أخو مندل قال: هو أصلح منه يعني: مندلا وقال: ما أقربهما. حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن يحدث عن حبان بن علي. حدثنا محمد بن عيسى قال','848','266','0');
INSERT INTO [t13041] VALUES (371,'حرب بن شداد حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: كان يحيى لا يحدث عن حرب بن شداد وكان عبد الرحمن يحدث عنه. وحدثني زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت يحيى حدث عن حرب بن شداد وكان عبد الرحمن يحدث عنه','849','266','0');
INSERT INTO [t13041] VALUES (372,'حرب بن ميمون الأنصاري أبو الخطاب مولى النضر بن أنس عن أنس حدثنا عبد الرحمن بن الفضل قال: حدثنا محمد بن إسماعيل قال: قال لي محمد بن عقبة: كان مجتهدا يعني: حرب بن ميمون الأنصاري، وقال سليمان بن حرب: هو أكذب الخلق','850','266','0');
INSERT INTO [t13041] VALUES (373,'حرب بن سريج المنقري حدثني آدم قال: سمعت البخاري قال: حرب بن سريج المنقري التميمي أبو سفيان قال البخاري: فيه نظر','851','266','0');
INSERT INTO [t13041] VALUES (374,'حرب بن أبي العالية أبو معاذ حدثنا عبد الله بن أحمد قال: سألت أبي عن حرب بن أبي العالية فقال: روى عنه هشيم ما أدري له أحاديث، كأنه ضعفه. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: حرب بن أبي العالية ثقة','852','266','0');
INSERT INTO [t13041] VALUES (375,'حرب أبو رجاء حدثني آدم قال: سمعت البخاري يقول: حرب أبو رجاء روى محمد بن حجاج عن خالد بن حميد عن سلام عن حرب: إسناد لا يعرف','853','266','0');
INSERT INTO [t13041] VALUES (376,'حبة العرني كوفي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: قد رأى الشعبي رشيدا الهجري وحبة العرني والأصبغ بن نباتة وليس يسوى هؤلاء كلهم شيئا. حدثنا محمد قال: حدثنا عباس في موضع آخر قال: سمعت يحيى قال: حبة العرني لا يكتب حديثه','854','266','0');
INSERT INTO [t13041] VALUES (377,'حديج بن معاوية الجعفي أخو زهير كوفي حدثنا أحمد بن علي الأبار قال: حدثنا محمد بن يحيى قال: قال أبو الوليد الطيالسي: كان زهير بن معاوية لا يحتج بحديث أخيه حديج بن معاوية. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: حديج بن معاوية ليس','855','266','0');
INSERT INTO [t13041] VALUES (378,'حريش بن الخريت أخو زبير بن الخريت بصري حدثني آدم قال: سمعت البخاري قال: حريش بن الخريت أخو الزبير عن ابن أبي مليكة فيه نظر.','857','266','0');
INSERT INTO [t13041] VALUES (379,'حشرج بن نباتة عن سعيد بن جمهان كوفي','859','266','0');
INSERT INTO [t13041] VALUES (380,'الحضرمي روى عنه سليمان التيمي حدثنا عبد الله بن أحمد قال: سألت أبي عن الحضرمي الذي حدث عنه سليمان التيمي قال: كان قاصا فزعم معتمر قال: رأيته، قال أبي: لا أعلم يروي عنه غير سليمان التيمي','862','266','0');
INSERT INTO [t13041] VALUES (381,'حاجب عن جابر بن زيد حدثنا بشر بن موسى قال: حدثنا الحميدي قال: حدثنا سفيان قال: سمعت حاجب الأزدي وكان رأسا في الإباضية','863','266','0');
INSERT INTO [t13041] VALUES (382,'حوشب بن عقيل أبو دحية بصري حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: سمعت علي بن عبد الله قال: سمعت عبد الرحمن بن مهدي يقول: حدثنا حوشب بن عقيل بكتاب عن سعيد بن عبد الله بن جروة قال عبد الرحمن: فلا أعلمه إلا كان يقول حدثنا، ثم قال بعد هذا:','864','266','0');
INSERT INTO [t13041] VALUES (383,'حميضة بن الشمردل كوفي','866','266','0');
INSERT INTO [t13041] VALUES (384,'حسام بن مصك بصري حدثنا عبد الله بن أحمد بن سعدويه المروزي قال: حدثنا أحمد بن عبد الله بن بشير قال: حدثنا سفيان بن عبد الملك قال: سمعت ابن المبارك يقول: حسام بن المصك ارم به. حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: كان عبد الرحمن لا يحدث عن','870','266','0');
INSERT INTO [t13041] VALUES (385,'حماد بن أبي سليمان واسم أبي سليمان مسلم مولى أبي موسى الأشعري كوفي','873','266','0');
INSERT INTO [t13041] VALUES (386,'حماد بن عمرو النصيبي حدثنا أحمد بن علي الأبار قال: سألت مجاهد بن موسى، عن حماد بن عمرو النصيبي فقال: ذهبت إليه وكان يروي عن زيد بن رفيع، عن عبد الله فقلت له: أخرج إلي كتاب خصيف فأخرج إلي كتاب حصين، فإذا هو ليس يفصل بين خصيف وحصين. حدثنا آدم بن','881','266','0');
INSERT INTO [t13041] VALUES (387,'حماد بن أبي حميد ويقال: محمد بن أبي حميد، ويقال: حميد بن أبي حميد أبو إبراهيم الزرقي الأنصاري المديني. حدثنا آدم قال: سمعت البخاري قال: حماد بن أبي حميد ويقال: محمد بن أبي حميد أبو إبراهيم الأنصاري الزرقي المديني منكر الحديث','883','266','0');
INSERT INTO [t13041] VALUES (388,'حماد بن الأبح أبو بكر بصري','886','266','0');
INSERT INTO [t13041] VALUES (389,'حماد بن الجعد بصري حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: حدثنا عبد الرحمن بن مهدي عن أبي داود عن حماد بن الجعد فقال: سبحان الله يحدث عن حماد بن الجعد ولا يحدث عن بحر وعثمان البري وأبي جزي والحسن بن دينار وهؤلاء أصحاب الحديث، ثم قال: كان','888','266','0');
INSERT INTO [t13041] VALUES (390,'حماد بن سعيد البراء في حديثه وهم','889','266','0');
INSERT INTO [t13041] VALUES (391,'حماد بن شعيب أبو شعيب الحماني حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين قال: حماد بن شعيب أبو شعيب الحماني ليس بشيء وقال في موضع آخر: حماد بن شعيب ضعيف. حدثني آدم بن موسى قال: سمعت البخاري قال: حماد بن شعيب الحماني يعد في الكوفيين','893','266','0');
INSERT INTO [t13041] VALUES (392,'حماد بن واقد الصفار بصري يخالف في حديثه حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين يقول: أبو عمر الصفار ضعيف','895','266','0');
INSERT INTO [t13041] VALUES (393,'حماد بن عبيد الكوفي حدثني آدم بن موسى قال: سمعت البخاري قال: حماد بن عبيد الله عن جابر ولم يصح حديثه','897','266','0');
INSERT INTO [t13041] VALUES (394,'حماد بن محمد الفزاري عن أيوب عن عتبة ولم يصح حديثه لا يعرف إلا به','899','266','0');
INSERT INTO [t13041] VALUES (395,'حصين بن عبد الرحمن السلمي أبو الهذيل كوفي حدثنا عبد الله بن أحمد بن حنبل قال: حدثني أبي قال: سمعت يزيد بن هارون قال: طلبت الحديث وحصين حي، كان يقرأ عليه، وكان نسيا. حدثنا محمد قال: حدثنا الحسن قلت لعلي: حصين؟ قال: حصين حديثه واحد وهو صحيح، قلت','901','266','0');
INSERT INTO [t13041] VALUES (396,'حصين بن عمر الأحمسي عن إسماعيل بن أبي خالد ومخارق كوفي. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين قال: حصين بن عمر ليس بشيء. حدثني آدم قال: سمعت البخاري قال: حصين بن عمر الأحمسي منكر الحديث، ضعفه أحمد','902','266','0');
INSERT INTO [t13041] VALUES (397,'حصين بن يزيد الثعلبي كوفي حدثنا آدم بن موسى قال: سمعت محمد بن إسماعيل البخاري يقول: حصين بن يزيد الثعلبي فيه نظر، ومن حديثه ما','904','266','0');
INSERT INTO [t13041] VALUES (398,'حصين والد داود بن الحصين مديني حدثني آدم بن موسى قال: سمعت البخاري قال: حصين والد داود بن الحصين أراه مولى عثمان بن عفان عن أبي رافع روى عنه ابنه، حديثه ليس بالقائم. حدثنا عبد الرحمن عن البخاري في الكتاب الكبير قال: حصين والد داود بن حصين في حديثه','906','266','0');
INSERT INTO [t13041] VALUES (399,'حكيم بن جبير الأسدي كوفي حدثنا عبد الله بن أحمد قال: حدثني أبي قال: حدثني يحيى بن آدم قال: قال سفيان لعبد الله بن عثمان صاحب شعبة: أبو بسطام يحدث عن حكيم بن جبير وكان سفيان يضعفه فقال عبد الله: لا. حدثنا محمد بن سعد قال: حدثنا أبو بكر الأعين قال:','907','266','0');
INSERT INTO [t13041] VALUES (400,'حكيم بن خذام أبو سمير كوفي حدثنا آدم قال: سمعت البخاري قال: حكيم بن خذام أبو سمير كان يرى القدر منكر الحديث','908','266','0');
INSERT INTO [t13041] VALUES (401,'حكيم الأثرم عن أبي تميمة الهجيمي حدثني آدم بن موسى قال: سمعت البخاري قال: حكيم عن أبي تميمة الهجيمي عن أبي هريرة قال البخاري: لم يتابع عليه ولا يعرف لأبي تميمة سماعا من أبي هريرة','910','266','0');
INSERT INTO [t13041] VALUES (402,'حبان بن يسار أبو روح الكلابي بصري. حدثنا آدم بن موسى قال: سمعت البخاري قال: حبان بن يسار أبو روح الكلابي ويقال: السلولي قال البخاري: قال لي الصلت بن محمد: رأيت حبان آخر عمره فذكر منه الاختلاط','912','266','0');
INSERT INTO [t13041] VALUES (403,'حيان بن عبيد الله أبو زهير بصري','914','266','0');
INSERT INTO [t13041] VALUES (404,'حيي بن عبد الله المعافري البصري حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: حيي ودراج وزبان هؤلاء الثلاثة أحاديثهم مناكير','916','266','0');
INSERT INTO [t13041] VALUES (405,'حوط عن زيد بن أرقم كوفي حدثني آدم قال: سمعت البخاري قال: حدثنا حوط عن زيد بن أرقم في ليلة القدر، قال البخاري: رواه المسعودي عن حوط منكر الحديث، لا يتابع عليه','918','266','0');
INSERT INTO [t13041] VALUES (406,'حرام بن عثمان المديني حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: حدثنا يحيى بن سعيد قال: قلت لحرام بن عثمان: عبد الرحمن بن جابر ومحمد بن جابر وأبو عثمان، واحد؟ قال: إن شئت جعلتهم عشرة. حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال:','920','266','0');
INSERT INTO [t13041] VALUES (407,'حريز بن عثمان الرحبي الحمصي حدثنا آدم بن موسى قال: حدثنا محمد بن إسماعيل البخاري قال: قال أبو اليمان: كان حريز بن عثمان يتناول من رجلي ثم ترك ذاك. حدثنا محمد بن أيوب بن يحيى بن ضريس قال: حدثنا يحيى بن المغيرة قال: ذكر جرير أن حريزا كان يشتم عليا','921','266','0');
INSERT INTO [t13041] VALUES (408,'حرملة بن يحيى المصري حدثنا الهيثم بن خلف قال: حدثنا عباس بن محمد قال: سمعت يحيى يقول: شيخ بمصر يقال له: حرملة فكان أعلم الناس بابن وهب فذكر عنه أشياء سمجة كرهت ذكرها، قال: وقد كان حرملة هذا بمصر حين دخلتها','922','266','0');
INSERT INTO [t13041] VALUES (409,'باب الخاء','923','0','0');
INSERT INTO [t13041] VALUES (410,'خالد بن أنس عن أنس بن مالك لا يعرف إلا بهذا، وعاصم بن سعيد مجهول بالنقل أيضا','924','409','0');
INSERT INTO [t13041] VALUES (411,'خالد بن إلياس العدوي مديني حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين قال: خالد بن إلياس ليس بشيء. حدثني آدم بن موسى قال: سمعت البخاري قال: خالد بن إلياس العدوي مديني ليس بشيء منكر الحديث','926','409','0');
INSERT INTO [t13041] VALUES (412,'خالد بن برد العجلي بصري في حديثه اضطراب مضطرب الحديث','928','409','0');
INSERT INTO [t13041] VALUES (413,'خالد بن مهران أبو المنازل الحذاء بصري حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: حدثنا يحيى بن آدم قال: حدثنا عبد الله بن نافع القرشي - مولى لآل عمر - أبو شهاب قال: قال لي شعبة: عليك بحجاج بن أرطاة، ومحمد بن إسحاق، فإنهما حافظان، واكتم','930','409','0');
INSERT INTO [t13041] VALUES (414,'خالد بن رباح الهذلي بصري حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي بن عبد الله قال: سمعت يحيى يقول: كان خالد بن رباح صاحب عربية، وكان ثبتا فأفسدوه بالقدر. حدثني آدم بن موسى قال: سمعت البخاري قال: " خالد بن رباح الهذلي سمع أبا','931','409','0');
INSERT INTO [t13041] VALUES (415,'خالد بن سلمة الفأفاء المخزومي حدثنا أحمد بن علي الأبار قال: حدثنا محمد بن حميد قال: حدثنا جرير قال: كان خالد بن سلمة الفأفاء رأسا في المرجئة، وكان يبغض عليا','932','409','0');
INSERT INTO [t13041] VALUES (416,'خالد بن شوذب بصري حدثنا آدم بن موسى قال: سمعت البخاري قال: خالد بن شوذب بصري فيه نظر. حدثنا عبد الله بن أحمد بن حنبل قال: حدثنا محمد بن أبي بكر المقدمي قال: قلت لخالد بن شوذب: ما لك لا تحدث عن الحسن، كما يحدث عنه يونس؟ قال: " ما جالس يونس الحسن','933','409','0');
INSERT INTO [t13041] VALUES (417,'خالد بن شريك عن عرباض بن سارية ولا يثبت سماعه منه، لا يتابع عليه، وليس يحفظ له غيره','934','409','0');
INSERT INTO [t13041] VALUES (418,'خالد بن سعيد المديني عن أبي حازم، ولا يتابع على حديثه','936','409','0');
INSERT INTO [t13041] VALUES (419,'خالد بن عبد الرحمن بن بكير يخالف في حديثه، بصري','938','409','0');
INSERT INTO [t13041] VALUES (420,'خالد بن عبد الرحمن المخزومي مكي حدثني آدم بن موسى قال: سمعت البخاري قال: خالد بن عبد الرحمن المخزومي مكي ذاهب','941','409','0');
INSERT INTO [t13041] VALUES (421,'خالد بن عبد الرحمن أبو الهيثم عن سماك، ليس بمعروف بالنقل، حديثه غير محفوظ ولا يعرف له أصل يعرف بالعسقلاني.','944','409','0');
INSERT INTO [t13041] VALUES (422,'خالد بن عبد الرحمن الخراساني في حفظه شيء','946','409','0');
INSERT INTO [t13041] VALUES (423,'خالد بن عبيد أبو عصام المروزي لا يتابع على حديثه','949','409','0');
INSERT INTO [t13041] VALUES (424,'خالد بن عمرو الأموي حدثنا عبد الله بن أحمد قال: سألت أبي عن خالد بن عمرو القرشي، فقال: " ليس بثقة، يروي أحاديث بواطيل. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين يقول: خالد بن عمرو السعيدي ليس حديثه بشيء. قال عبد الله بن','951','409','0');
INSERT INTO [t13041] VALUES (425,'خالد بن أبي طريف الصنعاني حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سمعت هشام بن يوسف سئل عن خالد بن أبي طريف، شيخ من أهل صنعاء روى عن وهب بن منبه، فضعفه','953','409','0');
INSERT INTO [t13041] VALUES (426,'خالد بن طهمان أبو العلاء الخفاف حدثنا أحمد بن محمود الهروي، حدثنا عثمان بن سعيد قال: سألت يحيى بن معين عن أبي العلاء الخفاف، فقال: ضعيف','954','409','0');
INSERT INTO [t13041] VALUES (427,'خالد بن كيسان عن الربيع في حديثه نظر','955','409','0');
INSERT INTO [t13041] VALUES (428,'خالد العبد بصري، كان يرى القدر. حدثنا زكريا بن يحيى قال: حدثنا يحيى بن الفضل الخرقي قال: حدثنا الأصمعي قال: " رأيت أبا جزي جاء بخالد العبد يقوده إلى مبارك بن فضالة فقال: أسألك بالله، هل رأيت هذا عند الحسن قط؟ فقال: " لا "','957','409','0');
INSERT INTO [t13041] VALUES (429,'خالد بن القاسم أبو الهيثم المدائني حدثنا أحمد بن علي الأبار قال: حدثنا مؤمل بن إهاب قال: سمعت يحيى بن حسان يقول: " جاء المدائني فلزق أحاديث الليث بن سعد، إذا كان عن الزهري، عن ابن عمر، أدخل سالما، وإذا كان عن الزهري، عن عائشة، أدخل عروة " قلت له','959','409','0');
INSERT INTO [t13041] VALUES (430,'خالد بن كلاب مجهول بالنقل، حديثه غير محفوظ لا أصل له','960','409','0');
INSERT INTO [t13041] VALUES (431,'خالد بن محمد بن زهير بن أبي أمية بن المغيرة المخزومي حدثنا آدم بن موسى قال: سمعت البخاري قال: حدثنا خالد بن محمد بن زهير بن أبي أمية المخزومي، عن صالح بن أبي الأخضر: قال البخاري: ولم يقم حديثه','962','409','0');
INSERT INTO [t13041] VALUES (432,'خالد بن محمد بن خالد بن الزبير عن علي بن حسين، لا يتابع على حديثه','964','409','0');
INSERT INTO [t13041] VALUES (433,'خالد بن محمد أبو الرحال الأنصاري بصري حدثني آدم قال: سمعت البخاري قال: خالد بن محمد أبو الرحال الأنصاري، عن النضر بن أنس، منكر الحديث. ومن حديثه ما','966','409','0');
INSERT INTO [t13041] VALUES (434,'خالد بن محدوج الواسطي حدثنا محمد بن إسماعيل، وأحمد بن علي الأبار، قالا: حدثنا الحسن بن علي قال: سمعت يزيد بن هارون يقول: حلفت أن لا أروي عن خالد بن محدوج، وكان يرميه بالكذب','968','409','0');
INSERT INTO [t13041] VALUES (435,'خالد بن مخلد القطواني كوفي حدثنا عبد الله بن أحمد قال: سألت أبي عن خالد بن مخلد القطواني، فقال: له أحاديث مناكير','969','409','0');
INSERT INTO [t13041] VALUES (436,'خالد بن يزيد القسري لا يتابع على حديثه','970','409','0');
INSERT INTO [t13041] VALUES (437,'خالد بن يزيد بن مسلم الغنوي بصري الغالب على حديثه الوهم','972','409','0');
INSERT INTO [t13041] VALUES (438,'خالد بن يزيد بن أبي مالك الدمشقي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين قال: خالد بن يزيد بن أبي مالك ضعيف','975','409','0');
INSERT INTO [t13041] VALUES (439,'خالد بن يزيد اللؤلؤي لا يتابع على كثير من حديثه','976','409','0');
INSERT INTO [t13041] VALUES (440,'خالد بن يزيد العمري الحذاء مولى لهم','978','409','0');
INSERT INTO [t13041] VALUES (441,'خلاد بن عطاء مولى قريش حدثنا آدم بن موسى قال: سمعت البخاري قال: خلاد بن عطاء مولى قريش منكر الحديث، ولم يصح حديثه','980','409','0');
INSERT INTO [t13041] VALUES (442,'خلاد بن بزيع صاحب المحامل ولا يتابع على حديثه','982','409','0');
INSERT INTO [t13041] VALUES (443,'خلاد بن عيسى مجهول بالنقل حديثه غير محفوظ','984','409','0');
INSERT INTO [t13041] VALUES (444,'خليد بن دعلج شامي حدثنا عبد الله بن أحمد قال: سئل أحمد عن خليد بن دعلج، فقال: ضعيف الحديث. حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى قال: خليد بن دعلج ليس بشيء ". حدثنا أحمد بن محمود قال: حدثنا عثمان بن سعيد: قلت ليحيى: خليد بن','986','409','0');
INSERT INTO [t13041] VALUES (445,'خليل بن مرة روى عنه الليث بن سعد وغيره. حدثنا آدم بن موسى قال: سمعت البخاري قال: خليل بن مرة روى عنه الليث بن سعد قال قتيبة: " فيه نظر "','987','409','0');
INSERT INTO [t13041] VALUES (446,'خليل بن عمر بن إبراهيم يخالف في بعض أحاديثه','988','409','0');
INSERT INTO [t13041] VALUES (447,'خليل بن زكريا بصري يحدث بالبواطيل عن الثقات','990','409','0');
INSERT INTO [t13041] VALUES (448,'خليفة بن قيس مولى خالد بن عرفطة. حدثني آدم بن موسى قال: سمعت البخاري قال: خليفة بن قيس مولى عرفطة، قال البخاري: " يعد في الكوفيين، لم يصح حديثه روى عنه عبد الرحمن بن إسحاق','993','409','0');
INSERT INTO [t13041] VALUES (449,'خليفة بن حميد بصري مجهول في النقل، حديثه غير محفوظ','995','409','0');
INSERT INTO [t13041] VALUES (450,'خليفة بن خياط البصري يعرف بشباب العصفري بصري. حدثني زكريا بن يحيى الساجي قال: حدثنا الحسن بن يحيى الأرزي قال: سمعت علي بن المديني، يقول في دار عبد الرحمن بن عمرو بن جبلة: وشباب بن خياط شجر يحمل الحديث "','997','409','0');
INSERT INTO [t13041] VALUES (451,'خلف بن مبارك كوفي لا يتابع على حديثه من وجه يثبت، وهو مجهول بالنقل','998','409','0');
INSERT INTO [t13041] VALUES (452,'خلف بن خليفة الأشجعي مولى لهم واسطي. حدثني عبد الله بن أحمد بن حنبل قال: حدثنا عبد الله بن سندل الختلي قال: حدثنا خلف بن خليفة قال: " رأيت عمرو بن حريث صاحب النبي عليه السلام وأنا ابن ست سنين ". حدثنا عبد الله بن أحمد بن حنبل قال: سمعت أبي يقول:','1000','409','0');
INSERT INTO [t13041] VALUES (453,'خلف بن ياسين بن معاذ الزيات عن المغيرة بن سعيد، كلاهما مجهولان بالنقل، والحديث غير محفوظ','1001','409','0');
INSERT INTO [t13041] VALUES (454,'خلف بن أيوب العامري بلخي','1003','409','0');
INSERT INTO [t13041] VALUES (455,'خطاب بن عمير الثوري عن الحسن، ولا يتابع على حديثه، ولا يعرف إلا بهذا الحديث','1006','409','0');
INSERT INTO [t13041] VALUES (456,'خطاب بن عمر الهمداني ولا يتابع عليه في حديثه، ولا يعرف إلا به','1008','409','0');
INSERT INTO [t13041] VALUES (457,'خارجة بن مصعب بن الحجاج الخراساني حدثني آدم بن موسى قال: سمعت البخاري قال: خارجة بن مصعب تركه وكيع، كان يدلس عن غياث بن إبراهيم، ولا يعرف صحيح حديثه. حدثنا عبد الله بن أحمد قال: نهاني أبي أن أكتب من حديث خارجة بن مصعب شيئا. حدثنا محمد بن عيسى قال','1010','409','0');
INSERT INTO [t13041] VALUES (458,'خثيم بن مروان السلمي حدثني آدم بن موسى قال: سمعت البخاري قال: خثيم بن مروان السلمي، عن عمر لا يتابع عليه','1011','409','0');
INSERT INTO [t13041] VALUES (459,'خازم بن خزيمة البصري من تيم الرباب يخالف في حديثه','1013','409','0');
INSERT INTO [t13041] VALUES (460,'خلاس بن عمرو حدثنا عبد الله بن أحمد بن حنبل، وأحمد بن علي، قالا: حدثنا عقبة بن مكرم قال: حدثنا الوليد بن خالد بن صخر أبو العباس الأعرابي، عن شعبة قال: قال لي أيوب: لا ترو عن خلاس فإنه صحفي، ثم قال: إني أراه صحفيا. حدثنا محمد بن سعيد بن بلج قال','1015','409','0');
INSERT INTO [t13041] VALUES (461,'خيثمة البصري عن الحسن. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين يقول: خيثمة البصري ليس بشيء','1016','409','0');
INSERT INTO [t13041] VALUES (462,'الخصيب بن جحدر بصري أحاديثه مناكير لا أصل لها','1018','409','0');
INSERT INTO [t13041] VALUES (463,'خضر بن جميل مجهول بالنقل، عن حفص بن عبد الرحمن، مجهول أيضا، عن عاصم الأحول، حديثهم والحديث غير محفوظ إلا من وجه لين.','1020','409','0');
INSERT INTO [t13041] VALUES (464,'خصيف الجزري حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: قلت ليحيى: أيما أعجب إليك: خصيف، عن مجاهد، عن ابن عباس: " الحج عرفة " أو قتادة، عن زرارة، عن ابن عباس؟ قال: قتادة، عن زرارة. قلت ليحيى: سمع زرارة من ابن عباس؟ قال','1022','409','0');
INSERT INTO [t13041] VALUES (465,'باب الدال','1023','0','0');
INSERT INTO [t13041] VALUES (466,'داود بن أبي صالح مديني، عن نافع، لا يتابع عليه، ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: داود بن أبي صالح، عن نافع، ولا يتابع عليه','1024','465','0');
INSERT INTO [t13041] VALUES (467,'داود بن عبد الجبار أبو سليمان الكوفي حدثنا محمد بن عيسى الهاشمي قال: حدثنا عباس قال: سمعت يحيى يقول: داود بن عبد الجبار ليس بثقة وقال في موضع آخر: داود بن عبد الجبار كان ينزل عند باب الطاق وقد رأيته كان يكذب. وحدثني آدم بن موسى قال: سمعت البخاري','1026','465','0');
INSERT INTO [t13041] VALUES (468,'داود بن الزبرقان حدثنا محمد بن عبد الحميد السهمي قال: حدثنا أحمد بن محمد الحضرمي قال: سألت يحيى بن معين عن داود بن الزبرقان، فقال: ليس بشيء. حدثنا محمد بن عيسى قال: حدثنا العباس قال: سمعت يحيى يقول: داود بن الزبرقان ليس بشيء','1029','465','0');
INSERT INTO [t13041] VALUES (469,'داود بن عطاء المديني حدثنا عبد الله بن أحمد قال: سألت أبي عن داود بن عطاء، شيخ من أهل المدينة قال: قد رأيته ليس حديثه بشيء. حدثنا عبد الله قال: سمعت عبد الله بن محمد بن إسحاق الأذرمي، وسأله أبي مرة أخرى عن داود بن عطاء، فقال: لا يحدث عنه، ليس','1030','465','0');
INSERT INTO [t13041] VALUES (470,'داود بن محبر بن قحذم البكراوي حدثنا عبد الله بن أحمد قال: سألت أبي عن داود بن المحبر، فضحك وقال شبه لا شيء، كان يدري ذاك إيش الحديث. حدثنا آدم بن موسى قال: سمعت البخاري قال: داود بن محبر منكر الحديث. حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال','1032','465','0');
INSERT INTO [t13041] VALUES (471,'داود بن حصين مديني حدثنا محمد بن زكريا البلخي قال: حدثنا الحصين بن شجاع قال: سمعت علي بن المديني يقول: مرسل الشعبي وسعيد بن المسيب أحب إلي من داود بن الحصين، عن عكرمة، عن ابن عباس','1033','465','0');
INSERT INTO [t13041] VALUES (472,'داود بن منصور قاضي المصيصة يخالف في حديثه','1034','465','0');
INSERT INTO [t13041] VALUES (473,'داود بن عبد الله الجعفري في حديثه وهم، مديني','1036','465','0');
INSERT INTO [t13041] VALUES (474,'داود بن أبي عوف أبو الجحاف حدثنا بشر بن موسى قال: حدثنا الحميدي قال: حدثنا سفيان قال: حدثنا أبو الجحاف، وكان من الشيعة','1038','465','0');
INSERT INTO [t13041] VALUES (475,'داود بن عبد الحميد الكوفي عن عمرو بن قيس الملائي، بأحاديث لا يتابع عليها','1039','465','0');
INSERT INTO [t13041] VALUES (476,'داود بن عثمان الثغري كان يحدث بمصر، عن الأوزاعي وغيره بالبواطيل','1041','465','0');
INSERT INTO [t13041] VALUES (477,'داود بن عجلان عن أبي عقال. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين يقول: داود بن عجلان مكي، عن أبي عقال، وما أظنه بشيء','1043','465','0');
INSERT INTO [t13041] VALUES (478,'داود الطفاوي بصري، حديثه باطل لا أصل له. حدثنا محمد بن أحمد بن حماد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين يقول: داود الطفاوي الذي روى عنه المقرئ حديث القرآن، ليس بشيء','1045','465','0');
INSERT INTO [t13041] VALUES (479,'داود بن فراهيج مدني حدثنا أحمد بن محمد بن صدقة قال: حدثنا أبو رفاعة عبد الله بن محمد بن عمر بن حبيب البصري قال: حدثنا حجاج بن نصير قال: حدثنا شعبة قال: حدثنا داود بن فراهيج، بعدما كبر وافتقر وافتتن. حدثنا محمد قال: حدثنا صالح بن أحمد، حدثنا علي','1047','465','0');
INSERT INTO [t13041] VALUES (480,'داود بن يزيد الأودي كوفي حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: سمعت محمد بن عبيد يقول: كنت جالسا يوما في المسجد الأعظم، وإنا يومئذ ابن ثمان عشرة سنة قال: فجاء داود بن يزيد الأودي حتى وقف عند أبواب كندة، قال: فجعل ينظر يمينا وشمالا،','1048','465','0');
INSERT INTO [t13041] VALUES (481,'دينار أبو سعيد عقيصا كوفي يقال التيمي كان من الرافضة. حدثنا أحمد بن علي الأبار قال: حدثنا أحمد بن سنان القطان قال: حدثنا علي بن أنس قال: قال أبو بكر بن عياش: نحدثهم عن أبي حصين، ويجيؤونا بأبي سعيد عقيصا ماص بظر أمه يشتم عثمان. حدثنا محمد بن عيسى','1049','465','0');
INSERT INTO [t13041] VALUES (482,'دهثم بن قران كوفي لا يتابع عليه، ولا يعرف إلا به. حدثنا عبد الله بن أحمد قال: سألت أبي عن دهثم بن قران، فقال: كان شيخا ليس به بأس، حدث عنه أبو بكر بن عياش، ثم أخرج كتابا عن يحيى بن أبي كثير، فترك حديثه، متروك الحديث. حدثنا محمد بن عثمان قال:','1050','465','0');
INSERT INTO [t13041] VALUES (483,'دراج أبو السمح بصري حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: دراج أبو السمح أحاديثه مناكير','1052','465','0');
INSERT INTO [t13041] VALUES (484,'دلهم بن صالح عن حجيز، عن عبد الله بن بريدة، كوفي. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: دلهم بن صالح ضعيف','1054','465','0');
INSERT INTO [t13041] VALUES (485,'ديلم بن هوشع أبو وهب الجيشاني حدثني آدم بن موسى قال: سمعت البخاري يقول: ديلم بن هوشع أبو وهب الجيشاني، وجيشان باليمن، سمع الضحاك، روى عنه، يزيد بن أبي حبيب، قال البخاري: في إسناده نظر','1056','465','0');
INSERT INTO [t13041] VALUES (486,'درست بن حمزة البصري حدثني آدم بن موسى قال: سمعت البخاري قال: درست بن حمزة البصري، عن مطر، عن قتادة، عن أنس، عن النبي عليه السلام في المتحابين، قال البخاري: لا يتابع عليه','1058','465','0');
INSERT INTO [t13041] VALUES (487,'دجين بن ثابت أبو الغصن مديني حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي بن عبد الله قال: سمعت عبد الرحمن بن مهدي، وسئل عن دجين بن ثابت الذي يروي عنه، عن أسلم مولى عمر فقال: عبد الرحمن قال لنا مرة: حدثني مولى لعمر بن عبد العزيز،','1060','465','0');
INSERT INTO [t13041] VALUES (488,'درمك بن عمرو عن أبي إسحاق لا يتابع على حديثه، ولا يعرف إلا به، كوفي','1062','465','0');
INSERT INTO [t13041] VALUES (489,'داهر بن يحيى الرازي كان ممن يغلو في الرفض ولا يتابع على حديثه','1064','465','0');
INSERT INTO [t13041] VALUES (490,'باب الذال','1067','0','0');
INSERT INTO [t13041] VALUES (491,'ذواد بن علبة الحارثي حدثنا محمد بن عثمان قال: سمعت يحيى بن معين، وسئل عن ذواد بن علبة، فقال: كان ضعيفا. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين يقول: ذواد بن علبة ليس بشيء. حدثني آدم بن موسى قال: سمعت البخاري يقول: ذواد بن','1068','490','0');
INSERT INTO [t13041] VALUES (492,'باب الراء','1073','0','0');
INSERT INTO [t13041] VALUES (493,'ربيع بن عبد الله بن خطاف حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي بن عبد الله قال: سألت يحيى بن سعيد، عن الربيع بن عبد الله بن خطاف الذي روى عن الحسن، وعن حفص المنقري، قلت ليحيى: إن عبد الرحمن يثني عليه، قال يحيى: أنا أعلم،','1074','492','0');
INSERT INTO [t13041] VALUES (494,'ربيع بن حبيب عن نوفل بن عبد الملك، كوفي. حدثنا عبد الله بن أحمد قال: سألت أبي عن ربيع بن حبيب، فقال: حدث عنه عبيد الله أحاديث مناكير. حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سألت يحيى عن الربيع بن حبيب أبي سلمة، فقال: " تعرف','1075','492','0');
INSERT INTO [t13041] VALUES (495,'ربيع بن مالك عن خولة، روى عنه حجاج بن أرطاة. حدثني آدم بن موسى قال: سمعت البخاري قال: ربيع بن مالك، عن خولة، روى عنه حجاج بن أرطأة، قال البخاري: لم يثبت حديثه','1077','492','0');
INSERT INTO [t13041] VALUES (496,'ربيع بن سهل بن الركين بن الربيع بن عميلة الفزاري كوفي حدثني آدم بن موسى قال: سمعت البخاري قال: ربيع بن سهل بن الربيع بن عميلة الفزاري، عن سعيد بن عبيد، قال البخاري: يخالف في حديثه. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى، يقول: الربيع','1079','492','0');
INSERT INTO [t13041] VALUES (497,'الربيع بن صبيح بصري حدثنا محمد بن عيسى قال: حدثنا نصر بن علي قال: حدثنا بشر بن عمر قال: ذهبت إلى شعبة يوما، فإذا هو يقول: تبلغون عني ما لم أتكلم به؟ من سمعني منكم أقع في الربيع بن صبيح؟ والله لا أحدثكم بحديثه حتى تأتوا الربيع بن صبيح فتكذبوا','1081','492','0');
INSERT INTO [t13041] VALUES (498,'الربيع بن بدر التميمي السعدي ويقال عليلة البصري. حدثنا محمد بن عثمان قال: سئل يحيى وأنا أسمع، عن الربيع بن بدر، فقال: كان ضعيفا. حدثني آدم قال: سمعت البخاري قال: الربيع بن بدر ضعفه قتيبة. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى،','1082','492','0');
INSERT INTO [t13041] VALUES (499,'الربيع بن برة بصري كان يرى القدر ويدعو إليه','1084','492','0');
INSERT INTO [t13041] VALUES (500,'ربيع بن سليمان صاحب لمازة بصري. حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين قال: ربيع بن سليمان صاحب لمازة ليس بشيء','1088','492','0');
INSERT INTO [t13041] VALUES (501,'ربيعة بن النابغة عن أبيه، عن علي، بصري. حدثني آدم بن موسى قال: سمعت البخاري قال: ربيعة بن النابغة، عن أبيه، عن علي، قال البخاري: ولا يصح حديثه','1089','492','0');
INSERT INTO [t13041] VALUES (502,'راشد أبو الكميت حدثني آدم بن موسى قال: سمعت البخاري قال: راشد أبو الكميت رأى ابن عمر، يعرف بحديث واحد. حدثنا الحسن بن علي بن زياد الرازي، حدثنا يحيى بن المغيرة، حدثنا جرير قال: قال أبو الكميت: دخلت على خالد بن عبد الله فقال: حاجتك أبا الكميت؟','1091','492','0');
INSERT INTO [t13041] VALUES (503,'راشد بن معبد الثقفي عن أنس بصري. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين قال: راشد بن معبد ضعيف','1092','492','0');
INSERT INTO [t13041] VALUES (504,'راشد أبو ميسرة العطار ولا يتابع على حديثه','1094','492','0');
INSERT INTO [t13041] VALUES (505,'روح بن غطيف الجزري','1096','492','0');
INSERT INTO [t13041] VALUES (506,'روح بن أسلم أبو حاتم الباهلي بصري حدثني آدم بن موسى قال: سمعت البخاري قال: روح بن أسلم يتكلمون فيه','1099','492','0');
INSERT INTO [t13041] VALUES (507,'روح بن مسافر أبو بشر، كوفي حدثنا عبد الله بن محمد بن سعدويه المروزي قال: حدثنا أحمد بن عبد الله بن بشير المروزي قال: حدثنا سفيان بن عبد الملك قال: سألت عبد الله بن المبارك عن روح بن مسافر: لم تركت حديثه؟ فأثنى عليه خيرا، ثم قال: حدثنا عن علقمة بن','1101','492','0');
INSERT INTO [t13041] VALUES (508,'روح بن عطاء بن أبي ميمونة بصري حدثنا عبد الله بن أحمد بن حنبل قال: سألت يحيى بن معين عن روح بن عطاء بن أبي ميمونة قال: حدث عنه داود، وهو ضعيف الحديث. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: روح بن عطاء بن أبي ميمونة ضعيف','1102','492','0');
INSERT INTO [t13041] VALUES (509,'روح بن عبد الواحد القرشي عن موسى بن أعين، عن ليث بن أبي سليم، ولا يتابع على حديثه، شامي','1104','492','0');
INSERT INTO [t13041] VALUES (510,'روح بن عبادة بن العلاء بن حسان القيسي بصري حدثنا محمد بن يحيى بن الضريس قال: أخبرنا حفص بن عمر المهرقاني قال: سمعت أبا الوليد يقول: أعرف روح بن عبادة منذ أربعين سنة، لم أره عند عالم قط، وكان وراقا. وقال عارم رأيته مرة عند حماد بن زيد. حدثني الحسين','1106','492','0');
INSERT INTO [t13041] VALUES (511,'روح بن جناح، عن الزهري قصة البيت المعمور، لا يتابع عليه، شامي','1107','492','0');
INSERT INTO [t13041] VALUES (512,'رجاء أبو يحيى الحرشي صاحب السقط عن يحيى بن أبي كثير، ولا يتابع عليه، بصري','1109','492','0');
INSERT INTO [t13041] VALUES (513,'رجاء بن الحارث أبو سلام حدثني آدم بن موسى قال: سمعت البخاري قال: رجاء بن الحارث حديثه ليس بالقائم','1111','492','0');
INSERT INTO [t13041] VALUES (514,'رباح بن عبيد الله العمري حدثني آدم بن موسى قال: سمعت البخاري يقول: رباح بن عبيد الله العمري قال البخاري: لم يتابع على حديثه، قال: وقال أحمد: منكر الحديث','1113','492','0');
INSERT INTO [t13041] VALUES (515,'رباح بن أبي معروف عن عطاء، مكي. حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: كان يحيى وعبد الرحمن لا يحدثان عن رباح بن أبي معروف، وكان عبد الرحمن يحدث عنه ثم تركه','1115','492','0');
INSERT INTO [t13041] VALUES (516,'ركين الضبي كوفي حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي بن عبد الله قال: سألت جريرا عن ركين الضبي الذي روى عن سفيان، فقال: قد رأيته، هو ركين بن عبد الأعلى قال: لم يكن ممن يؤخذ عنه الحديث، وكان عريفا، ولم يكن يرتفع بحديثه، وكان','1119','492','0');
INSERT INTO [t13041] VALUES (517,'رشيد الهجري روى عن أبيه، عن عبد الله بن عمرو، كوفي. حدثني آدم قال: سمعت البخاري قال: رشيد الهجري، عن أبيه، يتكلمون في رشيد','1120','492','0');
INSERT INTO [t13041] VALUES (518,'رؤبة بن رؤيبة مجهول بالنقل، ويزيد أبو خالد نحوه، ويونس بن أرقم ضعيف، والحديث غير محفوظ، بصري','1122','492','0');
INSERT INTO [t13041] VALUES (519,'رؤبة بن العجاج الشاعر عن أبيه، ولا يتابع عليه','1124','492','0');
INSERT INTO [t13041] VALUES (520,'رفدة بن قضاعة الغساني شامي، ولا يتابع على حديثه','1126','492','0');
INSERT INTO [t13041] VALUES (521,'رفاعة بن الهرير بن عبد الرحمن بن رافع بن خديج، مديني حدثني آدم بن موسى قال: سمعت البخاري يقول: رفاعة بن الهرير بن عبد الرحمن بن رافع بن خديج الأنصاري المديني، قال البخاري: فيه نظر','1128','492','0');
INSERT INTO [t13041] VALUES (522,'رشدين بن كريب مولى ابن عباس، كوفي. حدثنا عبد الله بن أحمد قال: سألت أبي عن رشدين بن كريب، مولى ابن عباس فكأنه ضعفه. حدثني الخضر بن داود قال: أخبرني أحمد بن محمد قال: قلت لأبي عبد الله: محمد بن كريب، ورشدين بن كريب أخوان؟ قال: نعم، قلت:','1130','492','0');
INSERT INTO [t13041] VALUES (523,'رشدين بن سعد أبو الحجاج المهري البصري حدثنا عبد الله بن أحمد بن حنبل قال: سمعت أبي يقول: رشدين بن سعد كذا وكذا. حدثني محمد بن عبد الرحمن قال: أخبرنا عبد الملك بن عبد الحميد الميموني قال: سمعت أبا عبد الله يقول: رشدين ليس يبالي عمن روى، لكنه رجل','1131','492','0');
INSERT INTO [t13041] VALUES (524,'رزق الله بن سلام الطبري عن ابن عيينة، ولا يتابع على حديثه','1132','492','0');
INSERT INTO [t13041] VALUES (525,'رزق الله بن الأسود القرشي عن ثابت، حديثه منكر غير محفوظ، بصري','1134','492','0');
INSERT INTO [t13041] VALUES (526,'رزق الله بن موسى في حديثه وهم، بغدادي','1136','492','0');
INSERT INTO [t13041] VALUES (527,'رواد بن الجراح أبو عصام العسقلاني حدثنا عبد الله بن أحمد قال: سألت أبي عن رواد بن الجراح أبو عصام، فقال: لا بأس به، صاحب سنة، إلا أنه حدث عن سفيان بأحاديث مناكير','1139','492','0');
INSERT INTO [t13041] VALUES (528,'رحمة بن مصعب أبو مصعب الواسطي أصله سرخسي. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: رحمة بن مصعب ليس بشيء، هو جرشي سرخسي','1143','492','0');
INSERT INTO [t13041] VALUES (529,'باب الزاي','1145','0','0');
INSERT INTO [t13041] VALUES (530,'زيد بن جبيرة بن محمود بن أبي جبيرة الأنصاري المديني حدثني آدم بن موسى قال: سمعت البخاري قال: زيد بن جبيرة بن محمود بن أبي جبيرة منكر الحديث','1146','529','0');
INSERT INTO [t13041] VALUES (531,'زيد بن عبد الرحمن بن زيد بن أسلم مولى عمر بن الخطاب، مديني , حدثني آدم بن موسى قال: سمعت البخاري قال: زيد بن عبد الرحمن بن زيد بن أسلم منكر الحديث','1149','529','0');
INSERT INTO [t13041] VALUES (532,'زيد أبو عمر عن أنس، بصري. حدثني آدم بن موسى قال: سمعت البخاري قال: زيد أبو عمر، عن أنس، سكتوا عنه','1151','529','0');
INSERT INTO [t13041] VALUES (533,'زيد بن حبان الرقي حدثنا عبد الله بن أحمد قال: سمعت أبي يذكر عن أبي جعفر السودي، عن معمر الرقي قال: أنا سمعت من زيد بن حبان قبل أن يفسد أو يتغير، قال أبي: كان زيد بن حبان يشرب، يعني المسكر، سألت أبي مرة أخرى عن زيد بن حبان الرقي فقال: حدثنا عنه','1153','529','0');
INSERT INTO [t13041] VALUES (534,'زيد بن أبي أنيسة الجزري حدثني الخضر بن داود قال: حدثنا أحمد بن محمد بن هانئ قال: قلت لأبي عبد الله: زيد بن أبي أنيسة، كيف هو عندك؟ فقال: إن حديثه لحسن مقارب، وإن فيها لبعض النكارة، وهو على ذلك حسن الحديث','1155','529','0');
INSERT INTO [t13041] VALUES (535,'زيد العمي، بصري حدثنا عبد الله بن أحمد قال: حدثنا زياد بن أيوب قال: حدثنا علي بن محمد قال: سمعت وكيعا يقول: حديث زيد العمي، عن أبي الصديق الناجي، ليس بشيء. حدثني جعفر بن أحمد قال: حدثنا محمد بن إدريس، عن كتاب أبي الوليد بن أبي الجارود، عن يحيى','1156','529','0');
INSERT INTO [t13041] VALUES (536,'زيد بن عياض أبو عياض، بصري حدثنا محمد بن إبراهيم بن جناد قال: حدثنا ابن عائشة قال: حدثنا سلام بن أبي مطيع قال: حدث رجل أيوب يوما حديثا، فأنكره أيوب، فقال أيوب: من حدثك بهذا؟ قال: محمد بن واسع قال: بخ ثقة، قال: عمن؟ قال: عن زيد بن عياض قال:','1158','529','0');
INSERT INTO [t13041] VALUES (537,'زياد بن بيان الرقي عن علي بن نفيل. حدثني آدم بن موسى قال: سمعت البخاري قال: زياد بن بيان الرقي عن علي بن نفيل، جد النفيلي، قال البخاري: في إسناده نظر','1161','529','0');
INSERT INTO [t13041] VALUES (538,'زياد بن الربيع اليحمدي أبو خداش بصري حدثني آدم بن موسى قال: سمعت البخاري قال: حدثنا زياد بن الربيع اليحمدي أبو خداش، في إسناده نظر','1163','529','0');
INSERT INTO [t13041] VALUES (539,'زياد بن أبي حسان النبطي، واسطي حدثني آدم بن موسى قال: سمعت البخاري يقول: زياد بن أبي حسان النبطي، قال البخاري: كان شعبة يتكلم فيه','1165','529','0');
INSERT INTO [t13041] VALUES (540,'زياد بن مالك عن ابن مسعود، وعلي، كوفي. حدثني آدم قال: سمعت البخاري قال: زياد بن مالك، عن ابن مسعود، وعلي، قال البخاري: لا يعرف سماع زياد من عبد الله وعلي، ولا الحكم منه','1167','529','0');
INSERT INTO [t13041] VALUES (541,'زياد بن ميمون أبو عمار البصري صاحب الفاكهة، عن أنس. حدثني آدم بن موسى قال: سمعت البخاري قال: زياد بن ميمون تركوه. حدثنا عبد الله بن أحمد بن حنبل قال: حدثني أحمد بن إبراهيم قال: سمعت أبا داود الطيالسي قال: أتينا زياد بن ميمون، فسمعته يقول:','1169','529','0');
INSERT INTO [t13041] VALUES (542,'زياد أبو عمر بصري حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: قلت ليحيى: إن عبد الرحمن يثبت شيخين من أهل البصرة، قال: من هما؟ قلت: زياد أبو عمر، فحرك يحيى رأسه فقال: كان يروي حديثين، ثلاثة، ثم جاءت بعد أشياء، وكان شيخا','1170','529','0');
INSERT INTO [t13041] VALUES (543,'زياد بن أبي زياد الجصاص واسطي حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن، يحدث عن زياد الجصاص. حدثنا محمد بن عيسى قال: سمعت عباسا قال: سمعت يحيى يقول: زياد بن أبي زياد الجصاص واسطي، ليس بشيء','1171','529','0');
INSERT INTO [t13041] VALUES (544,'زياد بن عبد الله البكائي كوفي حدثنا أحمد بن علي قال: حدثنا محمد بن يحيى قال: قال علي بن المديني: لا أروي عن زياد بن عبد الله البكائي. حدثنا محمد بن عثمان قال: سألت يحيى بن معين عن زياد بن عبد الله البكائي، فقال: كان زياد بن عبد الله البكائي ضعيفا','1173','529','0');
INSERT INTO [t13041] VALUES (545,'زياد أبو هشام مولى عثمان بن عفان، عن محجن، مديني. حدثني آدم قال: سمعت البخاري قال: زياد أبو هشام مولى عثمان بن عفان، عن محجن، روى عنه ابنه هشام، وحذيفة ليس بالمرضي','1174','529','0');
INSERT INTO [t13041] VALUES (546,'زائدة بن أبي الرقاد أبو معاذ الباهلي بصري، عن زياد النميري. حدثني محمد بن عثمان بن أبي شيبة قال: سمعت علي بن المديني قال: زائدة بن أبي الرقاد روى مناكير. حدثني آدم بن موسى قال: سمعت البخاري قال: زائدة بن أبي الرقاد، عن زياد النميري، منكر الحديث','1177','529','0');
INSERT INTO [t13041] VALUES (547,'زائدة مولى عثمان، سمع سعدا، مدني مجهول بالنقل ولا يتابع على حديثه، ولا يعرف إلا به. حدثني آدم قال: سمعت البخاري قال: زائدة مولى عثمان سمع سعدا، عن النبي صلى الله عليه وسلم. قال البخاري: قال له أبو عفان الأموي المديني عن ابن أبي الزناد وهو حديث','1179','529','0');
INSERT INTO [t13041] VALUES (548,'زبرقان بن عبد الله العبدي أبو الورقاء الكوفي، عن كعب حدثني آدم قال: سمعت البخاري يقول: زبرقان بن عبد الله العبدي أبو الورقاء الكوفي، عن كعب بن عبد الله، قال البخاري: وهم فيه','1181','529','0');
INSERT INTO [t13041] VALUES (549,'زميل بن عباس عن عروة، روى عنه، يزيد بن الهاد مدني. حدثني آدم قال: سمعت البخاري قال: زميل بن عباس، عن عروة، روى عنه، يزيد بن الهاد، قال البخاري: ولا يعرف لزميل سماع من عروة، ولا ليزيد سماع من زميل، فلا تقوم به الحجة','1183','529','0');
INSERT INTO [t13041] VALUES (550,'زربي أبو يحيى مولى هشام بن حسان، سمع أنس بن مالك، بصري. حدثني آدم قال: سمعت البخاري قال: زربي أبو يحيى مؤذن هشام بن حسان سمع أنسا، قال البخاري: فيه نظر','1185','529','0');
INSERT INTO [t13041] VALUES (551,'زكريا بن منظور بن ثعلبة بن أبي مالك القرظي مديني حدثني آدم قال: سمعت البخاري قال: زكريا بن منظور ليس هو عندهم بالقوي، منكر الحديث. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: زكريا بن منظور ليس بشيء، فراجعته فيه مرارا فزعم أنه ليس بشيء','1187','529','0');
INSERT INTO [t13041] VALUES (552,'زكريا بن عطية الحنفي مجهول النقل، عن سعد بن محمد بن المسور، ولا يتابع عليه','1188','529','0');
INSERT INTO [t13041] VALUES (553,'زكريا أبو يحيى الكوفي عن الشعبي، يقال له البدي. حدثني أحمد بن محمد الهروي، حدثنا عثمان بن سعيد قال: سألت يحيى بن معين عن زكريا بن يحيى الكوفي، عن الشعبي: من زكريا هذا؟ قال: ليس بشيء، قلت: ابن من هو؟ قال: ابن يحيى','1190','529','0');
INSERT INTO [t13041] VALUES (554,'زكريا بن يحيى بن الخطاب الطائي عن أبي هلال، ولا يتابع عليه','1191','529','0');
INSERT INTO [t13041] VALUES (555,'زكريا بن يحيى الكسائي كوفي حدثنا عبد الله بن أحمد بن حنبل قال: سألت يحيى بن معين قال: قلت: شيخ بالكوفة يقال له زكريا بن يحيى الكسائي؟ فقال يحيى: رجل سوء، يحدث بأحاديث سوء، قلت ليحيى: إنه قد قال لي أنك كتبت عنه؟ فحول يحيى وجهه إلى القبلة وحلف','1193','529','0');
INSERT INTO [t13041] VALUES (556,'زكريا بن يحيى أبو يحيى الوقاد مصري','1197','529','0');
INSERT INTO [t13041] VALUES (557,'زكريا بن أبي مريم الخزاعي واسطي عن أبي أمامة واسطي. حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سمعت عبد الرحمن بن مهدي، وذكر، زكريا بن أبي مريم الذي روى عنه، هشيم قال: قلنا لشعبة: لقيت زكريا بن أبي مريم سمع من أبي أمامة؟','1199','529','0');
INSERT INTO [t13041] VALUES (558,'زكريا بن حكيم البدي ويقال الحبطي، كوفي. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى، يقول: زكريا بن حكيم البدي كوفي، وليس بثقة، وفي موضع آخر: زكريا بن حكيم الحبطي ليس بشيء، وفي موضع آخر: زكريا البدي يحدث عنه أبو علي الحنفي ليس حديثه بشيء','1201','529','0');
INSERT INTO [t13041] VALUES (559,'زكريا بن أبي عبيدة الناجي عن بهز بن حكيم، حديثه غير محفوظ، لا يتابع عليه ولا يعرف زكريا إلا بهذا الحديث','1203','529','0');
INSERT INTO [t13041] VALUES (560,'الزبير بن سعيد الهاشمي نزل المدائن. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين يقول: الزبير بن سعيد سمع منه، جرير بن حازم، وأبو عاصم النبيل ليس بشيء، وفي موضع آخر: الزبير بن سعيد كان ينزل المدائن، وكان ضعيفا. وحدثنا','1205','529','0');
INSERT INTO [t13041] VALUES (561,'الزبير بن الشعشاع الشني أبو خثرم عن علي، بصري. حدثني آدم قال: سمعت البخاري قال: الزبير بن الشعشاع الشني أبو خثرم، قال البخاري: ولا يصح، لأن عليا روى عن النبي صلى الله عليه وسلم أنه " نهى عن أكل لحوم الحمر الأهلية "','1208','529','0');
INSERT INTO [t13041] VALUES (562,'الزبير بن عيسى الحميدي الأسدي مكي والد عبد الله بن الزبير، عن هشام بن عروة، حديثه غير محفوظ','1210','529','0');
INSERT INTO [t13041] VALUES (563,'زهير بن إسحاق السلولي بصري','1212','529','0');
INSERT INTO [t13041] VALUES (564,'زهير بن محمد أبو المنذر التميمي الخراساني حدثنا محمد بن عبد الرحمن قال: حدثنا عبد الملك بن عبد الحميد الميموني قال: سمعت أحمد بن حنبل قال: زهير بن محمد مقارب الحديث. حدثني أحمد قال: سمعت البخاري قال: زهير بن محمد روى أهل الشام، عنه أحاديث مناكير،','1214','529','0');
INSERT INTO [t13041] VALUES (565,'زهدم بن الحارث الطائي عن بهز بن حكيم، لا يتابع عليه ولا يعرف إلا به، بصري','1216','529','0');
INSERT INTO [t13041] VALUES (566,'زهدم بن الحارث المكي عن حفص بن غياث، ولا يتابع على حديثه','1218','529','0');
INSERT INTO [t13041] VALUES (567,'زيادة بن محمد الأنصاري عن محمد بن كعب القرظي، عن فضالة بن عبيد، مدني. حدثني آدم بن موسى قال: سمعت البخاري قال: زيادة بن محمد الأنصاري، عن محمد بن كعب القرظي، عن فضالة بن عبيد، روى عنه الليث، قال البخاري: منكر الحديث','1220','529','0');
INSERT INTO [t13041] VALUES (568,'زمعة بن صالح المكي أصله من الجند، يماني يروي عن سلمة بن وهرام، وابن طاوس، وهشام بن عروة، والزهري. حدثني آدم بن موسى قال: سمعت البخاري يقول: زمعة بن صالح المكي يروي عن سلمة بن وهرام، وابن طاوس، قال البخاري: يخالف في حديثه، وقال: تركه ابن مهدي','1222','529','0');
INSERT INTO [t13041] VALUES (569,'زاذان أبو عمر الكندي، كوفي حدثنا عبد الله بن أحمد قال: حدثني أبو سعيد الأشج قال: حدثنا ابن إدريس، عن شعبة قال: سألت الحكم وسلمة بن كهيل عن زاذان، فقال الحكم: أكثر، وقال سلمة بن كهيل: أبو البحتري أعجب إلي منه. حدثنا محمد بن عيسى قال: حدثنا عمرو','1223','529','0');
INSERT INTO [t13041] VALUES (570,'زافر بن سليمان أبو سليمان القهستاني كان يكون بالري. حدثني آدم قال: سمعت البخاري قال: زافر بن سليمان أبو سليمان القهستاني كان يكون بالري، عنده مراسيل ووهم','1224','529','0');
INSERT INTO [t13041] VALUES (571,'زبان بن فايد، مصري حدثنا عبد الله بن أحمد قال: سمعت أبي قال: زبان بن فايد أحاديثه مناكير','1226','529','0');
INSERT INTO [t13041] VALUES (572,'زرارة بن أعين كوفي حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سمعت سفيان يقول: وقيل له: روى زرارة بن أعين، عن أبي جعفر، كتابا؟ فقال: سفيان ما رأى هو أبا جعفر، ولكنه كان يتبع حديثه، ثم قال سفيان: كانوا ثلاثة إخوة: عبد','1228','529','0');
INSERT INTO [t13041] VALUES (573,'زنفل العرفي عن ابن أبي مليكة. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سألت يحيى، عن زنفل العرفي، فقال: ليس بشيء','1230','529','0');
INSERT INTO [t13041] VALUES (574,'زفر بن هذيل كوفي صاحب رأي. حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن يحدث عن زفر بن الهذيل شيئا قط. حدثنا محمد بن إسماعيل قال: حدثنا أبو الوليد الطيالسي قال: حدثنا بشر بن السري قال: ترحمت يوما على زفر وأنا مع سفيان','1232','529','0');
INSERT INTO [t13041] VALUES (575,'باب السين','1233','0','0');
INSERT INTO [t13041] VALUES (576,'سعيد بن أنس مجهول في النقل، بصري','1234','575','0');
INSERT INTO [t13041] VALUES (577,'سعيد بن إياس الجريري بصري','1236','575','0');
INSERT INTO [t13041] VALUES (578,'سعيد بن بشير النجاري عن محمد بن عبد الرحمن بن البيلماني. حدثني آدم بن موسى قال: سمعت النجاري قال: سعيد بن بشير النجاري، عن محمد بن عبد الرحمن بن البيلماني، روى عنه الليث بن سعد، قال البخاري: لا يصح حديثه','1238','575','0');
INSERT INTO [t13041] VALUES (579,'سعيد بن بشير مولى بني نصر، عن قتادة. حدثني آدم بن موسى قال: سمعت البخاري قال: سعيد بن بشير مولى بني نصر، عن قتادة، روى عنه الوليد بن مسلم، ومعن، يتكلمون في حفظه. حدثنا أحمد بن علي الأبار قال: حدثنا علي بن ميمون الرقي قال: حدثنا أبو خليد قال:','1240','575','0');
INSERT INTO [t13041] VALUES (580,'سعيد بن بشير المصري مصري، إسناده ليس بالقائم، حدث عنه محمد بن عبد الله بن عبد الحكم، ولا يتابع على حديثه','1242','575','0');
INSERT INTO [t13041] VALUES (581,'سعيد بن أبي بكر بن أبي موسى حديثه منكر غير محفوظ، ولا يعرف إلا بهذا، وعبد الله بن عبد الجبار مجهول بالنقل','1244','575','0');
INSERT INTO [t13041] VALUES (582,'سعيد التمار عن أنس، روى عنه، مروان بن نهيك. حدثني آدم بن موسى قال: سمعت البخاري قال: سعيد التمار، عن أنس، روى عنه مروان بن نهيك، قال البخاري: فيه نظر','1246','575','0');
INSERT INTO [t13041] VALUES (583,'سعيد بن خالد بن أبي طويل شامي، لا يتابع على حديثه','1248','575','0');
INSERT INTO [t13041] VALUES (584,'سعيد بن دينار التمار الدمشقي عن الربيع بن صبيح، لا يتابع على حديثه، وليس بمعروف بالنقل','1250','575','0');
INSERT INTO [t13041] VALUES (585,'سعيد بن داود أبو عثمان الزنبري مدني، ويقال ابن أبي زنبر. حدثنا أحمد بن علي الأبار قال: سألت مجاهد بن موسى عن سعيد بن داود الزنبري قال: سألت عبد الله بن نافع الصائغ فقلت: يا أبا محمد، زعم سعيد بن داود أن المهدي، أمر مالك بن أنس حين أخرج الموطأ يصير','1252','575','0');
INSERT INTO [t13041] VALUES (586,'سعيد بن دهثم المقدسي شامي، حديثه غير محفوظ، ولا يصح في متنه شيء وعبد الله بن نمير ليس بمعروف بالنقل','1253','575','0');
INSERT INTO [t13041] VALUES (587,'سعيد بن عبد الرحمن أخو أبي حرة، بصري. حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت يحيى بن سعيد، وقيل، له في سعيد بن عبد الرحمن أخي أبي حرة: إن عبد الرحمن يقول كان أتيت شيخا بالبصرة، قال يحيى: إيش أقول لك، كأنه يضعفه','1255','575','0');
INSERT INTO [t13041] VALUES (588,'سعيد بن ذي لعوة عن عمر، في النبيذ، كوفي. حدثني آدم قال: سمعت البخاري يقول: سعيد بن ذي لعوة، عن عمر، في النبيذ، قال البخاري: يخالف في حديثه، لا يعرف','1256','575','0');
INSERT INTO [t13041] VALUES (589,'سعيد بن راشد السماك عن عطاء، والزهري، بصري. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين قال: سعيد بن راشد السماك يروي: " من أذن فهو يقيم " ليس حديثه بشيء','1258','575','0');
INSERT INTO [t13041] VALUES (590,'سعيد بن زيد أخو حماد بن زيد الأزدي بصري. حدثنا محمد بن إسماعيل قال: حدثنا العباس بن عبد العظيم قال: حدثنا علي بن عبد الله قال: سمعت يحيى، ضعف سعيد بن زيد أخا حماد بن زيد في الحديث جدا، وأخذ شيئا من الأرض فقال: ما يسوى هذه، وقال: قد حدثني وكلمته','1260','575','0');
INSERT INTO [t13041] VALUES (591,'سعيد بن زون بصري، عن أنس. حدثني محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين، يقول: سعيد بن زون بصري ضعيف. حدثنا أحمد بن محمود، قال: حدثنا عثمان بن سعيد، قال: سألت يحيى عن سعيد بن زون، فقال: ليس بشيء. حدثني آدم بن موسى قال','1261','575','0');
INSERT INTO [t13041] VALUES (592,'سعيد بن زربي أبو عبيدة عن ثابت. حدثنا أحمد بن محمود، قال: حدثنا عثمان بن سعيد، قال: قلت ليحيى بن معين: ما حال سعيد بن زربي، قال: ليس بشيء. حدثني آدم قال: سمعت البخاري قال: سعيد بن زربي، عن ثابت، وغيره، عنده عجائب','1263','575','0');
INSERT INTO [t13041] VALUES (593,'سعيد بن سنان أبو سنان الشيباني، كوفي حدثنا عبد الله بن أحمد بن حنبل قال: عن أبيه، قال: أبو سنان سعيد بن سنان ليس بالقوي في الحديث، روى عنه الثوري، وزيد بن حباب، وهو الذي روى عن ثابت بن جابان، عن الضحاك، وكان أبو سنان، هذا يختلف إلى الضحاك مع','1265','575','0');
INSERT INTO [t13041] VALUES (594,'سعيد بن سنان أبو المهدي الحمصي الكندي عن أبي الزاهرية. حدثني آدم بن موسى قال: سمعت البخاري قال: سعيد بن سنان أبو المهدي الحمصي الكندي، عن أبي الزاهرية، منكر الحديث. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: سعيد بن سنان أبو المهدي ليس بثقة','1266','575','0');
INSERT INTO [t13041] VALUES (595,'سعيد بن سالم بن أبي الهيفاء القداح المكي كان ممن يغلو في الإرجاء، وفي حديثه وهم. حدثنا أحمد بن علي قال: سمعت محمد بن عبد الله المقرئ يقول: كان سعيد بن سالم القداح مرجئا، وقد كتبت عنه، وكان عبد المجيد يقول: لا أحدث من أتى هؤلاء الشكاك: سفيان بن','1268','575','0');
INSERT INTO [t13041] VALUES (596,'سعيد بن سلام العطار بصري حدثنا عبد الله بن أحمد بن حنبل قال: سمعت محمد بن عبد الله بن نمير، يقول: سعيد بن سلام بصري كذاب، يحدث عن الثوري. حدثنا عبد الله بن أحمد بن عبد السلام قال: حدثنا محمد بن إسماعيل قال: سعيد بن سلام العطار يذكر بوضع الحديث عن','1269','575','0');
INSERT INTO [t13041] VALUES (597,'سعيد بن زكريا المدائني حدثنا عبد الله بن أحمد قال: سألت أبي عن سعيد بن زكريا المدائني، فقال: كتبنا عنه أحاديث زمعة وعرضتها بعد على أبي داود الطيالسي فأجاب فيها إلا شيئا يسيرا، أربعة أحاديث أو خمسة أو أقل أو أكثر، ما به بأس إن شاء الله. حدثني الخضر','1271','575','0');
INSERT INTO [t13041] VALUES (598,'سعيد بن سليمان الواسطي حدثني الخضر بن داود قال: حدثنا أحمد بن محمد قال: سمعت أبا عبد الله، يسأل عن سعيد بن سليمان: ترى الكتابة عنه؟ فقال: أعفني عن المسألة عن هؤلاء، وذلك في حياة سعيد، وذلك بعد المحنة. حدثنا عبد الله قال: سمعت أبي، وذكر سعيد بن','1272','575','0');
INSERT INTO [t13041] VALUES (599,'سعيد بن عبد الرحمن أبو شيبة سمع مجاهدا، وابن أبي مليكة. حدثني آدم قال: سمعت البخاري قال: سعيد بن عبد الرحمن أبو شيبة سمع مجاهدا، وابن أبي مليكة، روى عنه عبد الواحد بن زياد، قال البخاري: لا يتابع على حديثه','1274','575','0');
INSERT INTO [t13041] VALUES (600,'سعيد بن كثير بن عفير حدثنا أبو علاثة محمد بن عمرو بن خالد قال: سمعت أخي، يقول: كنت عند أصبغ بن الفرج في المسجد، فمر به سعيد بن كثير بن عفير فقال: والله لولا أبوك وابن بكير لعلم ذا ما أصنع به','1276','575','0');
INSERT INTO [t13041] VALUES (601,'سعيد بن عبد الجبار الزبيدي الحمصي أبو عثمان حدثني آدم بن موسى قال: سمعت البخاري يقول: سعيد بن عبد الجبار الزبيدي الحمصي أبو عثمان، قال قتيبة: رأيته بالبصرة وكان جرير يكذبه. حدثني الفضل بن جعفر قال: حدثنا إسماعيل بن إسحاق قال: حدثنا علي بن المديني','1277','575','0');
INSERT INTO [t13041] VALUES (602,'سعيد بن مسلمة الأموي جزري حدثني آدم بن موسى قال: سمعت البخاري قال: سعيد بن مسلمة الأموي منكر الحديث، في حديثه نظر. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين قال: سعيد بن مسلمة كان عنده كتاب عن منصور، فقال له رجل: سمعت هذا الكتاب','1278','575','0');
INSERT INTO [t13041] VALUES (603,'سعيد بن أبي عروبة واسم أبي عروبة مهران، بصري. حدثنا عبد الله بن أحمد قال: قال أبي: من سمع من سعيد بن أبي عروبة قبل الهزيمة فسماعه جيد، ومن سمع بعد الهزيمة، فكان أبي يضعفهم، قلت: كان سعيد اختلط؟ قال: نعم، ثم قال: من سمع منه بالكوفة مثل محمد بن','1279','575','0');
INSERT INTO [t13041] VALUES (604,'سعيد بن مرزبان أبو سعد البقال كوفي حدثنا محمد بن إسماعيل قال: حدثنا محمود بن غيلان قال: سمعت وكيعا، سئل عن أبي سعد البقال، فقال: نعم كان يروي عن أبي وائل، وكان أبو وائل، ثقة. حدثنا محمد بن إسماعيل قال: حدثنا الحسن قال: حدثنا محمد بن داود','1284','575','0');
INSERT INTO [t13041] VALUES (605,'سعيد بن واصل بصري سمع شعبة، ووهيبا. حدثني آدم بن موسى قال: سمعت البخاري قال: سعيد بن واصل بصري، يقال عن علي: إنه ذهب حديثه','1285','575','0');
INSERT INTO [t13041] VALUES (606,'سعيد بن يزيد بن الصلت عن ابن جريج، ولا يتابع على حديثه، وهو حديثه خطأ','1287','575','0');
INSERT INTO [t13041] VALUES (607,'سعيد بن محمد الوراق حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين، يقول: سعيد بن محمد الوراق ليس بشيء','1289','575','0');
INSERT INTO [t13041] VALUES (608,'سعد بن سعيد بن قيس الأنصاري أخو يحيى بن سعيد، مدني. حدثنا عبد الله بن أحمد قال: قال أبي: سعد بن سعيد أخو يحيى بن سعيد الأنصاري ضعيف الحديث','1291','575','0');
INSERT INTO [t13041] VALUES (609,'سعد بن سعيد بن أبي سعيد المقبري مدني حدثنا الحسين بن أحمد قال: حدثنا أبو معمر قال: حدثنا سفيان قال: كان سعد بن سعيد قدريا. حدثني أحمد بن محمود، قال: حدثنا عثمان بن سعيد قلت ليحيى: سعد بن سعيد بن أبي سعيد المقبري؟ قال: ضعيف','1292','575','0');
INSERT INTO [t13041] VALUES (610,'سعد بن سعيد الجرجاني عن نهشل، عن الضحاك. حدثني آدم بن موسى قال: سمعت البخاري قال: سعد بن سعيد الجرجاني، عن نهشل، ولا يصح حديثه','1293','575','0');
INSERT INTO [t13041] VALUES (611,'سعد بن شعبة بن الحجاج بن الورد العتكي، بصري حدثني محمد بن مروان القرشي قال: حدثنا السكن بن سعيد أبو عمرو قال: حدثنا سهل بن الحسام قال: سمعت شعبة، يقول: سميت ابني سعدا، فما سعد ولا فلح. حدثنا محمد بن مروان قال: حدثنا الحسن بن إسحاق قال: حدثنا','1295','575','0');
INSERT INTO [t13041] VALUES (612,'سعد بن سنان عن أنس , روى عنه يزيد بن أبي حبيب، وقال ابن لهيعة: سنان بن سعد مصري. حدثنا محمد بن عيسى قال: سمعت محمد بن علي الوراق قال: سمعت أحمد بن حنبل، يقول في أحاديث يزيد بن أبي حبيب، عن سعد بن سنان، عن أنس قال: روى خمسة عشر حديثا، منكرة كلها','1296','575','0');
INSERT INTO [t13041] VALUES (613,'سعد بن طارق الأشجعي أبو مالك حدثنا محمد بن إسماعيل، وأحمد بن علي، قالا: حدثنا الحسن بن علي الحلواني قال: حدثنا أبو الوليد قال: حدثنا القاسم بن معن قال: سألت بعض ولد أبي مالك: ألقي أبو مالك رسول الله صلى الله عليه وسلم؟ فقال: لا، فذكرت ذلك ليحيى','1297','575','0');
INSERT INTO [t13041] VALUES (614,'سعد بن طريف الإسكاف كوفي حدثنا محمد بن سعيد بن بلج الرازي قال: سمعت عبد الرحمن بن الحكم بن بشير بن سلمان، يسأل عن سعد بن طريف، فقال: هو الإسكاف قال: وكان فيه غلو في التشيع. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين يقول: سعد بن','1299','575','0');
INSERT INTO [t13041] VALUES (615,'سليمان بن أرقم أبو معاذ مولى قريظة أو النضير، ويقال مولى قريش، عن الحسن ويحيى بن أبي كثير والزهري. قال البخاري: تركوه، حدثنا آدم بن موسى عنه','1301','575','0');
INSERT INTO [t13041] VALUES (616,'سليمان بن أحمد الواسطي حدثنا آدم بن موسى قال: سمعت البخاري قال: سليمان بن أحمد الواسطي فيه نظر','1304','575','0');
INSERT INTO [t13041] VALUES (617,'سليمان بن جنادة بن أبي أمية الدوسي حدثني آدم قال: سمعت البخاري قال: سليمان بن جنادة بن أبي أمية الدوسي، عن أبيه، عن عبادة، عن النبي صلى الله عليه وسلم في الجنازة، قال البخاري: لم يتابع في هذا، قاله صفوان بن عيسى، عن بشر بن نافع، وهو حديث منكر','1306','575','0');
INSERT INTO [t13041] VALUES (618,'سليمان بن جعفر الأسدي مجهول بنقل الحديث، ولا يتابع على حديثه','1308','575','0');
INSERT INTO [t13041] VALUES (619,'سليمان بن الحجاج الطائفي الغالب على حديثه الوهم','1310','575','0');
INSERT INTO [t13041] VALUES (620,'سليمان بن حيان أبو خالد الأحمر كوفي حدثنا أحمد بن علي قال: حدثنا الحسن بن شجاع، عن أبي نعيم قال: سئل سفيان عن أبي خالد الأحمر، فقال: ابن نمير رجل صالح. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى قال: قال لي حجاج الأعور: كان','1313','575','0');
INSERT INTO [t13041] VALUES (621,'سليمان بن حسان مصري وقع بالري، لا يتابع على حديثه','1315','575','0');
INSERT INTO [t13041] VALUES (622,'سليمان الخوزي كوفي، في حديثه وهم ولا يتابع على حديثه','1317','575','0');
INSERT INTO [t13041] VALUES (623,'سليمان بن داود اليمامي عن يحيى بن أبي كثير. حدثني آدم بن موسى قال: سمعت البخاري يقول: سليمان بن داود اليمامي، عن يحيى بن أبي كثير، منكر الحديث','1321','575','0');
INSERT INTO [t13041] VALUES (624,'سليمان بن داود القرشي عن ابن أبي مليكة، ولا يتابع عليه، مجهول','1324','575','0');
INSERT INTO [t13041] VALUES (625,'سليمان بن داود الخولاني حدثني أحمد بن محمود، حدثنا عثمان بن سعيد قال: قلت ليحيى بن معين: سليمان بن داود الذي يروي حديث الزهري في الصدقات؟ قال: ليس بشيء','1326','575','0');
INSERT INTO [t13041] VALUES (626,'سليمان بن داود المنقري الشاذكوني بصري حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: كان يحيى بن سعيد يسمي الشاذكوني الخائب. حدثنا محمد بن عبد الحميد السهمي قال: حدثنا أحمد بن محمد الحضرمي قال: سألت يحيى بن معين عن سليمان الشاذكوني، فقال لي: ليس بشيء','1328','575','0');
INSERT INTO [t13041] VALUES (627,'سليمان بن الحكم بن عوانة الكلبي كوفي حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين قال: سليمان بن الحكم بن عوانة ليس بشيء','1329','575','0');
INSERT INTO [t13041] VALUES (628,'سليمان بن زيد أبو إدام حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى يقول: أبو إدام كوفي ليس يسوى حديثه فلسا، اسمه سليمان','1331','575','0');
INSERT INTO [t13041] VALUES (629,'سليمان بن ذكوان القحذمي عن أنس، ولا يتابع عليه','1333','575','0');
INSERT INTO [t13041] VALUES (630,'سليمان بن زياد الثقفي الواسطي','1335','575','0');
INSERT INTO [t13041] VALUES (631,'سليمان بن شعيب بن الليث بن سعد حديثه غير محفوظ، ولا يتابع عليه من جهة تثبت، ولا يعرف أيضا بالنقل إلا به','1337','575','0');
INSERT INTO [t13041] VALUES (632,'سليمان بن عبد الله عن معاذة العدوية، عن علي. حدثني آدم بن موسى قال: سمعت البخاري قال: سليمان بن عبد الله، عن معاذة العدوية، عن علي: " أنا الصديق الأكبر "، قال البخاري: لا يتابع عليه، ولا يعرف سماع سليمان من معاذة','1339','575','0');
INSERT INTO [t13041] VALUES (633,'سليمان بن عبيد الله أبو أيوب الرقي الحطاب عن عبيد الله بن عمرو، لا يتابع عليه','1341','575','0');
INSERT INTO [t13041] VALUES (634,'سليمان بن عبد الرحمن الدمشقي حدثنا محمد بن أحمد، حدثنا معاوية بن صالح قال: سألت يحيى بن معين عن أبي أيوب سليمان بن عبد الرحمن فقال: ليس بالمسكين بأس إذا حدث عن المعروفين','1343','575','0');
INSERT INTO [t13041] VALUES (635,'سليمان بن عطاء عن مسلمة بن عبد الله. حدثني آدم بن موسى قال: سمعت البخاري قال: سليمان بن عطاء سمع مسلمة بن عبد الله، في حديثه بعض المناكير','1346','575','0');
INSERT INTO [t13041] VALUES (636,'سليمان بن عمرو أبو داود النخعي حدثنا أحمد بن علي الأبار قال: حدثنا الحسن بن علي الحلواني قال: حدثنا محمد بن داود الحداني قال: سمعت عيسى بن يونس، وسئل عن أبي داود النخعي، قال: هيه، أخوه كان لي صديقا، وكان فصيحا. حدثني آدم بن موسى قال: سمعت','1348','575','0');
INSERT INTO [t13041] VALUES (637,'سليمان العطار أبو صلة، واسطي حدثني آدم بن موسى قال: سمعت البخاري قال: بلغني عن ابن معين، أنه قال: والد صلة بن سليمان الواسطي وصلة ليس بثقة، ولا أدري كيف هو','1349','575','0');
INSERT INTO [t13041] VALUES (638,'سليمان بن سفيان المديني حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين قال: حدثنا سليمان بن سفيان، مديني ليس بثقة','1350','575','0');
INSERT INTO [t13041] VALUES (639,'سليمان بن أبي سليمان القافلاني حدثنا عبد الله بن أحمد بن حنبل قال: سمعت أبي يقول: سليمان أبو محمد القافلاني يحدث عن الحسن، وابن سيرين، ضعيف الحديث. قال أبي: زعموا أنه كان يجيء إلى حماد بن سلمة فيقول حماد: حدثنا قيس، عن عطاء قال: فيكتبه ثم يقول:','1352','575','0');
INSERT INTO [t13041] VALUES (640,'سليمان بن معاذ الضبي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: سليمان بن معاذ ليس بشيء، حدثنا أبو داود الطيالسي عنه','1353','575','0');
INSERT INTO [t13041] VALUES (641,'سليمان بن قرم الضبي حدثنا أبو موسى الأنصاري قال: حدثنا محمد بن عوف بن سفيان قال: قيل لأحمد بن حنبل: سليمان بن قرم؟ قال: لا أرى به بأسا، ولكنه كان يفرط في التشيع. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين قال: سليمان بن قرم كان','1354','575','0');
INSERT INTO [t13041] VALUES (642,'سليمان بن كثير أبو داود الواسطي مضطرب الحديث. حدثنا عبد الله بن علي قال: سمعت محمد بن يحيى، يقول: سمعت سليمان بن كثير العبدي، سكن البصرة، ما روى عن الزهري، فإنه قد اضطرب في أشياء منها، وهو في غير حديث الزهري أثبت، وقد روى سليمان بن كثير عن حصين','1356','575','0');
INSERT INTO [t13041] VALUES (643,'سليمان بن أبي كريمة عن هشام بن حسان، يحدث بمناكير ولا يتابع على كثير من حديثه','1358','575','0');
INSERT INTO [t13041] VALUES (644,'سليمان بن كراز الطفاوي بصري الغالب على حديثه الوهم','1360','575','0');
INSERT INTO [t13041] VALUES (645,'سليمان بن محمد الهاشمي مجهول بالنقل، حديثه غير محفوظ','1363','575','0');
INSERT INTO [t13041] VALUES (646,'سليمان بن مسلم أبو المعلى الخزاعي بصري مجهول عن سليمان التيمي، عن نافع، ولا يتابع على حديثه','1365','575','0');
INSERT INTO [t13041] VALUES (647,'سليمان بن مسلم مؤذن مسجد ثابت البناني , ولا يتابع على حديثه، ولا يعرف إلا به','1367','575','0');
INSERT INTO [t13041] VALUES (648,'سليمان بن موسى الدمشقي أبو أيوب سمع عطاء، وعمرو بن شعيب، ونافعا. حدثني أحمد بن محمود الهروي قال: حدثنا محمد بن أحمد بن مسعر قال: سمعت علي بن المديني يقول: سليمان بن موسى مطعون فيه. حدثنا محمد بن إسماعيل قال: حدثنا أبو خيثمة زهير بن حرب قال:','1369','575','0');
INSERT INTO [t13041] VALUES (649,'سليمان بن موسى أبو داود كوفي، عن دلهم، ولا يتابع على حديثه ولا يعرف إلا به','1370','575','0');
INSERT INTO [t13041] VALUES (650,'سليمان بن موسى عن مظاهر بن أسلم، ومظاهر، منكر الحديث، قاله البخاري. حدثنا آدم بن موسى، قال: سمعت البخاري، قال: سليمان بن موسى عن مظاهر بن أسلم، ومظاهر، منكر الحديث','1372','575','0');
INSERT INTO [t13041] VALUES (651,'سليمان بن مسافع الحجبي عن منصور ابن صفية، ولا يتابع عليه','1375','575','0');
INSERT INTO [t13041] VALUES (652,'سليمان بن مرثد عن عائشة، وأبى الدرداء. حدثني آدم بن موسى قال: سمعت البخاري قال: سليمان بن مرثد، عن عائشة، ولا يعرف له سماع من عائشة، وروى عن أبي الدرداء ولا يتبين فيه السماع','1378','575','0');
INSERT INTO [t13041] VALUES (653,'سليمان بن مرقاع الجندعي منكر الحديث، ولا يتابع عليه في حديثه، مدني','1382','575','0');
INSERT INTO [t13041] VALUES (654,'سليمان بن وهب الأنصاري بصري يخالف في حديثه','1385','575','0');
INSERT INTO [t13041] VALUES (655,'سليمان بن هرم عن محمد بن المنكدر، مجهول في الرواية، حديثه غير محفوظ','1389','575','0');
INSERT INTO [t13041] VALUES (656,'سليمان بن يسير أبو الصباح الكوفي النخعي حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: سمعت يحيى قال: روى شعبة عن أبي الصباح وهو سليمان بن يسير، وهو ضعيف، روى عن همام بن الحارث أحاديث منكرة، ولا أحفظ عن سفيان، عنه شيئا. حدثنا محمد بن زكريا قال:','1391','575','0');
INSERT INTO [t13041] VALUES (657,'سلمة بن عبيد الله بن محصن مجهول في النقل، ولا يتابع على حديثه، ولا يعرف إلا به','1393','575','0');
INSERT INTO [t13041] VALUES (658,'سلمة بن وهرام جندي حدثنا عبد الله بن أحمد قال: سألت أبي عن سلمة بن وهرام، فقال: روى عنه زمعة أحاديث مناكير، أخشى أن يكون حديثه حديث ضعيف','1395','575','0');
INSERT INTO [t13041] VALUES (659,'سلمة بن نبيط الأشجعي، كوفي حدثنا جعفر بن محمد السوسي قال: حدثنا زيد بن أخزم قال: سمعت عبد الله بن داود يقول: رأيت سلمة بن نبيط وما يبكي، ثم رأيته يبكي. حدثني آدم بن موسى قال: سمعت البخاري قال: سلمة بن نبيط الأشجعي يقال أنه كان اختلط في آخر عمره','1397','575','0');
INSERT INTO [t13041] VALUES (660,'سلمة بن وردان المديني حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: سلمة بن وردان منكر الحديث، وسألت أبي مرة أخرى عن سلمة بن وردان، فقال: ضعيف. حدثنا محمد بن عيسى قال: حدثنا العباس قال: سمعت يحيى بن معين قال: سلمة بن وردان ليس بشيء، حدثنا محمد بن','1398','575','0');
INSERT INTO [t13041] VALUES (661,'سلمة بن صالح الأحمر، واسطي حدثني أحمد بن أصرم، قال: سمعت أبا عبد الله، وسئل عن سلمة بن صالح الأحمر، وروح بن مسافر، فقال: روح لا أخبره، وسلمة بن صالح، ليس بشيء. حدثنا عبد الله بن أحمد قال: سألت أبي عن سلمة بن صالح الأحمر، فقال: ليس بشيء،','1399','575','0');
INSERT INTO [t13041] VALUES (662,'سلمة الضبي مجهول بالنقل، لا يعرف إلا بهذا الحديث، ولا يتابع عليه على حديثه من وجه يصح','1401','575','0');
INSERT INTO [t13041] VALUES (663,'سلمة بن مسلم العبدي عن عطاء، في حديثه وهم وغلط، ولا يتابع على أكثره غير حديثه','1403','575','0');
INSERT INTO [t13041] VALUES (664,'سلمة بن تمام الشقري، بصري حدثنا عبد الله بن أحمد بن حنبل قال: سألت أبي عن أبي عبد الله الشقري، فقال: اسمه سلمة بن تمام وقال: ليس هو بقوي في الحديث. حدثني محمد بن عبد الرحمن، حدثنا عبد الملك بن عبد الحميد قال: سألت أبا عبد الله عن سلمة بن تمام أبي','1405','575','0');
INSERT INTO [t13041] VALUES (665,'سلمة بن رجاء حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين يقول: سلمة بن رجاء، كوفي ليس بشيء','1406','575','0');
INSERT INTO [t13041] VALUES (666,'سلمة بن الفضل الأبرش رازي حدثنا محمد بن عيسى قال: حدثنا صالح قال: سمعت علي بن المديني يقول: ما خرجنا من الري حتى رمينا بحديث سلمة. حدثني آدم قال: سمعت البخاري قال: سلمة بن الفضل أبو عبد الله الأبرش، عن محمد بن إسحاق، وغيره، في حديثه بعض المناكير','1408','575','0');
INSERT INTO [t13041] VALUES (667,'سالم أبو العلاء المرادي، كوفي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين قال: سالم أبو العلاء ضعيف','1409','575','0');
INSERT INTO [t13041] VALUES (668,'سالم بن عبد الله الخياط، بصري حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: ما سمعت يحيى ولا عبد الرحمن يحدثان عن سالم الخياط بشيء قط، وقد روى سفيان عنه','1412','575','0');
INSERT INTO [t13041] VALUES (669,'سالم بن عجلان الأفطس حدثنا عبد الله قال: سمعت أبي يقول: سالم الأفطس ما أصلح حديثه، وهو مرجئ','1413','575','0');
INSERT INTO [t13041] VALUES (670,'سالم بن عبد الأعلى أبو الفيض، كوفي حدثنا محمد بن عيسى قال: حدثنا العباس قال: سمعت يحيى قال: سالم أبو الفيض ليس حديثه بشيء. حدثني آدم بن موسى قال: سمعت البخاري قال: سالم بن عبد الأعلى أبو الفيض، قال البخاري: تركوه','1414','575','0');
INSERT INTO [t13041] VALUES (671,'سالم بن أبي حفصة، كوفي من الشيعة حدثنا محمد بن الحسن الأصبهاني قال: حدثنا محمد بن عبد الله المخرمي قال: حدثنا محمد بن بشير العبدي قال: رأيت سالم بن أبي حفصة ذا لحية طويلة، أحمق بها من لحية، وهو يقول: وددت أني كنت شريك علي في جميع ما كان فيه.','1416','575','0');
INSERT INTO [t13041] VALUES (672,'سهيل بن مهران أخو حزم حدثني آدم بن موسى قال: سمعت البخاري قال: سهيل بن مهران القطعي أخو حزم، قال البخاري: ليس بالقوي عندهم','1417','575','0');
INSERT INTO [t13041] VALUES (673,'سهيل بن ذكوان المكي سكن واسط. حدثني آدم بن موسى قال: سمعت البخاري قال: سهيل بن ذكوان المكي سكن واسط أبو السندي، سمع عائشة، وابن الزبير، سمع منه، هشيم، ويزيد بن هارون، قال البخاري: قال عباد بن العوام: كنا نتهمه بالكذب، فاتهمه ابن معين. حدثنا','1419','575','0');
INSERT INTO [t13041] VALUES (674,'سهيل بن أبي الفرقد يمامي عن الحسن. حدثني آدم بن موسى قال: سمعت البخاري قال: سهيل بن أبي الفرقد يمامي، عن الحسن، روى عنه، عكرمة بن عمار، قال البخاري: منكر الحديث','1420','575','0');
INSERT INTO [t13041] VALUES (675,'سهيل بن ذكوان السمان مدني وهو ابن أبي صالح. حدثنا محمد بن زكريا البلخي قال: حدثنا الحسن بن شجاع قال: سمعت علي بن المديني قال: سمعت يحيى، وسئل عن سهيل بن أبي صالح، ومحمد بن عمرو، فقال: محمد أعلى منه. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سئل','1422','575','0');
INSERT INTO [t13041] VALUES (676,'سهل بن أبي الصلت السراج، بصري حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: سمعت عبد الرحمن، يحدث عن سهل السراج، وسمعت يحيى، وذكر سهل السراج، فقال: روى شيئا منكرا، روى أنه رأى الحسن يصلي بين سطور القبور. قال: وحدثنا الأشعث، عن الحسن، أن','1424','575','0');
INSERT INTO [t13041] VALUES (677,'سهل بن سليمان الأسود، بصري حدثنا عبد الله بن أحمد قال: سمعت أبي قال: كان سهل الأسود من أصحاب الحديث، أروى الناس عن شعبة، ترك الناس حديثه، وسمعت أبي مرة أخرى يقول: سهل بن الأسود كان من أصحاب شعبة، وكان من كبار أصحاب الحديث، وكان أروى الناس عن','1426','575','0');
INSERT INTO [t13041] VALUES (678,'سويد بن عبد العزيز الدمشقي حدثنا عبد الله بن أحمد قال: سألت أبي عن سويد بن عبد العزيز، فقال: متروك الحديث. حدثنا محمد بن أحمد قال: وحدثنا معاوية بن صالح قال: سمعت يحيى يقول: سويد بن عبد العزيز ضعيف. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت','1427','575','0');
INSERT INTO [t13041] VALUES (679,'سويد بن إبراهيم أبو حاتم، بصري حدثنا أحمد بن علي الأبار قال: حدثنا محمد بن موسى القطان قال: سألت أبا سلمة عن حديث لسويد أبي حاتم، فقال: لم يكن سويد بالصافي. حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن يحدث عن سويد أبي','1428','575','0');
INSERT INTO [t13041] VALUES (680,'سلام بن سلم المدائني الطويل عن زيد العمي، ويقال التميمي الشقري. حدثنا محمد بن عثمان قال: سمعت يحيى بن معين، وسئل عن سلام بن سلم، فقال يحيى: كان ضعيفا. حدثني محمد بن عيسى قال: سمعت عباسا قال: سمعت يحيى قال: سلام بن سلم ليس بشيء. حدثني آدم قال:','1430','575','0');
INSERT INTO [t13041] VALUES (681,'سلام بن أبي الصهباء أبو بشر العدوي، بصري عن ثابت. حدثني آدم بن موسى قال: سمعت البخاري قال: سلام بن أبي الصهباء العدوي سمع ثابتا، قال البخاري: منكر الحديث','1433','575','0');
INSERT INTO [t13041] VALUES (682,'سلام بن سليمان أبو المنذر القارئ بصري عن ثابت، ويونس بن عبيد، ولا يتابع على حديثه','1435','575','0');
INSERT INTO [t13041] VALUES (683,'سلام بن أبي خبزة أبو سعيد البصري حدثني آدم بن موسى قال: سمعت البخاري قال: سلام بن أبي خبزة أبو سعيد البصري ضعفه قتيبة بن سعيد ولم يحدث عنه','1438','575','0');
INSERT INTO [t13041] VALUES (684,'سلام بن سليمان المدائني في حديثه عن الثقات مناكير','1440','575','0');
INSERT INTO [t13041] VALUES (685,'سلام بن يزيد القارئ ولا يتابع على حديثه، بصري','1442','575','0');
INSERT INTO [t13041] VALUES (686,'سلام بن وهب الجندي عن ابن طاوس، لا يتابع عليه، ولا يعرف إلا به','1445','575','0');
INSERT INTO [t13041] VALUES (687,'سلام بن سوار عن مسلمة بن الصلت، عن الزهري، شامي، حديثه غير محفوظ، ولا أصل له من حديث الزهري ولا غيره','1447','575','0');
INSERT INTO [t13041] VALUES (688,'سلام بن واقد المروزي عن محمد بن عبد الله بن عبيد بن عمير إسناداهما غير محفوظين','1449','575','0');
INSERT INTO [t13041] VALUES (689,'سلام بن رزين قاضي أنطاكية حدثنا عبد الله بن أحمد بن محمد بن حنبل قال: حدثنا أبي بحديث','1452','575','0');
INSERT INTO [t13041] VALUES (690,'سليم بن عيسى مجهول في النقل، حديثه منكر غير محفوظ','1454','575','0');
INSERT INTO [t13041] VALUES (691,'سليم مولى الشعبي كوفي حدثنا محمد بن زكريا قال: حدثنا محمد بن المثنى قال: ما سمعت يحيى، ولا عبد الرحمن يحدثان عن سليم، مولى الشعبي. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى يقول: سليم مولى الشعبي ضعيف','1456','575','0');
INSERT INTO [t13041] VALUES (692,'سليم بن مسلم الخشاب مكي حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى ذكر سليم بن مسلم المكي فقال: كان ينزل مكة، وكان جهميا خبيثا','1457','575','0');
INSERT INTO [t13041] VALUES (693,'سلم العلوي، بصري حدثنا أحمد بن علي قال: حدثنا أبو سعيد الأشج، ح، وحدثنا عبد الله بن أحمد قال: حدثنا عمرو الناقد، قالا: حدثنا عبد الله بن إدريس قال: قلت لشعبة بن مالك: ولأبان بن أبي عياش؟ أخبرني مهدي بن ميمون، عن سلم العلوي، أنه رأى أبان بن','1458','575','0');
INSERT INTO [t13041] VALUES (694,'سلم بن سالم البلخي حدثنا أحمد بن علي الأبار قال: سمعت عباس بن صالح يقول: ذكرت لأسود بن سالم سلم بن سالم البلخي فقال: لا تذكره لي. حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: حدثنا نعيم بن حماد قال: سمعت ابن المبارك، وذكر عنده يوما حديث عن','1459','575','0');
INSERT INTO [t13041] VALUES (695,'سلم بن ميمون الخواص حدث بمناكير، لا يتابع عليها','1460','575','0');
INSERT INTO [t13041] VALUES (696,'سلم بن قتيبة أبو قتيبة الباهلي بصري','1462','575','0');
INSERT INTO [t13041] VALUES (697,'سلم بن سليمان الضبي أبو هشام، بصري عن أبي حرة، لا يقيم الحديث، في حديثه وهم.','1464','575','0');
INSERT INTO [t13041] VALUES (698,'سوار بن داود أبو حمزة صاحب الحلي قال وكيع: داود بن سوار','1467','575','0');
INSERT INTO [t13041] VALUES (699,'سوار بن مصعب المؤذن الأعمى حدثنا محمد بن عثمان بن أبي شيبة قال: سمعت يحيى بن معين سئل عن سوار بن مصعب، فقال: كان ضعيفا. وحدثني محمد بن عيسى قال: حدثني عباس قال: سمعت يحيى قال: سوار بن مصعب كوفي، ليس بشيء، قد رأيته وكان يجيئنا إلى منزلنا. حدثني','1470','575','0');
INSERT INTO [t13041] VALUES (700,'سوار الكوفي','1472','575','0');
INSERT INTO [t13041] VALUES (701,'سوار بن محمد بن قريش العنبري ولا يتابع على رفع حديثه، بصري كان بمصر','1474','575','0');
INSERT INTO [t13041] VALUES (702,'سوار بن عبد الله بن قدامة قاضي البصرة العنبري. حدثنا الهيثم بن خلف قال: حدثنا عبد الملك بن محمد قال: حدثنا سليمان قال: سمعت يحيى بن سعيد القطان قال: سألت سفيان عن سوار بن عبد الله، فقال: ليس بشيء','1478','575','0');
INSERT INTO [t13041] VALUES (703,'سنان بن ربيعة حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: سنان بن ربيعة ليس هو بالقوي عندهم، وقد روى عنه السهمي','1480','575','0');
INSERT INTO [t13041] VALUES (704,'سنان بن هارون البرجمي وحديثه غير محفوظ. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى، يقول: سنان بن هارون وسيف بن هارون ضعيفان، وسنان أعجبهما إلي','1482','575','0');
INSERT INTO [t13041] VALUES (705,'سيف بن وهب بصري حدثنا عبد الله بن أحمد قال: كتب إلي أبو بكر بن خلاد قال: حدثت يحيى بن سعيد بحديث سيف بن وهب، عن أبي حرب بن أبي الأسود، عن عميرة بن يثربي، عن أبي بن كعب قال: " إذا التقا ملتقاهما من وراء الختان وجب الغسل "، عن سهل بن يوسف، عن شعبة','1484','575','0');
INSERT INTO [t13041] VALUES (706,'سيف بن محمد ابن أخت الثوري عن عاصم، هو أخو عمار بن محمد، كوفي. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: كان سيف كذابا. حدثنا عبد الله قال: سمعت أبي يقول: لا يكتب حديث سيف بن محمد، ليس سيف بشيء، كان يضع الحديث. حدثنا عبد الله بن أحمد قال:','1486','575','0');
INSERT INTO [t13041] VALUES (707,'سيف بن أبي المغيرة التمار ولا يتابع على حديثه، كوفي','1488','575','0');
INSERT INTO [t13041] VALUES (708,'سيف بن سليمان ويقال ابن أبي سليمان','1490','575','0');
INSERT INTO [t13041] VALUES (709,'سيف بن هارون البرجمي حدثنا عبد الله قال: سألت يحيى بن معين عن سيف بن هارون البرجمي، وسنان بن هارون، فقال: سنان بن هارون أوثق من سيف وهو فوقه، فقلت له: إن سيفا حدث عن التيمي، عن أبي عثمان، عن سلمان، عن النبي صلى الله عليه وسلم في الفراء، فقال:','1492','575','0');
INSERT INTO [t13041] VALUES (710,'سيف بن عمر الضبي، كوفي حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى يقول: سيف بن عمر الضبي يحدث عنه المحاربي، هو ضعيف','1495','575','0');
INSERT INTO [t13041] VALUES (711,'سفيان بن الليل كوفي كان ممن يغلو في الرفض، لا يصح حديثه','1497','575','0');
INSERT INTO [t13041] VALUES (712,'سوادة عن أنس، مجهول بالنقل، حديثه غير محفوظ','1500','575','0');
INSERT INTO [t13041] VALUES (713,'السري بن إسماعيل الهمداني الكوفي حدثنا عبد الله بن أحمد الخفاف النيسابوري قال: حدثنا محمد بن إسماعيل قال: حدثنا عبيد الله بن سعيد قال: سمعت يحيى بن سعيد القطان، وذكر السري بن إسماعيل قال: استبان لي كذبه في مجلسي. حدثنا محمد بن حفص الجوزجاني قال:','1502','575','0');
INSERT INTO [t13041] VALUES (714,'سلمى بن عبد الله أبو بكر الهذلي بصري حدثني جعفر بن محمد السوسي قال: حدثنا أبو هبيرة محمد بن الوليد قال: حدثنا أبو مسهر قال: حدثنا مزاحم بن زفر قال: قلت لشعبة بن الحجاج: ما تقول في أبي بكر الهذلي؟ قال: دعني لا أقيء. حدثنا محمد بن عيسى قال: حدثنا','1503','575','0');
INSERT INTO [t13041] VALUES (715,'سماك بن حرب، كوفي حدثنا عبد الله بن أحمد قال: حدثني أبي قال: حدثنا حجاج قال: قال شعبة: كانوا يقولون لسماك: عكرمة، عن ابن عباس؟ فيقول: نعم، قال شعبة: فكنت أنا لا أفعل ذلك به. قال: سئل أبي عن سماك بن حرب، وعطاء بن السائب، فقال: ما أقر بهما','1504','575','0');
INSERT INTO [t13041] VALUES (716,'سدير الصيرفي وكان ممن يغلو في الرفض، كوفي. حدثني آدم بن موسى قال: سمعت البخاري قال: سدير بن حكيم الصيرفي سمع أبا جعفر قال ابن عيينة: رأيته وكان يكذب','1505','575','0');
INSERT INTO [t13041] VALUES (717,'سديف بن ميمون المكي كان من الغلاة في الرفض','1508','575','0');
INSERT INTO [t13041] VALUES (718,'باب الشين','1510','0','0');
INSERT INTO [t13041] VALUES (719,'شعيب بن كيسان عن أنس، كوفي. حدثني آدم بن موسى قال: سمعت البخاري قال: شعيب بن كيسان، عن أنس، لا يعرف له سماع عن أنس، ولا يتابع عليه','1511','718','0');
INSERT INTO [t13041] VALUES (720,'شعيب بن ميمون عن حصين روى عنه، شبابة وغيره، واسطي. حدثني آدم بن موسى قال: سمعت البخاري قال: شعيب بن ميمون، عن حصين بن عبد الرحمن، وغيره، فيه نظر','1515','718','0');
INSERT INTO [t13041] VALUES (721,'شعيب بن حيان بن شعيب بن درهم، بصري حدثني آدم بن موسى قال: سمعت البخاري قال: شعيب بن حيان بن شعيب بن درهم، قال البخاري: ولا يصح حديثه','1517','718','0');
INSERT INTO [t13041] VALUES (722,'شعيب بن بيان الصفار، بصري يحدث عن الثقات بالمناكير، وكاد أن يغلب، على حديثه الوهم','1519','718','0');
INSERT INTO [t13041] VALUES (723,'شجاع بن الوليد أبو بدر السكوني، كوفي','1521','718','0');
INSERT INTO [t13041] VALUES (724,'شداد بن سعيد أبو طلحة الراسبي، بصري حدثنا آدم بن موسى قال: سمعت البخاري قال: شداد بن سعيد أبو طلحة الراسبي، قال البخاري: ضعفه عبد الصمد، ولكنه صدوق، في حفظه بعض الشيء','1524','718','0');
INSERT INTO [t13041] VALUES (725,'شعبة مولى ابن عباس، مديني حدثنا زكريا بن يحيى الحلواني قال: حدثنا محمد بن المثنى قال: حدثنا بشر بن عمر قال: سألت مالكا عن شعبة الذي روى عنه ابن أبي ذئب، فقال: ليس بثقة. حدثنا عبد الله بن أحمد قال: حدثنا أبي قال: شعبة مولى ابن عباس، ما أرى به','1526','718','0');
INSERT INTO [t13041] VALUES (726,'شعبة بن عمرو، عن أنس، بصري حدثني آدم بن موسى البلخي قال: سمعت البخاري يقول: شعبة بن عمرو، عن أنس، روى عنه خليل بن مرة، قال البخاري: أحاديثه مناكير','1527','718','0');
INSERT INTO [t13041] VALUES (727,'شقيق القاص الضبي، كوفي','1528','718','0');
INSERT INTO [t13041] VALUES (728,'شرقي بن قطامي حدثنا أحمد بن علي قال: حدثنا محمد بن إسماعيل الواسطي قال: سمعت يزيد بن هارون، يقول: حدثت شعبة يوما بحديث عن شرقي بن قطامي، عن عمر بن الخطاب، أنه كان يبيت من وراء العقبة، فقال شعبة: حماري وإزاري في المساكين صدقة إن لم يكن شرقي كذب','1530','718','0');
INSERT INTO [t13041] VALUES (729,'شرقي الجعفي عن سويد بن غفلة. حدثني آدم بن موسى قال: سمعت البخاري، قال شرقي الجعفي، عن سويد بن غفلة، روى عنه جابر، قال البخاري: حديثه ليس بالقائم','1531','718','0');
INSERT INTO [t13041] VALUES (730,'شرحبيل أبو سعد، مديني حدثنا عبد الله بن أحمد قال: حدثنا يحيى بن معين قال: حدثنا حجاج، عن ابن أبي ذئب قال: كان شرحبيل متهما. حدثنا محمد بن عيسى قال: حدثنا محمد بن عبد الله المخرمي قال: حدثنا يزيد بن هارون قال: قال ابن أبي ذئب: حدثنا شرحبيل، هو','1533','718','0');
INSERT INTO [t13041] VALUES (731,'شعبة بن عياش أبو بكر يقال: اسمه شعبة ويقال: اسمه أبو بكر','1534','718','0');
INSERT INTO [t13041] VALUES (732,'شبيب بن شيبة السعدي الخطيب، بصري حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين يقول: شبيب بن شيبة ليس بثقة','1539','718','0');
INSERT INTO [t13041] VALUES (733,'شهر بن حوشب الأشعري، بصري حدثنا محمد بن إسماعيل قال: حدثنا هدية بن عبد الوهاب قال: حدثنا النضر بن شميل، عن ابن عون قال: إن شهرا قد تركوه. حدثنا محمد بن حفص الجوزجاني قال: حدثنا أبو قدامة قال: سمعت النضر بن شميل يقول: سئل ابن عون عن حديث شهر وهو','1541','718','0');
INSERT INTO [t13041] VALUES (734,'شملة بن هزال أبو حتروش الضبي، بصري حدثنا محمد بن عثمان قال: سمعت يحيى، وذكر له أبو بديل، أن يحيى الحماني يحدث عن أبي حتروش شملة بن هزال، فقال يحيى: إنما هذا أبو حتروش شملة، وكان ضعيفا. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول:','1543','718','0');
INSERT INTO [t13041] VALUES (735,'شريك بن عبد الله النخعي القاضي حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: كان يحيى بن سعيد لا يحدث عن شريك، ولا عن إسرائيل، وكان عبد الرحمن، يحدث عنهما. حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت يحيى، حدث عن شريك، وكان عبد','1545','718','0');
INSERT INTO [t13041] VALUES (736,'شبابة بن سوار المدائني','1547','718','0');
INSERT INTO [t13041] VALUES (737,'شبويه المروزي عن ابن المبارك حديث منكر غير محفوظ','1549','718','0');
INSERT INTO [t13041] VALUES (738,'شيخ بن أبي خالد عن حماد بن سلمة، منكر الحديث، لا يتابع على حديثه، وهو مجهول بالنقل','1551','718','0');
INSERT INTO [t13041] VALUES (739,'باب الصاد','1555','0','0');
INSERT INTO [t13041] VALUES (740,'صالح بن أبي الأخضر، بصري حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: سمعت معاذ بن معاذ، وذكر صالح بن أبي الأخضر فقال: سمعته يقول: سمعت من الزهري، وقرأت، عليه، فلا أدري هذا من هذا، فقال يحيى وهو إلى جنبه: لو كان هذا هكذا كان جيدا سمع وعرض','1556','739','0');
INSERT INTO [t13041] VALUES (741,'صالح بن بشير أبو بشر المري القاص بصري حدثنا محمد بن إسماعيل، وأحمد بن علي، قالا: حدثنا الحسن بن علي الحلواني قال: حدثنا عفان قال: حدثت حماد بن سلمة، عن صالح المري، بحديث عن ثابت، فقال: كذب قال: وحدثه همام، بحديث عن صالح المري، فقال: كذب.','1557','739','0');
INSERT INTO [t13041] VALUES (742,'صالح بن بيان السيرافي الغالب على حديثه الوهم، ويحدث بالمناكير عن من لا يحتمل','1560','739','0');
INSERT INTO [t13041] VALUES (743,'صالح بن حيان عن عبد الله بن بريدة. حدثنا جعفر بن محمد قال: حدثنا أحمد بن خالد الخلال قال: قلت لأحمد بن حنبل: حدثنا محمد بن عبيد الطنافسي، عن صالح بن حيان، عن ابن بريدة قال: شربت مع أنس بن مالك الطلي على النصف، فغضب أحمد قال: لا يرى هذا في كتاب','1562','739','0');
INSERT INTO [t13041] VALUES (744,'صالح بن حسان الأنصاري المديني حدثني آدم بن موسى قال: سمعت البخاري قال: صالح بن حسان الأنصاري المديني، عن محمد بن كعب، منكر الحديث. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى قال: صالح بن حسان، مديني ليس حديثه بشيء','1563','739','0');
INSERT INTO [t13041] VALUES (745,'صالح بن راشد، شامي حدثني آدم بن موسى قال: سمعت البخاري قال: صالح بن رشاد، عن عبد الله بن أبي مطرف، روى عنه، رفدة، قال البخاري: ولم يصح حديثه','1565','739','0');
INSERT INTO [t13041] VALUES (746,'صالح بن عبد الله أبو يحيى عن عمرو بن مالك، بصري إسناده غير محفوظ , والمتن معروف بغير هذا الإسناد. حدثني آدم بن موسى قال: سمعت البخاري يقول: صالح بن عبد الله أبو يحيى، عن عمرو بن مالك، عن أبي الجوزاء، فيه نظر','1567','739','0');
INSERT INTO [t13041] VALUES (747,'صالح بن محمد بن زائدة أبو واقد الليثي المدني حدثنا أبو علي محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت عبد الرحمن بن مهدي قال: أخبرني وهيب قال: قدم علينا أبو واقد الليثي البصرة، يعني صالح بن محمد بن زائدة قال: فسمعته يحدث، فلو شئت أن','1569','739','0');
INSERT INTO [t13041] VALUES (748,'صالح بن موسى الطلحي حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى بن معين قال: صالح بن موسى ليس بشيء','1570','739','0');
INSERT INTO [t13041] VALUES (749,'صالح بن عبد القدوس حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين قال: صالح بن عبد القدوس بصري ليس بشيء','1572','739','0');
INSERT INTO [t13041] VALUES (750,'صالح بن رستم أبو عامر الخزاز حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين قال: صالح بن رستم أبو عامر الخزاز ضعيف','1573','739','0');
INSERT INTO [t13041] VALUES (751,'صالح بن سرج حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: صالح بن سرج كان من الخوارج أرى. حدثنا عبد الله قال: حدثنا أبي، حدثنا أبو بكر بن عياش، حدثني أسلم المنقري قال: دخلت على صالح بن سرج في الصلاة وهو يقرأ، وعيناه تسكبان دموعا','1574','739','0');
INSERT INTO [t13041] VALUES (752,'صالح بن مهران مولى عمرو بن حريث ويقال: صالح بن أبي صالح روى عنه أبو بكر بن عياش. " حدثني " حدثنا أحمد بن محمود , قال: حدثنا عثمان بن سعيد , قال: قلت ليحيى بن معين: صالح بن مهران مولى عمرو بن حريث قال: ضعيف','1576','739','0');
INSERT INTO [t13041] VALUES (753,'صالح بن نبهان مولى التوأمة مديني. حدثنا محمد بن إسماعيل، وبشر بن موسى، قالا: حدثنا الحميدي قال: سمعت سفيان، يقول: لقيت صالحا مولى التوأمة سنة خمس أو ست وعشرين ومائة، أو نحوها، وقد تغير، ولقيه الثوري بعدي فجعلت أقول له: أسمعت من ابن عباس؟','1578','739','0');
INSERT INTO [t13041] VALUES (754,'صالح بن مسلم بن رومان عن أبي الزبير، فيه وفي إسناده نظر','1579','739','0');
INSERT INTO [t13041] VALUES (755,'صالح بن يحيى بن المقدام بن معدي كرب حدثني آدم بن موسى قال: سمعت البخاري قال: صالح بن يحيى بن المقدام بن معدي كرب الشامي الكندي فيه نظر','1582','739','0');
INSERT INTO [t13041] VALUES (756,'صدقة بن يزيد الخراساني حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: صدقة بن يزيد كان يكون ناحية بيت المقدس، حديثه حديث ضعيف، وهو ضعيف. حدثني آدم بن موسى قال: سمعت البخاري قال: صدقة بن يزيد الخراساني منكر الحديث','1584','739','0');
INSERT INTO [t13041] VALUES (757,'صدقة بن عبد الله أبو معاوية الدمشقي يعرف بالسمين. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: صدقة بن عبد الله السمين هو شامي الذي يروي عنه الوليد بن مسلم، وهو أبو معاوية ليس بشيء، هو ضعيف الحديث، أحاديثه مناكير، ليس يسوي حديثه شيئا وسألت أبي مرة','1586','739','0');
INSERT INTO [t13041] VALUES (758,'صدقة بن رستم الإسكاف كوفي حدثني آدم بن موسى قال: سمعت البخاري قال: صدقة بن رستم الإسكاف لم يصح حديثه','1587','739','0');
INSERT INTO [t13041] VALUES (759,'صدقة بن يسار، كوفي حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: قيل لسفيان: كان صدقة بن يسار كوفيا؟ قال: كان أصله كوفيا، كان يقول: المختار أحب إلي من أبي وأمي. قلت: نعم كان يقول هذا، ثم ثبت عنه أنه رجع إلى السنة، وهو معه حجة، روى','1589','739','0');
INSERT INTO [t13041] VALUES (760,'صدقة بن موسى الدقيقي، بصري حدثني محمد بن أحمد قال: قال ابن داود قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين يقول: صدقة بن موسى بصري ضعيف','1590','739','0');
INSERT INTO [t13041] VALUES (761,'الصلت بن سالم مدني حدثني آدم بن موسى قال: سمعت البخاري قال: الصلت بن سالم مدني، لا يصح حديثه','1593','739','0');
INSERT INTO [t13041] VALUES (762,'الصلت بن دينار أبو شعيب، بصري حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: سمعت عفان، عن يحيى بن سعيد قال: عاد عوف الصلت بن دينار، فكأن الصلت نال من علي، فقال عوف: ما لك، لا رفع الله جنبك، لا شفاك الله. حدثنا محمد بن عيسى قال: حدثنا','1595','739','0');
INSERT INTO [t13041] VALUES (763,'الصلت بن عبد الرحمن عن الثوري، مجهول بالنقل لا يتابع على حديثه','1596','739','0');
INSERT INTO [t13041] VALUES (764,'صفوان الأصم عن بعض أصحاب النبي صلى الله عليه وسلم. حدثني آدم بن موسى قال: سمعت البخاري قال: صفوان الأصم عن بعض أصحاب النبي صلى الله عليه وسلم، روى عنه الغاز، ولا يتابع على حديثه منكر في المكره','1599','739','0');
INSERT INTO [t13041] VALUES (765,'صفوان بن هبيرة المخدج ولا يتابع على حديثه، لا يعرف إلا به، بصري رحمه الله','1603','739','0');
INSERT INTO [t13041] VALUES (766,'صباح بن يحيى عن الحارث بن حصيرة، ويزيد بن أبي زياد جميعا، كوفي من الشيعة. حدثنا آدم بن موسى قال: سمعت البخاري قال: صباح بن يحيى، عن الحارث بن حصيرة، روى عنه، علي بن هشام فيه نظر','1605','739','0');
INSERT INTO [t13041] VALUES (767,'صباح بن سهل أبو سهل البصري حدثنا آدم بن موسى قال: سمعت البخاري قال: صباح بن سهل أبو سهل البصري منكر الحديث','1607','739','0');
INSERT INTO [t13041] VALUES (768,'صباح بن مجالد شامي مجهول بنقل الحديث، لا يعرف إلا بهذا ولا يتابع عليه','1609','739','0');
INSERT INTO [t13041] VALUES (769,'صباح بن محمد الأحمسي في حديثه وهم، ويرفع الموقوف، كوفي','1611','739','0');
INSERT INTO [t13041] VALUES (770,'صباح بن محارب كوفي سكن الري يخالف في حديثه','1613','739','0');
INSERT INTO [t13041] VALUES (771,'صبيح بغدادي حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى، وأبا خيثمة يقولان: كان صبيح نزل الخلد، وكان كذابا','1615','739','0');
INSERT INTO [t13041] VALUES (772,'صلة بن سليمان العطار الواسطي حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى قال: صلة بن سليمان ليس بثقة، وفي موضع آخر قال: صلة بن سليمان كان واسطيا، وكان ببغداد وكان كذابا. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى','1616','739','0');
INSERT INTO [t13041] VALUES (773,'صغدي بن عبد الله عن قتادة، حديثه غير محفوظ ولا يعرف إلا به، ولا يتابعه إلا من هو دونه أو مثله , بصري','1619','739','0');
INSERT INTO [t13041] VALUES (774,'صغدي بن سنان أبو معاوية العقيلي يقال اسمه عمر، بصري. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى، يقول: صغدي بن سنان ليس بشيء','1621','739','0');
INSERT INTO [t13041] VALUES (775,'صبح بن دينار البلدي','1623','739','0');
INSERT INTO [t13041] VALUES (776,'صاعد مولى الشعبي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: صاعد مولى الشعبي ليس بشيء','1626','739','0');
INSERT INTO [t13041] VALUES (777,'باب الضاد','1628','0','0');
INSERT INTO [t13041] VALUES (778,'الضحاك بن مزاحم، خراساني حدثنا محمد بن موسى قال: حدثنا صالح بن أحمد قال: حدثنا علي بن عبد الله قال: سمعت يحيى يقول: كان شعبة ينكر أن يكون الضحاك بن مزاحم لقي ابن عباس قط قال يحيى: وكان الضحاك بن مزاحم عندنا ضعيفا. حدثنا محمد قال: حدثنا صالح قال:','1629','777','0');
INSERT INTO [t13041] VALUES (779,'الضحاك بن يسار بصري حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى يقول: الضحاك بن يسار بصري ضعيف. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى يقول: الضحاك بن يسار البصري ضعيف','1630','777','0');
INSERT INTO [t13041] VALUES (780,'الضحاك بن نبراس عن ثابت بصري في حديثه وهم. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى بن معين قال: الضحاك بن نبراس ليس بشيء','1632','777','0');
INSERT INTO [t13041] VALUES (781,'الضحاك بن حمرة شامي نزل واسط. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين قال: الضحاك بن حمرة، واسطي كان أصله شاميا، ليس بشيء','1635','777','0');
INSERT INTO [t13041] VALUES (782,'الضحاك بن عباد عن عكرمة مجهول، والراوي عنه متروك','1637','777','0');
INSERT INTO [t13041] VALUES (783,'الضحاك بن زيد الأهوازي عن إسماعيل بن أبي خالد، يخالف في حديثه','1639','777','0');
INSERT INTO [t13041] VALUES (784,'ضرار بن عمرو عن أبي عبد الله الشامي، كوفي. حدثني آدم بن موسى قال: سمعت البخاري قال: ضرار بن عمرو، عن أبي عبد الله الشامي، روى عنه الحكم أبو عمرو، قال البخاري: فيه نظر','1642','777','0');
INSERT INTO [t13041] VALUES (785,'ضرار بن عمرو القاضي حدثنا أحمد بن علي قال: حدثنا أبو همام قال: كان سعيد بن عبد الرحمن قاضيا على بغداد، وكان ينزل عند السيب، قال: فجاء قوم فشهدوا على ضرار أنه زنديق، فقال: قد أبحت دمه، فمن شاء فليقتله، قال: فعزل سعيدا، وأمر لأبي يوسف بمائة ألف','1645','777','0');
INSERT INTO [t13041] VALUES (786,'ضرار بن صرد أبو نعيم الطحان كوفي حدثنا آدم بن موسى قال: سمعت البخاري قال: ضرار بن صرد أبو نعيم الطحان متروك الحديث','1646','777','0');
INSERT INTO [t13041] VALUES (787,'الضحاك بن مخلد أبو عاصم الشيباني حدثنا محمد بن يحيى بن منده الأصبهاني، حدثنا بشر بن آدم ابن بنت أزهر قال: قيل لأبي عاصم: إن يحيى بن سعيد القطان يتكلم فيك، فقال: لست بحي ولا ميت إذا لم أذكره','1647','777','0');
INSERT INTO [t13041] VALUES (788,'باب الطاء','1649','0','0');
INSERT INTO [t13041] VALUES (789,'طلحة بن نافع أبو سفيان واسطي حدثنا عبد الله بن أحمد قال: حدثني أبو بكر بن خلاد قال: سمعت يحيى بن سعيد، قال شعبة: هذه التي يحدث بها أبو سفيان صاحب الأعمش كتاب. حدثنا عبد الله قال: وحدثنا يحيى بن معين قال: حدثنا وكيع قال: سمعت شعبة يقول: حديث أبي','1650','788','0');
INSERT INTO [t13041] VALUES (790,'طلحة بن عمرو الحضرمي حدثنا محمد بن عبد الحميد السهمي قال: حدثنا أحمد بن محمد الحضرمي قال: سألت يحيى بن معين عن طلحة بن عمرو الحضرمي المكي، فقال: ليس بشيء. حدثنا محمد، حدثنا معاوية قال: سمعت يحيى قال: طلحة بن عمرو الحضرمي ضعيف. حدثنا محمد قال:','1651','788','0');
INSERT INTO [t13041] VALUES (791,'طلحة بن زيد الشامي كان يكون بواسط حدثنا آدم بن موسى قال: سمعت البخاري قال: طلحة بن زيد الشامي القرشي منكر الحديث','1656','788','0');
INSERT INTO [t13041] VALUES (792,'طلحة بن يحيى القرشي حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سمعت يحيى يقول: لم يكن طلحة بن يحيى بالقوي، قلت ليحيى: هو أحب إليك أو عمرو بن عثمان؟ قال: عمرو بن عثمان أحب إلي. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: طلحة','1658','788','0');
INSERT INTO [t13041] VALUES (793,'طلحة أبو اليسع بن طلحة ولا يتابع على حديثه','1660','788','0');
INSERT INTO [t13041] VALUES (794,'طارق بن عمار عن أبي الزناد. حدثني آدم بن موسى قال: سمعت البخاري قال: طارق بن عمار، عن أبي الزناد لا يتابع عليه','1662','788','0');
INSERT INTO [t13041] VALUES (795,'طارق بن عبد الرحمن حدثنا عبد الله قال: سمعت أبي قال: موسى الجهني أعجب إلي من طارق، وطارق في حديثه بعض الضعف. حدثنا عبد الله قال: سمعت أبي يقول: طارق بن عبد الرحمن ليس حديثه بذاك','1664','788','0');
INSERT INTO [t13041] VALUES (796,'طفيل بن عمرو التميمي بصري لا يتابع على حديثه، ولا يعرف إلا به. حدثنا آدم بن موسى قال: سمعت البخاري قال: طفيل بن عمرو التميمي، عن صعصعة بن ناجية، قال البخاري: ولا يصح','1667','788','0');
INSERT INTO [t13041] VALUES (797,'طريف بن شهاب أبو سفيان السعدي بصري حدثنا عبد الله بن أحمد قال: قال أبي: أبو سفيان السعدي ليس بشيء، لا يكتب عنه. حدثنا محمد بن زكريا قال: حدثنا محمد بن المثنى ح، وحدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: ما سمعت يحيى، ولا عبد الرحمن يحدثان','1669','788','0');
INSERT INTO [t13041] VALUES (798,'طريف بن سلمان أبو عاتكة بصري حدثني آدم بن موسى قال: سمعت البخاري قال: طريف بن سلمان أبو عاتكة بصري، قال البخاري: منكر الحديث','1672','788','0');
INSERT INTO [t13041] VALUES (799,'طريف بن زيد الحراني مجهول بنقل الحديث " بالنقل "، " حديثه خطأ " غير محفوظ، عن ابن جريج','1674','788','0');
INSERT INTO [t13041] VALUES (800,'طريف روى عنه مسلم بن خالد لا يعرف إلا به، لا يتابع عليه','1676','788','0');
INSERT INTO [t13041] VALUES (801,'طالب بن حبيب بن سهل حدثني آدم بن موسى قال: سمعت البخاري قال: طالب بن حبيب بن سهل يقال جده ضجيع حمزة، قال البخاري: فيه نظر','1679','788','0');
INSERT INTO [t13041] VALUES (802,'الطيب بن محمد اليمامي عن عطاء، يخالف في حديثه','1681','788','0');
INSERT INTO [t13041] VALUES (803,'باب العين','1684','0','0');
INSERT INTO [t13041] VALUES (804,'عبد الله بن إبراهيم الغفاري كان يغلب على حديثه الوهم','1685','803','0');
INSERT INTO [t13041] VALUES (805,'عبد الله بن إسحاق بن الفضل الهاشمي له أحاديث لا يتابع منها على شيء','1687','803','0');
INSERT INTO [t13041] VALUES (806,'عبد الله بن إسماعيل الجوداني بصري عن جرير بن حازم، منكر الحديث، لا يتابع على شيء من حديثه','1689','803','0');
INSERT INTO [t13041] VALUES (807,'عبد الله بن بسر الشامي حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي بن عبد الله قال: سمعت يحيى يقول: رأيت عبد الله بن بسر كان ها هنا، يعني عبد الله بن بسر الشامي الذي روى عنه يوسف السمتي، ومحمد بن حمران، قلت ليحيى: كيف كان؟ قال: لا','1691','803','0');
INSERT INTO [t13041] VALUES (808,'عبد الله بن بشر يروي عنه عبد السلام بن حرب. حدثنا أحمد بن محمود، حدثنا عثمان بن سعيد قال: سألت يحيى، عن عبد الله بن بشر، يروي عنه عبد السلام بن حرب، وهو يروي عن الزهري؟ فقال: ليس بذاك','1693','803','0');
INSERT INTO [t13041] VALUES (809,'عبد الله بن أحمد الحمصي عن ابن جريج ولا يتابع على حديثه','1698','803','0');
INSERT INTO [t13041] VALUES (810,'عبد الله بن بارق الحنفي يماني حدثنا محمد بن عيسى قال: حدثنا عباس قال: سألت يحيى عن عبد الله بن بارق الحنفي فقال: ليس بشيء ويقال: عبد ربه بن بارق. حدثنا عبد الله بن أحمد قال: سألت أبي عن عبد الله بن بارق، فقال: هو ابن أخي سماك الحنفي، وما به بأس','1701','803','0');
INSERT INTO [t13041] VALUES (811,'عبد الله بن بكار الأشعري مجهول في النسب والرواية، حديثه غير محفوظ','1702','803','0');
INSERT INTO [t13041] VALUES (812,'عبد الله بن بريدة بن الحصيب الأسلمي حدثنا الخضر بن داود، حدثنا أحمد بن محمد بن هانئ قال: قلت لأبي عبد الله: ابنا بريدة: سليمان وعبد الله؟ قال: أما سليمان فليس في نفسي منه شيء، وأما عبد الله، ثم سكت، ثم قال: كان وكيع يقول: كانوا لسليمان بن','1704','803','0');
INSERT INTO [t13041] VALUES (813,'عبد الله بن جابر، بصري مجهول بنقل الحديث، يخالف في حديثه','1705','803','0');
INSERT INTO [t13041] VALUES (814,'عبد الله بن جعفر بن نجيح المديني أبو علي بن المديني حدثني الهيثم بن خلف الدوري قال: حدثنا عمرو بن علي قال: سمعت أبا داود يقول: قدم علينا عبد الله بن جعفر، فأتيته أنا وعبد الصمد بن عبد الوارث، فقلنا: سمعت من ضمرة بن سعيد شيئا؟ فقال: لا، فقلنا له','1708','803','0');
INSERT INTO [t13041] VALUES (815,'عبد الله بن حسين أبو حريز قاضي سجستان حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: أبو حريز اسمه عبد الله بن حسين، حديثه منكر. وروى معتمر، عن فضيل، عن أبي حريز، أحاديث مناكير، وكان قاضي سجستان، حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال:','1711','803','0');
INSERT INTO [t13041] VALUES (816,'عبد الله بن حكيم أبو بكر الداهري حدثنا محمد بن عيسى قال حدثنا عباس قال: سمعت يحيى قال: عبد الله بن حكيم أبو بكر الداهري ليس حديثه بشيء','1713','803','0');
INSERT INTO [t13041] VALUES (817,'عبد الله بن حكيم شامي مجهول بالنقل، لا يتابع على حديثه هذا','1718','803','0');
INSERT INTO [t13041] VALUES (818,'عبد الله بن حكيم بن جبير الأسدي هو وأبوه من الغلاة في الرفض، وهما ضعيفان في الحديث','1720','803','0');
INSERT INTO [t13041] VALUES (819,'عبد الله بن خراش بن حوشب حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن خراش بن حوشب، عن العوام بن حوشب، منكر الحديث','1722','803','0');
INSERT INTO [t13041] VALUES (820,'عبد الله بن خليل الحضرمي عن زيد بن أرقم، في القرعة. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن خليل الحضرمي، عن زيد بن أرقم في القرعة، ولا يتابع عليه','1726','803','0');
INSERT INTO [t13041] VALUES (821,'عبد الله بن خالد بن سلمة المخزومي بصري حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن خالد بن سلمة المخزومي ينزل البصرة في بني راشد عن أبيه، روى عنه محمد بن عقبة وغيره، منكر الحديث','1728','803','0');
INSERT INTO [t13041] VALUES (822,'عبد الله بن خيران بغدادي، عن شعبة، والمسعودي لا يتابع على حديثه','1729','803','0');
INSERT INTO [t13041] VALUES (823,'عبد الله بن خلف الطغاوي عن هشام بن حسان وغيره في حديثه وهم ونكارة','1733','803','0');
INSERT INTO [t13041] VALUES (824,'عبد الله بن دينار مولى ابن عمر. حدثنا محمد بن نصر بن منصور الصائغ قال: حدثنا سريح بن يونس قال: حدثنا سفيان قال: حدثنا عبد الله بن دينار، ولم يكن بذاك، ثم صار. حدثنا بشر بن موسى قال: حدثنا الحميدي قال: حدثنا سفيان قال: حدثنا عبد الله بن دينار،','1736','803','0');
INSERT INTO [t13041] VALUES (825,'عبد الله بن داود الواسطي حدثني آدم بن موسى قال: عبد الله بن داود أبو محمد الواسطي قال: البخاري: فيه نظر','1745','803','0');
INSERT INTO [t13041] VALUES (826,'عبد الله بن داهر الرازي رافضي خبيث، عن عبد الله بن عبد القدوس، أشر منه، كلاهما رافضيان','1748','803','0');
INSERT INTO [t13041] VALUES (827,'عبد الله بن ذكوان السمان حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن ذكوان السمان منكر الحديث','1752','803','0');
INSERT INTO [t13041] VALUES (828,'عبد الله بن ذكوان أبو الزناد حدثنا محمد بن عيسى قال: حدثنا صالح قال: قال علي: سمعت سفيان قال: جلست إلى إسماعيل بن محمد بن سعد فقلت: حدثنا أبو الزناد فأخذ كفا من حصى فحصبني به، وكان مالك بن أنس لا يرضى أبا الزناد. حدثنا مقدام بن داود قال: حدثنا','1754','803','0');
INSERT INTO [t13041] VALUES (829,'عبد الله بن رجاء المكي','1755','803','0');
INSERT INTO [t13041] VALUES (830,'عبد الله بن زيد بن أسلم حدثنا أحمد بن محمود قال: حدثنا عثمان بن سعيد قال: سألت يحيى عن عبد الله بن زيد بن أسلم فقال: ضعيف. حدثني محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: أسامة وعبد الله وعبد الرحمن هؤلاء إخوة، ليس حديثهم بشيء','1760','803','0');
INSERT INTO [t13041] VALUES (831,'عبد الله بن زياد بن سليمان بن سمعان المديني حدثنا عمر بن عبد العزيز بن عمران بن مقلاص قال: حدثنا أبي قال: حدثنا أبو زيد عبد الحميد بن الوليد قال: حدثني ابن القاسم قال: سألت مالكا عن ابن سمعان، فقال: كذاب. حدثنا عبد الله بن أحمد قال: حدثني أبي قال','1761','803','0');
INSERT INTO [t13041] VALUES (832,'عبد الله بن زياد عن عكرمة بن عمار. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن زياد، عن عكرمة بن عمار، منكر الحديث','1772','803','0');
INSERT INTO [t13041] VALUES (833,'عبد الله بن سعيد بن أبي سعيد المقبري أبو عباد مدني. حدثنا عبد الله بن أحمد الخفاف النيسابوري قال: حدثنا محمد بن إسماعيل البخاري قال: حدثنا عبد الله بن سعيد قال: حدثنا يحيى بن سعيد قال: جلست إلى عبد الله بن سعيد بن أبي سعيد المقبري وكنيته أبو عباد،','1776','803','0');
INSERT INTO [t13041] VALUES (834,'عبد الله بن سعيد بن أبي هند حدثنا عبد الله بن أحمد قال: كتب إلي أبو بكر بن خلاد قال: سألت يحيى بن سعيد عن عبد الله بن سعيد بن أبي هند قال: كان صالحا يعرف وينكر. حدثنا جعفر بن محمد قال: حدثنا زيد بن أخزم قال: سمعت عبد الله بن داود يقول: رأيت عبد','1777','803','0');
INSERT INTO [t13041] VALUES (835,'عبد الله بن سليمان بن جنادة بن أبي أمية حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن سليمان بن جنادة بن أبي أمية روى عنه، بشر بن رافع، في حديثه نظر','1778','803','0');
INSERT INTO [t13041] VALUES (836,'عبد الله بن سلمة أبو العالية الهمداني الكوفي حدثنا محمد بن إسماعيل قال: حدثنا أبو الوليد قال: حدثنا شعبة، عن عمرو بن مرة قال: سمعت عبد الله بن سلمة يحدثنا، وإنا لنعرف وننكر، حدثنا عبد الله قال: حدثني أبو بكر بن خلاد قال: سمعت يحيى بن سعيد قال:','1783','803','0');
INSERT INTO [t13041] VALUES (837,'عبد الله بن سلمة الأفطس حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: حدثنا يحيى بن سعيد قال: حدثنا محمد بن عبد الله بن أبي مريم قال: شظا ظفر لي وأنا محرم، فسألت سعيد بن المسيب فقال: اقطعه "، فقلت ليحيى: إن الأفطس قال فيه: سألت سالم بن عبد الله','1785','803','0');
INSERT INTO [t13041] VALUES (838,'عبد الله بن سفيان الخزاعي واسطي عن يحيى بن سعيد ولا يتابع على حديثه','1786','803','0');
INSERT INTO [t13041] VALUES (839,'عبد الله بن سنان الزهري كوفي كان ينزل القطيعة قطيعة الربيع ببغداد. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين قال: عبد الله بن سنان كوفي، كان ينزل القطيعة قطيعة الربيع، ليس حديثه بشيء','1788','803','0');
INSERT INTO [t13041] VALUES (840,'عبد الله بن سراقة عن أبي عبيدة بن الجراح. حدثني آدم بن موسى قال: سمعت البخاري قال: لا يعرف سماع عبد الله بن سراقة من أبي عبيدة بن الجراح','1791','803','0');
INSERT INTO [t13041] VALUES (841,'عبد الله بن سيف عن مالك بن مغول حديثه غير محفوظ بالرفع، وهو مجهول بالنقل','1793','803','0');
INSERT INTO [t13041] VALUES (842,'عبد الله بن أبي السري عن محمد بن المنكدر، لا يتابع عليه، ولا يعرف إلا به، وقد رواه غير خلف فأدخل بين عبد الله بن السري ومحمد بن المنكدر رجلين مشهورين بالضعف','1795','803','0');
INSERT INTO [t13041] VALUES (843,'عبد الله بن سيدان المطرودي حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن سيدان المطرودي، قال البخاري: لا يتابع على حديثه','1798','803','0');
INSERT INTO [t13041] VALUES (844,'عبد الله بن شقيق العقيلي حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سمعت يحيى بن سعيد يقول: كان التيمي سيء الرأي في عبد الله بن شقيق، قلت ليحيى: سمعته منه؟ قال: نعم، قلت: وأبو المغيرة القواس؟ قال: كان أشر عنده، قال يحيى:','1800','803','0');
INSERT INTO [t13041] VALUES (845,'عبد الله بن شريك الأسدي كوفي كان ممن يغلو. حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي بن المديني قال: حدثنا سفيان قال: جالسنا عبد الله بن شريك وهو ابن مائة سنة، وكان ممن جاء إلى محمد بن علي بن الحنفية عليهم أبو عبد الله الجدلي','1801','803','0');
INSERT INTO [t13041] VALUES (846,'عبد الله بن شبرمة','1803','803','0');
INSERT INTO [t13041] VALUES (847,'عبد الله بن صفوان بن كلبي الصنعاني حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي بن المديني قال: سمعت هشام بن يوسف سئل عن عبد الله بن صفوان بن كلبي، شيخ من أهل صنعاء، فقال: كان ضعيفا، لم يكن يحفظ الحديث','1805','803','0');
INSERT INTO [t13041] VALUES (848,'عبد الله بن صالح العجلي المقرئ حدثنا الخضر بن داود قال: حدثنا أحمد بن محمد بن هانئ قال: سمعت أبا عبد الله، سئل عن عبد الله بن صالح بن مسلم الذي كان يحدث ببغداد ويقرئ، فقال: ما أدري ما كتبت عنه، وكأنه فيما ظننت لم يعجبه','1807','803','0');
INSERT INTO [t13041] VALUES (849,'عبد الله بن صالح كاتب الليث. حدثنا عبد الله بن أحمد قال: سألت أبي عن عبد الله بن صالح، كاتب الليث فقال: كان أول أمره متماسكا ثم فسد بآخرة، وليس هو بشيء، وسمعت أبي مرة أخرى، وذكر عبد الله بن صالح كاتب الليث بن سعد، فذمه وكرهه وقال: إنه روى عن','1808','803','0');
INSERT INTO [t13041] VALUES (850,'عبد الله بن ظالم عن سعيد بن زيد. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن ظالم، عن سعيد بن زيد، عن النبي صلى الله عليه وسلم، ولا يصح','1809','803','0');
INSERT INTO [t13041] VALUES (851,'عبد الله بن عبد الله بن أبي أمية المخزومي عن أبيه، عن أم سلمة حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن عبد الله بن أبي أمية، عن أبيه، عن أم سلمة في إسناده نظر','1811','803','0');
INSERT INTO [t13041] VALUES (852,'عبد الله بن عبد الله بن أويس، أو أويس بن أبي عامر الأصبحي المدني حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: أبو أويس صدوق وليس بحجة، وفي موضع آخر: أبو أويس مثل فليح فيه ضعف. حدثنا محمد قال: حدثنا معاوية قال: سمعت يحيى قال: أبو أويس','1814','803','0');
INSERT INTO [t13041] VALUES (853,'عبد الله بن عبد الله الأموي لا يتابع على حديثه، ولا يعرف إلا به','1817','803','0');
INSERT INTO [t13041] VALUES (854,'أبو بكر بن عبد الله بن عبيد الله بن محمد بن أبي سبرة بن أبي رهم بن عبد العزى من بني عامر بن لؤي السبري. حدثنا عبد الله بن أحمد قال: قال لي أبي: أبو بكر بن أبي سبرة كان يضع الحديث، ثم قال: قال حجاج: قال لي أبو بكر السبري: عندي سبعون ألف حديث في','1819','803','0');
INSERT INTO [t13041] VALUES (855,'عبد الله بن عبد الرحمن بن يعلى الثقفي الطائي حدثنا أحمد بن محمود الهروي، حدثنا عثمان بن سعيد قال: سألت يحيى بن معين عن عبد الله بن عبد الرحمن، فقال: صويلح','1820','803','0');
INSERT INTO [t13041] VALUES (856,'عبد الله بن عبد الرحمن عن ابن مغفل','1821','803','0');
INSERT INTO [t13041] VALUES (857,'عبد الله بن عبد الرحمن بن أسيد الأزدي عن أنس. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن عبد الرحمن بن أسيد الأزدي، عن أنس، قال البخاري: فيه نظر','1824','803','0');
INSERT INTO [t13041] VALUES (858,'عبد الله بن عبد الرحمن المسمعي بصري عن أبيه، مجهول بالنقل، ولا يتابع على حديثه، ولا يعرف إلا به','1826','803','0');
INSERT INTO [t13041] VALUES (859,'عبد الله بن عبيدة أخو موسى بن عبيدة عن جابر. حدثني محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى، يقول: قد روى موسى بن عبيدة، عن أخيه، عن جابر، ولم يسمع من جابر شيئا','1828','803','0');
INSERT INTO [t13041] VALUES (860,'عبد الله بن عبيد الله أبو عاصم العباداني عن الفضل بن عيسى الرقاشي منكر الحديث، وكان فضل قاصا يرى القدر، وكاد أن يغلب على حديثه الوهم، يعني فضلا.','1830','803','0');
INSERT INTO [t13041] VALUES (861,'عبد الله بن عبد الملك المسعودي أبو عبد الرحمن كان من الشيعة في حديثه نظر','1832','803','0');
INSERT INTO [t13041] VALUES (862,'عبد الله بن عبد الملك بن كرز القرشي عن يزيد بن رومان، وغيره، منكر الحديث','1834','803','0');
INSERT INTO [t13041] VALUES (863,'عبد الله بن عبد العزيز الليثي المدني حدثني آدم بن موسى قال: سمعت البخاري قال: قال لي إبراهيم بن المنذر الحزامي، عن أبي ضمرة: كان عبد الله بن عبد العزيز قد خلط','1836','803','0');
INSERT INTO [t13041] VALUES (864,'عبد الله بن عبد العزيز الزهري عن أخيه محمد بن عبد العزيز حديثه غير محفوظ، ولا يعرف إلا به، وليس له أصل من حديث الزهري','1839','803','0');
INSERT INTO [t13041] VALUES (865,'عبد الله بن عبد العزيز بن أبي رواد أخو عبد المجيد، عن أبيه أحاديثه مناكير، غير محفوظة ليس ممن يقيم الحديث','1841','803','0');
INSERT INTO [t13041] VALUES (866,'عبد الله بن عبد القدوس كوفي سكن الري. حدثنا عبد الله بن أحمد بن حنبل قال: سألت يحيى عن عبد الله بن عبد القدوس، فقال: ليس بشيء، رافضي خبيث. حدثنا أحمد بن علي الأبار قال: سألت زنيجا شيخا رازيا، عن عبد الله بن عبد القدوس فقال: تركته، لم أكتب عنه','1844','803','0');
INSERT INTO [t13041] VALUES (867,'عبد الله بن عمر بن حفص بن عاصم بن عمر بن الخطاب حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: كان يحيى لا يحدث عن عبد الله بن عمر، وكان عبد الرحمن يحدث عنه','1846','803','0');
INSERT INTO [t13041] VALUES (868,'عبد الله بن عمر بن أبان القرشي مشكدانة كوفي','1848','803','0');
INSERT INTO [t13041] VALUES (869,'عبد الله بن عثمان بن خثيم','1850','803','0');
INSERT INTO [t13041] VALUES (870,'عبد الله بن علي بن يزيد بن ركانة ولا يتابع على حديثه، مضطرب الإسناد','1852','803','0');
INSERT INTO [t13041] VALUES (871,'عبد الله بن علي بن بعجة عن أبيه في حديثه نظر. حدثني مصعب بن إبراهيم بن حمزة قال: حدثنا أبي قال: حدثنا إبراهيم بن علي الرافعي قال: سمعت علي بن عبد الله بن علي بن بعجة يحدث، عن أبيه، عن جده، قال: كأني أنظر إلى علي بن أبي طالب يوم قتل عثمان مقبلا','1855','803','0');
INSERT INTO [t13041] VALUES (872,'عبد الله بن عامر الأسلمي حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي الحلواني قال: قال أبو نعيم: كتبت عن عبد الله بن عامر الأسلمي، ها هنا بالكوفة، قال: وكان وكان، وحرك يده. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى قال: عبد','1856','803','0');
INSERT INTO [t13041] VALUES (873,'عبد الله بن عمرو بن مرة الهمداني كوفي','1857','803','0');
INSERT INTO [t13041] VALUES (874,'عبد الله بن عمرو الواقعي بصري حدثنا عبد الله بن الحسن، عن علي بن المديني قال: عبد الله بن عمرو بن حسان الواقعي، كان يضع الحديث','1859','803','0');
INSERT INTO [t13041] VALUES (875,'عبد الله بن عميرة عن الأحنف بن قيس. حدثني آدم بن موسى قال: سمعت البخاري يقول: عبد الله بن عبد الله بن عميرة، عن الأحنف بن قيس، عن العباس بن عبد المطلب، ولا نعلم له سماعا من الأحنف','1861','803','0');
INSERT INTO [t13041] VALUES (876,'عبد الله بن عصمة الجزري عن حماد بن سلمة " لا يقيم الحديث " يرفع الأحاديث ويزيد في الحديث','1863','803','0');
INSERT INTO [t13041] VALUES (877,'عبد الله بن عطية بن سعد عن أخيه الحسن بن عطية لا يتابع على حديثه، ولهم أخ ثالث يقال له عمرو بن عطيه يقاربهما في الضعف وقلة الضبط','1865','803','0');
INSERT INTO [t13041] VALUES (878,'عبد الله بن عيسى الجندي عن محمد بن أبي محمد، عن أبيه، عن أبي هريرة إسناده مجهول فيه نظر','1867','803','0');
INSERT INTO [t13041] VALUES (879,'عبد الله بن عيسى الخزاز أبو خلف بصري عن يونس بن عبيد، لا يتابع على أكثر حديثه','1869','803','0');
INSERT INTO [t13041] VALUES (880,'عبد الله بن عمران القرشي عن مالك بن دينار، لا يتابع على حديثه','1871','803','0');
INSERT INTO [t13041] VALUES (881,'عبد الله بن عرادة السدوسي يخالف في حديثه، ويهم كثيرا','1873','803','0');
INSERT INTO [t13041] VALUES (882,'عبد الله بن الفضل الخراساني أبو رجاء منكر الحديث','1875','803','0');
INSERT INTO [t13041] VALUES (883,'عبد الله بن فروخ خراساني حدث عنه ابن أبي مريم. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن فروخ خراساني حدث عنه ابن أبي مريم، يعرف وينكر','1877','803','0');
INSERT INTO [t13041] VALUES (884,'عبد الله بن قيس الرقاشي عن أيوب، حديثه غير محفوظ، ولا يتابع عليه، ولا يعرف إلا به','1879','803','0');
INSERT INTO [t13041] VALUES (885,'عبد الله بن قنبر عن أبيه عن علي، لا يتابع على حديثه من جهة تثبت','1881','803','0');
INSERT INTO [t13041] VALUES (886,'عبد الله بن قبيصة الفزاري كثير الوهم، لا يتابع على كثير من حديثه','1883','803','0');
INSERT INTO [t13041] VALUES (887,'عبد الله بن كيسان المروزي في حديثه وهم كثير','1886','803','0');
INSERT INTO [t13041] VALUES (888,'عبد الله بن كرز عن نافع. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن كرز، عن نافع، روى عنه، عبيدة بن حسان، في حديثه نظر','1890','803','0');
INSERT INTO [t13041] VALUES (889,'عبد الله بن أبي لبيد مولى الأخنسي مدني كان يرى القدر، يخالف في بعض حديثه. حدثنا عبد الله بن أحمد قال: حدثنا الحميدي قال: حدثنا سفيان قال: حدثنا ابن أبي لبيد، وكان من عباد أهل المدينة، وكان يرى القدر. وحدثنا جعفر بن محمد بن الحسن قال: حدثنا عبد','1892','803','0');
INSERT INTO [t13041] VALUES (890,'عبد الله بن لهيعة بن عقبة الحضرمي المصري حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن لهيعة بن عقبة الحضرمي مصري، أبو عبد الرحمن، ويقال الغافقي قاضي مصر. قال البخاري: قال الحميدي، عن يحيى بن سعيد: كان لا يراه شيئا. حدثنا الصائغ قال:','1894','803','0');
INSERT INTO [t13041] VALUES (891,'عبد الله بن محمد بن عبد الله بن زيد عن أبيه عن جده رضي الله عنه. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن محمد بن عبد الله بن زيد، عن أبيه عن جده، لم يذكر سماع بعضهم من بعض','1896','803','0');
INSERT INTO [t13041] VALUES (892,'عبد الله بن محمد بن عجلان مدني منكر الحديث، لا يتابع على هذين الحديثين','1898','803','0');
INSERT INTO [t13041] VALUES (893,'عبد الله بن محمد العدوي سمع عمر بن عبد العزيز، ولا يصح حديثه من هذا الطريق، ويصح من طريق آخر','1902','803','0');
INSERT INTO [t13041] VALUES (894,'عبد الله بن محمد العدوي عن علي بن زيد. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن محمد العدوي، عن علي بن زيد، روى عنه الوليد بن بكير أبو جناب، منكر الحديث','1904','803','0');
INSERT INTO [t13041] VALUES (895,'عبد الله بن محمد بن عقيل بن أبي طالب حدثنا إبراهيم البسري قال: حدثنا سعيد بن نصير قال: قلت ليحيى بن معين: إن ابن عيينة كان يقول: أربعة من قريش يمسك عن حديثهم، قلت: من هم؟ قال: فلان، وعلي بن زيد، ويزيد بن أبي زياد، وعبد الله بن محمد بن عقيل،','1906','803','0');
INSERT INTO [t13041] VALUES (896,'عبد الله بن محمد بن عبد الملك بصري حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن محمد بن عبد الملك سمع عبد الملك بن مسلم، سمع منه جعفر بن سليمان، قال البخاري: فيه نظر','1908','803','0');
INSERT INTO [t13041] VALUES (897,'عبد الله بن محمد بن يحيى بن عروة بن الزبير عن هشام بن عروة، لا يتابع على كثير من حديثه','1910','803','0');
INSERT INTO [t13041] VALUES (898,'عبد الله بن محمد بن عمار حدثنا أحمد بن محمود الهروي قال: حدثنا عثمان بن سعيد قال: قلت ليحيى بن معين: عبد الله بن محمد بن عمار بن سعد، وعمار، وعمر ابني حفص بن عمر بن سعد، عن آبائهم، عن أجدادهم، كيف حال هؤلاء؟ قال: ليسوا بشيء','1912','803','0');
INSERT INTO [t13041] VALUES (899,'عبد الله بن محمد بن المغيرة كوفي سكن مصر عن الثوري، ومسعر، وكامل، يخالف في بعض حديثه، ويحدث بما لا أصل له','1914','803','0');
INSERT INTO [t13041] VALUES (900,'عبد الله بن ميمون القداح حدثنا آدم بن موسى قال: سمعت البخاري قال: عبد الله بن ميمون القداح، عن جعفر بن محمد، ذاهب الحديث','1918','803','0');
INSERT INTO [t13041] VALUES (901,'عبد الله بن مسلم بن هرمز مكي حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: كان يحيى وعبد الرحمن لا يحدثان عن ابن مسلم بن هرمز يحدث عنه الثوري، ضعيف ليس بشيء. حدثنا محمد، قال: حدثنا عباس قال: سمعت يحيى قال: عبد الله بن مسلم بن هرمز ضعيف','1920','803','0');
INSERT INTO [t13041] VALUES (902,'عبد الله بن المؤمل المخزومي مكي لا يتابع على كثير من حديثه. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: أحاديث عبد الله بن المؤمل مناكير. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى يقول: عبد الله بن المؤمل صالح الحديث. حدثنا محمد قال: حدثنا معاوية','1921','803','0');
INSERT INTO [t13041] VALUES (903,'عبد الله بن المنكدر بن محمد بن المنكدر عن أبيه ولا يتابع عليه، ولا يعرف إلا به','1924','803','0');
INSERT INTO [t13041] VALUES (904,'عبد الله بن مسعر بن كدام عن أبيه لا يتابع عليه، ولا يعرف إلا به','1926','803','0');
INSERT INTO [t13041] VALUES (905,'عبد الله بن المثنى الأنصاري عن ثمامة وغيره ولا يتابع على أكثر حديثه، يعني عبد الله بن المثنى الأنصاري','1928','803','0');
INSERT INTO [t13041] VALUES (906,'عبد الله بن المطلب العجلي مجهول، وحديثه منكر غير محفوظ','1930','803','0');
INSERT INTO [t13041] VALUES (907,'عبد الله بن معبد الزماني روى عنه غيلان بن جرير. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن معبد الزماني روى عنه، غيلان بن جرير، وقتادة يحدث عن أبي قتادة، ولا يعرف سماعه من أبي قتادة','1932','803','0');
INSERT INTO [t13041] VALUES (908,'عبد الله بن المسور بن عون بن جعفر بن أبي طالب أبو جعفر الهاشمي المدائني روى عنه خالد بن أبي كريمة. حدثنا الحسن بن علي الرازي قال: حدثنا يحيى بن المغيرة، ح وحدثنا زكريا بن يحيى الحلواني قال: حدثنا عثمان بن أبي شيبة قالا: حدثنا جرير، عن رقبة قال:','1934','803','0');
INSERT INTO [t13041] VALUES (909,'عبد الله بن معاوية بن عاصم بن المنذر بن الزبير الزبيري يحدث عن هشام بن عروة، بمناكير لا أصل لها','1936','803','0');
INSERT INTO [t13041] VALUES (910,'عبد الله بن موسى التيمي حدثني جعفر بن أحمد قال: حدثنا محمد بن إدريس، عن كتاب أبي الوليد بن أبي الجارود، عن يحيى بن معين قال: عبد الله بن موسى صدوق، وهو كثير الخطأ','1938','803','0');
INSERT INTO [t13041] VALUES (911,'عبد الله بن معاذ الصنعاني حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن معاذ الصنعاني قال ابن معين: كان عبد الرزاق يكذبه، وقال هشام: هو صدوق. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: رأيت عبد الله بن معاذ الصنعاني بمكة ولم أكتب عنه شيئا','1941','803','0');
INSERT INTO [t13041] VALUES (912,'عبد الله بن مكنف عن أنس. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن مكنف عن أنس، فيه نظر','1942','803','0');
INSERT INTO [t13041] VALUES (913,'عبد الله بن ميسرة أبو إسحاق الكوفي حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: سمعت يحيى بن سعيد، قال له رجل: إن يزيد بن هارون حدثنا، عن عبد الله بن ميسرة، عن أبي غفار، أن ابن عمر، كان يمسح على الخرقة، فأنكره وجعل يضحك. حدثنا محمد قال:','1944','803','0');
INSERT INTO [t13041] VALUES (914,'عبد الله بن أبي مرة الزوفي عن خارجة بن حذافة. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن مرة الزوفي، عن خارجة بن حذافة، قال البخاري: ولا يعرف سماع بعضهم من بعض','1945','803','0');
INSERT INTO [t13041] VALUES (915,'عبد الله بن محرر الجزري','1947','803','0');
INSERT INTO [t13041] VALUES (916,'عبد الله بن نافع بن العمياء روى عنه عمران بن أبي أنس. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن نافع بن العمياء روى عنه، عمران بن أبي أنس، قال البخاري: لم يصح حديثه','1950','803','0');
INSERT INTO [t13041] VALUES (917,'عبد الله بن نافع الصائغ المدني حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن نافع الصائغ أبو محمد المدني، عن مالك، يعرف وينكر في حفظه، وكتابه أصح','1952','803','0');
INSERT INTO [t13041] VALUES (918,'عبد الله بن نافع مولى ابن عمر، عن أبيه. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن نافع مولى ابن عمر، عن أبيه، منكر الحديث. حدثني الفضل بن جعفر قال: حدثنا إسماعيل قال: حدثنا علي قال: سمعته يقول: روى عبد الله بن نافع، أحاديث منكرة.','1953','803','0');
INSERT INTO [t13041] VALUES (919,'عبد الله بن نجي الحضرمي حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن نجي فيه نظر','1955','803','0');
INSERT INTO [t13041] VALUES (920,'عبد الله بن واقد عن أبي الزبير، وقتادة. حدثني محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى قال: عبد الله بن واقد، عن قتادة، وأبي الزبير ليس بشيء','1957','803','0');
INSERT INTO [t13041] VALUES (921,'عبد الله بن واقد أبو قتادة الحراني عن ابن جريج. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن واقد أبو قتادة الحراني، قال البخاري: تركوه، منكر الحديث. وحدثنا عبد الله بن أحمد قال: سمعت يحيى يقول: أبو قتادة عبد الله بن واقد الحراني ليس','1959','803','0');
INSERT INTO [t13041] VALUES (922,'عبد الله بن أبي هند عن أبي عبيدة كوفي. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن أبي هند، عن أبي عبيدة بن عبد الله، روى عنه أبو مالك الأشجعي، قال البخاري: ولا يصح حديثه','1960','803','0');
INSERT INTO [t13041] VALUES (923,'عبد الله بن هانئ أبو الزعراء سمع ابن مسعود، وفيه كلام ليس في حديث الناس. حدثني آدم قال: سمعت البخاري قال: عبد الله بن هانئ أبو الزعراء الكندي كوفي سمع ابن مسعود، سمع منه سلمة بن كهيل في الشفاعة، ولا يتابع على حديثه','1962','803','0');
INSERT INTO [t13041] VALUES (924,'عبد الله بن يزيد الهذلي مدني حدثني آدم قال: سمعت البخاري قال: عبد الله بن يزيد الهذلي يقال ابن فنطس، قال البخاري: يقال حسين بن عبد الله بن عبيد الله بن عباس، وعبد الله بن يزيد بن فنطس، يتهمان بالزندقة','1965','803','0');
INSERT INTO [t13041] VALUES (925,'عبد الله بن يسار وهو ابن أبي ليلى، عن علي. حدثنا آدم قال: سمعت البخاري قال: عبد الله بن يسار وهو ابن أبي ليلى، عن علي، ولا يصح','1966','803','0');
INSERT INTO [t13041] VALUES (926,'عبد الله بن يسار وهو ابن أبي نجيح حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن أبي نجيح كان يتهم بالاعتزال والقدر. حدثنا أحمد بن علي الأبار قال: حدثنا أبو غسان قال: سمعت جريرا يقول: رأيت ابن أبي نجيح ولم أكتب عنه، كان يرى القدر. حدثنا','1968','803','0');
INSERT INTO [t13041] VALUES (927,'عبد الله بن يحيى التوأم ويقال: عبادة حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى، يقول: التوأم، عن ابن أبي مليكة، ضعيف','1969','803','0');
INSERT INTO [t13041] VALUES (928,'عبد الله بن يعلى بن مرة الثقفي حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله بن يعلى بن مرة الثقفي فيما روى ابنه عمر عنه فيه نظر، وروى عبد الرحمن بن إسحاق، عنه فيه نظر. حدثنا الحسن بن علي قال: حدثنا أحمد بن إشكاب قال: حدثنا القاسم بن مالك','1971','803','0');
INSERT INTO [t13041] VALUES (929,'عبد الله الهمداني عن أبي موسى. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الله الهمداني، ولا يصح','1973','803','0');
INSERT INTO [t13041] VALUES (930,'عبد الله والد منير عن سعد بن أبي ذباب. حدثني آدم قال: سمعت البخاري قال: عبد الله والد منير ولم يصح','1975','803','0');
INSERT INTO [t13041] VALUES (931,'عبد الرحمن بن إبراهيم دمشقي يحدث عن الليث بن سعد مجهول بالنقل، وحديثه موضوع لا أصل له','1977','803','0');
INSERT INTO [t13041] VALUES (932,'عبد الرحمن بن إبراهيم القاص بصري ويقال الكرماني. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عبد الرحمن بن إبراهيم ليس بشيء','1979','803','0');
INSERT INTO [t13041] VALUES (933,'عبد الرحمن بن إسحاق المدني القرشي حدثنا محمد بن عيسى قال: حدثنا محمد بن علي الوراق قال: سمعت أحمد بن حنبل، يقول: عبد الرحمن بن إسحاق المدني روى عنه، بشر بن المفضل، ويزيد بن زريع، لا يعرف بالمدينة، كان قدم عليهم البصرة، كان يحيى لا يستمرئه.','1982','803','0');
INSERT INTO [t13041] VALUES (934,'عبد الرحمن بن إسحاق أبو شيبة الواسطي حدثنا محمد بن عثمان قال: سألت يحيى بن معين عن عبد الرحمن بن إسحاق الواسطي، فقال: كان ضعيفا، وهو ابن أخت النعمان بن سعد. حدثنا محمد بن عيسى قال: حدثنا محمد بن علي قال: قلت لأبي عبد الله: عبد الرحمن بن إسحاق','1984','803','0');
INSERT INTO [t13041] VALUES (935,'عبد الرحمن بن أيوب السكوني عن عطاف، ولا يتابع عليه','1987','803','0');
INSERT INTO [t13041] VALUES (936,'عبد الرحمن بن أبي أمية الثقفي كوفي في حديثه وهم','1990','803','0');
INSERT INTO [t13041] VALUES (937,'عبد الرحمن بن بشر الغطفاني مجهول في النسب والرواية، حديثه غير محفوظ','1992','803','0');
INSERT INTO [t13041] VALUES (938,'عبد الرحمن بن أبي بكر المليكي حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الرحمن بن أبي بكر المليكي منكر الحديث. وحدثنا محمد قال: حدثنا معاوية قال: سمعت يحيى، يقول: عبد الرحمن بن أبي بكر بن أبي مليكة ضعيف','1994','803','0');
INSERT INTO [t13041] VALUES (939,'عبد الرحمن بن ثابت بن الصامت الأنصاري حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الرحمن بن ثابت بن الصامت، عن أبيه عن جده، قاله ابن أبي حبيبة، ولم يصح حديثه','1998','803','0');
INSERT INTO [t13041] VALUES (940,'عبد الرحمن بن ثابت بن ثوبان الشامي حدثنا محمد بن عيسى قال: حدثنا محمد بن علي قال: سمعت أحمد بن حنبل، قيل له: عبد الرحمن بن ثابت بن ثوبان، كيف هو؟ قال: لم يكن بالقوي في الحديث. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى يقول:','2000','803','0');
INSERT INTO [t13041] VALUES (941,'عبد الرحمن بن ثابت عن أنس مجهول بنقل الحديث، لا يتابع على حديثه','2004','803','0');
INSERT INTO [t13041] VALUES (942,'عبد الرحمن بن ثروان أبو قيس الأودي حدثنا عبد الله بن أحمد قال: سألت أبي عن أبي قيس عبد الرحمن بن ثروان، فقال: هو كذا وكذا، وحرك يده، وهو يخالف في أحاديث','2007','803','0');
INSERT INTO [t13041] VALUES (943,'عبد الرحمن بن حريز الليثي ويقال الفزاري مجهول بالنقل، لا يتابع على حديثه','2009','803','0');
INSERT INTO [t13041] VALUES (944,'عبد الرحمن بن حرملة المدني حدثنا عبد الله بن أحمد قال: حدثني أبو بكر بن خلاد قال: سمعت يحيى، وسئل عن ابن حرملة، فضعفه ولم يرضه. حدثنا محمد بن عيسى قال: حدثنا إبراهيم بن سعيد قال: حدثنا علي بن عبد الله قال: سمعت يحيى بن سعيد يقول: محمد بن عمرو','2011','803','0');
INSERT INTO [t13041] VALUES (945,'عبد الرحمن بن خضير حدثنا محمد بن إسماعيل، ومحمد بن أيوب، قالا: حدثنا محمود بن غيلان قال: سمعت وكيعا، وسئل عن عبد الرحمن بن خضير، فقال: نعم، كان يروي عن أبي نجيح، وكان أبو نجيح ثقة','2012','803','0');
INSERT INTO [t13041] VALUES (946,'عبد الرحمن بن حرملة عن ابن مسعود حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الرحمن بن حرملة عن ابن مسعود، روى عنه القاسم بن حسان، ولا يصح حديثه','2013','803','0');
INSERT INTO [t13041] VALUES (947,'عبد الرحمن بن حجوة عن عمرو بن رؤبة، حديثه غير محفوظ وليس بمشهور بالنقل','2015','803','0');
INSERT INTO [t13041] VALUES (948,'عبد الرحمن بن دينار أبو يحيى القتات كوفي حدثني آدم بن موسى قال: سمعت البخاري قال: قال لي يوسف بن يعقوب الصفار: سألت ابن أبي يحيى القتات عن اسم أبي يحيى القتات، فقال: اسمه عبد الرحمن بن دينار، قال يوسف: قلت لأبي نعيم فاستطرفه وقال: لم يكن هذا','2017','803','0');
INSERT INTO [t13041] VALUES (949,'عبد الرحمن بن زيد بن أسلم حدثنا عبد الله قال: سمعت أبي يضعف عبد الرحمن بن زيد بن أسلم قال: روى حديثا منكرا، حديث: أحلت لنا ميتتان ودمان. حدثنا محمد بن عبد الحميد قال: حدثنا أحمد بن محمد الحضرمي قال: سمعت يحيى يقول: عبد الرحمن بن زيد بن أسلم ليس','2019','803','0');
INSERT INTO [t13041] VALUES (950,'عبد الرحمن بن زياد بن أنعم الإفريقي حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت يحيى قال: حدثت هشام بن عروة، بحديث عن الإفريقي، عن ابن عمر في الوضوء، قال: هذا حديث مشرقي، وضعف يحيى بن سعيد الإفريقي، قال: قد كنت كتبت عنه كتابا','2022','803','0');
INSERT INTO [t13041] VALUES (951,'عبد الرحمن بن سلمان عن عقيل. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الرحمن بن سلمان، عن عقيل، سمع منه ابن وهب، قال البخاري: فيه نظر','2026','803','0');
INSERT INTO [t13041] VALUES (952,'عبد الرحمن بن سليمان بن الأصبهاني حدثنا محمد بن عيسى قال: حدثنا العباس قال: سمعت يحيى قال: عبد الرحمن بن سليمان بن الأصبهاني ليس بشيء، روى عنه حمران بن الأصبهاني وغيره','2028','803','0');
INSERT INTO [t13041] VALUES (953,'عبد الرحمن بن سليمان بن الغسيل حدثنا أحمد بن محمود الهروي قال: حدثنا عثمان بن سعيد قال: سألت يحيى بن معين عن عبد الرحمن بن الغسيل، فقال: صويلح','2029','803','0');
INSERT INTO [t13041] VALUES (954,'عبد الرحمن بن ضباب الأشعري حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الرحمن بن ضباب الأشعري، عن عبد الرحمن بن غنم، قال البخاري: فيه نظر','2030','803','0');
INSERT INTO [t13041] VALUES (955,'عبد الرحمن بن عثمان أبو بحر البكراوي الثقفي حدثنا عبد الله بن أحمد قال: سألت أبي عن عبد الرحمن بن عثمان البكراوي، فقال: طرح الناس حديثه. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: أبو بحر البكراوي ضعيف الحديث. حدثني آدم بن موسى قال:','2032','803','0');
INSERT INTO [t13041] VALUES (956,'عبد الرحمن بن عبد الله بن عتبة بن مسعود المسعودي كوفي تغير في آخر عمره، في حديثه اضطراب. حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: سمعت يحيى، يقول: رأيت المسعودي سنة رآه عبد الرحمن فلم أكلمه. حدثنا محمد قال: حدثنا عمرو بن علي قال: سمعت معاذ','2035','803','0');
INSERT INTO [t13041] VALUES (957,'عبد الرحمن بن أبي ليلى','2036','803','0');
INSERT INTO [t13041] VALUES (958,'عبد الرحمن بن عبد الله بن عمر العمري المدني حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: عبد الرحمن بن عبد الله بن عمر العمري ليس يسوى حديثه شيئا، حرقنا حديثه، سمعت منه ثم تركناه. وسمعت أبي مرة أخرى يقول: عبد الرحمن بن عبد الله العمري ليس ممن يروى','2038','803','0');
INSERT INTO [t13041] VALUES (959,'عبد الرحمن بن عبد الله بن دينار حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: لم أسمع عبد الرحمن يحدث عن عبد الله بن عبد الرحمن بن عبد الله بن دينار بشيء قط. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: حدث يحيى القطان، عن عبد الرحمن بن عبد','2041','803','0');
INSERT INTO [t13041] VALUES (960,'عبد الرحمن بن عبد الله بن عطية عن ابن جريج، مجهول بنقل الحديث، لا يتابع على هذا على حديثه','2043','803','0');
INSERT INTO [t13041] VALUES (961,'عبد الرحمن بن أبي الزناد واسم أبي الزناد عبد الله بن ذكوان المدني. حدثنا علي بن أحمد بن سليمان قال: حدثنا سليمان بن أيوب البغدادي قال: سمعت يحيى بن معين، يقول: إني لأعجب ممن يعد في المحدثين فليحا وابن أبي الزناد قال: وسمعت علي بن المديني، وذكر ابن','2045','803','0');
INSERT INTO [t13041] VALUES (962,'عبد الرحمن بن عبد الله أبو سعيد مولى بني هاشم. حدثني الخضر بن داود قال: حدثنا أحمد بن محمد قال: سئل أبو عبد الله عن أبي سعيد، مولى بني هاشم، ما كان اسمه؟ قال: عبد الرحمن بن عبد الله، فقال رجل: كان يلقب جرذقة، فقال أبو عبد الله برأسه، أي نعم','2046','803','0');
INSERT INTO [t13041] VALUES (963,'عبد الرحمن بن علي بن عجلان القرشي عن ابن جريج مجهول بنقل الحديث، حديثه غير محفوظ إلا عن عطاء، من قوله','2047','803','0');
INSERT INTO [t13041] VALUES (964,'عبد الرحمن بن قيس أبو معاوية الزعفراني حدثنا عبد الله بن أحمد بن حنبل قال: سألت أبي عن عبد الرحمن بن قيس الزعفراني، فقال: لم يكن حديثه بشيء، وسألت أبي مرة أخرى عن عبد الرحمن بن قيس الزعفراني فقال: كان جارا لحماد بن مسعدة، يحدث عن ابن عون، قد رأيته','2051','803','0');
INSERT INTO [t13041] VALUES (965,'عبد الرحمن بن أبي قيس عن ابن رفاعة بن رافع، عن أبيه عن جده. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الرحمن بن أبي قيس، عن ابن رفاعة بن رافع، عن أبيه عن جده، قال البخاري: لا يتابع على حديثه','2055','803','0');
INSERT INTO [t13041] VALUES (966,'عبد الرحمن بن محمد بن أبي بكر بن محمد بن عمرو بن حزم حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الرحمن بن محمد بن أبي بكر بن محمد بن عمرو بن حزم مدني، روى عنه الواقدي، عجائب','2057','803','0');
INSERT INTO [t13041] VALUES (967,'عبد الرحمن بن مسلمة عن أبي عبيدة بن الجراح. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الرحمن بن مسلمة، عن أبي عبيدة بن الجراح، قال البخاري: لا يصح','2059','803','0');
INSERT INTO [t13041] VALUES (968,'عبد الرحمن بن معاوية أبو الحويرث حدثنا محمد بن أحمد قال: حدثنا العباس قال: سمعت يحيى قال: أبو الحويرث ليس يحتج بحديثه. حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي، ح وحدثنا عبد الله بن أحمد قال: حدثنا عباس بن عبد العظيم، ح وحدثنا زكريا بن يحيى','2061','803','0');
INSERT INTO [t13041] VALUES (969,'عبد الرحمن بن مالك بن مغول حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: حرقنا حديث عبد الرحمن بن مالك بن مغول من دهر من الدهور، ليس بشيء','2063','803','0');
INSERT INTO [t13041] VALUES (970,'عبد الرحمن بن مسهر أخو علي بن مسهر يقال: كان قاضي الجبل، يكنى أبا الهيثم. حدثنا محمد بن سعيد بن بلج الرازي قال: سمعت عبد الرحمن بن الحكم بن بشير يذكر عن عبد الله بن إدريس قال: عاتبت أبا يوسف في أخ لعلي بن مسهر كان استقضاه، فظهر منه خيانة وجور،','2067','803','0');
INSERT INTO [t13041] VALUES (971,'عبد الرحمن بن محمد المحاربي حدثنا عبد الله بن أحمد بن حنبل قال: عرضت على أبي حديثا','2073','803','0');
INSERT INTO [t13041] VALUES (972,'عبد الرحمن بن أبي نصر عن أبيه، عن علي، رضي الله عنه. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الرحمن بن أبي نصر، عن أبيه عن علي في: " القارن يطوف طوافين "، قال البخاري: ولا يصح','2075','803','0');
INSERT INTO [t13041] VALUES (973,'عبد الرحمن بن نمر اليحصبي شامي، عن الزهري. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى، يقول: ابن نمر الذي روى عن الزهري، ضعيف','2077','803','0');
INSERT INTO [t13041] VALUES (974,'عبد الرحمن بن هانئ أبو نعيم النخعي حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سألت أبا نعيم عن أبي نعيم النخعي، فقال: من جالسه عرف ضعفه. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: أبو نعيم النخعي ليس بشيء، وعرضت عليه حديثه، عن شريك، عن','2078','803','0');
INSERT INTO [t13041] VALUES (975,'عبد الرحمن بن يزيد بن تميم حدثنا عبد الله بن أحمد قال: سألت أبي عن عبد الرحمن بن يزيد بن تميم، فقال: قلب أحاديث شهر بن حوشب، صيرها فجعلها أحاديث الزهري، وضعفه. حدثني الخضر بن داود قال: حدثنا أحمد بن محمد قال: سمعت الهيثم بن خارجة، وذكر أبا عبد','2080','803','0');
INSERT INTO [t13041] VALUES (976,'عبد الرحمن بن يحيى بن سعيد الأنصاري مجهول بالنقل، لا يقيم الحديث','2081','803','0');
INSERT INTO [t13041] VALUES (977,'عبد الرحمن بن يحيى العذري عن مالك مجهول أيضا، لا يقيم الحديث من جهته','2083','803','0');
INSERT INTO [t13041] VALUES (978,'عبد الرحمن بن يوسف عن الأعمش مجهول أيضا في النسب والرواية، حديثه غير محفوظ، ولا يعرف إلا به','2086','803','0');
INSERT INTO [t13041] VALUES (979,'عبد الرحمن بن يامين كوفي حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الرحمن بن يامين كوفي منكر الحديث، وقال أحمد عن أبي يحيى الحماني قال: حدثنا عبد الرحمن أبو العلاء','2088','803','0');
INSERT INTO [t13041] VALUES (980,'عبد الرحمن السدي عن داود بن أبي هند، مجهول أيضا، ولا يتابع على حديثه ولا يعرف من وجه يصح','2090','803','0');
INSERT INTO [t13041] VALUES (981,'عبد الرحمن مولى سليمان بن عبد الملك عن أنس حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الرحمن مولى سليمان بن عبد الملك، عن أنس يعد في الشاميين، قال البخاري: منكر الحديث','2092','803','0');
INSERT INTO [t13041] VALUES (982,'عبد الرحمن ابن أخي محمد بن المنكدر عن عمه، ولا يتابع عليه، ولا يعرف إلا به','2094','803','0');
INSERT INTO [t13041] VALUES (983,'عبد الرحمن الأصم حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي سمعت يحيى يقول: كان عبد الرحمن الأصم صاحب قدر، قال علي: قلت ليحيى: كان يرى القدر؟ قال: نعم، كان بصريا، وكان يكون بالمدائن','2096','803','0');
INSERT INTO [t13041] VALUES (984,'عبد الرحمن السدي عن داود بن أبي هند، مجهول أيضا، ولا يتابع على حديثه، ولا يعرف من وجه يصح','2097','803','0');
INSERT INTO [t13041] VALUES (985,'عبد الرحمن مولى سليمان بن عبد الملك عن أنس حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الرحمن مولى سليمان بن عبد الملك، عن أنس، يعد في الشاميين، قال البخاري: منكر الحديث','2099','803','0');
INSERT INTO [t13041] VALUES (986,'عبد الرحمن بن أخي محمد بن المنكدر عن عمه، ولا يتابع عليه ولا يعرف إلا به','2101','803','0');
INSERT INTO [t13041] VALUES (987,'عبد الرحمن الأصم حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي، سمعت يحيى، يقول كان عبد الرحمن الأصم صاحب قدر. قال علي: قلت ليحيى: كان يرى القدر؟ قال: نعم، كان بصريا وكان يكون بالمدائن','2103','803','0');
INSERT INTO [t13041] VALUES (988,'باب','2104','803','0');
INSERT INTO [t13041] VALUES (989,'عبد العزيز بن بكار البكراوي حديثه غير محفوظ','2105','803','0');
INSERT INTO [t13041] VALUES (990,'عبد العزيز بن عبد الرحمن البالسي القرشي','2107','803','0');
INSERT INTO [t13041] VALUES (991,'عبد العزيز بن أبي رواد واسم أبي رواد: ميمون. حدثنا محمد بن أيوب قال: حدثنا محمود بن غيلان قال: حدثنا مؤمل بن إسماعيل قال: مات عبد العزيز بن أبي رواد فجيء بجنازته فوضعت عند باب الصفا، واصطف الناس وجاء الثوري، فقال الناس: جاء الثوري، جاء الثوري،','2109','803','0');
INSERT INTO [t13041] VALUES (992,'عبد العزيز بن أبي حازم حدثني الخضر بن داود قال: حدثنا أحمد بن محمد قال: سمعت أبا عبد الله، يسأل عن عبد العزيز بن أبي حازم، فقيل: كيف هو؟ قال: أما روايته فيرون أنه قد سمع من أبيه، وأما هذه الكتب التي عن غير أبيه فيقولون: إن كتب سليمان بن بلال','2113','803','0');
INSERT INTO [t13041] VALUES (993,'عبد العزيز بن حوران حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي بن عبد الله قال: سمعت هشام بن يوسف، وسئل عن عبد العزيز بن حوران - شيخ من أهل صنعاء - روى عن وهب بن منبه، فقال: كان ضعيفا، كان يشبه القصاص','2114','803','0');
INSERT INTO [t13041] VALUES (994,'عبد العزيز بن المطلب عن الأعرج، ولا يتابع عليه. حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن بن مهدي، يحدث عن عبد العزيز بن المطلب المخزومي','2116','803','0');
INSERT INTO [t13041] VALUES (995,'عبد العزيز بن جريج عن عائشة، في الوتر. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد العزيز بن جريج، عن عائشة، في الوتر، روى عنه ابنه عبد الملك، ولا يتابع عليه','2118','803','0');
INSERT INTO [t13041] VALUES (996,'عبد العزيز بن عقبة بن سلمة بن الأكوع حدثنا آدم بن موسى قال: سمعت البخاري قال: عبد العزيز بن عقبة بن سلمة بن الأكوع جعل في أهل المدينة، عن عبد الله بن رافع، روى عنه، يزيد بن عمرو، ولا يصح حديثه','2121','803','0');
INSERT INTO [t13041] VALUES (997,'عبد العزيز بن عمران الزهري أبو ثابت حديثه غير محفوظ، ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد العزيز بن عمران لا يكتب حديثه، منكر الحديث','2123','803','0');
INSERT INTO [t13041] VALUES (998,'عبد العزيز بن حكيم الحضرمي حدثنا محمد بن إسماعيل الصائغ قال: حدثنا عباس بن عبد العظيم العنبري ح، وحدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد، قالا: حدثنا علي بن المديني قال: سألت جريرا قلت: رويت عن عبد العزيز بن حكيم، شيئا؟ قال: لا','2125','803','0');
INSERT INTO [t13041] VALUES (999,'عبد العزيز بن الحصين بن الترجمان أبو سهل المروزي عن الزهري، وأيوب. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد العزيز بن الحصين بن الترجمان أبو سهل المروزي عن الزهري، وأيوب، قال البخاري: ليس بالقوي عندهم','2127','803','0');
INSERT INTO [t13041] VALUES (1000,'عبد العزيز بن أبان أبو خالد القرشي كوفي عن الثوري','2130','803','0');
INSERT INTO [t13041] VALUES (1001,'عبد العزيز بن مسلم القسملي في حديثه بعض الوهم','2132','803','0');
INSERT INTO [t13041] VALUES (1002,'عبد العزيز بن عمر بن عبد العزيز بن مروان القرشي حدثنا أحمد بن زكريا العابدي قال: حدثنا ميمون بن الأصبغ النصيبي قال: قال أبو مسهر: عبد العزيز بن عمر بن عبد العزيز ضعيف الحديث','2134','803','0');
INSERT INTO [t13041] VALUES (1003,'عبد العزيز بن يحيى المديني يحدث عن الثقات، بالبواطيل، ويذكر من الحديث ما لا يعرف به غيره من المتقدمين عن مالك وغيره','2135','803','0');
INSERT INTO [t13041] VALUES (1004,'عبد العزيز بن يحيى الحراني أبو الأصبغ حدثني آدم بن موسى قال: سمعت البخاري قال: عبد العزيز بن يحيى أبو الأصبغ، عن عيسى بن يونس، عن بدر، لا يتابع عليه','2138','803','0');
INSERT INTO [t13041] VALUES (1005,'عبد العزيز بن محمد الدراوردي حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: كان عبد الرحمن يحدث عن الرجل بالحديث والشيء لا يحدث بحديثه كله، وأنه حدث عن الدراوردي بحديث','2140','803','0');
INSERT INTO [t13041] VALUES (1006,'عبد العزيز بن عبيد الله بن حمزة بن صهيب حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: عبد العزيز بن عبيد الله بن حمزة بن صهيب ضعيف، لم يحدث عنه إلا إسماعيل بن عياش','2143','803','0');
INSERT INTO [t13041] VALUES (1007,'باب عبد الملك','2145','803','0');
INSERT INTO [t13041] VALUES (1008,'عبد الملك بن الحسين أبو مالك النخعي حدثني آدم بن موسى قال: سمعنا البخاري قال: عبد الملك بن الحسين أبو مالك النخعي كوفي، وقال عيسى بن يونس: عبادة ليس بالقوي عنده','2146','803','0');
INSERT INTO [t13041] VALUES (1009,'عبد الملك بن سليمان القرقساني عن عيسى بن يونس حديثه غير محفوظ','2149','803','0');
INSERT INTO [t13041] VALUES (1010,'عبد الملك بن عبد الرحمن من ولد عتاب بن أسيد عن ابن جريج، حديثه غير محفوظ، ولا يعرف إلا به','2151','803','0');
INSERT INTO [t13041] VALUES (1011,'عبد الملك بن عبد الرحمن أبو العباس الشامي نزل البصرة حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الملك بن عبد الرحمن أبو العباس الشامي نزل البصرة، عن الأوزاعي، وابن أبي عبلة، قال البخاري: ضعفه عمرو بن علي جدا، منكر الحديث','2153','803','0');
INSERT INTO [t13041] VALUES (1012,'عبد الملك بن أبي جمعة المغني بصري، عن الحسن. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيي قال: عبد الملك بن أبي جمعة بصري ضعيف','2155','803','0');
INSERT INTO [t13041] VALUES (1013,'عبد الملك بن عبد الملك عن مصعب بن أبي ذئب حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الملك بن عبد الملك، عن مصعب بن أبي ذئب، قال البخاري: في حديثه نظر','2157','803','0');
INSERT INTO [t13041] VALUES (1014,'عبد الملك بن قدامة الجمحي حدثنا آدم بن موسى قال: سمعت البخاري يقول: عبد الملك بن قدامة بن إبراهيم بن محمد بن حاطب الجمحي يعرف وينكر، عنده عن عبد الله بن دينار مناكير','2159','803','0');
INSERT INTO [t13041] VALUES (1015,'عبد الملك بن أبي سليمان العرزمي عن عطاء','2161','803','0');
INSERT INTO [t13041] VALUES (1016,'عبد الملك بن محمد بن بشير، عن عبد الرحمن بن علقمة حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الملك بن محمد بن بشير، عن عبد الرحمن بن علقمة، عن النبي صلى الله عليه وسلم، حديثه في الكوفيين، قال البخاري: لم يتبين سماع بعضهم من بعض','2163','803','0');
INSERT INTO [t13041] VALUES (1017,'عبد الملك بن أعين حدثني عبد الله بن أحمد بن حنبل قال: حدثنا محمد بن عباد المكي قال: حدثنا سفيان قال: حدثنا عبد الملك بن أعين، وكان رافضيا. حدثنا محمد بن زكريا قال: حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن بن مهدي، يحدث عن سفيان، عن عبد الملك','2165','803','0');
INSERT INTO [t13041] VALUES (1018,'عبد الملك بن مهران صاحب مناكير، غلب على حديثه الوهم، لا يقيم شيئا من الحديث','2167','803','0');
INSERT INTO [t13041] VALUES (1019,'عبد الملك بن مسلم عن أبي جرو. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الملك بن مسلم، عن أبي جرو، سمع عليا، والزبير، قال البخاري: ولم يصح حديثه','2171','803','0');
INSERT INTO [t13041] VALUES (1020,'عبد الملك بن نافع ابن أخي القعقاع بن شور. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الملك بن نافع ابن أخي القعقاع بن شور، قال البخاري: لا يتابع على حديثه، وفي حديثه اختلاف','2173','803','0');
INSERT INTO [t13041] VALUES (1021,'عبد الملك بن خشك الصنعاني حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سألت هشام بن يوسف عن عبد الملك بن خشك قال: كان فيه ضعف، قلت لهشام: جالسته؟ قال: نعم','2175','803','0');
INSERT INTO [t13041] VALUES (1022,'عبد الملك بن خلج الصنعاني حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سألت هشام بن يوسف، عن عبد الملك بن خلج، شيخ من أهل صنعاء روى عن وهب بن منبه، فضعفه','2177','803','0');
INSERT INTO [t13041] VALUES (1023,'عبد الملك بن الوليد بن معدان الضبعي حدثني آدم بن موسى قال: سمعت البخاري يقول: عبد الملك بن الوليد بن معدان الضبعي فيه نظر','2179','803','0');
INSERT INTO [t13041] VALUES (1024,'عبد الملك بن هارون بن عنترة حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الملك بن هارون بن عنترة كوفي منكر الحديث. حدثنا محمد بن أيوب قال: حدثنا أبو غسان قال: سألت بهز بن أسد قلت: عبد الملك بن هارون بن عنترة؟ قال: حدثنا عنه نحو من عشرين، منكرات','2181','803','0');
INSERT INTO [t13041] VALUES (1025,'باب عبد الحميد','2183','803','0');
INSERT INTO [t13041] VALUES (1026,'عبد الحميد بن سالم عن أبي هريرة حدثني آدم بن موسى، قال سمعت البخاري قال عبد الحميد بن سالم، عن أبي هريرة: من لعق العسل لا يعرف له سماع من أبي هريرة','2184','803','0');
INSERT INTO [t13041] VALUES (1027,'عبد الحميد بن يحيى مجهول بالنقل، لا يتابع على حديثه','2186','803','0');
INSERT INTO [t13041] VALUES (1028,'عبد الحميد بن حبيب بن أبي العشرين أبو سعيد حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الحميد بن حبيب بن أبي العشرين أبو سعيد، قال البخاري: ربما يخالف في حديثه','2188','803','0');
INSERT INTO [t13041] VALUES (1029,'عبد الحميد بن بهرام الفزاري حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي الحلواني قال: حدثنا علي بن حفص المدائني قال: سمعت شعبة يقول: نعم الشيخ عبد الحميد بن بهرام ولكن لا تكتبوا عنه؛ فإنه يروي عن شهر بن حوشب. حدثنا زكريا بن يحيى قال: محمد بن','2190','803','0');
INSERT INTO [t13041] VALUES (1030,'عبد الحميد بن جعفر الأنصاري حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي بن عبد الله قال: سمعت يحيى يقول: كان سفيان بن سعيد يحمل على عبد الحميد بن جعفر، قال يحيى: وكلمته فيه فقلت: ما شأنه؟ ثم قال يحيى: ما أدري ما كان شأنه وشأنه.','2192','803','0');
INSERT INTO [t13041] VALUES (1031,'عبد الحميد بن يوسف الجزري عن ميمون بن مهران، ولا يتابع على حديثه، وليس بمشهور بالنقل، مجهول','2193','803','0');
INSERT INTO [t13041] VALUES (1032,'عبد الحميد بن سنان حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الحميد بن سنان، عن عبيد بن عمير، في حديثه نظر','2195','803','0');
INSERT INTO [t13041] VALUES (1033,'عبد الحميد بن الحسن الهلالي عن محمد بن المنكدر، لا يتابع على حديثه','2197','803','0');
INSERT INTO [t13041] VALUES (1034,'عبد الحميد بن سليمان أخو فليح حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عبد الحميد بن سليمان أخو فليح ليس بشيء','2199','803','0');
INSERT INTO [t13041] VALUES (1035,'عبد الحميد بن زياد بن صيفي بن صهيب حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الحميد بن زياد بن صيفي بن صهيب، عن أبيه، عن جده، ولا يعرف سماع بعضهم من بعض','2201','803','0');
INSERT INTO [t13041] VALUES (1036,'عبد الحميد بن قدامة عن أنس حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الحميد بن قدامة، عن أنس، في الفاغية، لا يتابع عليه','2203','803','0');
INSERT INTO [t13041] VALUES (1037,'عبد الحميد بن زيد العمي أخو عبد الرحيم: حديثه غير محفوظ، وليس بمشهور في النقل','2206','803','0');
INSERT INTO [t13041] VALUES (1038,'عبد الحميد بن الربيع اليمامي عن عبد الله بن يحيى بن زيد، مجهولين جميعا، والحديث غير محفوظ','2208','803','0');
INSERT INTO [t13041] VALUES (1039,'عبد الحميد بن موسى المصيصي يخالف في حديثه','2210','803','0');
INSERT INTO [t13041] VALUES (1040,'باب عبد الواحد','2212','803','0');
INSERT INTO [t13041] VALUES (1041,'عبد الواحد بن ثابت الباهلي عن ثابت البناني، لا يتابع على حديثه','2213','803','0');
INSERT INTO [t13041] VALUES (1042,'عبد الواحد بن ميمون أبو حمزة المدني اليماني حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الواحد بن ميمون أبو حمزة المدني، عن عروة، روى عنه الواقدي، وطلحة بن يحيى، منكر الحديث','2216','803','0');
INSERT INTO [t13041] VALUES (1043,'عبد الواحد بن قيس عن أبي هريرة. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الواحد بن قيس، عن أبي هريرة، روى عنه الأوزاعي، وهو والد عمر الشامي، وكان الحسن بن ذكوان يحدث عنه بعجائب. حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت','2218','803','0');
INSERT INTO [t13041] VALUES (1044,'عبد الواحد بن سليم مجهول في النقل، وحديثه غير محفوظ، ولا يتابع عليه','2220','803','0');
INSERT INTO [t13041] VALUES (1045,'عبد الواحد بن زيد البصري الزاهد حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الواحد بن زيد البصري عن الحسن، وعبادة بن نسي الكندي، وعبد الله بن راشد، وغيرهم، قال البخاري: تركوه. وحدثنا محمد قال: حدثنا عباس قال: سمعت يحيى بن معين قال: عبد الواحد','2222','803','0');
INSERT INTO [t13041] VALUES (1046,'عبد الواحد بن زياد أبو بشر العبدي حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت يحيى بن سعيد يقول: ما رأيت عبد الواحد بن زياد يطلب حديثا قط بالبصرة ولا بالكوفة. قال يحيى: وكنا نجلس على بابه يوم الجمعة بعد الصلاة أذاكره حديث الأعمش لا','2224','803','0');
INSERT INTO [t13041] VALUES (1047,'عبد الواحد بن أبي عمرو الأسدي عن عطاء، لا يتابع على حديثه، روى عنه يحيي بن محمد، وفى إسناده رجلان مجهولان','2225','803','0');
INSERT INTO [t13041] VALUES (1048,'عبد الواحد بن عبيد عن الرقاشي حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الواحد بن عتبة عن الرقاشي، روى عنه أبو معاوية، ولم يصح حديثه','2227','803','0');
INSERT INTO [t13041] VALUES (1049,'عبد الواحد الحجبي عن أبيه، عن وهب بن منبه، ليس بمشهور بالنقل، يضعف في حديثه من أجل عبد العزيز بن يحيى المدني','2228','803','0');
INSERT INTO [t13041] VALUES (1050,'باب عبد الأعلى','2230','803','0');
INSERT INTO [t13041] VALUES (1051,'عبد الأعلى بن عامر الثعلبي، من أهل الثعلبية حدثنا محمد بن جعفر بن محمد قال: حدثنا أبو بكر بن أبي الأسود قال: سمعت يحيى بن سعيد قال: سألت الثوري عن حديث عبد الأعلى، عن ابن الحنفية، فضعفها. حدثنا عبد الله بن أحمد قال: سمعت أبي قال: عبد الأعلى، عن','2231','803','0');
INSERT INTO [t13041] VALUES (1052,'عبد الأعلى بن عبد الأعلى السامي حدثنا عبد الله بن أحمد قال: قال أبي: عبد الأعلى بن عبد الأعلى، فقال: كان يرى القدر. قال: حدثنا محمد بن أحمد قال: سمعت محمد بن بشار، وحدثنا عن عبد الأعلى بن عبد الأعلى، فقال: والله ما كان يدري عبد الأعلى بن عبد','2232','803','0');
INSERT INTO [t13041] VALUES (1053,'عبد الأعلى بن الحسين بن ذكوان المعلم منكر الحديث، غير محفوظ','2233','803','0');
INSERT INTO [t13041] VALUES (1054,'عبد الأعلى بن عبد الله بن قيس لا يتابع على حديثه، وليس بمشهور في النقل، وإسماعيل مولى المزنيين نحوه','2235','803','0');
INSERT INTO [t13041] VALUES (1055,'عبد الأعلى بن حكيم عن معاذ بن جبل، حديثه غير محفوظ، وهو مجهول بالنقل، وأبو بكر بن أبي سبرة متروك، وسليمان الشاذكوني أيضا','2237','803','0');
INSERT INTO [t13041] VALUES (1056,'عبد الأعلى بن أعين عن يحيى بن أبي كثير، جاء بأحاديث منكرة ليس منها شيء محفوظ','2239','803','0');
INSERT INTO [t13041] VALUES (1057,'عبد الأعلى بن أبي المساور أبو مسعود الجرار حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى يقول: عبد الأعلى بن أبي المساور الجرار ليس بشيء. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الأعلى بن أبي المساور أبو مسعود الجرار منكر الحديث','2241','803','0');
INSERT INTO [t13041] VALUES (1058,'عبد الأعلى بن محمد التاجر يروي عن يحيى بن سعيد الأنصاري، بواطيل لا أصول لها','2242','803','0');
INSERT INTO [t13041] VALUES (1059,'باب عبد الكريم','2244','803','0');
INSERT INTO [t13041] VALUES (1060,'عبد الكريم بن أبي المخارق أبو أمية بصري حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي ح، وحدثنا داود بن محمد قال: حدثنا حجاج بن يوسف، قالا: حدثنا عبد الرزاق قال: أخبرنا معمر قال: ما رأيت أيوب اغتاب أحدا قط إلا عبد الكريم، فإنه ذكره فقال: رحمه','2245','803','0');
INSERT INTO [t13041] VALUES (1061,'عبد الكريم بن كيسان مجهول بالنقل، حديثه غير محفوظ','2247','803','0');
INSERT INTO [t13041] VALUES (1062,'باب عبد السلام','2249','803','0');
INSERT INTO [t13041] VALUES (1063,'عبد السلام روى عنه، إسماعيل بن أبي خالد. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد السلام روى عنه، إسماعيل بن أبي خالد عن علي، والزبير، ولا يثبت سماعه منهما','2250','803','0');
INSERT INTO [t13041] VALUES (1064,'عبد السلام بن أبي الجنوب عن الزهري، عن أبي سلمة، عن أبي هريرة. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد السلام بن أبي الجنوب عن الزهري، قال علي: منكر الحديث','2252','803','0');
INSERT INTO [t13041] VALUES (1065,'عبد السلام بن عبد القدوس شامي عن ابن جريج، وهشام بن عروة، لا يتابع على شيء من حديثه، وليس ممن يقيم الحديث','2254','803','0');
INSERT INTO [t13041] VALUES (1066,'عبد السلام بن عبد الله المذجحي عن أبي عمرو، عن أنس، إسناد مجهول غير محفوظ','2257','803','0');
INSERT INTO [t13041] VALUES (1067,'عبد السلام بن علي السلامي عن إسحاق بن عبد الله بن أبي طلحة، ولا يتابع على حديثه، ولا يعرف إلا به','2259','803','0');
INSERT INTO [t13041] VALUES (1068,'عبد السلام بن موسى بن حميد الأنصاري حدثني آدم بن موسى قال: سمعت البخاري قال: عبد السلام بن موسى بن حميد الأنصاري عن أبيه، عن أبي الحويرث، عن أبي ذر، لا يتبين سماع أبي الحويرث من أبي ذر','2261','803','0');
INSERT INTO [t13041] VALUES (1069,'عبد السلام بن حرب الملائي حدثنا عبد الله بن أحمد قال: حدثني حسن بن عيسى قال: سمعت عبد الله بن المبارك، وسألته عن عبد السلام بن حرب الملائي، فقال: قد عرفته، وكان إذا قال قد عرفته فقد أهلكه. حدثنا عبد الله قال: قال أبي: كنا ننكر من عبد السلام شيئا','2263','803','0');
INSERT INTO [t13041] VALUES (1070,'عبد السلام بن صالح أبو الصلت الهروي كان رافضيا خبيثا','2264','803','0');
INSERT INTO [t13041] VALUES (1071,'باب عبد الوهاب','2266','803','0');
INSERT INTO [t13041] VALUES (1072,'عبد الوهاب بن مجاهد بن جبر حدثنا محمد بن سعيد بن خالد الرازي قال: سمعت عبد الرحمن بن الحكم بن بشير بن سليمان أنه يقول: سألت وكيعا عن عبد الوهاب بن مجاهد قال: قال أبي: قال جابر في حديث الطاعون، قال: فقلت لعبد الوهاب: سمعته من أبيك؟ فذهب وتركني.','2267','803','0');
INSERT INTO [t13041] VALUES (1073,'عبد الوهاب بن نافع البناني ويقال العامري، عن مالك، وغيره، منكر الحديث، لا يقيمه','2271','803','0');
INSERT INTO [t13041] VALUES (1074,'عبد الوهاب بن همام أخو عبد الرزاق حدثنا أحمد بن علي الأبار قال: قلت لمحمد بن رافع: عبد الوهاب بن همام أخو عبد الرزاق كان يعرف بالحديث؟ قال: لا، وكان شديد التشيع يفرط جدا، ما رأيته صلى معنا جماعة','2273','803','0');
INSERT INTO [t13041] VALUES (1075,'عبد الوهاب بن عبد المجيد الثقفي تغير في آخر عمره. حدثنا محمد بن زكريا قال: حدثنا عقبة بن مكرم قال: كان عبد الوهاب الثقفي قد اختلط قبل موته بثلاث سنين أو أربع سنين. حدثنا الحسين بن عبد الله الذارع قال: حدثنا أبو داود قال: جرير بن حازم وعبد الوهاب','2275','803','0');
INSERT INTO [t13041] VALUES (1076,'عبد الوهاب بن هشام بن الغاز عن أبيه، ولا يتابع على حديثه ولا يعرف إلا به','2278','803','0');
INSERT INTO [t13041] VALUES (1077,'عبد الوهاب بن الحسن التميمي حدثنا عبد الله بن أحمد بن حنبل قال: حدثنا أبي قال: حدثنا محمد بن ميمون قال: حدثنا عبد الوهاب بن الحسن التميمي، عن شيبان، مولى الضحاك: قال عبد الله: سألت أبي عن عبد الوهاب قال: أحاديثه مناكير، ولا أعرفه','2280','803','0');
INSERT INTO [t13041] VALUES (1078,'عبد الوهاب بن عطاء الخفاف حدثني محمد بن عبد الرحمن قال: حدثنا عبد الملك بن عبد الحميد قال: سمعت أحمد بن حنبل قال: عبد الوهاب بن عطاء الخفاف ضعيف الحديث مضطرب','2281','803','0');
INSERT INTO [t13041] VALUES (1079,'عبد الوهاب بن الضحاك الحمصي شامي متروك الحديث','2282','803','0');
INSERT INTO [t13041] VALUES (1080,'باب عبد الرحيم','2284','803','0');
INSERT INTO [t13041] VALUES (1081,'عبد الرحيم بن زيد العمي أبو زيد حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن يحدث عن عبد الرحيم بن زيد العمي شيئا قط. حدثني محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عبد الرحيم بن زيد العمي ليس بشيء. حدثني أحمد بن','2285','803','0');
INSERT INTO [t13041] VALUES (1082,'عبد الرحيم بن عمر عن الزهري روى عنه، مسلم بن خالد الزنجي حديثه غير محفوظ ولا يعرف إلا به','2287','803','0');
INSERT INTO [t13041] VALUES (1083,'عبد الرحيم بن داود مجهول بالنقل، حديثه غير محفوظ ولا يعرف إلا به، قد ذكره في ترجمة عمر بن بسطام على وجه آخر','2289','803','0');
INSERT INTO [t13041] VALUES (1084,'عبد الرحيم بن خالد الأيلي عن يونس، مجهول بالنقل ولا يتابع على حديثه بهذا الإسناد','2291','803','0');
INSERT INTO [t13041] VALUES (1085,'عبد الرحيم بن حماد عن معاوية بن يحيى، روى عنه سليمان بن أحمد مجهول بالنقل، حديثه غير محفوظ','2293','803','0');
INSERT INTO [t13041] VALUES (1086,'عبد الرحيم بن حماد الثقفي السندي كان بالبصرة قال: أنبأنا جدي، رحمه الله: قدم علينا من السند شيخ كبير كان يحدث عن الأعمش وعن عمرو بن عبيد','2295','803','0');
INSERT INTO [t13041] VALUES (1087,'باب عبد الصمد','2298','803','0');
INSERT INTO [t13041] VALUES (1088,'عبد الصمد بن سليمان الأزرق حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الصمد بن سليمان الأزرق روى عنه، سعدويه وغيره منكر الحديث','2299','803','0');
INSERT INTO [t13041] VALUES (1089,'عبد الصمد بن حبيب الأزدي العوذي حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الصمد بن حبيب الأزدي العوذي البصري قال البخاري: لين الحديث','2302','803','0');
INSERT INTO [t13041] VALUES (1090,'عبد الصمد بن علي الهاشمي عن أبيه، عن جده، حديثه غير محفوظ، ولا يعرف إلا به.','2304','803','0');
INSERT INTO [t13041] VALUES (1091,'عبد الصمد بن الفضل الربعي عن ابن وهب، لا يتابع على حديثه ولا يعرف إلا به','2306','803','0');
INSERT INTO [t13041] VALUES (1092,'باب عبد الجبار','2309','803','0');
INSERT INTO [t13041] VALUES (1093,'عبد الجبار بن الورد المكي حدثنا آدم بن موسى قال: سمعت البخاري قال: عبد الجبار بن الورد المكي يخالف في بعض حديثه','2310','803','0');
INSERT INTO [t13041] VALUES (1094,'عبد الجبار بن سعيد المساحقي مديني في حديثه مناكير وما لا يتابع عليه','2312','803','0');
INSERT INTO [t13041] VALUES (1095,'عبد الجبار بن عمر الأيلي أبو عمر حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الجبار بن عمر الأيلي أبو عمر ليس بالقوي عندهم، عنده مناكير. حدثنا محمد قال: حدثنا عباس بن محمد قال: سمعت يحيى قال: عبد الجبار بن عمر الأيلي ضعيف، وفي موضع آخر: عبد','2314','803','0');
INSERT INTO [t13041] VALUES (1096,'عبد الجبار بن العباس الشبامي كوفي عن عون بن أبي جحيفة، ولا يتابع على حديثه، وكان يتشيع','2316','803','0');
INSERT INTO [t13041] VALUES (1097,'عبد الجبار بن نافع الضبي مجهول بنقل الحديث عن أيوب بن موسى، لا يقيم الحديث، حديثه غير محفوظ','2318','803','0');
INSERT INTO [t13041] VALUES (1098,'عبد الجبار بن وهب مجهول أيضا، وحديثه غير محفوظ','2320','803','0');
INSERT INTO [t13041] VALUES (1099,'عبد الجبار بن الحجاج بن ميمون عن مكرم بن حكيم، إسناده مجهول غير محفوظ','2322','803','0');
INSERT INTO [t13041] VALUES (1100,'عبد الجبار بن عمر العطاردي في حديثه وهم كثير','2324','803','0');
INSERT INTO [t13041] VALUES (1101,'عبد الجبار بن المغيرة حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الجبار بن المغيرة عن أم كثيرة، عن علي، في النفخ في الشاة لا يتابع عليه','2326','803','0');
INSERT INTO [t13041] VALUES (1102,'باب عبد المؤمن','2327','803','0');
INSERT INTO [t13041] VALUES (1103,'عبد المؤمن بن عباد عن سعيد بن أنس. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد المؤمن بن عباد عن سعيد بن أنس، عن عكرمة، عن ابن عباس، لا يتابع على حديثه','2328','803','0');
INSERT INTO [t13041] VALUES (1104,'عبد المؤمن بن القاسم الأنصاري أخو أبي مريم: كان من الشيعة، لا يتابع على كثير من حديثه','2330','803','0');
INSERT INTO [t13041] VALUES (1105,'عبد المؤمن بن سالم بن ميمون بصري لا يتابع على حديثه','2332','803','0');
INSERT INTO [t13041] VALUES (1106,'عبد المؤمن بن عبد الله العبسي كوفي حديثه غير محفوظ','2334','803','0');
INSERT INTO [t13041] VALUES (1107,'عبد المجيد بن عبد العزيز بن أبي رواد أبو عبد الحميد مولى الأزد حدثني آدم بن موسى قال: حدثنا البخاري قال: عبد المجيد بن عبد العزيز بن أبي رواد أبو عبد الحميد مولى الأزد خراساني سكن مكة، كان الحميدي يتكلم فيه، كان يرى الإرجاء. حدثنا أحمد بن علي قال:','2336','803','0');
INSERT INTO [t13041] VALUES (1108,'عبد القدوس بن حبيب الدمشقي عن مجاهد حدثني أبو محمد عبد الله بن محمد بن سعدويه المروزي قال: حدثنا أحمد بن عبد الله بن بشير المروزي قال: حدثنا سفيان بن عبد الملك قال: سمعت ابن المبارك يقول: لأن أقطع الطريق أحب إلي من أن أروي عن عبد القدوس الشامي.','2337','803','0');
INSERT INTO [t13041] VALUES (1109,'عبد ربه بن نافع أبو شهاب الحناط حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سمعت يحيى يقول: لم يكن أبو شهاب الحناط بالحافظ ولم يرض يحيى أمره','2339','803','0');
INSERT INTO [t13041] VALUES (1110,'عبد ربه بن بارق الحنفي حدثنا محمد بن عيسى قال: حدثنا العباس قال: سمعت يحيى قال: عبد ربه بن بارق الحنفي ليس بشيء','2340','803','0');
INSERT INTO [t13041] VALUES (1111,'عبد الوارث بن غالب العنبري عن ثابت، حديثه غير محفوظ، ولا يعرف إلا به','2341','803','0');
INSERT INTO [t13041] VALUES (1112,'عبد الوارث بن سعيد التنوري البصري حدثنا عبد الله بن أحمد بن حنبل قال: حدثنا عبد الوارث بن غياث أبو بحر قال: حدثني عدي بن الفضل قال: كلمت يونس بن عبيد في عبد الوارث فقال: رأيته على باب عمرو بن عبيد جالسا، لا تذكره لي. حدثنا محمد بن عيسى قال: حدثنا','2343','803','0');
INSERT INTO [t13041] VALUES (1113,'عبد الغفار المديني عن سعيد بن المسيب، مجهول بالنقل، حديثه غير محفوظ، ولا يعرف إلا به','2346','803','0');
INSERT INTO [t13041] VALUES (1114,'عبد الغفار بن القاسم أبو مريم الأنصاري كوفي حدثنا محمد بن يحيى بن منده قال: حدثنا علي بن يونس الأصبهاني قال: حدثنا أبو داود قال: حدثنا شعبة قال: سمعت سماكا الحنفي، يقول لأبي مريم في شيء ذكره: كذبت والله، حدثنا زكريا بن يحيى وأحمد بن الحسين الصوفي','2348','803','0');
INSERT INTO [t13041] VALUES (1115,'عبد الجليل عن عمه، عن أبي هريرة. حدثني آدم بن موسى قال: سمعت البخاري يقول: عبد الجليل عن عمه، عن أبي هريرة، ولا يتابع عليه','2349','803','0');
INSERT INTO [t13041] VALUES (1116,'عبد الحكيم بن عبد الله بن أبي فروة عن عباس بن سهل، لا يتابع عليه ولا يعرف إلا بالواقدي','2351','803','0');
INSERT INTO [t13041] VALUES (1117,'عبد الحكيم بن منصور الواسطي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عبد الحكيم بن منصور: واسطي كذاب، وفي موضع آخر: عبد الحكيم بن منصور واسطي ليس حديثه بشيء. حدثنا محمد قال: حدثنا معاوية قال: سمعت يحيى قال: عبد الحكيم بن منصور ضعيف','2353','803','0');
INSERT INTO [t13041] VALUES (1118,'عبد الحكم القسملي بصري حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الحكم القسملي عن أنس، وأبي الصديق، منكر الحديث','2355','803','0');
INSERT INTO [t13041] VALUES (1119,'عبد الخالق بن زيد بن واقد عن أبيه حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الخالق بن زيد بن واقد، عن أبيه، منكر الحديث','2358','803','0');
INSERT INTO [t13041] VALUES (1120,'عبد الرزاق بن عمر الدمشقي أبو بكر الشامي حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: سألت هشيما عن عبد الرزاق بن عمر قال: ذهبت كتبه، خرج إلى بيت المقدس فجعل كتبه في خرج جديد وثيابه في خرج خلق، فجاء اللصوص فأخذوا الخرج الجديد فذهبت كتبه، وكان','2360','803','0');
INSERT INTO [t13041] VALUES (1121,'عبد الرزاق بن همام بن نافع الحميري الصنعاني روى عنه أحمد ويحيى وإسحاق والناس. حدثنا عبد الله بن أحمد بن حنبل قال: قلت ليحيى بن معين، وعبد الرزاق تخشى كبير السن؟ قال: أما حيث رأيناه فما كان بلغ الثمانين، كان نحوا من سبعين، ثم قال يحيى: أخبرني أبو','2362','803','0');
INSERT INTO [t13041] VALUES (1122,'عبد المنعم بن نعيم أبو سعيد البصري منكر الحديث','2364','803','0');
INSERT INTO [t13041] VALUES (1123,'عبد المنعم بن إدريس ابن بنت وهب بن منبه حدثنا عبد الله بن أحمد قال: حدثني أبي قال: قدمنا اليمن في سنة ثمان وتسعين فسألنا عن عبد المنعم فقالوا: مات أبوه وله خمس أو ست سنين. وحدثنا عبد الله بن أحمد قال: سمعت الصاغاني قال: سمعت يحيى بن معين يقول:','2367','803','0');
INSERT INTO [t13041] VALUES (1124,'عبد المنعم بن بشير كان بمصر حدثنا زكريا بن يحيى الحلواني قال: حدثنا أحمد بن سعيد بن أبي مريم قال: ذاكرت يحيى بن معين يوما وهو بمصر عن أبي مودود، عن سليمان بن يسار قال: مرضت فعادني ابن عمر في يوم مهين، قال أحمد: فأعجب يحيى هذا الحديث وقال لي:','2369','803','0');
INSERT INTO [t13041] VALUES (1125,'عبد الغفور بن سعيد أبو الصباح الواسطي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: عبد الغفور أبو الصباح ليس حديثه بشيء. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد الغفور بن سعيد أبو الصباح الواسطي تركوه منكر الحديث','2371','803','0');
INSERT INTO [t13041] VALUES (1126,'عبد النور بن عبد الله المسمعي كان ممن يغلو في الرفض، لا يقيم الحديث، وليس من أهله','2373','803','0');
INSERT INTO [t13041] VALUES (1127,'عبد المهيمن بن عباس بن سهل بن سعد حدثنا أحمد بن زكريا الحضرمي، قال حدثنا أيوب بن إسحاق، قال سمعت يحيى بن معين يقول: عبد المهيمن وأبي ابنا العباس ضعيفان. حدثني آدم بن موسى قال: سمعت البخاري قال: عبد المهيمن بن عباس بن سهل بن سعد، عن أبيه، عن جده','2375','803','0');
INSERT INTO [t13041] VALUES (1128,'عبد الخبير بن ثابت بن قيس بن شماس لا يتابع على حديثه، ولا يعرف إلا به. حدثنا آدم بن موسى قال سمعت البخاري قال: عبد الخبير بن ثابت بن قيس بن شماس حديثه ليس بالقائم','2377','803','0');
INSERT INTO [t13041] VALUES (1129,'عبيد بن إسحاق العطار كوفي يقال له عطار المطلقات. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عبيد بن إسحاق يقال له عطار المطلقات ضعيف. حدثنا محمد قال حدثنا عباس في موضع آخر قال سمعت يحيى قال: عبيد بن إسحاق عطار المطلقات، قلت له هذه','2379','803','0');
INSERT INTO [t13041] VALUES (1130,'عبيد الأغر القرشي عن عطاء بن يسار. حدثني آدم بن موسى قال: سمعت البخاري قال: عبيد الأغر القرشي عن عطاء بن يسار روى عنه موسى ولا يصح حديثه','2381','803','0');
INSERT INTO [t13041] VALUES (1131,'عبيد بن أبي قرة عن الليث بن سعد، حديثه غير محفوظ ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: عبيد بن أبي قرة، عن الليث بن سعد في قصة العباس، ولا يتابع في حديثه','2383','803','0');
INSERT INTO [t13041] VALUES (1132,'عبيد بن القاسم حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى قال: عبيد بن القاسم قريب لسفيان الثوري، قد سمعت منه وليس بثقة وفي موضع آخر: عبيد بن القاسم كان يكون في مسجد الجامع، وكانت له هيئة، وكان كذابا','2385','803','0');
INSERT INTO [t13041] VALUES (1133,'عبيد بن الصباح كوفي عن كامل أبي العلاء ولا يتابع على حديثه، ولا يعرف إلا به','2387','803','0');
INSERT INTO [t13041] VALUES (1134,'عبيد الله بن أنس بن مالك روى عنه عبد الرحيم بن سليم الأنصاري، كلاهما مجهول بالنقل، والحديث غير محفوظ','2389','803','0');
INSERT INTO [t13041] VALUES (1135,'عبيد الله بن الأزور عن هشام بن حسان ولا يتابع على لفظه','2391','803','0');
INSERT INTO [t13041] VALUES (1136,'عبيد الله بن تمام أبو عاصم عن خالد الحذاء. حدثني آدم بن موسى قال: سمعت البخاري قال: عبيد الله بن تمام أبو عاصم عن خالد الحذاء أراه كان بواسط عنده عجائب عن خالد الحذاء','2393','803','0');
INSERT INTO [t13041] VALUES (1137,'عبيد الله بن أبي حميد أبو الخطاب عن أبي المليح. حدثنا زكريا بن يحيى قال حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن يحدث عن عبيد الله بن أبي حميد الهذلي ضعيف الحديث , وهو كوفي. حدثني آدم بن موسى قال: سمعت البخاري قال: عبيد الله بن أبي حميد أبو','2395','803','0');
INSERT INTO [t13041] VALUES (1138,'عبيد الله بن أبي زياد القداح مكي حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت يحيى يقول: عبيد الله بن أبي زياد القداح كان وسطا، لم يكن بذلك، ليس هو مثل عثمان بن الأسود ولا سيف، قال يحيى: ومحمد بن عمرو أحب إلي منه','2397','803','0');
INSERT INTO [t13041] VALUES (1139,'عبيد الله بن عبد الرحمن بن موهب، عن القاسم حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: عبيد الله بن عبد الرحمن بن موهب مديني يروي عن القاسم، وهو ضعيف','2400','803','0');
INSERT INTO [t13041] VALUES (1140,'عبيد الله بن زحر حدثنا محمد بن عيسى قال: حدثنا العباس قال: سمعت يحيى يقول: عبيد الله بن زحر ليس بشيء. حدثنا أحمد بن محمود قال: حدثنا عثمان بن سعيد قال: قلت ليحيى: فعبيد الله بن زحر كيف حديثه؟ فقال: كل حديثه عندي ضعيف. حدثنا محمد بن خزيمة قال:','2402','803','0');
INSERT INTO [t13041] VALUES (1141,'عبيد الله بن سعيد قائد الأعمش حدثني آدم بن موسى قال: سمعت البخاري قال: عبيد الله بن سعيد قائد الأعمش في حديثه نظر','2403','803','0');
INSERT INTO [t13041] VALUES (1142,'عبيد الله بن عبد الله أبو المنيب العتكي مروزي لا يتابع على حديثه، ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: عبيد الله بن عبد الله أبو المنيب العتكي مروزي، قال البخاري: عنده مناكير، روى عنه زيد بن الحباب','2405','803','0');
INSERT INTO [t13041] VALUES (1143,'عبيد الله بن عبد الله بن الحصين الخطمي حدثني آدم بن موسى قال: سمعت البخاري قال: عبيد الله بن عبد الله بن الحصين الخطمي في حديثه نظر','2407','803','0');
INSERT INTO [t13041] VALUES (1144,'عبيد الله بن عبد المجيد أبو علي الحنفي حدثنا أحمد بن محمود قال: حدثنا عثمان بن سعيد قال: قلت ليحيى: عبيد الله بن عبد المجيد الحنفي أخو أبي بكر ما حاله؟ قال: ليس بشيء','2409','803','0');
INSERT INTO [t13041] VALUES (1145,'عبيد الله بن عبد الرحمن بن الأصم بصري لا يتابع على حديثه من وجه يثبت، ولا يعرف إلا به','2411','803','0');
INSERT INTO [t13041] VALUES (1146,'عبيد الله بن عمر بن موسى التيمي عم عبيد الله بن عائشة عن ربيعة، ولا يتابع على حديثه','2413','803','0');
INSERT INTO [t13041] VALUES (1147,'عبيد الله بن عكراش بن ذؤيب حدثني آدم بن موسى قال: سمعت البخاري قال: عبيد الله بن عكراش بن ذؤيب في إسناده نظر','2415','803','0');
INSERT INTO [t13041] VALUES (1148,'عبيد الله بن غالب عن أبي المليح حدثني آدم بن موسى قال: سمعت البخاري قال: عبيد الله بن غالب ويقال هو عبيد الله بن أبي حميد، فإن كان هو هو فهو ذاهب','2417','803','0');
INSERT INTO [t13041] VALUES (1149,'عبيد الله بن موسى العبسي كوفي حدثنا عبد الله بن أحمد قال: قال أبي: رأيت عبيد الله بن موسى بمكة فما عرضت له، لم يكن لي فيه رأي. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سألت يحيى عن عبيد الله بن موسى، فقال: اكتب عنه، فقد كتبنا عنه. سمعت','2419','803','0');
INSERT INTO [t13041] VALUES (1150,'عبيد الله بن موسى بن معدان كوفي مجهول بنقل الحديث، حديثه منكر، لا يتابع عليه، ولا يعرف إلا به','2421','803','0');
INSERT INTO [t13041] VALUES (1151,'عبيد الله بن النضر بن أنس حدثنا عبد الله قال: حدثنا أبي قال: حدثنا عبد الرحمن بن مهدي، عن عبيد الله بن النضر، عن أبيه، عن قيس بن عباد، أنه كان يصلى العشاء مع العتامة. قال عبد الرحمن: فقدم علينا عبد الله بن المبارك بعد سنين، فأتيناه فسألناه عن','2423','803','0');
INSERT INTO [t13041] VALUES (1152,'عبيد الله بن الوليد الوصافي في حديثه مناكير، لا يتابع على كثير من حديثه','2424','803','0');
INSERT INTO [t13041] VALUES (1153,'عبيدة بن معتب الضبي أبو عبد الكريم كوفي حدثنا محمد بن موسى قال: حدثنا علي بن مسلم قال: حدثنا أبو داود قال: حدثنا شعبة قال: أخبرني عبيدة، قبل أن يتغير. حدثنا أحمد بن علي الأبار قال: حدثنا أحمد بن إبراهيم قال: حدثنا أسيد بن زيد الجمال قال: سمعت','2426','803','0');
INSERT INTO [t13041] VALUES (1154,'عبادة أبو يحيى سمع أبا داود، عن أبي الحمراء حدثني آدم بن موسى قال: سمعت البخاري قال: عبادة أبو يحيى سمع أبا داود، عن أبي الحمراء، قال البخاري: وأبو داود كان قتادة يرميه بالكذب','2427','803','0');
INSERT INTO [t13041] VALUES (1155,'عباد بن راشد التميمي البصري حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: كان عبد الرحمن يحدثنا عن عباد بن راشد، وكان، يحيى يقول إذا ذكره: قد رأيته حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عباد بن راشد وعباد بن منصور وعباد بن ميسرة','2429','803','0');
INSERT INTO [t13041] VALUES (1156,'عباد بن ميسرة المنقري البصري حدثنا عبد الله بن أحمد قال: حدثنا أبي قال: عباد بن راشد وعباد بن ميسرة المنقري قد روى عنهما ابن مهدي، جميعا، وعباد بن راشد أثبت من عباد بن ميسرة المنقري حدثني الخضر بن داود قال: حدثنا أحمد بن محمد بن هانئ قال: سمعت أبا','2431','803','0');
INSERT INTO [t13041] VALUES (1157,'عباد بن أبي صالح السمان حدثني آدم بن موسى قال: سمعت البخاري قال: عباد بن أبي صالح السمان قال البخاري: قال علي: عباد ليس بشيء','2433','803','0');
INSERT INTO [t13041] VALUES (1158,'عباد بن منصور الناجي بصري كان يرى القدر حدثنا محمد بن زكريا قال: حدثنا محمد بن المثنى قال: حدثنا معاذ بن معاذ العنبري قال: حدثنا عمرو بن الوليد الأغضف قال: قلت لعباد بن منصور: من حدثك أن أبي بن كعب زاد ابن مسعود عن حديثه في القدر؟ قال: فقال:','2435','803','0');
INSERT INTO [t13041] VALUES (1159,'عباد بن عبد الله الأسدي حدثني آدم بن موسى قال: سمعت البخاري قال: عباد بن عبد الله الأسدي عن علي، سمع من ابن عمر، وفيه نظر','2438','803','0');
INSERT INTO [t13041] VALUES (1160,'عباد بن عبد الصمد أبو معمر عن أنس أحاديثه مناكير لا يعرف أكثرها إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: عباد بن عبد الصمد عن أنس: منكر الحديث','2440','803','0');
INSERT INTO [t13041] VALUES (1161,'عباد بن عمرو العبدي عن أنس، والحسن لا يتابع عليه','2442','803','0');
INSERT INTO [t13041] VALUES (1162,'عباد بن أبي موسى عن سالم بن زياد، عن ميمونة، روى عنه يحيى بن سليم. حدثني آدم بن موسى قال: سمعت البخاري قال: عباد بن أبي موسى عن سالم بن زياد، عن ميمونة، روى عنه يحيى بن سليم، قال البخاري: إسناده مجهول','2444','803','0');
INSERT INTO [t13041] VALUES (1163,'عباد بن كثير الثقفي بصري سكن مكة حدثنا أحمد بن علي الأبار قال: حدثنا أبو غسان قال: كان جرير يحدث عن عباد بن كثير، فيقولون: أعفنا عنه، فيقول: ويحكم، كان شيخا صالحا، فيقولون: أعفنا عنه حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: عباد بن كثير','2445','803','0');
INSERT INTO [t13041] VALUES (1164,'عباد بن كثير الفلسطيني حدثنا آدم بن موسى قال: سمعت البخاري قال: عباد بن كثير الفلسطيني فيه نظر','2446','803','0');
INSERT INTO [t13041] VALUES (1165,'عباد بن جويرية بصري ولا يتابع على حديثه، ولا يعرف إلا به. حدثنا عبد الله قال: سألت أبي عن شيخ بصري يقال له عباد بن جويرية فقال: كذاب، أتيته أنا وعلي بن المديني، وإبراهيم بن عرعرة، فقلنا له: أخرج إلينا كتاب الأوزاعي، فأخرجه فإذا فيه مسائل','2448','803','0');
INSERT INTO [t13041] VALUES (1166,'عباد بن ليث صاحب الكرابيس ولا يتابع على حديثه، ولا يعرف إلا به. حدثنا عبد الله بن أحمد بن حنبل قال: سألت أبي عن عباد بن الليث، صاحب الكرابيس فقال: الذي يحدث عن عبد المجيد أبي وهب، عن العداء بن خالد بن هوذة؟ قلت: نعم، قال: ليس بشيء، يعني عباد','2450','803','0');
INSERT INTO [t13041] VALUES (1167,'عبادة بن صهيب أبو بكر الكلبي بصري كان يرى القدر. حدثنا محمد بن إسماعيل بن سالم قال: حدثنا عباس العنبري قال: حدثنا علي بن المديني قال: قلت ليحيى بن سعيد: إن في كتاب عباد بن صهيب أحاديث عن الجعد بن أوس، يقال فيها سمعت السائب بن يزيد، فقال يحيى:','2452','803','0');
INSERT INTO [t13041] VALUES (1168,'باب عمر','2454','803','0');
INSERT INTO [t13041] VALUES (1169,'عمر بن إبراهيم عن محمد بن كعب لا يتابع على حديثه','2455','803','0');
INSERT INTO [t13041] VALUES (1170,'عمر بن إبراهيم أبو حفص البصري عن قتادة. حدثنا عبد الله بن أحمد قال: سألت أبي عن عمر بن إبراهيم العبدي، فقال: روى عن قتادة، وهو بصري، فقلت له: هو ضعيف؟ فقال: هاه، له مناكير، كان عبد الصمد يحدث عنه. حدثني الخضر بن داود قال: حدثنا أحمد بن محمد','2457','803','0');
INSERT INTO [t13041] VALUES (1171,'عمر بن أبان بن عثمان عن أبيه حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن أبان بن عثمان عن أبيه، روى عنه أبو معشر البراء، في حديثه نظر','2460','803','0');
INSERT INTO [t13041] VALUES (1172,'عمر بن أبي الحجبي مولى لهم بصري حدث عن ابن جريج، ببواطيل','2462','803','0');
INSERT INTO [t13041] VALUES (1173,'عمر بن إسماعيل عن هشام بن عروة روى عن أبو ثمامة، كلاهما مجهول، والحديث غير محفوظ','2465','803','0');
INSERT INTO [t13041] VALUES (1174,'عمر بن إسماعيل بن مجالد بن سعيد الهمداني حدثنا عبد الله بن أحمد قال: سمعت يحيى بن معين يقول: كتبت عن إسماعيل بن مجالد، وليس به بأس، وكنت أرى ابنه هذا عمر بن إسماعيل شويطرا، ليس بشيء، كذاب رجل سوء خبيث. حدثنا عن أبي معاوية بحديث ليس له أصل، عن','2467','803','0');
INSERT INTO [t13041] VALUES (1175,'عمر بن بشير أبو هانئ عن الشعبي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: أبو هانئ ضعيف','2469','803','0');
INSERT INTO [t13041] VALUES (1176,'عمر بن بسطام عن نصير بن القاسم إسناد مجهول فيه نظر، لا يعرف إلا به','2471','803','0');
INSERT INTO [t13041] VALUES (1177,'عمر بن بزيغ الأزدي عن حارث بن الحجاج، عن أبي معمر، كلاهما مجهول، والحديث غير محفوظ، ولا يعرف إلا به','2473','803','0');
INSERT INTO [t13041] VALUES (1178,'عمر بن الحكم بن ثوبان حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن الحكم بن ثوبان ذاهب الحديث قال: حدثنا','2475','803','0');
INSERT INTO [t13041] VALUES (1179,'عمر بن حبيب القاضي بصري حدثني الخضر بن داود قال: حدثنا أحمد بن محمد قال: سمعت أبا عبد الله، ذكر عمر بن حبيب القاضي قال: قدم علينا هاهنا، ولم نكتب عنه ولا حرف، وكان مستخفا به جدا حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: عمر بن حبيب ضعيف','2477','803','0');
INSERT INTO [t13041] VALUES (1180,'عمر بن حمزة حدث عنه أبو أسامة، ومروان الفزاري. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: عمر بن حمزة أحاديثه أحاديث مناكير. وحدثني أحمد بن محمود الهروي قال: حدثنا عثمان بن سعيد قال: قلت ليحيى: عمر بن حمزة الذي يروي عن سالم؟ فقال: ضعيف','2479','803','0');
INSERT INTO [t13041] VALUES (1181,'عمر بن حفص بن محبر عن عثمان بن عطاء، روى عنه، سليمان بن الربيع وسليمان مجهول وعمر بن حفص، والحديث غير محفوظ','2481','803','0');
INSERT INTO [t13041] VALUES (1182,'عمر بن حفص أبو حفص العبدي، عن ثابت حدثنا عبد الله بن أحمد قال: سألت أبي عن أبي حفص العبدي، فقال: تركنا حديثه وحرقناه حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عمر بن حفص أبو حفص العبدي ليس بشيء حدثني آدم بن موسى قال: سمعت البخاري قال','2483','803','0');
INSERT INTO [t13041] VALUES (1183,'عمر بن أبي خليفة عن هشام بن حسان منكر الحديث. قال عمر بن أبي خليفة صاحب حديث هشام، عن محمد، عن أبي هريرة، عن النبي صلى الله عليه وسلم: " آخر الكلام في القدر لشرار هذه الأمة " وهذا الحديث حديث منكر','2485','803','0');
INSERT INTO [t13041] VALUES (1184,'عمر بن داود عن سنان بن أبي سنان، كلاهما مجهول، والحديث منكر غير محفوظ، ومعلى بن ميمون ضعيف','2487','803','0');
INSERT INTO [t13041] VALUES (1185,'عمر بن ذؤيب عن ثابت مجهول بالنقل، حديثه غير محفوظ، ولعله عمر بن حفص بن ذؤيب','2489','803','0');
INSERT INTO [t13041] VALUES (1186,'عمر بن راشد اليمامي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سألت يحيى عن عمر بن راشد، فقال: ضعيف، وفي موضع آخر، سمعت يحيى قال: عمر بن راشد ليس بشيء حدثنا عبد الله قال: سألت أبي عن عمر بن راشد، فقال: هو يمامي فقلت له: هو ثقة؟ فقال: حديثه حديث','2491','803','0');
INSERT INTO [t13041] VALUES (1187,'عمر بن راشد المديني عن يزيد بن عبد الملك النوفلي بن حرملة، منكر الحديث','2494','803','0');
INSERT INTO [t13041] VALUES (1188,'عمر بن رؤبة التغلبي شامي حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن رؤبة الثعلبي شامي فيه نظر','2498','803','0');
INSERT INTO [t13041] VALUES (1189,'عمر بن رياح أبو حفص الضرير ويقال عمر بن أبي عمر العبدي، ويقال السعدي، عن ابن طاوس، وهشام بن عروة، لا يتابع عليهما، ولا يعرفان إلا به','2500','803','0');
INSERT INTO [t13041] VALUES (1190,'عمر بن زياد الهلالي كوفي حدثنا آدم بن موسى قال: سمعت البخاري قال: عمر بن زياد الهلالي كوفي يعرف وينكر','2503','803','0');
INSERT INTO [t13041] VALUES (1191,'عمر بن زرعة الخارفي كوفي حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن زرعة الخارفي فيه نظر','2505','803','0');
INSERT INTO [t13041] VALUES (1192,'عمر بن سعد البصري حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن سعد البصري لم يصح حديثه','2507','803','0');
INSERT INTO [t13041] VALUES (1193,'عمر بن سعيد عن أبي سلمة مجهول بالنقل، حديثه غير محفوظ','2509','803','0');
INSERT INTO [t13041] VALUES (1194,'عمر بن سعيد بن سريج مولى عبد الرحمن بن عوف عن الزهري، في حديثه خطأ واضطراب','2511','803','0');
INSERT INTO [t13041] VALUES (1195,'عمر بن أبي سلمة بن عبد الرحمن بن عوف الزهري حدثنا عبد الله بن أحمد بن حنبل قال: سمعت أبي يقول: لم يسمع شعبة من عمر بن أبي سلمة شيئا حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت يحيى قال: كان شعبة يضعف عمر بن أبي سلمة','2513','803','0');
INSERT INTO [t13041] VALUES (1196,'عمر بن سعيد الأبح عن سعيد بن أبي عروبية. حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن سعيد الأبح بصري منكر الحديث','2515','803','0');
INSERT INTO [t13041] VALUES (1197,'عمر بن سعيد الدمشقي أبو حفص حدثنا عبد الله بن أحمد قال: سألت أبي عمر بن سعيد الدمشقي؟ قال: كتبت منه وقد تركت حديثه، وذلك أني ذهبت إليه أنا وأبو خيثمة فأخرج إلينا كتابا عن سعيد بن بشير فإذا هي أحاديث سعيد بن أبي عروبة، وتركناه','2518','803','0');
INSERT INTO [t13041] VALUES (1198,'عمر بن سفينة عن أبيه، عن جده، حديثه غير محفوظ، ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن سفينة عن أبيه، روى عنه ابنه بريه، قال البخاري: إسناده مجهول','2519','803','0');
INSERT INTO [t13041] VALUES (1199,'عمر بن سليم القرشي عن يوسف بن إبراهيم، جميعا غير مشهورين بالنقل، ويحدثان بمناكير','2521','803','0');
INSERT INTO [t13041] VALUES (1200,'عمر بن سليم المزني أبو حفص بصري حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن سليم المزني أبو حفص بصري قال البخاري: كناه ابن مهدي ونسبه، ولا يتابع، وأبو الوليد لا يعرف بالنقل','2523','803','0');
INSERT INTO [t13041] VALUES (1201,'عمر بن سهل المازني عن شعبة، يخالف في حديثه','2525','803','0');
INSERT INTO [t13041] VALUES (1202,'عمر بن سيار الرقي عن ابن أخي الزهري، ولا يتابع على حديثه','2527','803','0');
INSERT INTO [t13041] VALUES (1203,'عمر بن شبيب المسلي حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى يقول: عمر بن شبيب لم يكن بشيء، قد رأيته','2529','803','0');
INSERT INTO [t13041] VALUES (1204,'عمر بن شوذب','2531','803','0');
INSERT INTO [t13041] VALUES (1205,'عمر بن صهبان حدثني محمد بن أحمد بن حماد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين قال: عمر بن صهبان مديني حديثه ليس بذاك حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن صهبان قال إبراهيم بن أبي يحيى: منكر الحديث','2533','803','0');
INSERT INTO [t13041] VALUES (1206,'عمر بن صالح مدني مجهول بالنقل، لا يعرف إلا بهذا، ولا يتابع عليه','2536','803','0');
INSERT INTO [t13041] VALUES (1207,'عمر بن صالح بن أبي الزاهرية الأزدي بصري لا يتابع على حديثه، ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن صالح بن أبي الزاهرية الأزدي بصري منكر الحديث','2538','803','0');
INSERT INTO [t13041] VALUES (1208,'عمر بن صالح الواسطي عن علي بن عاصم، وغيره، ولا يتابع على حديثه، ولا يعرف إلا به','2540','803','0');
INSERT INTO [t13041] VALUES (1209,'عمر بن أبي صالح العتكي عن أبي غالب، حديثه منكر، وعمر هذا وسعيد بن الفضل الراوي عنه مجهولين جميعا بالنقل، ولا يتابع على حديثه، ولا يثبت في هذا المتن شيء','2542','803','0');
INSERT INTO [t13041] VALUES (1210,'عمر بن صبيح الكندي عن الأحنف بن قيس، حديثه ليس بالقائم، وليس بمعروف بالنقل، ولا يبين سماعه من الأحنف','2544','803','0');
INSERT INTO [t13041] VALUES (1211,'عمر بن عبد الله بن يعلى بن مرة الثقفي كوفي حدثنا أحمد بن علي الأبار قال: حدثنا يوسف بن موسى القطان قال: سمعت جرير بن عبد الحميد يقول: أردت أن أسأل، عمر بن عبد الله بن يعلى الثقفي عن أحاديث فقال لي زائدة: لا تسأله عن شيء فإني رأيته يشرب الخمر حدثنا','2546','803','0');
INSERT INTO [t13041] VALUES (1212,'عمر بن أبي زائدة كان يرى القدر، وفي الحديث مستقيم. حدثنا عبد الله بن أحمد قال: قال أبي: عمر بن أبي زائدة أخو زكريا بن أبي زائدة ليس به بأس وكان يرى القدر. حدثنا محمد بن صالح قال: حدثنا علي قال: سمعت يحيى يقول: قال عمر بن أبي زائدة: كتب أيوب بن','2549','803','0');
INSERT INTO [t13041] VALUES (1213,'عمر بن عبد الله مولى غفرة','2550','803','0');
INSERT INTO [t13041] VALUES (1214,'عمر بن علي المقدمي حدثنا عبد الله بن أحمد قال: سمعت يحيى بن معين، يذكر عمر بن علي المقدمي فقال: لم أكتب عنه شيئا، وأصله واسطي نزل البصرة وكان يدلس، وما كان به بأس حدثنا عبد الله قال: سمعت أبي يذكره، فأثنى عليه خيرا وقال: كان يدلس','2552','803','0');
INSERT INTO [t13041] VALUES (1215,'عمر بن عطاء بن وراز عن عكرمة حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عمر بن عطاء بن وراز قال يحيى: كل شيء عند ابن جريج عن عكرمة، فهو عن عمر بن عطاء بن وراز، وهم يضعفونه','2554','803','0');
INSERT INTO [t13041] VALUES (1216,'عمر بن عبيد أبو حفص الخزاز بصري عن سهل بن أبي صالح، في حديثه اضطراب','2556','803','0');
INSERT INTO [t13041] VALUES (1217,'عمر بن عيسى القرشي لعله عمر الحميدي عن ابن جريج، مجهول بالنقل، حديثه غير محفوظ، ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن عيسى عن ابن جريج، عن عطاء، عن ابن عباس، منكر الحديث','2559','803','0');
INSERT INTO [t13041] VALUES (1218,'عمر بن عامر السلمي حدثنا عبد الله بن أحمد قال: سئل أبي عن عمر بن عامر، فقال: كان شعبة لا يستمرئه، وقد حدثنا عنه معتمر بن سليمان وعباد بن العوام، وروى عنه سعيد بن أبي عروبة حدثنا عبد الله، في موضع آخر قال: سألت أبي عن عمر بن عامر السلمي فقال: أبو','2561','803','0');
INSERT INTO [t13041] VALUES (1219,'عمر بن غياث كوفي ويقال عمرو حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن غياث كوفي ويقال: عمرو، في حديثه نظر','2563','803','0');
INSERT INTO [t13041] VALUES (1220,'عمر بن فرقد الباهلي حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن فرقد الباهلي فيه نظر','2566','803','0');
INSERT INTO [t13041] VALUES (1221,'عمر بن قيس المكي يعرف بسندل','2568','803','0');
INSERT INTO [t13041] VALUES (1222,'عمر بن محمد عن ثابت، ولا يتابع عليه ولا يعرف إلا به','2570','803','0');
INSERT INTO [t13041] VALUES (1223,'عمر بن المغيرة المصيصي عن داود بن أبي هند، ولا يتابع على رفعه','2572','803','0');
INSERT INTO [t13041] VALUES (1224,'عمر بن مصعب بن الزبير عن عروة، ولا يتابع على حديثه، ولا يعرف إلا به','2574','803','0');
INSERT INTO [t13041] VALUES (1225,'عمر بن المثنى عن قتادة، روى عنه، بقية، حديثه غير محفوظ','2576','803','0');
INSERT INTO [t13041] VALUES (1226,'عمر بن موسى الوجيهي حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى قال: عمر بن موسى الوجيهي ليس بثقة حدثنا أحمد بن داود القومسي قال: حدثنا العباس بن الوليد الخلال قال: حدثني يحيى بن صالح، عن عفير، عن ابن معدان قال: قدم علينا عمر بن','2578','803','0');
INSERT INTO [t13041] VALUES (1227,'عمر بن مسكين عن نافع حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن مسكين عن نافع، لا يتابع عليه','2580','803','0');
INSERT INTO [t13041] VALUES (1228,'عمر بن معتب حدثنا الفضل بن جعفر قال: حدثنا إسماعيل بن إسحاق، عن علي بن المديني قال: عمر بن معتب منكر الحديث. ويقال عمر بن أبي مغيث','2582','803','0');
INSERT INTO [t13041] VALUES (1229,'عمر بن مساور العتكي عن أبي جمرة. حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن مساور ويقال ابن مسافر العتكي عن أبي جمرة، منكر الحديث','2583','803','0');
INSERT INTO [t13041] VALUES (1230,'عمر بن نبهان عن قتادة حدثني آدم بن موسى قال: سمعت البخاري قال: عمر بن نبهان عن قتادة، ولا يتابع في حديثه','2585','803','0');
INSERT INTO [t13041] VALUES (1231,'عمر بن الوليد الشني حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي أبو حفص قال: لم يحدثنا يحيى عن عمر بن الوليد الشني، وسمعت يحيى، عن أبي مكين حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سمعت يحيى، وذكر، عمر بن الوليد الشني فقال','2587','803','0');
INSERT INTO [t13041] VALUES (1232,'عمر بن هارون البلخي حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين قال: عمر بن هارون البلخي ليس بشيء حدثنا محمد بن إسماعيل قال: حدثنا محمود بن غيلان قال: سئل وكيع وأنا أسمع، عن عمر بن هارون البلخي، فقال: نعم، رحمه الله، بات','2588','803','0');
INSERT INTO [t13041] VALUES (1233,'عمر بن يزيد الشيباني الرفاء شيخ بصري مجهول بالنقل، جاء عن شعبة بحديث معضل','2590','803','0');
INSERT INTO [t13041] VALUES (1234,'عمر بن الهجنع عن أبي بكرة، لا يتابع عليه ولا يعرف إلا به، وعبد الجبار بن العباس من الشيعة','2592','803','0');
INSERT INTO [t13041] VALUES (1235,'عمر بن يزيد النصري عن الزهري، يخالف في حديثه','2594','803','0');
INSERT INTO [t13041] VALUES (1236,'عمر التميمي عن الحسن بن علي حدثني آدم بن موسى قال: سمعت البخاري قال: عمر التميمي عن الحسن بن علي، قال البخاري: لا أراه يصح','2596','803','0');
INSERT INTO [t13041] VALUES (1237,'باب عثمان','2598','803','0');
INSERT INTO [t13041] VALUES (1238,'عثمان بن حفص بن خلدة الزرقي المديني حدثني آدم بن موسى قال: سمعت البخاري قال: عثمان بن حفص بن خلدة الزرقي المديني روى عن عباد بن إسحاق، في إسناده نظر','2599','803','0');
INSERT INTO [t13041] VALUES (1239,'عثمان بن خالد العثماني أبو عفان عن مالك، وابن أبي الزناد الغالب على حديثه الوهم','2601','803','0');
INSERT INTO [t13041] VALUES (1240,'عثمان بن دينار أخو مالك بن دينار تروي عنه حكامة ابنته أحاديث بواطيل ليس لها أصل','2605','803','0');
INSERT INTO [t13041] VALUES (1241,'عثمان بن داود مجهول بنقل الحديث، لا يتابع على حديثه، ولا يعرف إلا به','2607','803','0');
INSERT INTO [t13041] VALUES (1242,'عثمان بن أبي راشد الأزدي عن أبي راشد، وله صحبة، ولا يصح حديثه من أجل شاذان، رمى الناس بحديثه','2609','803','0');
INSERT INTO [t13041] VALUES (1243,'عثمان بن رواد مؤذن مسجد بني عقيل بصري في حديثه اضطراب ووهم','2611','803','0');
INSERT INTO [t13041] VALUES (1244,'عثمان بن زائدة عن نافع، حديثه غير محفوظ. روى عنه عبد الملك بن مهران، وعبد الملك، متروك','2613','803','0');
INSERT INTO [t13041] VALUES (1245,'عثمان بن سالم بصري لا يتابع على حديثه','2615','803','0');
INSERT INTO [t13041] VALUES (1246,'عثمان بن ساج عن خصيف، ولا يتابع عليه','2617','803','0');
INSERT INTO [t13041] VALUES (1247,'عثمان بن سعد الكاتب حدثني الخضر بن داود قال: حدثنا أحمد بن محمد قال: سمعت أبا عبد الله، قيل عن عثمان بن سعد الكاتب، يروي عن مجاهد قال: كان روح يكثر عنه يحدث عن أنس، وقد حكوا عن يحيى بن سعيد القطان فيه شيئا شديدا حدثنا محمد قال: حدثنا صاح قال:','2620','803','0');
INSERT INTO [t13041] VALUES (1248,'عثمان بن سماك عن أبي هارون العبدي مجهول بالنقل، حديثه غير محفوظ، ولا يعرف إلا به','2622','803','0');
INSERT INTO [t13041] VALUES (1249,'عثمان بن عبد الله العبدي عن حميد الطويل، حديثه غير محفوظ ولا يعرف إلا به','2624','803','0');
INSERT INTO [t13041] VALUES (1250,'عثمان بن عبد الرحمن الوقاصي الزهري حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين يقول: عثمان بن عبد الرحمن الوقاصي ضعيف. وقال في موضع آخر: ليس بشيء حدثني آدم بن موسى قال: سمعت البخاري قال: عثمان بن عبد الرحمن الوقاصي تركوه','2626','803','0');
INSERT INTO [t13041] VALUES (1251,'عثمان بن عبد الرحمن الطرائفي حدثنا آدم بن موسى قال: سمعت البخاري قال: عثمان بن عبد الرحمن الطرائفي كان يسمع أحاديث طرائف فسمي بذلك، يروي عن قوم ضعاف','2628','803','0');
INSERT INTO [t13041] VALUES (1252,'عثمان بن عبد الله الشحام حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت يحيى، وذكر، أو ذكر عنده الشحام فقال: تعرف وتنكر، ولم يكن عنده بذاك','2630','803','0');
INSERT INTO [t13041] VALUES (1253,'عثمان بن عثمان القرشي عن علي بن زيد، في حديثه نظر. حدثنا يوسف بن يزيد قال: حدثنا نعيم بن حماد قال: حدثنا عثمان بن عثمان القرشي، عن علي بن زيد قال: سمعت سعيد بن المسيب يقول: لقد رأيت عليا وعثمان في هذا المقعد يتشاتمان بشيء لا أحدث به أحدا أبدا، ثم','2631','803','0');
INSERT INTO [t13041] VALUES (1254,'عثمان بن عطاء الخراساني حدثنا محمد بن عثمان بن أبي شيبة قال: سمعت يحيى بن معين، يسأل عن عثمان بن عطاء، فقال: كان ضعيفا','2632','803','0');
INSERT INTO [t13041] VALUES (1255,'عثمان بن عمير أبو اليقظان كوفي حدثنا محمد بن عيسى الهاشمي قال: حدثنا إبراهيم الزهري قال: حدثنا إبراهيم بن محمد بن عرعرة قال: حدثنا أبو أحمد الزبيري قال: كان الحارث بن حصيرة وعثمان أبو اليقظان يؤمنان بالرجعة','2634','803','0');
INSERT INTO [t13041] VALUES (1256,'عثمان بن فائد القرشي بصري حدثنا آدم بن موسى قال: سمعت البخاري قال: عثمان بن فائد القرشي بصري، روى عنه سليمان بن عبد الرحمن، في حديثه نظر','2636','803','0');
INSERT INTO [t13041] VALUES (1257,'عثمان بن غياث حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت يحيى يقول: كان عند عثمان بن غياث كتابا عن عكرمة، فلم يصححه لنا','2639','803','0');
INSERT INTO [t13041] VALUES (1258,'عثمان بن موسى المزني مجهول بالنقل، حديثه غير محفوظ، ولا يعرف إلا به','2641','803','0');
INSERT INTO [t13041] VALUES (1259,'عثمان مؤذن بني أفصى من الشيعة، وبكير أيضا منهم','2643','803','0');
INSERT INTO [t13041] VALUES (1260,'عثمان بن مطر الشيباني حدثنا محمد بن عثمان بن أبي شيبة قال: سئل يحيى بن معين وأنا أسمع من عثمان بن مطر قال: كان ضعيفا ضعيفا','2645','803','0');
INSERT INTO [t13041] VALUES (1261,'عثمان بن مقسم البري البصري حدثنا أحمد بن إبراهيم بن عنير البصري قال: حدثنا نصر بن علي قال: حدثنا مسلم قال: حدثنا شعبة، قال أفادني مرة عثمان البري عن قتادة، حدثنا قال: سألت قتادة فلم يعرفه، قال: فجعل عثمان يقول: بل أنت حدثني، فيقول: لا، فيقول','2647','803','0');
INSERT INTO [t13041] VALUES (1262,'عثمان بن أبي العاتكة','2650','803','0');
INSERT INTO [t13041] VALUES (1263,'عثمان البتي حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين قال: عثمان البتي ضعيف','2652','803','0');
INSERT INTO [t13041] VALUES (1264,'عثمان بن محمد بن أبي شيبة العبسي','2653','803','0');
INSERT INTO [t13041] VALUES (1265,'باب علي','2658','803','0');
INSERT INTO [t13041] VALUES (1266,'علي بن الجند مجهول في النسب والرواية، حديثه غير محفوظ','2659','803','0');
INSERT INTO [t13041] VALUES (1267,'علي بن الجعد الجوهري أحمد بن محمد بن صدقة قال: حدثنا أبو يحيى الناقد قال: سمعت أبا غسان المروزي يقول: كنت عند علي بن الجعد فذكروا عنده حديث ابن عمر: كنا نفاضل على عهد رسول الله صلى الله عليه وسلم فنقول: خير هذه الأمة بعد النبي صلى الله عليه وسلم أبو','2661','803','0');
INSERT INTO [t13041] VALUES (1268,'علي بن الحسين بن واقد المروزي حدثني عبد الله بن أحمد بن عبد السلام قال: سمعت البخاري قال: رأينا علي بن الحسين بن واقد في سنة عشر، وكان أبو يعقوب سيئ الرأي فيه في حياته لعلة الإرجاء، فتركناه، ثم كتبت عن إسحاق عنه','2662','803','0');
INSERT INTO [t13041] VALUES (1269,'علي بن حزور ويقال علي بن أبي فاطمة حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى قال: علي بن حزور وعيسى بن قرطاس وسعد بن طريف والنضر أبو عمر الخزاز ليس يحل لأحد أن يروي عنهم','2664','803','0');
INSERT INTO [t13041] VALUES (1270,'علي بن بذيمة حدثنا عبد الله بن أحمد قال: سألت أبي عن علي بن بذيمة، فقال: صالح الحديث، ولكن كان رأسا في التشيع','2666','803','0');
INSERT INTO [t13041] VALUES (1271,'علي بن حميد السلولي عن شعبة، ولا يتابع على رفع حديثه','2668','803','0');
INSERT INTO [t13041] VALUES (1272,'علي بن ربيعة القرشي مجهول بالنقل، حديثه غير محفوظ، ولا يتابعه إلا من هو دونه','2671','803','0');
INSERT INTO [t13041] VALUES (1273,'علي بن زيد بن جدعان حدثنا محمد بن أيوب بن يحيى بن الضريس قال: قلت لأبي الوليد الطيالسي: كيف سمعت شعبة يقول: حدثنا علي بن زيد بن جدعان وكان رفاعا؟ فقال لي مجيبا: هذه غيبة، ولم ينكره حدثني الهيثم بن خلف قال: حدثنا أبو بكر الأعين قال: حدثنا أبو','2673','803','0');
INSERT INTO [t13041] VALUES (1274,'علي بن سالم عن علي بن زيد بصري. حدثني آدم بن موسى قال: سمعت البخاري قال: علي بن سالم عن علي بن زيد بصري لا يتابع على حديثه','2674','803','0');
INSERT INTO [t13041] VALUES (1275,'علي بن أبي سارة عن ثابت، ولا يتابع عليه من جهة تثبت. حدثني آدم بن موسى قال: سمعت البخاري قال: علي بن أبي سارة الشيباني في حديثه نظر','2676','803','0');
INSERT INTO [t13041] VALUES (1276,'علي بن صالح بن حي الهمداني حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن يحدث عن علي بن صالح شيئا قط حدثنا محمد بن عبد الرحمن قال: حدثنا عبد الملك بن عبد الحميد الميموني قال: سمعت أحمد بن حنبل يقول: علي بن صالح صالح الحديث','2678','803','0');
INSERT INTO [t13041] VALUES (1277,'علي بن ظبيان عن عبيد الله بن عمر، حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: علي بن ظبيان ليس بشيء حدثني آدم بن موسى قال: سمعت البخاري قال: علي بن ظبيان عن عبيد الله بن عمر، منكر الحديث','2679','803','0');
INSERT INTO [t13041] VALUES (1278,'علي بن أبي طلحة حدثني محمد بن عبد الرحمن قال: حدثني عبد الملك بن عبد الحميد قال: قال لي أحمد بن حنبل: علي بن أبي طلحة له أشياء منكرات، وهو رجل من أهل حمص','2681','803','0');
INSERT INTO [t13041] VALUES (1279,'علي بن عبد الله بن جعفر بن نجيح جنح إلى ابن أبي داود والجهمية، وحديثه مستقيم إن شاء الله. حدثنا أحمد بن محمد بن سليمان الرازي قال: سمعت أزهر بن جميل يقول: " كنا عند يحيى بن سعيد القطان وثم سهل بن حسان بن أبي جروبة وابن المديني والشاذكوني وسليمان صاحب','2682','803','0');
INSERT INTO [t13041] VALUES (1280,'علي بن علي الرفاعي حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى يقول: كان علي بن علي يقول بالقدر','2685','803','0');
INSERT INTO [t13041] VALUES (1281,'علي بن أبي علي اللهبي، ويقال ابن علي حدثنا الخضر بن داود قال: حدثنا أحمد بن محمد قال: سمعت أبا عبد الله، يسأل عن علي بن أبي علي اللهبي، فلم أره يرضاه حدثني آدم بن موسى قال: سمعت البخاري قال: علي بن أبي علي اللهبي منكر الحديث','2686','803','0');
INSERT INTO [t13041] VALUES (1282,'علي بن علقمة الأنماري كوفي حدثني آدم بن موسى قال: سمعت البخاري قال: علي بن علقمة الأنصاري كوفي، في حديثه نظر','2688','803','0');
INSERT INTO [t13041] VALUES (1283,'علي بن عيسى الجندي عن أبيه، لا يتابع على حديثه','2690','803','0');
INSERT INTO [t13041] VALUES (1284,'علي بن عيسى الأصمعي عن ابن أبي عروبة، مجهول النقل، وحديثه غير محفوظ، ولا يعرف إلا به','2692','803','0');
INSERT INTO [t13041] VALUES (1285,'علي بن عابس الكوفي الأسدي عن العلاء بن المسيب. حدثني آدم بن موسى قال: سمعت البخاري قال: علي بن عابس الأسدي كوفي، عن العلاء بن المسيب، عن أبيه، ضعفه يحيى بن معين وقال: قد رأيته. وحدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: علي بن','2694','803','0');
INSERT INTO [t13041] VALUES (1286,'علي بن عاصم بن صهيب أبو الحسن الواسطي حدثنا جعفر بن محمد قال: حدثنا عثمان بن أبي شيبة يقول: كنا عند يزيد بن هارون أنا وأخي أبو بكر، فقال: يا أبا خالد علي بن عاصم أيش حاله عندك؟ قال: حسبكم، ما زلنا نعرفه بالكذب حدثنا معاذ بن المثنى قال: حدثنا محمد','2696','803','0');
INSERT INTO [t13041] VALUES (1287,'علي بن غراب أبو الحسن كوفي حدثنا عبد الله بن أحمد، قال سألت أبي عن علي بن غراب المحاربي قال: ليس لي به خبرة، سمعت منه مجلسا واحدا كان يدلس، ما أراه إلا كان صدوقا حدثني آدم بن موسى قال: سمعت البخاري قال: علي بن غراب قال أحمد: كان يدلس','2698','803','0');
INSERT INTO [t13041] VALUES (1288,'علي بن القاسم الكندي عن نعيم بن ضمضم، إسناد شيعي، فيه نظر، ولا يتابعه إلا من هو دونه أو نحوه','2700','803','0');
INSERT INTO [t13041] VALUES (1289,'علي بن قتيبة الرفاعي بصري يحدث عن الثقات، بالبواطيل وما لا أصل له','2702','803','0');
INSERT INTO [t13041] VALUES (1290,'علي بن قرين كان يضع الحديث، كان ببغداد. حدثني أحمد بن محمود قال: حدثنا عثمان بن سعيد قال: قال لي يحيى بن معين: لا تكتب عن علي بن قرين، شيخ ببغداد، فإنه كذاب خبيث','2705','803','0');
INSERT INTO [t13041] VALUES (1291,'علي بن مسعدة الباهلي بصري حدثني آدم بن موسى قال: سمعت البخاري قال: علي بن مسعد الباهلي، بصري فيه نظر','2707','803','0');
INSERT INTO [t13041] VALUES (1292,'علي بن مسهر كوفي حدثني الخضر بن داود قال: حدثنا أحمد بن محمد قال: سمعت أبا عبد الله يقول: أما علي بن مسهر فلا أدري كيف أقول، ثم قال: إن علي بن مسهر كان قد ذهب بصره وكان يحدثهم من حفظه','2709','803','0');
INSERT INTO [t13041] VALUES (1293,'علي بن مالك العبدي حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى يقول: علي بن مالك العبدي ليس حديثه بشيء','2710','803','0');
INSERT INTO [t13041] VALUES (1294,'علي بن أبي محمد عن عكرمة، مجهول بالنقل، حديثه غير محفوظ','2712','803','0');
INSERT INTO [t13041] VALUES (1295,'علي بن المهاجر العيشي بصري عن هيصم بن الشداخ، كلاهما مجهول، والحديث غير محفوظ','2714','803','0');
INSERT INTO [t13041] VALUES (1296,'علي بن مجاهد الكابلي حدثنا أحمد بن علي قال: سألت أبا غسان زنجا عن علي بن مجاهد الكابلي، فقال: تركته، ولم يرضه','2716','803','0');
INSERT INTO [t13041] VALUES (1297,'علي بن قادم حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين يقول: علي بن قادم ضعيف','2718','803','0');
INSERT INTO [t13041] VALUES (1298,'علي بن نافع عن بهز بن حكيم، مجهول بالنقل، حديثه غير محفوظ','2719','803','0');
INSERT INTO [t13041] VALUES (1299,'علي بن نفيل الحراني هو جد النفيلي عن سعيد بن المسيب، في المهدي، لا يتابع عليه، ولا يعرف إلا به','2722','803','0');
INSERT INTO [t13041] VALUES (1300,'علي بن يزيد بن ركانة','2724','803','0');
INSERT INTO [t13041] VALUES (1301,'علي بن يزيد الألهاني حدثني آدم قال: قال: سمعت البخاري قال: علي بن يزيد أبو عبد الملك الألهاني عن القاسم، شامي منكر الحديث','2726','803','0');
INSERT INTO [t13041] VALUES (1302,'علي بن هاشم بن البريد حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: حدثنا محمد بن داود الحراني قال: سمعت عيسى بن يونس، وسئل عن علي بن هاشم بن البريد، فقال: أهل بيت تشيع، وليس ثم كذاب','2728','803','0');
INSERT INTO [t13041] VALUES (1303,'علي بن يونس البلخي عن هشام بن الغاز، ولا يتابع على حديثه','2730','803','0');
INSERT INTO [t13041] VALUES (1304,'باب عمرو','2732','803','0');
INSERT INTO [t13041] VALUES (1305,'عمرو بن الأزهر العتكي حدثنا أحمد بن علي الأبار قال: سمعت مجاهد بن موسى يقول: قال أبو سعيد الحداد: كان عمرو بن الأزهر يكذب مجاوبة، قلت وكيف كان يكذب مجاوبة، قال: قالوا له: تعرف في الحائك يأخذ الخيوط؟ فقال: حدثنا هشام، عن الحسن قال: الخيوط','2733','803','0');
INSERT INTO [t13041] VALUES (1306,'عمرو بن أبي بكر يماني روى عنه، همام بن نافع، في حديثه نظر، ولعله عمرو بن برق','2734','803','0');
INSERT INTO [t13041] VALUES (1307,'عمرو بن بكر السكسكي عن أبي سنان الشيباني، حديثه غير محفوظ','2736','803','0');
INSERT INTO [t13041] VALUES (1308,'عمرو بن بشر بن السرح عن عنبسة بن سعيد بن غنيم، منكر الحديث','2738','803','0');
INSERT INTO [t13041] VALUES (1309,'عمرو بن برق وهو عمرو بن مسلم حدثني محمد بن عبد الرحمن قال: حدثنا عبد الملك بن عبد الحميد قال: سمعت أحمد بن حنبل يقول: عمرو بن برق له أشياء مناكير، ومعمر قد روى عنه، وكان عنده لا بأس به، وكانت له علة، ثم أشار أبو عبد الله بيده إلى فيه، أي يشرب','2742','803','0');
INSERT INTO [t13041] VALUES (1310,'عمرو بن تميم عن أبيه، عن أبي هريرة. حدثني آدم بن موسى قال: سمعت البخاري قال: عمرو بن تميم، عن أبيه، عن أبي هريرة، في فضل شهر رمضان روى عنه، كثير بن زيد، في حديثه نظر','2745','803','0');
INSERT INTO [t13041] VALUES (1311,'عمرو بن ثابت بن أبي المقدام واسم أبي المقدام هرمز كوفي. حدثني آدم بن موسى قال: سمعت البخاري قال: عمرو بن ثابت ليس بالقوي عندهم. حدثنا إبراهيم بن يوسف قال: حدثنا هناد بن السري قال: كتبت عن عمرو بن ثابت قال: حدثنا كثير، فبلغني عنه أنه كان يوما عند','2747','803','0');
INSERT INTO [t13041] VALUES (1312,'عمرو بن جابر الحضرمي مصري حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: بلغني أن عمرو بن جابر الأزدي، حدث عنه ابن لهيعة، وسعيد بن أبي أيوب، كان يكذب، قال أبي: يروي عن جابر، أحاديث مناكير حدثني يحيى بن عثمان قال: حدثنا سعيد بن أبي مريم قال: قلت','2748','803','0');
INSERT INTO [t13041] VALUES (1313,'عمرو بن جميع كوفي حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عمرو بن جميع كذاب خبيث، كان قاضي حلوان','2750','803','0');
INSERT INTO [t13041] VALUES (1314,'عمرو بن جرير عن إسماعيل بن أبي خالد، عنده مناكير','2752','803','0');
INSERT INTO [t13041] VALUES (1315,'عمرو بن حمزة القيسي بصري عن يونس بن عبيد، وغيره، لا يتابع على حديثه','2755','803','0');
INSERT INTO [t13041] VALUES (1316,'يقول: عمرو بن حكام بن أبي الوضاح الأزدي أبو عثمان بصري حدثنا عبد الله بن أحمد بن حنبل قال: سألت أبي، عن عمرو بن حكام، فقال: الزنجبيلي وسألته مرة أخرى، عن عمرو بن حكام، فقال: كان يروي عن شعبة نحو أربعة آلاف حديث، وترك حديثه، فقلت له: هو ثقة؟','2758','803','0');
INSERT INTO [t13041] VALUES (1317,'عمرو بن خالد الواسطي حدثنا يوسف بن يعقوب السمسار قال: حدثنا الفضل بن سهل قال: حدثنا معلى بن منصور قال: حدثنا أبو عوانة قال: كان عمرو بن خالد ليس بشيء، متروك الحديث','2760','803','0');
INSERT INTO [t13041] VALUES (1318,'عمرو بن دينار مولى آل الزبير أبو يحيى الأعور حدثنا زكريا بن يحيى قال: قال: حدثنا زياد بن أيوب قال: حدثنا إسماعيل ابن علية قال: حدثنا عمرو بن دينار، وكيل آل الزبير، قال إسماعيل: لم يكن هذا الشيخ يحفظ الحديث، قال: وقد قال أكثر من هذا حدثني آدم بن','2763','803','0');
INSERT INTO [t13041] VALUES (1319,'عمرو ذو مر كوفي روى عنه أبو إسحاق. حدثني آدم بن موسى قال: سمعت البخاري قال: عمرو ذو مر روى عنه أبو إسحاق وحده، لا يعرف','2765','803','0');
INSERT INTO [t13041] VALUES (1320,'عمرو بن الزبان كوفي مجهول في النقل: لا يعرف إلا بهذا، ولا يتابع عليه','2767','803','0');
INSERT INTO [t13041] VALUES (1321,'عمرو بن سعيد الخولاني عن أنس، لا يتابع عليه، ولا يعرف إلا به، ولا يتابعه إلا من هو دونه','2769','803','0');
INSERT INTO [t13041] VALUES (1322,'عمرو بن أبي سلمة التنيسي أبو حفص في حديثه وهم','2771','803','0');
INSERT INTO [t13041] VALUES (1323,'عمرو بن شعيب بن محمد بن عبد الله بن عمرو بن العاص السهمي حدثنا أحمد بن علي الأبار قال: حدثنا علي بن ميمون الرقي قال: سمعت ابن عيينة، وسئل عن عمرو بن شعيب، فقال: غيره خير منه، وقد روى عنه ثقات الناس: أيوب وعمرو بن دينار وقتادة وعبيد الله بن عمر','2774','803','0');
INSERT INTO [t13041] VALUES (1324,'عمرو بن زياد الثوباني من ولد ثوبان مولى رسول الله صلى الله عليه وسلم','2775','803','0');
INSERT INTO [t13041] VALUES (1325,'عمرو بن شمر أبو عبد الله الجعفي حدثنا محمد بن أيوب قال: حدثنا عبيد بن يعيش قال: حدثنا خلاد بن يزيد الجعفي، قال جاءني سفيان بن سعيد إلى هاهنا فقال: عمرو بن شمر هذا أكثر عن جابر، وما رأيته عنده قط. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال:','2778','803','0');
INSERT INTO [t13041] VALUES (1326,'عمرو بن صفوان بن عبد الله المزني عن عروة بن الزبير، ولا يتابع على حديثه، ولا يعرف بنقل الحديث','2780','803','0');
INSERT INTO [t13041] VALUES (1327,'عمرو بن عبيد بن باب البصري أبو عثمان حدثنا يحيى بن عثمان قال: حدثنا نعيم بن حماد قال: حدثنا أبو داود، عن شعبة، عن يونس قال: كان عمرو بن عبيد يكذب في الحديث. قال يحيى: حدثنا نعيم قال: سمعت ابن المبارك، وقيل له: تركت عمرو بن عبيد وتحدث عن هشام','2782','803','0');
INSERT INTO [t13041] VALUES (1328,'عمرو بن عبد الغفار الفقيمي منكر الحديث','2791','803','0');
INSERT INTO [t13041] VALUES (1329,'عمرو بن عبد الجبار السنجاري عن أبي شهاب، ولا يتابع على حديثه','2793','803','0');
INSERT INTO [t13041] VALUES (1330,'عمرو بن عثمان الكلابي الرقي حدثنا أحمد بن علي الأبار قال: سألت علي بن ميمون الرقي عن عمرو بن عثمان الكلابي، فقال: كان إنسان عندنا يقال له أبو مطر، فجاءني ابنه بكتبه لبيعها له، فقال لي عمرو بن عثمان الكلابي: جئني بشيء منها، فجئت، فكان يحدث منها،','2795','803','0');
INSERT INTO [t13041] VALUES (1331,'عمرو بن عثمان الثقفي عن الثوري، ولا يتابع عليه','2796','803','0');
INSERT INTO [t13041] VALUES (1332,'عمرو بن أبي عمرو مولى المطلب بن عبد الله بن حنطب حدثنا محمد بن إسماعيل قال: حدثنا العباس بن محمد قال: سمعت يحيى يقول: عمرو بن أبي عمرو لا يحتج بحديثه حدثنا محمد قال: حدثنا عباس في موضع آخر قال: سمعت يحيى قال: كان مالك يروي عن عمرو بن أبي عمرو،','2799','803','0');
INSERT INTO [t13041] VALUES (1333,'عمرو بن عطية الوادعي عن أبيه، عن عكرمة. حدثني آدم بن موسى قال: سمعت البخاري قال: عمرو بن عطية الوادعي، عن أبيه، عن عكرمة، في حديثه نظر','2800','803','0');
INSERT INTO [t13041] VALUES (1334,'عمرو بن عطية العوفي حدثني آدم بن موسى قال: سمعت البخاري قال: عمرو بن عطية العوفي في حديثه نظر','2802','803','0');
INSERT INTO [t13041] VALUES (1335,'عمرو بن فائد الأسواري كان يذهب إلى القدر والاعتزال ولا يقيم الحديث. حدثنا زكريا بن يحيى قال: حدثنا زياد بن يحيى الحساني قال: حدثنا مرحوم بن عبد الغفار العطار قال: رأيت في المنام ليلة مات عمرو بن فائد كأن سريره مروا به في المربد وعليه برد من حول','2804','803','0');
INSERT INTO [t13041] VALUES (1336,'عمرو بن مسلم الجندي حدثنا عبد الله بن أحمد قال: قلت ليحيى: شيخ روى عنه ابن عيينة، ومعمر، يقال له عمرو بن مسلم؟ قال: الجندي؟ قلت: نعم، قال: هو أضعف من هشام بن حجير، وضعف عمرا قلت ليحيى: هشام بن حجير أحب إليك من عمرو؟ قال: نعم حدثنا عبد الله','2806','803','0');
INSERT INTO [t13041] VALUES (1337,'عمرو بن مرزوق الباهلي أبو عثمان حدثنا محمد بن زكريا قال: حدثنا الحسن بن شجاع البلخي قال: سمعت علي بن المديني يقول: اتركوا حديث الفهدين والعمرين، يعني فهد بن عوف وفهد بن حيان وعمرو بن مرزوق وعمرو بن حكام رأيت في أصل كتاب محمد بن مسلم بن وارة أخرجه إلي','2807','803','0');
INSERT INTO [t13041] VALUES (1338,'عمرو بن النضر عن إسماعيل بن أبي خالد، لا يتابع على حديثه ولا يعرف إلا به','2808','803','0');
INSERT INTO [t13041] VALUES (1339,'عمرو بن واقد الدمشقي حدثني آدم بن موسى قال: سمعت البخاري قال: عمرو بن واقد الدمشقي مولى بني أمية منكر الحديث','2810','803','0');
INSERT INTO [t13041] VALUES (1340,'عمرو بن واقد بصري عن محمد بن عمرو، ولا يتابع على حديثه','2812','803','0');
INSERT INTO [t13041] VALUES (1341,'عمرو بن هاشم الجنبي كوفي حدثني آدم بن موسى قال: سمعت البخاري قال: عمرو بن هاشم الجنبي كوفي فيه نظر','2814','803','0');
INSERT INTO [t13041] VALUES (1342,'عمرو بن هاشم عن ابن عجلان، مجهول النقل، ولا يتابع على حديثه','2816','803','0');
INSERT INTO [t13041] VALUES (1343,'عمرو بن يزيد التميمي أبو بردة كوفي عن علقمة بن مرثد، ولا يتابع على حديثه','2818','803','0');
INSERT INTO [t13041] VALUES (1344,'عمران بن أوس بن ضمعج عن أبيه حدثني آدم بن موسى قال: سمعت البخاري قال: عمران بن أوس بن ضمعج، عن أبيه، ولا يتابع عليه ولا يتبين سماعه من عائشة','2820','803','0');
INSERT INTO [t13041] VALUES (1345,'عمران بن أنس أبو أنس عن ابن أبي مليكة، ولا يتابع على حديثه','2822','803','0');
INSERT INTO [t13041] VALUES (1346,'عمران بن أبان الواسطي عن محمد بن مسلم، ولا يتابع عليه، ولا على غير شيء من حديثه','2824','803','0');
INSERT INTO [t13041] VALUES (1347,'عمران بن حطان عن عائشة، ولا يتابع على حديثه، وكان يرى رأي الخوارج، ولا يتبين سماعه من عائشة','2826','803','0');
INSERT INTO [t13041] VALUES (1348,'عمران بن ظبيان عن حكيم بن سعد. حدثني آدم بن موسى قال: سمعت البخاري قال: عمران بن ظبيان كوفي، عن حكيم بن سعد، فيه نظر','2828','803','0');
INSERT INTO [t13041] VALUES (1349,'عمران بن أبي عطاء أبو حمزة القصاب واسطي عن ابن عباس، لا يتابع على حديثه، ولا يعرف إلا به. حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سمعت سفيان يقول: قدم علينا أبو حمزة صاحب ابن عباس فلم آته. حدثنا عبد الله قال: سمعت أبي يقول','2830','803','0');
INSERT INTO [t13041] VALUES (1350,'عمران بن عبد الله المعافري حدثنا أحمد بن عون قال: حدثنا عثمان بن سعيد قال: سألت يحيى بن معين عن عمران بن عبد الله، فقال: ضعيف، حدث عنه الإفريقي','2832','803','0');
INSERT INTO [t13041] VALUES (1351,'عمران بن عبد العزيز بن عمر بن عبد الرحمن بن عوف أبو ثابت الزهري المديني حدثني آدم بن موسى قال: سمعت البخاري قال: عمران بن عبد العزيز بن عمر بن عبد الرحمن بن عوف الزهري أبو ثابت منكر الحديث','2833','803','0');
INSERT INTO [t13041] VALUES (1352,'عمران بن داور القطان أبو العوام حدثنا عبد الله بن أحمد قال: سألت يحيى بن معين عن عمران القطان، فقال: أبو العوام بن داور ضعيف. وقال أبي: أرجو أن يكون صالح الحديث حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: كان عبد الرحمن يحدث عن عمران القطان،','2835','803','0');
INSERT INTO [t13041] VALUES (1353,'عمران بن عيينة أخو سفيان بن عيينة عن عبد الملك بن عمير، يخالف، في حديثه وهم وخطأ','2837','803','0');
INSERT INTO [t13041] VALUES (1354,'عمران أبو الفضل عن هشام بن عروة، روى عنه، إسماعيل بن عياش، حديثه غير محفوظ، وقد روى مناكير','2839','803','0');
INSERT INTO [t13041] VALUES (1355,'عمران بن قيس عن ابن عمر، روى عنه، حريث بن أبي مطر. حدثني آدم بن موسى قال: سمعت البخاري قال: عمران بن قيس، عن ابن عمر، روى عنه حريث بن أبي مطر، قال البخاري: ولم يصح حديثه في الكوفيين','2841','803','0');
INSERT INTO [t13041] VALUES (1356,'عمران بن مسلم الفزاري الأزدي كوفي حدثنا محمد بن عيسى قال: حدثنا أبو إبراهيم الزهري قال: حدثنا إبراهيم بن محمد بن عرعرة قال: حدثنا أبو أحمد الديري قال: كان عمران بن مسلم الذي قال: سألت مجاهدا عن السلام، رافضي كأنه جرو كلب','2842','803','0');
INSERT INTO [t13041] VALUES (1357,'عمران بن مسلم عن عبد الله بن دينار. حدثني آدم بن موسى قال: سمعت البخاري قال: عمران بن مسلم، عن عبد الله بن دينار، روى عنه يحيى بن سليم، منكر الحديث','2844','803','0');
INSERT INTO [t13041] VALUES (1358,'عمران بن مسلم القصير بصري أبو بكر حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سمعت يحيى يقول: ربما رأيت عمران القصير عند ابن أبي عروبة قد جاء يكتب في الألواح، قال يحيى: وكان عمران يرى القدر، وسمعت يحيى يقول: قال لي الحسن الجفري','2846','803','0');
INSERT INTO [t13041] VALUES (1359,'عمران بن ميثم من كبار الرافضة يروي أحاديث سوء كذب','2848','803','0');
INSERT INTO [t13041] VALUES (1360,'عمران بن يزيد مولي قريش بصري في حديثه وهم','2850','803','0');
INSERT INTO [t13041] VALUES (1361,'عمران بن يحيى العمي حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد قال: حدثنا علي قال: سألت يحيى، عن عمران العمي، فقال: لم يكن به بأس، ولكن لم يكن من أهل الحديث','2853','803','0');
INSERT INTO [t13041] VALUES (1362,'عامر بن هني عن محمد بن الحنفية. حدثني آدم بن موسى قال: سمعت البخاري قال: عامر بن هني، عن محمد بن الحنفية، كوفي، قال البخاري: لا يصح','2855','803','0');
INSERT INTO [t13041] VALUES (1363,'عامر بن خارجة بن سعد عن جده سعد. حدثني آدم بن موسى قال: سمعت البخاري قال: عامر بن خارجة بن سعد، عن جده، سعد، قال البخاري: في إسناده نظر','2857','803','0');
INSERT INTO [t13041] VALUES (1364,'عامر بن صالح بن رستم الخزاز عن أيوب بن موسى، ولا يتابع على حديثه، ولا يعرف إلا به','2859','803','0');
INSERT INTO [t13041] VALUES (1365,'عامر بن صالح الزبيري في حديثه وهم. حدثنا محمد بن عيسى، قال الأعور: استعار مني عامر كتب ابن لهيعة. وقال في موضع آخر: عامر بن صالح كان يكون عند مسجد خضر، وكان ضعيف الحديث','2861','803','0');
INSERT INTO [t13041] VALUES (1366,'عامر بن عبد الواحد الأحول حدثنا محمد بن جعفر الرازي بن حميد بن الأسود قال: سألت ابن علية عن عامر الأحول، فقال: سل جدك حميد بن الأسود، فسألته فأوهنه. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: عامر الأحول ليس بالقوي، هو ضعيف الحديث','2863','803','0');
INSERT INTO [t13041] VALUES (1367,'أبو بكر بن أبي مريم الغساني اسمه عامر، ويقال عمرو ويقال بكير. حدثنا عبد الله بن أحمد قال: حدثني أبي قال: سمعت إسحاق بن راهويه، يذكر عن عيسى بن يونس قال: لو أردت أبا بكر بن أبي مريم على أن يجمع، لي فلانا وفلانا وفلانا لفعل، يعني بقوله، عن راشد بن','2865','803','0');
INSERT INTO [t13041] VALUES (1368,'عامر بن أبي الحسين الواسطي عن يزيد بن عطاء، لا يتابع على حديثه','2866','803','0');
INSERT INTO [t13041] VALUES (1369,'عامر بن عمرو مؤذن مسجد أرسوف عن ثابت، لا يتابع على حديثه','2868','803','0');
INSERT INTO [t13041] VALUES (1370,'عمارة بن جوين أبو هارون العبدي حدثنا أحمد بن علي الأبار قال: حدثنا محمد بن موسى الواسطي قال: حدثنا خالد بن خراش قال: حدثنا حماد بن زيد قال: كان أبو هارون كذابا يحدث بالغداة بشيء وبالعشي شيئا حدثنا أحمد بن علي قال: حدثنا أحمد بن خالد الخلال قال:','2872','803','0');
INSERT INTO [t13041] VALUES (1371,'عمارة بن أبي مطرف عن زيد بن أبي مريم، ولا يتابع على حديثه، ولا يعرف إلا به','2873','803','0');
INSERT INTO [t13041] VALUES (1372,'عمارة بن زاذان الصيدلاني حدثنا آدم بن موسى قال: سمعت البخاري قال: عمارة بن زاذان الصيدلاني ربما يضطرب في حديثه','2876','803','0');
INSERT INTO [t13041] VALUES (1373,'عمارة بن غزية حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: قلت لسفيان: كنت جالست عمارة بن غزية؟ قال: نعم، جالسته كم من مرة، فلم أحفظ عنه شيئا، ثم قال لي سفيان: أيش روى؟ قلت: ابن أبي سعيد الخدري عن أبيه قال: من سأل وله أوقية، قال','2877','803','0');
INSERT INTO [t13041] VALUES (1374,'عمارة بن فيروز مديني عن ابن عمر، لا يتابع على حديثه','2878','803','0');
INSERT INTO [t13041] VALUES (1375,'عمارة بن عمار الأيلي عن زفر بن واصل، وزفر، مجهول، والحديث منكر','2880','803','0');
INSERT INTO [t13041] VALUES (1376,'عمير بن إسحاق أبو محمد حدثنا زكريا بن يحيى قال: حدثنا أبو موسى محمد بن المثنى قال: حدثني محمد بن عبد الله الأنصاري قال: حدثني رجل، قال: قلت لمالك بن أنس: من عمير بن إسحاق؟ قال: لا أدري، إلا أنه روى عنه، رجل لا نستطيع أن نقول، فيه شيئا، ابن','2883','803','0');
INSERT INTO [t13041] VALUES (1377,'عمير بن سعيد حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت يحيى يقول: لم يكن عمير بن سعيد ممن يعتمد عليه','2884','803','0');
INSERT INTO [t13041] VALUES (1378,'عمير بن المغلس شامي عن حريز بن عثمان، عن عبد الرحمن بن جبير، ولا يتابع عليه، ولا يعرف إلا به','2885','803','0');
INSERT INTO [t13041] VALUES (1379,'عمير بن عمران الحنفي عن ابن جريج، في حديثه وهم وغلط','2887','803','0');
INSERT INTO [t13041] VALUES (1380,'عمار بن سعد القرظ مديني حدثني آدم بن موسى قال: سمعت البخاري قال: عمار بن سعد القرظ لا يتابع على حديثه','2889','803','0');
INSERT INTO [t13041] VALUES (1381,'عمار بن هارون أبو ياسر قال لنا محمد بن أيوب بن الضريس: سألت علي بن عبد الله المديني عن هذا الشيخ، فلم يرض، يعني عمار بن هارون. قال أبو جعفر العقيلي: قال لي موسى بن هارون: عمار أبو ياسر متروك الحديث','2891','803','0');
INSERT INTO [t13041] VALUES (1382,'عمار بن علثم عن أمه أم سعيد، إسناد مجهول، ولا يتابع عليه. حدثني آدم بن موسى قال: سمعت البخاري قال: عمار بن علثم المحاربي لا يتابع عليه','2893','803','0');
INSERT INTO [t13041] VALUES (1383,'عمار بن أبي فروة عن الزهري. حدثني آدم بن موسى قال: سمعت البخاري قال: عمار بن أبي فروة عن الزهري، لا يتابع على حديثه','2895','803','0');
INSERT INTO [t13041] VALUES (1384,'عمار بن أبي معاوية الدهني حدثنا عبد الله بن أحمد قال: حدثنا عبيد الله بن عمر القواريري قال: سمعت أبا بكر بن عياش يقول: مر بي عمار الدهني فدعوته فقلت: يا عمار، تعال، فجاء، فقلت: سمعت من سعيد بن جبير؟ قال: لا، قلت: فاذهب. حدثني عبد الله بن','2897','803','0');
INSERT INTO [t13041] VALUES (1385,'عمار بن عمارة أبو هاشم صاحب الزعفران حدثني آدم بن موسى قال: سمعت البخاري قال: عمار بن عمارة أبو هاشم صاحب الزعفران، فيه نظر','2899','803','0');
INSERT INTO [t13041] VALUES (1386,'عمار بن سيف الضبي','2901','803','0');
INSERT INTO [t13041] VALUES (1387,'عمار بن عمر بن المختار عن أبيه ولا يتابع على حديثه، ولا يعرف إلا به','2903','803','0');
INSERT INTO [t13041] VALUES (1388,'عمار بن إسحاق أخو محمد بن إسحاق عن محمد بن المنكدر، ولا يتابع على حديثه، وليس مشهور بالنقل','2905','803','0');
INSERT INTO [t13041] VALUES (1389,'عمار بن زربي أبو المعتمر بصري، الغالب على حديثه الوهم، ولا يعرف إلا به','2907','803','0');
INSERT INTO [t13041] VALUES (1390,'عمار بن مطر الرهاوي يحدث عن الثقات، بمناكير','2909','803','0');
INSERT INTO [t13041] VALUES (1391,'عون بن عمارة العبدي بصري حدثني آدم بن موسى قال: سمعت البخاري قال: عون بن عمارة تعرف وتنكر','2912','803','0');
INSERT INTO [t13041] VALUES (1392,'عتبة بن عويم بن ساعدة حدثني آدم بن موسى قال: سمعت البخاري قال: عتبة بن عويم بن ساعدة، ولم يصح','2914','803','0');
INSERT INTO [t13041] VALUES (1393,'عتبة بن أبي عتبة الفزاري عن عكرمة، ولا يتابع عليه , روى عنه، مالك بن الحسن، وفي مالك نظر','2916','803','0');
INSERT INTO [t13041] VALUES (1394,'عتاب بن حرب أبو بشر المزني حدثنا عبد الله بن أحمد النيسابوري قال: حدثنا محمد بن إسماعيل قال: قال عمرو بن علي: عتاب بن حرب المزني ضعيف جدا، يحدث عن صالح بن رستم','2919','803','0');
INSERT INTO [t13041] VALUES (1395,'عتاب بن بشير الجزري حدثنا محمد بن عثمان بن أبي شيبة قال: سألت يحيى بن معين عن عتاب بن بشير، فقال: كان يضعف وحدثنا عبد الله بن أحمد قال: سألت أبي عن عتاب بن بشير، فقال: كذا وكذا حدثني أحمد بن محمود الهروي قال: حدثنا عثمان بن سعيد السجستاني قال:','2921','803','0');
INSERT INTO [t13041] VALUES (1396,'عتاب بن أعين عن الثوري في حديثه وهم','2922','803','0');
INSERT INTO [t13041] VALUES (1397,'عتيبة عن بريد بن أصرم حدثنا آدم بن موسى قال: سمعت البخاري قال: عتبة عن بريد بن أصرم، سمع من جعفر بن سليمان الضبعي','2924','803','0');
INSERT INTO [t13041] VALUES (1398,'عاصم بن عبيد الله بن عاصم بن عمر بن الخطاب رضي الله عنه حدثنا أحمد بن علي الأبار قال: حدثنا مجاهد بن موسى قال: حدثنا عفان قال: كان شعبة يقول: عاصم بن عبيد الله لو قلت له: من بنى مسجد النبي لقال: حدثني فلان، عن فلان، أن النبي صلى الله عليه وسلم','2926','803','0');
INSERT INTO [t13041] VALUES (1399,'عاصم بن كليب الجرمي حدثنا أحمد بن محمد بن إبراهيم قال: حدثنا علي بن حكيم قال: حدثنا شريك، عن الحسن بن عبيد الله قال: قلت لعاصم بن كليب الجرمي: إنك شيخ قد ذهب عقلك، فقال: أما أنا ربع من عقلي ما علم أنك خشبي. قال شريك: وكان عاصم بن كليب مرجئا،','2928','803','0');
INSERT INTO [t13041] VALUES (1400,'عاصم بن عمر أخو عبيد الله وعبد الله بن عمر. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى يقول: عاصم بن عمر صاحب حديث: من أضحى للشمس، ضعيف. حدثنا محمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى يقول: عاصم بن عمر بن حفص أخو عبيد الله','2929','803','0');
INSERT INTO [t13041] VALUES (1401,'عاصم بن أبي النجود وهو ابن بهدلة حدثنا عبد الله بن أحمد قال: حدثني أبو بكر بن خلاد قال: حدثني يحيى بن سعيد قال: سمعت شعبة يقول: حدثنا عاصم بن أبي النجود، وفي النفس ما فيها','2931','803','0');
INSERT INTO [t13041] VALUES (1402,'عاصم بن سليمان الأحول حدثني محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: كان يحيى بن سعيد لا يحدث عن عاصم الأحول، ويستضعفه حدثنا عبد الله بن أحمد بن عبد السلام قال: سمعت أحمد بن سعيد الدارمي قال: سألت أحمد بن إسحاق قلت: ما لوهيب لم يرو عن','2932','803','0');
INSERT INTO [t13041] VALUES (1403,'عاصم بن هلال البارقي حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين قال: عاصم بن هلال البارقي بصري ضعيف','2934','803','0');
INSERT INTO [t13041] VALUES (1404,'عاصم بن علي بن عاصم بن صهيب حدثنا محمد قال: حدثنا معاوية قال: سمعت يحيى يقول: عاصم بن علي بن عاصم ليس بشيء، وفي موضع آخر: علي بن عاصم ليس بشيء ولا ابنه عاصم، ولا ابنه الحسن','2935','803','0');
INSERT INTO [t13041] VALUES (1405,'عاصم بن سليمان الكوزي غلب على حديثه الوهم','2936','803','0');
INSERT INTO [t13041] VALUES (1406,'عاصم بن مضرس عن جبلة بن سليمان، حديثه غير محفوظ، ولا يتابع عليه، وجبلة لا بأس به، ولا يعرف هذا المتن إلا بعاصم بن مضرس','2938','803','0');
INSERT INTO [t13041] VALUES (1407,'عاصم بن عبد العزيز الأشجعي حدثني آدم بن موسى قال: سمعت البخاري قال: عاصم بن عبد العزيز الأشجعي فيه نظر','2940','803','0');
INSERT INTO [t13041] VALUES (1408,'عاصم بن مخلد عن أبي الأشعث، ولا يتابع عليه، ولا يعرف إلا به','2942','803','0');
INSERT INTO [t13041] VALUES (1409,'عصمة بن محمد الأنصاري يحدث بالبواطيل عن الثقات، ليس ممن يكتب حديثه إلا على جهة الاعتبار','2944','803','0');
INSERT INTO [t13041] VALUES (1410,'عصمة بن المتوكل عن شعبة، وغيره، قليل الضبط للحديث، يهم وهما','2946','803','0');
INSERT INTO [t13041] VALUES (1411,'عصمة عن الأعمش حدثنا عبد الله بن أحمد قال: نهاني أبي أن أكتب من حديث رجل يحدث عنه عباس الأنصاري في القراءات، يقال له عصمة عن الأعمش شيئا','2949','803','0');
INSERT INTO [t13041] VALUES (1412,'العلاء بن عبد الرحمن مولى الحرقة حدثني عبد الله بن أحمد قال: سمعت يحيى بن معين، وسئل، عن العلاء بن عبد الرحمن، فقال: مضطرب الحديث، ليس حديثه بحجة. وسمعته مرة أخرى يقول: هؤلاء الأربعة ليس حديثهم بحجة: سهيل بن أبي صالح، والعلاء بن عبد الرحمن،','2950','803','0');
INSERT INTO [t13041] VALUES (1413,'العلاء بن يزيد أبو محمد الثقفي الواسطي حدثنا أحمد بن أصرم قال: سمعت هارون المستملي، يقول لأبي عبد الله: سمعت أبا الوليد الطيالسي يقول: كان العلاء أبو محمد الثقفي كذابا، قال: عندي التفسير عن ابن عمر، وأنس حدثني آدم بن موسى قال: سمعت البخاري قال:','2951','803','0');
INSERT INTO [t13041] VALUES (1414,'العلاء بن زيدل عن أنس: منكر الحديث','2953','803','0');
INSERT INTO [t13041] VALUES (1415,'العلاء بن المنهال عن هشام بن عروة، لا يتابع عليه، ولا يعرف إلا به','2955','803','0');
INSERT INTO [t13041] VALUES (1416,'العلاء بن خالد الأسدي عن أبي وائل، يضطرب في حديثه. حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت يحيى يقول: تركت العلاء بن خالد الأسدي على عمد ثم كتبت عن سفيان، عنه','2957','803','0');
INSERT INTO [t13041] VALUES (1417,'العلاء بن خالد الواسطي حدثني آدم بن موسى قال: سمعت البخاري قال: العلاء بن خالد الواسطي، قال موسى بن إسماعيل: كان عنده أربعة أحاديث، ورماه بالكذب','2959','803','0');
INSERT INTO [t13041] VALUES (1418,'العلاء بن سليمان الرقي عن الزهري، ولا يتابع على حديثه','2961','803','0');
INSERT INTO [t13041] VALUES (1419,'العلاء بن الحارث حدثنا محمد قال: حدثنا العباس قال: سمعت يحيى قال: العلاء بن الحارث كان يرى القدر','2963','803','0');
INSERT INTO [t13041] VALUES (1420,'العلاء بن ميمون عن الحجاج الأسود، لا يتابع على حديثه ولا يعرف إلا به','2964','803','0');
INSERT INTO [t13041] VALUES (1421,'العلاء بن محمد بن سيار عن محمد بن عمرو، ولا يتابع على حديثه، وفي حديثه وهم كثير','2966','803','0');
INSERT INTO [t13041] VALUES (1422,'العلاء بن كثير عن مكحول. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سألت أحمد بن حنبل عن العلاء بن كثير قال: حديثه ليس بشيء. حدثني آدم بن موسى قال: سمعت البخاري قال: العلاء بن كثير عن مكحول، منكر الحديث','2968','803','0');
INSERT INTO [t13041] VALUES (1423,'العلاء بن عمرو الحنفي حدثنا أحمد بن علي الأبار قال: سمعت عبد الله بن عمر بن أبان قال: سمعت أنا والعلاء بن عمرو الحنفي، حديثا من رجل عن سعيد بن مسلمة، فسألوا العلاء عنه بحضرتي فقال: حدثنا سعيد بن مسلمة','2970','803','0');
INSERT INTO [t13041] VALUES (1424,'عياض بن سعيد المازني مجهول بالنقل، حديثه غير محفوظ بهذا الإسناد','2973','803','0');
INSERT INTO [t13041] VALUES (1425,'عياض بن عبد الله الفهري حديثه غير محفوظ. حدثني آدم بن موسى قال: سمعت البخاري قال: عياض بن عبد الله بن سعد الفهري منكر الحديث','2975','803','0');
INSERT INTO [t13041] VALUES (1426,'عقبة بن يريم الدمشقي حدثنا آدم بن موسى قال: سمعت البخاري قال: عقبة بن يريم الدمشقي، قال البخاري: في صحته نظر','2978','803','0');
INSERT INTO [t13041] VALUES (1427,'عقبة بن علي عن هشام بن عروة، ولا يتابع على حديثه، وربما حدث بالمنكر، عن الثقات','2980','803','0');
INSERT INTO [t13041] VALUES (1428,'عقبة بن شداد بن أمية عن عبد الله بن مسعود، روى عنه عبد الله بن سلمة الربعي، ليس يعرف عقبة إلا بهذا، وعبد الله بن سلمة منكر الحديث','2982','803','0');
INSERT INTO [t13041] VALUES (1429,'عقبة بن عبد الله الأصم عن عطاء. حدثنا عبد الله بن أحمد، قال سئل أبي عن عقبة، يعني الأصم، فقال: البراء بن عبد الله الغنوي أحب إلي منه. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: عقبة بن عبد الله الأصم ليس بشيء. حدثنا محمد قال:','2984','803','0');
INSERT INTO [t13041] VALUES (1430,'عقبة بن عبد الله العنزي عن قتادة، مجهول بالنقل، وحديثه منكر غير محفوظ، ولا يعرف إلا به، ولا يتابعه إلا نحوه في الضعيف','2986','803','0');
INSERT INTO [t13041] VALUES (1431,'عقبة بن علقمة البيروني عن الأوزاعي، ولا يتابع عليه','2988','803','0');
INSERT INTO [t13041] VALUES (1432,'عقبة بن خالد السكوني يقال المجدر عن عبيد الله، ولا يتابع على حديثه، ولا يعرف إلا به','2991','803','0');
INSERT INTO [t13041] VALUES (1433,'عطية بن بسر عن عكاف بن وداعة، ولا يتابع عليه. حدثني آدم بن موسى قال: سمعت البخاري قال: عطية بن بسر، عن عكاف بن وداعة، لم يقم حديثه','2993','803','0');
INSERT INTO [t13041] VALUES (1434,'عطية بن أبي عطية عن عطاء بن أبي رباح، مجهول بالنقل، وفي حديثه اضطراب، ولا يتابع عليه','2995','803','0');
INSERT INTO [t13041] VALUES (1435,'عطية بن سعد العوفي حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: حدثنا محمد بن عبيد قال: حدثنا سالم المرادي قال: كان عطية العوفي رجلا متشيعا حدثنا موسى بن إسحاق قال: حدثنا أبو كريب قال: حدثنا محمد بن عبيد، عن سالم المرادي قال: عطية العوفي','2997','803','0');
INSERT INTO [t13041] VALUES (1436,'عطية بن عامر عن سلمان، في إسناده نظر','2998','803','0');
INSERT INTO [t13041] VALUES (1437,'عطية بن عارض عن ابن عباس. حدثني آدم بن موسى قال: سمعت البخاري قال: عطية بن عارض عن ابن عباس، روى عنه أبو خالد الدالاني، ولم يصح حديثه','3000','803','0');
INSERT INTO [t13041] VALUES (1438,'عباس بن الفضل الأزرق بصري حدثني آدم بن موسى قال: سمعت البخاري قال: عباس بن الفضل بن الأزرق بصري ذهب حديثه','3001','803','0');
INSERT INTO [t13041] VALUES (1439,'عباس بن الفضل الأنصاري نزيل موصل حدثنا أحمد بن أصرم المزني قال: سمعت أحمد بن حنبل، وسئل، عن العباس بن الفضل الأنصاري، فقال: روى حديثا، شبيها بالموضوع وضعفه به، ولم يحمده حدثنا عبد الله بن أحمد قال: سمعت أبي وذكر العباس بن الفضل الأنصاري، فقال:','3002','803','0');
INSERT INTO [t13041] VALUES (1440,'عباس بن عبد الرحمن عن نافع بن جبير، مجهول بالنقل، في إسناده نظر','3003','803','0');
INSERT INTO [t13041] VALUES (1441,'عباس بن عتبة عن عطاء، روى عنه، إسماعيل بن عياش، لا يصح حديثه','3005','803','0');
INSERT INTO [t13041] VALUES (1442,'العباس بن بكار الضبي بصري الغالب على حديثه الوهم والمناكير','3007','803','0');
INSERT INTO [t13041] VALUES (1443,'عروة بن زهير العجلي عن ثابت، حديثه غير محفوظ. حدثني آدم بن موسى قال: سمعت البخاري، عن عروة بن زهير العجلي بصري، عن ثابت، منكر الحديث','3009','803','0');
INSERT INTO [t13041] VALUES (1444,'عروة بن علي السهمي عن أبي هريرة، مجهول بالنقل، وسلمة بن حبيب أيضا نحوه','3011','803','0');
INSERT INTO [t13041] VALUES (1445,'عروة بن عبد الله بن محمد بن يحيى بن عروة بن الزبير عن ابن أبي الزناد، مجهول بالنقل، لا يتابع على حديثه','3013','803','0');
INSERT INTO [t13041] VALUES (1446,'عنبسة بن مهران الحداد بصري عن الزهري، يهم في حديثه. حدثني آدم بن موسى قال: سمعت البخاري قال: عنبسة بن مهران الحداد بصري لا يتابع على حديثه','3015','803','0');
INSERT INTO [t13041] VALUES (1447,'عنبسة بن سعيد القطان حدثني زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن بن مهدي، يحدث عن عنبسة القطان','3018','803','0');
INSERT INTO [t13041] VALUES (1448,'عنبسة بن عبد الرحمن بن عنبسة القرشي بصري حدثني آدم بن موسى قال: سمعت البخاري قال: عنبسة بن عبد الرحمن القرشي بصري تركوه حدثنا أحمد بن محمود الهروي قال: سمعت أبا بكر الأعين قال: سمعت عبد الصمد بن عبد الوارث، يضعف عنبسة صاحب علاق','3019','803','0');
INSERT INTO [t13041] VALUES (1449,'عنبسة بن سعيد أخو أبي الربيع السمان حدثني محمد بن عيسى قال: حدثنا محمد بن عبد الله المخرمي قال: حدثنا يزيد بن هارون قال: حدثنا عنبسة بن سعيد، ذاك المجنون حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: عنبسة بن سعيد بصري هو أخو أبي الربيع السمان','3022','803','0');
INSERT INTO [t13041] VALUES (1450,'عنبسة بن جبير عن الربيع بن صبيح، مجهول بالنقل، لا يتابع على حديثه','3024','803','0');
INSERT INTO [t13041] VALUES (1451,'عدي بن الفضل أبو حاتم مولى بني تيم بن مرة حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى بن معين يقول: عدي بن الفضل ليس بشيء. وفي موضع آخر: عدي بن الفضل ضعيف. وفي موضع آخر قلت ليحيى: يكتب حديث عدي بن الفضل؟ فقال: لا، ولا كرامة','3026','803','0');
INSERT INTO [t13041] VALUES (1452,'عدي بن أبي عمارة الذارع بصري في حديثه اضطراب','3028','803','0');
INSERT INTO [t13041] VALUES (1453,'عدي بن أرطأة بن الأشعث عن أبيه، بصري، عن مجالد، حديثه غير محفوظ','3030','803','0');
INSERT INTO [t13041] VALUES (1454,'عدي بن ثابت الأنصاري حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: عدي بن ثابت الأنصاري حدث عن شعب، والمسعودي يعني عدي بن ثابت حدثنا أحمد بن علي الأبار قال: حدثنا مجاهد بن موسى قال: حدثنا عفان قال: كان شعبة يقول: عدي بن ثابت من الرفاعين حدثنا محمد','3032','803','0');
INSERT INTO [t13041] VALUES (1455,'عكرمة بن خالد المخزومي عن أبيه. حدثني آدم بن موسى قال: سمعت البخاري قال: عكرمة بن خالد المخزومي، عن أبيه، منكر الحديث','3033','803','0');
INSERT INTO [t13041] VALUES (1456,'عكرمة مولى ابن عباس وكنيته أبو مجلد حدثنا يوسف بن يعقوب قال: حدثنا سليمان بن حرب قال: حدثنا حماد بن زيد قال: قال رجل لأيوب: أكان عكرمة يتهم؟ فسكت ساعة ثم قال: أما أنا فلم أكن أتهمه. حدثنا محمد بن عيسى قال: حدثنا علي بن سهل قال: حدثنا عفان قال:','3035','803','0');
INSERT INTO [t13041] VALUES (1457,'عكرمة بن إبراهيم الأزدي الموصلي عن عاصم، وعبد الملك بن عمير، يخالف في حديثه، وفي حفظه اضطراب','3037','803','0');
INSERT INTO [t13041] VALUES (1458,'عكرمة بن عمار اليمامي حدثنا عبد الله بن أحمد قال: قال أبي: أحاديث عكرمة بن عمار عن يحيى بن أبي كثير، مضطربة ضعاف ليست بصحاح، ولكنه أتقن حديث إياس بن سلمة، فقلت له: من عكرمة أو من يحيى؟ قال: لا، إلا من عكرمة. وسمعت أبي مرة أخرى يقول: كان يحيى','3039','803','0');
INSERT INTO [t13041] VALUES (1459,'عكرمة بن أسد الحضرمي عن عبد الله بن الحارث بن جزي، في إسناده نظر','3041','803','0');
INSERT INTO [t13041] VALUES (1460,'عيسى بن شعيب بن ثوبان مديني عن فليح، لا يتابع على حديثه هذا، وعبيد بن أبي عبيد مجهول','3043','803','0');
INSERT INTO [t13041] VALUES (1461,'عيسى بن عبد الرحمن الزرقي عن الزهري. حدثني آدم بن موسى قال: سمعت البخاري قال: عيسى بن عبد الرحمن الزرقي عن الزهري، حديثه مقلوب','3045','803','0');
INSERT INTO [t13041] VALUES (1462,'عيسى بن يزداذ اليماني عن أبيه، ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: عيسى بن يزداذ اليماني عن أبيه، روى عنه زمعة بن صالح، ولا يصح','3047','803','0');
INSERT INTO [t13041] VALUES (1463,'عيسى بن سليم عن أبي وائل، روى عنه أبو بكر بن عياش. حدثنا عبد الله بن أحمد قال: سألت أبي عن عيسى بن سليم، فقال: لا أعرفه','3049','803','0');
INSERT INTO [t13041] VALUES (1464,'عيسى بن جارية روى عنه، يعقوب القمي. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عيسى بن جارية روى عن يعقوب القمي، حديثا ليس بذاك، وفي موضع آخر: عيسى بن جارية عنده مناكير تحدث عنه يعقوب القمي وعنبسة قاضي الري','3051','803','0');
INSERT INTO [t13041] VALUES (1465,'عيسى بن سنان حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عيسى بن سنان ضعيف','3053','803','0');
INSERT INTO [t13041] VALUES (1466,'عيسى بن موسى عن عمر، عن يحيى بن أبي كثير، وعيسى، مجهول، وعمر لا أدري من هو: ابن راشد أو غيره، والحديث غير محفوظ','3055','803','0');
INSERT INTO [t13041] VALUES (1467,'عيسى بن سعيد أبو عمار شامي حدثني آدم بن موسى قال: سمعت البخاري قال: عيسى بن سعيد أبو عمار شامي، قال البخاري: لم يصح حديثه','3057','803','0');
INSERT INTO [t13041] VALUES (1468,'عيسى بن طهمان عن أنس، ولا يتابع على حديثه ولعله أتي من قبل خالد، لأن أبا نعيم وخلادا يحدثان عنه أحاديث مقاربة','3059','803','0');
INSERT INTO [t13041] VALUES (1469,'عيسى بن المسيب البجلي حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى قال: عيسى بن المسيب ضعيف، وفي موضع آخر: عيسى بن المسيب ليس بشيء، كان أسد بن عمر ولاه القضاء بخراسان','3062','803','0');
INSERT INTO [t13041] VALUES (1470,'عيسى بن ميمون عن القاسم بن محمد بن كعب القرظي. حدثنا زكريا بن يحيى الساجي قال: حدثنا أحمد بن سنان قال: سمعت عبد الرحمن بن مهدي، قال استعديت على عيسى بن ميمون فقلت: هذه الأحاديث التي يحدث بها عن القاسم بن محمد عن عائشة، فقال: لا أعود. حدثنا محمد','3064','803','0');
INSERT INTO [t13041] VALUES (1471,'عيسى بن ماهان أبو جعفر الرازي حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: أبو جعفر الرازي ليس بالقوي في الحديث','3067','803','0');
INSERT INTO [t13041] VALUES (1472,'عيسى بن أبي عزة عن الشعبي','3069','803','0');
INSERT INTO [t13041] VALUES (1473,'عيسى بن يزيد المدني وهو ابن داب حدثني آدم بن موسى قال: سمعت البخاري قال: عيسى بن يزيد المدني هو ابن داب منكر الحديث','3072','803','0');
INSERT INTO [t13041] VALUES (1474,'عيسى بن أبي عيسى الحناط وهو ابن ميسرة حدثناه محمد بن عيسى قال: حدثنا عمرو بن علي قال: سمعت يحيى، وذكر، عيسى الحناط فلم يرضه، وذكر حفظا سيئا. حدثناه في موضع آخر قال: حدثنا عمرو بن علي قال: كان يحيى لا يحدث عن عيسى الحناط، وقال: كان منكر الحديث،','3074','803','0');
INSERT INTO [t13041] VALUES (1475,'عيسى بن صدقة ويقال ابن عباد بن صدقة حدثني آدم بن موسى قال: سمعت البخاري قال: عيسى بن صدقة، ويقال ابن عباد بن صدقة، قال لي أبو الوليد: هو ضعيف','3075','803','0');
INSERT INTO [t13041] VALUES (1476,'عيسى بن مسلم الأحمر عن ميسرة بن عمار، وميسرة، مجهول. حدثني الخضر بن داود قال: حدثنا أحمد بن محمد قال: سمعت أبا عبد الله، وذكر، عيسى بن مسلم الأحمر وقوله في الإرجاء فقال: نعم، ذاك خبيث القول، وحمل عليه','3077','803','0');
INSERT INTO [t13041] VALUES (1477,'عيسى بن إبراهيم الهاشمي حديثه غير محفوظ، ولا يعرف إلا به. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: عيسى بن إبراهيم الذي يروي عنه كثير بن هشام ليس بشيء. حدثنا آدم بن موسى قال: سمعت البخاري قال: عيسى بن إبراهيم الهاشمي منكر الحديث،','3079','803','0');
INSERT INTO [t13041] VALUES (1478,'عيسى بن قرطاس كان من الغلاة في الرفض. حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: قال أبو نعيم: عيسى بن قرطاس، وحمحم فيه. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عيسى بن قرطاس ليس بشيء، وقال في موضع آخر: ليس تحل الرواية','3081','803','0');
INSERT INTO [t13041] VALUES (1479,'عيسى بن لهيعة عن عكرمة، ولا يتابع عليه ولا يعرف إلا به','3083','803','0');
INSERT INTO [t13041] VALUES (1480,'عيسى بن محمد القرشي عن ابن أبي مليكة، مجهول بالنقل، ولا يعرف إلا به، ولا يتابع عليه','3085','803','0');
INSERT INTO [t13041] VALUES (1481,'عطاء بن السائب الثقفي يقال إنه تغير بآخرة. حدثنا أحمد بن علي الأبار قال: حدثنا عيسى بن عامر قال: حدثنا ابن أبي الطيب قال: حدثنا ابن علية قال: قال لي شعبة: ما حدثك عطاء بن السائب، من رجاله عن زاذان، وميسرة، وأبي البختري، فلا تكتبه، وما حدثك عن','3087','803','0');
INSERT INTO [t13041] VALUES (1482,'عطاء الشامي حدثني آدم بن موسى قال: سمعت البخاري قال: عطاء الشامي، عن أبي أسيد، روى عنه عبد الله بن عيسى، لم يقم حديثه','3089','803','0');
INSERT INTO [t13041] VALUES (1483,'عطاء بن عجلان العطار حدثنا أحمد بن علي الأبار قال: حدثنا أحمد بن إبراهيم قال: حدثنا أسيد بن زيد قال: سمعت زهير بن معاوية يقول: ما أتهم إلا عطاء بن عجلان. حدثنا أحمد بن علي قال: حدثنا يحيى بن أيوب قال: حدثنا أبو المنذر الكوفي قال: كنا بمكة فقدم','3091','803','0');
INSERT INTO [t13041] VALUES (1484,'عطاء بن أبي ميمونة وكان يرى القدر. حدثنا عبد الله بن أحمد بن حنبل قال: حدثني أبي قال: سمعت يحيى بن سعيد يقول: عطاء بن أبي ميمونة مات بعد الطاعون، وكان يرى القدر. حدثنا العباس بن الفضل الأسفاطي قال: حدثنا إبراهيم بن محمد بن عرعرة، حدثنا يحيى بن','3092','803','0');
INSERT INTO [t13041] VALUES (1485,'عطاء أبو محمد حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى يقول: عطاء أبو محمد روى عنه، حسن بن صالح، ضعيف','3094','803','0');
INSERT INTO [t13041] VALUES (1486,'عطاء بن مسلم الخفاف ولا يتابع على حديثه، ولا يعرف إلا به. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين قال: عطاء بن مسلم الخفاف ليس به بأس، وأحاديثه منكرات','3096','803','0');
INSERT INTO [t13041] VALUES (1487,'عطاء بن عبد الله الخراساني مولى المهلب بن أبي صفرة حدثنا إبراهيم بن محمد قال: حدثنا سليمان بن حرب، حدثنا حماد بن زيد قال: حدثنا أيوب قال: حدثني القاسم بن عاصم قال: قلت لسعيد بن المسيب: إن عطاء الخراساني حدثني عنك، أن النبي صلى الله عليه وسلم أمر','3098','803','0');
INSERT INTO [t13041] VALUES (1488,'عطاء بن يزيد مولى سعيد بن المسيب عن سعيد بن المسيب، ولا يصح إسناده','3099','803','0');
INSERT INTO [t13041] VALUES (1489,'عقيل الجعدي عن أبي إسحاق الهمداني، حديثه غير محفوظ، ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري يقول: عقيل الجعدي، عن أبي إسحاق الهمداني، منكر الحديث','3101','803','0');
INSERT INTO [t13041] VALUES (1490,'عائذ بن نسير عن عطاء: منكر الحديث. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: عائذ بن نسير ليس به بأس، ولكن روى أحاديث مناكير. حدثني أحمد بن محمود الهروي قال: حدثنا عثمان بن سعيد، قلت ليحيى بن معين: عائذ بن نسير كيف حديثه؟ قال:','3103','803','0');
INSERT INTO [t13041] VALUES (1491,'عائذ بن أيوب الطوسي عن إسماعيل بن أبي خالد، روى عنه عبد الله بن عبد العزيز بن أبي رواد، ولا يصح إسناده، والرواية في هذا النحو فيها لين','3106','803','0');
INSERT INTO [t13041] VALUES (1492,'عائذ بن حبيب حدثنا محمد بن أحمد، حدثنا معاوية بن صالح قال: سمعت يحيى يقول: قد سمعت من، عائذ بن حبيب أخي الربيع بن حبيب، وكان عائذ بن حبيب يقال إنه زيدي','3108','803','0');
INSERT INTO [t13041] VALUES (1493,'عجلان بن هلال عن عبد الغفور بن عبد العزيز، روى عنه، توبة بن علوان، ولا يصح إسناده، والمتن معروف بغير هذا الإسناد','3109','803','0');
INSERT INTO [t13041] VALUES (1494,'عجلان بن سهل الباهلي حدثني آدم بن موسى قال: سمعت البخاري قال: عجلان بن سهل الباهلي، عن أبي أمامة، روى عنه، سليمان بن موسى، ولم يصح حديثه','3111','803','0');
INSERT INTO [t13041] VALUES (1495,'عزرة بن قيس اليحمدي لا يتابع على حديثه. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى قال: عزرة بن قيس اليحمدي أزدي بصري ضعيف. حدثني آدم بن موسى قال: سمعت البخاري قال: عزرة بن قيس اليحمدي لا يتابع على حديثه','3112','803','0');
INSERT INTO [t13041] VALUES (1496,'عوام بن حمزة حدثنا عبد الله بن أحمد قال: سألت أبي عن العوام بن حمزة، فقال: له أحاديث مناكير، روى عنه يحيى حدثنا موسى بن إسحاق، حدثنا أبو بكر بن أبي شيبة، حدثنا يحيى بن سعيد القطان، عن العوام بن حمزة قال: سألت أبا عثمان عن القنوت، فقال: بعد','3114','803','0');
INSERT INTO [t13041] VALUES (1497,'عوسجة مولى ابن عباس حدثني آدم بن موسى قال: سمعت البخاري قال: عوسجة مولى ابن عباس، ولم يصح حديثه','3115','803','0');
INSERT INTO [t13041] VALUES (1498,'عفان بن سيار الجرجاني ولا يتابع على رفع حديثه','3117','803','0');
INSERT INTO [t13041] VALUES (1499,'عريف بن إبراهيم الثقفي عن حميد بن كلاب، في إسناده نظر','3120','803','0');
INSERT INTO [t13041] VALUES (1500,'عباية بن ربعي الأسدي روى عنه، موسى بن طريف، كلاهما غاليان ملحدان','3122','803','0');
INSERT INTO [t13041] VALUES (1501,'عباءة بن كليب الليثي عن جويرية بن أسماء، ولا يتابع عليه','3125','803','0');
INSERT INTO [t13041] VALUES (1502,'عبيس بن ميمون أبو عبيدة التيمي البصري حدثنا عبد الله بن أحمد قال: سألت أبي عن أحاديث، حدثنا بها، خلف بن هشام البزار عن عبيس بن ميمون، فقال أبي: أحاديث عبيس أحاديث مناكير حدثنا زكريا بن يحيى، حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن، يحدث عن','3127','803','0');
INSERT INTO [t13041] VALUES (1503,'عائذ الله المجاشعي عن أبي داود، لا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: عائذ الله المجاشعي، عن أبي داود، روى عنه، سلام بن مسكين، لا يصح حديثه','3132','803','0');
INSERT INTO [t13041] VALUES (1504,'علوان بن داود البجلي ويقال علوان بن صالح ولا يتابع على حديثه، ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: علوان بن داود البجلي ويقال علوان بن صالح منكر الحديث','3134','803','0');
INSERT INTO [t13041] VALUES (1505,'عوين بن عمرو القيسي عن الجريري، وغيره، ولا يتابع عليه، ويقال عون','3137','803','0');
INSERT INTO [t13041] VALUES (1506,'عطي بن مجدي الضمري حديثه منكر، ومحمد بن سليمان المسمولي رماه الحميدي بالكذب، والحديث غير محفوظ. حدثني آدم قال: سمعت البخاري قال: عطاء بن مجدي الضمري ولم يصح حديثه','3140','803','0');
INSERT INTO [t13041] VALUES (1507,'عوبد بن أبي عمران الجوني حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: عوبد بن أبي عمران ليس بشيء. حدثني آدم قال: سمعت البخاري قال: عوبد بن أبي عمران الجوني منكر الحديث','3142','803','0');
INSERT INTO [t13041] VALUES (1508,'عصام بن طليق عن شعيب، عن أبي هريرة، وشعيب، مجهول بالنقل. حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: عصام بن طليق ليس بشيء','3144','803','0');
INSERT INTO [t13041] VALUES (1509,'عطاف بن خالد المخزومي أبو صفوان المديني حدثنا أحمد بن علي، حدثنا محمد بن عبد الرحمن القرمطي، حدثني عبد الرحمن بن عبد الملك الحزامي قال: قيل لمالك بن أنس: قد حدث عطاف بن خالد قال: قد فعل؟ ليس هو من إبل القباب. حدثنا محمد بن إسماعيل، حدثنا الحسن بن','3146','803','0');
INSERT INTO [t13041] VALUES (1510,'عسل بن سفيان اليربوعي التميمي عن عطاء، في حديثه وهم. حدثني آدم قال: سمعت البخاري قال: عسل بن سفيان اليربوعي، عن عطاء، فيه نظر','3147','803','0');
INSERT INTO [t13041] VALUES (1511,'عنطوانة عن الحسن مجهول بالنقل، حديثه غير محفوظ. روى عنه الربيع بن بدر، والربيع، متروك','3150','803','0');
INSERT INTO [t13041] VALUES (1512,'عرفة عن أبي موسى مجهول أيضا، ولا يبين سماعه من أبي موسى رضي الله عنه','3152','803','0');
INSERT INTO [t13041] VALUES (1513,'عريف بن درهم الجمال','3154','803','0');
INSERT INTO [t13041] VALUES (1514,'عوف بن أبي جميلة الأعرابي حدثنا عبد الله بن أحمد، حدثنا محمد بن أبي بحر المقدم قال: سمعت عمرو بن علي يقول: رأيت عبد الله بن المبارك يقول لجعفر بن سليمان: رأيت أيوب وابن عون ويونس فكيف لم تجالسهم وجالست عوفا؟ والله ما رضي عوف ببدعة واحدة حتى كانت فيه','3156','803','0');
INSERT INTO [t13041] VALUES (1515,'عفير بن معدان عن سليم بن عامر، ولا يتابع على حديثه ولا يعرف إلا به. حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: عفير بن معدان ليس بثقة','3157','803','0');
INSERT INTO [t13041] VALUES (1516,'عرعرة بن البرند بن النعمان الشامي بصري حدثنا عبد الله بن أحمد قال: سمعت أبي قال: كنا بالبصرة وعرعرة حي فلم نكتب عنه شيئا حدثنا العباس بن السندي قال: سمعت علي بن عبد الله قال: عرعرة بن البرند ضعيف','3159','803','0');
INSERT INTO [t13041] VALUES (1517,'باب الغين','3160','0','0');
INSERT INTO [t13041] VALUES (1518,'غالب بن عبيد الله الجزري العقيلي حدثني إدريس بن عبد الكريم المقرئ، حدثنا الهيثم بن خارجة، حدثنا يحيى بن حمزة، عن غالب بن عبيد الله العقيلي، قال ابن خارجة: وكان غالب ينزل حران وتوفي في آخر أيام المهدي سنة خمس وثلاثين ومائة، وكان ضعيفا في الحديث','3161','1517','0');
INSERT INTO [t13041] VALUES (1519,'غالب بن حبيب أبو غالب اليشكري عن العوام بن حوشب. حدثني آدم قال: سمعت البخاري قال: غالب بن حبيب أبو غالب اليشكري عن العوام بن حوشب، منكر الحديث','3164','1517','0');
INSERT INTO [t13041] VALUES (1520,'غالب أبو الهذيل حدثنا عبد الله بن أحمد، حدثنا أبو سعيد الأشج، حدثنا عبد الله بن إدريس، عن أبيه، عن غالب أبي الهذيل قال: قلت له: ما كان غالب أبو الهذيل؟ قال: كان رافضيا','3167','1517','0');
INSERT INTO [t13041] VALUES (1521,'غالب بن غالب عن أبيه، عن جده، إسناده مجهول، لا يعرف إلا بهذا الحديث','3168','1517','0');
INSERT INTO [t13041] VALUES (1522,'غالب بن وزير الغزي عن ابن وهب، حديثه منكر لا أصل له، ولم يأت به عن ابن وهب غيره ولا يعرف إلا به','3170','1517','0');
INSERT INTO [t13041] VALUES (1523,'غالب بن فائد عن شريك، يخالف في حديثه، صاحب وهم','3172','1517','0');
INSERT INTO [t13041] VALUES (1524,'غالب بن الصعب العمي عن ابن عيينة، مجهول بالنقل، لا يعرف إلا به، ليس بمحفوظ','3174','1517','0');
INSERT INTO [t13041] VALUES (1525,'غيلان بن أبي غيلان مولى عثمان هو القدري. حدثنا محمد بن إسماعيل، حدثنا موسى بن إسماعيل، حدثنا سعد أبو عاصم قال: حج مسلمة بن عبد الملك وأبوه الخليفة سنة ست ومائة، وكان سنة سبع ومائة ومعه غيلان يفتي الناس، وكان محمد بن كعب معه. أبحر كل جمعة من قريته','3176','1517','0');
INSERT INTO [t13041] VALUES (1526,'غزوان بن يوسف المازني عن الحسن. حدثني آدم قال: سمعت البخاري قال: غزوان بن يوسف المازني بصري عن الحسن، تركوه','3177','1517','0');
INSERT INTO [t13041] VALUES (1527,'غزوان بن عتبة بن غزوان لا يعرف إلا بهذا، ولا يتابع عليه','3179','1517','0');
INSERT INTO [t13041] VALUES (1528,'غسان أبو عبد الرحمن السلمي عن عون بن ذكوان، مجهول بالنقل، ولا يعرف إلا به، ولا يتابع عليه','3181','1517','0');
INSERT INTO [t13041] VALUES (1529,'غسان بن عوف المازني عن الجريري، لا يتابع على كثير من حديثه','3183','1517','0');
INSERT INTO [t13041] VALUES (1530,'غسان بن عبيد الموصلي حدثنا عبد الله بن أحمد قال: قال أبي: غسان بن عبيد قدم علينا هاهنا من الموصل وحرقت حديثه منذ حين، وكان قد سمع من سفيان أحاديث يسيرة، وأنكر أن يكون قد سمع الجامع من سفيان','3185','1517','0');
INSERT INTO [t13041] VALUES (1531,'غياث بن عبد الحميد مجهول بالنقل، لا يتابع على حديثه، ولا يعرف إلا به','3186','1517','0');
INSERT INTO [t13041] VALUES (1532,'غياث بن إبراهيم أبو عبد الرحمن كوفي حدثناه محمد بن عثمان بن أبي شيبة قال: سمعت يحيى بن معين، يقول وذكر غيلان بن إبراهيم فقال يحيى: كان ضعيفا. حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: غياث بن إبراهيم كذاب، ليس بثقة ولا مأمون. حدثني آدم','3188','1517','0');
INSERT INTO [t13041] VALUES (1533,'غاز بن جبلة الجبلاني في طلاق المكره حدثني آدم بن موسى قال: سمعت البخاري قال: غاز بن جبلة الجبلاني حديثه منكر في طلاق المكره','3190','1517','0');
INSERT INTO [t13041] VALUES (1534,'باب الفاء','3192','0','0');
INSERT INTO [t13041] VALUES (1535,'الفضل بن عيسى الرقاشي كان يرى القدر. حدثنا محمد بن أيوب، حدثنا موسى بن إسماعيل قال: سمعت سلام بن أبي مطيع قال: لو أن الفضل بن عيسى الرقاشي، ولد أخرس كان خيرا له','3193','1534','0');
INSERT INTO [t13041] VALUES (1536,'الفضل بن عميرة الطفاوي عن ميمون بن سياه، ولا يتابع على حديثه','3195','1534','0');
INSERT INTO [t13041] VALUES (1537,'الفضل بن جبير الوراق واسطي ولا يتابع على حديثه، ولا يعرف لمرثد رواية من وجه تصح','3197','1534','0');
INSERT INTO [t13041] VALUES (1538,'الفضل بن العباس البصري مجهول بالنقل عن ثابت، لا يتابعه إلا من هو دونه أو مثله','3199','1534','0');
INSERT INTO [t13041] VALUES (1539,'الفضل بن دلهم حدثنا محمد بن إسماعيل، حدثنا الحسن بن علي قال: سمعت أحمد بن حنبل يقول: لا يحفظ الفضل بن دلهم قال: وذكر أشياء مما أخطأ فيها. حدثنا عبد الله بن أحمد قال: وجدت في كتاب أبي بخطه: قال يزيد بن هارون: كان الفضل بن دلهم عندنا قصابا شاعرا','3201','1534','0');
INSERT INTO [t13041] VALUES (1540,'الفضل بن معروف القطعي يخالف في حديثه، قليل الضبط','3202','1534','0');
INSERT INTO [t13041] VALUES (1541,'الفضل بن الربيع عن ابن جريج، لا يتابع عليه من وجه يثبت','3204','1534','0');
INSERT INTO [t13041] VALUES (1542,'الفضل بن بكر العبدي عن قتادة، ولا يتابع عليه من وجه يثبت','3206','1534','0');
INSERT INTO [t13041] VALUES (1543,'الفضل بن يسار عن غالب القطان، ولا يتابع من وجه يثبت','3208','1534','0');
INSERT INTO [t13041] VALUES (1544,'الفضل بن حماد الواسطي عن عبد الله بن عمران، في إسناده نظر','3210','1534','0');
INSERT INTO [t13041] VALUES (1545,'الفضل بن السكن الكوفي لا يضبط الحديث، وهو مع ذلك مجهول','3212','1534','0');
INSERT INTO [t13041] VALUES (1546,'الفضل بن المختار منكر الحديث','3215','1534','0');
INSERT INTO [t13041] VALUES (1547,'الفضل بن عطاء عن الفضل بن شعيب، إسناده مجهول، فيه نظر، لا يعرف إلا من هذا الوجه','3217','1534','0');
INSERT INTO [t13041] VALUES (1548,'الفضل بن صالح عن عطاء بن السائب، حديثه غير محفوظ، والراوي عنه فيه مقال','3219','1534','0');
INSERT INTO [t13041] VALUES (1549,'الفضل بن يحيى السبخي بصري ليس ممن يضبط الحديث','3221','1534','0');
INSERT INTO [t13041] VALUES (1550,'الفضل بن فرقد عن محمد بن عمرو، يخالف في حديثه','3223','1534','0');
INSERT INTO [t13041] VALUES (1551,'الفضل بن حرب البجلي عن عبد الرحمن بن بذيل، مجهول بالنقل، حديثه غير محفوظ، لا يعرف إلا به','3226','1534','0');
INSERT INTO [t13041] VALUES (1552,'الفضل بن سلام عن معاوية بن حفص، منكر الحديث، ومعاوية بن حفص مجهول، ولا يعرف إلا به','3228','1534','0');
INSERT INTO [t13041] VALUES (1553,'الفضل بن زياد عن شيبان، لا يعرف إلا بهذا وفيه نظر','3230','1534','0');
INSERT INTO [t13041] VALUES (1554,'فضيل بن يحيى روى عنه، سيف بن هارون، في إسناده نظر، وسيف ضعيف، ولا يعرف إلا به','3232','1534','0');
INSERT INTO [t13041] VALUES (1555,'فضالة بن حصين العطار حدثني آدم قال: سمعت البخاري قال: فضالة بن حصين العطار مضطرب الحديث','3234','1534','0');
INSERT INTO [t13041] VALUES (1556,'فضالة بن مفضل أبو ثوابة القتباني عن أبيه، في حديثه نظر، فأما المتن فيروى من غير هذا الوجه بأسانيد جياد','3236','1534','0');
INSERT INTO [t13041] VALUES (1557,'فضالة بن دينار الشحام منكر الحديث','3238','1534','0');
INSERT INTO [t13041] VALUES (1558,'فضالة بن سعيد بن زميل المأربي عن محمد بن يحيى المأربي، وحديثه، غير محفوظ، ولا يعرف إلا به','3240','1534','0');
INSERT INTO [t13041] VALUES (1559,'الفرات بن السائب قال البخاري: فرات بن سائب كوفي تركوه، منكر الحديث حدثني محمد بن عبد الرحمن، حدثنا عبد الملك بن عبد الحميد قال: سمعت أحمد بن حنبل قال: الفرات بن السائب قريب من محمد بن زياد الطحان في ميمون، يتهم بما يتهم ذاك حدثنا محمد بن عيسى،','3242','1534','0');
INSERT INTO [t13041] VALUES (1560,'فرقد السبخي وهو فرقد بن يعقوب بصري حدثنا محمد بن موسى، حدثنا عمر بن شبة، حدثنا يوسف بن عطية، حدثنا فرقد بن يعقوب السبخي، ح وحدثنا جعفر بن أحمد بن نعيم، حدثنا محمد بن حميد، حدثنا جرير، عن مغيرة قال: أول من دلنا على إبراهيم فرقد السبخي وكان حائكا،','3244','1534','0');
INSERT INTO [t13041] VALUES (1561,'فائد بن عبد الرحمن العطار أبو الورقاء عن ابن أبي أوفى، ذكر البخاري أنه كوفي، وقال غيره: بصري. حدثنا محمد بن إسماعيل، حدثنا الحسن بن علي الحلواني، حدثنا محمد بن داود الخراشي، حدثني أبو الفتح المغيرة، من أهل البصرة قال: كنا عند عيسى بن يونس بمكة','3245','1534','0');
INSERT INTO [t13041] VALUES (1562,'فرج بن يحيى كوفي عن ابن أبي ذئب، يخالف في حديثه، مضطرب الحديث','3247','1534','0');
INSERT INTO [t13041] VALUES (1563,'فرج بن فضالة الحمصي عن يحيى بن سعيد. حدثني آدم قال: سمعت البخاري يقول: فرج بن فضالة منكر الحديث. حدثني محمد بن عيسى، حدثنا عمرو بن علي قال: كان عبد الرحمن يحدث عن فرج بن فضالة، ويقول: حديثه عن يحيى بن سعيد، أحاديث منكرة مقلوبة','3249','1534','0');
INSERT INTO [t13041] VALUES (1564,'فهد بن حيان أبو بكر النهشلي بصري حدثني محمد بن زكريا البلخي، حدثنا الحسن بن شجاع قال: سمعت علي بن عبد الله المديني يقول: اتركوا حديث الفهدين والعمرين، يعني فهد بن حيان وفهد بن عوف، والعمرين عمرو بن حكام وعمرو بن مرزوق','3251','1534','0');
INSERT INTO [t13041] VALUES (1565,'فهد بن عوف أبو ربيعة العامري اسمه زيد بصري حدثنا أحمد بن علي الأبار، حدثنا الحسن بن شجاع قال: سمعت علي بن المديني يقول: فهد بن عوف أبو ربيعة صاحب أبي عوانة كذاب','3253','1534','0');
INSERT INTO [t13041] VALUES (1566,'فطر بن خليفة الحناط كوفي حدثنا أحمد بن علي، حدثنا عمرو بن هشام الحراني قال: سمعت أبا بكر بن عياش يقول: ما تركت الرواية عن فطر، إلا لسوء مذهبه حدثنا عبد الله قال: سمعت أبي يقول: كان فطر عند يحيى ثقة، ولكنه كان خشبيا مفرطا حدثنا عبد الله قال: سألت','3254','1534','0');
INSERT INTO [t13041] VALUES (1567,'فليح بن سليمان مديني حدثنا عبد الله بن أحمد قال: سمعت يحيى بن معين يقول: كان يقال: ثلاثة يتقى حديثهم: طلحة بن مصرف، وأيوب بن عتبة، وفليح بن سليمان، قلت له: ممن سمعت هذا؟ قال: سمعته من أبي كامل المظفر بن مدرك، وكنت آخذ عنه هذا الشأن حدثنا زكريا','3257','1534','0');
INSERT INTO [t13041] VALUES (1568,'باب القاف','3259','0','0');
INSERT INTO [t13041] VALUES (1569,'قيس بن عبد الله بن عبد الرحمن بن أبي صعصعة عن سعد بن إبراهيم. حدثني آدم قال: سمعت البخاري قال: قيس بن عبد الرحمن بن أبي صعصعة، عن سعد بن إبراهيم، قال موسى بن عبيدة: ولم يصح','3260','1568','0');
INSERT INTO [t13041] VALUES (1570,'قيس أبو عمارة الفارسي مولى سودة بنت سعيد مديني عن عبد الله بن أبي بكر، ومحمد بن عمرو بن حزم، فيه نظر','3262','1568','0');
INSERT INTO [t13041] VALUES (1571,'قيس بن ميناء عن سليمان، كوفي لا يتابع على حديثه، وكان له مذهب سوء','3265','1568','0');
INSERT INTO [t13041] VALUES (1572,'قيس بن سالم أبو حذرة عن أمامة بن سهل، ولا يتابع عليه','3267','1568','0');
INSERT INTO [t13041] VALUES (1573,'قيس بن الربيع أبو محمد الكوفي الأسدي حدثني آدم بن موسى، حدثنا محمد بن إسماعيل البخاري، حدثنا علي بن المديني قال: كان وكيع يضعف قيس بن الربيع حدثنا محمد بن عيسى، حدثنا عمرو بن علي قال: سمعت أبا داود يقول: سمعت شعبة يقول: من يعذرني من يحيى هذا','3269','1568','0');
INSERT INTO [t13041] VALUES (1574,'القاسم بن غصن كوفي لا يتابع على حديثه. حدثني آدم قال: سمعت البخاري قال: القاسم بن غصن كوفي، قال أحمد: يحدث بمناكير. وحدثنا عبد الله قال: سمعت أبي يقول: القاسم بن غصن يحدث بأحاديث مناكير','3270','1568','0');
INSERT INTO [t13041] VALUES (1575,'القاسم بن عبد الله بن عمر العمري مديني حدثنا عبد الله بن أحمد قال: سألت أبي عن القاسم بن عبد الله بن عمر بن حفص العمري، فقال: أف أف ليس بشيء وسمعت أبي مرة، أنه يقول: القاسم بن عبد الله بن عمر العمري هو عندي كان يكذب حدثنا محمد بن عيسى، حدثنا عباس','3272','1568','0');
INSERT INTO [t13041] VALUES (1576,'القاسم بن مهران عن عثمان بن حصين، ولا يثبت سماعه منه، روى عنه، موسى بن عبيدة، وموسى متروك','3276','1568','0');
INSERT INTO [t13041] VALUES (1577,'القاسم بن عبد الله بن محمد بن عقيل بن أبي طالب حدثنا عبد الله بن أحمد قال: سألت يحيى بن معين عن القاسم بن عبد الله بن محمد بن عقيل، فقال: ليس هو بشيء','3278','1568','0');
INSERT INTO [t13041] VALUES (1578,'القاسم بن غنام في حديثه اضطراب','3280','1568','0');
INSERT INTO [t13041] VALUES (1579,'القاسم أبو عبد الرحمن حدثنا عبد الله بن أحمد قال: سمعت أبي وذكر القاسم أبا عبد الرحمن قال: فقال بعض الناس: هذه الأحاديث المناكير التي يرويها عنه جعفر بن الزبير وبشر بن نمير ومطرح فقال أبي: علي بن يزيد من أهل دمشق حدث عنه مطرح ولكن يقولون: هذه من قبل','3283','1568','0');
INSERT INTO [t13041] VALUES (1580,'القاسم بن عوف الشيباني حدثنا محمد، حدثنا صالح، حدثنا علي قال: سمعت يحيى، وقيل، له: تحفظ حديث قتادة: إن هذه الحشوش محتضرة؟ قال: لا، فقلت له: إنما كان شعبة يحدثه عن قتادة، عن النضر بن أنس، عن زيد بن أرقم، وكان ابن أبي عروبة يحدثه عن قتادة،','3285','1568','0');
INSERT INTO [t13041] VALUES (1581,'القاسم بن الفضل الحداني','3286','1568','0');
INSERT INTO [t13041] VALUES (1582,'القاسم بن الحكم الأنصاري حدثني آدم بن موسى، عن قتادة، سمعت البخاري قال: القاسم بن الحكم الأنصاري، سمعت أبا عبادة الزرقي، قال البخاري: ولم يصح حديث أبي عبادة','3288','1568','0');
INSERT INTO [t13041] VALUES (1583,'القاسم بن سليمان روى عنه الخليل بن مرة، ولا يصح حديثه','3290','1568','0');
INSERT INTO [t13041] VALUES (1584,'القاسم بن عثمان عن أنس، لا يتابع على حديثه، حدث عنه، إسحاق الأزرق أحاديث لا يتابع منها على شيء','3292','1568','0');
INSERT INTO [t13041] VALUES (1585,'القاسم بن محمد بن أبي شيبة أخو أبي بكر وعثمان حدثني محمد بن عثمان بن أبي شيبة قال: سألت يحيى عن عمي القاسم، فقال لي: عمك ضعيف يا ابن أخي. قال أبو جعفر: ولو ظننت أنه يقول هذا لم أسأله','3294','1568','0');
INSERT INTO [t13041] VALUES (1586,'القاسم بن هانئ الأعمى مصري لا يقيم الحديث','3295','1568','0');
INSERT INTO [t13041] VALUES (1587,'القاسم بن يزيد بن عبد الله بن قسيط عن أبيه، عن عطاء، يقال هو عطاء بن يسار','3297','1568','0');
INSERT INTO [t13041] VALUES (1588,'قبيصة بن حريث الأنصاري سمع سلمة بن المحبق. حدثني آدم بن موسى قال: سمعت البخاري قال: قبيصة بن حريث سمع سلمة بن المحبق، قال البخاري: في حديثه نظر','3301','1568','0');
INSERT INTO [t13041] VALUES (1589,'قدامة بن وبرة العجيفي بصري عن سمرة. حدثني آدم بن موسى قال: سمعت البخاري قال: قدامة بن وبرة العجيفي بصري، عن سمرة، ولم يصح سماعه من سمرة','3303','1568','0');
INSERT INTO [t13041] VALUES (1590,'قرة بن عبد الرحمن بن حيوئيل حدثنا عبد الله بن محمد المروزي، حدثنا إبراهيم بن يعقوب قال: سمعت أحمد بن حنبل يقول: قرة بن عبد الرحمن صاحب الزهري منكر الحديث جدا','3305','1568','0');
INSERT INTO [t13041] VALUES (1591,'قرة بن العلاء السعدي عن أبي يونس الخصاف، عن داود بن أبي هند، وأبو يونس مجهول، والحديث غير محفوظ','3307','1568','0');
INSERT INTO [t13041] VALUES (1592,'قطبة بن العلاء بن المنهال الغنوي عن أبيه، وسفيان، لا يتابع على حديثه. حدثني آدم قال: سمعت البخاري قال: قطبة بن العلاء بن المنهال الغنوي، عن أبيه، وسفيان، ليس بالقوي','3309','1568','0');
INSERT INTO [t13041] VALUES (1593,'قزعة بن سويد بن حجير الباهلي حدثني آدم قال: سمعت البخاري قال: قزعة بن سويد بن حجير الباهلي بصري ليس بذاك حدثنا محمد بن عيسى، حدثنا عباس بن محمد قال: سمعت يحيى بن معين يقول: قزعة بن سويد ضعيف حدثنا محمد بن عيسى قال: سمعت عمرو بن علي يقول: كنت عنده','3311','1568','0');
INSERT INTO [t13041] VALUES (1594,'قتيبة بن سعيد التيمي مجهول في النسب، والرواية عن يحيى بن أبي أنيسة، عن الزهري، حديثه غير محفوظ وإسناده لا يصح إلا موقوفا','3312','1568','0');
INSERT INTO [t13041] VALUES (1595,'قنان بن عبد الله النهمي حدثنا عبد الله بن أحمد، سمعت أبي يقول: سمعت يحيى بن آدم يقول: قنان ليس من بابتكم، قال أبي: كان يحيى قليل الذكر للناس، ما سمعته ذاكرا أحدا غير قنان','3315','1568','0');
INSERT INTO [t13041] VALUES (1596,'قابوس بن أبي ظبيان الجنبي حدثني أحمد بن علي قال: سمعت يعقوب بن إبراهيم يقول: سمعه جرير بن عبد الحميد يقول: نفق قابوس، نفق قابوس، يعني قابوس بن أبي ظبيان حدثنا عبد الله بن أحمد، قال أبي: سئل جرير عن شيء، من حديث قابوس فقال: نفق قابوس، نفق قابوس','3317','1568','0');
INSERT INTO [t13041] VALUES (1597,'قطن بن سعير بن الخمس حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى يقول: قطن بن سعير بن الخمس رجل سوء يتهم بأمر قبيح','3318','1568','0');
INSERT INTO [t13041] VALUES (1598,'قرط بن حريث مولى باهلة بصري حدثنا محمد، حدثنا عباس قال: سمعت يحيى قال: قرط بن حريث قدري، أتيناه في منزله فقال لنا: نزهوا الأمة عن هذه المعاصي، وكان مولى لباهلة','3319','1568','0');
INSERT INTO [t13041] VALUES (1599,'باب الكاف','3320','0','0');
INSERT INTO [t13041] VALUES (1600,'كثير مولى ابن سمرة حدثنا يوسف بن يعقوب، حدثنا سليمان بن حرب، حدثنا حماد بن زيد قال: قلت لأيوب: علمت أحدا قال: أمرك بيدك بقول الحسن , فقال: لا , ثم قال: اللهم بلى , إن قتادة حدثنا، عن كثير مولى ابن سمرة , عن أبي سلمة , عن أبي هريرة , عن النبي صلى','3321','1599','0');
INSERT INTO [t13041] VALUES (1601,'كثير بن أبي كثير المؤذن عن عطاء ولا يتابع عليه','3322','1599','0');
INSERT INTO [t13041] VALUES (1602,'كثير بن عبد الله بن عمرو بن عوف المزني حدثنا أحمد بن زكير الحضرمي، حدثنا أحمد بن سعيد الفهري، حدثنا إبراهيم بن المنذر، عن مطرف بن عبد الله قال: رأيت كثير بن عبد الله بن عمرو بن عوف المزني وكان كثير الخصومة ولم يكن أحد من أصحابنا يأخذ عنه، فقال له','3324','1599','0');
INSERT INTO [t13041] VALUES (1603,'كثير بن عبد الله اليشكري عن الحسن بن عبد الرحمن بن عوف ولا يصح إسناده','3325','1599','0');
INSERT INTO [t13041] VALUES (1604,'كثير بن سليم الضبي عن أنس. حدثنا محمد بن عيسى، حدثنا عباس بن محمد قال: سمعت يحيى قال: كثير بن سليم ضعيف','3327','1599','0');
INSERT INTO [t13041] VALUES (1605,'كثير بن شنظير حدثنا محمد بن عيسى، حدثنا عمرو بن علي قال: كان يحيى لا يحدث عن كثير بن شنظير. وحدثته يوما عن بشر بن المفضل، عن كثير بن شنظير، فقال: كثير بن شنظير، كثير بن شنظير وكان عبد الرحمن يحدث عنه. حدثنا محمد، حدثنا عباس قال: سمعت يحيى قال:','3329','1599','0');
INSERT INTO [t13041] VALUES (1606,'كثير بن مروان المقدسي حدثني محمد، حدثنا عباس، قال سمعت يحيى، يقول: كثير بن مروان ضعيف، وقد سمعت أنا منه وفي موضع آخر , كثير بن مروان ليس بشيء','3331','1599','0');
INSERT INTO [t13041] VALUES (1607,'كثير بن عبد الله أبو هاشم الأبلي ولا يتابعه إلا نحوه في الضعف. حدثني آدم قال: سمعت البخاري قال: كثير بن عبد الله أبو هاشم الأبلي منكر الحديث','3333','1599','0');
INSERT INTO [t13041] VALUES (1608,'كامل أبو العلاء عن أبي صالح مولى ضباعة. حدثنا زكريا بن يحيى، حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن، يحدث عن كامل أبي العلاء شيئا قط','3335','1599','0');
INSERT INTO [t13041] VALUES (1609,'كامل بن طلحة الجحدري حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى، قال كامل بن طلحة: ليس بشيء. وحدثنا أحمد بن أصرم قال: سمعت أحمد بن حنبل، وسئل عن كامل بن طلحة الجحدري، فقال: كان مقارب الحديث. حدثنا عبد الله بن أحمد قال: سمعت أبي وسئل عن كامل','3338','1599','0');
INSERT INTO [t13041] VALUES (1610,'كنانة بن عباس بن مرداس السلمي عن أبيه. حدثني آدم قال: سمعت البخاري قال: كنانة بن عباس بن مرداس السلمي , عن أبيه روى عنه ابنه , قال البخاري: ولم يصح','3339','1599','0');
INSERT INTO [t13041] VALUES (1611,'كنانة بن جبلة حدثنا أحمد بن محمد المديني، حدثنا عثمان بن سعيد قال: سألت يحيى قلت: كنانة بن جبلة الذي كان يكون بخراسان من أهل الحديث؟ قال: ذاك كذاب خبيث , قال: عثمان: وهو قريب مما قال يحيى: هو خبيث الحديث','3341','1599','0');
INSERT INTO [t13041] VALUES (1612,'كريم عن الحارث كوفي حدثني آدم قال: سمعت البخاري قال: كريم عن الحارث، كوفي روى عنه أبو إسحاق، لا يصح','3342','1599','0');
INSERT INTO [t13041] VALUES (1613,'كوثر بن حكيم الحلبي عن نافع. حدثنا آدم قال: سمعت البخاري قال: كوثر بن حكيم عن نافع، منكر الحديث. حدثنا عبد الله بن أحمد قال: قال أبي: كان هشيم ذهب إلى حلب فسمع من كوثر بن حكيم بحلب , وليس هو بشيء. وقال في موضع آخر: لا يسوى حديثه شيئا. حدثنا','3344','1599','0');
INSERT INTO [t13041] VALUES (1614,'كيسان أبو عمر عن يزيد بن بلال','3346','1599','0');
INSERT INTO [t13041] VALUES (1615,'كدير الضبي كان من الشيعة. حدثنا محمد بن عيسى، حدثنا محمد بن علي، يقال له حمدان الوراق ثقة. حدثنا إسحاق بن إسماعيل، حدثنا جرير، عن مغيرة، عن سماك بن سلمة قال: دخلت على كدير الضبي أعوده بعد الغداة , فقالت لي امرأته: ادن منه فإنه يصلي حتى يتوكأ','3349','1599','0');
INSERT INTO [t13041] VALUES (1616,'باب اللام','3351','0','0');
INSERT INTO [t13041] VALUES (1617,'ليث بن أبي سليم واسم أبي سليم زياد مولى معاوية بن أبي سفيان رضي الله عنه. حدثني محمد بن عيسى، حدثنا الجوهري إبراهيم بن سعيد , حدثنا يحيى بن معين قال: ليث بن أبي سليم: ليث بن عيسى. حدثنا الحسين بن أحمد، حدثنا أبو معمر قال: كان ابن عيينة يضعف ليث بن','3352','1616','0');
INSERT INTO [t13041] VALUES (1618,'ليث بن أنس بن ذنيم الليثي كان يرى القدر رأي الصفرية , سمع ابن سيرين، روى عنه، وليد بن كريز، قاله لنا آدم، عن البخاري','3353','1616','0');
INSERT INTO [t13041] VALUES (1619,'لمازة بن زبار أبو لبيد بصري حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى، حدثنا وهب بن جرير، حدثنا أبي، عن أبي لبيد وكان شتاما , قلت لأبي: ما كان يشتم، قال: نراه علي بن أبي طالب رضي الله عنه','3355','1616','0');
INSERT INTO [t13041] VALUES (1620,'لوط أبو مخنف حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: أبو مخنف ليس بشيء، وفي موضع آخر ليس بثقة. حدثنا محمد، حدثنا عباس قال: سمعت يحيى قال: أبو مخنف , وأبو مريم , وعمرو بن شمر ليسوا هم بشيء، قلت ليحيى: هما مثل عمرو بن شمر قال: هما شر','3357','1616','0');
INSERT INTO [t13041] VALUES (1621,'باب الميم','3358','0','0');
INSERT INTO [t13041] VALUES (1622,'محمد بن الأشعث عن أبي سلمة: مجهول في النسب والرواية , وحديثه غير محفوظ','3359','1621','0');
INSERT INTO [t13041] VALUES (1623,'محمد بن إبراهيم التيمي مديني حدثنا عبد الله بن أحمد قال: سمعت أبي، وذكر محمد بن إبراهيم التيمي المديني، فقال: في حديثه شيء يروي أحاديث مناكير أو منكرة والله أعلم','3361','1621','0');
INSERT INTO [t13041] VALUES (1624,'محمد بن إبراهيم القرشي عن أبي صالح، مجهولان جميعا بالنقل، والحديث غير محفوظ','3363','1621','0');
INSERT INTO [t13041] VALUES (1625,'محمد بن أبان بن صالح بن عمير القرشي حدثنا زكريا بن يحيى، قال: حدثنا محمد بن المثنى، قال: ما سمعت عبد الرحمن بن مهدي يحدث عن محمد بن أبان. حدثني الخضر بن داود، قال: حدثنا أحمد بن محمد، قال: ذكر لأبي عبد الله، عن أبي الوليد عن محمد بن أبان، فقال','3365','1621','0');
INSERT INTO [t13041] VALUES (1626,'محمد بن إسماعيل بن طريح الثقفي حدثني آدم، قال: سمعت البخاري قال: محمد بن إسماعيل الثقفي: لا يتابع عليه','3367','1621','0');
INSERT INTO [t13041] VALUES (1627,'محمد بن إسماعيل الضبي عن أبي المعلى العطار حدثني آدم قال: سمعت البخاري قال: محمد بن إسماعيل الضبي , عن أبي المعلى العطار منكر الحديث','3369','1621','0');
INSERT INTO [t13041] VALUES (1628,'محمد بن إسماعيل الوساوسي بصري قال العقيلي: قال لي أحمد بن عمرو بن عبد الخالق البزار: كان يضع الحديث، وحديثه يدل على ذلك','3371','1621','0');
INSERT INTO [t13041] VALUES (1629,'محمد بن إسحاق بن يسار أبو بكر مولى قيس بن مخرمة مديني. حدثنا العباس بن الفضل الأسفاطي، حدثنا سليمان بن داود، حدثنا يحيى بن سعيد قال: حدثنا وهيب قال: سمعت هشام بن عروة، يقول: محمد بن إسحاق كذاب. حدثنا جعفر بن محمد، حدثنا عباس العنبري، حدثنا أبو','3373','1621','0');
INSERT INTO [t13041] VALUES (1630,'محمد بن إسحاق بن إبراهيم الأسدي العكاشي حدثني آدم قال: سمعت البخاري قال: محمد بن إسحاق بن إبراهيم بن محمد الأسدي العكاشي , عن الأوزاعي، وجعفر بن برقان يقال له: الأندلسي، قال البخاري: منكر الحديث، وقال البخاري: قال ابن معين: كذاب','3374','1621','0');
INSERT INTO [t13041] VALUES (1631,'محمد بن أنس ابن عبد الحميد، ابن أخي جرير بن عبد الحميد، عن الأعمش، بأحاديث لم يتابعه عليها أحد','3376','1621','0');
INSERT INTO [t13041] VALUES (1632,'محمد بن أسعد الثعلبي عن زهير بن معاوية، منكر الحديث','3378','1621','0');
INSERT INTO [t13041] VALUES (1633,'محمد بن أبي المليح الهذلي أخو مبشر. حدثنا زكريا بن يحيى، حدثنا محمد بن المثنى قال: ما سمعت يحيى، ولا عبد الرحمن بن مهدي يحدثان , عن محمد بن أبي المليح الهذلي شيئا قط','3380','1621','0');
INSERT INTO [t13041] VALUES (1634,'محمد بن الأزهر الجوزجاني حدثنا عبد الله بن أحمد بن حنبل قال: سمعت أبي يقول لرجل من أهل خراسان وسأله عن محمد بن الأزهر الجوزجاني، فقال: لا تكتبوا عنه حتى يتوب، وذاك أنه بلغه أنه تكلم في القرآن، فقال: لا تكتبوا عنه','3382','1621','0');
INSERT INTO [t13041] VALUES (1635,'محمد بن بلال، بصري عن همام، وعمران القطان، بصري يهم في حديثه كثيرا','3385','1621','0');
INSERT INTO [t13041] VALUES (1636,'محمد بن بحر الهجيمي , بصري منكر الحديث كثير الوهم','3390','1621','0');
INSERT INTO [t13041] VALUES (1637,'محمد بن ثابت العبدي بصري , عن نافع حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: محمد بن ثابت العبدي ليس بشيء. حدثنا محمد بن أحمد، حدثنا معاوية بن صالح قال: سمعت يحيى قال: الحسن بن ثابت العبدي ليس به بأس، ينكر عليه حديث ابن عمر في التيمم،','3392','1621','0');
INSERT INTO [t13041] VALUES (1638,'محمد بن ثابت بن أسلم البناني , بصري حدثني آدم قال: سمعت البخاري قال: محمد بن ثابت بن أسلم البناني بصري فيه نظر. حدثنا محمد بن عيسى، حدثنا عباس قال: وحدثنا محمد بن أحمد، حدثنا معاوية بن صالح قال: سمعنا يحيى بن معين يقول: قال: محمد بن ثابت البناني','3395','1621','0');
INSERT INTO [t13041] VALUES (1639,'محمد بن أبي الجعد الكوفي','3397','1621','0');
INSERT INTO [t13041] VALUES (1640,'محمد بن جابر اليمامي كوفي حدثني آدم قال: سمعت البخاري قال: محمد بن جابر اليمامي , عن قيس بن طلق، وحماد، ليس بالقوي عندهم. حدثنا عبد الله بن أحمد قال: سئل أبي عن محمد بن جابر، وأيوب بن جابر، فقال: محمد يروي أحاديث مناكير، وهو معروف بالسماع،','3400','1621','0');
INSERT INTO [t13041] VALUES (1641,'محمد بن جابر عن عبد الله بن دينار مجهول بالنقل حديثه غير محفوظ','3403','1621','0');
INSERT INTO [t13041] VALUES (1642,'محمد بن جابر الحلبي عن الأوزاعي لا يتابع عليه','3405','1621','0');
INSERT INTO [t13041] VALUES (1643,'محمد بن جحادة حدثنا عبد الله بن أحمد بن حنبل، حدثني أبو بكر بن خلاد، عن يحيى بن سعيد، عن أبي عوانة قال: كان محمد بن جحادة يغلو في التشيع','3407','1621','0');
INSERT INTO [t13041] VALUES (1644,'محمد بن جعفر المدائني حدثني محمد بن عبد الرحمن، حدثنا عبد الملك بن عبد الحميد قال: سمعت أبا عبد الله، يقول: محمد بن جعفر ذاك الذي كان بالمدائن وقد سمعت منه، ولكن، لم أرو عنه شيئا قط، أو لا أحدث عنه بشيء أبدا','3408','1621','0');
INSERT INTO [t13041] VALUES (1645,'محمد بن الحجاج اللخمي الواسطي حدثنا أحمد بن محمود الهروي، حدثنا عثمان بن سعيد، قلت ليحيى: محمد بن الحجاج اللخمي الواسطي، كيف هو؟ قال: كذاب. حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى بن معين قال: محمد بن الحجاج الواسطي كان يحدث، " أطعمني','3409','1621','0');
INSERT INTO [t13041] VALUES (1646,'محمد بن الحجاج بن رشدين بن سعد المهري في حديثه نظر','3413','1621','0');
INSERT INTO [t13041] VALUES (1647,'محمد بن الحجاج المصفر حدثنا عبد الله بن أحمد بن حنبل قال: سألت أبي عن محمد بن الحجاج المصفر، فقال: قد تركت حديثه، أو تركنا حديثه , حدثنا محمد بن عيسى , حدثنا العباس بن محمد قال: سمعت يحيى قال: محمد بن الحجاج المصفر ليس بشيء، حدث عن شعبة بأحاديث','3415','1621','0');
INSERT INTO [t13041] VALUES (1648,'محمد بن الحارث القرشي كوفي مجهول بالنقل حديثه غير محفوظ','3416','1621','0');
INSERT INTO [t13041] VALUES (1649,'محمد بن الحارث بن وقدان العتكي عن شعبة بن الحجاج، ولا يتابع عليه','3418','1621','0');
INSERT INTO [t13041] VALUES (1650,'محمد بن الحارث بصري , عن ابن البيلماني حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى، يقول محمد بن الحارث الحارثي بصري ليس بشيء','3420','1621','0');
INSERT INTO [t13041] VALUES (1651,'محمد بن الحسن بن أبي يزيد الهمداني الكوفي أبو الحسن حدثنا عبد الله بن أحمد قال: سمعت أبي سئل عن محمد بن الحسن بن أبي يزيد الهمداني، فقال: ما أرى يسوى شيئا، كان ينزل عند مقابر الخيزران، وجعل يحدث بأحاديث يجيء بها كما يحدث بها ابن أبي زائدة وأبو','3422','1621','0');
INSERT INTO [t13041] VALUES (1652,'محمد بن الحسن بن عطية العوفي أبو سعيد مضطرب الحفظ','3424','1621','0');
INSERT INTO [t13041] VALUES (1653,'محمد بن الحسن الأسدي يعرف بالتل كوفي، لا يتابع على حديثه. حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى، حدثنا محمد بن الحسن الأسدي، قد أدركته وليس بشيء','3426','1621','0');
INSERT INTO [t13041] VALUES (1654,'محمد بن الحسن القردوسي , بصري حديثه غير محفوظ، وليس بمشهور بالنقل','3428','1621','0');
INSERT INTO [t13041] VALUES (1655,'محمد بن الحسن الصدفي ليس بمشهور بالنقل، وحديثه غير محفوظ','3430','1621','0');
INSERT INTO [t13041] VALUES (1656,'محمد بن الحسن الهاشمي ولا يتابع على حديثه وله مناكير عن الثقات','3432','1621','0');
INSERT INTO [t13041] VALUES (1657,'محمد بن الحسن صاحب أبي حنيفة كوفي حدثنا أحمد بن محمد بن صدقة قال: سمعت العباس بن محمد البصري، يقول سمعت يحيى بن معين، يقول: محمد جهمي كذاب. حدثنا محمد بن أحمد، حدثنا معاوية بن صالح قال: سمعت يحيى قال: محمد بن الحسن صاحب الرأي ضعيف. حدثنا الهيثم','3434','1621','0');
INSERT INTO [t13041] VALUES (1658,'محمد بن الحسن الشيباني حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: محمد بن الحسن الشيباني ليس بشيء','3435','1621','0');
INSERT INTO [t13041] VALUES (1659,'محمد بن الحسن بن أتش الصنعاني حدثني محمد بن عبد الرحمن، حدثنا عبد الملك بن عبد الحميد قال: سمعت أبا عبد الله، يقول: محمد بن الحسن بن أتش من الفرس من القدرية الكبار','3437','1621','0');
INSERT INTO [t13041] VALUES (1660,'محمد بن الحسن بن زبالة المخزومي المديني حدثنا محمد بن عيسى، حدثنا العباس بن محمد قال: سمعت يحيى بن معين، يقول: ابن زبالة المديني ليس بثقة، كان يسرق الحديث، وقال في موضع آخر: محمد بن الحسن بن زبالة مديني كان كذابا ولم يكن بشيء. حدثني أحمد بن محمود','3439','1621','0');
INSERT INTO [t13041] VALUES (1661,'محمد بن حجر بن عبد الجبار بن وائل بن حجر كوفي حدثني آدم قال: سمعت البخاري قال: محمد بن حجر بن عبد الجبار بن وائل بن حجر كوفي فيه بعض النظر','3441','1621','0');
INSERT INTO [t13041] VALUES (1662,'محمد بن حميد أبو سفيان المعمري في حديثه نظر','3443','1621','0');
INSERT INTO [t13041] VALUES (1663,'محمد بن حميد الرازي حدثني آدم قال: سمعت البخاري قال: محمد بن حميد أبو عبد الله الرازي فيه نظر. حدثني إبراهيم بن يوسف قال: كتب أبو زرعة، ومحمد بن مسلم , عن محمد بن حميد، حديثا كثيرا، ثم تركا الرواية','3445','1621','0');
INSERT INTO [t13041] VALUES (1664,'محمد بن أبي حميد المديني، ويقال: حماد حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: محمد بن أبي حميد أحاديثه أحاديث مناكير , وقال في موضع آخر: ليس هو بقوي في الحديث. حدثني محمد بن عبد الرحمن، حدثنا عبد الملك بن عبد الحميد قال: قال لي أبو عبد الله في','3446','1621','0');
INSERT INTO [t13041] VALUES (1665,'محمد بن حميد صاحب السابري مجهول في النسب والرواية، حديثه غير محفوظ','3447','1621','0');
INSERT INTO [t13041] VALUES (1666,'محمد بن خالد بن عبد الله الطحان الواسطي حدثني محمد بن عبد الحميد السهمي، حدثنا أحمد بن محمد الحضرمي قال: قال لنا يحيى بن معين: محمد بن خالد الواسطي حديثه ليس بشيء. حدثني آدم بن موسى قال: سمعت البخاري قال: محمد بن خالد بن عبد الله الواسطي , قال:','3449','1621','0');
INSERT INTO [t13041] VALUES (1667,'محمد بن دينار الطاحي بصري في حديثه وهم. حدثنا محمد بن أحمد، حدثنا معاوية بن صالح قال: سمعت يحيى بن معين، يقول: محمد بن دينار الطاحي بصري ضعيف','3450','1621','0');
INSERT INTO [t13041] VALUES (1668,'محمد بن درهم , بصري حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: محمد بن درهم ليس بثقة , كان شبابة يروي عنه','3454','1621','0');
INSERT INTO [t13041] VALUES (1669,'محمد بن ذكوان , مولى الجهاضم بصري حدثني آدم قال: سمعت البخاري قال: محمد بن ذكوان مولى الجهاضم , منكر الحديث','3456','1621','0');
INSERT INTO [t13041] VALUES (1670,'محمد بن راشد الخزاعي , يقال له: المكحولي حدثنا محمد بن إسماعيل، حدثنا محمود بن غيلان قال: سمعت أبا النضر يقول: كنت عند باب الرصافة فسلم علي شعبة , فمر بي محمد بن راشد الخزاعي , فقال لي: كتبت عن هذا شيئا؟ فقلت: نعم , حديث كذا وكذا , فقال: لا تكتب','3458','1621','0');
INSERT INTO [t13041] VALUES (1671,'محمد بن زياد , صاحب ميمون بن مهران يقال له: اليشكري كوفي. حدثني آدم قال: سمعت البخاري قال: محمد بن زياد صاحب ميمون بن مهران , يقال له: اليشكري متروك الحديث. حدثني آدم، حدثنا محمد بن إسماعيل، حدثنا عمرو بن زرارة، أنه قال: كان محمد بن زياد يتهم','3459','1621','0');
INSERT INTO [t13041] VALUES (1672,'محمد بن أبي الزعيزعة عن نافع شامي. حدثني آدم قال: سمعت البخاري قال: محمد بن أبي الزعيزعة , عن نافع، منكر الحديث','3462','1621','0');
INSERT INTO [t13041] VALUES (1673,'محمد بن الزبير الحنظلي بصري حدثني آدم قال: سمعت البخاري قال: محمد بن الزبير الحنظلي منكر الحديث وفيه نظر','3464','1621','0');
INSERT INTO [t13041] VALUES (1674,'محمد بن زاذان مدني حدثني آدم قال: سمعت البخاري قال: محمد بن زاذان مدني منكر الحديث , لا يكتب حديثه','3466','1621','0');
INSERT INTO [t13041] VALUES (1675,'محمد بن سليمان بن مسمول مكي حدثنا عبد الله بن أحمد، عن عبد السلام قال: حدثنا محمد بن إسماعيل قال: سمعت الحميدي، يتكلم في محمد بن سليمان بن مسمول المسمولي المخزومي، سكن مكة','3470','1621','0');
INSERT INTO [t13041] VALUES (1676,'محمد بن سعيد المصلوب شامي حدثني آدم قال: سمعت البخاري قال: محمد بن سعيد الشامي المصلوب , كان صلب في الزندقة متروك الحديث. حدثنا علي بن عبد العزيز، حدثنا سليمان بن أحمد، حدثني أبو مسهر، حدثنا عيسى بن يونس قال: قدم علينا محمد بن سعيد العراق , فقال','3474','1621','0');
INSERT INTO [t13041] VALUES (1677,'محمد بن سليمان بن معاذ القرشي , عن مالك منكر الحديث','3475','1621','0');
INSERT INTO [t13041] VALUES (1678,'محمد بن سليمان بن علي بن عبد الله بن عباس أمير البصرة ليس يعرف بالنقل , وحديثه هذا غير محفوظ , ولا يعرف إلا به','3478','1621','0');
INSERT INTO [t13041] VALUES (1679,'محمد بن سليمان بن أبي كريمة عن هشام بن عروة، ببواطيل لا أصل لها','3480','1621','0');
INSERT INTO [t13041] VALUES (1680,'محمد بن سليمان بن سليط الأنصاري السالمي مجهول بالنقل، روى عنه عبد العزيز بن يحيى، وعبد العزيز متروك','3482','1621','0');
INSERT INTO [t13041] VALUES (1681,'محمد بن سليم أبو هلال الراسبي، مولى بني سالم بن لؤي حدثني آدم قال: سمعت البخاري قال: كان يحيى بن سعيد لا يروي عن أبي هلال الراسبي، وكان ابن مهدي يروي عنه. حدثنا محمد بن عيسى، حدثنا عمرو بن علي قال: كان يحيى لا يحدث عن أبي هلال، وكان عبد الرحمن','3484','1621','0');
INSERT INTO [t13041] VALUES (1682,'محمد بن سالم أبو سهل كوفي حدثنا عبد الله بن أحمد، حدثنا الحسن بن عيسى قال: قال ابن المبارك: محمد بن سالم , والسري بن إسماعيل , وعبيدة ترك الحديث عنهم. حدثنا عبد الله بن أحمد قال: سألت أبي عن محمد بن سالم أبي سهل، فقال: هو شبه المتروك. حدثنا عبد','3485','1621','0');
INSERT INTO [t13041] VALUES (1683,'محمد بن السائب الكلبي أبو النضر كوفي حدثنا أحمد بن علي الأبار، حدثنا أحمد بن الحسن الترمذي قال: سمعت يحيى بن يعلى قال: سمعت زائدة، يقول: اطرحوا حديث الأربعة: الحجاج وجابر وحميد صاحب مجاهد والكلبي , فأما الكلبي , ورفع إصبعيه إلى أذنيه , صمتا إن لم','3487','1621','0');
INSERT INTO [t13041] VALUES (1684,'محمد بن أبي سلمة المكي عن محمد بن عمرو، لا يتابع على حديثه , ولا يعرف إلا به','3488','1621','0');
INSERT INTO [t13041] VALUES (1685,'محمد بن سلمة بن كهيل حدثنا محمد بن عيسى، حدثنا العباس بن محمد قال: سمعت يحيى قال: محمد بن سلمة بن كهيل ويحيى بن سلمة , أما يحيى فضعيف , وأما محمد فلم يكن ليحيى فيه رأي','3490','1621','0');
INSERT INTO [t13041] VALUES (1686,'محمد بن سكين , مؤذن بني شقرة','3492','1621','0');
INSERT INTO [t13041] VALUES (1687,'محمد بن أبي سهل عن مكحول مرسل. حدثني آدم قال: سمعت البخاري قال: محمد بن أبي سهل , عن مكحول، مرسل , روى عنه أبو بكر بن عياش قال: البخاري لا يتابع عليه في حديثه','3494','1621','0');
INSERT INTO [t13041] VALUES (1688,'محمد بن سلام الخزاعي عن أبيه، عن أبي هريرة. حدثني آدم قال: سمعت البخاري قال: محمد بن سلام الخزاعي , عن أبيه، عن أبي هريرة، في الذي يأتي البهيمة , قال: هو , عن دحيم , عن ابن أبي فديك , لا يتابع عليه','3495','1621','0');
INSERT INTO [t13041] VALUES (1689,'محمد بن شعيب عن داود بن علي كوفي، حديثه غير محفوظ','3496','1621','0');
INSERT INTO [t13041] VALUES (1690,'محمد بن أبي الشمال العطاردي أبو سفيان بصري حدثني آدم قال: سمعت البخاري قال: محمد بن أبي الشمال العطاردي أبو سفيان بصري , عن أم طلحة، عن عائشة، رضي الله عنها , في دم الحيض، لا يتابع عليه ولا يصح','3498','1621','0');
INSERT INTO [t13041] VALUES (1691,'محمد بن شجاع النبهاني مروزي حدثني عبد الله بن محمد بن سعدويه المروزي، حدثنا أحمد بن عبد الله بن بشير المروزي، حدثنا سفيان بن عبد الملك قال: سمعت ابن المبارك يقول: محمد بن شجاع ليس بشيء , ولا يعرف الحديث. حدثني الفضل بن عيسى الهاشمي، حدثنا هدية بن','3500','1621','0');
INSERT INTO [t13041] VALUES (1692,'محمد بن طلحة بن مصرف اليامي كوفي عن زبيد، وأبيه. حدثنا عبد الله بن أحمد قال: سمعت يحيى بن معين، يقول كان يقال: ثلاثة يتقى حديثهم، محمد بن طلحة بن مصرف، وأيوب بن عتبة، وفليح بن سليمان، قلت له: ممن سمعت هذا؟ قال: سمعته من أبي كامل مظفر بن مدرك','3502','1621','0');
INSERT INTO [t13041] VALUES (1693,'محمد بن عبد الله الكناني عن عمرو بن دينار. حدثني آدم قال: سمعت البخاري قال: محمد بن عبد الله الكناني , عن عمرو بن دينار، لا يتابع عليه','3506','1621','0');
INSERT INTO [t13041] VALUES (1694,'محمد بن عبد الله بن مسلم ابن أخي الزهري مدني حدثنا أحمد بن محمود الهروي، حدثنا عثمان بن سعيد قال: قلت ليحيى بن معين: ابن أخي الزهري ما حاله؟ قال: ضعيف. حدثنا محمد بن عيسى، حدثنا العباس بن محمد قال: سمعت يحيى بن معين، يقول: محمد بن عبد الله ابن','3508','1621','0');
INSERT INTO [t13041] VALUES (1695,'محمد بن عبد الله بن المثنى الأنصاري حدثنا عبد الله بن أحمد قال: سمعت أبي، عن معاذ بن معاذ قال: والله ما رأيته عند الأشعث , يعني محمد بن عبد الله الأنصاري. حدثنا عبد الله قال: قال أبي: قال أبو خيثمة: أنكر يحيى بن سعيد ومعاذ بن معاذ بحديث حبيب بن','3513','1621','0');
INSERT INTO [t13041] VALUES (1696,'محمد بن عبد الله بن علاثة العقيلي القاضي حدثني آدم قال: سمعت البخاري قال: محمد بن عبد الله بن علاثة العقيلي القاضي، في حفظه نظر','3515','1621','0');
INSERT INTO [t13041] VALUES (1697,'محمد بن عبد الله بن إنسان الطائفي حدثني آدم قال: سمعت البخاري قال: محمد بن عبد الله بن إنسان الطائفي ولا يتابع عليه','3517','1621','0');
INSERT INTO [t13041] VALUES (1698,'محمد بن عبد الله العمي عن ثابت لا يقيم الحديث','3519','1621','0');
INSERT INTO [t13041] VALUES (1699,'محمد بن عبد الله بن عبيد بن عمير الليثي المكي حدثني آدم قال: سمعت البخاري قال: محمد بن عبد الله بن عبيد بن عمير ليس بذاك القوي','3522','1621','0');
INSERT INTO [t13041] VALUES (1700,'محمد بن عبد الله بن عمر بن القاسم العمري عن مالك ولا يصح حديثه، ولا يعرف بنقل الحديث','3525','1621','0');
INSERT INTO [t13041] VALUES (1701,'محمد بن عبد الرحمن بن قدامة بصري حدثني آدم قال: سمعت البخاري قال: محمد بن عبد الرحمن بن قدامة بصري، سمع أبا مالك الأشجعي، فيه نظر','3527','1621','0');
INSERT INTO [t13041] VALUES (1702,'محمد بن عبد الله أبو سلمة الأنصاري عن مالك بن دينار منكر الحديث','3529','1621','0');
INSERT INTO [t13041] VALUES (1703,'محمد بن عبد الرحمن بن هشام المخزومي الأوقص كان قاضي المدينة يخالف في حديثه','3531','1621','0');
INSERT INTO [t13041] VALUES (1704,'محمد بن عبد الرحمن بن أبي ليلى حدثنا عبد الله بن أحمد بن عبد السلام، حدثنا أحمد بن سعيد الدارمي، حدثنا النضر بن شميل قال: سمعت شعبة، يقول: أفادني محمد بن عبد الرحمن بن أبي ليلى أحاديث، فإذا هي مقلوبة. حدثنا حيان بن إسحاق المروزي، حدثنا إسحاق بن','3534','1621','0');
INSERT INTO [t13041] VALUES (1705,'محمد بن عبد الرحمن بن البيلماني حدثنا أحمد بن محمود، حدثنا عثمان بن سعيد، قال: قلت ليحيى بن معين: محمد بن عبد الرحمن بن البيلماني؟ فقال: ليس بشيء. حدثني آدم، قال: سمعت البخاري، قال: محمد بن عبد الرحمن بن البيلماني: كان الحميدي يتكلم فيه','3539','1621','0');
INSERT INTO [t13041] VALUES (1706,'محمد بن عبد الرحمن بن أبي بكر الجدعاني حدثني آدم قال: سمعت البخاري قال: محمد بن عبد الرحمن بن أبي بكر الجدعاني المدني عن عبيد الله بن عمر , روى عنه ابن أبي أويس. قال البخاري: منكر الحديث','3543','1621','0');
INSERT INTO [t13041] VALUES (1707,'محمد بن عبد الرحمن السهمي حدثني آدم، قال: سمعت البخاري، قال: محمد بن عبد الرحمن السهمي البصري الباهلي لا يتابع على روايته','3545','1621','0');
INSERT INTO [t13041] VALUES (1708,'محمد بن عبد الرحمن أبو جابر البياضي حدثنا عبد الله بن أحمد، حدثني أبي , حدثنا بشر بن عمر، قال: سألت مالك بن أنس عن محمد بن عبد الرحمن أبي جابر البياضي؟ فقال: يتهمه بالكذب. حدثنا جعفر بن محمد، حدثتا أبو قدامة ح وحدثنا زكريا بن يحيى، حدثنا محمد بن','3547','1621','0');
INSERT INTO [t13041] VALUES (1709,'محمد بن عبد الرحمن بن المجبر بصري حدثنا محمد بن عيسى , حدثنا عباس قال: سمعت يحيى بن معين , قال: محمد بن عبد الرحمن بن مجبر ليس بشيء. حدثني آدم قال: سمعت البخاري قال: محمد بن عبد الرحمن بن مجبر سكتوا عنه','3549','1621','0');
INSERT INTO [t13041] VALUES (1710,'محمد بن عبد الرحمن القشيري عن مسعر حديثه منكر ليس له أصل لا يتابع عليه، وهو مجهول بالنقل','3551','1621','0');
INSERT INTO [t13041] VALUES (1711,'محمد بن عبد الملك الأنصاري عن محمد بن المنكدر حدثنا عبد الله بن أحمد قال: سألت أبي عن شيخ روى عن يحيى بن صالح الوحاظي أبي خاطر يقال له: محمد بن عبد الملك الأنصاري , حدثنا عطاء , عن ابن عباس قال: نهى رسول الله صلى الله عليه وسلم أن يتخلل بالقصب والآس ,','3553','1621','0');
INSERT INTO [t13041] VALUES (1712,'محمد بن عبد العزيز بن عمر بن عبد الرحمن ابن عوف الزهري القاضي حدثني آدم قال: سمعت البخاري قال محمد بن عبد العزيز بن عمر بن عبد الرحمن بن عوف الزهري القاضي منكر الحديث، ومن حديثه ما حدثناه عبد الله بن أحمد بن أبي مسرة حدثنا يعقوب بن محمد الزهري حدثنا','3557','1621','0');
INSERT INTO [t13041] VALUES (1713,'محمد بن عبد الجبار حدث عنه، شعبة , مجهول بالنقل','3558','1621','0');
INSERT INTO [t13041] VALUES (1714,'محمد بن عبيد الله بن أبي رافع حدثنا محمد , حدثنا عباس , قال سمعت يحيى قال: ابن أبي رافع الذي يحدث عنه حبان ليس حديثه بشيء. حدثني آدم , قال: سمعت البخاري قال: محمد بن عبيد الله بن أبي رافع منكر الحديث قال ابن معين ليس بشيء هو وابنه معمر','3560','1621','0');
INSERT INTO [t13041] VALUES (1715,'محمد بن عبيد الله العرزمي حدثنا أحمد بن علي الأبار، حدثنا أبو هشام الرفاعي، حدثنا يحيى بن آدم، قال: شهدت سفيان وهو يقول: أي شيء تحفظون في الرجل يوصي للرجل بسهم من ماله؟ فقال له رجل من أصحاب الحديث: أبو قيس عن هذيل , عن عبد الله , قال: من دونه ,','3562','1621','0');
INSERT INTO [t13041] VALUES (1716,'محمد بن عمر بن واقد الواقدي مديني حدثني آدم بن موسى قال: سمعت البخاري قال: محمد بن عمر بن واقد الواقدي مديني , سكن بغداد كان قاضيا , قال البخاري: متروك الحديث , تركه أحمد، وابن نمير، وابن المبارك، وإسماعيل بن زكريا. حدثنا عبد الله بن أحمد، حدثني','3564','1621','0');
INSERT INTO [t13041] VALUES (1717,'محمد بن عمرو بن علقمة بن وقاص الليثي حدثنا محمد بن زكريا، حدثنا الحسن بن شجاع، حدثنا علي بن المديني قال: سألت يحيى، عن محمد بن عمرو بن علقمة، كيف هو؟ قال: تريد العفو أو تشدد؟ قلت: بل أتشدد , قال: فليس هو ممن تريد , كان يقول شيئا , حدثنا أبو','3566','1621','0');
INSERT INTO [t13041] VALUES (1718,'محمد بن عمرو الأنصاري أبو سهل بصري حدثنا محمد بن عيسى، حدثنا عمرو بن علي قال: ذكرت ليحيى بن سعيد القطان حديث محمد بن عمرو أبي سهل الأنصاري , فقلت: حدثنا عبد الرحمن، حدثنا محمد بن عمرو، عن القاسم بن محمد، عن عائشة، في العقيقة , فقال هو أثبت من عبد','3567','1621','0');
INSERT INTO [t13041] VALUES (1719,'محمد بن عمرو السوسي كوفي كان بمصر , كان يذهب إلى الرفض , وحدث بمناكير','3572','1621','0');
INSERT INTO [t13041] VALUES (1720,'محمد بن عون الخراساني مروزي حدثني محمد بن عيسى، حدثنا العباس بن محمد قال: سمعت يحيى قال: محمد بن عون الخراساني ليس بشيء. وحدثني آدم بن موسى قال: سمعت البخاري قال: محمد بن عون الخراساني مروزي منكر الحديث','3574','1621','0');
INSERT INTO [t13041] VALUES (1721,'محمد بن عطية بن سعد العوفي حدثني آدم قال: سمعت البخاري قال: محمد بن عطية العوفي روى عنه، أسيد بن زيد الجمال عجائب','3576','1621','0');
INSERT INTO [t13041] VALUES (1722,'محمد بن عيسى العبدي عن محمد بن المنكدر. حدثني آدم قال: سمعت البخاري قال: محمد بن عيسى بصري , عن محمد بن المنكدر، في المؤذنين منكر الحديث','3578','1621','0');
INSERT INTO [t13041] VALUES (1723,'محمد بن عيسى بن سميع الدمشقي عن ابن أبي ذئب، حدثنا أحمد بن منصور، حدثنا هشام بن عمار، حدثنا محمد بن عيسى بن القاسم بن سميع، عن محمد بن عبد الرحمن بن أبي ذئب، عن محمد بن مسلم الزهري قال: قلت لسعيد بن المسيب: هل أنت مخبري كيف كان قتل عثمان رضي الله','3581','1621','0');
INSERT INTO [t13041] VALUES (1724,'محمد بن عثيم أبو ذر حدثنا أحمد بن محمود الهروي، حدثنا عثمان بن سعيد قال: قلت ليحيى بن معين: محمد بن عثيم من هو؟ قال: ليس بشيء. حدثنا محمد بن عيسى، حدثنا العباس بن محمد قال: سمعت يحيى، يقول: محمد بن عثيم كذاب. حدثني آدم قال: سمعت البخاري قال:','3582','1621','0');
INSERT INTO [t13041] VALUES (1725,'محمد بن عيسى بصري مجهول بالنقل لا يتابع على حديثه','3585','1621','0');
INSERT INTO [t13041] VALUES (1726,'محمد بن عنبسة بصري مجهول بالنقل , حديثه غير محفوظ , ويشركه فيه عدي بن الفضل , وعدي أيضا ضعيف','3587','1621','0');
INSERT INTO [t13041] VALUES (1727,'محمد بن عجلان المديني حدثنا عبد الله بن أحمد، حدثني أبو بكر بن خلاد قال: سمعت يحيى، يقول: كان ابن عجلان مضطرب الحديث، في حديث نافع، ولم يكن له تلك القيمة عنده. حدثنا محمد بن عيسى، حدثنا عمرو بن علي قال: سألت يحيى عن حديث ابن عجلان، عن المقبري،','3589','1621','0');
INSERT INTO [t13041] VALUES (1728,'محمد بن الفضيل بن غزوان الضبي حدثنا محمد بن عيسى الهاشمي، حدثنا أحمد بن الموفق، حدثنا حسن بن الربيع قال: سمعت أبا الأحوص قال: الحصين يقول: أنشد الله رجلا يجالس محمد بن فضيل وعمرو بن ثابت أن يجالسنا. حدثنا محمد بن إسماعيل الأصبهاني، حدثنا يحيى بن','3590','1621','0');
INSERT INTO [t13041] VALUES (1729,'محمد بن الفضل بن عطية الخراساني حدثنا محمد بن عثمان قال: قلت ليحيى بن معين: إن عون بن سلام يحدث عن محمد بن الفضل بن عطية الخراساني، فقال: كان محمد بن الفضل كذابا. حدثنا محمد بن عيسى، حدثنا العباس قال: سمعت يحيى بن معين، يقول: محمد بن الفضل بن','3593','1621','0');
INSERT INTO [t13041] VALUES (1730,'محمد بن الفضل السدوسي أبو النعمان ولقبه عارم، اختلط في آخر عمره. حدثني الحسين بن عبد الله الذارع، حدثنا أبو داود قال: بلغنا أن عارما، أنكر سنة ثلاث عشرة , ثم راجعه عقله واستحكم الاختلاط سنة ست عشرة ومائتين ,، قال أبو جعفر: وعلي بن عبد العزيز سمع','3595','1621','0');
INSERT INTO [t13041] VALUES (1731,'محمد بن الفرات الكوفي حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: محمد بن الفرات ليس بشيء. حدثني آدم قال: سمعت البخاري، يقول: محمد بن الفرات أبو علي منكر الحديث رماه أحمد','3598','1621','0');
INSERT INTO [t13041] VALUES (1732,'محمد بن فليح بن سليمان مديني لا يتابع في حديثه','3601','1621','0');
INSERT INTO [t13041] VALUES (1733,'محمد بن فضاء الجهضمي كنيته أبو يحيى أخو خالد بن فضاء الأزدي , لا يتابع على حديثه','3603','1621','0');
INSERT INTO [t13041] VALUES (1734,'محمد بن القاسم أبو إبراهيم الأسدي كوفي ولا يتابع على حديثه. حدثنا عبد الله بن أحمد بن حنبل قال: ذكرت لأبي حديثا , حدثناه أبو معمر، حدثنا محمد بن القاسم الأسدي، حدثنا سعيد بن عبيد الطائي، عن علي بن ربيعة الوالبي، عن علي، رضي الله عنه , قال: ولا','3605','1621','0');
INSERT INTO [t13041] VALUES (1735,'محمد بن قيس الهمداني كوفي','3606','1621','0');
INSERT INTO [t13041] VALUES (1736,'محمد بن كريب مولى ابن عباس , رضي الله عنه. حدثني الخضر بن داود، حدثنا أحمد بن محمد قال: قلت لأبي عبد الله: محمد بن كريب ورشدين بن كريب أخوان؟ قال: نعم , قلت: فأيهما أحب إليك؟ قال: كلاهما عندي منكر الحديث , أما محمد فيجيء بعجائب , عن ابن عباس ,','3608','1621','0');
INSERT INTO [t13041] VALUES (1737,'محمد بن كثير الصنعاني حدثنا عبد الله بن أحمد بن حنبل قال: وذكر أبي محمد بن كثير المصيصي , وهو الصنعاني , فضعفه جدا , وقال سمع من معمر , ثم بعث إلى اليمن , فأخذها , فرواها , وضعف حديثه عن معمر جدا , وقال: هو منكر. وحدثني آدم قال: سمعت البخاري قال:','3611','1621','0');
INSERT INTO [t13041] VALUES (1738,'محمد بن كثير الكوفي القرشي في حديثه وهم. حدثنا عبد الله بن أحمد قال: سألت أبي عن محمد بن كثير، الذي يحدث عن ليث بن أبي سليم، والحارث بن حصيرة، وعمرو بن قيس، فقال: خرقنا حديثه , ولم يرضه. حدثني آدم قال: سمعت البخاري قال: محمد بن كثير القرشي كوفي','3613','1621','0');
INSERT INTO [t13041] VALUES (1739,'محمد بن كثير البصري القصاب ولا يتابع على حديثه. حدثني آدم قال: سمعت البخاري قال: قال عمرو بن علي: محمد بن كثير كان من الدباغين، ذاهب الحديث','3616','1621','0');
INSERT INTO [t13041] VALUES (1740,'محمد بن مسلم بن تدرس أبو الزبير مولى حكيم بن حزام. حدثنا الحسن بن علي بن ياسر البغدادي، حدثنا أبو بكر الأعين، حدثنا محمد بن جعفر المدائني، حدثنا ورقاء قال: قلت لشعبة: ما لك تركت حديث أبي الزبير؟ قال: رأيته يزن ويسترجح في الميزان. حدثنا أحمد بن','3618','1621','0');
INSERT INTO [t13041] VALUES (1741,'محمد بن مروان العقيلي بصري حدثنا عبد الله بن أحمد، حدثنا أبي قال: رأيت محمد بن مروان العقيلي وحدث بأحاديث وأنا شاهد ولم أكتبها , وكتبها أصحابنا , وكان يروي عن عمارة بن أبي حفصة , تركته على عمد ولم أكتب عنه كأنه ضعفه. حدثنا محمد بن عيسى، حدثنا عباس','3619','1621','0');
INSERT INTO [t13041] VALUES (1742,'محمد بن مسلم الطائفي حدثني محمد بن عبد الرحمن، حدثنا عبد الملك بن عبد الحميد الميموني قال: سمعت أحمد بن محمد بن حنبل، رحمة الله عليه، يقول: إذا حدث محمد بن مسلم، من غير كتاب يعني أخطأ، قلت: الطائفي؟ قال: نعم , ثم ضعفه على كل حال من كتاب وغير','3621','1621','0');
INSERT INTO [t13041] VALUES (1743,'محمد بن أبي محمد مجهول بالنقل , ولا يتابع عليه , ولا يعرف إلا به','3625','1621','0');
INSERT INTO [t13041] VALUES (1744,'محمد بن مزاحم أخو الضحاك بن مزاحم , عن صدقة. حدثني آدم قال: سمعت البخاري قال: محمد بن مزاحم أخو الضحاك , ولا يتابع عليه','3627','1621','0');
INSERT INTO [t13041] VALUES (1745,'محمد بن مهاجر القرشي عن نافع. حدثني آدم قال: سمعت البخاري قال: محمد بن مهاجر القرشي , عن نافع، لا يتابع على حديثه','3629','1621','0');
INSERT INTO [t13041] VALUES (1746,'محمد بن مروان السدي مولى الخطابيين، يقال له الكلبي. حدثنا الحسن بن عليب، حدثنا يحيى بن سليمان الجعفي قال: سمعت ابن نمير، يقول: محمد بن مروان الكلبي كذاب , وما سمعته وقع، في أحد غيره. حدثنا محمد، حدثنا عباس قال: سمعت يحيى قال: السدي الصغير محمد','3631','1621','0');
INSERT INTO [t13041] VALUES (1747,'محمد بن ميمون أبو النضر الزعفراني المفلوج حدثني آدم قال: سمعت البخاري قال: محمد بن ميمون أبو النضر الزعفراني المفلوج منكر الحديث , كان ببغداد','3633','1621','0');
INSERT INTO [t13041] VALUES (1748,'محمد بن موسى الجريري عن جويرية ولا يتابع عليه','3635','1621','0');
INSERT INTO [t13041] VALUES (1749,'محمد بن موسى بن مسكين أبو غزية القاضي الأنصاري المدني حدثني آدم قال: سمعت البخاري قال: محمد بن موسى بن مسكين أبو غزية المديني قاضي عنده مناكير','3637','1621','0');
INSERT INTO [t13041] VALUES (1750,'محمد بن مصعب القرقساني كان ببغداد. حدثنا عبد الله بن أحمد قال: سألت يحيى بن معين , عن محمد بن مصعب القرقساني، فقال: ليس بشيء، وقال: كان لي رفيق , وكان غزا كثيرا. فحدثنا يوما عن أبي الأشهب، عن أبي رجاء، عن عمران بن حصين، أنه كره بيع السلاح في','3639','1621','0');
INSERT INTO [t13041] VALUES (1751,'محمد بن مسلمة الأنصاري عن أبي سعيد، وأبي هريرة في ساعة الجمعة. حدثني آدم بن موسى قال: سمعت البخاري قال: محمد بن مسلمة الأنصاري , عن أبي سعيد، وأبي هريرة في ساعة الجمعة لا يتابع عليه','3641','1621','0');
INSERT INTO [t13041] VALUES (1752,'محمد بن ميسر الصغاني أبو سعد خراساني حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: أبو سعد الصغاني كان جهميا , وكان مكفوفا , وليس هو بشيء , شيطان من الشياطين. حدثني آدم قال: سمعت البخاري قال: محمد بن ميسر أبو سعد فيه اضطراب','3643','1621','0');
INSERT INTO [t13041] VALUES (1753,'محمد بن مجيب الصائغ حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: محمد بن مجيب كان جار عباد بن العوام , وكان كذابا عدوا لله عز وجل','3645','1621','0');
INSERT INTO [t13041] VALUES (1754,'محمد بن أبي حفصة وهو محمد بن ميسرة. حدثنا محمد بن عيسى، حدثنا صالح، حدثنا علي، قلت ليحيى بن معين: حملت عن محمد بن أبي حفصة؟ قال: نعم , كتب لي حديثه كله , ثم رميت به بعد ذاك , قال: هو نحو صالح بن أبي الأخضر. حدثنا محمد، حدثنا صالح، حدثنا علي،','3647','1621','0');
INSERT INTO [t13041] VALUES (1755,'محمد بن مهران','3648','1621','0');
INSERT INTO [t13041] VALUES (1756,'محمد بن محصن الحراني الغالب على حديثه الوهم في النكارة','3650','1621','0');
INSERT INTO [t13041] VALUES (1757,'محمد المحرم عن عطاء، والحسن، فرق البخاري بينه وبين محمد بن عبد الله بن عبيد بن عمير , وقال ابن مهدي: هما واحد. حدثني محمد بن عيسى، حدثنا العباس بن محمد قال: سمعت يحيى قال: محمد المحرم ليس بشيء','3652','1621','0');
INSERT INTO [t13041] VALUES (1758,'محمد بن المعلى الرازي الأزدي عن محمد بن إسحاق وغيره. حدثنا محمد بن سعيد بن بلج قال: سئل أبو عبد الله , يعني عبد الرحمن بن الحكم بن بشير بن سلمان , عن محمد بن المعلى، فقال: لم يكن صاحب حديث , وكان رجلا , قال: وكان في كتابه سعيد بن أبي عروبة , عن أبي','3654','1621','0');
INSERT INTO [t13041] VALUES (1759,'محمد بن معاوية النيسابوري حدثنا عبد الله بن محمد بن سعدويه المروزي، حدثنا إبراهيم بن يعقوب قال: سمعت أحمد بن حنبل، وسئل عن محمد بن معاوية أبي علي النيسابوري، فقال: هو كذاب. حدثني آدم قال: سمعت البخاري قال: محمد بن معاوية أبو علي النيسابوري سكن','3656','1621','0');
INSERT INTO [t13041] VALUES (1760,'محمد بن مصفى الحمصي حدثنا عبد الله بن أحمد قال: سألت أبي عن حديث، رواه محمد بن مصفى عن الوليد، فأنكره أبي جدا , وقال: ليس يروي إلا عن الحسن','3657','1621','0');
INSERT INTO [t13041] VALUES (1761,'محمد بن معاذ بصري في حديثه وهم','3659','1621','0');
INSERT INTO [t13041] VALUES (1762,'محمد بن النعمان عن يحيى بن العلاء مجهول , ويحيى متروك الحديث , ولم يأت بالحديث غيره','3662','1621','0');
INSERT INTO [t13041] VALUES (1763,'محمد بن يزيد بن صيفي بن صهيب حدثني آدم قال: سمعت البخاري قال: محمد بن يزيد بن صيفي مختلف في إسناده','3664','1621','0');
INSERT INTO [t13041] VALUES (1764,'محمد بن يزيد بن أبي زياد حدثني آدم قال: سمعت البخاري قال: محمد بن يزيد بن أبي زياد , روى عنه، إسماعيل بن رافع حديث الصور مرسلا , ولم يصح','3666','1621','0');
INSERT INTO [t13041] VALUES (1765,'محمد بن يوسف المسمعي بصري إسناده مجهول , ولا يتابع عليه من جهة , ولا يعرف إلا به','3668','1621','0');
INSERT INTO [t13041] VALUES (1766,'محمد بن يحيى الحجري عن عبد الله بن الأجلح، عن أبيه، ولا يتابع عليه','3670','1621','0');
INSERT INTO [t13041] VALUES (1767,'محمد بن يحيى بن يسار مديني مجهول بالنقل وحسين بن صدقة نحوا منه , وحديثه غير محفوظ','3673','1621','0');
INSERT INTO [t13041] VALUES (1768,'محمد بن يعلى بن زنبور السلمي حدثني آدم قال: سمعت البخاري قال: محمد بن يعلى بن زنبور السلمي يقال: ذاهب الحديث','3675','1621','0');
INSERT INTO [t13041] VALUES (1769,'مسلم بن خالد الزنجي أبو خالد عن ابن جريج، وهشام بن عروة. حدثنا محمد بن عثمان العبسي قال: سمعت يحيى بن معين، وذكر مسلم بن خالد , فقال: كان ضعيفا. حدثنا عبد الله قال: قال أبي: مسلم بن الزنجي كذا وكذا. حدثني آدم قال: سمعت البخاري، يقول: مسلم بن','3677','1621','0');
INSERT INTO [t13041] VALUES (1770,'مسلم بن عبد الله عن نافع، مجهول بالنقل , حديثه غير محفوظ','3685','1621','0');
INSERT INTO [t13041] VALUES (1771,'مسلم بن عمر أبو عازب عن النعمان بن بشير. حدثني آدم قال: سمعت البخاري قال: مسلم بن عمر أبو عازب , عن النعمان بن بشير، روى عنه، جابر الجعفي , ولا يتابع عليه','3687','1621','0');
INSERT INTO [t13041] VALUES (1772,'مسلم بن كيسان أبو عبد الله الضبي الملائي الأعور حدثنا عبد الله بن أحمد، حدثني أبي، حدثني وكيع، حدثني أبي، عن رجل، عن سعيد بن جبير، عن ابن عباس، قال أبي: هذا مسلم الأعور كان وكيع لا يسميه على عمد. حدثنا محمد بن عيسى، حدثنا صالح بن أحمد، حدثنا','3689','1621','0');
INSERT INTO [t13041] VALUES (1773,'موسى بن أيوب الغافقي حدثنا محمد بن عثمان قال: سمعت يحيى، سأل عن موسى بن أيوب الغافقي، فقال: تنكر عليه ما روى عن عمه، مما رفعه','3690','1621','0');
INSERT INTO [t13041] VALUES (1774,'موسى بن جعفر الأنصاري مجهول بالنقل , لا يتابع على حديثه , ولا يصح إسناده','3692','1621','0');
INSERT INTO [t13041] VALUES (1775,'موسى بن جعفر الجعفري في حديثه نظر','3694','1621','0');
INSERT INTO [t13041] VALUES (1776,'موسى بن جعفر بن محمد بن علي بن حسين عن أبيه، حديثه غير محفوظ , والحمل فيه على أبي الصلت الهروي','3696','1621','0');
INSERT INTO [t13041] VALUES (1777,'موسى بن دينار مكي حدثنا محمد بن عيسى، حدثنا عمرو بن علي قال: سمعت يحيى، يقول: كنا عند شيخ من أهل مكة أنا وحفص بن غياث , وأبو شيخ جارية بن هرم , فكتب عنه فجعل حفص يضع الحديث , فيقول حدثتك عائشة بنت طلحة , عن عائشة بكذا وكذا , فيقول حدثتني عائشة بنت','3698','1621','0');
INSERT INTO [t13041] VALUES (1778,'موسى بن دهقان حدثنا محمد بن عيسى، حدثنا عباس بن محمد قال: سمعت يحيى، يقول موسى بن دهقان ليس بشيء. حدثنا محمد بن أحمد، حدثنا معاوية بن صالح قال: سمعت يحيى قال: موسى بن دهقان ضعيف الحديث. حدثنا محمد، حدثنا صالح، حدثنا علي قال: سمعت يحيى بن سعيد','3699','1621','0');
INSERT INTO [t13041] VALUES (1779,'موسى بن طريف حدثنا يحيى بن عثمان، حدثنا نعيم بن حماد، حدثنا أبو بكر بن عياش قال: قد رأيت موسى بن طريف , وصليت على جنازته , وكان يقول في تلك الأحاديث التي يرم بها عن علي: إني لأسخر بهم. حدثنا محمد بن جميل، حدثنا الحسن، حدثنا نعيم، حدثنا أبو بكر بن','3700','1621','0');
INSERT INTO [t13041] VALUES (1780,'موسى بن عبد الله بن حسن عن أبيه. حدثني آدم قال: سمعت البخاري قال: موسى بن عبد الله بن الحسن , عن أبيه، فقلت لسالم " في أدبار النساء " , فقال: كذب العبد أو أخطأ، قال البخاري: فيه نظر. وهذا الحديث , حدثناه محمد بن بشير بن الهيثم، حدثنا أحمد بن','3702','1621','0');
INSERT INTO [t13041] VALUES (1781,'موسى بن عمير عن الحكم، منكر الحديث. حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: موسى بن عمير , عن الحكم، ليس بشيء','3703','1621','0');
INSERT INTO [t13041] VALUES (1782,'موسى بن عبيدة بن نشيط أبو عبد العزيز الربذي حدثنا محمد بن عيسى، حدثنا عمرو بن علي قال: ذكرت ليحيى حديث موسى بن عبيدة , عن عمر بن الحكم، سمع سعدا، يحدث عن النبي صلى الله عليه وسلم , في: " صلاة في مسجدي " , فأنكر أن يكون عمر سمع سعدا , ولم يرض موسى بن','3705','1621','0');
INSERT INTO [t13041] VALUES (1783,'موسى بن أبي شيبة حدث عنه معمر. حدثنا عبد الله بن أحمد قال: سألت أبي عن موسى بن أبي شيبة، فقال: روى عنه معمر أحاديث مناكير','3709','1621','0');
INSERT INTO [t13041] VALUES (1784,'موسى بن مطير كوفي حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: موسى بن مطير كذاب. حدثنا محمد بن سعيد بن بلج الرازي قال: سئل أبو عبد الله , يعني عبد الرحمن بن الحكم بن بشير بن سلمان , عن موسى بن مطير، فقال: ضعيف ترك الناس حديثه','3711','1621','0');
INSERT INTO [t13041] VALUES (1785,'موسى بن نافع أبو شهاب الكبير كوفي حدثنا محمد بن عيسى، حدثنا صالح بن أحمد، حدثنا علي قال: سألت يحيى , عن موسى بن نافع، فقال: أفسدوه علينا','3713','1621','0');
INSERT INTO [t13041] VALUES (1786,'موسى بن قيس الحضرمي كوفي يلقب عصفور الجنة , من الغلاة في الرفض','3714','1621','0');
INSERT INTO [t13041] VALUES (1787,'موسى بن القاسم التغلبي كوفي حدثني آدم قال: سمعت البخاري قال: موسى بن القاسم التغلبي لا يتابع عليه','3720','1621','0');
INSERT INTO [t13041] VALUES (1788,'موسى بن إبراهيم المروزي منكر الحديث لا يتابع على حديثه','3722','1621','0');
INSERT INTO [t13041] VALUES (1789,'موسى بن أبي كثير أبو الصباح كوفي حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: حدثنا أبو نعيم قال: حدثنا أبو عبد الله الشيباني قال: كنا جلوسا مع أبي جعفر , فاختصم هو وموسى بن أبي كثير طويلا , قال: أبو جعفر: هل رأيت مؤمنا ضالا؟ فقال رجل من','3724','1621','0');
INSERT INTO [t13041] VALUES (1790,'موسى بن مسعود أبو حذيفة بصري حدثنا عبد الله بن أحمد قال: سمعت أبي، وذكر قبيصة , وأبا حذيفة , فقال: قبيصة أثبت منه حديثا، في حديث سفيان أبو حذيفة شبه لا شيء , وقد كتبت عنهما جميعا. حدثني عبد الله بن محمد قال: حدثنا إبراهيم بن يعقوب قال: سمعت أحمد','3725','1621','0');
INSERT INTO [t13041] VALUES (1791,'موسى بن محمد بن إبراهيم المديني الهذلي روى عنه الواقدي، لا يتابع','3726','1621','0');
INSERT INTO [t13041] VALUES (1792,'موسى بن محمد بن إبراهيم التيمي عن أبيه، عن أنس، مديني لا يتابع على حديثه , ولا يعرف إلا به. حدثني آدم قال: سمعت البخاري قال: موسى بن محمد بن إبراهيم , عن أبيه، عن أنس، منكر الحديث','3728','1621','0');
INSERT INTO [t13041] VALUES (1793,'موسى بن محمد بن عطاء بن الجملي البلقاوي يحدث عن الثقات، بالبواطيل في الموضوعات','3730','1621','0');
INSERT INTO [t13041] VALUES (1794,'موسى بن هلال سكن الكوفة , عن عبيد الله بن عمر، ولا يصح حديثه , ولا يتابع عليه','3733','1621','0');
INSERT INTO [t13041] VALUES (1795,'موسى بن يسار سيار الأسواري كان يرى القدر , بصري. حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت يحيى، حدث عن موسى الأسواري، شيئا قط , وقد كان حدث عنه، فيما بلغني , ثم تركه بآخرة. حدثنا محمد بن موسى البربري قال: حدثنا المفضل بن غسان','3735','1621','0');
INSERT INTO [t13041] VALUES (1796,'مالك بن مالك , ضيف مسروق كوفي حدثني آدم قال: سمعت البخاري قال: مالك بن مالك , ضيف مسروق لم يعرف إلا بهذا , ولم يتابع عليه','3736','1621','0');
INSERT INTO [t13041] VALUES (1797,'مالك بن سليمان النهشلي بصري عن ثابت، وغيره، يروي مناكير','3738','1621','0');
INSERT INTO [t13041] VALUES (1798,'مالك بن سليمان الهروي في حديثه نظر','3740','1621','0');
INSERT INTO [t13041] VALUES (1799,'مالك بن يحيى بن عمرو بن مالك النكري حدثني آدم قال: سمعت البخاري قال: مالك بن يحيى بن عمرو بن مالك النكري في حديثه نظر','3743','1621','0');
INSERT INTO [t13041] VALUES (1800,'مالك بن أبي المؤمل شيخ من أهل المدينة. حدثني آدم قال: سمعت البخاري قال: مالك بن أبي المؤمل شيخ من أهل المدينة روى عنه، عبيد الله بن زحر , ولا يتابع عليه','3745','1621','0');
INSERT INTO [t13041] VALUES (1801,'مغيرة بن أبي الحر الكندي حدثني آدم قال: سمعت البخاري قال: مغيرة بن أبي الحر الكندي كوفي يخالف في حديثه الكوفيين','3746','1621','0');
INSERT INTO [t13041] VALUES (1802,'مغيرة بن زياد الموصلي عن عطاء، ونافع','3748','1621','0');
INSERT INTO [t13041] VALUES (1803,'مغيرة بن موسى البصري حدثني آدم قال: سمعت البخاري قال: مغيرة بن موسى البصري , عن ابن أبي عروبة، وشعبة، منكر الحديث','3751','1621','0');
INSERT INTO [t13041] VALUES (1804,'مغيرة بن الأشعث كان أميرا على واسط , ولا يتابع على حديثه','3753','1621','0');
INSERT INTO [t13041] VALUES (1805,'المغيرة بن سعيد من كبار الرافضة , وممن يؤمن بالرجعة','3755','1621','0');
INSERT INTO [t13041] VALUES (1806,'مغيرة بن جميل كوفي عن سليمان بن علي منكر الحديث','3760','1621','0');
INSERT INTO [t13041] VALUES (1807,'مغيرة بن سقلاب الجزري حدثنا أحمد بن علي الأبار قال: سألت علي بن ميمون الرقي عن المغيرة بن سقلاب، فقال: كان يسوى بعرة','3762','1621','0');
INSERT INTO [t13041] VALUES (1808,'معاوية بن يحيى الصدفي حدثني آدم بن موسى قال: سمعت البخاري، يقول: روى معقل بن زياد، عن الصدفي، أحاديث مستقيمة , كأنها من كتاب , وروى عيسى بن يونس، وإسحاق بن سليمان، أحاديث مناكير , كأنها من حفظه. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال:','3764','1621','0');
INSERT INTO [t13041] VALUES (1809,'معاوية بن صالح الأندلسي حدثنا محمد بن عيسى قال: حدثنا العباس قال: سمعت ابن معين قال: كان يحيى بن سعيد لا يرضى معاوية بن صالح. حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي، سألت يحيى بن سعيد , عن معاوية بن صالح قال: ما كنا نأخذ عنه ذلك الزمان','3765','1621','0');
INSERT INTO [t13041] VALUES (1810,'معاوية بن عطاء بصري كان يرى القدر , عن الثوري، وغيره، في حديثه مناكير , وما لا يتابع على أكثره','3766','1621','0');
INSERT INTO [t13041] VALUES (1811,'ميمون أبو عبد الله مولى عبد الرحمن بن سمرة , رضي الله عنه , بصري. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: قال شعبة: ميمون أبو عبد الله فسل , قلت لأبي: من دون شعبة؟ قال: يحيى , قال أبي: وهو الذي حدث عنه عوف , وفي موضع آخر قلت لأبي: سمعته من','3770','1621','0');
INSERT INTO [t13041] VALUES (1812,'ميمون بن موسى المرائي بصري حدثنا عبد الله بن أحمد قال: قلت لأبي: ميمون بن موسى المرائي , قال: ما أرى به بأس , وكان دلس , ولكن لا يقول: حدثنا الحسن. سمعت أبي يقول: سمعت يحيى القطان، يقول: أتيت ميمونا المرائي , فما صحح لي إلا هذه الأحاديث التي','3772','1621','0');
INSERT INTO [t13041] VALUES (1813,'ميمون بن عطاء بن زيد عن أبي إسحاق السبيعي، منكر الحديث','3774','1621','0');
INSERT INTO [t13041] VALUES (1814,'ميمون أبو حمزة القصاب كوفي حدثنا محمد بن زكريا قال: حدثنا محمد بن المثنى قال: ما سمعت يحيى، ولا عبد الرحمن حدثا عن سفيان، عن أبي حمزة الأعور، شيئا قط. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: أبو حمزة ميمون صاحب إبراهيم متروك الحديث , وقال في','3776','1621','0');
INSERT INTO [t13041] VALUES (1815,'ميمون بن جابر الرفاء أبو خلف ولا يصح حديثه','3778','1621','0');
INSERT INTO [t13041] VALUES (1816,'ميمون بن سياه بصري حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: ميمون بن سياه , ويزيد الرقاشي , وزياد النميري كلهم ضعفى','3780','1621','0');
INSERT INTO [t13041] VALUES (1817,'منصور بن وردان الكوفي العطار حدثني آدم بن موسى قال: سمعت البخاري قال: منصور بن وردان الكوفي العطار الأسدي، سمع علي بن عبد الأعلى البخاري، لا يعرف له إسناد','3782','1621','0');
INSERT INTO [t13041] VALUES (1818,'منصور بن دينار الضبي ويقال: المنقري , بصري. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: منصور بن دينار ضعيف الحديث. حدثني آدم بن موسى قال: سمعت البخاري قال: منصور بن دينار الضبي , عن نافع، وحماد، في حديثه نظر','3784','1621','0');
INSERT INTO [t13041] VALUES (1819,'منصور بن إسماعيل الحراني عن ابن جريج، ولا يتابع عليه','3786','1621','0');
INSERT INTO [t13041] VALUES (1820,'منصور بن سقير الجزري الحراني عن موسى بن أعين، في حديثه بعض الوهم','3788','1621','0');
INSERT INTO [t13041] VALUES (1821,'منصور بن عمار القاص لا يقيم الحديث , وكان فيه تجهم من مذهب جهم','3792','1621','0');
INSERT INTO [t13041] VALUES (1822,'مصعب بن إبراهيم جزري في حديثه نظر','3795','1621','0');
INSERT INTO [t13041] VALUES (1823,'مصعب بن سلام التميمي كوفي حدثنا عبد الله بن أحمد، عن أبيه، قال: مصعب بن سلام انقلبت عليه أحاديث يوسف بن صهيب , جعلها عن الزبرقان السراج، وقدم ابن أبي شيبة، مرة , فجعل يذاكر عنه أحاديث , عن شعبة، هي أحاديث الحسن بن عمارة , انقلبت عليه أيضا','3797','1621','0');
INSERT INTO [t13041] VALUES (1824,'مصعب بن ثابت بن عبد الله بن الزبير حدثنا عبد الله بن أحمد بن حنبل قال: سألت أبي عن مصعب بن ثابت، فقال: أراه ضعيف الحديث. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى بن معين قال: مصعب بن ثابت بن عبد الله بن الزبير مدني ليس بشيء','3801','1621','0');
INSERT INTO [t13041] VALUES (1825,'مصعب بن شيبة الحجبي حدثنا إبراهيم بن عبد الوهاب قال: حدثنا أحمد بن محمد بن هانئ قال: ذكرت لأبي عبد الله " الوضوء من الحجامة " , فقال: ذاك حديث منكر , رواه مصعب بن شيبة أحاديثه مناكير , منها هذا الحديث وعشرة من الفطرة , وخرج رسول الله صلى الله عليه','3803','1621','0');
INSERT INTO [t13041] VALUES (1826,'مصعب بن ماهان حدثني الخضر بن داود قال: حدثنا أحمد بن محمد قال: سمعت أبا عبد الله، وذكر مصعب بن ماهان صاحب الثوري، فأثنى عليه خيرا , وقال جاءني إنسان مرة بكتاب عنه , فإذا كثير الخطأ , فإذا أخال من الذي كتب عنه , فلما نظرت بعد في حديثه , فإذا أحاديثه','3807','1621','0');
INSERT INTO [t13041] VALUES (1827,'مصعب النوفلي عن أبي ذئب مجهول بالنقل , حديثه غير محفوظ , ولا يتابع عليه ولا يعرف إلا به','3809','1621','0');
INSERT INTO [t13041] VALUES (1828,'منذر بن زياد الطائي عن زيد بن أسلم منكر الحديث','3811','1621','0');
INSERT INTO [t13041] VALUES (1829,'أبو نضرة منذر بن مالك بن قطيعة حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: سمعت يحيى بن سعيد يقول: جاء التيمي يوما إلى ابن عون , فقال التيمي: حدثنا أبو نضرة، فقال ابن عون: قد رأيت أبا نضرة؟ فقال له التيمي: فإن كنت رأيت أبا نضرة فمه , فسكت ابن','3814','1621','0');
INSERT INTO [t13041] VALUES (1830,'منذر أبو حسان عن سمرة','3815','1621','0');
INSERT INTO [t13041] VALUES (1831,'معاذ بن محمد الهذلي عن يونس بن عبيد، في حديثه نظر , ولا يتابع على رفعه','3817','1621','0');
INSERT INTO [t13041] VALUES (1832,'معاذ بن ياسين الزيات عن الأبرد بن الأشرس، رجل مجهول وحديثه غير محفوظ','3820','1621','0');
INSERT INTO [t13041] VALUES (1833,'معاذ بن محمد الأنصاري في حديثه وهم يحمل حديث رجل على غيره','3823','1621','0');
INSERT INTO [t13041] VALUES (1834,'مروان بن عبد الله بن صفوان مجهول بنقل الحديث , هو وأبوه , وحديثه غير محفوظ , ولا يعرف إلا به','3825','1621','0');
INSERT INTO [t13041] VALUES (1835,'مروان بن معاوية الفزاري حدثنا محمد بن عثمان بن أبي شيبة قال: سئل يحيى بن معين وأنا أسمع، كيف كان مروان بن معاوية في الحديث؟ فقال: كان ثقة فيما يروي عمن يعرف , وذاك أنه كان يروي عن أقوام لا يدري من هم ويغير أسماءهم , وكان يحدث عن محمد بن سعيد المصلوب','3827','1621','0');
INSERT INTO [t13041] VALUES (1836,'مروان أبو سلمة بصري عن شهر بن حوشب. حدثني آدم بن موسى قال: سمعت البخاري قال: مروان أبو سلمة بصري , عن شهر بن حوشب، روى عنه عبد الصمد، منكر الحديث','3828','1621','0');
INSERT INTO [t13041] VALUES (1837,'مروان بن سالم الجزري عن عبد الملك بن أبي سليمان، والأعمش، وغيرهما، أحاديثه مناكير , لا يتابع عليها إلا من طريق يقاربه','3830','1621','0');
INSERT INTO [t13041] VALUES (1838,'مروان بن محمد الطاطري حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى، يقول: كان مروان الطاطري لا بأس به , وكان مرجئا','3834','1621','0');
INSERT INTO [t13041] VALUES (1839,'معمر بن عبد الله التميمي عن سعيد بن أبي عروبة، منكر الحديث , ولا يعرف بالنقل , حديثه غير محفوظ','3835','1621','0');
INSERT INTO [t13041] VALUES (1840,'معمر بن زائدة عن الأعمش ولا يتابع على حديثه','3837','1621','0');
INSERT INTO [t13041] VALUES (1841,'معمر بن عبد الله الأنصاري عن شعبة، لا يتابع على رفع حديثه','3841','1621','0');
INSERT INTO [t13041] VALUES (1842,'معمر بن بكار السعدي في حديثه وهم , ولا يتابع على أكثره','3844','1621','0');
INSERT INTO [t13041] VALUES (1843,'مهاجر بن مخلد أبو مخلد مولى أبي بكرة بصري. حدثنا محمد بن زكريا البلخي قال: حدثنا محمد بن المثنى قال: حدثني أبو هشام المخزومي قال: كان وهيب بن خالد يعيب المهاجر أبا مخلد ويقول: لا يحفظ , وهو الذي روى عنه عوف , وعبد الوهاب الثقفي , ووهيب','3847','1621','0');
INSERT INTO [t13041] VALUES (1844,'مهاجر بن المنيب مجهول بالنقل , لا يتابع على حديثه , ولا يعرف إلا به','3849','1621','0');
INSERT INTO [t13041] VALUES (1845,'مرزوق بن أبي الهذيل شامي عن الزهري. حدثني آدم بن موسى قال: سمعت البخاري قال: مرزوق بن أبي الهذيل شامي , سمع الزهري، سمع منه الوليد بن مسلم، يعرف وينكر','3851','1621','0');
INSERT INTO [t13041] VALUES (1846,'مرزوق بن ميمون الناجي عن حميد بن مهران، في حديثه نظر','3853','1621','0');
INSERT INTO [t13041] VALUES (1847,'مختار بن نافع التمار عن أبي مطر. حدثني آدم بن موسى قال: سمعت البخاري قال: مختار بن نافع التمار , عن أبي مطر، منكر الحديث','3856','1621','0');
INSERT INTO [t13041] VALUES (1848,'مسلمة بن علي الخشني حدثنا أحمد بن علي الأبار قال: حدثنا أبو القاسم بن عبد الله بن عبد الحكم قال: حدثنا أبي، قال: كنا في مجلس الليث بن سعد , ونحن نقابل بكتاب البيوع لمالك بن أنس , ومسلمة بن علي حاضر , فقال: ليس عندكم في هذا شيء إلا عن مالك؟ فقلت له','3858','1621','0');
INSERT INTO [t13041] VALUES (1849,'مسلمة بن علقمة المازني أبو محمد حدثنا عبد الله بن أحمد بن حنبل قال: سمعت أبي يقول: بلغني عن يحيى بن سعيد، أنه لم يكن بالراضي , عن مسلمة بن علقمة. حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: ما سمعت عبد الرحمن بن مهدي، يحدث عن مسلمة بن علقمة','3861','1621','0');
INSERT INTO [t13041] VALUES (1850,'معلى بن عرفان عن أبي وائل، حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى، يقول: المعلى بن عرفان ليس بشيء. حدثني آدم بن موسى قال: سمعت البخاري قال: معلى بن عرفان منكر الحديث','3863','1621','0');
INSERT INTO [t13041] VALUES (1851,'معلى بن هلال الطحان كوفي حدثنا محمد بن عمرو بن خالد قال: حدثني أبي، قال: سمعت زهيرا، يقول لعيسى بن يونس: ينبغي للرجل أن يدع رواية غريب الحديث , فإني أعرف رجلا كان يصلي في يومه مائة ركعة , ما أفسده عند الناس إلا رواية غريب الحديث , فظنناه يعني معلى','3865','1621','0');
INSERT INTO [t13041] VALUES (1852,'معلى بن عبد الرحمن الواسطي حدثني أبو أسامة البصري قال: سمعت أبا داود السجستاني يقول: سمعت يحيى بن معين، وسئل عن المعلى بن عبد الرحمن، فقال: أحسن أحواله عندي أنه قيل له عند موته: ألا تستغفر الله؟ فقال: ألا أرجو أن يغفر لي وقد وضعت في فضل علي بن','3866','1621','0');
INSERT INTO [t13041] VALUES (1853,'معلى بن منصور حدثنا الخضر بن داود قال: حدثنا أحمد بن محمد بن هانئ قال: قلت لأبي عبد الله: معلى بن منصور كتبت عنه شيئا؟ فقال: لا , ولا حرف','3867','1621','0');
INSERT INTO [t13041] VALUES (1854,'معلى بن ميمون بصري منكر الحديث لا يتابع على حديثه ولا يعرف إلا به','3868','1621','0');
INSERT INTO [t13041] VALUES (1855,'مطرف بن مازن الصنعاني حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى بن معين يقول: قال لي هشام بن يوسف: جاءني مطرف بن مازن , فقال: اعطني حديث ابن جريج , ومعمر حتى أسمعه منك , فأعطيته فكتبها , ثم جعل يحدث بها عن معمر نفسه , وعن ابن جريج','3870','1621','0');
INSERT INTO [t13041] VALUES (1856,'مطرف بن معقل عن ثابت، منكر الحديث , لا يتابع على حديثه , ولا يعرف إلا به','3873','1621','0');
INSERT INTO [t13041] VALUES (1857,'معبد الجهني كان أول من تكلم في القدر بالبصرة. حدثنا محمد بن إسماعيل قال: أخبرنا المقرئ قال: حدثنا كهمس، عن عبد الله بن بريدة، عن يحيى بن معمر قال: كان أول من تكلم بالقدر بالبصرة معبد الجهني. حدثنا محمد بن أيوب قال: حدثنا عبد الرحمن بن المبارك قال','3875','1621','0');
INSERT INTO [t13041] VALUES (1858,'مطر بن طهمان أبو رجاء الخراساني حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: سألت يحيى عن حديث مطر , عن الحسن، أن رسول الله صلى الله عليه وسلم , قال: لا أعافي رجلا قتل بعد أخذه الدية , فقال: حدثنا موسى بن يسار , عن الحسن , قلت: أريده من حديث مطر','3876','1621','0');
INSERT INTO [t13041] VALUES (1859,'مطر بن ميمون المحاربي حدثني آدم بن موسى قال: سمعت البخاري قال: مطر بن ميمون المحاربي كوفي منكر الحديث','3877','1621','0');
INSERT INTO [t13041] VALUES (1860,'معروف بن خربوذ المكي لا يتابع على حديثه ولا يعرف إلا به. حدثنا عبد الله بن أحمد قال: قال لي: معروف بن خربوذ: ما أدري كيف حديثه','3879','1621','0');
INSERT INTO [t13041] VALUES (1861,'معقل بن عبيد الله الجزري حدثنا عبد الله بن أحمد قال: سألت أبي عن معقل بن عبيد الله، فقال: صالح الحديث. حدثنا محمد بن أحمد قال: حدثنا معاوية قال: سمعت يحيى قال: معقل بن عبيد الله ضعيف','3881','1621','0');
INSERT INTO [t13041] VALUES (1862,'مسكين بن بكير الحذاء حدثنا الخضر بن داود قال: حدثنا أحمد بن محمد قال: سمعت أبا عبد الله وذكر أبا جعفر النفيلي، فأثنى عليه خيرا , وقال: كان يحيى معي إلى مسكين بن بكير وكأنه حسن أمره , قلت لأبي عبد الله: نظرت في حديث مسكين , عن شعبة فإذا فيها خطأ ,','3882','1621','0');
INSERT INTO [t13041] VALUES (1863,'مشرح بن هاعان حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: حدثنا موسى بن داود قال: بلغني أن مشرح بن هاعان، كان ممن جاء مع الحجاج , ونصب المنجنيق على الكعبة','3884','1621','0');
INSERT INTO [t13041] VALUES (1864,'مخرمة بن بكير بن عبد الله بن الأشج حدثنا عبد الله بن أحمد قال: حدثني أبي، قال: حدثنا حماد بن خالد الخياط قال: أخرج إلي مخرمة بن بكير كتبا , فقال: هذه كتب إني لم أسمع منها شيئا. حدثنا حجاج بن عمران قال: حدثنا أحمد بن سعيد قال: حدثنا عمر قال: سمعت','3885','1621','0');
INSERT INTO [t13041] VALUES (1865,'مبارك بن سحيم مولى عبد العزيز بن صهيب. حدثنا عبد الله قال: عرضت على أبي أحاديث مبارك أبي سحيم الذي حدثنا عنه، سويد , فأنكرها , ولم يحمده , وأظنه قال: ليس هو ثقة , فأنكرها إنكارا شديدا , وأظنه قال: اضربوا عليها. وحدثني آدم قال: سمعت البخاري قال:','3886','1621','0');
INSERT INTO [t13041] VALUES (1866,'مبارك بن فضالة بصري مولى عمر بن الخطاب رضي الله عنه. حدثنا عبد الله بن أحمد بن حنبل قال: حدثنا أحمد بن إبراهيم الدورقي قال: حدثنا حجاج بن محمد قال: سألت شعبة عن المبارك بن فضالة، وربيع، فقال: مبارك أحب إلي منه. حدثنا محمد بن عيسى قال: حدثنا عمرو','3890','1621','0');
INSERT INTO [t13041] VALUES (1867,'مبارك بن مجاهد حدثني آدم بن موسى قال: سمعت البخاري قال: مبارك بن مجاهد أبو الأزهر المروزي , قال قتيبة بن سعيد: كان قدريا , وضعفه','3891','1621','0');
INSERT INTO [t13041] VALUES (1868,'مبارك بن سعيد بن مسروق حدثنا عبد الله بن أحمد قال: سمعت أبي قال: رأيت مبارك بن سعيد بن مسروق أخو الثوري من ذاك الجانب يعني ببغداد , ولم أكتب عنه شيئا','3893','1621','0');
INSERT INTO [t13041] VALUES (1869,'مهدي بن هلال البصري كان يرى القدر. حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت عبد الرحمن، في قصة مهدي بن هلال , قلت له: أما أتيته أنت وبشر فكلمتماه في حديث مالك في التسليمة , قال عبد الرحمن: كتبت إلى إبراهيم بن حبيب المدني , وكان','3896','1621','0');
INSERT INTO [t13041] VALUES (1870,'مهران بن أبي عمر الرازي حدثني آدم بن موسى قال: سمعت البخاري قال: مهران بن أبي عمر الرازي في حديثه اضطراب. حدثنا عبد الله بن أحمد بن عبد السلام قال: حدثنا محمد بن إسماعيل قال: سمعت إبراهيم بن موسى يضعف مهران','3900','1621','0');
INSERT INTO [t13041] VALUES (1871,'مخارق بن ميسرة عن أبيه، إسناده مجهول غير محفوظ','3902','1621','0');
INSERT INTO [t13041] VALUES (1872,'محرر بن هارون الهديري حدثني آدم قال: سمعت البخاري قال: محرر بن هارون بن عبد الله بن محرز الهديري , عن الأعرج، قال البخاري: منكر الحديث , روى عنه، أحمد بن أبي بكر الزهري','3904','1621','0');
INSERT INTO [t13041] VALUES (1873,'مخلد بن خفاف بن إيماء بن رحضة الغفاري حدثني آدم بن موسى قال: سمعت البخاري قال: مخلد بن خفاف بن إيماء الغفاري فيه نظر','3906','1621','0');
INSERT INTO [t13041] VALUES (1874,'مخلد بن الضحاك والد أبي عاصم، لا يتابع على حديثه','3908','1621','0');
INSERT INTO [t13041] VALUES (1875,'مخلد أبو الهذيل عن عبد الرحمن المديني، في إسناده نظر','3910','1621','0');
INSERT INTO [t13041] VALUES (1876,'مجالد بن سعيد بن عمير الهمداني كوفي حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: سمعت يحيى، يقول: لو شئت أن تجعلها لي مجالدا كلها عن الشعبي , عن مسروق , عن عبد الله فعلت. حدثنا أحمد بن علي الأبار قال: حدثنا محمد بن حرب الواسطي قال: حدثنا عاصم بن','3912','1621','0');
INSERT INTO [t13041] VALUES (1877,'مبشر السعيدي عن الزهري، لا يتابع عليه','3914','1621','0');
INSERT INTO [t13041] VALUES (1878,'مبشر بن عبيد روى عنه بقية بن الوليد. حدثنا عبد الله قال: سمعت أبي يقول: شيخ يقال له مبشر بن عبيد كان يكون بحمص: وأصله كوفي , روى عنه، بقية , وأبو المغيرة , أحاديثه أحاديث موضوعة , كذب , وسمعته مرة أخرى يقول: مبشر بن عبيد ليس بشيء , يضع الحديث.','3916','1621','0');
INSERT INTO [t13041] VALUES (1879,'مبشر بن الفضيل مجهول بالنقل، عن محمد بن سعيد بن أبي وقاص إسناده لا يصح','3919','1621','0');
INSERT INTO [t13041] VALUES (1880,'منهال بن عمرو حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: ترك شعبة المنهال بن عمرو على عمد، وسمعت أبي يقول: أبو بشر أحب إلي من المنهال بن عمرو , قلت له: أحب إليك من المنهال بن عمرو؟ قال: نعم شديدا , أبو بشر أوثق إلا أن المنهال أمتن. حدثنا محمد بن','3921','1621','0');
INSERT INTO [t13041] VALUES (1881,'منهال بن خليفة أبو قدامة العجلي حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى قال: منهال بن خليفة ضعيف. حدثني آدم بن موسى قال: سمعت البخاري قال: منهال بن خليفة أبو قدامة العجلي بصري فيه نظر','3922','1621','0');
INSERT INTO [t13041] VALUES (1882,'منهال بن بحر أبو سلمة العقيلي بصري في حديثه نظر','3925','1621','0');
INSERT INTO [t13041] VALUES (1883,'مقاتل بن سليمان الخراساني حدثنا ابن غليب الأردني قال: حدثنا يحيى بن سليمان الجعفي قال: سمعت وكيعا، يقول: مقاتل بن سليمان كذاب. حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: حدثنا محمد بن داود الحراني قال: سمعت عيسى بن يونس، وسئل عن مقاتل بن','3927','1621','0');
INSERT INTO [t13041] VALUES (1884,'مفضل بن صالح كوفي حدثني آدم قال: سمعت البخاري قال: مفضل بن صالح كوفي منكر الحديث , وكان نخاسا','3930','1621','0');
INSERT INTO [t13041] VALUES (1885,'مفضل بن فضالة بصري ليس بمشهور بالنقل. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: مفضل بن فضالة يحدث عنه جناح , ويونس بن محمد , ليس هو بذاك','3932','1621','0');
INSERT INTO [t13041] VALUES (1886,'مفضل بن صدقة أبو حماد الحنفي حدثنا أبو علي محمد بن عيسى , قال: حدثنا العباس قال: سمعت يحيى قال: أبو حماد الحنفي يروي عنه يحيى بن آدم وغيره , وليس بشيء','3935','1621','0');
INSERT INTO [t13041] VALUES (1887,'مسيب بن شريك أبو سعيد حدثني آدم بن موسى قال: سمعت البخاري: مسيب بن شريك أبو سعيد سكتوا عنه','3936','1621','0');
INSERT INTO [t13041] VALUES (1888,'مسور بن الصلت أبو الحسن المديني وقال ابن معين: كوفي. حدثني محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى، يقول: مسور بن الصلت كوفي , سمع منه، سعدويه , كان يحدث بأحاديث الشيعة , ضعيف. حدثني آدم بن موسى قال: سمعت البخاري قال: مسور بن الصلت ضعيف','3938','1621','0');
INSERT INTO [t13041] VALUES (1889,'مسعدة بن اليسع الباهلي حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: مسعدة بن اليسع ليس بشيء , خرقنا حديثه وتركنا حديثه منذ دهر','3940','1621','0');
INSERT INTO [t13041] VALUES (1890,'مسمع بن عاصم المسمعي بصري ولا يتابع على حديثه , وليس بمشهور بالنقل','3942','1621','0');
INSERT INTO [t13041] VALUES (1891,'مسمع بن محمد الأشعري عن ابن أبي ذئب، ولا يتابع أيضا , ولا يعرف بالنقل','3944','1621','0');
INSERT INTO [t13041] VALUES (1892,'مسروح أبو شهاب عن سفيان الثوري، لا يتابع على حديثه ولا يعرف إلا به','3946','1621','0');
INSERT INTO [t13041] VALUES (1893,'مثنى بن بكر العبدي العطار أبو حاتم بصري عن بهز بن حكيم، ولا يتابع على حديثه','3948','1621','0');
INSERT INTO [t13041] VALUES (1894,'المثنى بن الصباح عن عطاء، وعمرو بن شعيب. حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: كان يحيى وعبد الرحمن لا يحدثان عن المثنى بن الصباح. حدثنا محمد بن زكريا قال: حدثنا محمد بن المثنى قال: ما سمعت يحيى بن أبي عبد الرحمن، حدث عن سفيان، عن','3951','1621','0');
INSERT INTO [t13041] VALUES (1895,'مثنى بن دينار الجهضمي عن أنس، في حديثه نظر','3952','1621','0');
INSERT INTO [t13041] VALUES (1896,'مطير سمع ذا اليدين. حدثني آدم بن موسى قال: سمعت البخاري قال: مطير سمع ذا اليدين، ولم يثبت حديثه','3954','1621','0');
INSERT INTO [t13041] VALUES (1897,'مطير بن أبي خالد مولى طلحة بن عبيد الله. حدثني آدم قال: سمعت البخاري قال: مطير بن أبي خالد , مولى طلحة بن عبيد الله هو والد موسى بن مطير , ولا يصح حديثه','3956','1621','0');
INSERT INTO [t13041] VALUES (1898,'محل بن محرز الضبي حدثنا محمد بن زكريا قال: حدثنا الحسن بن علي بن شجاع قال: حدثنا علي بن عبد الله قال: سألت يحيى عن محل الضبي، فقال: كان وسطا , ولم يكن بذاك','3958','1621','0');
INSERT INTO [t13041] VALUES (1899,'مينا مولى عبد الرحمن بن عوف روى عنه، همام بن نافع أحاديث مناكير لا يتابع منها على شيء. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى قال: مينا مولى عبد الرحمن بن عوف ليس بثقة','3959','1621','0');
INSERT INTO [t13041] VALUES (1900,'منكدر بن محمد بن المنكدر التيمي حدثنا بشر بن موسى الأسدي قال: حدثنا الحميدي قال: حدثنا سفيان بن عيينة قال: قدم علينا المنكدر بن محمد بن المنكدر قال: فقلت: لو أتيته لعلي أستفيد منه شيئا عن أبيه فلما صرت إليه , قلت: أختبره , قال: قلت: كيف حديث أبيك','3961','1621','0');
INSERT INTO [t13041] VALUES (1901,'مجاعة بن الزبير الأسدي بصري','3962','1621','0');
INSERT INTO [t13041] VALUES (1902,'معارك بن عباد العيشي ويقال: ابن عبيد الله , عن عبد الله بن سعيد المقبري، ولا يصح حديثه','3964','1621','0');
INSERT INTO [t13041] VALUES (1903,'مسرور بن سعيد عن الأوزاعي، حديثه غير محفوظ , ولا يعرف إلا به','3966','1621','0');
INSERT INTO [t13041] VALUES (1904,'معان بن رفاعة السلامي حدثنا محمد بن عثمان قال: سمعت يحيى بن معين، وسئل عن معان بن رفاعة، فقال: كان ضعيفا','3968','1621','0');
INSERT INTO [t13041] VALUES (1905,'معان أبو صالح بصرى عن أبي حرة وغيره، حديثه غير محفوظ ولا يتابع عليه','3970','1621','0');
INSERT INTO [t13041] VALUES (1906,'مكي بن عبد الله الرعيني عن ابن عيينة، حديثه غير محفوظ , ولا يعرف إلا به','3972','1621','0');
INSERT INTO [t13041] VALUES (1907,'مكي بن قمير العنبري بصري عن جعفر بن سليمان، مجهول بالنقل حديثه غير محفوظ','3974','1621','0');
INSERT INTO [t13041] VALUES (1908,'مضر بن نوح السلمي عن عبد العزيز بن أبي رواد، ولا يعرف بالنقل , وحديثه غير محفوظ','3976','1621','0');
INSERT INTO [t13041] VALUES (1909,'مؤرق بن سخيت عن أبي هلال الراسبي، ولا يتابع عليه بهذا الإسناد , وقد روي من غير هذا الوجه بإسناد جيد','3978','1621','0');
INSERT INTO [t13041] VALUES (1910,'محبر بن قحذم عن أبيه قحذم بن سليمان , في حديثهما وهم وغلط','3980','1621','0');
INSERT INTO [t13041] VALUES (1911,'مؤمل بن الفضل الحراني ولا يتابع على حديثه بهذا الإسناد , هذا يعرف بالماجشون , عن عبد الله بن الفضل , عن الأعرج , عن أبي هريرة','3983','1621','0');
INSERT INTO [t13041] VALUES (1912,'معمر بن محمد بن عبيد الله بن أبي رافع ولا يتابع على حديثه , ولا يعرف إلا به','3985','1621','0');
INSERT INTO [t13041] VALUES (1913,'مطهر بن الهيثم بصري عن شبل، ولا يصح حديثه','3987','1621','0');
INSERT INTO [t13041] VALUES (1914,'مطرح بن يزيد الكناني لا يتابع على حديثه , ولا يعرف إلا به. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى، يقول: مطرح بن يزيد كوفي ضعيف، وفي موضع آخر: مطرح بن يزيد يكنى بالمهلب ليس بشيء، وفي موضع آخر: مطرح ليس بثقة','3989','1621','0');
INSERT INTO [t13041] VALUES (1915,'مخول بن إبراهيم الكوفي كان يغلو في الرفض. حدثنا محمد بن عبد الله الحضرمي قال: سمعت ابن نمير يقول: سمعت أبا نعيم، يقول: كان إلى جنبي مخول , فوقف علينا بعض المسودة , فرأى مخول أنامله , وكان حائل اللون , وعليه سواد كريه المنظر , فتنحيت عنه , فقال لي','3991','1621','0');
INSERT INTO [t13041] VALUES (1916,'مهند بن عبد الرحمن عن أم الدرداء، حديثه غير محفوظ , ولا يعرف إلا بهذا الإسناد , وقد روي بغير هذا الإسناد من طريق أصلح من هذا','3992','1621','0');
INSERT INTO [t13041] VALUES (1917,'مخيس بن تميم الأشجعي عن بهز بن حكيم، لا يتابع على حديثه','3994','1621','0');
INSERT INTO [t13041] VALUES (1918,'ميسرة بن عبد ربه أحاديثه بواطيل , غير محفوظة. حدثني آدم بن موسى قال: سمعت البخاري قال: ميسرة بن عبد ربه يرمى بالكذب','3996','1621','0');
INSERT INTO [t13041] VALUES (1919,'مجاشع بن عمرو حديثه منكر غير محفوظ. حدثنا محمد بن عثمان قال: حدثنا يحيى بن معين، يقول مجاشع بن عمرو قد رأيته أحد الكذابين','3998','1621','0');
INSERT INTO [t13041] VALUES (1920,'مرجى بن رجاء البصري حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى قال: مرجى بن رجاء ضعيف , ومرجى بن وداع ضعيف , إلا أن مرجى بن رجاء، صالح الحديث','4000','1621','0');
INSERT INTO [t13041] VALUES (1921,'مرجى بن وداع الراسبي حدثنا محمد بن عيسى قال: حدثنا عباس بن محمد قال: سمعت يحيى قال: مرجى بن وداع الراسبي ضعيف','4002','1621','0');
INSERT INTO [t13041] VALUES (1922,'مصدع أبو يحيى الأعرج حدث علي بن أحمد قال: حدثنا صالح قال: حدثنا علي قال: سمعت سفيان قال: قال عمرو بن دينار: اسم أبي يحيى الأعرج: مصدع , قال: سفيان , وقال أهل الكوفة: قطع بشر بن مروان عرقوبيه , قيل لسفيان: في أي شيء قطع عرقوبيه؟ قال: في التشيع','4004','1621','0');
INSERT INTO [t13041] VALUES (1923,'مندل بن علي العنزي','4005','1621','0');
INSERT INTO [t13041] VALUES (1924,'محفوظ بن أبي توبة كان معهم باليمن إلا أنه لم يكتب كل ذلك , كان يسمع مع إبراهيم أخي أبان , ولم يكن ينسخ , وضعف أمره جدا','4008','1621','0');
INSERT INTO [t13041] VALUES (1925,'باب النون','4009','0','0');
INSERT INTO [t13041] VALUES (1926,'النعمان بن راشد الجزري حدثنا محمد بن عيسى قال: حدثنا صالح قال: سمعت عليا قال: ذكرت ليحيى بن سعيد النعمان بن راشد فضعفه. حدثنا عبد الرحمن بن أحمد قال: سمعت النعمان بن راشد مضطرب الحديث , وسألته مرة أخرى عن النعمان بن راشد، فقال: روى أحاديث، مناكير','4010','1925','0');
INSERT INTO [t13041] VALUES (1927,'النعمان بن ثابت أبو حنيفة حدثنا سليمان بن داود القطان قال: حدثنا أحمد بن الحسين الترمذي، قال , حدثنا أبو نعيم ضرار بن صرد , قال: حدثنا سليم المقرئ قال: سمعت الثوري يقول: قال لنا حماد: أفيكم من يأتي أبا حنيفة؟ بلغوا عني أبا حنيفة أني بريء منه.','4011','1925','0');
INSERT INTO [t13041] VALUES (1928,'نافع مولى يوسف بن عبد الله بصري. حدثني آدم بن موسى قال: سمعت البخاري قال: نافع مولى يوسف بن عبد الله بصري منكر الحديث','4012','1925','0');
INSERT INTO [t13041] VALUES (1929,'نافع بن الحارث الهمداني كوفي روى عنه زياد بن المنذر. حدثني آدم بن موسى قال: سمعت البخاري قال: نافع بن الحارث الهمداني كوفي روى عنه، زياد بن المنذر ولم يصح حديثه','4014','1925','0');
INSERT INTO [t13041] VALUES (1930,'نافع بن عبد الواحد أبو هرمز الغالب على حديثه الوهم. حدثنا محمد بن عثمان قال: سمعت يحيى بن معين، وذكر له أبو الخشني نافع أبو هرمز الذي يحدث عن أنس؟ فقال: كان ضعيفا , لا يكتب حديثه. حدثنا عبد الله بن أحمد، عن أبيه، قال: نافع السلمي , عن أنس، ضعيف','4016','1925','0');
INSERT INTO [t13041] VALUES (1931,'النضر بن عاصم الهجيمي عن قتادة، ولا يتابع عليه ولا يعرف إلا به','4018','1925','0');
INSERT INTO [t13041] VALUES (1932,'النضر بن مطرف حدثنا محمد بن عيسى قال: حدثنا العباس قال: سمعت يحيى قال: النضر بن مطرف كوفي روى عن الفزاري وغيره، وهو ضعيف , وحدثنا في موضع آخر , قال: النضر بن مطرف ليس بشيء. حدثنا هارون بن علي قال: حدثنا علي بن مسلم قال: حدثنا مروان قال: حدثنا','4020','1925','0');
INSERT INTO [t13041] VALUES (1933,'النضر بن محرز المروزي عن محمد بن المنكدر، لا يتابع على حديثه , ولا يعرف إلا به','4021','1925','0');
INSERT INTO [t13041] VALUES (1934,'النضر بن حميد الكندي عن ثابت، وأبي الجارود. حدثني آدم بن موسى قال: سمعت البخاري قال: النضر بن حميد الكندي , عن ثابت، وأبي الجارود، منكر الحديث','4023','1925','0');
INSERT INTO [t13041] VALUES (1935,'النضر بن إسماعيل البجلي أبو المغيرة الكوفي حدثنا عبد الله بن أحمد قال: حدثنا أبي قال: حدثنا النضر بن إسماعيل أبو المغيرة، لم يكن يحفظ الإسناد , روى عن إسماعيل، عن قيس: رأيت أبا بكر أخذ بلسانه , وإنما هو عند زيد بن أسلم , يعني عن أبيه , عن عمر رأيت','4026','1925','0');
INSERT INTO [t13041] VALUES (1936,'النضر بن معبد أبو قحذم عن محمد بن سيرين، ولا يتابع عليه','4030','1925','0');
INSERT INTO [t13041] VALUES (1937,'النضر بن عبد الرحمن الخزاز أبو عمر حدثني آدم بن موسى قال: سمعت البخاري قال: النضر بن عبد الرحمن الخزاز أبو عمر منكر الحديث. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى، يقول: النضر أبو عمر الخزاز ليس يحل لأحد أن يروي عنه. حدثنا عبد الله بن','4032','1925','0');
INSERT INTO [t13041] VALUES (1938,'النضر بن كثير السعدي حدثني آدم قال: سمعت البخاري، يقول: النضر بن كثير السعدي عنده مناكير , وقال لنا عبد الرحمن بن الفضل , عن البخاري في الكتاب الكبير: النضر بن كثير السعدي فيه نظر','4034','1925','0');
INSERT INTO [t13041] VALUES (1939,'النضر بن شميل حدثنا أحمد بن علي قال: حدثنا محمد بن علي بن الحسن بن شقيق قال: حدثنا إبراهيم بن شماس قال: سألت وكيعا، عن النضر بن شميل، فتغير وجهه ورفع حاجبه وقال: إن له مشيخة , شبه الرضا','4036','1925','0');
INSERT INTO [t13041] VALUES (1940,'النضر بن منصور العنزي حدثني آدم بن موسى قال: سمعت البخاري، يقول: النضر بن منصور العنزي , عن علي، منكر الحديث','4037','1925','0');
INSERT INTO [t13041] VALUES (1941,'النضر بن حفص بن النضر بن أنس بصري مجهول بالنقل , حديثه غير محفوظ','4039','1925','0');
INSERT INTO [t13041] VALUES (1942,'نعيم بن مورع بن توبة العنبري عن هشام بن عروة، حديثه غير محفوظ , إلا عن أبي مسعد السدي، وفيه نظر. حدثنا آدم بن موسى قال: سمعت البخاري، يقول: نعيم بن مورع العنبري , عن هشام بن عروة، منكر الحديث','4041','1925','0');
INSERT INTO [t13041] VALUES (1943,'نعيم بن يعقوب ابن أخت سفيان بن عيينة، عن أبي إسحاق، لا يتابع على حديثه','4043','1925','0');
INSERT INTO [t13041] VALUES (1944,'نصر بن نجيح الباهلي عن عمر أبي حفص، ونصر، وعمر مجهولان بالنقل , والحديث غير محفوظ','4045','1925','0');
INSERT INTO [t13041] VALUES (1945,'نصر بن طريف أبو جزي الباهلي حدثنا علي بن الحسن بن الجنيد قال: حدثنا عبد الرحمن بن عمر رستة , قال: حدثنا أبو داود قال: قال عبد الرحمن: بعث إلي أبو جزي وهو مريض , فقال حديث كذا وكذا , كيف كتبته عني؟ فقلت حدثتني عن قتادة , قال: اجعله , عن سعيد , عن','4047','1925','0');
INSERT INTO [t13041] VALUES (1946,'نصر القصاب عن قتادة. حدثني آدم بن موسى قال: سمعت البخاري قال: نصر القصاب , عن قتادة، في حديثه نظر','4049','1925','0');
INSERT INTO [t13041] VALUES (1947,'نصر بن عاصم الأنطاكي عن الوليد، ولا يتابع عليه , ولا يعرف إلا به','4052','1925','0');
INSERT INTO [t13041] VALUES (1948,'نصر بن قديد أبو صفوان القديدي حدثنا عبد الله بن أحمد بن عبد السلام قال: حدثنا محمد بن إسماعيل قال: قال لي أبو إسحاق , قال لي يحيى بن معين: نصر بن قديد أبو صفوان الليثي كذاب','4054','1925','0');
INSERT INTO [t13041] VALUES (1949,'نصر بن جميل عن حفص بن عبد الرحمن، مجهولين بالنقل حديثهما غير محفوظ','4056','1925','0');
INSERT INTO [t13041] VALUES (1950,'نصر بن مزاحم المنقري كان يذهب إلى التشيع , وفي حديثه اضطراب وخطأ كثير','4058','1925','0');
INSERT INTO [t13041] VALUES (1951,'نصر بن حماد أبو الحارث الوراق حدثني آدم قال: سمعت البخاري قال: نصر بن حماد أبو الحارث الوراق يتكلمون فيه','4061','1925','0');
INSERT INTO [t13041] VALUES (1952,'نصر بن حاجب شامي خراساني حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى قال: نصر بن حاجب قرشي خراساني , وكان شاميا، ليس بشيء','4063','1925','0');
INSERT INTO [t13041] VALUES (1953,'نصر بن باب حدثنا عبد الله بن أحمد قال: سألت أبي عن نصر بن باب، فقال: إنما أنكر الناس عليه حين حدث عن إبراهيم الصائغ , وما كان فيه بأس , قلت له: فإن أبا حنيفة قال: نصر بن باب كذاب , قال: ما أخبرني على هذا أن أقوله استغفر الله. حدثنا محمد قال:','4065','1925','0');
INSERT INTO [t13041] VALUES (1954,'نوح عن أبي مجلز، ولا يتابع عليه , ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: نوح , عن أبي مجلز، الذي عنه ليث بن أبي سليم , منكر الحديث','4067','1925','0');
INSERT INTO [t13041] VALUES (1955,'نوح بن طلحة بن عبد الله بن عبد الرحمن بن أبي بكر الصديق عن أبيه، عن عائشة، رضي الله عنها , ولم يسمع منها , هو مرسل , والحديث غير محفوظ ولا يصح إسناده','4069','1925','0');
INSERT INTO [t13041] VALUES (1956,'نوح بن أبي مريم أبو عصمة المروزي قاضي مرو. حدثنا عبد الله بن محمد بن سعدويه المروزي قال: حدثنا أحمد بن عبد الله بن بشر المروزي قال: حدثنا سفيان بن عبد الملك قال: سمعت ابن المبارك، ذكر حديث أبي عصمة , وضعفه وأنكر كثيرا منه , وقيل: إنه يروي عن الزهري','4071','1925','0');
INSERT INTO [t13041] VALUES (1957,'نوح بن دراج حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى، يقول: نوح بن دراج ليس بشيء وفي موضع آخر: نوح بن دراج كذاب , حيث قضى سنتين وهو أعمى , وفي موضع آخر , قال: سئل يحيى عن نوح بن دراج فقال: لم يكن يدري ما الحديث , ولا يحسن شيئا ,','4072','1925','0');
INSERT INTO [t13041] VALUES (1958,'نوح بن ربيعة أبو مكين ولا يتابع على حديثه , ولا يعرف إلا به. حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا يعلى قال: قلت ليحيى: أبو مكين؟ قال: هو فوقه يعني عمر بن الوليد الشني','4073','1925','0');
INSERT INTO [t13041] VALUES (1959,'نفيع بن الحارث الهمداني أبو داود الضرير ممن يغلو في الرفض. حدثنا زكريا بن يحيى قال: حدثنا محمد بن المثنى قال: حدثنا عفان قال: حدثنا همام قال: قدم علينا أبو داود , فجعل يقول: حدثنا البراء بن عازب، وزيد بن أرقم، فقلنا لقتادة: إن أبا داود يحدثنا عن','4075','1925','0');
INSERT INTO [t13041] VALUES (1960,'نجيح أبو معشر المديني مولى المهدي. حدثني آدم بن موسى قال: سمعت محمد بن إسماعيل قال: قال عبيد الله بن سعيد، سمعت ابن مهدي، يقول: كان أبو معشر يعرف وينكر. قال البخاري: أبو معشر منكر الحديث. حدثنا عبد الله بن أحمد قال: سألت يحيى عن أبي معشر','4077','1925','0');
INSERT INTO [t13041] VALUES (1961,'نهشل بن سعيد عن الضحاك، وغيره. حدثني آدم بن موسى قال: سمعت البخاري قال: نهشل بن سعيد , قال إسحاق: كان كذابا. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى، يقول: نهشل بن سعيد الخراساني ليس بثقة','4079','1925','0');
INSERT INTO [t13041] VALUES (1962,'ناصح بن العلاء مولى بني هاشم. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: ناصح بن العلاء البصري ليس بشيء. حدثني آدم بن موسى قال: سمعت البخاري قال: ناصح بن العلاء البصري مولى بني هاشم منكر الحديث','4082','1925','0');
INSERT INTO [t13041] VALUES (1963,'ناصح بن عبد الله المحلمي الحائك حدثنا أحمد بن علي الأبار قال: سألت يحيى بن معين , عن ناصح الكوفي، صاحب سماك , فقال: ليس بشيء. حدثني آدم بن موسى قال: سمعت البخاري قال: ناصح بن عبد الله المحلمي الحائك منكر الحديث , كان يذهب إلى الرفض','4085','1925','0');
INSERT INTO [t13041] VALUES (1964,'نهاس بن قهم حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سمعت يحيى، يقول: كتبت عن النهاس بن قهم كذا وكذا , ثم قال ليحيى: كان يروي عن عطاء , عن ابن عباس أشياء منكرة , وروى عن عطاء , عن أبي هريرة , ثم طاف بالبيت سبعا وصلى خلف المقام ركعتين','4088','1925','0');
INSERT INTO [t13041] VALUES (1965,'نائل بن نجيح','4090','1925','0');
INSERT INTO [t13041] VALUES (1966,'باب الواو','4095','0','0');
INSERT INTO [t13041] VALUES (1967,'الوليد بن عيسى أبو وهب حدثنا آدم بن موسى قال: سمعت البخاري قال: الوليد بن عيسى أبو وهب كوفي فيه نظر','4096','1966','0');
INSERT INTO [t13041] VALUES (1968,'الوليد بن كريز ولا يتابع على حديثه , ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: الوليد بن كريز بصري , عن ابن سيرين، لا تصح الرواية عنه','4098','1966','0');
INSERT INTO [t13041] VALUES (1969,'الوليد بن زياد أخو هشام بن زياد، ضعيف من أجل أخيه , والحديث غير محفوظ','4101','1966','0');
INSERT INTO [t13041] VALUES (1970,'الوليد بن عبد الله بن جميع الزهري في حديثه اضطراب. حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: كان يحيى لا يحدثنا عن الوليد بن جميع، فإنما كان قبل موته بقليل أخذتها من علي الصائغ , فحدثني بها , وكانت ستة أحاديث','4103','1966','0');
INSERT INTO [t13041] VALUES (1971,'الوليد بن محمد الموقري البلقاوي حدثني محمد بن عيسى قال: حدثنا العباس بن محمد قال: سألت يحيى عن الموقري، فقال: ليس بشيء. حدثنا عبد الله بن أحمد قال: سألت أبي عن الموقري، فقال: ما أظنه ثقة لم أره يحمده. حدثني آدم بن موسى قال: سمعت البخاري قال:','4106','1966','0');
INSERT INTO [t13041] VALUES (1972,'الوليد بن أبي ثور الهمداني حدثنا عبد الله بن أحمد بن حنبل قال: سألت يحيى بن معين عن الوليد بن أبي ثور قال: ليس بشيء. حدثنا محمد بن عثمان قال: سألت ابن نمير عن الوليد بن أبي ثور، فقال: كذاب','4108','1966','0');
INSERT INTO [t13041] VALUES (1973,'الوليد بن كثير المخزومي مولى لهم. حدثني أحمد بن زكير قال: حدثنا أحمد بن سعيد الفهري قال: حدثنا محمد بن عبيد بن ميمون التبان قال: سمعني أبي، وأنا أقول: حدثنا عيسى بن يونس، عن الوليد بن كثير، فقال: يا بني، تدري من الوليد بن كثير؟ قال: والله كان','4110','1966','0');
INSERT INTO [t13041] VALUES (1974,'الوليد بن عمرو بن ساج حدثني محمد بن عيسى قال: حدثنا العباس قال: سمعت يحيى، يقول: الوليد بن عمرو بن ساج ضعيف. ومن حديثه ما حدثناه محمد بن عمار بن عطية قال: حدثنا محمد بن إدريس قال: حدثني محمد بن يزيد بن سنان قال: حدثنا الوليد بن عمرو بن ساج، عن','4111','1966','0');
INSERT INTO [t13041] VALUES (1975,'الوليد بن موسى الدمشقي عن الأوزاعي، أحاديثه بواطيل , لا أصول لها , ليس ممن يقيم الحديث','4112','1966','0');
INSERT INTO [t13041] VALUES (1976,'وهب بن راشد عن مالك بن دينار، وفرقد السنجي، منكر الحديث','4115','1966','0');
INSERT INTO [t13041] VALUES (1977,'وهب الله بن راشد أبو زرعة الحجري مصري حدثنا زكريا بن يحيى قال: حدثنا أحمد بن سعيد بن أبي مريم قال: أردت أن أكتب عن أبي زرعة وهب الله بن راشد فنهاني عمي أن أكتب عنه','4118','1966','0');
INSERT INTO [t13041] VALUES (1978,'وهب بن إسماعيل الأسدي حدثنا عبد الله بن أحمد بن حنبل قال: سألت أبي عن وهب بن إسماعيل الأسدي قال: كتبنا عنه أحاديث , قلت نرجو أن يكون صالح الحديث , قال: ما أدري فراجعته , فقال روى بعدنا أحاديث مناكير , عن ورقاء بن إياس','4119','1966','0');
INSERT INTO [t13041] VALUES (1979,'وهب بن حكيم الأزدي عن محمد بن سيرين، مجهول بالنقل , ولا يتابع على حديثه','4120','1966','0');
INSERT INTO [t13041] VALUES (1980,'وهب بن جرير بن حازم أبو الحسن الأزدي حدثني عبد الله بن أحمد بن حنبل قال: سمعت أبي يقول، قال: عبد الرحمن بن مهدي: ها هنا قوم يحدثون عن شعبة، ما رأيناهم عند شعبة , قلت: من تعني بهذا؟ قال: وهب بن جرير , قال أبي: ما رؤي وهب عند شعبة قط، ولكن وهب','4122','1966','0');
INSERT INTO [t13041] VALUES (1981,'وهب بن وهب أبو البختري القاضي المدني حدثنا الحسن بن غليب الأزدي قال: حدثنا أبو سعيد الجعفي قال: سمعت أبا بكر بن عياش، وذكر أبا البختري , فقال: لم يكن بصاحب حديث. حدثنا أحمد بن علي الأبار قال: حدثنا علي بن ميمون الرقي قال: حدثنا أبو خليد عتبة بن','4123','1966','0');
INSERT INTO [t13041] VALUES (1982,'واصل بن عبد الرحمن أبو حرة حدثنا عبد الله بن أحمد قال: سمعت يحيى بن معين يقول: حدثني غندر قال: وقفت أبا حرة على أحاديث , فقال: لم أسمعها من الحسن , إذ قال غندر: فلم يقف على شيء منها أنه سمعه من الحسن إلا حديثا أو اثنين. حدثنا عبد الله، حدثني أبي،','4126','1966','0');
INSERT INTO [t13041] VALUES (1983,'واصل بن أبي السائب عن عطاء، وأبي سورة. حدثني آدم بن موسى قال: سمعت البخاري قال: واصل بن السائب , عن عطاء، وأبي سورة منكر الحديث','4127','1966','0');
INSERT INTO [t13041] VALUES (1984,'ورقاء بن عمر اليشكري تكلموا فيه , في حديثه عن منصور. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى، يقول: سمعت معاذ بن معاذ، يقول ليحيى بن سعيد: سمعت حديث منصور من ورقاء؟ قال: لا يساوي شيئا','4129','1966','0');
INSERT INTO [t13041] VALUES (1985,'وكيع بن محرز السامي حدثني آدم قال: سمعت البخاري قال: وكيع بن محرز السامي عنده عجائب','4130','1966','0');
INSERT INTO [t13041] VALUES (1986,'وضاح بن خيثمة عن هشام بن عروة، ولا يتابع على حديثه','4132','1966','0');
INSERT INTO [t13041] VALUES (1987,'وقاء بن إياس الأسدي حدثنا عبد الله بن أحمد قال: سألت أبي عن وقاء بن إياس، فقال: كذا وكذا , ثم قال: يحيى ضعفه. حدثنا محمد قال: حدثنا صالح قال: حدثنا علي قال: سمعت يحيى، يقول: ما كان وقاء بن إياس بالذي يعتمد عليه. سمعت يحيى يقول أيضا: لم يكن','4134','1966','0');
INSERT INTO [t13041] VALUES (1988,'وضين بن عطاء حدثنا عبد الله بن أحمد قال: قال أبي: الوضين بن عطاء ليس به بأس , كان يرى القدر. حدثنا أحمد بن يحيى قال: حدثنا الهيثم بن خارجة قال: حدثنا الوليد بن مسلم قال: رأيت الوضين بن عطاء وكان صاحب خطب , ولم يكن في حديثه بذاك. حدثنا يحيى بن','4135','1966','0');
INSERT INTO [t13041] VALUES (1989,'وازع بن نافع العقيلي جزري حدثنا عبد الله بن أحمد قال: سئل يحيى بن معين , وأنا أسمع عن الوازع بن نافع، فقال: ليس بثقة , وهو عقيلي من أهل الجزيرة , وسألت أبي عنه , فقال: ليس حديثه بشيء. حدثني آدم قال: سمعت البخاري قال: وازع بن نافع العقيلي منكر','4137','1966','0');
INSERT INTO [t13041] VALUES (1990,'واقد بن سلامة حدثني آدم بن موسى قال: سمعت البخاري قال: واقد بن سلامة النضري , ولم يصح حديثه','4139','1966','0');
INSERT INTO [t13041] VALUES (1991,'وزير بن عبد الرحمن الجزري حديثه غير محفوظ. حدثنا محمد قال: حدثنا عباس قال: سألت يحيى بن معين , من وزير الذي يحدث بحديث معاوية أن النبي صلى الله عليه وسلم , أعطاه سهما؟ فقال: ليس بشيء','4141','1966','0');
INSERT INTO [t13041] VALUES (1992,'وثيمة بن موسى أصله فارسي , سكن مصر، صاحب أغاليط، ورواية عن كل. ومن حديثه ما حدثناه عمارة بن وثيمة قال: حدثنا أبي وثيمة بن موسى قال: حدثنا سلمة بن الفضل قال: حدثنا محمد بن إسحاق، عن الزهري، وعن صالح بن كيسان، عن عبيد الله بن عبد الله بن عتبة،','4143','1966','0');
INSERT INTO [t13041] VALUES (1993,'باب الهاء','4144','0','0');
INSERT INTO [t13041] VALUES (1994,'هشام بن حسان القردوسي حدثنا محمد بن إسماعيل قال: حدثنا الحسن بن علي قال: حدثنا يحيى بن آدم قال: حدثنا أبو شهاب قال: قال لي شعبة: عليك بحجاج , ومحمد بن إسحاق فإنهما حافظان , واكتم علي عند النضر بن خالد , وهشام. حدثنا محمد بن عيسى قال: حدثنا صالح','4145','1993','0');
INSERT INTO [t13041] VALUES (1995,'هشام بن لاحق المدائني لا يتابع على رفع حديثه','4150','1993','0');
INSERT INTO [t13041] VALUES (1996,'هشام بن حجير مكي حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: سمعت يحيى عن حديث هشام بن حجير فأبى أن يحدث به , ولم يرضه. حدثنا محمد قال: حدثنا صالح قال: حدثنا علي قال: قرأت على يحيى بن سعيد كتابا , فيه عن هشام بن يحيى حديث , فتكلم فيه بشيء , فقلت','4152','1993','0');
INSERT INTO [t13041] VALUES (1997,'هشام بن سليمان المخزومي في حديثه عن غير ابن جريج وهم','4153','1993','0');
INSERT INTO [t13041] VALUES (1998,'هشام بن محمد بن السائب الكلبي أبو المنذر حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: هشام بن محمد بن السائب الكلبي من يحدث عنه , إنما هو صاحب سمر ونسب , ما ظننت أن أحدا يحدث عنه. ومن حديثه ما حدثناه أحمد بن داود قال: حدثنا محمد بن سعيد بن الوليد','4155','1993','0');
INSERT INTO [t13041] VALUES (1999,'هشام بن زياد بن سعدويه المروزي أبو المقدام مولى عثمان بن عفان رضي الله عنه. حدثنا عبد الله بن أحمد قال: حدثنا أحمد بن عبد الله ابن بشيرة قال: حدثنا سفيان بن عبد الملك قال: سمعت عبد الله بن المبارك، يقول: هشام بن زياد ارم به. حدثنا محمد قال: حدثنا','4156','1993','0');
INSERT INTO [t13041] VALUES (2000,'هشام بن سعد الخشاب مديني حدثنا عبد الله بن أحمد قال: سألت أبي عن هشام بن سعد، فقال: كذا وكذا , وكان يحيى لا يروي عنه. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: هشام بن سعد فيه ضعف , وداود بن قيس أحب إلي منه. حدثنا محمد بن أحمد قال: حدثنا','4158','1993','0');
INSERT INTO [t13041] VALUES (2001,'هاشم بن عيسى اليزني الحمصي عن أبيه، عن يحيى بن سعيد، منكر الحديث , وهو وأبوه مجهولان بالنقل','4159','1993','0');
INSERT INTO [t13041] VALUES (2002,'هاشم بن محمد الربعي عن حماد بن زيد، ولا يتابع على حديثه','4161','1993','0');
INSERT INTO [t13041] VALUES (2003,'هاشم بن يحيى بن هاشم المزني عن أبي دغفل، مجهولان جميعا , ولا يتابع على حديثه , ولا يعرف إلا به','4163','1993','0');
INSERT INTO [t13041] VALUES (2004,'هلال بن زيد بن يسار بن بولا بصري عن أنس. حدثني آدم بن موسى قال: سمعت البخاري قال: هلال بن زيد بن يسار بن بولا بصري , عن أنس، قال البخاري: يقال له أبو عقال , في حديثه مناكير','4165','1993','0');
INSERT INTO [t13041] VALUES (2005,'هلال أبو ظلال القسملي عن أنس، عنده مناكير. حدثنا محمد قال: حدثنا العباس قال: سمعت يحيى قال: أبو ظلال القسملي ليس بشيء. حدثني آدم بن موسى قال: سمعت البخاري قال: هلال أبو ظلال القسملي , عن أنس، عنده مناكير. وحدثنا محمد بن أحمد قال: حدثنا معاوية','4167','1993','0');
INSERT INTO [t13041] VALUES (2006,'هلال بن سويد الأحمري ولا يتابع إلا من طريق تقاربه. حدثني آدم بن موسى قال: سمعت البخاري قال: هلال بن سويد الأحمري أبو المعلى كوفي , لا يتابع عليه','4169','1993','0');
INSERT INTO [t13041] VALUES (2007,'هلال بن خباب أبو العلاء مولى زيد بن صوحان، في حديثه وهم وتغير بآخرة. حدثنا محمد بن جعفر قال: حدثنا أبو بكر بن أبي الأسود قال: حدثنا يحيى بن سعيد قال: أتيت هلال بن خباب وقد تغير','4171','1993','0');
INSERT INTO [t13041] VALUES (2008,'هلال بن عبد الله الباهلي عن أبي إسحاق، ولا يتابع على حديثه','4173','1993','0');
INSERT INTO [t13041] VALUES (2009,'هلال بن عبد الرحمن الحنفي منكر الحديث','4175','1993','0');
INSERT INTO [t13041] VALUES (2010,'الهيثم بن بدر الضبي حدثنا محمد بن عيسى قال: حدثنا صالح قال: حدثنا علي قال: سألت جريرا عن الهيثم بن بدر، فقال: ضبي كان على خراج الري. حدثني آدم بن موسى قال: سمعت البخاري قال: قال علي: سألت جريرا عن الهيثم بن بدر، فقال: ضبي كان على خراج الري ,','4178','1993','0');
INSERT INTO [t13041] VALUES (2011,'الهيثم بن الأشعث يخالف في حديثه , ولا يصح إسناده','4180','1993','0');
INSERT INTO [t13041] VALUES (2012,'الهيثم بن عدي حدثني محمد بن عبد الرحمن قال: حدثنا عبد الملك بن عبد الحميد، ذاكروا أبا عبد الله بحديث وأنا حاضر، فقال: من يرو ذا كذب , فقال له رجل: الهيثم بن عدي , عن مجالد فتبسم أبو عبد الله متعجبا من ذلك , وأظنه قد قال: في هذا الموضع كذب. حدثنا','4182','1993','0');
INSERT INTO [t13041] VALUES (2013,'الهيثم بن الربيع العقيلي بصري في حديثه وهم','4184','1993','0');
INSERT INTO [t13041] VALUES (2014,'الهيثم بن رزيق المالكي عن الحسين، ولا يتابع عليه , ولا يعرف إلا به','4186','1993','0');
INSERT INTO [t13041] VALUES (2015,'الهيثم بن قيس العيشي ولا يصح حديثه من هذا الطريق , وأما المتن فثابت من غير هذا الوجه','4188','1993','0');
INSERT INTO [t13041] VALUES (2016,'الهيثم بن عقاب كوفي مجهول بالنقل , حديثه غير محفوظ , ولا يعرف إلا به','4190','1993','0');
INSERT INTO [t13041] VALUES (2017,'الهيثم بن جماز الحنفي حديثه غير محفوظ. حدثنا محمد، حدثنا عباس قال: سمعت يحيى قال: الهيثم بن جماز ضعيف , وفي موضع آخر: الهيثم بن جماز قاض كان بالبصرة , ليس بذاك يروي عن هشيم','4192','1993','0');
INSERT INTO [t13041] VALUES (2018,'الهيثم بن صالح الهزاني عن سلام أبي المنذر، عن مطر، لا يتابع عليه , ولا يعرف إلا به','4194','1993','0');
INSERT INTO [t13041] VALUES (2019,'الهيثم بن الحسين العقيلي عن الثوري، منكر الحديث','4196','1993','0');
INSERT INTO [t13041] VALUES (2020,'الهيثم بن عبد الغفار الطائي حدثنا عبد الله بن أحمد بن حنبل قال: حدثنا أبي، قال: عرضت على ابن مهدي أحاديث الهيثم بن عبد الغفار , عن همام، وغيره، فقال: هذا يضع الحديث. وسألت الأقرع فذكر مثله , أو نحوه , وقال أبي: وكان الأقرع من أصحاب الحديث. قال','4198','1993','0');
INSERT INTO [t13041] VALUES (2021,'هارون بن أبي عيسى صاحب السيرة , ولا يتابع على حديثه. حدثني آدم بن موسى: قال: سمعت البخاري قال: هارون بن أبي عيسى صاحب السيرة يخطئ في حديثه , عن غير ابن إسحاق','4199','1993','0');
INSERT INTO [t13041] VALUES (2022,'هارون بن هارون الأزدي حدثني آدم قال: سمعت البخاري قال: هارون بن هارون ليس بذاك','4201','1993','0');
INSERT INTO [t13041] VALUES (2023,'هارون بن محمد أبو الطيب السرخسي الغالب على حديثه الوهم','4203','1993','0');
INSERT INTO [t13041] VALUES (2024,'هارون بن حيان أبو الصقر العقيلي عن ابن المنكدر. حدثني آدم بن موسى قال: سمعت البخاري قال: هارون بن حيان , عن محمد بن المنكدر، في حديثه نظر','4205','1993','0');
INSERT INTO [t13041] VALUES (2025,'هارون بن يحيى الحاطبي مديني لا يتابع على حديثه من هذا الوجه , وقد روي بغير هذا الإسناد , خلاف هذا اللفظ من طريق أصلح من هذا','4207','1993','0');
INSERT INTO [t13041] VALUES (2026,'هارون بن قزعة مديني روى عنه، سوار بن ميمون , سوار بن منصور في كتاب غيري. حدثنا آدم بن موسى قال: سمعت البخاري قال: هارون بن قزعة مديني لا يتابع عليه','4209','1993','0');
INSERT INTO [t13041] VALUES (2027,'هارون بن سعد كوفي كان يغلو في الرفض. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى، يقول: قال هارون بن سعيد من الغالية في التشيع','4211','1993','0');
INSERT INTO [t13041] VALUES (2028,'هارون بن الجهم بن ثوير بن أبي فاختة عن عبد الملك بن عمير، يخالف في حديثه , وليس بمشهور بالنقل','4213','1993','0');
INSERT INTO [t13041] VALUES (2029,'هانئ بن خالد بصري حديثه غير محفوظ , وليس بمعروف بالنقل , ولا يتابع عليه , ولا يعرف إلا به','4215','1993','0');
INSERT INTO [t13041] VALUES (2030,'هذيل بن بلال الفزاري أبو البهلول المدائني حدثني أبو علي محمد بن عيسى , قال: حدثنا العباس بن محمد قال: سمعت يحيى، يقول: الهذيل بن بلال ليس بشيء , وفي موضع آخر: الهذيل بن بلال , كان علي يقول: المدائني وليس بشيء , كان ينزل المدائن , وليس بشيء','4217','1993','0');
INSERT INTO [t13041] VALUES (2031,'هذيل بن الحكم الأزدي أبو المنذر لا يقيم الحديث. حدثني آدم بن موسى قال: سمعت البخاري قال: هذيل بن الحكم الأزدي أبو المنذر منكر الحديث','4219','1993','0');
INSERT INTO [t13041] VALUES (2032,'هياج بن بسطام الحنظلي حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: هياج بن بسطام ليس بشيء , وفي موضع آخر: هياج بن بسطام هروي ضعيف الحديث','4222','1993','0');
INSERT INTO [t13041] VALUES (2033,'همام بن يحيى العوذي حدثنا عبد الله بن أحمد قال: حدثني أبي، حدثنا يحيى بن سعيد، عن شعبة، وهشام، بحديث قتادة , قال: سمعت مطرفا، عن عياض بن حمار في القدر. قال أبي: وقال همام , عن قتادة، عن العلاء بن زياد، ويزيد أخي مطرف , وعقبة , ورجل آخر نسيه','4224','1993','0');
INSERT INTO [t13041] VALUES (2034,'همام بن نافع والد عبد الرزاق، عن سالم حديثه غير محفوظ','4228','1993','0');
INSERT INTO [t13041] VALUES (2035,'باب لا','4230','0','0');
INSERT INTO [t13041] VALUES (2036,'أبو مجلز لاحق بن حميد حدثنا محمد بن عيسى قال: حدثنا صالح بن أحمد، حدثنا علي قال: سمعت أبا داود يقول: قال شعبة: أبو مجلز هذا يجيء عنه حديث كأنه شيعي , ثم يجيء عنه حديث كأنه عثماني. حدثنا محمد بن عثمان، حدثنا حسين بن حيان قال: سمعت يحيى، يقول:','4231','2035','0');
INSERT INTO [t13041] VALUES (2037,'باب الياء','4232','0','0');
INSERT INTO [t13041] VALUES (2038,'يزيد بن أبان الرقاشي حدثنا أحمد بن علي الأبار، حدثنا يوسف بن عيسى، عن النضر بن شميل قال: قال شعبة: لأن أقطع الطريق أحب إلي من أن أروي عن يزيد الرقاشي , قلت ليوسف: سمعته من النضر؟ قال: حدثني إسحاق بن راهويه عن النضر. حدثنا زكريا بن يحيى، حدثنا','4233','2037','0');
INSERT INTO [t13041] VALUES (2039,'يزيد بن بابنوس حدثني آدم قال: سمعت البخاري قال: يزيد بن بابنوس من السبعة الذين قاتلوا عليا , قاله مرحوم , عن ابن أبي عمران','4234','2037','0');
INSERT INTO [t13041] VALUES (2040,'يزيد بن بلال بن الحارث الفزاري عن علي. حدثني آدم بن موسى قال: سمعت البخاري قال: يزيد بن بلال بن الحارث الفزاري , عن علي، فيه نظر','4236','2037','0');
INSERT INTO [t13041] VALUES (2041,'يزيد بن بيان المعلم لا يتابع عليه , ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: يزيد بن بيان المعلم فيه نظر','4238','2037','0');
INSERT INTO [t13041] VALUES (2042,'يزيد بن بزيع ولا يتابع عليه , ولا يعرف إلا به. حدثنا محمد بن عيسى قال: حدثنا العباس قال: سمعت يحيى، يقول: يزيد بن بزيع الرملي ضعيف , وأحسب أن يحيى قال: قد رأيته ببغداد','4240','2037','0');
INSERT INTO [t13041] VALUES (2043,'يزيد بن حصين بن نمير حدثني آدم قال: سمعت البخاري قال: يزيد بن حصين بن نمير , عن أبيه، وسمع منه، محمد بن الزبير لم يصح إسناده','4242','2037','0');
INSERT INTO [t13041] VALUES (2044,'يزيد بن ربيعة الرحبي أبو كامل الصنعاني حدثني آدم قال: سمعت البخاري قال: يزيد بن ربيعة الرحبي أبو كامل الصنعاني عنده مناكير','4243','2037','0');
INSERT INTO [t13041] VALUES (2045,'يزيد بن زيد عن خولة بنت الصامت. حدثني أحمد بن موسى قال: سمعت البخاري قال: يزيد بن زيد , عن خولة بنت الصامت، في صحته نظر','4245','2037','0');
INSERT INTO [t13041] VALUES (2046,'يزيد بن زياد مولى بني هاشم , عن محمد بن كعب قال: سمعت معاوية. حدثني آدم بن موسى قال: سمعت البخاري قال: يزيد بن زياد مولى بني هاشم , عن محمد بن كعب، سمعت معاوية، ولا يتابع عليه , كان البخاري أحسب أنكر أن محمد بن كعب قد سمع معاوية. وهذا الحديث رواه','4246','2037','0');
INSERT INTO [t13041] VALUES (2047,'يزيد بن خمير الرحبي حدثنا محمد بن عيسى قال: حدثنا عمرو بن علي قال: سمعت سفيان بن زياد الراسبي، يسأل وكيعا عن أحاديث أبي بكر، فجعل لا يصحح فيها شيئا , وذكر له حديث يزيد بن خمير , فقال: ذاك شامي , وما سمعت وكيعا ذاكرا أحدا بسوء قط , سمعت يحيى , قال:','4247','2037','0');
INSERT INTO [t13041] VALUES (2048,'يزيد بن أبي زياد أبو عبد الله مولى بني هشام. حدثنا محمد بن أيوب بن يحيى بن الضريس، حدثنا محمد بن عبد العزيز بن أبي رزمة قال: سمعت النضر بن شميل يقول: سمعت شعبة، يقول: كان يزيد بن أبي زياد رفاعا. حدثنا عبد الله بن محمد، حدثنا أحمد بن عبد الله بن','4248','2037','0');
INSERT INTO [t13041] VALUES (2049,'يزيد بن أبي زياد الشامي عن الزهري. حدثني أحمد بن محمود قال: حدثنا عثمان بن سعيد قال: سألت يحيى بن معين , عن يزيد بن أبي زياد الشامي، عن الزهري، من هو يروي عنه مروان؟ قال: هذا ابن سنان أبو قرة ليس بشيء. حدثني آدم قال: سمعت البخاري قال: يزيد بن','4250','2037','0');
INSERT INTO [t13041] VALUES (2050,'يزيد بن سنان أبو فروة الرهاوي الجزري لا يتابع عليه , ولا يعرف إلا به. حدثنا محمد قال: حدثنا العباس قال: سمعت يحيى قال: يزيد بن سنان أبو فروة الجزري قد روى الكوفيون عنه وليس بثقة واسمه يزيد بن سنان. حدثنا محمد بن أحمد قال: حدثنا معاوية بن صالح قال:','4252','2037','0');
INSERT INTO [t13041] VALUES (2051,'يزيد بن سفيان أبو المهزم ولا يتابع على حديثه , ولا يعرف إلا به. حدثنا إبراهيم بن محمد، ومحمد بن إبراهيم قال: حدثنا مسلم قال: سمعت شعبة، يقول: كان أبو المهزم في مسجد ثابت مطروحا , لو أعطاه إنسان فلسا حدثه سبعين حديثا. حدثنا محمد قال: حدثنا عمرو بن','4254','2037','0');
INSERT INTO [t13041] VALUES (2052,'يزيد بن سفيان أبو خالد بصري عن سليمان التيمي، ولا يتابع على حديثه , ولا يعرف بالنقل , والحديث يروى من غير هذا الوجه بخلاف هذا اللفظ','4256','2037','0');
INSERT INTO [t13041] VALUES (2053,'يزيد بن عبد الملك النوفلي مديني حدثني آدم بن موسى قال: سمعت البخاري قال: يزيد بن عبد الملك بن مغيرة بن نوفل بن الحارث بن عبد المطلب المديني عن المقبري، وسهيل، أو يزيد بن حصيفة , قال أحمد: عنده مناكير. حدثنا زكريا بن يحيى الحلواني قال: سمعت أحمد بن','4258','2037','0');
INSERT INTO [t13041] VALUES (2054,'يزيد بن عمر التميمي عن أبيه، عن الحسن بن علي، ولا يتابعه إلا من هو دونه أو مثله. حدثني آدم بن موسى قال: سمعت البخاري، يقول: يزيد بن عمر التميمي , عن أبيه، عن الحسن بن علي، في حديثه نظر','4260','2037','0');
INSERT INTO [t13041] VALUES (2055,'يزيد بن أبي عدي بن حاتم ولا يصح إسناده','4262','2037','0');
INSERT INTO [t13041] VALUES (2056,'يزيد بن درهم','4264','2037','0');
INSERT INTO [t13041] VALUES (2057,'يزيد أبو سلمان كوفي حدثنا محمد قال: سمعت يحيى، يقول: أبو سلمان ضعيف , عن الأعمش، اسمه يزيد، وهو سيأتي','4266','2037','0');
INSERT INTO [t13041] VALUES (2058,'يزيد بن عطاء اليشكري قال يزيد بن عطاء اليشكري ضعيف. حدثنا محمد، حدثنا عباس قال: سمعت يحيى قال: يزيد بن عطاء مولى أبي عوانة ليس بشيء. قال: سمعت أبي سئل، عن يزيد بن عطاء، فقال: ليس به بأس , ثم قال: حديثه حديث مقارب','4267','2037','0');
INSERT INTO [t13041] VALUES (2059,'يزيد بن عياض بن يزيد ابن جعدبة قال البخاري: قال الحميدي: هو أخو أنس بن عياض. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى، يقول: يزيد بن عياض ابن جعدبة ضعيف، وفي موضع آخر: ليس بشيء. حدثنا محمد قال: حدثنا معاوية بن صالح قال: سمعت يحيى قال: يزيد','4269','2037','0');
INSERT INTO [t13041] VALUES (2060,'يزيد بن عوانة الكلبي عن محمد بن ذكوان، لا يتابع عليه','4271','2037','0');
INSERT INTO [t13041] VALUES (2061,'يزيد بن عمر عن مجالد، عن الشعبي، قاله البخاري , عن محمد بن سلام، عن يحيى بن واضح، ولم يتابع عليه','4273','2037','0');
INSERT INTO [t13041] VALUES (2062,'يزيد بن مروان الخلال حدثني أحمد بن محمود الهروي، حدثنا عثمان بن سعيد السجستاني قال: سمعت يحيى بن معين، يقول: يزيد بن مروان الخلال كذاب','4274','2037','0');
INSERT INTO [t13041] VALUES (2063,'يزيد بن كيسان أبو منين ويقال أبو إسماعيل. حدثني آدم بن موسى قال: سمعت البخاري قال: يزيد بن كيسان أبو منين , ويقال أبو إسماعيل , قال يحيى القطان: هو صالح وسط , ليس ممن يعتمد عليه','4275','2037','0');
INSERT INTO [t13041] VALUES (2064,'يزيد بن يوسف الشامي حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى بن معين قال: يزيد بن يوسف كان شاميا نزل على أبي عبد الله وكان يحدث عن القاسم بن مخيمرة، وقد حدث عنه الوليد بن مسلم وليس هو بشيء. وفي موضع آخر قال: يزيد بن يوسف صاحب الأوزاعي كان ببغداد','4277','2037','0');
INSERT INTO [t13041] VALUES (2065,'يحيى بن أيوب البجلي حدثنا أحمد بن علي قال: سمعت يحيى بن معين قال: يحيى بن أيوب البجلي ضعيف. حدثني أحمد بن محمود قال: حدثنا عثمان بن سعيد قال: قلت ليحيى بن معين: يحيى بن أيوب البجلي ما حاله؟ قال: ليس بشيء','4279','2037','0');
INSERT INTO [t13041] VALUES (2066,'يحيى بن أيوب أبو العباس المصري حدثنا محمد بن إسماعيل قال: حدثنا عمرو بن علي قال: سمعت ابن أبي مريم قال: حدثت مالكا بحديث حدثنا به يحيى بن أيوب عنه , فسألته عنه , فقال: كذب , وحدثته بآخر، عنه فقال: كذب. حدثنا عبد الله بن أحمد قال: سمعت أبي يقول:','4281','2037','0');
INSERT INTO [t13041] VALUES (2067,'يحيى بن أبي أنيسة الجزري أخو زيد حدثنا أحمد بن علي الأبار قال: حدثنا هارون بن سفيان، حدثنا عبد الله بن جعفر قال: سمعت عبيد الله بن عمرو يقول: قال زيد بن أبي أنيسة: لا تكتبوا عن أخي، فإنه يكذب. حدثني الخضر بن داود قال: حدثنا أحمد بن محمد قال: قال','4283','2037','0');
INSERT INTO [t13041] VALUES (2068,'يحيى بن بسطام المصفر حديثه غير محفوظ. حدثني آدم بن موسى قال: سمعت البخاري قال: يحيى بن بسطام المصفر كان يذكر بالقدر','4286','2037','0');
INSERT INTO [t13041] VALUES (2069,'يحيى بن راشد السماك بصري حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: يحيى بن راشد كان في مجلس معتمر وليس هو بشيء , كان يروي عن الجريري','4288','2037','0');
INSERT INTO [t13041] VALUES (2070,'يحيى بن الحارث الطائي عن أخيه زهدم، ولا يصح حديثه','4292','2037','0');
INSERT INTO [t13041] VALUES (2071,'يحيى بن الجزار ولقبه زبان. حدثنا عبد الله قال: حدثني أبي، قال: حدثني يحيى بن سعيد، حدثنا شعبة، عن الحكم قال: كان يحيى بن الجزار يغلو في التشيع. حدثنا يحيى بن عثمان قال: حدثنا نعيم، حدثنا حسين بن حسن، عن ابن عون قال: قال لي محمد: إني أعرض','4294','2037','0');
INSERT INTO [t13041] VALUES (2072,'يحيى بن أبي الحجاج المنقري حدثنا محمد قال: حدثنا معاوية قال: سمعت يحيى قال: يحيى بن أبي الحجاج ليس بشيء','4295','2037','0');
INSERT INTO [t13041] VALUES (2073,'يحيى بن حمزة قاضى دمشق حدثنا محمد، حدثنا عباس قال: سمعت يحيى قال: يحيى بن حمزة قاضى دمشق يرمى بالقدر','4297','2037','0');
INSERT INTO [t13041] VALUES (2074,'يحيى بن حميد عن قرة. حدثني آدم بن موسى قال: سمعت البخاري قال: يحيى بن حميد , عن قرة، لا يتابع','4298','2037','0');
INSERT INTO [t13041] VALUES (2075,'يحيى بن أبي حية أبو جناب الكلبي كوفي حدثنا محمد بن زكريا البلخي، حدثنا محمد بن المثنى قال: ما سمعت يحيى، ولا عبد الرحمن حدثنا عن أبي جناب يحيى بن أبي حية شيئا قط. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: أبو جناب ليس به بأس، وقال: أبو نعيم','4301','2037','0');
INSERT INTO [t13041] VALUES (2076,'يحيى بن أبي إسحاق حدثني عبد الله قال: قلت لابن يحيى بن أبي إسحاق قال: في حديثه نكارة قلت: فأيما أحب إليك هو أو عبد العزيز بن صهيب؟ قال: عبد العزيز أوثق','4303','2037','0');
INSERT INTO [t13041] VALUES (2077,'يحيى بن زياد بن عبد الرحمن الثقفي حدثنا آدم بن موسى قال: سمعت البخاري قال: يحيى بن زياد بن عبد الرحمن الثقفي فيه نظر','4305','2037','0');
INSERT INTO [t13041] VALUES (2078,'يحيى بن زكريا بن أبي زائدة حدثنا محمد بن عيسى قال: حدثنا عمرو بن شبة، حدثنا أبو نعيم، حدثنا يحيى بن زكريا، عن أبي بزة: ما هو بأهل أن أحدث عنه، عن إسماعيل بن أبي خالد قال: " ما رأيت أميرا على منبر أحسن من مصعب "','4307','2037','0');
INSERT INTO [t13041] VALUES (2079,'يحيى بن سعيد التميمي','4308','2037','0');
INSERT INTO [t13041] VALUES (2080,'يحيى بن سعيد الأموي حدثنا الخضر بن داود قال: حدثنا أحمد بن محمد قال: سمعت أبا عبد الله وذكر يحيى بن سعيد الأموي , فقال لي: ما كنت أرى أن عنده هذا الحديث الكثير , فإذا هم يزعمون أن عنده عن الأعمش حديثا كثيرا , وكان له أخ قد روى علما , يقال له: عبد','4310','2037','0');
INSERT INTO [t13041] VALUES (2081,'يحيى بن سعيد العطار شامي منكر الحديث','4311','2037','0');
INSERT INTO [t13041] VALUES (2082,'يحيى بن سعيد العبشمي عن ابن جريج، لا يتابع على حديثه , وليس بمشهور بالنقل','4313','2037','0');
INSERT INTO [t13041] VALUES (2083,'يحيى بن سعيد بن سالم القداح في حديثه مناكير','4315','2037','0');
INSERT INTO [t13041] VALUES (2084,'يحيى بن سلمة بن كهيل حدثنا عبد الله بن محمد المروزي، حدثنا أحمد بن عبد الله بن بشير، حدثنا سفيان بن عبد الملك قال: سمعت عبد الله بن المبارك، يقول: يحيى بن سلمة بن كهيل ضعيف. وحدثنا محمد، حدثنا عباس قال: سمعت يحيى، يقول: يحيى بن سلمة بن كهيل ليس','4317','2037','0');
INSERT INTO [t13041] VALUES (2085,'يحيى بن أبي سليمان المديني عن شعيب المقبري. حدثنا آدم بن موسى قال: سمعت البخاري قال: يحيى بن أبي سليم المقبري منكر الحديث','4320','2037','0');
INSERT INTO [t13041] VALUES (2086,'يحيى بن سليمان المديني عن هشام بن عروة، لا يتابع عليه','4322','2037','0');
INSERT INTO [t13041] VALUES (2087,'يحيى بن سليمان المحاربي كوفي عن مسعر، لا يصح حديثه','4324','2037','0');
INSERT INTO [t13041] VALUES (2088,'يحيى بن صالح الوحاظي الحمصي جهمي حدثنا عبد الله بن أحمد قال: سألت أبي عن الوحاظي، فقال: رأيته في جنازة أبي المغيرة , فجعل أبي يصفه , فقال أبي: أخبرني إنسان، من أصحاب الحديث قال: قال يحيى بن صالح لو ترك أصحاب الحديث عشرة أحاديث - يعني هذه التي في','4326','2037','0');
INSERT INTO [t13041] VALUES (2089,'يحيى بن صالح الأيلي عن إسماعيل بن أمية، عن عطاء، أحاديثه مناكير , أخشى أن تكون، منقلبة , هو بعمر بن قيس أشبه','4329','2037','0');
INSERT INTO [t13041] VALUES (2090,'يحيى بن عبد الله التيمي وهو الجابر كوفي. حدثنا عبد الله بن أحمد قال: سألت يحيى بن معين , عن يحيى الجابر، فقال: هو ضعيف الحديث. وقال أبي: يحيى الجابر ليس به بأس , ولكن الذي يحدث عنه يحيى أبو ماجد الحنفي لا يعرف رجل مجهول. حدثنا عبد الله بن أحمد،','4334','2037','0');
INSERT INTO [t13041] VALUES (2091,'يحيى من ولد بريد بن أبي بردة حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى قال: كان بجارنا رجل يقال له يحيى من ولد بريد بن أبي بردة , كان نازلا على السيب، وقد سمعت منه، وهو ضعيف','4336','2037','0');
INSERT INTO [t13041] VALUES (2092,'يحيى البكاء أبو سلمة حدثنا محمد بن أحمد بن سفيان، حدثنا عبيد الله بن عمر بن ميسرة قال: حدثنا حماد بن يزيد قال: اشتكى محمد بن واسع , فدخلت عليه أعوده , فبينا أنا عنده، أو قيل له: يحيى على الباب , قال: من يحيى؟ قال: أبو سلمة , قال: من أبو سلمة؟','4338','2037','0');
INSERT INTO [t13041] VALUES (2093,'يحيى بن عبد الحميد أبو زكريا الحماني حدثنا عبد الله بن أحمد قال: حدثنا محمد بن إسماعيل البخاري قال: كان أحمد وعلي يتكلمان في يحيى بن عبد الحميد الحماني. حدثنا عبد الله بن أحمد قال: قلت لأبي: بلغني أن ابن الحماني، حدث عن شريك، عن هشام بن عروة، عن','4340','2037','0');
INSERT INTO [t13041] VALUES (2094,'يحيى بن عبيد الله بن موهب المديني حدثنا عبد الله بن أحمد بن حنبل قال: سمعت أبي يقول: سألت يحيى بن سعيد يوما , عن يحيى بن عبد الله، فقال: من تحدث عنه؟ قيل له: ابن المبارك , روى عنه في الرقائق يعني الزهد. سمعت أبي مرة، أخرى يقول: يحيى بن عبيد الله','4341','2037','0');
INSERT INTO [t13041] VALUES (2095,'يحيى بن عباد البصري صاحب حديث ابن جريج في صدقات الفطر , فدلت روايته على أنه واه','4342','2037','0');
INSERT INTO [t13041] VALUES (2096,'يحيى بن عباد مجهول بالنقل لا يقيم الحديث','4347','2037','0');
INSERT INTO [t13041] VALUES (2097,'يحيى بن عثمان عن أبي حازم. حدثني آدم قال: سمعت البخاري قال: يحيى بن عثمان , عن أبي حازم، حديثه ليس بالقائم','4351','2037','0');
INSERT INTO [t13041] VALUES (2098,'يحيى بن عثمان التيمي عن يحيى بن عبد الله بن أبي مليكة، ولا يتابع على حديثه , ولا يعرف إلا به. حدثني آدم بن موسى قال: سمعت البخاري قال: يحيى بن عثمان التيمي , عن حيي بن عبد الله بن أبي مليكة منكر الحديث','4353','2037','0');
INSERT INTO [t13041] VALUES (2099,'يحيى بن عثمان الحربي بغدادي عن هقل لا يتابع على حديثه','4355','2037','0');
INSERT INTO [t13041] VALUES (2100,'يحيى بن عمرو بن مالك النكري لا يتابع على حديثه. حدثني محمد بن عيسى، حدثنا عباس قال: سمعت يحيى، يقول: يحيى بن عمرو بن مالك النكري ضعيف','4357','2037','0');
INSERT INTO [t13041] VALUES (2101,'يحيى بن عيسى الرملي حدثنا محمد بن عثمان قال: سمعت يحيى بن معين، وذكروا له حديث يحيى بن عيسى الرملي , فقال: كان ضعيفا وكان يسكن الرملة. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى، يقول: يحيى بن عيسى الرملي ليس بشيء. حدثنا أبي قال: سألت أبي عن','4360','2037','0');
INSERT INTO [t13041] VALUES (2102,'يحيى بن عقبة بن أبي العيزار حدثني محمد بن عيسى، حدثنا عباس قال: سمعت يحيى، يقول: يحيى بن عقبة أبي العيزار ليس بشيء. حدثني آدم قال: سمعت البخاري قال: يحيى بن عقبة بن أبي العيزار منكر الحديث','4361','2037','0');
INSERT INTO [t13041] VALUES (2103,'يحيى بن أبي روق عن أبيه، عن الضحاك. حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى قال: يحيى بن أبي روق قد رأيته , وليس بثقة','4363','2037','0');
INSERT INTO [t13041] VALUES (2104,'يحيى بن غالب العبشمي في إسناده نظر','4365','2037','0');
INSERT INTO [t13041] VALUES (2105,'يحيى بن أبي كثير اليمامي ذكر بالتدليس. حدثنا يحيى بن عثمان، حدثنا نعيم بن حماد، حدثنا ابن المبارك، عن همام قال: كما يحدث يحيى بن أبي كثير بالغداة , فإذا كان بالعشي قلبه عنا. حدثنا محمد بن إسماعيل، حدثنا الحسن بن علي، حدثنا يزيد بن هارون، حدثنا','4367','2037','0');
INSERT INTO [t13041] VALUES (2106,'يحيى بن كثير أبو النضر صاحب البصري منكر الحديث','4368','2037','0');
INSERT INTO [t13041] VALUES (2107,'يحيى بن مالك بن أنس الأصبحي عن أبيه، بمناكير','4371','2037','0');
INSERT INTO [t13041] VALUES (2108,'يحيى بن ميمون بن عطاء القرشي أبو أيوب التمار بصري حدثني آدم بن موسى قال: سمعت البخاري قال: يحيى بن ميمون بن عطاء القرشي , قال عمرو بن علي: يحيى بن ميمون كذاب','4373','2037','0');
INSERT INTO [t13041] VALUES (2109,'يحيى بن محمد بن قيس أبو زكير بصري عن هشام بن عروة، وعمرو بن أبي عمرو، لا يتابع على حديثه','4376','2037','0');
INSERT INTO [t13041] VALUES (2110,'يحيى بن محمد بن عباد بن هانئ الشجري عن محمد بن إسحاق، في حديثه مناكير وأغاليط , وكان ضريرا , فيما بلغني أنه يلقن حدثنا محمد بن أيوب، حدثنا إبراهيم بن يحيى بن محمد بن عباد بن هانئ الشجري، حدثنا أبي، عن محمد بن إسحاق، عن الزهري، عن عبد الله بن كعب بن','4380','2037','0');
INSERT INTO [t13041] VALUES (2111,'يحيى بن محمد الجاري مديني حدثني آدم بن موسى قال: سمعت البخاري قال: يحيى بن محمد الجاري يتكلمون فيه','4382','2037','0');
INSERT INTO [t13041] VALUES (2112,'يحيى بن المتوكل المكفوف صاحب بهية. حدثنا عبد الله بن محمد بن سعدون المروزي، حدثنا أحمد بن عبد الله بن بشير، حدثنا سفيان بن عبد الملك قال: سمعت ابن المبارك، يقول: أبو عقيل المحجوب يحيى المتوكل صاحب بهية , عن عائشة، هو ضعيف. حدثنا محمد، حدثنا عباس','4384','2037','0');
INSERT INTO [t13041] VALUES (2113,'يحيى بن مسلم كوفي أبو الضحاك روى عنه، وكيع. حدثنا محمد بن عيسى، حدثنا عباس قال: سمعت يحيى قال: يحيى بن مسلم كوفي ضعيف. ومن حديثه ما حدثناه محمد بن زكريا، حدثنا نصر بن علي، حدثنا عبد الله بن داود، عن يحيى بن مسلم، أن زيد بن وهب، أتى النبي صلى','4386','2037','0');
INSERT INTO [t13041] VALUES (2114,'يحيى بن مسلمة القعنبي عن حماد بن زيد، لا يتابع على حديثه , وقد حدث بمناكير','4387','2037','0');
INSERT INTO [t13041] VALUES (2115,'يحيى بن المنذر الكندي كوفي عن إسرائيل، في حديثه نظر','4389','2037','0');
INSERT INTO [t13041] VALUES (2116,'يحيى بن المثنى أبو شعبة عن نعيم بن أبي هند، حديثه غير محفوظ , ولا يعرف بالنقل','4391','2037','0');
INSERT INTO [t13041] VALUES (2117,'يحيى بن هاشم السمسار كان يضع الحديث على الثقات','4393','2037','0');
INSERT INTO [t13041] VALUES (2118,'يحيى بن نصر بن حاجب القرشي منكر الحديث','4395','2037','0');
INSERT INTO [t13041] VALUES (2119,'يحيى بن يمان لا يتابع على حديثه','4397','2037','0');
INSERT INTO [t13041] VALUES (2120,'يحيى بن يعلى الأسلمي القطواني حدثنا عبد الله بن أحمد قال: سئل أبي عن يحيى بن يعلى الأسلمي، فقال: لا أخبرك. حدثني آدم بن موسى قال: سمعت البخاري قال: يحيى بن يعلى الأسلمي القطواني مضطرب الحديث','4400','2037','0');
INSERT INTO [t13041] VALUES (2121,'يحيى بن يعقوب بن مدرك أبو طالب القاص حدثني آدم بن موسى قال: سمعت البخاري قال: يحيى بن يعقوب بن مدرك أبو طالب القاص منكر الحديث','4403','2037','0');
INSERT INTO [t13041] VALUES (2122,'يحيى بن يزيد الهنائي حدثني محمد بن أحمد بن محمد بن حماد، حدثنا معاوية بن صالح قال: سمعت يحيى بن معين قال: محمد بن يزيد الهنائي بصري صويلح , يروي عن أنس بن مالك','4404','2037','0');
INSERT INTO [t13041] VALUES (2123,'يحيى بن العلاء الرازي حدثنا زكريا بن يحيى الحلواني قال: حدثنا محمد بن عمارة بن صبيح الكوفي، حدثنا عون بن سلام، حدثنا يحيى بن العلاء، قال أبو يحيى: وكان معنا فضلك , فقال فضلك: سمعت إسحاق الكوفي، عن عبد الرزاق قال: سمعت مكيا، وذكر يحيى بن العلاء ,','4405','2037','0');
INSERT INTO [t13041] VALUES (2124,'يعقوب بن مجاهد أبو حرزة القاص حدثنا محمد بن عيسى، حدثنا إبراهيم بن سعيد الجوهري، حدثنا يحيى بن معين قال: أبو حرزة القاص اسمه يعقوب بن مجاهد , كان في الحديث صويلحا , مات سنة تسع وأربعين','4406','2037','0');
INSERT INTO [t13041] VALUES (2125,'يعقوب بن إبراهيم أبو يوسف القاضي حدثنا محمد بن عثمان قال: سمعت يحيى بن معين سئل عن أبي يوسف القاضي، فقال: لم يكن يعرف الحديث. حدثنا محمد بن زكريا، حدثنا أبو موسى محمد بن المثنى قال: ما سمعت عبد الرحمن، يحدث عن أبي يوسف، شيئا قط. حدثنا محمد بن','4408','2037','0');
INSERT INTO [t13041] VALUES (2126,'يعقوب بن إبراهيم النيلي عن محمد بن عجلان، لا يتابع عليه من هذا الوجه , وهو معروف بغير هذا الإسناد','4411','2037','0');
INSERT INTO [t13041] VALUES (2127,'يعقوب بن محمد بن عيسى الزهري في حديثه وهم كثير , ولا يتابعه عليه إلا من هو نحوه. حدثنا عبد الله بن أحمد بن حنبل قال: سمعت أبي يقول: يعقوب بن محمد الزهري ليس بشيء , ليس يسوى شيئا','4413','2037','0');
INSERT INTO [t13041] VALUES (2128,'يعقوب بن عطاء بن أبي رباح حدثنا محمد بن عيسى، حدثنا صالح، حدثنا علي قال: سمعت يحيى، يذكر عن ابن جريج قال: أخبرني يعقوب بن عطاء بن أبي رباح، عن عطاء، عن ابن عباس، في الإيلاء واحدة بائنة , قال: فدخلت على أبيه فأنكره , فخرجت إليه , فقال: قد سمعته','4415','2037','0');
INSERT INTO [t13041] VALUES (2129,'يعقوب بن حميد بن كاسب أبو يوسف المديني كان بمكة. حدثنا محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى بن معين، يقول: يعقوب بن حميد بن كاسب ليس بشيء. حدثنا زكريا بن يحيى الحلواني قال: رأيت أبا داود السجستاني صاحب أحمد بن حنبل قد ظاهر بحديث ابن كاسب ,','4416','2037','0');
INSERT INTO [t13041] VALUES (2130,'يعقوب بن الوليد المديني أبو يوسف حدثنا عبد الله بن أحمد قال: سمعت أبي يقول: يعقوب بن الوليد أبو يوسف من أهل المدينة , وكان من الكذابين الكبار , يحدث عن أبي حازم , عن سهل بن سعد , عن النبي صلى الله عليه وسلم , كان يأكل البطيخ بالرطب، وسمعت أبي مرة،','4421','2037','0');
INSERT INTO [t13041] VALUES (2131,'يوسف بن إبراهيم أبو شيبة التميمي حدثني آدم بن موسى قال: سمعت النحاس قال: يوسف بن إبراهيم أبو شيبة التميمي اللال عنده عجائب','4423','2037','0');
INSERT INTO [t13041] VALUES (2132,'يوسف بن طهمان مولى آل معاوية. حدثني آدم بن موسى قال: سمعت البخاري قال: سمعت يوسف بن طهمان، مولى آل معاوية , ولا يتابع عليه','4425','2037','0');
INSERT INTO [t13041] VALUES (2133,'يوسف بن محمد بن يزيد بن صيفي ولا يتابع على حديثه. حدثني آدم بن موسى قال: سمعت البخاري قال: يوسف بن محمد بن يزيد بن صيفي بن صهيب فيه نظر','4427','2037','0');
INSERT INTO [t13041] VALUES (2134,'يوسف بن إسحاق بن أبي إسحاق الهمداني يخالف في حديثه , ولعله أتى من منصور بن وردان','4429','2037','0');
INSERT INTO [t13041] VALUES (2135,'يوسف بن السفر عن الأوزاعي يحدث بمناكير. حدثنا محمد بن أحمد بن حماد قال: حدثنا سعيد بن محمد البيروني قال: سمعت إنسانا، قال لدحيم: ما تقول في يوسف بن السفر الذي يروي عن الأوزاعي وكان ينزل بيروت؟ فقال له دحيم: لا في السماء ولا في الأرض. حدثني آدم','4431','2037','0');
INSERT INTO [t13041] VALUES (2136,'يوسف بن خالد السمتي حدثنا عبد الله بن أحمد قال: سمعت يحيى، يقول: وذكر يوسف بن خالد السمتي فقال: كذاب خبيث عدو الله , رجل سوء , يخاصم في الدين. لا يحدث عنه أحد فيه خير , رأيته ما لا أحصي بالبصرة. حدثنا العباس بن الفضل قال: سمعت يحيى بن معين، يقول','4435','2037','0');
INSERT INTO [t13041] VALUES (2137,'يوسف بن زياد أبو عبد الله كان يحفظ ولا يتابع على حديثه , ولا يعرف إلا به','4437','2037','0');
INSERT INTO [t13041] VALUES (2138,'يوسف بن أسباط كان من العابدين دفن كتبه , فحدث بعد، من حفظه بأحاديث منها ما لا أصل له , ومنها ما يخطئ فيه','4439','2037','0');
INSERT INTO [t13041] VALUES (2139,'يوسف بن عطية أبو سهل الصفار حدثني آدم قال: سمعت البخاري قال: يوسف بن عطية منكر الحديث. حدثني محمد بن عيسى قال: حدثنا عباس قال: سمعت يحيى، يقول: يوسف بن عطية الصفار ليس بشيء','4441','2037','0');
INSERT INTO [t13041] VALUES (2140,'يوسف بن محمد بن المنكدر عن أبيه، ولا يتابع على حديثه','4443','2037','0');
INSERT INTO [t13041] VALUES (2141,'يوسف بن عبدة أبو عبدة له أحاديث مناكير , عن حميد، وثابت , كأنه ضعفه محمد بن إسماعيل','4447','2037','0');
INSERT INTO [t13041] VALUES (2142,'يونس بن أبي إسحاق السبيعي حدثنا محمد بن عيسى قال: حدثنا بندار قال: قال سلم بن قتيبة: قدمت من الكوفة فقال لي شعبة من لقيت؟ قال: لقيت فلانا , وفلانا , ولقيت يونس بن أبي إسحاق , قال: ما حدثك؟ فأخبرته , فسكت ساعة , وقلت له , قال: حدثنا بكر بن ماعز','4449','2037','0');
INSERT INTO [t13041] VALUES (2143,'يونس بن خباب كان ممن يغلو في الرفض. حدثنا علي بن عبد العزيز، حدثنا إبراهيم بن زياد سبلان , حدثنا عباد بن عباد قال: أتيت يونس بن خباب بمنى عند المباره , وهو يقص , فسألته عن حديث القبر , فحدثني به , ثم قال: إن فيه شيئا قد كتمته المرجئة الفسقة , قلت:','4451','2037','0');
INSERT INTO [t13041] VALUES (2144,'يونس بن شعيب حديثه غير محفوظ. حدثني آدم قال: سمعت البخاري قال: يونس بن شعيب منكر الحديث','4452','2037','0');
INSERT INTO [t13041] VALUES (2145,'يونس بن أبي يعفور العبدي حدثنا محمد بن عثمان قال: سمعت يحيى، يسأل عن يونس بن أبي يعفور، فقال لي: أبوك يروي عنه , وكان ضعيفا. حدثنا محمد، حدثنا عباس قال: سمعت يحيى، يقول: يونس بن أبي يعفور ضعيف','4454','2037','0');
INSERT INTO [t13041] VALUES (2146,'يونس بن سليم الصنعاني لا يتابع على حديثه , ولا يعرف إلا به. حدثنا عبد الله بن أحمد قال: حدثني أبي قال: سألت عبد الرزاق عن يونس بن سليم، فقال: هو أمثل من عمرو , قال أبي: هو عمرو بن عبد الله , روى عنه معمر','4455','2037','0');
INSERT INTO [t13041] VALUES (2147,'يونس بن بكير حدثنا محمد قال: حدثنا عباس قال: سمعت يحيى، يقول: يونس كان صدوقا , كان يتبع السلطان , وكان مرجئا','4457','2037','0');
INSERT INTO [t13041] VALUES (2148,'يونس بن الحارث الطائفي حدثنا عبد الله بن أحمد قال: سألت أبي عن يونس بن الحارث الطائفي، فضعفه. حدثنا محمد بن عيسى قال: حدثنا العباس بن محمد قال: سمعت يحيى، يقول: يونس بن الحارث الطائفي ضعيف','4458','2037','0');
INSERT INTO [t13041] VALUES (2149,'يونس الكذوب حدثنا عبد الله بن أحمد قال: سمعت أبي قال: قلت ليونس الصدوق: حماد بن سلمة عن من، كان يعيد في آخر عمره؟ قال: عن سعيد الجريري , يعني يحدث عنه , قال أبي: ورأيت يونس الصدوق عند إبراهيم بن سعد , أظن أبي قال: فجعل يذاكره , ويستخرج منه , أو','4460','2037','0');
INSERT INTO [t13041] VALUES (2150,'يسع بن طلحة عن عطاء. حدثني آدم قال: سمعت البخاري قال: يسع بن طلحة منكر الحديث','4461','2037','0');
INSERT INTO [t13041] VALUES (2151,'يمان بن المغيرة العنزي أبو حذيفة حدثنا محمد، حدثنا عباس قال: سمعت يحيى بن معين، يقول: يمان بن المغيرة العنزي ليس حديثه بشيء. حدثني آدم قال: سمعت البخاري قال: يمان بن المغيرة العنزي أبو حذيفة منكر الحديث','4463','2037','0');
INSERT INTO [t13041] VALUES (2152,'يمان بن عدي الحمصي حدثني آدم بن موسى قال: سمعت البخاري قال: يمان بن عدي الحمصي في حديثه نظر','4465','2037','0');
INSERT INTO [t13041] VALUES (2153,'ياسين بن معاذ الزيات أبو خلف حدثنا محمد، حدثنا عباس قال: سمعت يحيى قال: ياسين بن معاذ الزيات ضعيف. حدثني أحمد بن محمود قال: حدثنا عثمان بن سعيد قال: قلت ليحيى: ياسين بن معاذ الزيات ليس بشيء. حدثني آدم قال: سمعت البخاري، يقول: ياسين بن معاذ','4467','2037','0');
INSERT INTO [t13041] VALUES (2154,'ياسين بن سيار العجلي كوفي عن إبراهيم بن محمد ابن الحنفية. حدثني آدم بن موسى قال: سمعت البخاري قال: ياسين بن سيار العجلي كوفي في حديثه نظر','4469','2037','0');
INSERT INTO [t13041] VALUES (2155,'يغنم بن سالم بن قيس عن أنس، منكر الحديث','4471','2037','0');
