INSERT INTO [t13042] VALUES (1,'مقدمة الحمد لله رب العالمين أبدا وصلى الله على محمد دائما سرمدا. فهمت قولك أخي، ألهمك الله الصواب وأراك المحاب، وما قد أذكرتني به من أمر كتاب العزلة وبعثتني عليه من إتمامه بعد ابتدائه والفراغ منه بعد إنشائه. وما يشكونه مع ذلك من طول مأخذه. وما خفته','3','0','0');
INSERT INTO [t13042] VALUES (2,'باب في حكاية ما احتج به من أنكر العزلة قال المنكر لها: قد أمر الله سبحانه بالاجتماع وحض عليه ونهى عن الافتراق وحذر منه فقال تعالى ذكره: واعتصموا بحبل الله جميعا ولا تفرقوا واذكروا نعمة الله عليكم إذ كنتم أعداء فألف بين قلوبكم فأصبحتم بنعمته إخوانا','4','0','0');
INSERT INTO [t13042] VALUES (3,'باب ما جاء في العزلة قال الله تعالى ذكره حكاية عن إبراهيم عليه السلام: وأعتزلكم وما تدعون من دون الله وأدعو ربي عسى ألا أكون بدعاء ربي شقيا اعتصم خليل الله سبحانه بالعزلة واستظهر بها على قومه عند جفائهم إياه وخذلانهم له في عبادة الأصنام ومعاندة الحق','9','0','0');
INSERT INTO [t13042] VALUES (4,'باب في ذكر أسباب تسهل على المرء العزلة وتفطمه عن صحبة كثير من ذوي الخلطة','66','0','0');
INSERT INTO [t13042] VALUES (5,'باب في خفة الظهر وقلة العيال والأهل','86','0','0');
INSERT INTO [t13042] VALUES (6,'باب في ترك الاستكثار من الأصدقاء وما يستحب من قلة الالتقاء','95','0','0');
INSERT INTO [t13042] VALUES (7,'كتاب جامع في ترك ما لا يعني ورفض الاشتغال بما لا يجدي','111','0','0');
INSERT INTO [t13042] VALUES (8,'باب في التحذير من قرناء السوء وحسن ارتياد الجليس والصاحب','127','0','0');
INSERT INTO [t13042] VALUES (9,'ذكر أبواب تشتمل على وصف عوام الناس وبيان أحوالهم والتحذير من آفاتهم وما جاء من فساد الزمان وذم أهله وما يدخل في ذلك من كلام يرغب في العزلة وينهى عن الإكثار، من الخلطة','147','0','0');
INSERT INTO [t13042] VALUES (10,'باب في اختلاف طبقات الناس','148','0','0');
INSERT INTO [t13042] VALUES (11,'باب في ذكر أخلاق العامة وما يوجد فيهم من قلة الاستفاضة','157','0','0');
INSERT INTO [t13042] VALUES (12,'باب في التحذير من عوام الناس والتحرز منهم بسوء الظن فيهم وقلة الثقة بهم وترك الاستنامة إليهم','162','0','0');
INSERT INTO [t13042] VALUES (13,'باب في فساد الزمان وأهله','182','0','0');
INSERT INTO [t13042] VALUES (14,'باب فيمن يتمنى الموت وآثر المرض والعمى على لقاء الناس','206','0','0');
INSERT INTO [t13042] VALUES (15,'باب في ترك الاعتداد بعوام الناس وقلة الاكتراث بهم والتحاشي لهم','211','0','0');
INSERT INTO [t13042] VALUES (16,'تفسير الغوغاء','224','0','0');
INSERT INTO [t13042] VALUES (17,'باب في فساد الخاصة وما جاء في علماء السوء وذكر آفاتهم','233','0','0');
INSERT INTO [t13042] VALUES (18,'باب في آفات القراء','253','0','0');
INSERT INTO [t13042] VALUES (19,'باب في فساد الأئمة وما جاء في الإقلال من صحبة السلاطين','266','0','0');
INSERT INTO [t13042] VALUES (20,'باب في لزوم القصد في حالتي العزلة والخلطة قد انتهى منا الكلام في أمر العزلة إلى حيث شرطنا أن نبلغه وأوردنا فيها من الأخبار ما خفنا أن نكون قد حسنا معه الجفاء من حيث أردنا الاحتراز منه، وليس إلى هذا أجرينا ولا إياه أردنا فإن الإغراق في كل شيء مذموم وخير','280','0','0');
