INSERT INTO [t13046] VALUES (1,'لم أدعكم لرغبة ولا لرهبة نزلت، ولكن تميم الداري أخبرني أن ناسا من أهل فلسطين ركبوا البحر','1','0','0');
INSERT INTO [t13046] VALUES (2,'ذكر الدجال فقال: أعور هجان أزهر جفال , كأن رأسه أصلة أشبه الناس بعبد العزى بن قطن , ولكن','2','0','0');
INSERT INTO [t13046] VALUES (3,'الدجال أعور عين اليمنى , وعينه الأخرى كأنها عنبة طافية','3','0','0');
INSERT INTO [t13046] VALUES (4,'فحدثهم عن الأعور الدجال , حتى خلع قلوبنا فرقا من الدجال، فخرج رسول الله صلى الله عليه','4','0','0');
INSERT INTO [t13046] VALUES (5,'وفتنته شديدة نعوذ بالله من شرها','5','0','0');
INSERT INTO [t13046] VALUES (6,'عامة أتباعه بعد اليهود أعاريب الناس , ونصارى العرب والنساء , يسحرون أعين الناس , ويخرج','6','0','0');
INSERT INTO [t13046] VALUES (7,'بين يدي الساعة ثلاث سنوات , تمسك السماء أول سنة ثلث قطرها , والأرض ثلث نباتها , والسنة','7','0','0');
INSERT INTO [t13046] VALUES (8,'بعدي الكذاب المضل , وإن رأسه من ورائه حبك حبك , يقول: أنا ربكم , ومن قال: ربي الله لا إله','8','0','0');
INSERT INTO [t13046] VALUES (9,'ويل أمها من قرية يتركها أهلها أعمر ما تكون , يأتيها الدجال , فيجد على كل باب من أبوابها','9','0','0');
INSERT INTO [t13046] VALUES (10,'يمكث أبوا الدجال لا يولد لهما ثلاثين عاما , ثم يولد لهما غلام أعور أضر شيء وأقله نفعا ,','10','0','0');
INSERT INTO [t13046] VALUES (11,'من سمع بالدجال فلينأ عنه , فإن الرجل يأتيه، وهو يحسب أنه مؤمن فما يزال به حتى يتبعه مما','11','0','0');
INSERT INTO [t13046] VALUES (12,'لا تزال طائفة من أمتي يقاتلون على الحق , ظاهرين على من ناوأهم , حتى يقاتل آخرهم المسيح','12','0','0');
INSERT INTO [t13046] VALUES (13,'وما يوم الخلاص؟ فجعل يرددها فقالوا: يا رسول الله , وما يوم الخلاص؟ قال يجيء الدجال حتى','13','0','0');
INSERT INTO [t13046] VALUES (14,'من سمع منكم بالدجال فليفر منه , فإنه يأتيه الرجل يحسب أنه مؤمن , فيتبعه مما يرى معه من','14','0','0');
INSERT INTO [t13046] VALUES (15,'يتعوذ بالله من شر المحيا والممات ومن شر المسيح الدجال','15','0','0');
INSERT INTO [t13046] VALUES (16,'الدجال أعور , وربكم ليس بأعور , مكتوب بين عينيه: كافر , يقرؤه كل مؤمن قارئ أو غير','16','0','0');
INSERT INTO [t13046] VALUES (17,'لم يكن نبي إلا وقد أنذر الدجال أمته , وإني أنذركموه , إنه أعور ذو حدقة جاحظة , لا تخفى ,','17','0','0');
INSERT INTO [t13046] VALUES (18,'جهدا شديدا يكون بين يدي الدجال فقلت: يا رسول الله أين العرب يومئذ؟ قال: يا عائشة إن العرب','18','0','0');
INSERT INTO [t13046] VALUES (19,'يطأ الأرض كلها إلا مكة والمدينة , فيأتي المدينة فيجد بكل نقب من نقابها صفوفا من الملائكة','19','0','0');
INSERT INTO [t13046] VALUES (20,'لن تقوم - يعني الساعة - حتى يكون قبلها الدجال , وعيسى ابن مريم صلى الله عليه','20','0','0');
