INSERT INTO [t13046] VALUES (23,'يخرج الدجال من أرض يقال لها: خراسان , وأشار بيده نحو المشرق , ومعه قوم كأن وجوههم','23','0','0');
INSERT INTO [t13046] VALUES (24,'الدجال يخرج من أرض يقال لها: خراسان , يتبعه قوم كأن وجوههم المجان','24','0','0');
INSERT INTO [t13046] VALUES (25,'خروج الملحمة فتح القسطنطينية , وفتح القسطنطينية خروج الدجال ثم ضرب على فخذ الذي حدثه -','25','0','0');
INSERT INTO [t13046] VALUES (26,'انطلق رسول الله صلى الله عليه وسلم إلى ابن صائد , فلم يجده , فرأى أمه فسألها عنه فقالت:','26','0','0');
INSERT INTO [t13046] VALUES (27,'فناره جنة , وجنته نار معه ملكان من الملائكة يشبهان نبيين من الأنبياء , لو شئت سميتها','27','0','0');
INSERT INTO [t13046] VALUES (28,'لم يكن نبي قبلي إلا حذره أمته , وسأصفه لكم بما لم يصفه نبي قبلي: إنه أعور , مكتوب بين','28','0','0');
INSERT INTO [t13046] VALUES (29,'ذكر رسول الله صلى الله عليه وسلم الدجال ذات غداة , فخفض فيه ورفع , حتى ظنناه في طائفة','29','0','0');
INSERT INTO [t13046] VALUES (30,'أخوف عليكم عندي , أن يقوم الرجل يزين صلاته لما يرى من نظر الرجل إليه','30','0','0');
INSERT INTO [t13046] VALUES (31,'الدجال أعور بين عينيه: كافر , يقرؤه كل مؤمن قارئ وغير قارئ وقد قال حماد أيضا: مكتوب بين','31','0','0');
INSERT INTO [t13046] VALUES (32,'مكتوب بين عينيه: كافر يقرؤها كل مسلم','32','0','0');
INSERT INTO [t13046] VALUES (33,'إن معه نهرا من نار , ونهرا من ماء , فالذي ترون أنه نار ماء , والذي ترون أنه ماء نار , فمن','33','0','0');
INSERT INTO [t13046] VALUES (34,'ما بعث نبي إلا وقد أنذر أمته الأعور الكذاب , ألا وإنه أعور وليس ربكم بأعور , وأن بين','34','0','0');
INSERT INTO [t13046] VALUES (35,'دعا رسول الله صلى الله عليه وسلم ابن صائد , فقال: قد خبأت لك خبيا فقال ابن صائد: دخ ,','35','0','0');
INSERT INTO [t13046] VALUES (36,'ينزل الدجال في هذه السبخة مجرى قناة , فيكون أكثر من يخرج إليه النساء , حتى إن الرجل ليرجع','36','0','0');
INSERT INTO [t13046] VALUES (37,'يخرج من خلة بين الشام والعراق , فيعيث يمينا ويعيث شمالا , عباد الله اثبتوا , يا عباد الله','37','0','0');
INSERT INTO [t13046] VALUES (38,'الدجال لا يولد له ولا يدخل المدينة ولا مكة قال: قلت: بلى , قال: فقد ولد لي وقد خرجت من','38','0','0');
INSERT INTO [t13046] VALUES (39,'يخرج الدجال من غضبة يغضبها','39','0','0');
INSERT INTO [t13046] VALUES (40,'يمكث أبوا الدجال ثلاثين عاما لا يولد لهما , ثم يولد لهما غلام أعور، أضر شيء , وأقله نفعا','40','0','0');
INSERT INTO [t13046] VALUES (41,'أحذركم المسيح وأنذركموه وكل نبي كان قبلي قد أنذر قومه , وهو فيكم أيتها الأمة يكون قبل','41','0','0');
INSERT INTO [t13046] VALUES (42,'لا يأتي أربعة مساجد: مكة , والمدينة , وبيت المقدس , والطور أحسبه قال: يسلط على رجل فيقتله','42','0','0');
INSERT INTO [t13046] VALUES (43,'يأتي الدجال وهو محرم عليه أن يدخل أنقاب المدينة , فيخرج إليه رجل يومئذ - وهو خير الناس ,','43','0','0');
INSERT INTO [t13046] VALUES (44,'تفترقون أيها الناس لخروجه ثلاث فرق , فرقة تتبعه , وفرقة تلحق بأرض بها منابت الشيح , وفرقة','44','0','0');
INSERT INTO [t13046] VALUES (45,'أول أهل أبيات يقرعهم الدجال، قال: من هم؟ قال: أنتم يا أهل الكوفة','45','0','0');
INSERT INTO [t13046] VALUES (46,'لو أصبح ببابك , لأوشك بعضكم أن يشكو إليه الحفاء من السرعة إليه','46','0','0');
INSERT INTO [t13046] VALUES (47,'أكثر من يتبعه اليهود , وأولاد الموامس','47','0','0');
INSERT INTO [t13046] VALUES (48,'إذا رأى الدجال عيسى ابن مريم ماث كما تماث الشحمة','48','0','0');
INSERT INTO [t13046] VALUES (49,'يخرج الدجال من كوثى','49','0','0');
INSERT INTO [t13046] VALUES (50,'ثم ينزل عيسى عليه السلام فيمكث في الأرض أربعين صباحا , اليوم كالساعة , والشهر كالجمعة ,','50','0','0');
