INSERT INTO [t13048] VALUES (1,'باب ذكر الروايات عن النبي صلى الله عليه وسلم في فضل التفقه والأمر به والحث عليه والترغيب فيه قوله عليه السلام: من يرد الله به خيرا يفقهه الدين','2','0','0');
INSERT INTO [t13048] VALUES (2,'ذكر قول النبي صلى الله عليه وسلم: تجدون الناس معادن خيارهم في الجاهلية خيارهم في الإسلام إذا فقهوا','29','0','0');
INSERT INTO [t13048] VALUES (3,'فضل مجالس الفقه على مجالس الذكر','34','0','0');
INSERT INTO [t13048] VALUES (4,'ذكر الرواية أن حلق الفقه هي رياض الجنة','41','0','0');
INSERT INTO [t13048] VALUES (5,'فضل التفقه على كثير من العبادات','49','0','0');
INSERT INTO [t13048] VALUES (6,'تفضيل الفقهاء على العباد','64','0','0');
INSERT INTO [t13048] VALUES (7,'ذكر الرواية أنه يقال للعابد: ادخل الجنة ويقال للفقيه: اشفع','74','0','0');
INSERT INTO [t13048] VALUES (8,'ذكر الرواية عن النبي صلى الله عليه وسلم أنه قال: ما عبد الله تعالى بشيء أفضل من فقه في دين','77','0','0');
INSERT INTO [t13048] VALUES (9,'ذكر الرواية عن النبي صلى الله عليه وسلم: أن فقيها واحدا أشد على الشيطان من ألف عابد','91','0','0');
INSERT INTO [t13048] VALUES (10,'تأويل قول الله تعالى: أطيعوا الله وأطيعوا الرسول وأولي الأمر منكم أنهم الفقهاء','102','0','0');
INSERT INTO [t13048] VALUES (11,'تأويل قوله تعالى: ومن يؤت الحكمة فقد أوتي خيرا كثيرا أنها الفقه','119','0','0');
INSERT INTO [t13048] VALUES (12,'ذكر الرواية , أن الله يبعث يوم القيامة كل عبد على مرتبته التي مات عليها','125','0','0');
INSERT INTO [t13048] VALUES (13,'ذكر الرواية أن الله تعالى لا يخلي الوقت من فقيه أو متفقه','130','0','0');
INSERT INTO [t13048] VALUES (14,'ذكر من ارتفع من العبيد بالفقه حتى جلس مجالس الملوك','133','0','0');
INSERT INTO [t13048] VALUES (15,'ذكر أحاديث وأخبار شتى يدل جميعها على جلالة الفقه والفقهاء','140','0','0');
INSERT INTO [t13048] VALUES (16,'ذكر ما روي أن من إدبار الدين ذهاب الفقهاء','173','0','0');
INSERT INTO [t13048] VALUES (17,'وجوب التفقه في الدين على كافة المسلمين','180','0','0');
INSERT INTO [t13048] VALUES (18,'ما جاء في تعليم الرجال أولادهم ونساءهم والسادات عبيدهم وإماءهم','194','0','0');
INSERT INTO [t13048] VALUES (19,'ذكر ضرب النبي صلى الله عليه وسلم المثل في مراتب من تفقه في الدين','202','0','0');
INSERT INTO [t13048] VALUES (20,'ذكر تقسيم أمير المؤمنين علي بن أبي طالب أحوال الناس في طلب العلم وتركه','204','0','0');
INSERT INTO [t13048] VALUES (21,'باب بيان الفقه','214','0','0');
INSERT INTO [t13048] VALUES (22,'باب بيان أصول الفقه أصول الفقه: الأدلة التي يبنى عليها الفقه , وهي: كتاب الله سبحانه , وسنة رسوله صلى الله عليه وسلم , مما حفظ عنه خطابا وفعلا وإقرارا , وإجماع الأمة من أهل الاجتهاد , فهي ثلاثة أصول , ونحن نذكر كل أصل منها على التفصيل , وكيف يرتب بعضها','221','0','0');
INSERT INTO [t13048] VALUES (23,'القول في الأصل الأول: وهو الكتاب قال الله تعالى: وإنه لكتاب عزيز لا يأتيه الباطل من بين يديه ولا من خلفه تنزيل من حكيم حميد وقال الله تعالى: كتاب أنزلناه إليك لتخرج الناس من الظلمات إلى النور بإذن ربهم إلى صراط العزيز الحميد وقال تعالى: وأنزلنا إليك','222','0','0');
INSERT INTO [t13048] VALUES (24,'باب القول في المحكم والمتشابه','234','0','0');
INSERT INTO [t13048] VALUES (25,'باب القول في الحقيقة والمجاز كل كلام مفيد فإنه ينقسم إلى حقيقة ومجاز: فأما الحقيقة , فهو الأصل في اللغة , وحده: كل لفظ استعمل فيما وضع له من غير نقل , فقد يكون للحقيقة مجاز , كالبحر , فإنه حقيقة في الماء المجتمع الكثير , ومجاز في الرجل العالم والفرس','250','0','0');
INSERT INTO [t13048] VALUES (26,'باب القول في الأمر والنهي الأمر هو: قول يستدعي به القائل الفعل ممن هو دونه فأما الأفعال التي ليست بقول فإنها تسمى أمرا على سبيل المجاز , وكذلك ما ليس فيه استدعاء , كالتهديد مثل قوله تعالى: اعملوا ما شئتم , وكالتعجيز مثل قوله: قل فأتوا بعشر سور مثله','258','0','0');
INSERT INTO [t13048] VALUES (27,'باب القول في العموم والخصوص العموم: كل لفظ عم شيئين فصاعدا , وقد يكون متناولا لشيئين , كقولك: عممت زيدا وعمرا بالعطاء , وقد يتناول جميع الجنس كقولك: عممت الناس بالعطاء , فأقله ما يتناول شيئين , وأكثره ما يستغرق الجنس وله صيغة إذا تجردت اقتضت العموم','265','0','0');
INSERT INTO [t13048] VALUES (28,'باب القول في المبين والمجمل أما المبين فهو: ما استقل بنفسه , في الكشف عن المراد , ولم يفتقر في معرفة المراد إلى غيره , وذلك على ضربين: ضرب يفيد بنطقه , وضرب يفيد بمفهومه فالذي يفيد بنطقه هو: النص , والظاهر , والعموم فالنص: كل لفظ دل على الحكم بصريحه','275','0','0');
INSERT INTO [t13048] VALUES (29,'باب القول في الناسخ والمنسوخ','286','0','0');
INSERT INTO [t13048] VALUES (30,'بيان وجوه النسخ يجوز النسخ إلى غير بدل , كعدة المتوفى عنها زوجها , فإنها كانت سنة , ثم نسخ منها ما زاد على أربعة أشهر وعشر إلى غير بدل ويجوز النسخ إلى بدل , كنسخ القبلة من بيت المقدس إلى الكعبة','293','0','0');
INSERT INTO [t13048] VALUES (31,'الكلام في الأصل الثاني من أصول الفقه وهو سنة رسول الله صلى الله عليه وسلم السنة: ما رسم ليحتذى , ولهذا قال النبي صلى الله عليه وسلم: " من سن في الإسلام سنة حسنة فله أجرها وأجر من عمل بها ولا فرق بين أن يكون هذا المرسوم واجبا , أو غير واجب , يدل عليه ما','304','0','0');
INSERT INTO [t13048] VALUES (32,'باب القول في سنن رسول الله صلى الله عليه وسلم التي ليس فيها نص كتاب , هل سنها بوحي أم بغير وحي قال بعض أهل العلم: لم يسن رسول الله صلى الله عليه وسلم سنة إلا بوحي , واحتج من قال هذا بظاهر قول الله تعالى: وما ينطق عن الهوى , إن هو إلا وحي يوحى','319','0','0');
INSERT INTO [t13048] VALUES (33,'ذكر الخبر عن رسول الله صلى الله عليه وسلم بأن سنته لا تفارق كتاب الله عز وجل','328','0','0');
INSERT INTO [t13048] VALUES (34,'باب القول في السنة المسموعة من النبي صلى الله عليه وسلم والمسموعة من غيره عنه السنة على ضربين: ضرب يؤخذ من النبي صلى الله عليه وسلم مشافهة وسماعا , فهذا يجب على كل أحد من المسلمين قبوله واعتقاده , على ما جاء به من وجوب وندب , وإباحة وحظر , ومن لم يقبله','332','0','0');
INSERT INTO [t13048] VALUES (35,'باب القول في وجوب العمل بخبر الواحد العدل قال الله سبحانه فلولا نفر من كل فرقة منهم طائفة ليتفقهوا في الدين ولينذروا قومهم إذا رجعوا إليهم لعلهم يحذرون','335','0','0');
INSERT INTO [t13048] VALUES (36,'وصف الخبر الذي يلزم قبوله ويجب العمل به لا يقبل خبر الواحد , حتى تثبت عدالة رجاله , واتصال إسناده وثبوت العدالة , أن يكون الراوي بعد بلوغه وصحة عقله ثقة مأمونا جميل الاعتقاد غير مبتدع مجتنبا للكبائر متنزها عن كل ما يسقط المروءة من المجون والسخف والأفعال','347','0','0');
INSERT INTO [t13048] VALUES (37,'باب أوصاف وجوه السنن ونعوتها قد مضى الكلام في الإسناد , والكلام ها هنا في المتن وجملته: أن في سنن رسول الله صلى الله عليه وسلم , مثل ما في كتاب الله من الحقيقة والمجاز , والخاص والعام , والمجمل والمبين , والناسخ والمنسوخ ونحن نورد من كل معنى ذكرناه شيئا','350','0','0');
INSERT INTO [t13048] VALUES (38,'باب من العام والخاص إذا تعارض لفظان عن رسول الله صلى الله عليه وسلم , وكان أحدهما عاما والآخر خاصا مثل ما:','355','0','0');
INSERT INTO [t13048] VALUES (39,'ذكر ما يجوز التخصيص به وما لا يجوز الأدلة التي يجوز التخصيص بها ضربان: متصل ومنفصل فأما المتصل: فهو: الاستثناء , والشرط , والتقييد بالصفة فأما الاستثناء: فلا يصح إلا أن يكون متصلا بالمستثنى منه وأما الشرط: فهو ما لا يصح المشروط إلا به , وقد يثبت','372','0','0');
INSERT INTO [t13048] VALUES (40,'ذكر القول في اللفظ الوارد على سبب اللفظ الوارد على سبب , لا يجوز إخراج السبب منه , لأنه يؤدي إلى تأخير البيان عن وقت الحاجة إليه , وذلك لا يجوز , وهل يدخل فيه غيره أم لا؟ ينظر فإن كان اللفظ لا يستقل بنفسه كان ذلك مقصورا على ما فيه من السبب , ويصير الحكم','375','0','0');
INSERT INTO [t13048] VALUES (41,'باب من المجمل والمبين','379','0','0');
INSERT INTO [t13048] VALUES (42,'باب من الناسخ والمنسوخ','396','0','0');
INSERT INTO [t13048] VALUES (43,'القول فيما يعرف به الناسخ من المنسوخ اعلم أن النسخ , قد يعلم بصريح النطق كما ذكرنا في حديث تحريم المتعة وقد يعلم بالإجماع , وهو: أن تجمع الأمة على خلاف ما ورد من الخبر , فيستدل بذلك على أنه منسوخ لأن الأمة لا تجتمع على الخطأ , مثال ذلك:','409','0','0');
INSERT INTO [t13048] VALUES (44,'باب القول في أفعال رسول الله صلى الله عليه وسلم لا يخلو فعل رسول الله صلى الله عليه وسلم من أن يكون قربة أو ليس بقربة فإن لم يكن قربة فهو يدل على الإباحة , كما:','422','0','0');
INSERT INTO [t13048] VALUES (45,'باب القول فيما يرد به خبر الواحد','426','0','0');
INSERT INTO [t13048] VALUES (46,'فصل وأما الحنفيون فقد قال من يحتج لهم: إذا عم البلوى , كثر السؤال , وإذا كثر السؤال , كثر الجواب , ويكون النقل على حسب البيان , فإذا نقل خاصا علم أنه لا أصل له وهذا عندنا غير صحيح والدليل على وجوب قبوله , أنه خبر عدل فيما يتعلق بالشرع مما لا طريق فيه','440','0','0');
INSERT INTO [t13048] VALUES (47,'ذكر ما روي من رجوع الصحابة عن آرائهم التي رأوها إلى أحاديث النبي صلى الله عليه وسلم إذا سمعوها ووعوها','442','0','0');
INSERT INTO [t13048] VALUES (48,'باب القول في الصحابي يروي حديثا عن رسول الله صلى الله عليه وسلم ثم يعمل بخلافه إذا روى الصحابي عن رسول الله صلى الله عليه وسلم حديثا , ثم روى عن ذلك الصحابي خلاف لما روى , فإنه ينبغي الأخذ بروايته , وترك ما روي عنه من فعله , أو فتياه , لأن الواجب علينا','454','0','0');
INSERT INTO [t13048] VALUES (49,'باب تعظيم السنن والحث على التمسك بها والتسليم لها والانقياد إليها وترك الاعتراض عليها','458','0','0');
INSERT INTO [t13048] VALUES (50,'ما جاء في ترك المخاطبة لمن عارض السنة بالمخالفة','494','0','0');
INSERT INTO [t13048] VALUES (51,'الكلام في الأصل الثالث من أصول الفقه وهو: إجماع المجتهدين إجماع أهل الاجتهاد في كل عصر حجة من حجج الشرع ودليل من أدلة الأحكام , مقطوع على مغيبه , ولا يجوز أن تجتمع الأمة على الخطأ وذهب إبراهيم بن سيار النظام إلى أنه يجوز اجتماع الأمة على الخطأ وقالت','504','0','0');
INSERT INTO [t13048] VALUES (52,'ومن الدليل أيضا على أصل المسألة قول الله تعالى: وكذلك جعلناكم أمة وسطا لتكونوا شهداء على الناس والوسط: العدل','508','0','0');
INSERT INTO [t13048] VALUES (53,'باب القول في أن إجماع أهل كل عصر حجة وأنه لا يقف على الصحابة خاصة إذا أجمع أهل عصر على شيء , كان إجماعهم حجة , ولا يجوز اجتماعهم على الخطأ وقال داود بن علي: الإجماع: إجماع الصحابة دون غيرهم , واحتج بقوله تعالى: وكذلك جعلناكم أمة وسطا , وبقوله: كنتم','545','0','0');
INSERT INTO [t13048] VALUES (54,'باب القول فيما يعرف به الإجماع ومن يعتبر قوله ومن لا يعتبر اعلم أن الإجماع يعرف بقول , وبفعل , وبقول وإقرار , وبفعل وإقرار فأما القول: فهو أن يتفق قول الجميع على الحكم , بأن يقولوا كلهم , هذا حلال أو حرام وأما الفعل: فهو أن يفعلوا كلهم الشيء وأما القول','547','0','0');
INSERT INTO [t13048] VALUES (55,'القول فيمن رد الإجماع الإجماع على ضربين: - أحدهما: إجماع الخاصة والعامة , وهو مثل: إجماعهم على القبلة أنها الكعبة , وعلى صوم رمضان , ووجوب الحج , والوضوء , والصلوات وعددها وأوقاتها , وفرض الزكاة وأشباه ذلك والضرب الآخر: هو إجماع الخاصة دون العامة ,','556','0','0');
INSERT INTO [t13048] VALUES (56,'باب القول في أنه يجب اتباع ما سنه أئمة السلف من الإجماع والخلاف , وأنه لا يجوز الخروج عنه إذا اختلف الصحابة في مسألة على قولين , وانقرض العصر عليه , لم يجز للتابعين أن يتفقوا على أحد القولين , فإن فعلوا ذلك لم يزل خلاف الصحابة والدليل عليه أن الصحابة','557','0','0');
INSERT INTO [t13048] VALUES (57,'باب ما جاء في القول الواحد من الصحابة إذا قال بعض الصحابة قولا , ولم ينتشر في علماء الصحابة , ولم يعرف له مخالف , لم يكن ذلك إجماعا , وهل هو حجة أو لا؟ فيه قولان: أحدهما: أنه حجة والقول الثاني: أنه ليس بحجة فمن ذهب إلى القول الأول احتج بأن الصحابي لا','561','0','0');
INSERT INTO [t13048] VALUES (58,'ذكر الكلام في القياس اعلم أن القياس فعل القائس وهو: حمل فرع على أصل في بعض أحكامه , لمعنى يجمع بينهما وقيل هو: الاجتهاد والأول: أجمع لحده , لأن الاجتهاد هو بذل المجهود في طلب العلم , فيدخل فيه حمل المطلق على المقيد , وترتيب الخاص على العام , وجميع','578','0','0');
INSERT INTO [t13048] VALUES (59,'ذكر الأحاديث الواردة في ذم القياس وتحريمه والمنع منه','580','0','0');
INSERT INTO [t13048] VALUES (60,'باب القول في الاحتجاج لصحيح القياس ولزوم العمل به قال الله سبحانه: يا أيها الذين آمنوا لا تقتلوا الصيد وأنتم حرم ومن قتله منكم متعمدا فجزاء مثل ما قتل من النعم فنص الله تعالى على وجوب الجزاء من النعم في المقتول من الصيد , ولم ينص على ما يعتبر من','619','0','0');
INSERT INTO [t13048] VALUES (61,'ذكر ما روي عن الصحابة والتابعين في الحكم بالاجتهاد وطريق القياس','642','0','0');
INSERT INTO [t13048] VALUES (62,'باب في سقوط الاجتهاد مع وجود النص','662','0','0');
INSERT INTO [t13048] VALUES (63,'ذكر القياس المحمود والقياس المذموم القياس على ضربين: ضرب منه في التوحيد , وضرب في أحكام الشريعة: فالقياس في التوحيد على ضربين: ضرب هو القياس الصحيح وهو: ما استدل به على معرفة الصانع تعالى وتوحيده , والإيمان بالغيب , والكتب , وتصديق الرسل , فهذا قياس','674','0','0');
INSERT INTO [t13048] VALUES (64,'باب الكلام في ذكر ما يشتمل القياس عليه القياس يشتمل على أربعة أشياء: على: الفرع , والأصل , والعلة , والحكم فأما الفرع: فهو ما ثبت حكمه بغيره وأما الأصل: فهو ما عرف حكمه بلفظ تناوله , أو ما عرف حكمه بنفسه , ويستعمل الفقهاء هذا الاسم , أعني الأصل في','675','0','0');
INSERT INTO [t13048] VALUES (65,'باب بيان ما يدل على صحة العلة اعلم أن العلة الشرعية أمارة على الحكم , ودلالة عليه , ولا بد في رد الفرع إلى الأصل من علة تجمع بينهما , ويلزم أن يدل على صحتها , أن العلة شرعية كما أن الحكم شرعي , فكما لا بد من الدلالة على الحكم , فكذلك لا بد من الدلالة على','676','0','0');
INSERT INTO [t13048] VALUES (66,'باب بيان ما يفسد العلة يفسد العلة أشياء: منها: أن لا يكون على صحتها دليل , فيدل ذلك على فسادها , لأنا قد بينا فيما تقدم , أن العلة شرعية , فإذا لم يكن على صحتها دليل من قبل الشرع , دل على أنها ليست بعلة , ووجب الحكم بفسادها ومنها: أن تكون منتزعة من','686','0','0');
INSERT INTO [t13048] VALUES (67,'باب القول في تعارض العلتين وترجيح إحداهما على الأخرى اعلم أن الترجيح لا يقع بين دليلين موجبين للعلم , لأن العلم لا يتزايد , وإن كان بعضه أقوى من بعض وكذلك لا يقع الترجيح بين دليل موجب للعلم أو علة موجبة له , وبين دليل أو علة يوجب كل واحد منهما الظن لما','689','0','0');
INSERT INTO [t13048] VALUES (68,'باب الكلام في: استصحاب الحال استصحاب الحال ضربان: أحدهما: استحباب حال العقل والثاني: استصحاب حال الإجماع فأما استصحاب حال العقل فهو: الرجوع إلى براءة الذمة في الأصل , وذلك طريق يفزع المجتهد إليه عند عدم أدلة الشرع , مثاله: أن يسأل شافعي عن الوتر','691','0','0');
INSERT INTO [t13048] VALUES (69,'باب القول في: حكم الأشياء قبل الشرع اختلف أهل العلم في الأعيان المنتفع بها قبل ورود الشرع فمنهم من قال: هي على الحظر , فلا يحل الانتفاع بها ولا التصرف فيها ومنهم من قال: هي على الإباحة , فمن رأى شيئا جاز له تناوله وتملكه ومنهم من قال: إنها على الوقف','693','0','0');
INSERT INTO [t13048] VALUES (70,'باب ترتيب استعمال الأدلة واستخراجها','695','0','0');
INSERT INTO [t13048] VALUES (71,'ذكر الكلام في النظر والجدل النظر ضربان: ضرب هو: النظر بالعين , فهذا حده الإدراك بالبصر والثاني: النظر بالقلب , فهذا حده الفكر في حال المنظور فيه , والمنظور فيه هو: الأدلة والأمارات الموصلة إلى المطلوب والمنظور له هو: الحكم , لأنه ينظر لطلب الحكم','724','0','0');
INSERT INTO [t13048] VALUES (72,'باب ذكر ما تعلق به من أنكر المجادلة وإبطاله احتج من ذهب إلى إبطال الجدال , بقول الله تعالى: الذين يجادلون في آياتنا ما لهم من محيص , وبقوله تعالى: فإن حاجوك فقل أسلمت وجهي لله ومن اتبعن','725','0','0');
INSERT INTO [t13048] VALUES (73,'باب القول في السؤال عن الحادثة والكلام فيها قبل وقوعها','749','0','0');
INSERT INTO [t13048] VALUES (74,'ذكر ما لا بد للمتجادلين من معرفته','791','0','0');
INSERT INTO [t13048] VALUES (75,'ذكر الدليل ومعناه','806','0','0');
INSERT INTO [t13048] VALUES (76,'باب أدب الجدال ينبغي للمجادل , أن يقدم على جداله تقوى الله تعالى , لقوله سبحانه: فاتقوا الله ما استطعتم ولقوله: إن الله مع الذين اتقوا والذين هم محسنون','810','0','0');
INSERT INTO [t13048] VALUES (77,'باب في السؤال والجواب وما يتعلق بهما من الكراهة والاستحباب','840','0','0');
INSERT INTO [t13048] VALUES (78,'باب تقسيم الأسئلة والجوابات , ووصف وجوه المطاعن والمعارضات السؤال على أربعة أضرب , يقابل كل ضرب منها ضرب من الجواب من جهة المسئول فأولها: السؤال عن المذهب , بأن يقول السائل: ما تقول في كذا؟ فيقابله جواب من جهة المسئول , فيقول: كذا والثاني: السؤال عن','872','0','0');
INSERT INTO [t13048] VALUES (79,'فصل وإذا صح الجواب من جهة المسئول قال له السائل: ما الدليل عليه؟ وهو السؤال الثاني: فإذا ذكر المسئول الدليل فإن كان السائل يعتقد أن ما ذكره ليس بدليل مثل أن يكون قد احتج بالقياس , والسائل ظاهري لا يقول بالقياس فقال للمسئول: هذا ليس بدليل، فإن','876','0','0');
INSERT INTO [t13048] VALUES (80,'فصل وأما السؤال الثالث: وهو السؤال عن وجه الدليل وكيفيته , فإنه ينظر فيه , فإن كان الدليل الذي استدل به غامضا يحتاج إلى بيان وجب السؤال عنه , وإن تجاوزه إلى غيره كان مخطئا , لأنه لا يجوز تسليمه إلا بعد أن ينكشف وجه الدليل منه , من وجهة المسئول على ما','878','0','0');
INSERT INTO [t13048] VALUES (81,'فصل وأما السؤال الرابع: وهو السؤال على سبيل الاعتراض والقدح في الدليل , فإن ذلك يختلف على حسب اختلاف الدليل: فإن كان دليله من القرآن كان الاعتراض عليه من ثلاثة أوجه: أحدها: أن ينازعه في كونه محكما , ويدعي أنه منسوخ مثاله: أن يحتج الشافعي , بقول الله','880','0','0');
INSERT INTO [t13048] VALUES (82,'فصل وإن كان دليله الإجماع , فإن الاعتراض عليه من ثلاثة أوجه: أحدها: أن يطالب بظهور القول لكل مجتهد من الصحابة','889','0','0');
INSERT INTO [t13048] VALUES (83,'فصل وإن كان دليله الذي احتج به هو القياس , فإن الاعتراض عليه من وجوه: أحدها: أن يكون مخالفا لنص القرآن , أو نص السنة , أو الإجماع , وإذا كان كذلك , فإنه قياس غير صحيح , لأن ما ذكرناه أقوى من القياس , وأولى منه , فوجب تقديمها عليه، ومنها: أن تكون','893','0','0');
INSERT INTO [t13048] VALUES (84,'فصل يجوز للسائل أن يسأل الخصم , فيقول له: ما تقول في كذا؟ ويفوض الجواب إليه , وإن كان عالما بجوابه قال الله تعالى مخبرا عن إبراهيم عليه السلام: إذ قال لأبيه وقومه ما تعبدون قالوا نعبد أصناما وذلك معلوم له من جوابهم , وهذا يسمى سؤال التفويض , ولو سأل','896','0','0');
INSERT INTO [t13048] VALUES (85,'فصل إذا ذكر المجادل جواب أقسام قسمها , أو ألزم أسئلة سألها , فليس عليه أن يرتب جوابه , بل يجوز أن يذكر جواب سؤال متقدم أو متأخر , ويأتي بالآخر من غير ترتيب , قال الله تعالى: يوم تبيض وجوه وتسود وجوه فقسم الوجوه قسمين بدأ منهما بذكر المبيضة وجوههم , ثم','897','0','0');
INSERT INTO [t13048] VALUES (86,'فصل التقسيم على ضربين كلاهما جائز: أحدهما: أن يقسم المقسم حال الشيء , فيذكر جميع أقسامه , ثم يرجع فيذكر حكم كل قسم , كما فعلنا في تقسيم الأسئلة والجوابات , ووصف وجوه المطاعن والمعارضات والضرب الثاني: أن يذكر قسما ثم يذكر حكمه ثم يذكر القسم الآخر ثم','898','0','0');
INSERT INTO [t13048] VALUES (87,'فصل قد يعبر السائل عن المسألة بالاسم الذي يعرف به المسألة ولا يكون ذلك تسليما منه للاسم فيها كقائل سأل حنفيا فقال: لم قلت: إن الطهارة بغير نية تصح؟ فليس للحنفي أن يقول قد سلمت لي أنها طهارة في لفظ سؤالك , ومسألتك عن بطلانها بفقد النية دعوى , فقد سقط','899','0','0');
INSERT INTO [t13048] VALUES (88,'فصل يجوز لمن طولب بمقدمة في كلامه أن يشترط على من طالبه بها الالتزام لما تقتضيه المقدمة والعمل بحكمها والوفاء بمقتضاها , قال الله تعالى: إذ قال الحواريون يا عيسى ابن مريم هل يستطيع ربك أن ينزل علينا مائدة من السماء قال اتقوا الله إن كنتم مؤمنين إلى أن','900','0','0');
INSERT INTO [t13048] VALUES (89,'فصل يجوز للمتكلم تقديم علة الحكم , ثم يعقب ذلك بالحكم , ويجوز أن يقدم الحكم ثم يذكر علته , قال الله تعالى: ويسألونك عن المحيض قل هو أذى فاعتزلوا النساء في المحيض فقدم العلة قبل الفتوى بحكم ما سئل عنه , وقدم الحكم في موضع آخر , فقال: فطلقوهن لعدتهن','901','0','0');
INSERT INTO [t13048] VALUES (90,'فصل يجوز للمتكلم إذا عين في نوبة من كلامه شيئا ثم أعاد النوبة أن يعيد ما كان عينه بلفظ مبهم , قال الله تعالى: إلا عجوزا في الغابرين ولم يعين من هي العجوز , وذلك بعد ما عينها في قوله: إنا منجوك وأهلك إلا امرأتك كانت من الغابرين','902','0','0');
INSERT INTO [t13048] VALUES (91,'فصل يجوز للمتكلم إذا عادت نوبته في النظر واقتضى الكلام إعادة مثل ما تقدم أن يقول لخصمه: هذا قد تكلمت به أولا وقد تقدم جوابي عنه , فأغنى عن إعادته طلبا للتخفيف , قال الله تعالى: وعلى الذين هادوا حرمنا ما قصصنا عليك من قبل ولم يعده اكتفاء بما تقدم','903','0','0');
INSERT INTO [t13048] VALUES (92,'فصل كثيرا يجري من المناظر في حال الكلام واشتداد الخاطر , إذا وثق بما يقول أن يحلف عليه فيقول: والله , إنه لصحيح , فيقول له الخصم: ليس في يدك حجة , وهذا شيء لا يجيء بالإيمان , وخصمك أيضا يحلف على ضد ما تقول؟ فجوابه أن يقول: ما حلفت ليلزمك يميني حجة ,','904','0','0');
INSERT INTO [t13048] VALUES (93,'فصل قد يشبه الخصم لخصمه الحق عنده بما هو حق عنده أيضا , فيقول: هذا عندي مثل أن الشمس طالعة , أو هذا واجب , كوجوب الصلاة الخمس , قال الله تعالى: فورب السماء والأرض إنه لحق مثل ما أنكم تنطقون وليس هذا مثال حجاج , وإنما هو مثال تشبيه , أي أن حكم هذا عندي','906','0','0');
INSERT INTO [t13048] VALUES (94,'فصل قد يمثل الخصم لخصمه قوله بقول باطل عنده , ليعلم خصمه بطلان قوله , كبطلان ما مثله به , قال الله تعالى: يا أيها الذين آمنوا لا تتولوا قوما غضب الله عليهم قد يئسوا من الآخرة كما يئس الكفار من أصحاب القبور وتقديره: إنكم في إياسكم من الآخرة كما يئس','907','0','0');
INSERT INTO [t13048] VALUES (95,'فصل إذا اعترض أحد الخصمين على الآخر بشيء يخالف أصله فله أن يرده بأصله , وله أن يرده بمعنى نظري أو فقهي , قال الله تعالى: سيقول السفهاء من الناس ما ولاهم عن قبلتهم التي كانوا عليها قل لله المشرق والمغرب يهدي من يشاء إلى صراط مستقيم فأخبر الله تعالى بقول','908','0','0');
INSERT INTO [t13048] VALUES (96,'فصل القلب على الخصم والمعارضة والنقض , كل ذلك صحيح في النظر , قال الله سبحانه وتعالى حاكيا عن قول المنافقين: لو كانوا عندنا ما ماتوا وما قتلوا فأجابهم بما أقلبه عليهم في أنفسهم , وإن جعلته نقضا صح , وإن جعلته معارضة أيضا صح , ولكل واحد وجه , فقال: قل','910','0','0');
INSERT INTO [t13048] VALUES (97,'فصل السكوت عن الجواب للعجز من أقسام الانقطاع , قال الله تعالى: فبهت الذي كفر وأقسام الانقطاع من وجوه: هذا أحدها، والثاني: أن يعلل ولا يجدي، والثالث: أن ينقض ببعض كلامه بعضا، والرابع: أن يؤدي كلامه إلى المحال، والخامس: أن ينتقل من دليل إلى دليل','911','0','0');
INSERT INTO [t13048] VALUES (98,'باب الكلام في أقوال المجتهدين , وهل الحق في واحد أو كل مجتهد مصيب إذا اختلف المجتهدون من العلماء في مسألة على قولين أو أكثر , فقد ذكر عن أبي حنيفة أنه قال: كل مجتهد مصيب والحق ما غلب على ظن المجتهد , وهو ظاهر مذهب مالك بن أنس , وذكر عن الشافعي أن له في','916','0','0');
INSERT INTO [t13048] VALUES (99,'باب الكلام في التقليد وما يسوغ منه وما لا يسوغ قد ذكرنا الأدلة التي يرجع إليها المجتهد في معرفة الأحكام , وبقي الكلام في بيان ما يرجع إليه العامي في العمل وهو التقليد وجملته أن التقليد هو: قبول القول من غير دليل والأحكام على ضربين عقلي وشرعي، فأما','931','0','0');
INSERT INTO [t13048] VALUES (100,'باب القول فيمن يسوغ له التقليد ومن لا يسوغ أما من يسوغ له التقليد فهو العامي: الذي لا يعرف طرق الأحكام الشرعية , فيجوز له أن يقلد عالما , ويعمل بقوله , قال الله تعالى: فاسألوا أهل الذكر إن كنتم لا تعلمون','938','0','0');
INSERT INTO [t13048] VALUES (101,'باب في فضل العلم والعلماء','946','0','0');
INSERT INTO [t13048] VALUES (102,'باب: إخلاص النية والقصد بالتفقه وجه الله عز وجل ينبغي لمن اتسع وقته وأصح الله تعالى له جسمه , وحبب إليه الخروج من طبقة الجاهلين , وألقى في قلبه العزيمة على التفقه في الدين , أن يغتنم المبادرة إلى ذلك , خوفا من حدوث أمر يقتطعه عنه , وتجدد حال يمنعه منه ,','993','0','0');
INSERT INTO [t13048] VALUES (103,'باب التفقه في الحداثة وزمن الشبيبة','1005','0','0');
INSERT INTO [t13048] VALUES (104,'باب حذف المتفقه العلائق كان بعض الفلاسفة لا يعلم أحدا يتعلق بشيء من الدنيا , ويقول: العلم أجل من أن يشتغل عنه بغيره','1019','0','0');
INSERT INTO [t13048] VALUES (105,'باب اختيار الفقهاء الذين يتعلم منهم ينبغي للمتعلم أن يقصد من الفقهاء من اشتهر بالديانة , وعرف بالستر والصيانة','1037','0','0');
INSERT INTO [t13048] VALUES (106,'باب تعظيم المتفقه الفقيه وهيبته إياه وتواضعه له','1046','0','0');
INSERT INTO [t13048] VALUES (107,'باب: ترتيب أحوال المبتدئ بالتفقه ينبغي للمبتدئ إذا حضر مجلس التفقه , أن يقرب من الفقيه , حتى يكون بحيث لا يخفى عنه شيء مما يقوله , ويصمت ويصغي إلى كلامه','1052','0','0');
INSERT INTO [t13048] VALUES (108,'باب القول في التحفظ وأوقاته وإصلاح ما يعرض من علله وآفاته اعلم أن للحفظ ساعات , ينبغي لمن أراد التحفظ أن يراعيها وللحفظ أماكن ينبغي للمتحفظ أن يلزمها فأجود الأوقات: الأسحار , ثم بعدها وقت انتصاف النهار , وبعدها الغدوات دون العشيات , وحفظ الليل أصلح من','1066','0','0');
INSERT INTO [t13048] VALUES (109,'باب: ذكر مقدار ما يحفظه المتفقه اعلم أن القلب جارحة من الجوارح , تحتمل أشياء , وتعجز عن أشياء , كالجسم الذي يحتمل بعض الناس أن يحمل مائتي رطل , ومنهم من يعجز عن عشرين رطلا , وكذلك منهم من يمشي فراسخ في يوم , لا يعجزه , ومنهم من يمشي بعض ميل , فيضر ذلك','1079','0','0');
INSERT INTO [t13048] VALUES (110,'باب: ذكر أخلاق الفقيه وآدابه وما يلزمه استعماله مع تلاميذه وأصحابه يلزم الفقيه أن يتخير من الأخلاق أجملها , ومن الآداب أفضلها , فيستعمل ذلك مع البعيد والقريب , والأجنبي والنسيب , ويتجنب طرائق الجهال , وخلائق العوام والأرذال','1087','0','0');
INSERT INTO [t13048] VALUES (111,'حسن مجالسة الفقيه لمن جالسه','1095','0','0');
INSERT INTO [t13048] VALUES (112,'استعماله التواضع ولين الجانب ولطف الكلام','1103','0','0');
INSERT INTO [t13048] VALUES (113,'استقباله المتفقهة بالترحيب بهم وإظهار البشر لهم','1110','0','0');
INSERT INTO [t13048] VALUES (114,'باب آداب التدريس إذا أراد الفقيه الخروج إلى أصحابه ليذكر لهم دروسهم فينبغي له أن يتفقد حاله قبل خروجه , فإن كان جائعا أصاب من الطعام ما يسكن عنه فورة الجوع','1130','0','0');
INSERT INTO [t13048] VALUES (115,'ما جاء في المذاكرة بالفقه ليلا','1163','0','0');
INSERT INTO [t13048] VALUES (116,'فضل تدريس الفقه في المساجد','1168','0','0');
INSERT INTO [t13048] VALUES (117,'إلقاء الفقيه المسائل على أصحابه أستحب أن يخص يوم الجمعة بالمذاكرة لأصحابه في المسجد الجامع , وإلقاء المسائل عليهم ويأمرهم بالكلام فيها , والمناظرة عليها','1176','0','0');
INSERT INTO [t13048] VALUES (118,'تنبيه الفقيه على مراتب أصحابه','1202','0','0');
INSERT INTO [t13048] VALUES (119,'باب: القول فيمن تصدى لفتاوى العامة وما ينبغي أن يكون عليه من الأوصاف ويستعمله من الأخلاق والآداب','1245','0','0');
INSERT INTO [t13048] VALUES (120,'ما جاء من الوعيد لمن أفتى وليس هو من أهل الفتوى','1257','0','0');
INSERT INTO [t13048] VALUES (121,'باب ذكر شروط من يصلح للفتوى أول أوصاف المفتي الذي يلزم قبول فتواه: أن يكون بالغا , لأن الصبي لا حكم لقوله ثم يكون عاقلا لأن القلم مرفوع عن المجنون لعدم عقله ثم يكون عدلا ثقة , لأن القلم مرفوع عن المجنون لعدم عقله ثم يكون عدلا ثقة , لأن علماء المسلمين لم','1263','0','0');
INSERT INTO [t13048] VALUES (122,'ما جاء في ورع المفتي وتحفظه','1279','0','0');
INSERT INTO [t13048] VALUES (123,'اعتماد المفتي على الكتاب والسنة','1291','0','0');
INSERT INTO [t13048] VALUES (124,'ذكر ما يلزم الإمام أن يفرض للفقهاء ومن نصب نفسه للفتوى من الرزق والعطاء لا يسوغ للمفتي أن يأخذ الأجرة من أعيان من يفتيه , كالحاكم الذي لا يجوز له أن يأخذ الرزق من أعيان من يحكم له وعليه وعلى الإمام أن يفرض لمن نصب نفسه لتدريس الفقه والفتوى في الأحكام ,','1297','0','0');
INSERT INTO [t13048] VALUES (125,'باب الزجر عن التسرع إلى الفتوى مخافة الزلل قال الله تبارك وتعالى: ستكتب شهادتهم ويسألون وقال تعالى: ليسأل الصادقين عن صدقهم , وقال تعالى: ما يلفظ من قول إلا لديه رقيب عتيد , وكانت الصحابة رضوان الله عليهم , لا تكاد تفتي إلا فيما نزل ثقة منهم بأن الله','1300','0','0');
INSERT INTO [t13048] VALUES (126,'باب ما جاء في الإحجام عن الجواب إذا خفي على المسئول وجه الصواب قال الله تعالى: وفوق كل ذي علم عليم فإذا سئل المفتي عن حكم نازلة فأشكل عليه , وهناك من هو عارف به , لزمه أن يرشد السائل إليه , ويدله عليه','1326','0','0');
INSERT INTO [t13048] VALUES (127,'باب أدب المستفتي أول ما يلزم المستفتي إذا نزلت به نازلة أن يطلب المفتي , ليسأله عن حكم نازلته فإن لم يكن في محلته وجب عليه أن يمضي إلى الموضع الذي يجده فيه فإن لم يكن ببلده لزمه الرحيل إليه , وإن بعدت داره , فقد رحل غير واحد من السلف في مسألة','1354','0','0');
INSERT INTO [t13048] VALUES (128,'باب ما يفعله المفتي في فتواه إذا لم يكن بالموضع الذي هو فيه مفت سواه لزمه فتوى من استفتاه , لقول الله تبارك وتعالى: إن الذين يكتمون ما أنزلنا من البينات والهدى من بعد ما بيناه للناس في الكتاب أولئك يلعنهم الله ويلعنهم اللاعنون','1374','0','0');
INSERT INTO [t13048] VALUES (129,'باب التمحل في الفتوى متى وجد المفتي للسائل مخرجا في مسألته , وطريقا يتخلص به , أرشده إليه , ونبهه عليه كرجل حلف أن لا ينفق على زوجته , ولا يطعمها شهرا , أو شبه هذا , فإنه يفتيه بإعطائها من صداقها , أو دين لها عليه , أو يقرضها ثم يبرئها , أو يبيعها سلعة','1418','0','0');
INSERT INTO [t13048] VALUES (130,'باب في خزن بعض ما يسمع من العلم والإمساك عنه لعذر في ذلك','1427','0','0');
INSERT INTO [t13048] VALUES (131,'باب رجوع المفتي عن فتواه إذا تبين له أن الحق في غيرها','1442','0','0');
INSERT INTO [t13048] VALUES (132,'التوثق في استفتاء الجماعة','1454','0','0');
