INSERT INTO [t13056] VALUES (362,'أبو مسلم سلمة بن الأكوع رضي الله عنه سمعت العباس بن محمد قال: سمعت يحيى بن معين يقول: سلمة بن الأكوع كنيته أبو مسلم. وعبيدة السلماني أبو مسلم. وأهبان بن صيفي كنيته أبو مسلم. وأبو مسور مخرمة بن نوفل بن عبد مناف بن زهرة له صحبة، ولابنه المسور بن','834','0','0');
