INSERT INTO [t13056] VALUES (373,'أبو الوليد حسان بن ثابت رضي الله عنه حدثنا عبد الله بن أحمد بن حنبل قال: ثنا أبي قال: حدثني هارون بن إسماعيل بن النعمان بن عبد الله بن كعب بن مالك قال: كان حسان بن ثابت يكنى بأبي الحسام، وكانت كنيته أبو الوليد فكأنه كرهها.','853','0','0');
