INSERT INTO [t13056] VALUES (376,'عقبة بن أبي معيط أبو وهب رضي الله عنه سمعت العباس يقول: سمعت يحيى يقول: سمعت أن عقبة بن أبي معيط يكنى أبا وهب','859','0','0');
INSERT INTO [t13056] VALUES (377,'شرحبيل ابن حسنة أبو وهب رضي الله عنه سمعت أحمد بن عبد الله بن عبد الرحيم يقول: شرحبيل ابن حسنة يكنى أبا واثلة وهو ابن أبي المطاع، وأمه حسنة بنت حبيب بن معمر الجمحي فيما أخبرني مصعب بن عبد الله الزبيري','860','0','0');
