INSERT INTO [t13056] VALUES (381,'عائذ أبو هبيرة، وأبو هالة ذكر لي عن علي بن المديني، أن عائذ بن عمرو كنيته أبو هبيرة. وسمعت العباس قال: قال يحيى: أبو هالة التميمي اسمه النباش بن زرارة','866','0','0');
