INSERT INTO [t13056] VALUES (764,'من كنيته أبو يسار، وأبو اليسر أبو يسار عبد الله بن أبي نجيح. وأبو اليسر بن درهم مولى لآل جبير بن مطعم سمعت العباس بن محمد قال: سمعت يحيى بن معين يقول: " كنية عبد الله بن أبي نجيح أبو يسار "','2916','0','0');
