INSERT INTO [t13056] VALUES (769,'من كنيته أبو يزيد أبو يزيد الهمداني حدثنا أبو بكر بن أبي الدنيا، قال: حدثني محمد بن الحسين قال: حدثنا الصلت بن حكيم قال حدثني أبو يزيد الهمداني قال: انصرفت ذات يوم من الجمعة وإذا عطاء السليمي وعمر بن درهم القرشي يمشيان وكان عطاء بكى حتى عمي، وكان','2936','0','0');
